(window.webpackJsonp=window.webpackJsonp||[]).push([[1778],{2134:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-522-tat-ca-đeu-bat-luc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-522-tat-ca-đeu-bat-luc"}},[n._v("#")]),n._v(" Chương 522: Tất cả đều bất lực")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Đúng vậy a, Tiết viên chủ nói một chút đi, nếu không chúng ta cũng là luống cuống!”"),t("br"),t("br"),n._v("“Mọi người không biết, cũng là bất lực, dược chủ không ngại nói rõ chi tiết một chút tình huống, chúng ta cũng muốn tìm biện pháp tốt hơn!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Lư lão lời nói, để mọi người đồng thời gật đầu, hô lên."),t("br"),t("br"),n._v("Coi như vì Tiết viên chủ ban thưởng, cũng không dám qua loa."),t("br"),t("br"),n._v("“Vậy thì tốt, ta liền nói một chút Địa Mạch Linh Đằng tình huống!”"),t("br"),t("br"),n._v("Biết chỉ có nói ra, mọi người mới tốt nghĩ biện pháp, Tiết viên chủ cũng không chối từ: “Địa Mạch Linh Đằng, luôn luôn sống rất thoải mái, không bệnh thời điểm, căn này dây leo mượt mà sung mãn, toàn thân xanh biếc, cành lá cũng mười phần um tùm, lại tới đây, cho người ta một loại nồng đậm sinh cơ cảm giác... Đại khái nửa năm trước, tình huống thay đổi!”"),t("br"),t("br"),n._v("“Nửa năm trước, ta phái đến trông giữ dược điền thị nữ hồi báo, nói linh đằng xuất hiện ố vàng lá cây, lúc ấy ta còn chưa tin, lập tức chạy tới, vừa nhìn phía dưới, quả là thế! Bất quá, chỉ cần nuôi dưỡng qua dược liệu, đều hẳn phải biết, cây cối lá cây sẽ theo mùa biến hóa mà biến vàng, đồng thời không chú trọng!”"),t("br"),t("br"),n._v("“Ai ngờ mười ngày sau, lây bệnh đồng dạng, lá vàng càng ngày càng nhiều, do trước đó vài miếng, diễn biến đến trọn vẹn một nửa. Lúc này ta rốt cục ý thức được không được bình thường, lập tức nghĩ biện pháp trị liệu!”"),t("br"),t("br"),n._v("“Lá cây ố vàng, ta đầu tiên hoài nghi là bồi dưỡng nước vấn đề, rất nhiều cây cối xuất hiện loại tình huống này hàng đầu nhân tố chính là nước!”"),t("br"),t("br"),n._v("“Vì thế, ta chuyên môn mang tới Thiên Hàn sơn tuyết nước, đối với nó tiến hành đổ vào, vốn cho rằng nương theo thời gian trôi qua, lá vàng tình huống liền sẽ làm dịu, ai ngờ, một tháng sau, toàn bộ linh đằng đã phủ đầy lá vàng, không chỉ có như thế, chi mạch cũng bắt đầu khô héo!”"),t("br"),t("br"),n._v("“Cái này ta có chút luống cuống, cẩn thận nghiên cứu đất đai vấn đề, thậm chí vận dụng cực lớn đại giới, đem trọn cái dây leo hấp thu đất đai đều đổi đi... Vẫn không có hiệu quả! Sau đó lại sử dụng đủ loại phương pháp, kết quả đều không có mảy may tác dụng, cho tới bây giờ lại không biện pháp gì, đành phải mời mọi người qua đến giúp đỡ!”"),t("br"),t("br"),n._v("Vừa nói vừa lắc đầu, Tiết viên chủ sinh ra cô đơn chi ý."),t("br"),t("br"),n._v("“Lá cây ố vàng, chi mạch khô héo, dựa theo bình thường đạo lý, hoặc là dinh dưỡng không đầy đủ, hoặc là dinh dưỡng quá thừa... Tiết viên chủ có thể hay không thử qua?”"),t("br"),t("br"),n._v("Lư lão mở lời."),t("br"),t("br"),n._v("“Những này ta đều thử qua, không dùng!”"),t("br"),t("br"),n._v("Tiết viên chủ lắc đầu."),t("br"),t("br"),n._v("“Ta trước đến xem đi!”"),t("br"),t("br"),n._v("Nghe xong giải thích, trong đám người một cái lão giả đi tới."),t("br"),t("br"),n._v("“Là Lưu lão!”"),t("br"),t("br"),n._v("“Cái nào Lưu lão?”"),t("br"),t("br"),n._v("“Vạn Quốc thành còn có ai dám tự xưng Lưu lão? Đương nhiên là thành bắc dược viên Lưu viên chủ!”"),t("br"),t("br"),n._v("“Hắn cũng tới? Thành bắc dược viên thế nhưng là Vạn Quốc thành thứ hai lớn dược viên!”"),t("br"),t("br"),n._v("“Nếu như hắn cũng nhìn không ra, chúng ta cũng không có biện pháp...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nhìn thấy trong đám người đi ra lão giả, mọi người lập tức xôn xao."),t("br"),t("br"),n._v("“Cái tên này rất nổi danh?”"),t("br"),t("br"),n._v("Trương Huyền nghi ngờ nhìn về phía Lư Trạm."),t("br"),t("br"),n._v("“Khụ khụ...” Lư Trạm bị nước bọt sặc một cái: “Trương huynh, vị này Lưu lão một trăm năm trước cũng đã danh chấn Vạn Quốc thành, từng vì minh chủ phủ ngự dụng dưỡng dược người! Gia gia của ta, năm đó cũng từng hướng hắn hỏi qua, bàn về dưỡng dược tư cách, tuyệt đối là toàn bộ Vạn Quốc thành già nhất! Ngươi... Sẽ không liền hắn đều không nghe hắn nói qua đi!”"),t("br"),t("br"),n._v("Lư Trạm đều nhanh cảm thấy điên rồi."),t("br"),t("br"),n._v("Trước mắt vị này Trương Huyền đến cùng phải hay không Vạn Quốc thành người, chỉ cần là, làm sao có thể không biết Lưu lão?"),t("br"),t("br"),n._v("Phải biết danh tiếng của hắn không giới hạn trong dưỡng dược, vô luận thực lực vẫn là cái gì, đều chấn nhiếp vương thành."),t("br"),t("br"),n._v("“Ah!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“A liền xong rồi?” Lư Trạm không một cái lão huyết phun ra ngoài."),t("br"),t("br"),n._v("Đây chính là Lưu lão, coi như Danh Sư đường đương nhiệm Khang đường chủ, nói đến hắn, đều sẽ phát ra từ nội tâm tôn trọng, nổi lòng tôn kính, ngươi liền một cái “A”, khuôn mặt bình tĩnh không lay động, thật giống trông thấy “Trương Tam, Lý Tứ” đồng dạng, có ý tứ gì?"),t("br"),t("br"),n._v("“Làm sao vậy?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Không có gì!”"),t("br"),t("br"),n._v("Thấy hắn vẻ mặt vô tội, Lư Trạm hai mắt choáng váng."),t("br"),t("br"),n._v("Lúc này hắn mới biết được, người thiếu niên trước mắt này không là giả vờ, mà là thật không biết Lưu lão địa vị cùng thân phận."),t("br"),t("br"),n._v("Vừa đem trong lòng bất đắc dĩ áp chế xuống, dự định về sau phải thật tốt cho vị này Trương huynh phổ cập một chút dưỡng dược tri thức, liền nghe đến một câu, lần nữa để hắn phát điên lời nói."),t("br"),t("br"),n._v("“Lão nhân này trình độ còn chưa đủ, trị không hết Địa Mạch Linh Đằng!”"),t("br"),t("br"),n._v("Lập tức chỉ thấy thanh niên nhìn về phía Lưu lão, không ngừng lắc đầu, dường như mang theo thất vọng."),t("br"),t("br"),n._v("Lắc đầu? Thất vọng?"),t("br"),t("br"),n._v("Chỉ cảm thấy lần nữa ngực sôi trào, Lư Trạm nổi điên."),t("br"),t("br"),n._v("Ngươi thất vọng cái cọng lông ah, ngươi liền xem như danh sư, nhưng đối với dưỡng dược một chữ cũng không biết, nhân gia Lưu lão là danh khắp thiên hạ dưỡng dược mọi người, coi như trị không được, ngươi thất vọng lấy ư?"),t("br"),t("br"),n._v("Hơn nữa... Giọng điệu này!"),t("br"),t("br"),n._v("Cùng nói vãn bối đồng dạng, đại ca, ngươi từ nơi nào xuất hiện? Ta có thể hay không đừng như thế cuồng, phách lối như vậy ư?"),t("br"),t("br"),n._v("Bên này Lư Trạm sắp ngất đi, bên kia Lưu lão rốt cục vây thô to dây leo dạo qua một vòng."),t("br"),t("br"),n._v("“Đất đai, nước chất, không khí, dinh dưỡng... Đều không có vấn đề, đây là có chuyện gì?”"),t("br"),t("br"),n._v("Lưu lão mày nhăn lại, dường như có cái gì nghi vấn khó xử lý không nghĩ ra."),t("br"),t("br"),n._v("Nhờ vào hắn đối dược liệu nghiên cứu, chỉ nhìn lên một cái, liền có thể phân biệt ra được đất đai, nước chất loại hình có phải hay không hợp cách, trước mắt những này có thể ảnh hưởng dược liệu đồ vật, đều không thể bắt bẻ, không có kẽ hở, có thể to lớn như vậy Địa Mạch Linh Đằng sắp phải chết, để hắn không nói ra được kỳ quái."),t("br"),t("br"),n._v("“Tiết viên chủ, có phải hay không là căn này Địa Mạch Linh Đằng tuổi thọ đến?”"),t("br"),t("br"),n._v("Suy tư một lát, Lưu lão mở lời."),t("br"),t("br"),n._v("“Tuổi thọ? Sẽ không! Địa Mạch Linh Đằng có thể đủ sống hơn ngàn năm, căn này đến bây giờ chẳng qua hơn một trăm năm, làm sao có thể tuổi thọ đến?”"),t("br"),n._v("Tiết viên chủ lắc đầu."),t("br"),t("br"),n._v("Điểm ấy nàng cũng đã sớm cân nhắc đến, cũng chuyên môn phân tích qua, phát hiện cả cây dây leo sinh cơ bừng bừng, căn bản không phải sắp chết phải có thái độ."),t("br"),t("br"),n._v("“Lại nói, nếu như là tuổi thọ đến, hẳn là dần dần khô héo, từ gốc bắt đầu thối rữa, nhưng bây giờ lại là lá cây trước ố vàng, sau đó chi mạch tử vong, rõ ràng không phải tuổi thọ đến!”"),t("br"),t("br"),n._v("Thực vật, dược liệu tuổi thọ đại nạn, cơ bản đều là gốc khô héo, rễ cây khô quắt, sau đó mới là lá cây, hiện tại cái này lá cây trước vàng, rõ ràng trình tự không đúng."),t("br"),t("br"),n._v("“Đúng vậy a!”"),t("br"),t("br"),n._v("Nghe được đối phương phân tích, Lưu lão thực sự nhịn không được lắc đầu: “Tại hạ tư chất ngu dốt, trải qua điều tra, thực sự tìm không ra vấn đề, còn xin Tiết viên chủ thứ lỗi!”"),t("br"),t("br"),n._v("“Vẫn là làm phiền Lưu lão!”"),t("br"),t("br"),n._v("Thấy hắn bất lực, Tiết viên chủ ánh mắt ảm đạm mấy phần, bất quá vẫn là lễ phép tính trả lời một câu."),t("br"),t("br"),n._v("“Cũng không giúp một tay! Thật có lỗi!”"),t("br"),t("br"),n._v("Lưu lão lui về phía sau mấy bước, đứng tại một bên khổ sở suy nghĩ."),t("br"),t("br"),n._v("Dường như trước mắt Địa Mạch Linh Đằng tình huống quả thực để hắn không có biện pháp tốt hơn."),t("br"),t("br"),n._v("“Lưu lão thật không có giải quyết?”"),t("br"),t("br"),n._v("Nhìn thấy Lưu lão quả nhiên nói mình hết cách rồi, Lư Trạm nhớ tới vừa rồi thanh niên cảm khái, nhịn không được con mắt trợn tròn."),t("br"),t("br"),n._v("Mới quen vị này Trương huynh, đến cùng là trong lúc vô tình nói đúng, vẫn là thật có cái này bản lĩnh, có thể một chút nhìn ra Lưu lão trị không hết?"),t("br"),t("br"),n._v("Hắn không phải đối dưỡng dược hoàn toàn không biết gì cả ư?"),t("br"),t("br"),n._v("“Lưu lão không được, ta chỉ sợ cũng không được, có điều, vẫn là có ý định thử một chút!”"),t("br"),t("br"),n._v("Lư lão đi ra."),t("br"),t("br"),n._v("Đứng tại dây leo mạch trước, cẩn thận gõ gõ, lại lột đi một khối da nhìn kỹ một chút, chẳng biết trầm tư bao lâu, cuối cùng cùng Lưu lão đồng dạng, lắc đầu."),t("br"),t("br"),n._v("Có phải hay không chứng bệnh, bọn hắn nhiều năm như vậy cùng dược vật liên hệ, có thể rất mau nhìn đi ra, trước mắt Địa Mạch Linh Đằng xuất hiện chứng bệnh, là bọn hắn chưa từng thấy qua, đương nhiên cũng là không có cách nào trị liệu."),t("br"),t("br"),n._v("Lưu lão, Lư lão hai vị tư cách già nhất đều hết cách rồi, những người khác lần lượt đến đây quan sát, cũng đưa ra không ít đề nghị, có điều, những này đề nghị, đều bị Tiết viên chủ bác bỏ."),t("br"),t("br"),n._v("Bởi vì những thủ đoạn này, đều rất phổ thông, nàng trước kia đều dùng dùng qua, hoàn toàn không có tác dụng."),t("br"),t("br"),n._v("Một canh giờ sau, đi tới cái này viên chủ, dưỡng dược mọi người đã quan sát hơn phân nửa, đều bất lực, những người còn lại, cơ hồ đều là đi cùng bản thân trưởng bối, hoặc là chỉ là tới mở mang kiến thức một chút, tài nghệ chân chính cùng trước mặt mọi người kém quá nhiều, người phía trước đều hết cách rồi, bọn hắn đương nhiên cũng nghĩ không ra tốt hơn phương pháp."),t("br"),t("br"),n._v("“Chư vị còn có biện pháp gì tốt ư?”"),t("br"),t("br"),n._v("Gặp triệu tập nhiều như vậy dược sư, đều không có cách nào giải quyết, Tiết viên chủ thất vọng chi ý càng ngày càng đậm."),t("br"),t("br"),n._v("“Không có!”"),t("br"),t("br"),n._v("“Ta có thể nghĩ tới biện pháp, Tiết viên chủ đều thí nghiệm qua, xem ra quả thực không được!”"),t("br"),t("br"),n._v("“Lưu lão, Lư lão cùng Tiết viên chủ đều hết cách rồi, chúng ta làm sao có thể nghĩ đến phương pháp tốt...”"),t("br"),t("br"),n._v("“Khó trách Tiết viên chủ cho ra lớn như thế thù lao, cái này thù lao, quả thực không phải là cái gì người đều có thể lấy được!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được Tiết viên chủ tra hỏi, mọi người đồng loạt lắc đầu."),t("br"),t("br"),n._v("Phần lớn người đều lên đi tự mình nhìn qua, Địa Mạch Linh Đằng đến loại bệnh tật này, đã vượt ra khỏi có thể lý giải phạm trù, coi như nhiều năm dưỡng dược người, đều không có bất kỳ biện pháp nào."),t("br"),t("br"),n._v("“Vậy được rồi, làm phiền chư vị, phòng khách đã chuẩn bị kỹ càng yến hội, còn xin nhấm nháp xong lại đi...”"),t("br"),t("br"),n._v("Thấy mọi người lại hết cách rồi, Tiết viên chủ lắc đầu."),t("br"),t("br"),n._v("Lần này dùng yến hội danh nghĩa mời người tới, đương nhiên sớm liền chuẩn bị xong đồ ăn rượu ngon."),t("br"),t("br"),n._v("“Không giúp một tay, thực sự áy náy, yến hội liền thôi...”"),t("br"),t("br"),n._v("“Trong nhà còn có việc, liền nên rời đi trước...”"),t("br"),t("br"),n._v("Mặc dù Tiết viên chủ thịnh tình nhưng nhưng, mọi người lại đều vẻ mặt khó coi."),t("br"),t("br"),n._v("Cũng đúng, bọn hắn đều là dưỡng dược danh nhân, mọi người, tới nhiều như vậy, lại ngay cả thực vật nguyên nhân bệnh đều không có điều tra ra, cũng khó trách thẹn thùng."),t("br"),t("br"),n._v("“Cái này gốc Địa Mạch Linh Đằng, bệnh cổ quái, mọi người tìm không ra chứng bệnh, cũng không tính là gì, ta cũng giống vậy không tìm được, mong rằng chư vị lưu lại, ăn xong yến hội lại đi, vừa vặn Tiết mỗ cũng có chuyện muốn nhờ cậy chư vị...”"),t("br"),t("br"),n._v("Thấy mọi người mặt mũi tràn đầy hổ thẹn, Tiết viên chủ biết bọn hắn nghĩ cái gì, an ủi một câu, chính muốn nói tiếp, đột nhiên sửng sốt một cái: “Vị công tử này, ngươi muốn làm gì?”"),t("br"),t("br"),n._v("Nghe được lời của nàng, mọi người đồng loạt nhìn lại, chỉ thấy một thanh niên, chẳng biết lúc nào trong đám người đi ra, đi tới thô to dây leo trước mặt, xòe bàn tay ra ở phía trên nhẹ nhàng sờ soạng một chút."),t("br"),t("br"),n._v("Sờ hết, tại nguyên dạo qua một vòng, không coi ai ra gì, lại liên tục sờ soạng mấy bụi trân quý dược liệu, cái này mới dừng lại."),t("br"),t("br"),n._v("“Làm cái gì vậy?”"),t("br"),t("br"),n._v("Mọi người tất cả đều choáng váng."),t("br"),t("br"),n._v("Dược liệu sinh trưởng cần đặc thù môi trường, thậm chí có chút dược vật, đối với người cực kỳ mẫn cảm, kiêng kỵ nhất đụng vào, bởi vậy, mọi người mặc dù nghiên cứu Địa Mạch Linh Đằng vì cái gì loại chứng bệnh, nhưng cơ bản không có người tới gần."),t("br"),t("br"),n._v("Coi như tiếp xúc, cũng đều mang theo găng tay loại hình."),t("br"),t("br"),n._v("Dù sao, mỗi người tu luyện tu luyện công pháp không giống, làm không cẩn thận liền sẽ cùng dược liệu lên phản ứng, xuất hiện không cách nào tưởng tượng cục diện."),t("br"),t("br"),n._v("Trước kia, liền có một vị dương thuộc tính công pháp cường giả chạm đến một gốc âm thuộc tính dược liệu, dẫn đến sống không bao lâu, liền trực tiếp thối rữa."),t("br"),t("br"),n._v("Đây cũng là vì cái gì dưỡng dược người, đều trong tu luyện cùng công pháp nguyên nhân."),t("br"),t("br"),n._v("Hiện tại, nhiều như vậy dưỡng dược tất cả mọi người tại bên cạnh đứng đấy, quan sát chẩn bệnh triệu chứng, không dám phụ cận, mà cái tên này thế mà vụng trộm chạy vào đi sờ loạn, không kiêng nể gì cả..."),t("br"),t("br"),n._v("Làm gì?"),t("br"),t("br"),n._v("Có hiểu quy củ hay không?"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-530"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-532"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);