(window.webpackJsonp=window.webpackJsonp||[]).push([[1779],{2135:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-523-nguoi-la-heo-u"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-523-nguoi-la-heo-u"}},[n._v("#")]),n._v(" Chương 523: Ngươi là heo ư?")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Mọi người choáng váng, một bên Lư Trạm càng là giật nảy mình, kém chút không ngất ngay tại chỗ."),i("br"),i("br"),n._v("Người khác không nhận ra, hắn mới vừa rồi còn cùng vị này nói chuyện kia mà, thế nào... Một chút không coi chừng, liền chạy dược viên sờ loạn?"),i("br"),i("br"),n._v("Ngươi không hiểu dưỡng dược, có thể hỏi ah, coi như không hỏi, nhìn nhiều người như vậy đều không có di chuyển, chẳng lẽ liền sẽ không muốn muốn chuyện gì xảy ra?"),i("br"),i("br"),n._v("Sờ một chút Địa Mạch Linh Đằng ngược lại cũng thôi, thứ này cực lớn, đừng nói chạm đến, chém lên hai đao cũng sẽ không chết, có thể... Những linh dược kia, là tuyệt đối không thể đụng vào!"),i("br"),i("br"),n._v("Chính mặt mũi tràn đầy sốt ruột, nghĩ đến giúp thế nào bề bộn giải thích, chỉ thấy thanh niên xoay đầu lại, vỗ tay một cái, thần sắc lạnh nhạt nhìn qua."),i("br"),i("br"),n._v("“A, ta muốn thấy nhìn, có thể hay không trị liệu cái này gốc Địa Mạch Linh Đằng!”"),i("br"),i("br"),n._v("“Trị liệu?” Lư Trạm thổ huyết."),i("br"),i("br"),n._v("Của ngươi vị tôn sùng, tứ tinh danh sư cũng vì đó kiêng kị, có thể... Vừa rồi hàn huyên nửa ngày, đối dưỡng dược một chữ cũng không biết, cái đồ chơi này muốn thật sự là tùy tiện sờ sờ là có thể trị liệu, bọn hắn những này dưỡng dược thế gia cũng không cần công việc!"),i("br"),i("br"),n._v("Sờ loạn liền sờ loạn, hiếu kỳ liền nói hiếu kỳ, tại sao phải nói trị liệu?"),i("br"),i("br"),n._v("Chẳng lẽ không thấy được nhiều người như vậy đều bất lực ư?"),i("br"),i("br"),n._v("“Khẩu khí thật lớn!”"),i("br"),i("br"),n._v("“Ngươi là ai a? Ở đây phát ngôn bừa bãi? Nếu là đơn giản như vậy có thể trị liệu, Lưu lão, Lư lão đám người khẳng định đã sớm cho ra đề nghị, còn cần ngươi một tên mao đầu tiểu tử nói lung tung?”"),i("br"),i("br"),n._v("“Cái tên này từ nơi nào xuất hiện? Trần dược chủ, là người của ngươi ư?”"),i("br"),i("br"),n._v("“Không nhận ra...”"),i("br"),i("br"),n._v("Quả nhiên, nghe được hắn, mọi người tất cả đều nghị luận ầm ĩ, từng cái hừ lạnh."),i("br"),i("br"),n._v("Nhiều như vậy dưỡng dược tất cả mọi người không nhìn ra chứng bệnh, ngươi một người hai mươi tuổi không đến gia hỏa, có thể nhìn ra cái gì?"),i("br"),i("br"),n._v("Thật sự là nói khoác không biết ngượng!"),i("br"),i("br"),n._v("Tiết viên chủ cũng là sững sờ, nhướng mày, nghi ngờ nhìn qua: “Ngươi chẳng lẽ nhìn ra cái gì?”"),i("br"),i("br"),n._v("“Là nhìn ra một chút, bất quá... Khả năng còn có chút sự tình, cần Tiết viên chủ phối hợp!”"),i("br"),i("br"),n._v("Thanh niên nói."),i("br"),i("br"),n._v("“Phối hợp? Nói đi, chỉ cần ta có thể làm được, nhất định đi làm!” Tiết viên chủ gật đầu."),i("br"),i("br"),n._v("Mặc dù cùng ý nghĩ của mọi người đồng dạng, đối trước mắt vị này không ôm hy vọng quá lớn, nhưng vẫn là muốn cố gắng một chút."),i("br"),i("br"),n._v("“Cái kia tốt...”"),i("br"),i("br"),n._v("Thanh niên có chút xấu hổ, xấu hổ cười một tiếng: “Ngươi có thể hay không... Đánh hai quyền để ta xem một chút ư?”"),i("br"),i("br"),n._v("“Đánh quyền?”"),i("br"),i("br"),n._v("Tiết viên chủ ngẩn ngơ."),i("br"),i("br"),n._v("Những người khác cũng sững sờ tại nguyên chỗ."),i("br"),i("br"),n._v("Vốn cho rằng sẽ có yêu cầu gì, không nghĩ tới để Tiết viên chủ đánh quyền!"),i("br"),i("br"),n._v("Ngươi không phải đến xem Địa Mạch Linh Đằng sao? Đánh quyền làm gì?"),i("br"),i("br"),n._v("Thế nào nghe cùng khỉ làm xiếc đồng dạng... Đánh hai quyền, ta ném quả đào cho ngươi..."),i("br"),i("br"),n._v("“Làm càn! Dám cầm viên chủ tiêu khiển?”"),i("br"),i("br"),n._v("“Tự tìm cái chết!”"),i("br"),i("br"),n._v("Rầm rầm!"),i("br"),i("br"),n._v("Mấy cái dưỡng dược thế gia con em trẻ tuổi, xông tới."),i("br"),i("br"),n._v("Tiết viên chủ là toàn bộ Vạn Quốc thành đều nổi danh đại nhân vật, càng là đệ nhất dược vườn viên chủ, mở lời để nàng đánh hai quyền, cùng đùa giỡn giống như... Ngươi đây là có ý khiêu khích chứ?"),i("br"),i("br"),n._v("“Chư vị không vội, khẳng định có chỗ hiểu lầm...”"),i("br"),i("br"),n._v("Lư Trạm cũng nhịn không được nữa, đi lên phía trước."),i("br"),i("br"),n._v("Vị này Trương huynh, có thể là tứ tinh danh sư, đừng nói những người này, liền xem như gia gia, cũng không dám đắc tội."),i("br"),i("br"),n._v("“Hiểu lầm? Có thể có hiểu lầm gì đó? Trị liệu Địa Mạch Linh Đằng, cùng Tiết viên chủ đánh quyền có quan hệ gì?” Một thanh niên khẽ nói."),i("br"),i("br"),n._v("“Cái này...” Lư Trạm cũng không rõ ràng cho lắm, nhịn không được nhìn về phía thanh niên trước mắt: “Trương sư, đây rốt cuộc chuyện gì xảy ra...”"),i("br"),i("br"),n._v("“A, ta đã nhìn ra cái này gốc Địa Mạch Linh Đằng xuất hiện chứng bệnh, muốn xác nhận một chút, Tiết viên chủ chỉ cần đánh hai quyền để ta xem một chút là được!” Thanh niên nói."),i("br"),i("br"),n._v("Người này dĩ nhiên chính là Trương Huyền."),i("br"),i("br"),n._v("Minh Lý Chi Nhãn nghiên cứu hồi lâu, cho ra cùng mọi người đồng dạng kết luận, cái này gốc linh đằng không bệnh."),i("br"),i("br"),n._v("Rơi vào đường cùng, lúc này mới va đụng một cái, thông qua thư viện, quả nhiên để hắn nhìn ra không đúng."),i("br"),i("br"),n._v("Ngay sau đó lại liên tục sờ soạng mấy bụi dược liệu, phát hiện cũng có tương đồng tình huống."),i("br"),i("br"),n._v("Trong lòng toát ra một cái suy đoán, lúc này mới như yêu cầu này."),i("br"),i("br"),n._v("“Trương sư?”"),i("br"),i("br"),n._v("Nghe được xưng hô, mọi người tất cả đều nhướng mày."),i("br"),i("br"),n._v("“Đúng, vị này Trương huynh là một vị danh sư, đã hắn làm như vậy, khẳng định có chính mình đạo lý...” Lư Trạm vội nói."),i("br"),i("br"),n._v("“Danh sư?” Mọi người sững sờ, đồng thời nhìn qua."),i("br"),i("br"),n._v("Nếu là danh sư, băn khoăn thân phận, cũng không khả năng ăn nói lung tung, hồ ngôn loạn ngữ đi!"),i("br"),i("br"),n._v("Hơn nữa danh sư, nhìn người thi triển võ kỹ, là chuyện thường xảy ra, chỉ là... Đều có chút không rõ, cái này cùng Địa Mạch Linh Đằng sinh bệnh có quan hệ gì?"),i("br"),i("br"),n._v("“Tốt!” Biết đối phương là danh sư, Tiết viên chủ mặc dù cũng tò mò mục đích của đối phương, vẫn gật đầu."),i("br"),i("br"),n._v("Trong cơ thể khí tức cổ đãng, chân khí ngang dọc, một quyền đánh ra."),i("br"),i("br"),n._v("Đôm đốp!"),i("br"),i("br"),n._v("Không khí tại nàng một quyền bên dưới phát ra kịch liệt âm bạo, tựa như sấm rền, mọi người chung quanh chỉ cảm thấy gió táp đập vào mặt, kém chút bị thổi lật."),i("br"),i("br"),n._v("Tuy chỉ là rất phổ thông một quyền, nhưng điều động thiên địa tự nhiên lực lượng, đem không khí đều có thể áp súc khí bạo, chỉ một điểm này, vô số Hóa Phàm tứ trọng cường giả đỉnh phong đều làm không được."),i("br"),i("br"),n._v("Khó trách có thể để cho Triệu minh chủ kiêng kỵ như vậy, vị này Tiết viên chủ thực lực, quả thực đáng sợ!"),i("br"),i("br"),n._v("“Như vậy có thể chứ?”"),i("br"),i("br"),n._v("Một quyền đánh xong, Tiết viên chủ thu quyền mà đứng, an tĩnh nhìn qua."),i("br"),n._v("“Ừm!” Trương Huyền lên tiếng, giống như là xác định chuyện gì, nhịn không được lắc đầu, lần nữa nhìn qua, trên mặt không nói ra được đồng tình: “Ngươi Địa Mạch Linh Đằng mấu chốt cùng vấn đề, ta đã hoàn toàn biết! Bất quá, trước đó, chỉ muốn hỏi ngươi một câu...”"),i("br"),i("br"),n._v("Tiết viên chủ đám người tất cả đều đồng loạt nhìn qua."),i("br"),i("br"),n._v("Bọn hắn nhiều người như vậy nghiên cứu lâu như vậy, cũng không biết, trước mắt vị danh sư này, chỉ nhìn thoáng qua viên chủ đánh quyền, liền xác định được..."),i("br"),i("br"),n._v("Thật hay giả?"),i("br"),i("br"),n._v("Tất cả đều dựng lên lỗ tai, sợ lọt cái gì, ngay sau đó liền nghe đến thanh niên mang theo than thở tiếng âm vang lên: “Ngươi... Là heo ư?”"),i("br"),i("br"),n._v("Phốc!"),i("br"),i("br"),n._v("Tiết viên chủ nhoáng một cái, kém chút nổ tung."),i("br"),i("br"),n._v("“Ngươi nói cái gì?”"),i("br"),i("br"),n._v("Ầm ầm!"),i("br"),i("br"),n._v("Khí tức trên thân tựa như nhen lửa dầu nóng, bất cứ lúc nào cũng sẽ phun ra ngoài."),i("br"),i("br"),n._v("Nàng là Vạn Quốc thành nổi danh cường giả, thành tây dược viên viên chủ, coi như ngươi là danh sư, trực tiếp mở miệng vũ nhục, cũng không được!"),i("br"),i("br"),n._v("“Hắn nói Tiết viên chủ là heo?”"),i("br"),i("br"),n._v("“Không phải nói danh sư rất đáng tin cậy, không có khả năng ăn nói lung tung, hồ ngôn loạn ngữ ư?”"),i("br"),i("br"),n._v("“Ta cũng không biết...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Một bên vây xem những người khác, cũng từng người trợn to hai mắt, kém chút không điên mất."),i("br"),i("br"),n._v("Trực tiếp mắng Tiết viên chủ là heo, cái tên này lá gan rất lớn, quả thực có thể nói vô pháp vô thiên tới cực điểm."),i("br"),i("br"),n._v("Lư Trạm cũng thiếu chút không cắn đầu lưỡi."),i("br"),i("br"),n._v("Vừa rồi sờ loạn loạn đụng, nói ngươi là danh sư, còn có thể viên hồi đến, hiện tại ở trước mặt mắng lên, thế nào hình cầu?"),i("br"),i("br"),n._v("Sớm biết như thế hổ, liền không nhận ra hắn."),i("br"),i("br"),n._v("Cái này được rồi, trêu đến Tiết viên chủ giận dữ, về sau cũng không biết viên chủ có thể hay không trách tội đến Lư gia trên người..."),i("br"),i("br"),n._v("Đi theo đám người đằng sau, dự định tùy thời đem linh thạch trung phẩm đổi lại Tống Siêu, cũng là cứng đờ."),i("br"),i("br"),n._v("Hắn là tứ tinh danh sư, nhãn lực cực giai, Tiết viên chủ mặc dù chỉ phô bày một chiêu, nhưng có thể nhìn ra thực lực siêu cường, chiến lực vô cùng, đừng nói hắn, coi như tông môn trưởng lão, có thể thắng được cũng không nhiều!"),i("br"),i("br"),n._v("Nói mạnh như thế người là heo..."),i("br"),i("br"),n._v("Cái này Trương sư lá gan còn thật là lớn."),i("br"),i("br"),n._v("Mọi người biểu lộ không giống nhau, chẳng qua tất cả đều cảm thấy kẻ trước mắt này có phải điên rồi hay không."),i("br"),i("br"),n._v("“Không cần phải gấp, hãy nghe ta nói hết!”"),i("br"),i("br"),n._v("Gặp Tiết viên chủ khóe mắt, bất cứ lúc nào cũng sẽ động thủ, Trương Huyền đồng thời không vội vã, mà là thần sắc lạnh nhạt khoát tay áo, một chỉ Địa Mạch Linh Đằng cách đó không xa chỗ trống: “Nếu như ta không nhìn lầm, ngươi hẳn là thường xuyên ở đây tu luyện chứ?”"),i("br"),i("br"),n._v("“Không sai!”"),i("br"),i("br"),n._v("Tiết viên chủ gật đầu, chẳng qua lửa giận chưa tiêu, dường như nếu không phải là bởi vì đối phương là danh sư thân phận, khẳng định sớm một cái tát đánh chết rồi."),i("br"),i("br"),n._v("Trong dược điền, bởi vì có vô số linh dược, ở đây tu luyện, tâm thần yên tĩnh, lại càng dễ đột phá."),i("br"),i("br"),n._v("“Ngươi ý tứ, Địa Mạch Linh Đằng xuất hiện loại tình huống này, cùng ta tu luyện có quan hệ?”"),i("br"),i("br"),n._v("Lông mày nhíu một cái, Tiết viên chủ khẽ nói."),i("br"),i("br"),n._v("“Đâu chỉ có quan hệ! Quả thực chính là thật to liên quan!” Trương Huyền lắc đầu: “Ta hiện tại nói cho ngươi nguyên nhân bệnh đi, cái này gốc Địa Mạch Linh Đằng, kỳ thật không bệnh, cũng không có chịu đến sâu bệnh, càng không có chết héo loại hình xâm nhập, mà là... Đã mất đi linh tính cùng hồn phách!”"),i("br"),i("br"),n._v("“Linh tính hồn phách?”"),i("br"),i("br"),n._v("Tất cả mọi người sững sờ tại nguyên chỗ."),i("br"),i("br"),n._v("“Vạn vật có linh, Linh cấp dược liệu, cùng linh binh đồng dạng, cũng có thuộc về mình linh tính. Địa Mạch Linh Đằng, làm làm dược viên chí bảo, nếu như không có loại này đặc thù, làm sao có thể đem mỗi một gốc dược liệu cần vật phẩm phân phối đi qua?”"),i("br"),i("br"),n._v("Trương Huyền nói."),i("br"),i("br"),n._v("Mọi người gật đầu."),i("br"),i("br"),n._v("Sinh mệnh cấp độ đạt tới Linh cấp, không chỉ vũ khí có linh, thực vật, dược liệu cũng có."),i("br"),i("br"),n._v("Cái gọi là linh dược, như vậy mà tới."),i("br"),i("br"),n._v("Chính là bởi vì loại này linh tính, mới có thể đối chân khí không phù hợp người tu luyện sinh ra bài xích, thậm chí dẫn đến cái chết, mới có thể nghe được ca múa mới có thể phát triển, mới có thể phân biệt ra tấm thân xử nữ..."),i("br"),i("br"),n._v("“Không có linh tính, Địa Mạch Linh Đằng liền cùng mất đi thứ trọng yếu nhất, tự nhiên sẽ mang theo tử khí, bất cứ lúc nào cũng sẽ tử vong.”"),i("br"),i("br"),n._v("“Linh dược đã mất đi linh tính, cùng không có người linh hồn đồng dạng, rất chết nhanh mất đây là sự thực, có thể... Cùng Tiết viên chủ tu luyện có quan hệ gì?”"),i("br"),i("br"),n._v("Mọi người nghi hoặc."),i("br"),i("br"),n._v("“Nếu như ta không nhìn lầm, Tiết viên chủ vây ở Hóa Phàm tứ trọng Trọc Thanh cảnh, chí ít thất, tám mươi năm đi!” Không trả lời tra hỏi, Trương Huyền nhìn qua."),i("br"),i("br"),n._v("“Tám mươi bảy năm!” Tiết viên chủ gật đầu."),i("br"),i("br"),n._v("“Tám mươi bảy năm đều bị nhốt tại chỗ, muốn không đột phá, cái kia là giả! Nhưng Hóa Phàm ngũ trọng Hợp Linh cảnh, là Hóa Phàm cửu trọng bên trong, cái thứ nhất cửa ải khó, cần linh hồn hoàn toàn dung hợp tự nhiên, làm đến Hợp Linh như một, mới có thể đột phá! Tiết viên chủ một mực không thành công, cũng không phải là thiên phú không tốt, mà là linh hồn... Nhận qua tổn thương!”"),i("br"),i("br"),n._v("Trương Huyền nói."),i("br"),i("br"),n._v("“Ngươi, ngươi... Làm sao biết?” Tiết viên chủ sững sờ, toàn thân cứng ngắc."),i("br"),i("br"),n._v("Vừa rồi nàng còn mười phần tức giận, dự định đem cái tên này chém giết, nghe nói như thế, trên người tích súc chân khí tiêu tán, không nói ra được khiếp sợ."),i("br"),i("br"),n._v("Nàng đích xác linh hồn nhận qua tổn thương, nếu không, nhờ vào thiên phú, khẳng định sớm đã đột phá, làm sao đến mức tám mươi bảy năm bị nhốt tại chỗ!"),i("br"),i("br"),n._v("Chỉ là... Chuyện này, là nàng cơ mật, chưa hề cùng người nói qua, trước mắt vị này, làm thế nào nhìn ra được tới?"),i("br"),i("br"),n._v("“Đừng quản ta như thế nào biết được! Ngươi biết linh hồn bị hao tổn, không cách nào đột phá, lại không giống những người khác đồng dạng nản chí, mà là hi vọng thông qua thủ đoạn khác để đền bù vết thương!”"),i("br"),i("br"),n._v("“Không biết từ nơi nào lấy được tin tức, nghe nói linh dược bên trong linh tính có thể bổ sung, ngay sau đó liền lấy dươc viên này, nuôi dưỡng dược liệu, không vì bán ra, chỉ vì hấp thu trong đó linh tính, bổ sung linh hồn của mình!”"),i("br"),i("br"),n._v("“Ý tưởng tuy tốt, đáng tiếc vừa bắt đầu liền đi nhầm phương hướng! Thực vật linh tính nếu như có thể bổ sung người... Chẳng phải người người đều có thể tu luyện đến Hợp Linh cảnh?”"),i("br"),i("br"),n._v("Trương Huyền an tĩnh nhìn qua, ánh mắt bên trong mang theo chỉ tiếc rèn sắt không thành thép: “Thân là dưỡng dược đại tông sư, nương theo linh dược lâu như vậy, đều không nhìn thấu điểm ấy...”"),i("br"),i("br"),n._v("“Không phải heo... Là cái gì?”"),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-531"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-533"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);