(window.webpackJsonp=window.webpackJsonp||[]).push([[1781],{2139:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-525-tinh-hon-thao-đao-tao-thanh-cong"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-525-tinh-hon-thao-đao-tao-thanh-cong"}},[n._v("#")]),n._v(" Chương 525: Tỉnh Hồn thảo đào tạo thành công!")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Chương 525: Tỉnh Hồn thảo đào tạo thành công!"),i("br"),i("br"),n._v("“Trầm Tình hoa? Tốt!” Tiết viên chủ gật đầu, ngay sau đó vẻ mặt có chút xấu hổ: “Linh thạch trung phẩm, ta chỗ này chỉ có một cái...”"),i("br"),i("br"),n._v("Trầm Tình hoa nàng dươc viên này quả thực có mấy bụi, niên đại lớn nhất đã đạt tới bảy trăm năm, có thể lấy ra, chỉ là... Linh thạch trung phẩm, coi như nàng là Hóa Phàm tứ trọng cường giả đỉnh phong, cũng chỉ có một cái."),i("br"),i("br"),n._v("Loại cấp bậc này linh thạch, chỉ có Huyễn Vũ đế quốc mới có tư cách lưu thông, vạn quốc liên minh lưu thông liền hạ phẩm linh thạch mà thôi, toàn bộ vương thất đều không bỏ ra nổi mấy cái, chớ nói chi là nàng."),i("br"),i("br"),n._v("“Chỉ có một cái? Một cái cũng tốt!” Trương Huyền gật đầu."),i("br"),i("br"),n._v("Đòi Trầm Tình hoa, là vì bồi dưỡng Tỉnh Hồn thảo, thuộc về tư tâm, linh thạch trung phẩm lại không phải."),i("br"),i("br"),n._v("Muốn cho nhỏ yếu thực vật linh tính nhanh chóng tăng trưởng, linh thạch trung phẩm tinh thuần linh khí, ắt không thể thiếu, chỉ bằng vào hạ phẩm linh thạch thôi động, thời gian chỉ sợ cũng phải kéo cực kỳ dài."),i("br"),i("br"),n._v("“Ta hiện tại liền chuẩn bị!” Tiết viên chủ vội vã quay người rời đi."),i("br"),i("br"),n._v("Thời gian không dài, đi trở về."),i("br"),i("br"),n._v("Trương Huyền nói đồ vật, đối người khác mà nói, muốn muốn tìm đủ, khó càng thêm khó, không có mấy tháng công phu, gần như không có khả năng, nhưng làm Vạn Quốc thành đệ nhất dược vườn, cần thiết dược liệu, bên trong vườn cơ bản đều có, tìm ra được liền đơn giản."),i("br"),i("br"),n._v("Thậm chí liền một gốc Địa Mạch Linh Đằng mầm non cũng tìm tới."),i("br"),i("br"),n._v("“Ta trị liệu cần tuyệt đối thanh tịnh, còn xin Tiết viên chủ đem mọi người mang đi ra ngoài!”"),i("br"),i("br"),n._v("Nhìn thấy đồ vật chuẩn bị đầy đủ, Trương Huyền khoát tay áo."),i("br"),i("br"),n._v("Chiết cây linh tính, cần vu hồn sư thủ đoạn, mặc dù loại nghề nghiệp này đã thất truyền, coi như thi triển, có thể nhận ra cũng không nhiều, nhưng vẫn là cẩn thận cho thỏa đáng."),i("br"),i("br"),n._v("“Đúng!”"),i("br"),i("br"),n._v("Tiết viên chủ cũng biết rất nhiều thủ đoạn vì độc nhất vô nhị chi bí, không ít cường giả là không nguyện ý biểu hiện ra cho người khác nhìn, lúc này gật gật đầu, mang theo mọi người quay người rời khỏi dược điền."),i("br"),i("br"),n._v("Thấy mọi người lộ hàng, Trương Huyền lúc này mới thở ra một hơi, nhìn trước mắt linh đằng mầm non, tinh thần khẽ động, Vu hồn xuất thể, bắt đầu trị liệu."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("“Trương sư thế nào đi lâu như vậy, còn chưa có đi ra? Ngươi nói Trầm Tình hoa hắn có thể mua được ư?”"),i("br"),i("br"),n._v("Trong phòng khách, Triệu Phi Vũ, Kim Tòng Hải qua lại loạn chuyển, thỉnh thoảng hướng nhìn ra ngoài."),i("br"),i("br"),n._v("Chỉ có dưỡng dược người hoặc là danh sư mới có thể đi vào dược điền, bọn hắn xem như tùy tùng, là không cách nào tiến vào, chỉ có thể ở nơi này khô chờ lấy."),i("br"),i("br"),n._v("Ai ngờ chờ đợi ròng rã hai, ba canh giờ, hiện tại trời đã đại hắc, còn không có gặp người đi ra, trong lòng tràn đầy sốt ruột."),i("br"),i("br"),n._v("“Hẳn là có thể chứ... Hắn nhưng là Trương sư!” Kim Tòng Hải chần chờ một chút, nói."),i("br"),i("br"),n._v("Từ khi biết vị thanh niên này đến nay, bất kể chuyện gì, chỉ cần hắn đi làm, đều có thể tuỳ tiện hoàn thành, để cho người ta đều có chút hoài nghi, cái tên này còn có cái gì sẽ không."),i("br"),i("br"),n._v("“Trương sư năng lực ta biết, chỉ là... Tiết viên chủ tính cách cổ quái ta đã sớm nghe nói, liền phụ vương mặt mũi cũng không cho, hiện tại Địa Mạch Linh Đằng lại xảy ra vấn đề, khẳng định càng thêm nôn nóng, lúc này muốn tại nàng nơi này mua thuốc, tuyệt sẽ không dễ dàng, ta sợ... Trương sư mới mở miệng, đối phương không những không đồng ý, sẽ còn đem hắn đuổi đi ra!”"),i("br"),i("br"),n._v("Mặc dù bội phục Trương sư bản lĩnh, nhưng Triệu Phi Vũ vẫn còn có chút lo lắng."),i("br"),i("br"),n._v("Vị này Tiết viên chủ, thế nhưng là liền phụ vương hắn, vạn quốc liên minh minh chủ đều không để vào mắt người, tâm tình tốt, còn dễ nói, trả giá đầy đủ đánh đổi, lại nói tốt hơn lời nói, có lẽ còn có thể mua được, nhưng bây giờ... Địa Mạch Linh Đằng nửa chết nửa sống, tâm tình chìm vào đáy cốc, mua thuốc độ khó, sợ rằng sẽ khó hơn gấp bội."),i("br"),i("br"),n._v("Liền sợ Trương sư lời nói đều không có nói, đối phương liền phái người đuổi ra ngoài."),i("br"),i("br"),n._v("Loại sự tình này, trước kia cũng không phải chưa từng xảy ra."),i("br"),i("br"),n._v("Minh chủ phủ sứ giả, chính là như vậy bị đuổi đi."),i("br"),i("br"),n._v("“Cái này... Thật có khả năng...”"),i("br"),i("br"),n._v("Kim Tòng Hải sững sờ, cũng lộ ra lo lắng."),i("br"),i("br"),n._v("“Nhìn Trương sư ý tứ đối Trầm Tình hoa tình thế bắt buộc, thực sự không được, vẫn là trở về kính xin phụ vương ra mặt đi, hắn ra mặt, Tiết viên chủ hẳn là... Không đến mức cự tuyệt!”"),i("br"),i("br"),n._v("Lại đợi một hồi, thấy còn không có động tĩnh, Triệu Phi Vũ nhịn không được nói."),i("br"),i("br"),n._v("Nói ra cuối cùng, liền nàng cũng không có quá lớn tự tin."),i("br"),i("br"),n._v("Nếu như phụ vương hắn mặt mũi có dùng, lúc trước sứ giả liền sẽ không bị đuổi ra ngoài."),i("br"),i("br"),n._v("Tiết viên chủ tự kiềm chế thực lực cao cường, khó chơi, coi như nàng rất có mưu trí, cũng cảm thấy đầu đau nhức, không có biện pháp nào."),i("br"),i("br"),n._v("Thực lực tuyệt đối trước mặt, hết thảy mưu lược đều là vô dụng."),i("br"),i("br"),n._v("“Chỉ có thể như thế... Mau nhìn, có người tới!” Kim Tòng Hải gật gật đầu, cũng nghĩ không ra biện pháp tốt, chính muốn tiếp tục nói chuyện, đột nhiên sững sờ, chỉ về phía trước."),i("br"),i("br"),n._v("Bên ngoài phòng khách mặt, một bóng người đi tới."),i("br"),i("br"),n._v("“Là Trương sư...”"),i("br"),i("br"),n._v("Chính là trước kia đi tới Trương Huyền."),i("br"),i("br"),n._v("“Không cần suy nghĩ, khẳng định là bị đuổi ra ngoài...”"),i("br"),i("br"),n._v("Thấy hắn một người xuất hiện, Triệu Phi Vũ cười khổ một tiếng, cùng Kim Tòng Hải hai người nóng nảy vội vàng nghênh đón."),i("br"),i("br"),n._v("“Trương sư, ngươi đi ra, không có mua đến Trầm Tình hoa không sao, trở về ta liền bẩm báo phụ vương, để hắn ra mặt, giúp ngươi mua sắm...”"),i("br"),i("br"),n._v("Đi tới trước mặt, thấy thanh niên vẻ mặt hơi có vẻ trắng xanh, có vẻ uể oải, hẳn là thụ khuất nhục, Triệu Phi Vũ vội vàng an ủi một câu."),i("br"),i("br"),n._v("“Đúng vậy a! Tiết viên chủ tính cách cổ quái, luôn luôn nổi danh, ngươi đừng nóng giận...” Kim Tòng Hải cũng vội vàng nói."),i("br"),i("br"),n._v("“Ách?”"),i("br"),i("br"),n._v("Vừa về đến còn chưa kịp nói chuyện, liền bị hai người an ủi, Trương Huyền dở khóc dở cười, đang muốn giải thích, liền nghe đến phía sau một hồi tiếng bước chân dồn dập truyền đến."),i("br"),i("br"),n._v("Tiết viên chủ cùng những người khác vội vã đi tới."),i("br"),i("br"),n._v("“Các ngươi muốn làm gì?” Thấy những người này mặt mũi tràn đầy sốt ruột, Kim Tòng Hải vội vàng ngăn tại Trương Huyền cùng công chúa trước mặt."),i("br"),n._v(" “Trương sư, trong này có một trăm gốc năm khoảng trăm năm dược liệu, là của ta một chút tâm ý. Hơn nữa, từ hôm nay trở đi, ta thành tây dược viên, là bằng hữu vĩnh viễn của ngươi, chỉ cần Trương sư cần, theo liền mở miệng, ta chỗ này có thể xuất ra dược vật, trực tiếp miễn phí cung cấp!”"),i("br"),i("br"),n._v("Không để ý tới tràn đầy khẩn trương Kim Tòng Hải, đi tới Trương Huyền trước mặt, Tiết viên chủ đột nhiên khom người bái thật sâu, đưa tới một cái trữ vật giới chỉ, nói."),i("br"),i("br"),n._v("“Vĩnh viễn bằng hữu?”"),i("br"),i("br"),n._v("“Miễn phí cung cấp?”"),i("br"),i("br"),n._v("Nhìn thấy tính cách cổ quái, liên minh chủ phủ mặt mũi cũng không cho Tiết viên chủ, vừa đến liền cúi đầu, còn đưa vật, Triệu Phi Vũ, Kim Tòng Hải con mắt đồng thời trợn tròn."),i("br"),i("br"),n._v("Đến cùng xảy ra chuyện gì..."),i("br"),i("br"),n._v("Cái tên này không phải khó chơi sao? Thế nào... Đột nhiên cùng một cái học sinh giống như lại là cúi đầu, lại là đưa?"),i("br"),i("br"),n._v("“Cùng Trương sư so sánh, chúng ta dưỡng dược thủ đoạn, nhỏ yếu đáng thương!”"),i("br"),i("br"),n._v("“Nhờ có Trương sư, nếu không, cái này gốc Địa Mạch Linh Đằng, lại không hi vọng phục sinh!”"),i("br"),i("br"),n._v("Chính kỳ quái đến cùng chuyện gì xảy ra, liền nghe đến đám người thảo luận tiếng âm vang lên."),i("br"),i("br"),n._v("“Trương sư... Đem gốc kia Địa Mạch Linh Đằng trị liệu được rồi?”"),i("br"),i("br"),n._v("Hiểu được, hai người nhìn nhau, đồng thời phát điên."),i("br"),i("br"),n._v("Vừa rồi Trương sư cùng Lư Trạm nói chuyện phiếm bọn hắn đều nghe được, một cái liền dưỡng dược là cái gì cũng không biết gia hỏa, đi qua chữa khỏi dưỡng dược thế gia không cách nào chữa trị Địa Mạch Linh Đằng..."),i("br"),i("br"),n._v("Thế nào đều cảm giác chuyện này như thế không đáng tin cậy đây?"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("“Trương sư, không chỉ mua được Trầm Tình hoa, còn để Tiết viên chủ tự mình đưa ra dược viên, thậm chí công bố về sau tất cả dược liệu miễn phí cung cấp... Càng là chữa khỏi Địa Mạch Linh Đằng?”"),i("br"),i("br"),n._v("Minh chủ phủ, Triệu minh chủ trừng to mắt, nghe nữ nhi cùng Kim Tòng Hải báo cáo, khiếp sợ trong lòng, thật lâu không thể dẹp loạn..."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Khoanh chân ngồi tại gian phòng, Trương Huyền chằm chằm lên trước mắt sâu đóa hoa màu đỏ."),i("br"),i("br"),n._v("Bảy trăm năm phần mới mẻ Trầm Tình hoa!"),i("br"),i("br"),n._v("Địa Mạch Linh Đằng, có đầy đủ dược vật cùng vu hồn sư thủ đoạn, chữa khỏi không tính là quá khó, thông qua chiết cây, để dây leo một lần nữa có linh tính, Tiết viên chủ tự nhiên thiên ân vạn tạ."),i("br"),i("br"),n._v("Lần này đi dược viên mục đích chủ yếu chính là Trầm Tình hoa, vật tới tay, lại không có bất kỳ dừng lại gì, mang theo Triệu Phi Vũ hai người về tới chuẩn bị cho hắn thật nơi ở."),i("br"),i("br"),n._v("Là một cái không lớn phủ đệ, khoảng cách minh chủ phủ rất gần, hộ vệ, người hầu, nha hoàn đầy đủ mọi thứ, cũng là tiết kiệm Tôn Cường lại đi tìm."),i("br"),i("br"),n._v("Đến tới chỗ, an bài Yêu Thần thú nghỉ ngơi, Trương Huyền liền trực tiếp đi vào gian phòng của mình, đem bụi dược liệu này lấy ra ngoài."),i("br"),i("br"),n._v("“Dẫn hồn xuất thể!”"),i("br"),i("br"),n._v("Tinh thần khẽ động, một cái rõ ràng hồn phách từ huyệt đạo phun ra ngoài, Trương Huyền vẫy tay một cái, thân thể trước mặt đóa hoa đi tới trước mặt."),i("br"),i("br"),n._v("“Tỉnh Hồn thảo, là tại có thể ẩn chứa linh hồn dấu ấn thực vật bên trên điêu khắc, tụ tập linh hồn trận pháp... Sau đó dùng linh hồn cung cấp lực lượng, để nó nhanh chóng tăng trưởng...”"),i("br"),i("br"),n._v("Đem Tỉnh Hồn thảo bồi dưỡng phương pháp tại trong óc chuyển một lần, Trương Huyền con mắt rơi ở trước mắt trên đóa hoa."),i("br"),i("br"),n._v("Trầm Tình hoa, mặc dù thuộc về linh dược, nhưng không có bản thân linh tính, bởi vậy có thể tuỳ tiện dung nạp vu hồn sư lực lượng linh hồn."),i("br"),i("br"),n._v("Nếu không, đổi lại có linh tính dược vật, nó linh hồn hắn một khi đụng vào, lập tức tán loạn, cũng không có tỉnh hồn hiệu quả."),i("br"),i("br"),n._v("Hô!"),i("br"),i("br"),n._v("Hồn lực cổn động, một cổ lực lượng cường đại đem trước mắt đóa hoa bao vây, cả đóa hoa chậm chạp cải biến bộ dáng cùng hình thái."),i("br"),i("br"),n._v("Tu luyện xong Vu hồn Thiên Đạo công pháp, hắn chỉ bằng vào lực lượng linh hồn, đã đạt tới Hóa Phàm nhị trọng đỉnh phong, so tu vi chân khí còn mạnh hơn rất nhiều, bồi dưỡng Tỉnh Hồn thảo phương pháp mặc dù không dễ dàng, nhưng bằng mượn hắn hiện tại hồn lực, vẫn là có thể tuỳ tiện làm được."),i("br"),i("br"),n._v("Liên tục ba ngày, chân không bước ra khỏi nhà."),i("br"),i("br"),n._v("Rốt cục ngày thứ tư thời điểm, trước mắt nghĩ tươi đóa hoa màu đỏ, “Ông!” Một tiếng, nhẹ nhàng nhoáng một cái, biến thành một gốc trắng noãn mầm non."),i("br"),i("br"),n._v("Tỉnh Hồn thảo đào tạo thành công!"),i("br"),i("br"),n._v("“Về sau mỗi ngày dùng linh hồn tẩm bổ, một, hai tháng nên có thể hoàn thiện!”"),i("br"),i("br"),n._v("Trương Huyền hài lòng gật đầu."),i("br"),i("br"),n._v("Mặc dù đem Trầm Tình hoa đào tạo thành Tỉnh Hồn thảo, nhưng bây giờ vẫn là mầm non, không thể trực tiếp sử dụng, chẳng qua cũng không vội vã, nhiều nhất một, hai tháng công phu liền có thể đào tạo thành chín."),i("br"),i("br"),n._v("Đến lúc đó, chế tác thành Tỉnh Hồn hương, nên có thể cứu sống Lộ Xung, giải quyết một cọc tâm nguyện."),i("br"),i("br"),n._v("Ba ngày này một mực trốn ở trong phòng sử dụng hồn lực, để hắn toàn thân cao thấp đều có chút mệt mỏi."),i("br"),i("br"),n._v("Linh hồn quy vị, lấy ra từ Tống Siêu trong tay lừa gạt tới linh thạch trung phẩm, hấp thu một hồi, triệt để khôi phục, cái này mới ra khỏi phòng."),i("br"),i("br"),n._v("Bên ngoài mặt trời chói chang, còn chưa đi vào sân nhỏ, chỉ thấy Tôn Cường đi tới."),i("br"),i("br"),n._v("“Thiếu gia, Tô sư cùng Lăng sư tới, nói muốn gặp ngươi.”"),i("br"),i("br"),n._v("Tô sư Tô Phàm, Lăng sư Lăng Vũ Hằng, chính là lúc trước tại Thiên Vũ vương quốc mời mời hắn tới tham gia danh sư thi đấu hai vị tứ tinh danh sư."),i("br"),i("br"),n._v("“Bọn hắn làm sao biết ta tới nơi này?” Trương Huyền sửng sốt một chút, lập tức lắc đầu."),i("br"),i("br"),n._v("Bản thân đi tới Vạn Quốc thành về sau, không những không có che lấp dấu vết, còn chạy đến thành tây dược viên giải quyết Địa Mạch Linh Đằng vấn đề, Danh Sư đường làm trấn áp một phương tồn tại, tra ra tin tức, đồng thời không kỳ quái."),i("br"),i("br"),n._v("Trước đó nói qua, chỉ cần vừa đến Vạn Quốc thành liền đi tìm bọn họ, giờ phút này tới mấy ngày đều không có đi Danh Sư đường, cũng không ngồi yên được nữa, tìm tới."),i("br"),i("br"),n._v("“Đi qua nhìn một chút!”"),i("br"),i("br"),n._v("Suy nghĩ ra chuyện gì xảy ra, cũng không nhiều lời, Trương Huyền nhấc chân hướng phòng khách phương hướng đi đến."),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-533"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-535"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);