(window.webpackJsonp=window.webpackJsonp||[]).push([[1784],{2138:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-528-3-cai-van-đe"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-528-3-cai-van-đe"}},[n._v("#")]),n._v(" Chương 528: 3 cái vấn đề")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 528: 3 cái vấn đề"),t("br"),t("br"),n._v("Vì phòng ngừa người khác hoài nghi Thiên Đạo chân khí, Trương Huyền không có ẩn giấu tu vi, bởi vậy, chỉ cần là người tu luyện, đều có thể nhìn ra, chỉ có Hóa Phàm nhất trọng đỉnh phong thực lực."),t("br"),t("br"),n._v("Khang đường chủ làm tứ tinh đỉnh phong danh sư, tâm cảnh khắc độ đạt tới 14. 0 trở lên tồn tại, muốn nhìn ra tự nhiên vô cùng đơn giản."),t("br"),t("br"),n._v("Hơn nữa, tu luyện võ kỹ cường giả, mưu tính đều sẽ lưu lại dấu vết, lúc hành tẩu, cũng sẽ lơ đãng thể hiện ra một loại nào đó quen thuộc. Trước mắt người thanh niên này những này đặc thù hoàn toàn không có, ngược lại nhất cử nhất động không có kết cấu gì, cùng không có tu luyện qua đồng dạng qua đồng dạng..."),t("br"),t("br"),n._v("Làm sao có thể là thiên tài?"),t("br"),t("br"),n._v("Danh sư, cùng cấp bậc vô địch, cũng không phải là đơn chỉ có thể nhìn ra chiêu số thiếu hụt, lỗ thủng, càng quan trọng hơn là, bản thân đối chân khí, võ kỹ khống chế cũng đạt tới lô hỏa thuần thanh."),t("br"),t("br"),n._v("Một cái liền võ kỹ đều không có tu luyện thế nào qua gia hỏa, mệnh danh thiên tài, còn muốn đại biểu Vạn Quốc thành Danh Sư đường, tham gia danh sư thi đấu, có chút quá trò đùa đi!"),t("br"),t("br"),n._v("“Trương sư thực lực mặc dù thấp chút, nhưng đối danh sư lĩnh ngộ, đã đạt tới cảnh giới cực sâu...” Nghe đường chủ mới mở miệng, liền tràn đầy nghi ngờ, Tô sư vội vàng giải thích."),t("br"),t("br"),n._v("“Lĩnh ngộ?” Khang đường chủ khuôn mặt trầm xuống: “Tô trưởng lão, chúng ta tuyển bạt chính là người mới, là muốn tham gia danh sư thi đấu, làm Danh Sư đường lão nhân, ngươi hẳn phải biết đối thủ là cái gì, nắm giữ dạng gì năng lực!”"),t("br"),t("br"),n._v("Cũng khó trách hắn sức sống, Tô sư, Lăng sư hai vị trưởng lão, lấy mệnh đảm bảo, tranh thủ cái này danh ngạch, vốn cho rằng là cái bao nhiêu lợi hại thiên tài, không nghĩ tới, tu vi chỉ có Hóa Phàm nhất trọng đỉnh phong, hơn nữa võ kỹ cũng không chút tu luyện... Loại thực lực này, làm sao có thể tại tứ tinh danh sư bên trong trổ hết tài năng?"),t("br"),t("br"),n._v("Danh sư thi đấu, mặc dù khảo hạch cũng không phải là tất cả đều là thực lực, nhưng thực lực cũng là một cái trong đó bộ phận, cảnh giới của mình không đến, lại như thế nào cho người khác chỉ điểm?"),t("br"),t("br"),n._v("Coi như có thể nói ra, cũng là đàm binh trên giấy, giống như bèo trôi không rễ, không có bất kỳ cái gì giá trị tham khảo."),t("br"),t("br"),n._v("“Ta biết, thế nhưng là...” Tô sư vẻ mặt quýnh lên."),t("br"),t("br"),n._v("“Không có thế nhưng là, dẫn đi đi!” Khang đường chủ khoát khoát tay."),t("br"),t("br"),n._v("“Đường chủ đừng có gấp làm quyết định...”"),t("br"),t("br"),n._v("Lăng sư tiến lên một bước: “Trương sư tu vi hiện tại, mặc dù chỉ có Hóa Phàm nhất trọng đỉnh phong, nhưng... Ngươi có biết lần trước chúng ta nhìn thấy thời điểm, là loại nào thực lực?”"),t("br"),t("br"),n._v("“Hơn hai mươi ngày trước?” Khang đường chủ mí mắt vừa nhấc."),t("br"),t("br"),n._v("Hai vị này trưởng lão, là một tháng trước trái phải đi xuống, trở về cũng chỉ hơn mười ngày, theo thời gian suy tính, về khoảng cách lần nhìn thấy trước mắt vị này, cũng chỉ hơn hai mươi ngày thời gian."),t("br"),t("br"),n._v("“Ừm!” Lăng sư gật đầu."),t("br"),t("br"),n._v("“Loại thực lực nào?” Khang đường chủ nâng chung trà lên, khẽ nhấp một cái."),t("br"),t("br"),n._v("Hai mươi ngày tới, coi như tu luyện nhanh, từ Hóa Phàm nhất trọng trung kỳ, tu luyện tới đỉnh phong, lại như thế nào?"),t("br"),t("br"),n._v("Khoảng cách danh sư thi đấu, chỉ có hai tháng, lại thiên phú nghịch thiên, xung kích Hóa Phàm nhị trọng là tốt lắm rồi, còn có thể thế nào?"),t("br"),t("br"),n._v("“Tông sư! Hơn hai mươi ngày trước, lần thứ nhất nhìn thấy Trương sư thời điểm... Hắn mới Tông Sư cảnh!” Lăng sư vội nói."),t("br"),t("br"),n._v("“Phốc!” Một miệng nước trà phun ra, Khang đường chủ con mắt trợn tròn: “Võ giả bát trọng... Tông sư?”"),t("br"),t("br"),n._v("Mở cái gì trò đùa!"),t("br"),t("br"),n._v("Hai mươi ngày tới thời gian, từ Tông sư đột phá Chí Tôn, sau đó nửa bước Hóa Phàm, Hóa Phàm nhất trọng... Càng là đạt tới đỉnh phong..."),t("br"),t("br"),n._v("Liên tiếp ba cái đại cấp bậc, vô số cái tiểu cấp đừng..."),t("br"),t("br"),n._v("Ăn thuốc nổ cũng không có nhanh như vậy đi!"),t("br"),t("br"),n._v("Kỳ thật không chỉ hắn không tin, Lăng sư, Tô sư, nhìn thấy Trương Huyền thực lực đạt tới Hóa Phàm nhất trọng, cũng là lòng tràn đầy sụp đổ."),t("br"),t("br"),n._v("Bất quá, biết đối phương lão sư có thể là lục tinh trở lên danh sư lúc, lúc này mới tâm lý hơi thăng bằng chút."),t("br"),t("br"),n._v("Dương sư loại này cấp bậc cao danh sư, che giấu thanh danh, không báo tin công hội, tại Thiên Huyền vương quốc loại địa phương nhỏ này thu đồ, rất rõ ràng là không muốn nói cho người khác biết."),t("br"),t("br"),n._v("Xem như vãn bối, không được đến cho phép, tự nhiên không dám truyền ra ngoài."),t("br"),t("br"),n._v("Cho nên, bọn hắn mặc dù mãnh liệt đề cử Trương Huyền, lại không nói qua sư thừa lai lịch, Khang đường chủ cũng là không rõ lắm, lúc này nghe được ngắn ngủi không đến một tháng thời gian, từ Tông Sư cảnh đạt tới Hóa Phàm nhất trọng đỉnh phong... Cả người đều cảm thấy sắp điên rồi."),t("br"),t("br"),n._v("Đây cũng không phải là tu luyện... Mà là quán đỉnh!"),t("br"),t("br"),n._v("Thậm chí, coi như quán đỉnh, cũng không có khoa trương như vậy chứ!"),t("br"),t("br"),n._v("“Chắc chắn 100%!” Lăng sư gật đầu, Tô sư cũng đi theo gật đầu."),t("br"),t("br"),n._v("“Hơn hai mươi ngày, từ Tông Sư cảnh đột phá đến Hóa Phàm nhất trọng đỉnh phong... Chỉ cần còn lại hai tháng, thật tốt tu luyện, đạt tới Hóa Phàm nhị trọng đỉnh phong, thậm chí tam trọng sơ kỳ... Vẫn là có hi vọng cùng những thiên tài kia một trận chiến...”"),t("br"),t("br"),n._v("Biết hai vị trưởng lão nhân phẩm, không có khả năng nói dối, Khang đường chủ trong lòng suy tư."),t("br"),t("br"),n._v("Đến đây tham gia thi đấu tuổi trẻ tài tuấn, cơ bản đều là tứ tinh danh sư."),t("br"),t("br"),n._v("Nhất tinh danh sư, tu vi muốn đạt tới Thông Huyền cảnh; Nhị tinh muốn đạt tới Tông Sư cảnh; Tam tinh thì là Chí Tôn cảnh."),t("br"),t("br"),n._v("Mà tứ tinh danh sư yêu cầu thấp nhất, là Hóa Phàm nhị trọng, chỉ cần đạt tới Nguyên Khí cảnh sơ kỳ, liền có khảo hạch tứ tinh danh sư tư cách."),t("br"),t("br"),n._v("Bởi vậy, thi đấu tuổi trẻ tài tuấn, thực lực cơ bản đều tại nhị trọng Nguyên Khí cảnh cùng tứ trọng Trọc Thanh cảnh trong lúc đó."),t("br"),t("br"),n._v("Vị này Trương sư, tốc độ tu luyện nhanh như vậy, nếu có thể ở thi đấu trước trong hai tháng, đem tu vi đạt tới Hóa Phàm tam trọng sơ kỳ, tại rất nhiều thiên tài bên trong, được cho trung du, có lẽ thật có thể cầm tới thứ tự cũng chưa biết chừng!"),t("br"),t("br"),n._v("“Đã như vậy, cho hắn một cơ hội, hỏi ba cái vấn đề, chỉ cần có thể trả lời đi ra, hơn nữa chính xác, liền có thể tham gia tuyển bạt giải thi đấu. Còn có thể hay không trổ hết tài năng, vậy liền nhìn hắn năng lực!”"),t("br"),t("br"),n._v("Suy tư hoàn tất, Khang đường chủ nhìn lại."),t("br"),t("br"),n._v("“Tốt!” Nghe được có cơ hội, Tô sư, Lăng sư đồng thời nhẹ nhàng thở ra, vội vàng hướng Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Vừa nhìn phía dưới, đồng thời một cái lảo đảo, thiếu chút nữa ngất đi."),t("br"),n._v(" Chỉ thấy vị này Trương sư, căn bản không nghe bọn hắn nói chuyện phiếm, mà là đứng tại bên tường giá sách bên cạnh, ngón tay ở phía trên nhẹ nhàng lướt qua, dường như lại tìm kiếm sách gì."),t("br"),t("br"),n._v("Bọn hắn khẩn trương muốn mạng, sợ không chiếm được danh ngạch, cái tên này ngược lại tốt, căn bản là không có để ý tới..."),t("br"),t("br"),n._v("Trái tim thật là quá lớn đi!"),t("br"),t("br"),n._v("Vì để cho ngươi tham gia thi đấu, hai chúng ta lão gia hỏa mặt đều không thèm đếm xỉa, ngươi nhưng cái gì đều mặc kệ..."),t("br"),t("br"),n._v("Suy nghĩ một chút cũng là say."),t("br"),t("br"),n._v("“Trương sư...” Nhịn không được đi tới trước mặt, Tô sư cắt ngang hắn cử động: “Đường chủ để ngươi trả lời hắn mấy vấn đề, trả lời tốt, liền có thể tham gia tuyển bạt!”"),t("br"),t("br"),n._v("“Ah!” Trương Huyền gật gật đầu, đi tới."),t("br"),t("br"),n._v("Biết Khang đường chủ không thi triển võ kỹ, thi triển sư ngôn thiên bẩm vô dụng, cũng là không nghĩ nhiều nữa, ngắm nhìn chung quanh thời điểm, phát hiện bức tường trên giá sách, thế mà còn có không ít Hóa Phàm nhị trọng tu luyện công pháp, nhịn không được đi qua, đem thu nhập thư viện."),t("br"),t("br"),n._v("Còn mọi người thảo luận, thật đúng là không chút chú ý nghe."),t("br"),t("br"),n._v("Lúc trước tại Hồng Hải thành, Trại các chủ hỗ trợ sưu tập chí ít hơn năm trăm bản Nguyên Khí cảnh công pháp bí tịch, hiện tại trên vách tường cũng có mấy trăm bản, cả hai cộng lại, đã hình thành hoàn chỉnh Thiên Đạo công pháp."),t("br"),t("br"),n._v("Chuyên môn nghiên cứu thứ này thời gian đều không đủ, đâu còn có công phu để ý tới bọn hắn nói cái gì."),t("br"),t("br"),n._v("“Vấn đề của ta đều là liên quan tới tu luyện cùng dạy bảo học sinh, chỉ cần có thể trả lời đi ra, liền cho ngươi một lần tham gia tuyển bạt cơ hội!”"),t("br"),t("br"),n._v("Khang đường chủ nhìn qua, tiện tay đem mới vừa nhìn thư tịch cầm lấy, vẻ mặt nghiêm túc: “Đây là ta trước kia trải qua một cái án lệ!”"),t("br"),t("br"),n._v("“Một vị nửa bước Hóa Phàm cấp bậc người tu luyện, đã từng nhận qua ám thương, bên trái phổi bị xuyên thủng, bởi vậy, bên trái mạch chân khí không cách nào khơi thông, loại tình huống này phải như thế nào chỉ điểm, mới có thể để cho nàng thuận lợi đột phá cảnh giới, xung kích Hóa Phàm thành công?”"),t("br"),t("br"),n._v("Vấn đề này, là hắn tự mình trải qua, lúc trước cũng nghiên cứu hồi lâu tra duyệt không ít sách vở, mới để giải quyết, xem như rất khó."),t("br"),t("br"),n._v("Bất quá, danh sư thi đấu, so cái này khó nhiều, nếu như vấn đề này đều không có biện pháp giải quyết, loại trừ cũng vô dụng, còn không bằng sớm xoạt xuống."),t("br"),t("br"),n._v("Còn vấn đề bản thân, thuộc về Hóa Phàm cảnh trở xuống, không vượt qua đối phương có thể lý giải phạm vi."),t("br"),t("br"),n._v("“Hóa Phàm cảnh, muốn người tu luyện mượt mà như một, mới có thể câu thông tự nhiên, thân cận tự nhiên, khống chế tự nhiên! Bên trái phổi xuyên thủng, bên trái mạch chân khí không cách nào khơi thông... Thì tương đương với một cái vòng tròn bên trên xuất hiện thiếu hụt, còn muốn câu thông đột phá, gần như không có khả năng!”"),t("br"),t("br"),n._v("Nghe được vấn đề này, Tô sư nhướng mày: “Thật là khó!”"),t("br"),t("br"),n._v("“Đúng vậy a, liền xem như ta, muốn giải quyết, cũng cần chí ít tám bước!” Lăng sư cũng gật gật đầu."),t("br"),t("br"),n._v("Bọn hắn là tứ tinh danh sư, vấn đề này tuy khó, muốn giải quyết, vẫn là có thể làm được, chỉ bất quá, cần tốn hao cực lớn mưu tính."),t("br"),t("br"),n._v("“Ta cũng muốn bảy bước!” Tô sư suy tư một chút, nói."),t("br"),t("br"),n._v("“Ngươi tiểu Thuần Dương công, tu luyện tới thứ bảy chuyển, thực lực cường thịnh, so ta thiếu một bước bình thường. Bất quá, theo ta được biết, cái vấn đề khó khăn này là lúc trước đường chủ tại tam tinh danh sư thời điểm giải quyết, mà hắn... Chỉ dùng năm bước!”"),t("br"),t("br"),n._v("Lăng sư nói."),t("br"),t("br"),n._v("“Năm bước?”"),t("br"),t("br"),n._v("Tô sư con ngươi co rụt lại."),t("br"),t("br"),n._v("Danh sư chỉ điểm, bước số càng ít càng tinh luyện, bị chỉ điểm người, thành công khả năng cũng là càng lớn."),t("br"),t("br"),n._v("Hiệu quả cũng là càng tốt."),t("br"),t("br"),n._v("Vừa rồi cái vấn đề khó khăn kia, coi như thực lực của hắn bây giờ, cộng thêm thủ đoạn, muốn giải quyết tốt đẹp, cũng muốn bảy bước nhiều, đường chủ tam tinh lúc, liền năm bước có thể hoàn thành... Thủ đoạn cao, nghe rợn cả người."),t("br"),t("br"),n._v("“Cũng không biết vị này Trương sư, có thể sử dụng mấy bước giải quyết!”"),t("br"),t("br"),n._v("Sau khi khiếp sợ, nhịn không được hướng cách đó không xa thanh niên nhìn lại."),t("br"),t("br"),n._v("“Mấy bước? Ta sợ trong thời gian ngắn, căn bản nghĩ không ra phương pháp giải quyết!” Lăng sư lắc đầu."),t("br"),t("br"),n._v("Mặc dù đối Trương sư có rất lớn lòng tin, có thể đường chủ vấn đề này thực sự quá khó khăn, coi như bọn hắn, đều cần tốn hao rất nhiều thời gian nghiên cứu, vị này có thể thành công hay không, cũng còn không biết."),t("br"),t("br"),n._v("Hai người khẩn trương, nghe xong vấn đề của đối phương, Trương Huyền nhướng mày: “Bên trái phổi bị thương, phạm vi bao lớn, có thể hay không cho ta ghi chú một chút vị trí cụ thể! Hơn nữa, là bị cái gì gây thương tích, đối phương chân khí thuộc tính như thế nào?”"),t("br"),t("br"),n._v("“Là bị trường kiếm gây thương tích, đối phương lĩnh ngộ kiếm ý, kiếm khí thâm nhập huyết mạch, đây là chẩn bệnh, ngươi có thể kỹ càng nhìn một chút!”"),t("br"),t("br"),n._v("Dường như biết hắn lại như vậy hỏi, Khang đường chủ đem trong tay thư tịch thấp tới."),t("br"),t("br"),n._v("Trương Huyền tiện tay lật ra, bên trong quả nhiên lít nha lít nhít ghi chép người tu luyện này tình huống cặn kẽ."),t("br"),t("br"),n._v("Nội dung mười phần kỹ càng, không chỉ bị thương vị trí, thậm chí bị cái chiêu số gì làm bị thương, đều viết rõ rõ ràng ràng."),t("br"),t("br"),n._v("Rất mau nhìn xong."),t("br"),t("br"),n._v("“Nhưng có phương pháp giải quyết?” Khang đường chủ nhìn lại."),t("br"),t("br"),n._v("“A, cái này đơn giản, một lần nữa cho hắn thiết kế cái tu luyện lộ tuyến là được.” Trương Huyền nói."),t("br"),t("br"),n._v("“Tu luyện lộ tuyến?” Khang đường chủ khuôn mặt trầm xuống: “Ngươi ý tứ... Là sửa chữa tu luyện công pháp?”"),t("br"),t("br"),n._v("“Đúng a, bên trái phổi bị thương, cũng không phải là bên trái mạch chân khí không thể vận chuyển, mà là chân khí đi lại lộ tuyến cần đổi một chút, chỉ cần hơi điều chỉnh, liền sẽ cùng bình thường người tu luyện đồng dạng, đột phá không khó!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Sửa chữa công pháp?” Một bên Tô sư, Lăng sư đồng thời sững sờ: “Có thể tiết kiệm đi trị liệu thương thế, một lần nữa khơi thông kinh mạch, điều dưỡng thân thể rất nhiều trình tự... Thật muốn có thể giải quyết, tương đương với chỉ dùng...”"),t("br"),t("br"),n._v("“Một bước?”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-536"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-538"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);