(window.webpackJsonp=window.webpackJsonp||[]).push([[1785],{2140:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-529-con-khong-rot-nuoc-đi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-529-con-khong-rot-nuoc-đi"}},[n._v("#")]),n._v(" Chương 529: Còn không rót nước đi!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Lăng sư trị liệu cần tám bước, Tô sư cần bảy bước, coi như đường chủ cũng muốn năm bước mới có thể hoàn thành, kẻ trước mắt này thế mà muốn một bước giải quyết?"),t("br"),t("br"),n._v("“Nói càn, ý nghĩ hão huyền!”"),t("br"),t("br"),n._v("Ngay tại nghi hoặc, liền nghe đến Khang đường chủ tiếng hừ lạnh vang lên, ngẩng đầu nhìn lại, chỉ thấy sắc mặt hắn xanh xám, tràn đầy không vui: “Mỗi một bộ tu luyện công pháp, đều là tiền bối sáng chế, chịu đến vô số người kiểm nghiệm, mới có thể truyền lưu thế gian, há lại ngươi nói đổi liền sửa? Đối phương bản thân bị trọng thương, dùng chưa kiểm nghiệm qua tu luyện công pháp, đừng nói không cách nào xung kích Hóa Phàm, làm không cẩn thận lại còn tẩu hỏa nhập ma, bị thương nặng mà chết, chân nếu như thế, danh sư uy nghiêm ở đâu?”"),t("br"),t("br"),n._v("Vốn là nghe được hai vị trưởng lão lời nói, đối cái tên này báo rất lớn hi vọng, không nghĩ tới cư nhiên như thế qua loa cho xong."),t("br"),t("br"),n._v("Tu luyện công pháp, tốt như vậy đổi, chẳng phải khắp nơi đều là bí tịch?"),t("br"),t("br"),n._v("Huống chi, trong cơ thể còn ẩn chứa dị chủng chân khí, không hóa giải, không chữa thương, không điều chỉnh trạng thái, không hội tụ tinh thần... Làm sao có thể xung kích cảnh giới cao hơn?"),t("br"),t("br"),n._v("Thật sự cho rằng là một thiên tài, không nghĩ tới là cái chày gỗ!"),t("br"),t("br"),n._v("Thật muốn làm như vậy, không những không thể chữa thương, làm không cẩn thận còn đem người quản lý chết, Danh Sư đường uy nghiêm đều sẽ quét sạch sành sanh."),t("br"),t("br"),n._v("“Tẩu hỏa nhập ma? Ngươi suy nghĩ nhiều!”"),t("br"),t("br"),n._v("Khoát khoát tay cắt ngang hắn quát lớn, Trương Huyền nhàn nhạt nhìn qua: “Ta nói một chút vận chuyển chân khí lộ tuyến, ngươi nghe một chút liền biết là có phải có dùng! Chân khí chạy, từ đan điền ra, qua Thái Ất, quan môn, lương môn... Cuối cùng hợp ở khí xá, qua lại ba cái tuần hoàn về sau, liền có thể xung kích Hóa Phàm, ước chừng chín mươi phần trăm tỷ lệ thành công!”"),t("br"),t("br"),n._v("Nắm giữ Thiên Đạo công pháp, đối tu luyện nắm giữ đến bản chất, đối phương nói loại tình huống này, nói đến khó, đối với hắn mà nói, vẫn là vô cùng đơn giản."),t("br"),t("br"),n._v("Chỉ cần hơi cải biến tu luyện lộ tuyến, vòng qua bị thương vị trí, trung hoà trong cơ thể dị chủng chân khí, đột phá Hóa Phàm không khó."),t("br"),t("br"),n._v("“Qua Thái Ất, quan môn, lương môn...”"),t("br"),t("br"),n._v("Ba người cau mày."),t("br"),t("br"),n._v("Cái này lộ tuyến, nghe mười phần quái dị, cùng bình thường pháp quyết tu luyện hoàn toàn khác biệt, coi như bọn hắn là tứ tinh danh sư, cũng trong lúc nhất thời khó mà đánh giá ra chính xác cùng sai lầm."),t("br"),t("br"),n._v("Mỗi một cái công pháp, đều muốn tu luyện mới có thể thực tiễn, cho dù là danh sư, cũng không có khả năng nghe xong một lần, liền đánh giá ra có dùng vẫn là vô dụng, hiệu quả như thế nào."),t("br"),t("br"),n._v("“Ngươi nói chỉ cần dựa theo môn công pháp này chạy, có thể trực tiếp có thể đột phá?” Khang đường chủ vẻ mặt càng thêm khó coi."),t("br"),t("br"),n._v("Bình thường người tu luyện, muốn đột phá cũng khó khăn, để một cái trọng thương người, vận chuyển ba lần pháp quyết đã đột phá, nói đùa cái gì?"),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Trương Huyền thần sắc thản nhiên: “Ta mới vừa nhìn, người này là bị nóng rực kiếm khí gây thương tích, loại này kiếm khí, mang theo cực mạnh phong mang, tổn thương ngực trái về sau, dọc theo kinh mạch, tuôn hướng đan điền. Người bị thương tu luyện chính là , coi trọng chính là sắc bén, một tiếng trống tăng khí thế, hai tiếng thì suy, ba tiếng thì kiệt! Cùng nóng rực kiếm khí cương mãnh, trùng hợp tương đồng, đều thuộc về cuồng bạo không lui bước lực lượng.”"),t("br"),t("br"),n._v("“Hai loại chân khí gặp gỡ, sẽ xuất hiện ngõ hẹp gặp nhau dũng giả thắng cục diện. Vốn là cương mãnh đối bính, chân khí bản thân liên tục không ngừng, đối phương kiếm khí, chẳng qua bèo trôi không rễ, không đủ gây sợ, đáng tiếc... Vị này người tu luyện kinh mạch quá nhỏ, đối bính quá ác, rất dễ dàng đem nó xé nát, để tu vi phế bỏ! Ngay sau đó liền xuất hiện một người giữ ải vạn người không thể qua cục diện. Kiếm khí không những không cách nào khu trừ, thương thế còn... Càng ngày càng nặng!”"),t("br"),t("br"),n._v("Nói đến đây, Trương Huyền ngừng lại, hai mắt như điện nhìn lại: “Nếu như ta không có đoán sai, ghi chép những bệnh trạng này thời điểm, hẳn là người này bị thương ngày thứ bảy đi!”"),t("br"),t("br"),n._v("“Cái này...” Khang đường chủ sững sờ: “Không sai!”"),t("br"),t("br"),n._v("Người kia vấn đề là hắn tự mình giải quyết, một mực vẫn lấy làm kiêu ngạo, ngay lúc đó tất cả chi tiết, đều nhớ rõ rõ ràng ràng, chính là bị thương ngày thứ bảy."),t("br"),t("br"),n._v("Chỉ là, vừa rồi ghi chép, chỉ viết bị thương vị trí, cùng bị cái chiêu số gì làm bị thương... Bị thương mấy ngày, nhưng không có miêu tả, chẳng lẽ... Chỉ nhìn điểm ấy nội dung, liền có thể suy đoán ra thời gian?"),t("br"),t("br"),n._v("“Bị thương ngày thứ bảy, khống chế không nổi chân khí đụng đến, ngực trái bị xuyên thủng chỗ, một mực không cách nào khôi phục, thậm chí bắt đầu thối rữa! Trước mắt tình huống, hai loại cuồng bạo chân khí đối bính, nếu như cưỡng ép sử dụng trước kia tu luyện công pháp, sẽ chỉ làm đụng đến càng thêm kịch liệt! Để thương thế càng thêm nghiêm trọng.”"),t("br"),t("br"),n._v("Trương Huyền nhẹ nhàng cười một tiếng: “Dựa theo bình thường phương pháp giải quyết, ứng đầu tiên để hắn đem chân khí tiêu hao sạch sẽ... Cái này tương đương với triệt tiêu cùng cửa ải đối chiến binh sĩ, để đạo kiếm khí kia, trước từ chật hẹp kinh mạch chảy ra tới... Thứ yếu, lại nghĩ biện pháp đem nó xóa!”"),t("br"),t("br"),n._v("“Đúng...” Tô Phàm, lăng hằng vũ đồng thời gật đầu."),t("br"),t("br"),n._v("Bọn hắn nói tới tám bước, bảy bước đều là như thế."),t("br"),t("br"),n._v("Muốn đột phá, trước phải giải quyết thương thế, mà trước muốn chữa thương, nhất định phải hóa giải căn nguyên... Đạo kiếm khí này, chính là căn nguyên."),t("br"),t("br"),n._v("Tiêu hao chân khí bản thân, là lui, chỉ có như vậy, mới có thể để cho cương mãnh kiếm khí thả lỏng cảnh giác, chỉ cần từ chật hẹp kinh mạch đi ra, còn muốn giải quyết liền sẽ dễ dàng rất nhiều, chí ít có mấy loại phương pháp."),t("br"),t("br"),n._v("Khang đường chủ cũng gật gật đầu."),t("br"),t("br"),n._v("Lúc trước hắn cũng là làm như vậy, chỉ cần là danh sư, cơ bản đều sẽ như vậy lựa chọn, chẳng lẽ... Như vậy không đúng?"),t("br"),t("br"),n._v("“Đúng? Không những không đúng, vẫn là sai lầm lớn!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu: “Tản mất chân khí của mình, thật giống như dọn đi ngăn ở dòng suối bên trên nham thạch, quả thực có thể để cho kiếm khí chảy ra đến, đáng tiếc... Cái kia là sắc bén vô song nóng rực kiếm khí, mà bị chận địa phương, ở bên trái phổi chỗ... Nơi này, tới gần trái tim, một khi kiếm khí xông vào mà ra, làm không cẩn thận liền sẽ đâm xuyên trái tim... Để cho người ta tại chỗ tử vong!”"),t("br"),t("br"),n._v("“Tử vong?” Tô Phàm nhịn không được mở miệng: “Kiếm khí bị nhốt bảy ngày, hẳn là không trước đó sắc bén...”"),t("br"),t("br"),n._v("“Là không có trước đó sắc bén, đâm xuyên trái tim tỷ lệ, không đủ một phần trăm, nhưng nếu như nó không đi bên trái phế kinh mạch, mà là thông qua hội trì huyệt, vào chân huyệt tâm mạch đâu?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Cái này...” Tô sư, Lăng sư đồng thời chấn động."),t("br"),t("br"),n._v("Hội trì huyệt, là bên trái phổi kinh mạch cùng chân huyệt tâm mạch giao hội chỗ, cái trước vào phổi, người sau thông tâm."),t("br"),t("br"),n._v("Mặc dù kiếm khí đột nhiên thả lỏng sẽ xuôi dòng mà xuống, hướng đan điền phương hướng đi lại, có thể... Cũng không bảo đảm, vị này người tu luyện sẽ có băn khoăn, bảo lưu cùng nhau chân khí tại thể nội."),t("br"),t("br"),n._v("Một khi như thế, đạo kiếm khí này liền vô cùng có khả năng đi ngược dòng nước, đi vào chân huyệt tâm mạch, đem trái tim đâm xuyên."),t("br"),t("br"),n._v("Nếu thực như thế, đừng nói danh sư, coi như Thần Tiên cũng không cứu sống nổi."),t("br"),t("br"),n._v("Khang đường chủ vẻ mặt cũng là tái đi, toàn thân căng thẳng."),t("br"),t("br"),n._v("Lúc trước gặp được cái này án lệ, hắn chuyên tâm muốn chữa khỏi, không có cân nhắc quá nhiều, về sau cũng phát hiện vấn đề này, mặc dù tỷ lệ không lớn, có thể... Cũng là có khả năng!"),t("br"),t("br"),n._v("Chỉ có thể nói ngay lúc đó vận khí rất dễ, nếu không đừng nói chữa khỏi, người làm không cẩn thận đã chết rồi."),t("br"),t("br"),n._v("Thật muốn như thế, còn khảo hạch tứ tinh danh sư, có thể giữ được hay không ngay lúc đó địa vị, không để tiếng xấu muôn đời, cũng rất không tệ."),t("br"),t("br"),n._v("Chỉ là... Loại tình huống này, hay là hắn đạt tới tứ tinh đỉnh phong danh sư về sau, cẩn thận xem lúc ấy tình huống, mới phát hiện... Trước mắt thanh niên này, chỉ nhìn một chút, liền nghĩ đến điểm ấy, chẳng lẽ... Hắn nói công pháp, thật có hiệu quả?"),t("br"),n._v(" Trước đó cảm thấy đối phương là ăn nói lung tung, bây giờ đối phương liền loại tình huống này đều có thể nhìn ra... Chỉ sợ công pháp, không có đơn giản như vậy."),t("br"),t("br"),n._v("“Phương pháp của ta, căn bản không cần sớm tản quang chân khí, càng không cần đem đạo kiếm khí này dẫn ra!”"),t("br"),t("br"),n._v("Không để ý tới ba người khiếp sợ, Trương Huyền tiếp tục nói: “Thái Ất huyệt là chi phối kinh mạch cái thứ nhất chi nhánh điểm, ta để chân khí đi qua từ nơi này, có thể rất dễ dàng gây nên lực lượng cộng minh, để đạo kiếm khí kia, thuận thế mà xuống, vọt tới nơi đây. Chỉ cần tới, liền đơn giản, Thái Ất huyệt tới gần đan điền, người tu luyện có đầy đủ chân khí duy trì, có thể rất dễ dàng đem nó bức ra bên ngoài cơ thể!”"),t("br"),t("br"),n._v("“Nếu như đem thân thể xem như trận pháp, Thái Ất là sinh, cả hai va chạm, kiếm khí sẽ rất tự nhiên từ nơi này huyệt đạo phun ra ngoài...”"),t("br"),t("br"),n._v("Con mắt trợn tròn, Tô sư tự lẩm bẩm."),t("br"),t("br"),n._v("Căn cứ trận pháp sư lý luận, thân thể, cũng có thể nhìn thành trận pháp, cũng có hưu môn, sinh môn, thương môn, đỗ môn, cảnh môn, tử môn, kinh môn, mở cửa... Tám chỗ môn hộ."),t("br"),t("br"),n._v("Thái Ất huyệt, là chi phối kinh mạch chỗ giao hội, càng là thân thể trận pháp sinh môn vị trí, đem kiếm khí hấp dẫn đến đây, khẳng định sẽ từ cái này chạy ra, như vậy trải qua, tương đương với dùng cái giá thấp nhất, đem nó hủy diệt, không uổng phí một binh một tốt..."),t("br"),t("br"),n._v("So trước đó loại kia tản quang chân khí, cưỡng ép khu trừ hiệu quả, tốt chí ít mấy chục lần!"),t("br"),t("br"),n._v("“Không đúng... Kiếm khí tới, làm như vậy xác thực rất đơn giản, nhưng bất quá đến đâu?” Khang đường chủ mày nhăn lại."),t("br"),t("br"),n._v("Kiếm khí cùng chân khí đối kháng bảy ngày, không đem chân khí rút lui, làm sao có thể xung kích đến Thái Ất huyệt?"),t("br"),t("br"),n._v("Thật giống như hai bên giao chiến, đối phương canh giữ ở cửa ải, mới có thể cam đoan không chết, ngươi đại binh chưa rút lui, ta tại sao phải xông lại, chẳng lẽ tự chui đầu vào lưới, chờ lấy bị ngươi khu trục?"),t("br"),t("br"),n._v("“Ai!”"),t("br"),t("br"),n._v("Nghe được câu hỏi của hắn, Trương Huyền than thở một tiếng, có chút bất đắc dĩ lắc đầu: “Xem ra ngươi đối người bị thương tu luyện  không hiểu nhiều!”"),t("br"),t("br"),n._v("“Ta...” Khang đường chủ hơi đỏ mặt."),t("br"),t("br"),n._v(" thuộc về thiên môn công pháp, hắn nghe nói qua, nhưng lại chưa tu luyện qua, biết được cũng không nhiều."),t("br"),t("br"),n._v("Muốn nói không hiểu nhiều, cũng là sự thật."),t("br"),t("br"),n._v("“, coi trọng chính là sắc bén, một tiếng trống tăng khí thế, hai tiếng thì suy, ba tiếng thì kiệt! Vừa rồi ta cũng đã nói, để chân khí lại lần nữa thông đạo vận chuyển ba lần... Như vậy trải qua, sắc bén chi khí tiêu tán, kiếm khí nhìn thấy cơ hội như vậy, tự nhiên muốn lao ra, thừa cơ phá hư đan điền... Đi tới Thái Ất huyệt, là tất nhiên chuyện phát sinh! Điểm ấy... Ngươi không có khả năng không nghĩ tới sao!”"),t("br"),t("br"),n._v("Trương Huyền nhàn nhạt nhìn qua."),t("br"),t("br"),n._v("“Cái này...” Khang đường chủ chấn động toàn thân."),t("br"),t("br"),n._v("Bị sắc bén chân khí ngăn chặn bảy ngày, kiếm khí coi như không có linh trí, cũng sẽ cùng áp súc lò xo đồng dạng, tùy thời mà động, thừa cơ phản kháng...  liên tục mà suy về sau, uy lực yếu bớt, chính là tiến công thời cơ tốt nhất, tuyệt không chịu có thể buông tha!"),t("br"),t("br"),n._v("Điểm ấy, chỉ cần là người tu luyện, cẩn thận suy nghĩ, liền có thể nghĩ thông suốt."),t("br"),t("br"),n._v("“Vẫn là không đúng! Ngươi nói vận chuyển ba lần, liền có thể xung kích Hóa Phàm, hiện tại chỉ là đem kiếm khí khu trừ...” Sau khi hết khiếp sợ, Khang đường chủ vẫn còn có chút không tin."),t("br"),t("br"),n._v("“Chỉ là đem kiếm khí khu trừ? Ngươi suy nghĩ lại một chút, ta mới vừa nói vận chuyển chân khí phương hướng...” Trương Huyền mỉm cười."),t("br"),t("br"),n._v("“Chân khí chạy, từ đan điền ra, qua Thái Ất, quan môn, lương môn... Cuối cùng hợp ở khí xá...”"),t("br"),t("br"),n._v("Khang đường chủ kìm lòng không được nhớ tới thanh niên mới vừa nói nội dung, biến sắc, con mắt trợn tròn: “Chân khí suy giảm, kiếm khí truy kích, gặp nhau Thái Ất huyệt, cả hai đụng phía sau... Kiếm khí từ huyệt đạo phun ra, mà người tu luyện chân khí, sẽ mượn nhờ đụng lực lượng, xung kích quan môn, lương môn... Xung kích Hóa Phàm?”"),t("br"),t("br"),n._v("“Như vậy thì tương đương với mượn kiếm khí lực lượng cuồng bạo, xông phá huyền quan, cơ hội thành công... Quả thực không thua kém chín mươi phần trăm!” Tô sư cũng yết hầu phát khô."),t("br"),t("br"),n._v("Làm tứ tinh danh sư, đối chân khí trong cơ thể chạy sẽ xuất hiện tình huống, nắm giữ tới cực điểm, lý giải đến cốt tủy."),t("br"),t("br"),n._v("Kiếm khí cùng chân khí tại Thái Ất huyệt chạm vào nhau, mặc dù từ sinh môn tán chỗ, có thể ẩn chứa trong đó cuồng bạo lực lượng, lại dung nhập chân khí bên trong."),t("br"),t("br"),n._v("Có thể thương tổn được vị này người tu luyện, nói rõ thực lực, so với hắn cao hơn. Đã vị này có thể xung kích Hóa Phàm, tự nhiên đã sớm đạt tới nửa bước Hóa Phàm đỉnh phong nhất, bất cứ lúc nào cũng sẽ đột phá... Thực lực mạnh hơn hắn, chẳng phải là Hóa Phàm cường giả?"),t("br"),t("br"),n._v("Hóa Phàm cường giả cuồng bạo chân khí lực lượng, cùng hắn bản thân lực lượng dung hợp, cả hai điệp gia, uy lực vô cùng... Xông phá bình chướng, tự nhiên cũng là đơn giản rất nhiều, cực kỳ dễ dàng!"),t("br"),t("br"),n._v("Nào chỉ là chín mươi phần trăm, có thể nói, thật muốn làm như vậy, xác suất thành công... Trăm phần trăm!"),t("br"),t("br"),n._v("“Ngươi, ngươi...”"),t("br"),t("br"),n._v("Tất cả mọi người nghĩ đến điểm này, từng người trợn to hai mắt."),t("br"),t("br"),n._v("Bọn hắn nghe được người tu luyện này triệu chứng, nghĩ tới nhiều nhất là, như thế nào giải quyết kiếm khí, khôi phục thương thế, điều chỉnh trạng thái, sau đó lại nghĩ biện pháp xung kích Hóa Phàm... Mà trước mắt vị này, thế mà mượn nhờ trong cơ thể ẩn chứa kiếm khí, mở ra lối riêng, sáng chế một bộ trực tiếp có thể đột phá pháp quyết tu luyện..."),t("br"),t("br"),n._v("Mấu chốt nhất là, từ nhìn xong án lệ, đến nói ra phương pháp giải quyết, tổng cộng cũng là mấy chục cái hô hấp..."),t("br"),t("br"),n._v("Đến cùng... Làm sao làm được?"),t("br"),t("br"),n._v("Còn là người sao?"),t("br"),t("br"),n._v("“Ta môn công pháp này, khả năng chữa khỏi đối phương, đồng thời để hắn đột phá?” Biết đối phương đã hiểu phương pháp của hắn, Trương Huyền nhàn nhạt nhìn qua."),t("br"),t("br"),n._v("“Là... Có thể trị hết!” Khang đường chủ sắc mặt trắng nhợt, bình ổn lại hô hấp, nhẹ gật đầu."),t("br"),t("br"),n._v("Hắn hao tốn ròng rã năm bước, mới có thể hoàn thành, đã cảm thấy rất lợi hại, không ai bằng, trước mắt vị này, chỉ dùng một bước liền thành công..."),t("br"),t("br"),n._v("Xem ra Tô sư, Lăng sư nói đúng, trước mắt vị này, mặc dù thực lực không đủ, nhưng đối với danh sư lý giải, tuyệt đối không phải người bình thường có thể so sánh được."),t("br"),t("br"),n._v("“Vậy ta có thể tính trả lời ra vấn đề của ngươi?” Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("“Là...” Khang đường chủ gật đầu."),t("br"),t("br"),n._v("“Đã đúng, còn đứng ngây đó làm gì?”"),t("br"),t("br"),n._v("Lời còn chưa dứt, liền nghe đến đối diện thanh niên, mang theo không vui thanh âm vang lên: “Nói nhiều như vậy, không thấy được ta khát nước, còn không mau một chút rót cốc nước đi!”"),t("br"),t("br"),n._v("(Tiếp cận bốn ngàn chữ đại chương đoạn, còn thấy thoải mái? Nguyệt phiếu rớt xuống thứ năm, chư vị nhưng còn có phiếu, thoải mái a, xin giúp lão nhai một chút sức lực! Thấy có người nói, tăng tệ không ảnh hưởng, mồ hôi, ảnh hưởng rất lớn ah, tăng tệ không tính là đặt mua, nói cách khác, dùng tăng tệ đặt mua, ta một phân tiền cũng lấy không được, khẩn cầu bậc thầy đừng có dùng... Ô ô!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-537"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-539"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);