(window.webpackJsonp=window.webpackJsonp||[]).push([[1786],{2141:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-530-han-la-troi-nhan-danh-su"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-530-han-la-troi-nhan-danh-su"}},[n._v("#")]),n._v(" Chương 530: Hắn là trời nhận danh sư!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 530: Hắn là trời nhận danh sư!"),t("br"),t("br"),n._v("Phù phù!"),t("br"),t("br"),n._v("“Rót nước?”"),t("br"),t("br"),n._v("“Hắn bảo đường chủ rót nước?”"),t("br"),t("br"),n._v("Nghe nói như thế, Tô Phàm, Lăng Hằng Vũ khóe miệng giật một cái, đồng thời nhoáng một cái, hai vị đường đường Hóa Phàm tứ trọng cường giả, kém chút không có cùng một chỗ té ngã trên đất."),t("br"),t("br"),n._v("Vừa rồi sau khi vào cửa, dặn đi dặn lại, đường chủ nghiêm túc, nói chuyện phải chú ý đúng mực, không muốn đường đột... Ai ngờ cái này hai hàng, trực tiếp hạ mệnh lệnh, cùng giáo huấn người đồng dạng..."),t("br"),t("br"),n._v("Em gái ngươi!"),t("br"),t("br"),n._v("Ngươi đây là muốn điên ah!"),t("br"),t("br"),n._v("Hai ta cầu gia gia nói với nãi nãi xin tới danh ngạch, dễ dàng ư? Ngươi là không mất còn chưa xong ah..."),t("br"),t("br"),n._v("Hai người nước mắt ào ào, đang cảm thấy đường chủ sẽ giận tím mặt, trở mặt tại chỗ, chỉ thấy luôn luôn nghiêm túc đường chủ đại nhân, vội vàng đứng lên, vẻ mặt cung kính lên tiếng: “Vâng!”"),t("br"),t("br"),n._v("Nói xong quay người đem trên lò lửa nước nóng bưng lên, xuất ra trân tàng lá trà, tràn đầy rót một chén, đưa tới."),t("br"),t("br"),n._v("“Mẹ nó!”"),t("br"),t("br"),n._v("“Cái này... Tình huống gì?”"),t("br"),t("br"),n._v("Hai vị tứ tinh danh sư, Hóa Phàm tứ trọng trưởng lão, da mặt run rẩy, vẻ mặt phát điên."),t("br"),t("br"),n._v("Vị này chính là ăn nói có ý tứ, uy danh hiển hách Vạn Quốc thành Khang đường chủ, coi như Triệu minh chủ đích thân tới, cũng không có tư cách uống hắn ngâm chế nước trà, trước mắt vị này Trương sư, nói chuyện vô lễ như thế, đối phương không nhưng nghe, còn bộ dáng này..."),t("br"),t("br"),n._v("Đến cùng xảy ra chuyện gì?"),t("br"),t("br"),n._v("“Là... Sư ngôn thiên bẩm!”"),t("br"),t("br"),n._v("Đột nhiên, Tô sư nhớ ra cái gì đó, hóng gió, không ngừng run rẩy."),t("br"),t("br"),n._v("Danh sư trong lúc đó, tuân thủ một cách nghiêm chỉnh lời nói của đối phương, không dám vi phạm, lớn nhất khả năng chính là cái này!"),t("br"),t("br"),n._v("Vừa rồi vị này Trương sư, trong lời nói ẩn chứa thiên âm, tâm cảnh lực lượng ba động... Bởi vì không có nhằm vào bọn họ, vẫn cho là là tại phân tích bệnh tình, không nghĩ tới... Lại là đang thi triển cái đồ chơi này!"),t("br"),t("br"),n._v("Đối Khang đường chủ thi triển sư ngôn thiên bẩm... Còn thế mà thành công!"),t("br"),t("br"),n._v("Ta lặc cái đi!"),t("br"),t("br"),n._v("Tô sư, Lăng sư nhìn nhau, từng người cảm giác muốn tự tử đều có."),t("br"),t("br"),n._v("Khang đường chủ thế nhưng là tâm cảnh khắc độ vượt qua 14. 0 tồn tại, Vạn Quốc thành đệ nhất danh sư, bị sư ngôn thiên bẩm mê hoặc, mấu chốt nhất... Thi triển thủ đoạn này, chỉ là cái không đủ hai mươi nhị tinh danh sư... Truyền đi, tuyệt đối có thể hù chết một đám người lớn."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Hai người khiếp sợ, Khang đường chủ giờ phút này cũng kịp phản ứng, con mắt thoáng cái trợn tròn, da mặt quất thẳng tới, kém chút máu tươi phun ra."),t("br"),t("br"),n._v("Đường đường Danh Sư đường cường đại nhất danh sư, thế mà bị một cái nhị tinh danh sư dùng sư ngôn thiên bẩm mê hoặc, cam tâm tình nguyện bưng trà rót nước..."),t("br"),t("br"),n._v("Suy nghĩ một chút đều cảm thấy nổi điên."),t("br"),t("br"),n._v("Đang muốn phía dưới nên làm cái gì, chỉ thấy thanh niên trước mắt, nhấp một ngụm trà lá, nhàn nhạt xoay đầu lại: “Nói vấn đề thứ hai đi!”"),t("br"),t("br"),n._v("“Cái thứ hai...”"),t("br"),t("br"),n._v("Khang đường chủ khóe mắt giật một cái."),t("br"),t("br"),n._v("Vấn đề thứ nhất, đối phương trực tiếp sư ngôn thiên bẩm, bảo hắn thân bất do kỷ, nếu như hỏi lại một cái, có thể hay không xuất hiện tương đồng tình huống?"),t("br"),t("br"),n._v("“Không cần, ba cái vấn đề, là vì kiểm nghiệm ngươi có hay không tham gia khiêu chiến tư cách, vấn đề thứ nhất, trả lời so với chúng ta đều muốn hợp lý, đủ để chứng minh thực lực, còn lại hai cái cũng là không cần thiết!”"),t("br"),t("br"),n._v("Nghiêm sắc mặt, Khang đường chủ đại nghĩa lăng nhiên."),t("br"),t("br"),n._v("Khảo nghiệm ba cái vấn đề, là muốn khảo thí đối phương đối danh sư lĩnh ngộ, bản thân tốn hao năm bước mới có thể giải quyết nan đề, nhân gia một bước giải quyết, càng là dùng sư ngôn thiên bẩm mê hoặc bản thân... Chỉ bằng vào điểm ấy, liền có đầy đủ tư cách tham gia tuyển bạt!"),t("br"),t("br"),n._v("Đã như vậy, tiếp tục hỏi thăm nữa, cũng mất ý nghĩa, còn không bằng rộng lượng chút, miễn cho một lát nữa lại bị mê hoặc, như thế liền càng thêm mất mặt."),t("br"),t("br"),n._v("Vung tay lên, Khang đường chủ quay đầu: “Lăng trưởng lão, ngươi bây giờ liền đi gõ vang tụ hợp chuông, làm cho tất cả mọi người đi danh sư điện, tiến hành tuyển bạt!”"),t("br"),t("br"),n._v("“Vâng!” Lăng trưởng lão gật đầu, quay người lui ra ngoài."),t("br"),t("br"),n._v("Một lát sau, tiếng chuông vang lớn, chỉ cần là vạn quốc liên minh danh sư, nghe được thanh âm, nhất định phải khẩn cấp tụ hợp."),t("br"),t("br"),n._v("“Người tới, mang Trương sư đi danh sư điện, ta cùng Tô sư lập tức tới ngay!”"),t("br"),t("br"),n._v("Nghe được tiếng chuông vang lên, biết danh sư điện chẳng mấy chốc sẽ kín người hết chỗ, Khang đường chủ khoát tay áo, nghe được mệnh lệnh, giữ ở ngoài cửa thanh niên đi đến, cung kính nhìn Trương Huyền một chút, phía trước dẫn đường."),t("br"),t("br"),n._v("Biết muốn đi danh sư điện tuyển bạt, Trương Huyền cũng không chối từ, theo ở phía sau rời khỏi."),t("br"),t("br"),n._v("Hai người vừa đi, trong phòng an tĩnh lại,"),t("br"),t("br"),n._v("Chỉ còn lại có đường chủ cùng Tô Phàm trưởng lão."),t("br"),t("br"),n._v("Nhìn trước mắt ở chung được mấy chục năm bạn cũ, Khang đường chủ do dự nửa ngày, nhịn không được nhìn qua: “Đối với cái này Trương sư, ngươi không có gì có thể nói?”"),t("br"),t("br"),n._v("“Hắn là... Thiên Vũ vương quốc mới hướng lên tới nhị tinh danh sư, năm nay vẫn chưa tới hai mươi...” Tô sư nói."),t("br"),t("br"),n._v("“Ta hỏi không phải cái này!” Khang đường chủ mắt tối sầm lại, cố nén lửa giận: “Ta muốn kỹ càng tư liệu, cùng các ngươi liều mạng đề cử hắn lý do!”"),t("br"),t("br"),n._v("Hố cha đây!"),t("br"),t("br"),n._v("Nếu như chỉ là loại này lý lịch, ngươi đường đường Vạn Quốc thành Danh Sư đường trưởng lão, làm sao có thể ngay cả mình học sinh đều không đề cử, nhất định phải đề cử hắn?"),t("br"),t("br"),n._v("Phải biết Tô sư có một cái học sinh, cũng đạt tới tam tinh đỉnh phong, kinh tài tuyệt diễm, không kém chút nào Phó Tiếu Trần."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Hàng Xô Viết chần chờ một chút: “Có thể hay không đi cơ mật phòng!”"),t("br"),n._v(" Cơ mật phòng là một cái ẩn chứa đặc thù trận pháp gian phòng, trong này đàm luận cơ mật, người lợi hại hơn nữa, cũng không cách nào nghe lén, nhìn trộm, không cần lo lắng bí mật tiết ra ngoài."),t("br"),t("br"),n._v("“Đến đây đi!”"),t("br"),t("br"),n._v("Khang đường chủ vẻ mặt ngưng tụ, đứng dậy."),t("br"),t("br"),n._v("Làm Danh Sư đường đường chủ, quản lý một phương, tiếp xúc vô số cơ mật, loại này cơ mật phòng, trong phòng của hắn liền có một cái, đẩy cửa đi vào, kích hoạt trận pháp, hai người lập tức đi vào tuyệt đối an tĩnh môi trường."),t("br"),t("br"),n._v("“Nói đi!”"),t("br"),t("br"),n._v("Hai người ngồi xuống, Khang đường chủ nhìn qua."),t("br"),t("br"),n._v("Tô trưởng lão ngưng trọng như thế, đủ thấy muốn nói sự tình mười phần quan trọng, giờ phút này, hắn cũng bình phục lại, ánh mắt như điện."),t("br"),t("br"),n._v("“Hơn 20 ngày trước, tổng bộ bên này tiên sư bị từ chối, hóa chuông than khóc... Đường chủ còn có nhớ đi!” Tô sư nói."),t("br"),t("br"),n._v("“Chuyện này như thế lớn, làm sao có thể không nhớ rõ, ta đã thông biết tất cả cấp dưới Danh Sư đường, để bọn hắn tra rõ, chỉ bất quá... Không được đến tin tức mà thôi, để cho ta hoài nghi, có phải hay không đến đây tham gia thi đấu thế lực khác danh sư!”"),t("br"),t("br"),n._v("Khang đường chủ gật đầu."),t("br"),t("br"),n._v("Lúc đó bọn hắn ngay tại tổ chức hội nghị trưởng lão, Phong Sư điện liền không ngừng run rẩy, không ngừng vang lên, huyên náo như thế lớn, mặc dù tin tức bị hắn ép xuống, có thể trở thành người trong cuộc, như thế nào không nhớ được?"),t("br"),t("br"),n._v("“Ngươi đưa tin đến Thiên Vũ vương quốc Danh Sư đường, nói... Chỉ cần có tượng rung động, Phong Sư điện sụp đổ cả hai một trong, lập tức bẩm báo...” Tô sư nói tiếp."),t("br"),t("br"),n._v("“Ừm! Tiên sư bị từ chối, hóa chuông than khóc... Xuất hiện loại tình huống này trực tiếp nhất biểu hiện chính là tượng chấn động, Phong Sư điện sụp đổ, một khi cái nào Danh Sư đường xuất hiện loại tình huống này, liền có thể xác nhận, vị thiên tài này chuẩn xác vị trí...”"),t("br"),t("br"),n._v("Lên tiếng, Khang đường chủ đột nhiên nhớ tới cái gì, hô hấp dồn dập nhìn qua: “Hơn 20 ngày trước, ngươi tại Thiên Vũ vương quốc, chẳng lẽ... Nơi đó Phong Sư điện sụp đổ?”"),t("br"),t("br"),n._v("“Vâng! Chẳng qua chuyện này liên quan lớn, sợ tin tức rò rỉ, không dùng đưa tin tường bẩm báo, mà là để bọn hắn trở về cái ‘Không’!” Tô sư ôm quyền: “Mong rằng đường chủ chớ trách!”"),t("br"),t("br"),n._v("Lúc trước tổng bộ phát tới tin tức hỏi thăm, là hắn yêu cầu trả lời không."),t("br"),t("br"),n._v("Nếu không, chuyện này một khi vỡ lở ra, Trương sư cực khả năng gặp được nguy hiểm."),t("br"),t("br"),n._v("“Tin tức rò rỉ, gây nên chuyện này danh sư, có thể sẽ bị điều tra ra, xuất hiện vô số phiền phức... Ngươi làm rất đúng!” Khang đường chủ gật đầu."),t("br"),t("br"),n._v("Nếu là hắn ở bên cạnh, cũng khẳng định sẽ làm như vậy."),t("br"),t("br"),n._v("Thân là danh sư, có trách nhiệm bảo hộ có thiên phú hậu bối, không bị thương tổn."),t("br"),t("br"),n._v("“Thiên Vũ vương quốc Phong Sư điện lúc sụp đổ, chính là... Trương sư khảo hạch nhị tinh danh sư!” Tô sư nói tiếp."),t("br"),t("br"),n._v("“Hắn... Ngươi nói là, hắn khả năng chính là cái kia cự tuyệt tiền bối tán thành, hóa chuông than khóc thiên tài?” Khang đường chủ con ngươi co vào."),t("br"),t("br"),n._v("Thẳng đến lúc này, hắn rốt cuộc minh bạch, vì cái gì hai người kia liều mạng đề cử, có thể để cho nhiều như vậy tiên sư tán thành, mấu chốt còn cự tuyệt... Nên mạnh bao nhiêu thiên phú?"),t("br"),t("br"),n._v("“Không chỉ có như thế, căn cứ ta suy đoán... Vị này Trương sư, còn có thể là... Trời nhận danh sư!”"),t("br"),t("br"),n._v("Tô sư nhịn không được nói."),t("br"),t("br"),n._v("Kỳ thật chuyện này, trở lại Vạn Quốc thành nên hướng đường chủ bẩm báo, sở dĩ không nói, cũng không phải là hắn cùng Lăng sư cố ý giấu diếm, mà là... Không cách nào xác nhận!"),t("br"),t("br"),n._v("Những ngày này một mực tại thẩm tra liên quan thư tịch."),t("br"),t("br"),n._v("Chỉ tiếc, trời nhận danh sư, chỉ có Khổng sư một người trải qua, tình huống cụ thể, không thể nào biết được, coi như hắn cùng Lăng trưởng lão hai người, hao phí hơn mười ngày, cũng không hiểu ra sao."),t("br"),t("br"),n._v("“Ngươi nói cái gì?”"),t("br"),t("br"),n._v("Khang đường chủ con ngươi co rụt lại."),t("br"),t("br"),n._v("“Trương sư khảo hạch danh sư thời điểm, không chỉ Phong Sư điện sụp đổ, tượng Khổng sư cũng đồng thời vỡ vụn, loại trừ trời nhận danh sư, Thánh Nhân môn sinh, ta thực sự nghĩ không ra chuyện gì xảy ra!” Tô sư sẽ tại Thiên Vũ vương quốc nghe được sự tình nói rõ chi tiết một lần."),t("br"),t("br"),n._v("“Cái này, cái này...” Nghe xong Thiên Vũ vương quốc Danh Sư đường xuất hiện đủ loại hiện tượng quái dị, Khang đường chủ bờ môi phát khô."),t("br"),t("br"),n._v("Mặc dù hắn cũng không cách nào xác nhận, nhưng vị này Trương sư, tám chín phần mười, chính là gây nên những chuyện này kẻ cầm đầu, là một vị... Trời nhận danh sư!"),t("br"),t("br"),n._v("“Mười ngày trước, trời nhận danh sư lại xuất hiện một lần, có thể hay không hay là hắn?”"),t("br"),t("br"),n._v("Đột nhiên, một việc toát ra trong óc."),t("br"),t("br"),n._v("Mười ngày trước, Danh Sư đường đồng dạng xuất hiện một lần “Vạn bài chấn động, tiếng chuông cùng reo vang” hiện tượng, căn cứ lịch sử ghi chép, đây là trời nhận danh sư dấu hiệu."),t("br"),t("br"),n._v("Đáng tiếc, liên tục tra xét mười ngày, tại Vạn Quốc thành không được đến bất luận cái gì có dùng tin tức."),t("br"),t("br"),n._v("Đã vị này Trương sư, có thể là trời nhận danh sư, có thể hay không... Hay là hắn?"),t("br"),t("br"),n._v("“Ta đây cũng không biết, mười ngày trước, hắn hẳn là còn chưa tới vương thành...” Tô sư lắc đầu."),t("br"),t("br"),n._v("Trương sư đều không tại vương thành, cụ thể có phải hay không, tự nhiên là càng không cách nào xác nhận."),t("br"),t("br"),n._v("“Bất kể có phải hay không là... Có thể là trời nhận danh sư, chỉ điều này, tham gia lần thi đấu này, liền không có vấn đề gì, chỉ tiếc...”"),t("br"),t("br"),n._v("Chần chờ một chút, Khang đường chủ lắc đầu: “Tuổi của hắn quá nhỏ, tu vi cũng không đủ, chỉ sợ chưa hẳn có thể vượt qua những người khác!”"),t("br"),t("br"),n._v("Mặc dù nhìn trời nhận danh sư, có cực mạnh lòng tin, có thể... Vị này Trương sư, tuổi tác quá nhỏ."),t("br"),t("br"),n._v("Có thể tới tham gia thi đấu, cơ bản đều tiếp cận ba mươi, nhỏ nhất cũng muốn hai mươi tám, chín, chênh lệch mười năm thời gian, coi như thiên phú lại cao hơn, muốn chân chính đối kháng, cũng có chút khó."),t("br"),t("br"),n._v("“Được rồi, không thèm nghĩ nữa, chuyện này, ngươi làm rất đúng, nhất định muốn giữ bí mật, bất kỳ người nào đều không cần nói cho!”"),t("br"),t("br"),n._v("Lắc đầu, không suy nghĩ thêm nữa những này, Khang đường chủ đứng dậy: “Tiếng chuông vang lên, danh sư không sai biệt lắm đúng chỗ, chúng ta cũng nhanh lên một chút đi!”"),t("br"),t("br"),n._v("“Ừm!” Tô sư theo sát lấy đứng dậy, mới vừa đi hai bước, chỉ thấy đường chủ dừng ở cách đó không xa, vẻ mặt lúng túng nhìn qua."),t("br"),t("br"),n._v("“Cái kia... Vừa rồi ta bị mê hoặc sự tình... Còn hy vọng có thể giữ bí mật, chớ nói ra ngoài!”"),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Tô sư."),t("br"),t("br"),n._v("(Đề cử đại thần ek chocolate sách mới, siêu võ xuyên qua, mang theo cơ giáp đi dị giới trang bức cố sự, lão nhai ngay tại theo đuổi xem, mọi người có thể nhìn xem ah!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-538"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-540"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);