(window.webpackJsonp=window.webpackJsonp||[]).push([[1788],{2142:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-531-ta-chinh-la-cai-kia-hai-hang"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-531-ta-chinh-la-cai-kia-hai-hang"}},[n._v("#")]),n._v(" Chương 531: Ta chính là cái kia hai hàng")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Cùng thanh niên phía sau đi một hồi, đi tới một cái cực lớn rộng gian phòng."),t("br"),t("br"),n._v("Danh sư điện."),t("br"),t("br"),n._v("Vạn Quốc thành tất cả danh sư, nghị sự, đại hội địa phương, còn chưa tới đến trước mặt, liền thấy bên trong chất đầy người."),t("br"),t("br"),n._v("Dòng người như thoi đưa, chen chúc như nước thủy triều."),t("br"),t("br"),n._v("“Trương sư, còn xin ngươi đem trường bào cùng huy chương đeo lên đi...”"),t("br"),t("br"),n._v("Đi tới trước mặt, thanh niên nói."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Đây là danh sư tụ hội, tự nhiên muốn mặc đại biểu thân phận đồ vật, nếu không có vẻ không hợp nhau."),t("br"),t("br"),n._v("Cổ tay khẽ đảo, đem trường bào lấy ra ngoài, mặc lên người, lại đem huy chương đừng ở ngực."),t("br"),t("br"),n._v("Bộ này đồ vật, từ khi Thiên Vũ vương quốc khảo hạch danh sư lấy được về sau, chưa hề mặc qua, không phải là không muốn mặc, mà là kiếp trước quần áo bó sát người quen thuộc, trường bào quấn ở trên người, hết sức bất tiện."),t("br"),t("br"),n._v("Chỉnh lý tốt trang phục, đi theo đám người đằng sau, đi vào."),t("br"),t("br"),n._v("Trước đó ở đại sảnh, liền biết danh sư rất nhiều, hiện tại hội tụ một đường, mới phát hiện, số lượng bao lớn, quả thực kinh người."),t("br"),t("br"),n._v("Lít nha lít nhít, thế mà không dưới năm trăm!"),t("br"),t("br"),n._v("Năm trăm danh sư... Nhìn xem đều cảm thấy đáng sợ."),t("br"),t("br"),n._v("Bất quá, nghĩ đến đây là vạn quốc liên minh vị trí, cũng là giật mình."),t("br"),t("br"),n._v("Danh sư thi đấu hấp dẫn vô số cường giả tới quan sát, hơi có chút thực lực danh sư, cơ hồ đều tới, mặc dù còn có một số lưu thủ, mà dù sao là số ít, coi như toàn bộ cộng thêm, toàn bộ liên minh cảnh nội, cũng sẽ không vượt qua một ngàn."),t("br"),t("br"),n._v("Mà trong liên minh, phong hào vương quốc, nhất đẳng vương quốc, nhị đẳng vương quốc thậm chí bất nhập lưu vương quốc, vô số kể, nhân khẩu tổng cộng tuyệt đối vượt qua chục tỷ, nhiều như vậy người tu luyện, mới có nhiều như vậy danh sư, dựa theo tỉ lệ, có thể nói tạm thời làm cho người giận sôi."),t("br"),t("br"),n._v("Thanh niên còn không phải danh sư, cũng là không có theo tới, Trương Huyền theo dòng người đi vào gian phòng."),t("br"),t("br"),n._v("Bên trong bày mấy hàng bàn ghế, lít nha lít nhít, giương mắt nhìn lại, phía trước nhất số lượng thưa thớt, chỉ có hai mươi chỗ ngồi, phía sau đại khái bảy, tám mươi trái phải, dùng cái này tăng lên."),t("br"),t("br"),n._v("“Xem ra là dựa theo thân phận ngồi...”"),t("br"),t("br"),n._v("Nhìn thoáng qua, hiểu được."),t("br"),t("br"),n._v("Danh sư mặc dù coi trọng người người như rồng, có thể đã chia làm cửu tinh, cũng là có đẳng cấp."),t("br"),t("br"),n._v("Phía trước nhất chỗ ngồi, chỉ có tứ tinh danh sư mới có thể ngồi xuống, đằng sau là tam tinh, sau đó là nhị tinh..."),t("br"),t("br"),n._v("Hắn hiện tại chỉ là nhị tinh danh sư, tự nhiên chỉ có thể ngồi ở phía sau."),t("br"),t("br"),n._v("Bất quá, đối với hắn mà nói cũng không quan trọng, dù sao một hồi liền muốn tuyển bạt, đoán chừng cũng ngồi không được bao dài thời gian."),t("br"),t("br"),n._v("Tìm một vòng, đi tới thuộc về nhị tinh danh sư khu vực, ngồi xuống."),t("br"),t("br"),n._v("“Khẩn cấp như vậy triệu tập chúng ta, muốn làm gì?”"),t("br"),t("br"),n._v("“Ta cũng không biết, có điều, trước mấy ngày nghe nói, muốn sớm tuyển bạt tham gia thi đấu người, đoán chừng là muốn trước mặt mọi người cử hành tuyển bạt thi đấu đi!”"),t("br"),t("br"),n._v("“Có khả năng! Không biết ai có thể bị tuyển bạt lên!”"),t("br"),t("br"),n._v("“Đây còn phải nói? Khẳng định là Phó Tiếu Trần, Phó sư!”"),t("br"),t("br"),n._v("“Cái kia không nhất định, ta lại thêm xem trọng Nhược Hoan công tử, Quân Nhược Hoan, hắn nhưng là thần tượng của ta!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mới vừa ngồi xuống, liền nghe đến chung quanh thảo luận thanh âm vang lên."),t("br"),t("br"),n._v("Mười ngày trước, đường chủ quyết định tiến hành tuyển bạt, ai có thể được tuyển chọn tin tức, liền huyên náo mặt trời lên cao, các loại thuyết pháp đều có, thậm chí có không ít địa phương đều coi đây là tiền đặt cược."),t("br"),t("br"),n._v("Rất nhiều danh sư biết nhau, cũng từng người đều có ủng hộ và tín nhiệm người."),t("br"),t("br"),n._v("Trương Huyền nghe một hồi, phát hiện cùng Tô sư, Lăng sư nói đồng dạng, thảo luận nhiều nhất chính là Nhược Hoan công tử cùng Phó Tiếu Trần."),t("br"),t("br"),n._v("Xem ra hai người này, tại mọi người trong suy nghĩ có được cực cao địa vị."),t("br"),t("br"),n._v("Theo ánh mắt của bọn hắn nhìn lại, quả nhiên thấy được mấy cái hơn người bóng người."),t("br"),t("br"),n._v("Đi đầu một cái, môi hồng răng trắng, tay cầm quạt xếp, phong độ nhẹ nhàng, cho người ta một loại mơ hồ thế giai công tử cảm giác, cặp mắt mang theo hào quang chói sáng, vừa nhìn liền biết tu vi không thấp."),t("br"),t("br"),n._v("“Xem ra đây chính là vị kia Nhược Hoan công tử, Quân Nhược Hoan!”"),t("br"),t("br"),n._v("Trước đó một mực kỳ quái, vì cái gì đường đường danh sư, những người khác không xưng hô “Quân sư”, ngược lại gọi “Nhược Hoan công tử”."),t("br"),t("br"),n._v("Nguyên lai, phần này khí chất, liền không giống bình thường, nếu không phải người mặc danh sư trường bào, thật không nghĩ tới là một vị đã đạt tới tứ tinh cấp bậc tuyệt thế thiên tài, mà sẽ tưởng rằng cái nào con em thế gia."),t("br"),t("br"),n._v("Nhược Hoan công tử cách đó không xa, một thanh niên song song đứng thẳng, mặc dù ngực huy chương bên trên ngôi sao là ba viên, thật đáng giận thế bên trên không hề yếu."),t("br"),t("br"),n._v("Dung mạo không tính là xuất chúng, ngược lại còn có chút hài nhi mập, thân thể cũng hơi có vẻ mập mạp, có điều, ánh mắt như điện, khí tức lại là cực mạnh, tu vi so với Quân Nhược Hoan, chỉ cao hơn chứ không thấp hơn, chỉ sợ cũng đạt đến Hóa Phàm tứ trọng trái phải!"),t("br"),t("br"),n._v("Đây chính là một cái khác danh khí rất lớn Phó Tiếu Trần."),t("br"),t("br"),n._v("Đang muốn nhìn xem còn lại ba người đều là dạng gì, liền nghe đến bên tai một cái tiếng cười vang lên: “Vị bằng hữu này lạ mặt rất ah, trước kia chưa bao giờ thấy qua, chẳng biết từ chỗ nào cái vương quốc tới?”"),t("br"),t("br"),t("br"),n._v("Quay đầu nhìn lại, chỉ thấy một thanh niên ở bên cạnh trên chỗ ngồi ngồi xuống."),t("br"),t("br"),n._v("Chừng ba mươi tuổi bộ dáng, trước ngực hai ngôi sao lấp lóe."),t("br"),t("br"),n._v("“Thiên Huyền vương quốc!” Trương Huyền trả lời."),t("br"),t("br"),n._v("“Thiên Huyền? Mấy cấp vương quốc?” Thanh niên gãi đầu một cái, dường như chưa từng nghe qua."),t("br"),n._v(" “Không có đẳng cấp, xem như... Bất nhập lưu đi!” Trương Huyền nói."),t("br"),t("br"),n._v("“Bất nhập lưu vương quốc?” Thanh niên ngẩn ngơ, lập tức xấu hổ cười một tiếng: “Như thế địa phương có thể xuất hiện một vị trẻ tuổi như vậy nhị tinh danh sư, quả thực bất phàm!”"),t("br"),t("br"),n._v("Biết đối phương là khách khí ngữ điệu, Trương Huyền cười cười, cũng không nhiều lời."),t("br"),t("br"),n._v("Mặc dù dọc đường Thiên Vũ, Hiên Viên, nhưng muốn nói ra thân, vẫn là Thiên Huyền."),t("br"),t("br"),n._v("“Danh sư thi đấu thế nhưng là thịnh sự, đối chúng ta danh sư tới nói, so chuyện gì đều quan trọng, tốn hao lại lớn đánh đổi, cũng muốn tới quan sát!” Thấy hắn không nói lời nào, thanh niên nói sang chuyện khác."),t("br"),t("br"),n._v("“Đúng vậy a!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Có thể hội tụ nhiều như vậy danh sư đến đây, đích xác là trước nay chưa có thịnh sự."),t("br"),t("br"),n._v("Danh sư tụ tập lẫn nhau thảo luận, học tập, đối sau này phát triển, có tác dụng cực lớn, chỉ sợ đây cũng là thi đấu người đề xuất dự tính ban đầu."),t("br"),t("br"),n._v("“Đúng rồi, lần chọn lựa này thi đấu, ngươi xem trọng ai?”"),t("br"),t("br"),n._v("Thanh niên mãn thị hưng phấn nhìn qua."),t("br"),t("br"),n._v("“Xem trọng ai? Ta còn không rõ lắm!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Cũng đúng, ngươi vừa tới, không rõ ràng bình thường! Vừa rồi bọn hắn thảo luận, ngươi chắc hẳn cũng nhìn thấy, Nhược Hoan công tử cùng Phó Tiếu Trần, là tiếng hô lớn nhất, Nhược Hoan công tử xuất sinh vạn quốc vương thành tứ đại gia tộc đứng đầu Quân gia, xuất sinh hậu đãi, càng là Vạn Quốc thành Tứ đại công tử, liền Triệu minh chủ con trai thái tử Triệu Huy đều bị đè ép một đầu. Phó Tiếu Trần Phó sư là Hải trưởng lão học sinh thân truyền, nếu như tuyển bạt hai người, bọn hắn chiến thắng tỷ lệ quả thực rất lớn.”"),t("br"),t("br"),n._v("Thanh niên nói: “Bất quá, ta lại thêm xem trọng Phùng trưởng lão học sinh, Phùng Mạc Sinh.”"),t("br"),t("br"),n._v("“Phùng Mạc Sinh?”"),t("br"),t("br"),n._v("“Không sai, Phùng sư ta từng gặp một mặt, làm người khiêm tốn khiêm tốn, nhưng là thực lực nhưng một chút không kém! Ba năm trước đây ta từng tiếp thụ qua chỉ điểm của hắn, khi đó, hắn chính là tam tinh đỉnh phong danh sư.” Nói ra vị này Phùng sư, thanh niên hai mắt tỏa ánh sáng, mang theo sùng bái."),t("br"),t("br"),n._v("“Còn còn lại hai cái, nghe nói đến từ những vương quốc khác, một cái gọi Lạc Khê, là vị mỹ nữ danh sư, người kia kêu là Đỗ Hổ... Đối bọn hắn thực lực, ta biết không nhiều, chẳng qua có thể từ những vương quốc khác tuyển ra đến, khẳng định cũng không đơn giản!”"),t("br"),t("br"),n._v("Nói đến đây, thanh niên đột nhiên thấp giọng, thoạt nhìn có chút thần thần bí bí: “Đúng rồi, ta nghe nói tham gia tuyển bạt thi đấu, không phải năm cái, mà là sáu cái! Đường chủ mười ngày trước liền tuyên bố muốn tiến hành tuyển bạt, kết quả một mực không có bắt đầu, nghe nói chính là đang chờ người này!”"),t("br"),t("br"),n._v("“Ồ? Ngươi biết người này?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Lão sư ta cũng là trưởng lão một trong, lúc ấy tham gia nghị hội, hình như là Tô sư, Lăng sư kiệt lực đề cử, kêu cái gì... Trương Huyền!”"),t("br"),t("br"),n._v("Thanh niên hừ một tiếng: “Nếu không phải cái tên này, tuyển bạt thi đấu khẳng định đã sớm bắt đầu, để cho người ta chờ lâu như vậy, nhưng vẫn không xuất hiện, thật là một cái hai hàng!”"),t("br"),t("br"),n._v("“Khụ khụ, hai hàng?” Trương Huyền ho khan một tiếng."),t("br"),t("br"),n._v("“Danh sư thi đấu trọng yếu như vậy sự tình, cái tên này thế mà cũng có thể đến muộn đến, để nhiều như vậy danh sư chờ lấy... Nếu là hắn thật rất lợi hại ngược lại cũng thôi, nghe nói chỉ là cái nhị tinh danh sư, hơn nữa tuổi tác thật giống không lớn... Chẳng biết tôn trọng tiền bối, tùy ý làm bậy, không phải hai hàng là cái gì? Nghe nói nếu không phải Tô sư, Lăng sư liều mạng ra sức bảo vệ, khẳng định danh ngạch đã sớm hết rồi!”"),t("br"),t("br"),n._v("Thanh niên vẻ mặt xem thường."),t("br"),t("br"),n._v("“Được rồi!” Trương Huyền vò đầu."),t("br"),t("br"),n._v("Bị người ở trước mặt mắng thành hai hàng, suy nghĩ một chút cũng là say."),t("br"),t("br"),n._v("Bất quá, lúc trước Tô sư nói với hắn thời điểm, chỉ cần trong vòng ba tháng đuổi tới Vạn Quốc thành là được, ai nghĩ đến còn có cái sớm tuyển bạt?"),t("br"),t("br"),n._v("“Không chỉ ta nói như vậy, những người khác cũng đều cho rằng như vậy! Thật không biết tiểu tử kia như thế nào, nếu như bị ta gặp lên, không phải thật tốt đánh một trận không thể!” Thanh niên hung hăng nói: “Nếu không phải các loại lần chọn lựa này thi đấu, ta đã sớm bế quan xung kích cảnh giới cao hơn, mà bây giờ, chỉ có thể kéo lấy...”"),t("br"),t("br"),n._v("Không nghĩ tới bản thân tới chậm, để không ít người ghi hận, Trương Huyền vẻ mặt cười khổ, đang muốn nói chuyện, liền nghe đến toàn bộ đại sảnh an tĩnh lại, ngẩng đầu nhìn lại, chỉ thấy Khang đường chủ cùng Tô sư đi đến, đi tới đám người phía trước nhất."),t("br"),t("br"),n._v("“Gõ vang khẩn cấp tụ hợp chuông, chắc hẳn chư vị cũng đều đoán được nguyên nhân!”"),t("br"),t("br"),n._v("Khang đường chủ vung tay lên: “Không sai, hôm nay cử hành danh sư thi đấu trước tuyển bạt, triệu tập mọi người tới, chính là muốn cùng một chỗ chứng kiến.”"),t("br"),t("br"),n._v("“Quả nhiên là tuyển bạt!”"),t("br"),t("br"),n._v("“Đúng vậy a, xem ra thật đoán đúng, nói như vậy, cái kia gọi Trương Huyền danh sư tới?”"),t("br"),t("br"),n._v("“Tới? Ở đâu?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được đường chủ xác nhận, mọi người khe khẽ bàn luận, ngắm nhìn chung quanh."),t("br"),t("br"),n._v("“Danh sư thi đấu, là Huyễn Vũ đế quốc Danh Sư đường cấp dưới rất nhiều phân bộ, lớn nhất thịnh sự, chúng ta trước kia thành tích, chắc hẳn không cần ta nhiều lời, các vị cũng đều biết... Lần này, vừa lúc là Vạn Quốc thành chủ nhà, ta hy vọng có thể có chút đột phá, đánh ra chúng ta phân bộ khí thế!”"),t("br"),t("br"),n._v("Khang đường chủ vòng quy một vòng."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Nghe nói như thế, mặc dù biết bản thân không có tư cách tham gia, dưới đài rất nhiều danh sư, cũng đều là kích động vẻ mặt đỏ lên, từng cái xiết chặt nắm đấm."),t("br"),t("br"),n._v("Làm Vạn Quốc thành phân bộ danh sư, nhìn thấy bản thân Danh Sư đường, mỗi lần tỷ thí, đều rơi vào cuối cùng, buồn bực trong lòng có thể nghĩ."),t("br"),t("br"),n._v("Lần này làm chủ nhà, nhất định muốn lấy được không giống thứ tự, nếu không, như thế nào đối mặt Phong Sư điện bên trong rất nhiều tiền bối!"),t("br"),t("br"),n._v("“Tốt, mấy người các ngươi lên đây đi!”"),t("br"),t("br"),n._v("Nói xong, Khang đường chủ vẫy tay một cái, Nhược Hoan công tử, Phó Tiếu Trần đám người đồng loạt đi tới."),t("br"),t("br"),n._v("Tổng cộng năm người."),t("br"),t("br"),n._v("“Ừm, Trương sư đâu?” Thấy Trương Huyền đồng thời không cùng bọn hắn cùng một chỗ, Khang đường chủ lông mày nhíu một cái."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Hắc hắc, ngươi nhìn, cái này cũng bắt đầu tuyển bạt, cái kia gọi Trương Huyền hai hàng thế mà còn chưa tới, chỉ mong đường chủ dưới cơn nóng giận hủy bỏ hắn danh ngạch...”"),t("br"),t("br"),n._v("Thấy Khang đường chủ tìm kiếm khắp nơi cái kia Trương sư, thanh niên hừ lạnh một tiếng, lắc đầu, nhịn không được nhìn về phía bên người vị này mới quen tiểu nhị: “Đúng rồi, huynh đệ, nói cái này nửa ngày còn không biết tên của ngươi đây!”"),t("br"),t("br"),n._v("“Ta?” Trương Huyền cười khổ đứng dậy."),t("br"),t("br"),n._v("“Dường như... Ta chính là trong miệng ngươi cái kia hai hàng... Trương Huyền!”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-539"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-541"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);