(window.webpackJsonp=window.webpackJsonp||[]).push([[1789],{2143:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-532-tuyen-bat-bat-đau"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-532-tuyen-bat-bat-đau"}},[n._v("#")]),n._v(" Chương 532: Tuyển bạt bắt đầu!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Ngươi, ngươi... Là Trương Huyền, Trương sư?”"),t("br"),t("br"),n._v("Thanh niên khẽ run rẩy, nước mắt ào ào, kém chút không có ngất ngay tại chỗ."),t("br"),t("br"),n._v("Hắn mặc dù ngoài miệng nói buồn bực, trên thực tế cũng biết, có thể được tô, lăng hai vị trưởng lão vừa ý, lớn nhất cường độ đề cử, vị này Trương sư khẳng định không chỉ là nhị tinh danh sư đơn giản như vậy."),t("br"),t("br"),n._v("Chỉ là... Cùng là nhị tinh, đối phương có thể đi vào tuyển bạt, hắn lại không thể, lòng có ghen ghét, lúc này mới nhìn thấy cái người xa lạ, tùy tiện phát lẩm bẩm, quen thuộc người, ngược lại không cách nào lối ra."),t("br"),t("br"),n._v("Dù sao cũng không nhận ra, thuận miệng nói một chút, phát tiết một chút phẫn uất, thuận tiện qua qua miệng nghiện, không nghĩ tới... Trực tiếp tìm được chính chủ, còn tưởng là nhân gia mặt nói hắn là hai hàng!"),t("br"),t("br"),n._v("Em gái ngươi!"),t("br"),t("br"),n._v("Ngươi không phải muốn tham gia tuyển bạt ư? Không nên cùng Nhược Hoan công tử, Phó Tiếu Trần bọn hắn ở một chỗ sao? Chạy nơi này ngồi làm gì..."),t("br"),t("br"),n._v("Thanh niên chỉ cảm thấy trong đầu Thiên Lôi từng cơn, muốn tự tử đều có."),t("br"),t("br"),n._v("Cái này gọi chuyện gì... Tùy tiện tìm một người nói chuyện phiếm, chính là vị này ai cũng chưa thấy qua Trương sư... Cũng quá củ chuối đi đi!"),t("br"),t("br"),n._v("Chính vẻ mặt lo lắng, sợ đối phương tìm hắn để gây sự, chỉ thấy vị này Trương sư, nhấc chân đi về phía phía trước nhất đi tới."),t("br"),t("br"),n._v("“Không trách tội ta?”"),t("br"),t("br"),n._v("Thấy đối phương trước khi đi đều không có nói câu nào, thanh niên miệng há lớn, từ đầu đến cuối không thể tin được."),t("br"),t("br"),n._v("Danh sư, chú trọng thân phận, ở trước mặt mắng người ta hai hàng, tương đương với không nể mặt mũi, thật muốn tìm hắn để gây sự, cũng chỉ có thể nhẫn nhịn lấy... Đối phương một câu không nói liền đi, đơn phần khí độ này, cũng không phải là bình thường danh sư có thể có được."),t("br"),t("br"),n._v("“Không biết hắn... Có thể hay không bị tuyển bạt bên trên...”"),t("br"),t("br"),n._v("Ngăn chặn buồn bực trong lòng, nhìn về phía trước."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Hắn chính là cái kia Trương Huyền?”"),t("br"),t("br"),n._v("“Thật giống so trong truyền thuyết lại thêm tuổi trẻ!”"),t("br"),t("br"),n._v("“Đúng vậy a, không đủ hai mươi, hơn nữa còn chỉ là cái nhị tinh danh sư, có tư cách gì tham gia tuyển bạt?”"),t("br"),t("br"),n._v("“Cái này không nhất định, hắn mặc dù mang theo nhị tinh huy chương, chưa hẳn chỉ có nhị tinh, không phải người nào đều ưa thích đem thực lực tú ở trước ngực!”"),t("br"),t("br"),n._v("“Như thế... Nếu như không có chút bản lĩnh thật sự, Tô sư, Lăng sư không có khả năng liều mạng đề cử. Có thể coi là không chỉ nhị tinh, niên kỷ trong này bày biện, cũng hẳn là sẽ không quá lợi hại đi!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nhìn thấy đi lên phía trước thanh niên, tất cả mọi người không thể tưởng tượng nổi."),t("br"),t("br"),n._v("Sớm nghe nói vị này Trương sư, tuổi không lớn lắm, lại chỉ là nhị tinh danh sư, tận mắt thấy mới biết được... Thực sự tuổi trẻ có chút quá mức."),t("br"),t("br"),n._v("Danh sư, không chỉ có muốn minh lý, biết vật, càng quan trọng hơn còn muốn có vượt qua bình thường người tu luyện tri thức tích lũy."),t("br"),t("br"),n._v("Coi như vị này thiên phú cực giai, so với người ta thiếu đi mười năm đọc sách thời gian, lại có thể gom góp tích luỹ bao nhiêu tri thức?"),t("br"),t("br"),n._v("“Đã người đã đến đông đủ, ta liền nói một chút lần chọn lựa này quy tắc!”"),t("br"),t("br"),n._v("Thấy Trương Huyền đi tới, Khang đường chủ lúc này mới nhẹ gật đầu, nhìn quanh một vòng, nói: “Khảo hạch tổng cộng chia làm năm cửa, đối danh sư rất nhiều điều kiện, tiến hành so sánh! Lấy được thứ nhất, ghi sáu điểm, thứ hai ghi năm điểm, theo thứ tự suy ra, một tên sau cùng, ghi một điểm.”"),t("br"),t("br"),n._v("“Năm cửa xuống, điểm tích lũy hàng trước nhất hai vị, thông qua tuyển bạt, tham gia hai mươi tám thế lực lớn liên hợp tổ chức thi đấu!”"),t("br"),t("br"),n._v("Nói xong, Khang đường chủ bàn tay lớn vẫy một cái: “Chư vị, còn có nghi ngờ ư?”"),t("br"),t("br"),n._v("“Không có!”"),t("br"),t("br"),n._v("Mọi người vội vàng gật đầu, Quân Nhược Hoan mấy người cũng lên tiếng."),t("br"),t("br"),n._v("“Cửa ải, ta đã cùng tất cả trưởng lão thiết kế tốt, phân biệt từ tâm cảnh, phân biệt công pháp, nhớ, giảng bài, chỉ điểm năm cái phương diện vào tay.”"),t("br"),t("br"),n._v("Thấy mọi người không có vấn đề, Khang đường chủ cùng hàng trước nhất các trưởng lão liếc mắt nhìn nhau, nhẹ gật đầu: “Hiện tại liền tiến hành cửa thứ nhất tỷ thí, tâm cảnh khắc độ khảo hạch. Ngô trưởng lão, liền làm phiền ngươi!”"),t("br"),t("br"),n._v("“Đường chủ khách khí, đây là ta phải làm!”"),t("br"),t("br"),n._v("Một cái lão giả cười lên tiếng, đi tới phía trước, cổ tay khẽ đảo, một cái cổ cầm xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("“Không nghĩ tới cửa thứ nhất, để Ngô trưởng lão xuất mã!” Trong đại sảnh có người dám."),t("br"),t("br"),n._v("“Thế nào, chẳng lẽ vị này Ngô trưởng lão có cái gì không giống?” Bên cạnh hắn một người không hiểu hỏi."),t("br"),t("br"),n._v("“Đương nhiên không giống! Ngô trưởng lão cùng đường chủ đồng dạng, cũng là tứ tinh đỉnh phong danh sư, nhưng... Hắn có cái rất ít thấy phụ trợ chức nghiệp, đối ma luyện tâm cảnh, có rất lớn trợ giúp!” Cái thứ nhất nói chuyện người biết chuyện nói."),t("br"),t("br"),n._v("“Ồ? Nghề nghiệp gì có thể đối ma luyện tâm cảnh hữu dụng?”"),t("br"),t("br"),n._v("Người thứ hai kỳ quái."),t("br"),t("br"),n._v("Danh sư, cơ sở nhất đồ vật, chính là tâm cảnh, tâm cảnh khắc độ càng cao, đối danh sư lý giải, cũng là càng dễ dàng, vị này Ngô trưởng lão lại có am hiểu ma luyện tâm cảnh chức nghiệp, đến cùng là cái gì, có thể được đến như thế khen ngợi?"),t("br"),t("br"),n._v("“Ma âm sư!” Người biết chuyện nói."),t("br"),t("br"),n._v("“Ma âm sư? Loại kia có thể lấy âm nhạc loạn tâm trí người đặc thù chức nghiệp?” Người thứ hai sững sờ."),t("br"),t("br"),n._v("“Ừm!” Người biết chuyện nói: “Ma âm sư, mặc dù chỉ là hạ cửu lưu chức nghiệp, nhưng có thể sử dụng âm nhạc mê hoặc nhân tâm, khó lòng phòng bị, mười phần kinh khủng. Chúng ta Vạn Quốc thành không có ma âm sư công biết, Ngô trưởng lão cái nghề nghiệp này, cũng coi là phần độc nhất!”"),t("br"),t("br"),n._v("“Đúng vậy a, để ma âm sư đến khảo thí tâm cảnh khắc độ, cửa thứ nhất này, sẽ rất khó ah!”"),t("br"),t("br"),n._v("Người thứ hai cảm khái."),t("br"),t("br"),n._v("Bình thường khảo hạch danh sư, đều là dùng khảo thí tâm cảnh Minh Lý Ngọc Tinh khảo thí, chỉ cần trị số lớn, tâm cảnh khắc độ liền cao, lần này dùng ma âm sư đến tỷ thí, rõ ràng độ khó lớn hơn."),t("br"),t("br"),n._v("..."),t("br"),n._v(" Hai người trong lúc nói chuyện với nhau, cầm trong tay cổ cầm Ngô trưởng lão đi tới ngay phía trước, nhẹ nhàng đem đàn đặt lên bàn, nhàn nhạt nhìn về phía tham gia tuyển bạt mấy người."),t("br"),t("br"),n._v("“Cửa thứ nhất, khảo hạch tâm cảnh! Làm danh sư, tâm cảnh, không chỉ muốn khắc độ cao, càng quan trọng hơn là, có thể chống cự quấy nhiễu, tại phân tạp môi trường bên trong, rõ ràng hơn phân tích cùng xử lý sự tình! Ta chịu đường chủ yêu cầu, khảo hạch các ngươi tâm cảnh, tự nhiên sẽ dốc hết toàn lực, hi vọng các ngươi cũng có thể chuẩn bị sẵn sàng.”"),t("br"),t("br"),n._v("Nói xong, bàn tay khẽ vỗ, trên bàn xuất hiện mấy cái hình tròn sắt bàn."),t("br"),t("br"),n._v("“Đây là trận pháp sư thường dùng trống không trận bàn, một lát nữa, ta sẽ đàn tấu quấy nhiễu linh hồn , mà các ngươi, có thể ở trên đây vẽ tranh. Một khúc kết thúc, ai khắc hoạ hoa văn nhiều nhất, nhẵn nhụi nhất, người đó là quán quân!”"),t("br"),t("br"),n._v("Ngô trưởng lão nhìn về phía mọi người: “Nói trước một tiếng, của ta Phong Ma khúc, mỗi lần chỉ có thể đối một người đưa đến tác dụng, các ngươi ai tới trước?”"),t("br"),t("br"),n._v("“Tốt biến thái khảo hạch!”"),t("br"),t("br"),n._v("“Phong Ma khúc, ta tại thư tịch bên trên thấy qua, có thể khiến người ta tâm cảnh mê loạn, khó mà tự kiềm chế. Bình thường người tu luyện, rơi vào trong đó, sẽ cùng say rượu, tư duy hỗn loạn, đông tây nam bắc đều không phân biệt được, loại tình huống này điêu khắc đồ vật... Khẳng định là loạn!”"),t("br"),t("br"),n._v("“Đúng vậy a, loại tình huống này đừng nói tại lớn chừng bàn tay trống không trận bàn bên trên điêu khắc tế văn, có thể hay không đứng vững, cũng còn khó nói!”"),t("br"),t("br"),n._v("“Mấu chốt nhất là, hắn xuất ra đều là tam tinh trống không trận bàn, ở phía trên điêu khắc, không chỉ muốn khống chế tốt tinh thần, đối lực lượng khống chế cũng phải có cực mạnh, nếu không, rất dễ dàng vỡ vụn!”"),t("br"),t("br"),n._v("“Cửa này, không chỉ khảo hạch tâm cảnh, còn khảo hạch tại tư duy hỗn loạn tình huống dưới, như thế nào lực khống chế lượng... Khó, thực sự quá khó khăn!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe xong Ngô trưởng lão cửa thứ nhất khảo hạch nội dung, phía dưới rất nhiều danh sư, tất cả đều hít vào một ngụm khí lạnh."),t("br"),t("br"),n._v("Ma âm sư, tuy chỉ là hạ cửu lưu chức nghiệp, đã có thể có như thế đại danh khí, khẳng định không đơn giản! Làm người tu luyện, có thể tại Phong Ma khúc bên trong, duy trì trấn tĩnh, không bị công phá tâm lý phòng tuyến, cũng rất không tệ, còn muốn tại trống không trận bàn bên trên điêu khắc..."),t("br"),t("br"),n._v("Độ khó rất lớn!"),t("br"),t("br"),n._v("Không nói những này tham gia tuyển bạt người, coi như phía trước nhất rất nhiều tứ tinh trưởng lão, có thể làm được, liền không có mấy cái."),t("br"),t("br"),n._v("“Ma âm sư? Điêu khắc?”"),t("br"),t("br"),n._v("Cùng mọi người khiếp sợ không giống, Trương Huyền mày nhăn lại."),t("br"),t("br"),n._v("Trước đó nhìn qua thư tịch, đối với ma âm sư cái nghề nghiệp này, cũng hơi có ghi chép, có điều, chỉ sơ lược, cụ thể năng lực, cũng không cảm kích."),t("br"),t("br"),n._v("Vốn cho rằng cùng thư hoạ đồng dạng, đối âm nhạc tinh thông đến cực hạn, hiện tại xem ra, chỉ sợ không có đơn giản như vậy."),t("br"),t("br"),n._v("Nghe được Ngô sư bàn giao, Nhược Hoan công tử cười nhạt một tiếng, hướng mấy cái đối thủ nhìn sang, muốn nhìn một chút phản ứng của bọn hắn."),t("br"),t("br"),n._v("Phó Tiếu Trần vẻ mặt yên tĩnh, dường như sớm biết sẽ có cái khảo hạch này, ngay tại điều chỉnh trạng thái."),t("br"),t("br"),n._v("Mấy người khác cũng tất cả đều vận chuyển tâm cảnh, vẻ mặt nghiêm túc, duy chỉ có một thanh niên, mày nhăn lại, vẻ mặt mê mang."),t("br"),t("br"),n._v("Chính là để bọn hắn đợi vài ngày Trương Huyền."),t("br"),t("br"),n._v("“Vừa nhìn liền biết chưa từng nghe qua ma âm sư!”"),t("br"),t("br"),n._v("Thấy hắn vẻ mặt mê mang, Nhược Hoan công tử liền biết, cái tên này khẳng định chưa từng nghe qua ma âm sư, bản thân đường đường Tứ đại công tử, tứ tinh sơ kỳ danh sư, thế mà cùng kẻ như vậy cùng một chỗ tỷ thí..."),t("br"),t("br"),n._v("Suy nghĩ một chút đều cảm thấy buồn bực."),t("br"),t("br"),n._v("“Trương sư đúng không, mặc dù ngươi để chúng ta đợi tiếp cận mười ngày, nhưng ngươi đường xa mà đến, là khách, không bằng như vậy... Cửa này khảo hạch, ngươi tới trước!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Nhược Hoan công tử nhìn qua."),t("br"),t("br"),n._v("“Ta?” Trương Huyền lắc đầu: “Đừng khách khí, vẫn là ngươi tới đi, ta xem trước một chút!”"),t("br"),t("br"),n._v("Mặc dù đối ma âm sư không hiểu rõ, nhưng đã có thể trở thành đặc thù chức nghiệp, khẳng định không đơn giản, chưa hề tiếp xúc qua, vẫn là xem trước một chút đến cùng chuyện gì xảy ra lại nói."),t("br"),t("br"),n._v("“Xem trước một chút?”"),t("br"),t("br"),n._v("Nhược Hoan công tử cười nhạo một tiếng: “Có phải hay không cảm thấy Ngô trưởng lão hiện tại tinh lực dồi dào, thực lực mạnh nhất, cái thứ nhất bên trên, tương đương với cái thứ nhất làm liều đầu tiên, chịu đến công kích, cũng mãnh liệt nhất?”"),t("br"),t("br"),n._v("“Đã khảo hạch là tâm cảnh, liền muốn có dũng cảm tiến tới khí thế, lo lắng những này trù trừ không tiến, nói rõ lo lắng, sợ hãi, tâm cảnh đã có sơ hở, còn muốn cầm cẩn thận thành tích? Nói đùa cái gì!”"),t("br"),t("br"),n._v("Cửa này khảo hạch là tâm cảnh, lên trước, phía sau bên trên, kỳ thật cũng là một loại khảo nghiệm."),t("br"),t("br"),n._v("Biết rõ cái thứ nhất bên trên, chịu đến công kích mạnh nhất mà trù trừ không tiến lên lời nói, tâm cảnh chẳng khác nào có thiếu hụt."),t("br"),t("br"),n._v("Mà một khi có thiếu hụt, gặp lại ma âm sư Phong Ma khúc, liền vô cùng có khả năng bị thừa lúc vắng mà vào, lại không cách nào chống lại."),t("br"),t("br"),n._v("Nhược Hoan công tử biết đạo lý này, cố ý nói ra lời kia, chính là để vị này Trương Huyền, nếm chút khổ sở."),t("br"),t("br"),n._v("Đã cạnh tranh với nhau, tự nhiên là kẻ địch, hắn nhưng không có cho kẻ địch biện pháp dự phòng thói quen."),t("br"),t("br"),n._v("“Đã e ngại sợ hãi, vậy ta trước hết tới đi!”"),t("br"),t("br"),n._v("Lần nữa đả kích vị này Trương sư thoáng cái, Nhược Hoan công tử nhẹ nhàng cười một tiếng, đi tới bàn trước mặt, liền muốn cầm lấy một cái trống không trận bàn."),t("br"),t("br"),n._v("“Nhược Hoan công tử đừng có gấp, ta nhìn, vẫn là ta tới đi!”"),t("br"),t("br"),n._v("Lại một cái thanh âm nhàn nhạt vang lên, Phó Tiếu Trần đi tới."),t("br"),t("br"),n._v("Hắn đã điều chỉnh tốt tâm cảnh, hai mắt yên tĩnh, vẻ mặt thản nhiên."),t("br"),t("br"),n._v("“Thế nào? Ngươi muốn cùng ta tranh?” Nhược Hoan công tử nhướng mày lên."),t("br"),t("br"),n._v("“Tranh? Ngươi suy nghĩ nhiều, nhờ vào Ngô trưởng lão thực lực, bất kể đệ nhất vẫn là thứ hai, thậm chí thứ năm thứ sáu, chịu đến Phong Ma khúc ảnh hưởng khẳng định đều là giống nhau, tuần tự không tuần tự, không có bất kỳ cái gì ảnh hưởng... Ngươi dùng để đối phó những người khác thủ đoạn, đối ta không dùng!”"),t("br"),t("br"),n._v("Phó Tiếu Trần thản nhiên nói."),t("br"),t("br"),n._v("Hoành Tảo Thiên Nhai nói"),t("br"),t("br"),n._v("Buổi chiều bắt đầu thi vụ biết, ngày mai học sinh liền bắt đầu thi cấp ba, chúc thi cấp ba các học sinh, đều có thể kiểm tra cái thành tích tốt! Đáng thương lão nhai lại muốn vội vàng ba ngày. Mồ hôi. Vừa nghĩ tới không có thời gian gõ chữ, nước mắt liền không cầm được chảy xuống.. Nhân sinh thật sự là tịch mịch như tuyết!"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-540"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-542"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);