(window.webpackJsonp=window.webpackJsonp||[]).push([[1792],{2146:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-535-triet-đe-sup-đo-ngo-truong-lao-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-535-triet-đe-sup-đo-ngo-truong-lao-ha"}},[n._v("#")]),n._v(" Chương 535: Triệt để sụp đổ Ngô trưởng lão (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Đây là có chuyện gì?”"),t("br"),t("br"),n._v("“Ngô trưởng lão muốn làm gì?”"),t("br"),t("br"),n._v("Bên này giao chiến say sưa, phía sau danh sư, từng cái mắt lớn trừng mắt nhỏ, đều cảm thấy có chút bối rối."),t("br"),t("br"),n._v("Thế nào cái tình huống?"),t("br"),t("br"),n._v("Vừa rồi Ngô trưởng lão không còn rất bình thường ư?"),t("br"),t("br"),n._v("Tại sao lại là trên đầu bốc khói, lại là nổ nát vụn cái bàn, hiện tại càng tốt hơn, còn không ngừng đi loạn, hình như đang thi triển trận pháp gì..."),t("br"),t("br"),n._v("Chẳng phải khảo hạch tâm cảnh... Còn nhất kinh nhất sạ, lại nhảy lại nhảy sao?"),t("br"),t("br"),n._v("“Ngô trưởng lão... Có phải điên rồi hay không?”"),t("br"),t("br"),n._v("Trong đám người không biết ai hô một tiếng, làm cho tất cả mọi người kìm lòng không được yên lặng."),t("br"),t("br"),n._v("Dường như... Hắn hiện tại bộ dáng, quả thực cùng như bị điên..."),t("br"),t("br"),n._v("“Không phải điên rồi, mà là Dao Cầm Bát Quái bộ! Chỉ là... Ngô trưởng lão thế nào đem chiêu này xuất ra?”"),t("br"),t("br"),n._v("Trong đám người một cái tứ tinh danh sư nhướng mày."),t("br"),t("br"),n._v("“Ngươi biết cái này bộ pháp?”"),t("br"),t("br"),n._v("Ngồi ở một bên một vị trưởng lão, không rõ ràng cho lắm, nhịn không được nhìn qua."),t("br"),t("br"),n._v("“Đương nhiên biết, đây là Ngô trưởng lão công kích mạnh nhất thủ đoạn, đem trận pháp cùng ma âm hoàn mỹ phối hợp...”"),t("br"),t("br"),n._v("Nói đến đây, vị này tứ tinh danh sư dừng lại, nói: “ ngươi hẳn là còn có thể nhớ a!”"),t("br"),t("br"),n._v("“Đương nhiên nhớ kỹ, mệnh danh Vạn Quốc thành trăm năm qua kinh khủng nhất tai hoạ! Triệu minh chủ phái ra vô số cao thủ, đều không thể làm gì, cuối cùng nhờ giúp đỡ Danh Sư đường, là ngươi cùng Ngô trưởng lão liên thủ giải quyết!” Vị thứ hai trưởng lão gật đầu nói."),t("br"),t("br"),n._v("Thất Kiếm Sát tai họa, là Vạn Quốc thành một lần ác tính sự kiện, hung thủ giết người, mỗi lần động thủ, cũng sẽ ở trên người người chết lưu lại bảy cái lỗ thủng, mặc kệ thực lực mạnh cỡ nào, cho dù là Hóa Phàm tứ trọng, đồng dạng bảy kiếm chém giết!"),t("br"),t("br"),n._v("Liên tục chém giết hơn mười vị Hóa Phàm tứ trọng cường giả, đưa tới toàn bộ liên minh khủng hoảng, Triệu minh chủ tự mình dẫn người vây bắt, kết quả vẫn là để đối phương trốn."),t("br"),t("br"),n._v("đọcngantruyen.com/"),t("br"),n._v("Không có cách nào xin giúp đỡ đến Danh Sư đường, lúc ấy Khang đường chủ còn không có kế nhiệm, tiền nhiệm đường chủ phái Ngô trưởng lão cùng trước mắt vị này, đồng loạt ra tay, tập hợp hơn mười vị Hóa Phàm tứ trọng cường giả, đem nó vòng vây tại ngõ cụt, lúc này mới thành công chém giết."),t("br"),t("br"),n._v("Chuyện này huyên náo động tĩnh rất lớn, để cho người ta nghe đến đã biến sắc, không ít lão nhân, đều không muốn nói lên, thế nào hắn đột nhiên nhấc lên?"),t("br"),t("br"),n._v("“Lúc ấy cùng vị kia Thất Kiếm Sát, ngõ hẹp gặp nhau, thực lực của đối phương mạnh, so với thời khắc này Khang đường chủ đều không hề yếu, là Ngô trưởng lão dùng tới Dao Cầm Bát Quái bộ, đối với hắn tiến hành âm ba công kích, nhiễu loạn tâm trí, lúc này mới một lần hành động đắc thắng! Nếu không... Chỉ bằng thực lực của chúng ta, muốn lưu lại, gần như không có khả năng!”"),t("br"),t("br"),n._v("Cái thứ nhất nói chuyện trưởng lão sắc mặt ngưng trọng."),t("br"),t("br"),n._v("Ngày đó cảnh tượng, đến bây giờ còn rõ mồn một trước mắt, nếu không phải Ngô trưởng lão thời khắc mấu chốt, thi triển ma âm năng lực, nhiễu loạn đối phương tinh thần, đừng nói chém giết, làm không cẩn thận, toàn quân bị diệt, đều không đả thương được một sợi lông!"),t("br"),t("br"),n._v("Có thể xưng là Vạn Quốc thành trong vòng trăm năm, mối họa lớn nhất, Thất Kiếm Sát tuyệt không phải chỉ là hư danh, như quỷ mị bảy kiếm, coi như hiện tại nhớ tới, đều cảm thấy phía sau toát ra mồ hôi lạnh, không cách nào chống lại."),t("br"),t("br"),n._v("“Đối phó Thất Kiếm Sát, dùng tới cái này bộ pháp? Cái kia...”"),t("br"),t("br"),n._v("Thứ hai trưởng lão sắc mặt tái đi, thân thể phát run, Thất Kiếm Sát, thế nhưng là có thể so với đường chủ nhân vật tuyệt thế, loại nhân vật này, Dao Cầm Bát Quái bộ phối hợp Phong Ma khúc, đều hữu hiệu quả, cái kia... Đối diện thanh niên có thể chịu được ư?"),t("br"),t("br"),n._v("Một khi không chịu nổi, chẳng phải sẽ làm trận giết chết?"),t("br"),t("br"),n._v("“Ngô trưởng lão chuyện gì xảy ra? Thế nào đối một cái vãn bối dùng ra loại thủ đoạn này?”"),t("br"),t("br"),n._v("Rõ ràng chuyện gì xảy ra, cái thứ hai trưởng lão khí sắc mặt đỏ lên."),t("br"),t("br"),n._v("Vị này Trương Huyền, coi như tới chậm, không theo lẽ thường ra bài, dù sao cũng là vãn bối, ngươi đường đường Danh Sư đường trưởng lão, tứ tinh đỉnh phong danh sư, đối với hắn bên dưới như thế ngoan thủ, không khỏi quá phận đi!"),t("br"),t("br"),n._v("“Ta cũng không biết, bất quá... Tại sao ta cảm giác, hắn mặc dù dùng ra chiêu này, không những không có chiếm được tiện nghi, còn ở vào hạ phong?” Cái thứ nhất nói chuyện trưởng lão nhìn một hồi, vẻ mặt cổ quái."),t("br"),t("br"),n._v("“Hạ phong?”"),t("br"),t("br"),n._v("Cái thứ hai trưởng lão sững sờ, nhịn không được nhìn sang, vừa nhìn phía dưới, con ngươi co rụt lại."),t("br"),t("br"),n._v("Chỉ thấy lúc này Ngô trưởng lão, không chỉ trên đầu bốc lên hơi nước, toàn thân trên dưới đều khí tức sôi trào, tựa như bị mới vừa ném tới trong nồi nấu đồng dạng."),t("br"),t("br"),n._v("Dưới chân bước chân cứ việc không có chút nào ngừng, nhưng đi một bước, phun một ngụm máu tươi, như là bị người dùng cự chùy, không ngừng đấm vào ngực, kìm lòng không được."),t("br"),t("br"),n._v("Mẹ nó!"),t("br"),t("br"),n._v("Đến cùng xảy ra chuyện gì?"),t("br"),t("br"),n._v("Thi triển liền Thất Kiếm Sát đều không chịu nổi âm ba công kích người khác, bản thân cũng không ngừng thổ huyết... Một màn trước mắt cũng không tránh khỏi quá quỷ dị đi!"),t("br"),t("br"),n._v("“Là vị này Trương sư...”"),t("br"),t("br"),n._v("Cái thứ nhất trưởng lão, rốt cuộc minh bạch tới, thanh âm mang theo thanh âm rung động."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("“Vị này Trương sư, dùng trường thương gõ trận bàn thanh âm, nghe tuy nhiên hỗn tạp loạn, nhưng mỗi một cái đều đụng vào Phong Ma khúc yếu nhất khâu, để Ngô trưởng lão chân khí trong cơ thể chịu đến xao động, cuối cùng bị thương...”"),t("br"),t("br"),n._v("Vị thứ nhất trưởng lão nói."),t("br"),t("br"),n._v("“Còn có thể như vậy?” Cái thứ hai trưởng lão con mắt trợn tròn."),t("br"),t("br"),n._v("Làm tứ tinh danh sư, kiến thức rộng rãi, có thể loại tình huống này, vẫn là chưa từng nghe thấy."),t("br"),t("br"),n._v("“Ta cũng cảm thấy kỳ quái, có điều, loại trừ cái này, ta cũng nghĩ không ra cái gì khác lý do!” Cái thứ nhất trưởng lão thanh âm trầm thấp, dường như phân tích ra kết quả, ngay cả hắn, cũng không dám tin tưởng."),t("br"),t("br"),n._v("Một cái Hóa Phàm nhất trọng đỉnh phong tiểu gia hỏa, dùng thương cùng trận bàn gõ, cứ thế mà chấn tứ tinh ma âm sư thổ huyết..."),t("br"),t("br"),n._v("Cái này... Đến cùng phải hay không thật?"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Lấy âm phá âm!”"),t("br"),t("br"),n._v("Đem một màn này nhìn ở trong mắt, Khang đường chủ nhớ tới cái gì, khuôn mặt cũng là tái đi."),t("br"),t("br"),n._v("“Lấy âm phá âm?” Tô, lăng hai vị trưởng lão nhịn không được nhìn qua."),t("br"),t("br"),n._v("“Ừm, đây là cấp cao ma âm sư, đối phó cấp thấp ma âm sư phương pháp, cùng võ giả nghiền ép đồng dạng, lợi dụng thanh âm công kích đối phương sóng âm bên trong nhược điểm, dẫn đến đối phương chân khí hỗn loạn, bản thân bị trọng thương!”"),t("br"),t("br"),n._v("Khang đường chủ chậm rãi nói."),t("br"),n._v(" “Cái kia... Còn xin đường chủ ra tay, cứu Trương sư, hắn vô cùng có khả năng đại biểu chúng ta Danh Sư đường tham gia thi đấu, tuyệt đối không thể xảy ra chuyện gì...”"),t("br"),t("br"),n._v("Tô sư vẻ mặt lo lắng."),t("br"),t("br"),n._v("“Hắn xảy ra chuyện?” Khang đường chủ da mặt co lại: “Xảy ra chuyện chính là Ngô trưởng lão, hơn nữa nhìn bộ dáng, sắp không kiên trì nổi!”"),t("br"),t("br"),n._v("“Không kiên trì nổi?”"),t("br"),t("br"),n._v("Tô sư, Lăng sư lúc này mới phát hiện, Ngô trưởng lão vừa đi vừa thổ huyết."),t("br"),t("br"),n._v("“Cái kia... Đường chủ, chỉ là khảo hạch, không cần thiết làm bản thân bị trọng thương... Còn xin ra tay ngăn cản!”"),t("br"),t("br"),n._v("Lăng sư nói."),t("br"),t("br"),n._v("“Ngăn cản? Lấy âm phá âm, cùng so đấu chân khí đồng dạng, trừ phi một phương bị thua, nếu không thì không có cách nào dừng lại! Nếu có thể đình chỉ, vừa rồi Ngô trưởng lão đã sớm ngừng... Còn cần đến thi triển Dao Cầm Bát Quái bộ, thậm chí bị chấn sắp chết rồi?”"),t("br"),t("br"),n._v("Khang đường chủ lắc đầu."),t("br"),t("br"),n._v("Ma âm công kích, liên lụy đến công kích linh hồn, ảo diệu phức tạp, cùng chân khí hoàn toàn khác biệt, không kiên trì nổi, nhiều nhất bị thương, chỉ khi nào ngăn cản, làm không cẩn thận liền sẽ xung kích trong óc, để người tu luyện biến thành ngớ ngẩn!"),t("br"),t("br"),n._v("Chính vì vậy, loại này đối bính, một khi bắt đầu, trừ phi có người bị thua, nếu không, tuyệt không có khả năng dừng lại!"),t("br"),t("br"),n._v("“Không phải khảo hạch tâm cảnh khắc độ ư? Thế nào... Biến thành âm ba công kích?”"),t("br"),t("br"),n._v("Tô sư hai người vẻ mặt trắng bệch."),t("br"),t("br"),n._v("Không phải khảo hạch tâm cảnh ư? Đánh như thế nào đi lên?"),t("br"),t("br"),n._v("Còn làm ra không chết không thôi cục diện?"),t("br"),t("br"),n._v("Sớm biết cái tên này sẽ gặp rắc rối, thật không nghĩ đến, khảo hạch cái tâm cảnh, cũng huyên náo động tĩnh như thế lớn..."),t("br"),t("br"),n._v("Tô sư, Lăng sư nhìn nhau, đều cảm thấy có chút phát điên."),t("br"),t("br"),n._v("“Ta cũng... Không hiểu rõ!”"),t("br"),t("br"),n._v("Khang đường chủ càng là vẻ mặt mất tinh thần."),t("br"),t("br"),n._v("Đường đường Danh Sư đường đường chủ, vạn quốc liên minh đệ nhất nhân, đối mặt loại tình huống này, bó tay toàn tập, suy nghĩ một chút cũng đủ buồn bực."),t("br"),t("br"),n._v("Mấu chốt nhất là... Tại sao lại xuất hiện loại tình huống này, liền hắn đều không có hiểu rõ!"),t("br"),t("br"),n._v("Thật sự là say."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Xảy ra chuyện gì?”"),t("br"),t("br"),n._v("“Hắn đây là khắc hoạ trận bàn?”"),t("br"),t("br"),n._v("Cái khác danh sư khiếp sợ dục tiên dục tử, Lạc Hoa công tử, Phó Tiếu Trần mấy người cũng trừng to mắt, cảm thấy trái tim đều nhanh nhảy ra ngoài."),t("br"),t("br"),n._v("Vừa rồi bọn hắn tự mình đã trải qua Phong Ma khúc công kích, biết cái này khúc nhạc mạnh mẽ!"),t("br"),t("br"),n._v("Ngô trưởng lão ngồi tại nguyên chỗ, mây trôi nước chảy đều đỡ không nổi, hiện tại đi lấy tám quẻ bộ, làm sao có thể chống cự?"),t("br"),t("br"),n._v("Nhưng vì sao... Hắn đối diện người thanh niên kia, không có việc gì?"),t("br"),t("br"),n._v("“Hẳn là cái tên này lấy được một loại nào đó cổ quái âm nhạc, mượn nhờ trận bàn gõ vang, khắc chế Ngô trưởng lão Phong Ma khúc!” Qua nửa ngày, Phó Tiếu Trần nhớ tới một loại khả năng, nhịn không được nói."),t("br"),t("br"),n._v("Loại trừ thuyết pháp này, thực sự nghĩ không ra cái khác."),t("br"),t("br"),n._v("Nếu như không có đoán sai, khẳng định là cái này gọi Trương Huyền gia hỏa, không biết cái gì vận khí cứt chó, học xong một loại khắc chế Ngô trưởng lão nhạc khúc, lúc này mới dẫn đến trưởng lão bị động như vậy."),t("br"),t("br"),n._v("“Khắc chế trưởng lão Phong Ma khúc lại như thế nào? Lần khảo hạch này chính là tâm cảnh, kiểm nghiệm chính là một khúc hoàn tất, tại trận bàn bên trên khắc vẽ hoa văn số lượng cùng đồ hình! Hắn đem trận bàn làm trống đến gõ, coi như gõ khá hơn nữa cũng là uổng phí!”"),t("br"),t("br"),n._v("Nhược Hoan công tử khẽ nói."),t("br"),t("br"),n._v("Mấy người sững sờ, đồng thời gật đầu."),t("br"),t("br"),n._v("Đúng vậy a, lần khảo hạch này chính là tâm cảnh, không phải khảo hạch ngươi sức chiến đấu, coi như đem Ngô trưởng lão làm thổ huyết, không có ở trận bàn lên lưu lại đủ nhiều hoa văn cùng dấu vết, cũng là uổng công!"),t("br"),t("br"),n._v("“Yên tâm đi, ta nhìn hắn trường thương tại trận bàn lên đâm loạn, không có bất kỳ cái gì chương pháp... Cố ý đối kháng Ngô trưởng lão, làm dư luận xôn xao, cũng chỉ lòe người mà thôi!”"),t("br"),t("br"),n._v("Bàn tay lớn vẫy một cái, Nhược Hoan công tử tiếp tục nói."),t("br"),t("br"),n._v("Đâm vào nhiều hơn nữa, lực công kích mạnh hơn, không dùng!"),t("br"),t("br"),n._v("Liền cùng khảo thí đồng dạng, để ngươi trả lời một cộng một đẳng cấp mấy, ngươi đem vi phân và tích phân công thức suy luận quá trình viết lên, lại cao hơn trình độ, hỏi một đằng, trả lời một nẻo, cũng là sai lầm đáp án!"),t("br"),t("br"),n._v("“Phong Ma khúc lập tức liền sắp xong rồi!”"),t("br"),t("br"),n._v("Ngay tại hừ lạnh, trong đám người một thanh âm vang lên, mọi người đồng loạt nhìn sang."),t("br"),t("br"),n._v("Chỉ thấy lúc này Ngô trưởng lão, vừa thổ huyết bên cạnh đi về phía trước, đã đi tới khoảng cách Trương Huyền không đến ba mét địa phương, hai bên giao chiến thanh âm, cũng càng thêm vang dội, nghe vào trong tai, lộn xộn không chịu nổi, đã phân không ra ngược lại là không phải Phong Ma khúc, không phải tiếng đánh."),t("br"),t("br"),n._v("Bất quá, dựa theo thời gian suy tính, ba phần nhiều loại Phong Ma khúc, hẳn là lập tức liền phải kết thúc."),t("br"),t("br"),n._v("“Chỉ mong... Ngô trưởng lão không có việc gì!”"),t("br"),t("br"),n._v("Nhìn thấy ói ra không biết bao nhiêu ngụm máu tươi, sắc mặt tái nhợt như là người chết Ngô trưởng lão, đáy lòng của mọi người âm thầm cầu nguyện."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Cùng mọi người đoán đồng dạng, quả thực lập tức liền phải kết thúc."),t("br"),t("br"),n._v("Phong Ma khúc lại dài cũng có thời gian giới hạn, mà bây giờ, đã tới điểm cuối cùng."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Trường thương không ngừng lay động, Trương Huyền nhẹ nhàng thở ra, vừa rồi một mực dựa theo nhạc khúc thiếu hụt va chạm trận bàn, tới đối kháng, cũng không quan sát chung quanh."),t("br"),t("br"),n._v("Lúc này, lập tức kết thúc, nhịn không được thở ra một hơi."),t("br"),t("br"),n._v("Trường thương trong tay run lên, đem cái cuối cùng âm tiết gõ xong, nhịn không được xoay đầu lại, lập tức nhìn thấy một tấm trắng bệch đến sụp đổ mặt."),t("br"),t("br"),n._v("“Ah!”"),t("br"),t("br"),n._v("Giật nảy mình, Trương Huyền bàn tay lắc một cái, trường thương trực tiếp quất tới."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-543"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-545"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);