(window.webpackJsonp=window.webpackJsonp||[]).push([[1796],{2151:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-539-nguoi-tra-chim-cho-ta"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-539-nguoi-tra-chim-cho-ta"}},[n._v("#")]),n._v(" Chương 539: Ngươi trả chim cho ta!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Linh trí, do thư họa sư giao cho linh tính, hấp thu thiên địa linh khí thoát nét mà ra."),t("br"),t("br"),n._v("Bởi vì không phải sinh mệnh, đối với linh khí càng thêm mẫn cảm, yêu cầu cũng là càng cao, bình thường linh khí, muốn để hắn duy trì lâu dài, gần như không có khả năng. Đừng nói nhị tinh, tam tinh, coi như ngũ tinh danh sư lời tụ linh khí, muốn để hắn dừng lại thêm một đoạn thời gian, đều rất khó."),t("br"),t("br"),n._v("Nguyên nhân chính là như thế, cái khảo hạch này, đồng thời không tưởng tượng bên trong dễ dàng như vậy, nếu không, không có khả năng nhiều như vậy danh sư, đều không biết."),t("br"),t("br"),n._v("Tô sư xem ra, vị này Trương Huyền, mặc dù tại thư hoạ bên trên cũng có chút thiên phú, dù sao chỉ là tam tinh thư họa sư (tại Thiên Vũ vương quốc khảo hạch), truyền thụ đạo lý cũng đều là cơ sở bên trong cơ sở, muốn hội tụ đầy đủ linh khí, để linh trí nhiều kiên trì một đoạn thời gian, chỉ sợ không quá có thể."),t("br"),t("br"),n._v("“Nếu như hắn giảng giải tu luyện, đại khái khoảng mười lăm phút, dù sao, Vân trưởng lão cũng chỉ có thể duy trì mười ba phút đồng hồ!” Chần chờ một chút, Khang đường chủ nói."),t("br"),t("br"),n._v("Xác định khảo hạch nội dung, để Vân trưởng lão chuyên môn thử qua, đường đường tứ tinh danh sư, chỉ có thể duy trì mười ba phút đồng hồ."),t("br"),t("br"),n._v("Vị này Trương Huyền, giảng giải tu luyện, có thể để cho hắn đều trúng chiêu, vượt qua cái thành tích này không khó."),t("br"),t("br"),n._v("Bất quá, mười lăm phút đến đỉnh."),t("br"),t("br"),n._v("Dù sao, giảng giải nội dung càng cơ sở, dẫn động linh khí khả năng cũng là càng nhỏ."),t("br"),t("br"),n._v("“Hiện tại nói chính là thư hoạ... Hơn nữa còn như thế cơ sở, đoán chừng duy trì tám phút cũng khó khăn!”"),t("br"),t("br"),n._v("“Đúng vậy a!” Tô sư gật đầu."),t("br"),t("br"),n._v("Hai người bọn họ mặc dù không có thư hoạ chức nghiệp, nhưng cũng nhìn qua không ít liên quan thư tịch, biết vị thanh niên này giảng giải chính là cơ sở nhất đồ vật, hơn nữa chung quanh không có rõ ràng sóng linh khí, muốn duy trì thời gian dài hơn, khả năng không lớn."),t("br"),t("br"),n._v("“Chỉ là tám phút, muốn cầm thứ nhất khó khăn, Nhược Hoan công tử, tứ tinh thư họa sư, lại thêm tứ tinh danh sư, giảng bài lời nói, tuyệt đối có thể vượt qua mười phút đồng hồ!”"),t("br"),t("br"),n._v("Lăng sư nhịn không được chen vào nói."),t("br"),t("br"),n._v("Hai người đồng thời gật đầu."),t("br"),t("br"),n._v("Quân Nhược Hoan, quen thuộc linh trí, lại là danh sư, duy trì mười phút đồng hồ không khó, Trương sư nếu là chỉ có tám phút lời nói, đừng nói thứ nhất, thứ hai cũng không quá có thể."),t("br"),t("br"),n._v("“Mau nhìn, linh trí dường như đã bắt đầu lắc lư, sắp tiêu tán!”"),t("br"),t("br"),n._v("Mọi người đồng loạt nhìn lại, quả nhiên thấy lúc này thư hoạ nổi lên hiện rất nhiều chim tước, tiếng kêu to càng ngày càng thấp, từng cái thân thể không ngừng lắc lư, hình như bất cứ lúc nào cũng sẽ dập tắt."),t("br"),t("br"),n._v("“Lúc này mới ba phút nhiều một chút? Thành tích sẽ không như thế kém chứ?”"),t("br"),t("br"),n._v("“Đúng vậy a, coi như không giảng bài, cũng có thể duy trì hai phút đồng hồ, đây là có chuyện gì?”"),t("br"),t("br"),n._v("Tất cả mọi người nhíu mày."),t("br"),t("br"),n._v("Linh trí ra nét, coi như không cần giảng bài linh khí, cũng có thể đợi hơn hai phút đồng hồ, hiện tại chẳng qua ba phút nhiều một chút, liền bắt đầu lắc lư... Nói rõ Trương sư truyền thụ nội dung, đối với mấy cái này linh trí, tác dụng rất nhỏ, có thể nói cực kỳ bé nhỏ."),t("br"),t("br"),n._v("“Thứ nhất, ta cầm chắc!”"),t("br"),t("br"),n._v("Nhược Hoan công tử con mắt càng ngày càng sáng."),t("br"),t("br"),n._v("Nhìn tình huống trước mắt, cái tên này năm phút đồng hồ đều không kiên trì nổi, không phải thứ sáu cũng không xê xích gì nhiều, bản thân hai cửa trước mặc dù thành tích kém một chút, nhưng bằng mượn cơ hội lần này, hoàn toàn có thể một lần hành động nghịch tập, trở thành đệ nhất!"),t("br"),t("br"),n._v("Phía dưới tới chính là phía dưới tới, danh sư thi đấu, kiểm tra chính là tổng hợp năng lực, mà không phải đơn độc tâm cảnh cao, thực lực mạnh, liền có thể rút đến thứ nhất."),t("br"),t("br"),n._v("“Xem ra để hắn đi lên trước là chuyện tốt... Không có kinh nghiệm, đáng đời ăn thiệt thòi!”"),t("br"),t("br"),n._v("Khóe miệng vung lên."),t("br"),t("br"),n._v("  Cái tên này lợi hại hơn nữa, cũng chỉ là phía dưới vương quốc đi lên, linh trí lưu giữ, nhiều như vậy danh sư đều không có nghe qua, một cái đồ nhà quê, có thể biết cái gì?"),t("br"),t("br"),n._v("Xem ra lần sau khảo hạch, để hắn lên trước, liền khẳng định lấy không được thành tích tốt."),t("br"),t("br"),n._v("Càng nghĩ càng cao hứng, còn kém sắp cười ra tiếng."),t("br"),t("br"),n._v("Phó Tiếu Trần cũng nhẹ nhàng thở ra, hai cửa trước tổng hợp, tên của hắn lần xếp hạng thứ ba, để cái tên này một mực đến quán quân lời nói, hắn khẳng định sẽ bị đào thải, nhìn thấy loại tình huống này, cũng coi như yên lòng."),t("br"),t("br"),n._v("“Còn tốt, không có kiếm chuyện...” Vân trưởng lão xoa xoa đầu."),t("br"),t("br"),n._v("Vốn cho rằng cái tên này sẽ trước mặt hai cửa đồng dạng, làm ra sự tình gì, làm bản thân chật vật không chịu nổi... Hiện tại xem ra suy nghĩ nhiều, có lẽ tại thư hoạ phương diện, chỉ biết là cơ sở, cái khác cũng không am hiểu..."),t("br"),t("br"),n._v("Đáy lòng thả lỏng, đang muốn các loại linh trí biến mất, chỉnh lý thư hoạ, để kế tiếp tới vượt quan, đột nhiên nhớ tới cái gì, sửng sốt một cái, thân thể cứng ngắc."),t("br"),t("br"),n._v("“Không đúng... Nếu là sắp tiêu tán, hẳn là duy trì không được mấy hơi thở, thế nào còn tại? Hơn nữa những này linh trí lắc lư mặc dù kịch liệt, nhưng không có tán loạn dấu hiệu...”"),t("br"),t("br"),n._v("Bình thường linh trí, một khi bắt đầu lắc lư, chẳng mấy chốc sẽ tiêu tán, cũng là vài giây đồng hồ thời gian mà thôi, thế nào cái này đều lung lay hai, ba phút, vẫn còn tiếp tục?"),t("br"),t("br"),n._v("“Không đúng ah! Thế nào thấy giống như là tán loạn, nhưng cảm giác càng lắc càng chắc chắn?”"),t("br"),t("br"),n._v("“Thật đúng là...”"),t("br"),t("br"),n._v("Hắn phát hiện không đúng, những người khác cũng đã nhìn ra."),t("br"),t("br"),n._v("Vừa rồi linh trí xuất hiện, bọn hắn đều cẩn thận quan sát, một khi lắc lư, thật vất vả hội tụ linh khí, liền sẽ phân giải, duy trì không được bao lâu."),t("br"),t("br"),n._v("Nhưng trước mắt này cái, nương theo Trương sư giảng bài, lắc lư cùng chổi lông gà giống như, bình thường chim bay, đều chỉ sợ đều muốn đi đầy đất lông gà, một đoàn linh khí tạo thành đồ vật, không những không có tản ra, còn rất có càng ngày càng ngưng kết xu thế..."),t("br"),t("br"),n._v("Nhà khác linh trí lắc lư, là muốn tản ra, cái tên này trước mặt nhưng thật giống như càng lắc càng thoải mái..."),t("br"),t("br"),n._v("Chuyện ra sao?"),t("br"),t("br"),n._v("“Không đúng...”"),t("br"),t("br"),n._v("Mày nhíu lại thành u cục, thấy lại qua vài phút, ngay lúc sắp phá mười phút đồng hồ đại quan, lắc lư linh trí, không những không có vỡ vụn, ngược lại càng ngày càng tiên diễm, giống như là chân chính đang sống, Vân trưởng lão nhớ tới cái gì, con ngươi co rụt lại: “Chẳng lẽ là...”"),t("br"),t("br"),n._v("“Cái này... Không có khả năng!”"),t("br"),t("br"),n._v("Một tiếng thấp giọng hô, ngay sau đó vẻ mặt phát điên."),t("br"),t("br"),n._v("“Vân trưởng lão đây là thế nào?”"),t("br"),n._v("“Không thi toàn quốc hạch tiểu tử này trưởng lão đều muốn như vậy đi?”"),t("br"),t("br"),n._v("Nhìn thấy luôn luôn trầm ổn Vân trưởng lão, cùng Ngô, bạch hai vị đồng dạng, cũng cùng co giật đồng dạng, tất cả mọi người khóe miệng giật một cái."),t("br"),t("br"),n._v("Trước hai cái khảo hạch cái tên này, tất cả đều thổ huyết trọng thương, hiện tại vị này lại cái dạng này... Là tiểu tử này có gì đó quái lạ, vẫn là chuyện ra sao?"),t("br"),t("br"),n._v("“Vân trưởng lão!” Khang đường chủ nhịn không được đi lên phía trước, vỗ bờ vai của hắn: “Làm sao vậy?”"),t("br"),t("br"),n._v("“Không có gì... Chỉ là nghĩ đến một cái không thể tin được sự tình...” Vân trưởng lão khôi phục lại, nói."),t("br"),t("br"),n._v("“Không thể tin được sự tình?” Khang đường chủ cau mày."),t("br"),t("br"),n._v("“Ngươi nhìn...” Vân trưởng lão chỉ hướng Trương Huyền trước mặt linh trí: “Cái này linh trí, vừa rồi liền bắt đầu lắc lư, hiện tại đã không sai biệt lắm hơn mười phút, không những không có tán loạn, ngược lại càng ngày càng ngưng thực...”"),t("br"),t("br"),n._v("Khang đường chủ gật đầu."),t("br"),t("br"),n._v("Tất cả mọi người thấy được, cũng cảm thấy kỳ quái, chẳng lẽ cái này có vấn đề gì?"),t("br"),t("br"),n._v("“Nếu như ta không có đoán sai, đây không phải muốn tán loạn, mà là...”"),t("br"),t("br"),n._v("Vân trưởng lão bờ môi run rẩy: “Chân vận xao động, linh trí phá phàm!”"),t("br"),t("br"),n._v("“Phá phàm? Ngươi nói chính là... Thư hoạ đệ thất cảnh? Đi đến loại cảnh giới này tác phẩm, nét bên trong động vật, thực vật, giống như là đang sống, có được chính mình thần trí, bay lượn đến thư hoạ bên ngoài, hồi lâu cũng sẽ không biến mất?”"),t("br"),t("br"),n._v("Khang đường chủ sững sờ."),t("br"),t("br"),n._v("“Chính là cái này!” Vân trưởng lão gật đầu."),t("br"),t("br"),n._v("“Không đúng, linh trí phá phàm ta biết, cần lục tinh cấp bậc thư họa sư, sửa chữa tác phẩm hội họa, hoặc là một lần nữa thư hoạ, mới có thể thành công, giảng bài... Sao có thể đi?”"),t("br"),t("br"),n._v("Trừng to mắt, Khang đường chủ có chút không tin."),t("br"),t("br"),n._v("Lĩnh ngộ linh trí phá phàm ý cảnh thư họa sư, chỉ cần vẽ tranh, có thể nhẹ nhõm vẽ ra chim tước, bay lượn trời cao, chạy nhanh Vân Tiêu."),t("br"),t("br"),n._v("Nghe nói trước kia có một vị lợi hại lục tinh thư họa sư, ở trên tường hoa một đầu tiên hạc, phá tường mà ra, mang theo phá vân ly mở, lưu lại Tiên Nhân đạp chim mà đi truyền thuyết."),t("br"),t("br"),n._v("Còn có một vị thư hoạ đại tông sư, bị thú triều tập kích, vẽ ra một đầu đỉnh phong linh thú, chấn nhiếp khắp nơi, dọa đến vô số linh thú sợ hãi mà chạy, không dám cùng là địch."),t("br"),t("br"),n._v("Bất kỳ nghề nghiệp nào tu luyện tới cảnh giới cao thâm, đều sẽ có quỷ thần khó lường năng lực cùng thủ đoạn."),t("br"),t("br"),n._v("Chỉ bất quá, lần này đủ loại, cần ít nhất lục tinh cấp bậc thư họa sư, tại chỗ vẽ tranh, hoặc là tại nguyên bản thư hoạ bên trên tăng thêm, lưu bút... Còn chưa từng nghe nói qua, cho linh trí giảng bài, có thể để cho phá phàm."),t("br"),t("br"),n._v("“Trương sư giảng giải nội dung, mười phần phù hợp những này linh trí, không chỉ để linh khí hội tụ, còn dẫn động sư ngôn thiên bẩm, để cái này linh trí, không tự chủ được tu luyện... Nếu như không có đoán sai, những này lắc lư, muốn đi trừ trước đó phù phiếm linh khí, để lực lượng càng thêm ngưng thực, cũng chính là... Hóa hư thành thực!”"),t("br"),t("br"),n._v("Vân trưởng lão nói."),t("br"),t("br"),n._v("“Hóa hư thành thực? Cái này... Làm sao có thể?” Khang đường chủ giật nảy mình, kém chút không có tại chỗ ngã sấp xuống."),t("br"),t("br"),n._v("Thật hay giả?"),t("br"),t("br"),n._v("Giảng bài nói linh trí lấy hư hóa thực, linh trí phá phàm... Thế nào cảm giác giống như là đang nghe thiên thư đâu?"),t("br"),t("br"),n._v("“Ta cũng không dám tin tưởng, có thể loại trừ loại tình huống này, thực sự không có cách nào giải thích!” Vân trưởng lão nói."),t("br"),t("br"),n._v("Coi như đoán được, cũng không dám tin tưởng, lúc này mới thất thố như vậy."),t("br"),t("br"),n._v("“Nếu như là thật, vẽ tranh để linh trí hiện lên, bức tranh đúng đúng linh trí sào huyệt, còn có thể trở về, mà loại này giảng bài, để hắn phá phàm... Linh trí vẫn sẽ hay không trở lại tác phẩm hội họa bên trên?”"),t("br"),t("br"),n._v("Đột nhiên, Khang đường chủ nghĩ đến một vấn đề."),t("br"),t("br"),n._v("Tiên Nhân ngồi hạc mà đi, linh trí từ bức tranh sinh ra, coi như phá phàm, lúc nghỉ ngơi, vẫn là sẽ trở lại trong bức họa đến, mà trước mắt cái này, nghe giảng bài thuế biến, không có rễ không bình, một khi chân chính ngưng tụ thành hình, hóa hư thành thực, nên đi chỗ nào?"),t("br"),t("br"),n._v("Nếu như bay thẳng đến lấy, trước bay cái mấy tháng, không trở lại tác phẩm hội họa, hắn cái thành tích này lại nên như thế nào đi tính toán? Cũng không thể để mọi người một mực làm chờ xem!"),t("br"),t("br"),n._v("“A? Nguy rồi, đừng để hắn tiếp tục giảng bài...”"),t("br"),t("br"),n._v("Sửng sốt một chút, Vân trưởng lão nhớ tới cái gì, vội vàng la lên, tiếng la còn không có kết thúc, chỉ thấy trước mắt thư hoạ bên trong bay lên rất nhiều chim tước, dừng lại lắc lư, trong lúc đó, cùng kêu lên vang lên."),t("br"),t("br"),n._v("Chít chít chít chít! Thu thu thu thu!"),t("br"),t("br"),n._v("Thanh âm êm tai, tràn ngập đại sảnh, tựa như thật tới một đám chim bay, chơi đùa kêu to, cho người ta một loại dị thường hưởng thụ."),t("br"),t("br"),n._v("Trước đó thoạt nhìn có chút hư ảo chim tước, lúc này cũng giống như lấy được thuế biến, lông vũ càng thêm ngưng thực, móng vuốt, miệng càng thêm dày hơn trọng, như là triệt để đang sống."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Tiếng kêu sau đó, những này chim tước đồng thời cánh chớp động, lăng không mà lên, thẳng tắp hướng nơi xa phóng đi, bay ra đại điện, biến mất tại mọi người ánh mắt."),t("br"),t("br"),n._v("Nhìn thấy những này chim tước bay đi, cùng trên mặt bàn còn lại trụi lủi một tấm giấy trắng, Vân trưởng lão sững sờ tại nguyên chỗ, thân thể nhoáng một cái."),t("br"),t("br"),n._v("Đây chính là lão sư hắn lưu lại vô thượng tác phẩm hội họa, hắn trân quý chưa hề lấy ra gặp người, thậm chí bằng hữu tốt nhất, cũng khó khăn đến thấy một lần."),t("br"),t("br"),n._v("Vốn cho rằng chỉ là kiểm tra cái thử, đơn giản đến cực điểm, ai ngờ... Những này linh trí thế mà bay đi!"),t("br"),t("br"),n._v("Em gái ngươi ah!"),t("br"),t("br"),n._v("Linh trí bay đi, muốn tấm này giấy trắng có cái rắm dùng?"),t("br"),t("br"),n._v("Mới vừa nhẹ nhàng thở ra cảm thấy đối phương không có gây sự, kết quả là tới một màn như thế... Cái tên này đến cùng là đến tranh tài tuyển bạt, vẫn là tới làm loạn?"),t("br"),t("br"),n._v("Lắc người một cái đi tới Trương Huyền trước mặt, Vân trưởng lão khóe mắt, hận không thể đem trước mắt cái này hai hàng bóp chết."),t("br"),t("br"),n._v("“Ta chim! Ngươi trả chim cho ta...”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-547"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-549"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);