(window.webpackJsonp=window.webpackJsonp||[]).push([[1799],{2154:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-541-hong-lien-cuu-kiem"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-541-hong-lien-cuu-kiem"}},[n._v("#")]),n._v(" Chương 541: Hồng Liên Cửu Kiếm")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Danh sư, muốn đối tu vi có cực sâu lý giải, mới có thể xuất khẩu thành thơ, chỉ điểm thiếu hụt, nếu không, cái gọi là giảng bài, giảng giải, đều là lý luận suông!”"),t("br"),t("br"),n._v("“Có thể xem thấu tu luyện bản chất, nhanh chóng tu luyện, cực kỳ trọng yếu, không thể so với giáo thư dục nhân kém! Bởi vậy, ta cùng tất cả trưởng lão chuyên môn thương nghị qua, cửa này khảo hạch, cũng sẽ không bảo thủ không chịu thay đổi, mà là chuyên môn thiết kế một bộ mới võ kỹ đi ra. Đến lúc đó, sẽ cho các vị thời gian một nén nhang học tập, có thể hiểu được bao nhiêu liền lý giải bao nhiêu. Sau đó tiến hành khảo hạch, xác định thành tích!”"),t("br"),t("br"),n._v("“Yên tâm, bộ võ kỹ này, chưa hề xuất hiện qua, không cần lo lắng sớm tiết lộ đề.”"),t("br"),t("br"),n._v("Nhìn quanh một tuần, Khang đường chủ cười cười."),t("br"),t("br"),n._v("“Tiết lộ đề, đường chủ nói đùa, danh sư khảo hạch, coi trọng nhất công bằng, nếu không, một khi tra ra, huỷ bỏ tư cách, làm sao có thể có người biết rõ rồi mà còn cố phạm phải!”"),t("br"),t("br"),n._v("“Coi như không công bằng, Nhược Hoan công tử là đường chủ học sinh, cũng muốn đối với hắn có lợi... Dường như, lần này bị đả kích sắp không được!”"),t("br"),t("br"),n._v("“Đúng vậy a, ngươi nhìn lúc trước hắn, Tứ đại công tử đứng đầu, khí phách phấn chấn, mà bây giờ, cùng bại gà trống đồng dạng, cũng thật khó cho...”"),t("br"),t("br"),n._v("“Đúng rồi, Khang đường chủ, đến cùng cái gì võ kỹ? Nếu như là đơn giản, dựa theo thiên phú của bọn hắn, không dùng đến thời gian một nén nhang liền lĩnh ngộ được cảnh giới tiểu thành, còn thế nào so?”"),t("br"),t("br"),n._v("Nghe được tỷ thí quy tắc, có người nhịn không được mở miệng."),t("br"),t("br"),n._v("Võ kỹ chia làm năm cấp bậc, theo thứ tự là: Nhập môn, tinh thông, tiểu thành, đại thành, viên mãn như ý."),t("br"),t("br"),n._v("Nhập môn, là chỉ đối võ kỹ mới tính vừa mới lý giải, có thể miễn cưỡng sử xuất."),t("br"),t("br"),n._v("Tinh thông, tăng lên một cái cấp bậc, có thể phát huy ra lực lượng, nhưng ở vận chuyển bên trên không có như vậy tự nhiên."),t("br"),t("br"),n._v("Còn tiểu thành, đã coi như là đối võ kỹ lý giải rất nhiều, có thể làm thành bảo mệnh tuyệt chiêu sử dụng."),t("br"),t("br"),t("br"),n._v("Phải biết đại bộ phận võ giả, cũng chỉ đem võ kỹ tu luyện tới loại cảnh giới này mà thôi."),t("br"),t("br"),n._v("Còn phía sau đại thành, viên mãn như ý, không tại một cái chiêu số bên trên, thấm nhuần mười thời gian mấy năm, căn bản không thể nào làm được."),t("br"),t("br"),n._v("Nếu như võ kỹ này đơn giản, thời gian một nén nhang liền tu luyện tới tiểu thành, mọi người trình độ đều tương đồng, rất khó xác thực phân thắng thua."),t("br"),t("br"),n._v("“Tiểu thành?”"),t("br"),t("br"),n._v("Biết đạo chúng nhân lo lắng, Khang đường chủ mỉm cười: “Nếu như chư vị biết bộ võ kỹ này, là ai thiết kế, khẳng định liền sẽ không như vậy nói!”"),t("br"),t("br"),n._v("“Ai thiết kế?”"),t("br"),t("br"),n._v("“Chẳng lẽ là... Hồng trưởng lão?”"),t("br"),t("br"),n._v("“Hồng trưởng lão, mệnh danh vạn quốc Danh Sư đường, đối võ kỹ nghiên cứu thấu đáo nhất người, năm mươi năm đến, hắn sáng lập ra võ kỹ, không có ba mươi, cũng chí ít hai mươi bảy, tám bộ!”"),t("br"),t("br"),n._v("“Đúng vậy a, ta tu luyện Đại Trọng Huyễn Ảnh quyền, Vô Ba Liên Hoàn chưởng, đều là hắn sáng tạo!”"),t("br"),t("br"),n._v("“Ai nói không phải, ta cũng có ba bộ võ kỹ, xuất từ chương trình học của hắn...”"),t("br"),t("br"),n._v("“Chỉ là... Rất mười năm gần đây, Hồng trưởng lão không phải một mực bế quan ư? Lúc nào xuất quan?”"),t("br"),t("br"),n._v("“Ta cũng không biết, có điều, nếu thật là hắn ra tay sáng lập ra võ kỹ, đừng nói tiểu thành, ngắn như vậy thời gian, có thể đi đến nhập môn, cũng khó khăn!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được đường chủ lời nói, mọi người nhớ tới một cái nhân vật truyền kỳ, nhịn không được thảo luận lên tiếng."),t("br"),t("br"),n._v("“Không sai, thiết kế bộ võ kỹ này, chính là Hồng trưởng lão!”"),t("br"),t("br"),n._v("Khang đường chủ nhẹ nhàng cười một tiếng, mở ra đáp án: “Hồng trưởng lão, mười năm qua một mực bế quan, chính là vì bộ võ kỹ này, rốt cục tại trước mấy ngày, triệt để sáng chế! Mười năm mài một kiếm, đủ thấy độ khó, trải qua ta cùng các trưởng lão khác đánh giá, đã đạt đến Linh cấp trung phẩm đỉnh phong cấp độ!”"),t("br"),t("br"),n._v("“Linh cấp trung phẩm đỉnh phong võ kỹ?”"),t("br"),t("br"),n._v("“Lợi hại!”"),t("br"),t("br"),n._v("Mọi người hít vào một ngụm khí lạnh."),t("br"),t("br"),n._v("Loại cấp bậc này võ kỹ, coi như Hóa Phàm tứ trọng cường giả đỉnh phong, muốn triệt để lĩnh ngộ cũng khó khăn, chớ nói chi là những này tham dự tuyển bạt người."),t("br"),t("br"),n._v("“Tốt, phải nói ta cũng nói hết rồi, hiện tại liền từ Hồng trưởng lão, tự mình tuyên bố khảo hạch quy tắc, tự mình khảo hạch, đến, cho mời Hồng trưởng lão!”"),t("br"),t("br"),n._v("Khang đường chủ cười cười, ngắm nhìn chung quanh một tuần, đột nhiên sững sờ: “Ai, Hồng trưởng lão, chớ đi, ngươi đây là... Đi đâu?”"),t("br"),t("br"),n._v("Theo thanh âm, mọi người đồng loạt nhìn lại, chỉ thấy một cái lão giả, chẳng biết lúc nào chạy tới đại trước cửa điện, còn kém một bước liền muốn rời khỏi."),t("br"),t("br"),n._v("“Khụ khụ, ta... Tùy tiện đi dạo!”"),t("br"),t("br"),n._v("Thấy ánh mắt mọi người sáng ngời, lão giả vội vàng dừng bước, hơi đỏ mặt."),t("br"),t("br"),n._v("“Cửa thứ tư khảo hạch lập tức bắt đầu...” Khang đường chủ nóng nảy vội vàng đi tới, đem hắn kéo về."),t("br"),t("br"),n._v("“Ta...” Hồng trưởng lão sắc mặt kìm nén đỏ lên: “Đường chủ, nếu không... Vẫn là ngươi tới đi!”"),t("br"),t("br"),n._v("Vốn là lời thề son sắt, phải chịu trách nhiệm khảo hạch, nhưng nhìn đến trước mặt ba vị trưởng lão thảm trạng, có chút lui bước."),t("br"),t("br"),n._v("Tiểu tử kia thực sự rất cổ quái, thật muốn khảo hạch hắn, thật không biết lại sẽ xuất hiện cái gì hiện tượng quái dị, thật không muốn toàn cả đời uy danh, bởi vì một hồi khảo thí mà chôn vùi."),t("br"),t("br"),n._v("“Như vậy sao được, bộ võ kỹ này là ngươi sáng chế, hơn nữa cũng tu luyện tới cảnh giới đại thành, ngoại trừ ngươi, ai còn có thể đối bọn hắn tiến hành khảo hạch cùng khảo thí? Liền xem như ta, cũng làm không được ah!”"),t("br"),t("br"),n._v("Khang đường chủ nói."),t("br"),t("br"),n._v("Bộ võ kỹ này là Hồng trưởng lão sáng chế, bản thân cũng tu luyện đến đại thành tình trạng, loại trừ hắn có thể khảo hạch mọi người, còn ai có loại này bản lĩnh?"),t("br"),t("br"),n._v("Chí ít hắn làm không được."),t("br"),t("br"),n._v("“Ây...” Hồng trưởng lão chần chờ."),t("br"),t("br"),n._v("“Yên tâm đi, thời gian một nén nhang, lợi hại hơn nữa thiên tài, cũng chỉ nhập môn mà thôi, khẳng định sẽ không xảy ra vấn đề!”"),t("br"),t("br"),n._v("Biết hắn lo lắng cái gì, Khang đường chủ truyền âm an ủi."),t("br"),t("br"),n._v("Cái kia Trương sư, quả thực cổ quái, nhưng võ kỹ thứ này, cũng không phải là một lần mà thôi, hắn không tin chỉ học được một nén nhang, liền có thể đạt đến đại thành, để Hồng trưởng lão cái này sáng chế công pháp người, cũng không sánh nổi."),t("br"),n._v(" Cho nên, cũng không có gì có thể xoắn xuýt."),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Suy tư một chút, cũng cảm thấy lời nói của đối phương có đạo lý, Hồng trưởng lão trở lại mọi người trước mặt."),t("br"),t("br"),n._v("“Vừa rồi Khang đường chủ đem khảo hạch nội dung cùng các ngươi nói, ta bây giờ nói nói kỹ càng tỷ thí quy tắc!”"),t("br"),t("br"),n._v("“Bộ võ kỹ này, gọi là , là ta quan sát Hồng Liên, lóe lên một cái rồi biến mất, mới có linh cảm. Quy tắc rất đơn giản, một lát nữa, ta sẽ đem kiếm pháp phương pháp tu luyện cho các ngươi, mỗi người đều có thời gian một nén nhang quan sát, lĩnh ngộ!”"),t("br"),t("br"),n._v("“Về sau, ta sẽ đích thân khảo thí các ngươi lĩnh ngộ tình huống. Yên tâm, lĩnh ngộ bộ kiếm pháp kia, cùng đối tu vi lý giải, thiên phú có quan hệ, cùng bản thân tu vi quan hệ không lớn, chỉ cần đi đến Hóa Phàm cảnh, đều có thể học được, thậm chí thi triển.”"),t("br"),t("br"),n._v("“Khảo nghiệm thời điểm, đều không không thể sử dụng tu vi, chỉ bằng vào kiếm chiêu tỷ thí, có thể ở trước mặt ta đâm ra ba kiếm, coi như lĩnh ngộ nhập môn, sáu kiếm vì tinh thông, đi đến chín kiếm, đã nói lên đã lĩnh ngộ được tiểu thành tình trạng... Mà cùng ta đánh tới mười chiêu trở lên, xem như đại thành...”"),t("br"),t("br"),n._v("Hồng trưởng lão đem quy tắc chi tiết nói một lần."),t("br"),t("br"),n._v("Kỳ thật cùng cửa thứ hai tu vi khảo thí có chút tương tự, chỉ bất quá, chỉ có thể động dụng mới vừa học Hồng Liên Cửu Kiếm, một khi vượt qua phạm vi, phán định thất bại."),t("br"),t("br"),n._v("Một nén nhang ước chừng mười lăm phút, thời gian ngắn như vậy, một bộ võ kỹ muốn xem xong cũng khó khăn, chớ nói chi là lĩnh ngộ, đồng thời dùng ra chiến đấu!"),t("br"),t("br"),n._v("Cửa này khảo hạch, cẩn thận nói đến, so ba cửa trước cộng lại đều muốn khó hơn không ít."),t("br"),t("br"),n._v("“Vì công bằng, cửa này khảo hạch, cùng trước đó luận võ đồng dạng, sẽ ở căn phòng đơn độc tiến hành, ta hiện tại trước đi qua, các ngươi từng cái đi vào là đủ...”"),t("br"),t("br"),n._v("Nói đến đây, Hồng trưởng lão dừng lại một chút: “Như vậy đi, trước cho các ngươi xác định trình tự, Đỗ Hổ cái thứ nhất, đằng sau là Lạc Khê, Phùng Mạc Sinh, Phó Tiếu Trần, Quân Nhược Hoan cùng Trương sư!”"),t("br"),t("br"),n._v("Trương Huyền cổ quái, trước đem hắn biến thành một tên sau cùng lại nói, nếu không, cái thứ nhất chạy vào, vạn nhất làm ra cái gì không cách nào khống chế cục diện, đằng sau muốn tiếp tục tiến hành cũng khó."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Mọi người gật đầu."),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Xác định rõ trình tự, Hồng trưởng lão đi đầu đi tiến gian phòng, Đỗ Hổ theo sát mà lên."),t("br"),t("br"),n._v("Thời gian một nén nhang trái phải, trên vách tường ánh sáng lóe lên, một hàng con số xuất hiện... Ba!"),t("br"),t("br"),n._v("Không nghĩ tới ngắn như vậy thời gian, Đỗ Hổ liền lĩnh ngộ được nhập môn cảnh giới, tại Hồng trước mặt trưởng lão thi triển ba kiếm."),t("br"),t("br"),n._v("Không hổ là các trưởng lão khác đề cử đi lên thiên tài, quả thực bất phàm."),t("br"),t("br"),n._v("Phía dưới là Lạc Khê."),t("br"),t("br"),n._v("Nàng cùng Đỗ Hổ xem như trong sáu người bài danh thấp nhất, biết bị tuyển bạt bên trên vô vọng, không có áp lực, học rất nhanh, thời gian một nén nhang phía sau, đồng dạng đạt đến cấp độ nhập môn đừng, sử xuất ba kiếm."),t("br"),t("br"),n._v("Tiếp theo là Phùng Mạc Sinh, không hổ là Phùng trưởng lão học sinh, đối võ kỹ lĩnh ngộ, rõ ràng so từ phía dưới vương quốc tới Đỗ Hổ, Lạc Khê càng hơn một bậc, ngắn ngủi thời gian một nén nhang, vậy mà bước qua nhập môn, đi đến tinh thông."),t("br"),t("br"),n._v("Trọn vẹn thi triển ra năm kiếm!"),t("br"),t("br"),n._v("Phó Tiếu Trần cũng không yếu, đồng dạng năm kiếm."),t("br"),t("br"),n._v("Bốn người sau đó, mọi người đem tất cả ánh mắt tập trung vào Nhược Hoan công tử trên người."),t("br"),t("br"),n._v("Bản thân hắn liền dùng kiếm, lại thêm lại tiếng hô lớn nhất, Khang đường chủ học sinh thân truyền, kỳ vọng lớn hơn."),t("br"),t("br"),n._v("Mặc dù ba cửa trước bị đả kích quá sức, có điều, dù sao cũng là đi đến tứ tinh cấp bậc danh sư, tâm cảnh siêu nhiên, trải qua cái này nửa ngày, Nhược Hoan công tử đã điều chỉnh xong trạng thái, không có để mọi người thất vọng, một nén nhang về sau, một cái cự đại “Sáu” chữ lập loè chói mắt."),t("br"),t("br"),n._v("Đã chứng minh hắn đối võ kỹ lĩnh ngộ, là cùng thế hệ bên trong, tài năng xuất chúng nhất."),t("br"),t("br"),n._v("“Tại Hồng trước mặt trưởng lão thi triển ra sáu kiếm, nói rõ đã đem võ kỹ lĩnh ngộ được tinh thông đỉnh phong, khoảng cách tiểu thành cũng không xa!”"),t("br"),t("br"),n._v("“Đúng vậy a, thật sự là thiên tài!”"),t("br"),t("br"),n._v("“Cũng không biết Trương sư có thể hay không vượt qua cái kỷ lục này!”"),t("br"),t("br"),n._v("“Trương sư... Nếu như ta không nhìn lầm, hắn am hiểu nhất vũ khí hẳn là thương đi, đây là kiếm chiêu, chỉ sợ có chút ăn thiệt thòi!”"),t("br"),t("br"),n._v("“Như thế...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thấy Nhược Hoan công tử ra khỏi phòng, ánh mắt của mọi người toàn bộ tập trung ở cái này đột nhiên toát ra hắc mã trên người."),t("br"),t("br"),n._v("Trước đó, mọi người đối với hắn đồng thời không coi trọng, có thể liên tục phá ba cửa ải ghi chép, lông không tranh cãi trở thành thứ nhất, đã có để cho người ta không dám khinh thường thực lực."),t("br"),t("br"),n._v("Bất quá, dù vậy, đối với cửa này, tất cả mọi người vẫn là báo thái độ hoài nghi."),t("br"),t("br"),n._v("Dù sao, từ cửa thứ nhất khảo hạch, đó có thể thấy được, trước mắt vị này, am hiểu là thương pháp, mà để hắn học tập kiếm pháp, cũng không rất dễ dàng."),t("br"),t("br"),n._v("Tiếng nghị luận bên trong, Trương Huyền đi vào phòng."),t("br"),t("br"),n._v("Rất nhanh, thời gian một nén nhang kết thúc."),t("br"),t("br"),n._v("“Ừm? Thế nào không có con số?”"),t("br"),t("br"),n._v("“Đúng vậy a, dựa theo mấy người khác tình huống, hiện tại cũng đã xuất hiện con số, mặc kệ hắn học được nhập môn, vẫn là tinh thông, đều hẳn là có vẻ bày ra mới là?”"),t("br"),t("br"),n._v("“Thế nào làm?”"),t("br"),t("br"),n._v("Thời gian kết thúc, trên vách tường chẳng những không có con số, trong phòng cũng an tĩnh có chút quá trớn, đại điện bên trong rất nhiều danh sư, hai mặt nhìn nhau, đều tràn đầy kỳ quái."),t("br"),t("br"),n._v("Dựa theo cái tên này trước mấy cửa nước tiểu tính, coi như cửa này một chiêu đem Hồng trưởng lão đánh bại, mọi người cũng có thể tiếp nhận, có thể... Động tĩnh gì đều không có, liền một chiêu đều không có biểu hiện... Cái quỷ gì?"),t("br"),t("br"),n._v("Không phải là Hồng trưởng lão mất hứng, đem... Cái tên này giết chết chứ?"),t("br"),t("br"),n._v("Lại có lẽ là... Cái tên này đem Hồng trưởng lão giết chết?"),t("br"),t("br"),n._v("Đến cùng chuyện gì xảy ra?"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-549"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-551"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);