(window.webpackJsonp=window.webpackJsonp||[]).push([[1800],{2155:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-542-hong-truong-lao-hoang-so"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-542-hong-truong-lao-hoang-so"}},[n._v("#")]),n._v(" Chương 542: Hồng trưởng lão hoảng sợ")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 542: Hồng trưởng lão hoảng sợ"),t("br"),t("br"),n._v("Nhìn thấy thanh niên đi tới, Hồng trưởng lão khóe miệng không tự chủ được co lại."),t("br"),t("br"),n._v("Phía trước mấy cái trưởng lão bi thảm trải qua, đã sâu tận xương tủy, để hắn đối kẻ trước mắt này tràn đầy kiêng kị."),t("br"),t("br"),n._v("“Hồng Liên Cửu Kiếm, là ta tốn hao vô số tâm huyết sáng tạo ra, Khang đường chủ tu luyện, một nén nhang cũng chỉ tinh thông mà thôi, khoảng cách tiểu thành cũng còn cách một đoạn, cái tên này coi như thiên phú không yếu, muốn muốn đạt tới tiểu thành cũng hầu như không thể nào, không cần lo lắng”"),t("br"),t("br"),n._v("Đáy lòng tự an ủi mình một câu, Hồng trưởng lão lúc này mới thở phào nhẹ nhõm, chỉ về phía trước: “Trước mặt ngươi trên bàn thư tịch, chính là Hồng Liên Cửu Kiếm phương pháp tu luyện, hiện tại ngươi có thời gian một nén nhang quan sát!”"),t("br"),t("br"),n._v("Nói xong cũng không nói nhảm, đốt lên bên người thơm, lập tức bụi mù tung bay, nhàn nhạt hương vị di tán cả phòng."),t("br"),t("br"),n._v("Trương Huyền đi tới cái bàn trước mặt, nhẹ nhàng sờ một cái, thư viện đã xuất hiện một bản giống nhau như đúc thư tịch."),t("br"),t("br"),n._v("Trên tay làm bộ không ngừng lật sách, tinh thần nhưng đắm chìm trong óc."),t("br"),t("br"),n._v("“Hồng Liên Cửu Kiếm, Vạn Quốc thành Danh Sư đường Hồng Tần trưởng lão sáng tạo, tổng cộng chín kiếm, thiếu hụt”"),t("br"),t("br"),n._v("Đem nội dung lật hết, Trương Huyền vẻ mặt cổ quái."),t("br"),t("br"),n._v("“Cái này nhiều thiếu hụt? Còn dám nói là cao minh kiếm pháp?”"),t("br"),t("br"),n._v("Trước mắt bộ kiếm pháp kia thiếu hụt lại đã đạt tới hơn một trăm chỗ, nhiều như vậy thiếu hụt, thế mà còn mệnh danh là cao minh kiếm pháp muốn hắn học tập thật không biết cái tên này ở đâu ra tự tin."),t("br"),t("br"),n._v("Bất quá, suy nghĩ kỹ một chút cũng là hiểu được, hắn trước kia lấy được kiếm pháp, võ kỹ, cơ bản đều là trải qua không biết bao nhiêu năm truyền thừa, trải qua vô số tiền bối sửa chữa, thiếu hụt đã ít đi rất nhiều, mà bộ này, vừa mới sáng chế, thậm chí còn không có trải qua thực tiễn, thiếu hụt nhiều một ít, cũng coi như bình thường."),t("br"),t("br"),n._v("“Thiên Đạo kiếm pháp, dung hợp!”"),t("br"),t("br"),n._v("Tràn đầy im lặng, tinh thần khẽ động, Thiên Đạo kiếm pháp cùng trước kia lấy được rất nhiều kiếm pháp bí tịch, kịch liệt lắc lư, cùng quyển sách này chậm chạp dung hợp."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Một bản tân Hồng Liên Cửu Kiếm xuất hiện ở trước mắt, vội vàng lật ra."),t("br"),t("br"),n._v("Rất nhanh, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Hắn sưu tập linh phẩm trung kỳ kiếm pháp quá ít, chỉ dựa vào trước kia học được kiếm pháp cơ sở, liền muốn cùng cao minh như vậy kiếm pháp dung hợp, rõ ràng có chút khó khăn."),t("br"),t("br"),n._v("Thật giống như khá hơn nữa phân trâu, cũng không chống đỡ được hoàng kim đồng dạng, bản chất không giống, kiếm đạo cơ sở dù là lại chính xác, muốn hoàn toàn dung hợp linh phẩm trung kỳ kiếm pháp cũng lực có thua."),t("br"),t("br"),n._v("“Còn có vượt qua ba mươi chỗ thiếu hụt”"),t("br"),t("br"),n._v("Đem dung hợp sau thư tịch lật hết, Trương Huyền buồn rười rượi."),t("br"),t("br"),n._v("Từ trùng sinh đến bây giờ, một mực liền tu luyện không chỗ thiếu hụt nào bí tịch, đối với hắn mà nói đã tạo thành hoàn mỹ ý thức, coi như trước đó có ba chỗ thiếu hụt Hồng Trần Đạp Thiên bộ, đều là nắm lỗ mũi tu luyện trước mắt cái này hơn năm mươi chỗ thiếu hụt kiếm pháp, thực sự không luyện được ah!"),t("br"),t("br"),n._v("Cái này rất giống ăn đã quen ngũ tinh đầu bếp thức ăn ngon, để hắn ăn quán ven đường, khẳng định nắm lỗ mũi khó mà nuốt xuống."),t("br"),t("br"),n._v("Nhưng nếu như không tu luyện, liền không khả năng thông qua khảo hạch, dẫn đến tu luyện thất bại."),t("br"),t("br"),n._v("Trong lúc nhất thời trăm mối lo, không biết nên làm gì bây giờ."),t("br"),t("br"),n._v("Hắn bên này xoắn xuýt, bên kia Hồng trưởng lão, vẻ mặt lúc đỏ lúc trắng, cảm thấy phổi đều nhanh nổi khùng!"),t("br"),t("br"),n._v("Mặc dù không muốn để cho gom góp tích luỹ cả đời thanh danh bởi vì cái tên này chôn vùi, nhưng cũng không có khả năng cái gì đều mặc kệ, đối phương một cầm tới thư tịch, liền lặng lẽ quan sát."),t("br"),t("br"),n._v("Không nhìn còn khá, vừa nhìn chỉ cảm thấy muốn phải lập tức tiến lên đem cái tên này xé nát."),t("br"),t("br"),n._v("Bộ này Hồng Liên Cửu Kiếm, là hắn xem Hồng Liên mà sáng chế, hao tốn ròng rã thời gian mười năm hoàn thiện, tự nhận là là Vạn Quốc thành tốt nhất võ kỹ một trong, hoàn toàn có thể lưu danh bách thế, danh thùy ngàn năm, cái tên này đến tốt, một cầm tới liền tùy tiện xoay loạn, căn bản không có xem thật kỹ ý tứ."),t("br"),t("br"),n._v("Cái này ngược lại cũng thôi, một hồi cau mày, một hồi vẻ mặt ưu sầu cái quỷ gì?"),t("br"),t("br"),n._v("Xem ra thật giống như gặp phân người, bất cứ lúc nào cũng sẽ phun ra"),t("br"),t("br"),n._v("Mẹ nó!"),t("br"),t("br"),n._v("Chẳng lẽ ta sáng lập ra công pháp cứ như vậy để ngươi buồn nôn? Để ngươi ghét bỏ?"),t("br"),t("br"),n._v("Tức giận râu ria thổi lên, Hồng trưởng lão suýt nổi khùng."),t("br"),t("br"),n._v("Hắn nghĩ tới đối phương không biết tu luyện, cũng nghĩ qua thiên phú tuyệt hảo, thời gian một nén nhang, liền tu luyện tới viên mãn như ý, có thể làm giấc mơ đều không nghĩ tới, cái tên này không có tu luyện ngược lại cũng thôi, còn ghét bỏ một bộ muốn thổ biểu lộ"),t("br"),t("br"),n._v("Ta đây chính là linh phẩm trung kỳ đỉnh phong cấp bậc võ kỹ, ngay cả Khang đường chủ tất cả trưởng lão đều khen không dứt miệng tuyệt chiêu ngươi đây là ý gì?"),t("br"),t("br"),n._v("Đang cảm thấy lên cơn giận dữ, chỉ thấy đối diện thanh niên thực sự nhịn không được, hai mắt nhắm lại, răng khẽ cắn, một bộ tráng sĩ một đi không trở lại biểu lộ: “Được rồi, chết thì chết đi, cùng lắm thì tu luyện xong về sau lại cũng không dùng là được!”"),t("br"),t("br"),n._v("“Ngươi!”"),t("br"),t("br"),n._v("Hồng trưởng lão cũng nhịn không được nữa, trực tiếp nổ tung, tiến về phía trước một bước đi tới trước mặt: “Trương sư, ngươi có ý tứ gì?”"),t("br"),t("br"),n._v("Đang định bắt đầu tu luyện, chỉ thấy lão giả nổi giận đùng đùng đi tới trước mặt, Trương Huyền không rõ ràng cho lắm, nghi ngờ nhìn thoáng qua: “Làm sao vậy?”"),t("br"),t("br"),n._v("“Ta sáng lập ra kiếm pháp để ngươi ghét bỏ?” Hồng trưởng lão không có chút nào bảo lưu, trực tiếp chất vấn."),t("br"),t("br"),n._v("“Kỳ thật cũng không hoàn toàn là ghét bỏ, mà là sai lầm thiếu sót quá nhiều, thực sự không muốn tu luyện” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Ngươi” Hồng trưởng lão chỉ cảm thấy da đầu nổ tung, cả người đều nhanh muốn điên rồi."),t("br"),t("br"),n._v("Người khác hỏi như vậy, coi như lại chán ghét, cũng tất nhiên chiếu cố mặt mũi, phủ nhận một cái, thuận tiện lại trái lương tâm tán dương vài câu"),t("br"),n._v(" Mà cái tên này, thế mà trực tiếp không biết xấu hổ thừa nhận!"),t("br"),t("br"),n._v("Không hoàn toàn là ghét bỏ nói rõ đã đầy đủ ghét bỏ!"),t("br"),t("br"),n._v("Còn sai lầm thiếu sót quá nhiều em gái ngươi ah!"),t("br"),t("br"),n._v("Ngươi biết ta vì bộ võ kỹ này, hao phí bao nhiêu tâm huyết ư? Mười năm không nghỉ không ngủ, vốn cho rằng một khi ra tay thiên hạ biết kết quả tới một câu, thiếu sót quá nhiều"),t("br"),t("br"),n._v("“Không biết kiếm pháp của ta nơi nào có thiếu sót?”"),t("br"),t("br"),n._v("Hừ lạnh một tiếng, Hồng trưởng lão cưỡng ép áp chế phẫn nộ của mình, sợ vừa xung động trực tiếp vào tay đem cái tên này tại chỗ quất chết."),t("br"),t("br"),n._v("“Muốn cho ta chỉ điểm? Cũng tốt, vậy ta liền cho ngươi chỉ điểm một chút! Nhìn kỹ, thời gian của ta có hạn, chỉ nói một lần.”"),t("br"),t("br"),n._v("Trương Huyền gật gật đầu, hai tay chắp sau lưng, chậm rãi mà đi: “Trước nói ngươi chín kiếm bên trong kiếm thứ nhất, Hồng Liên Hoa Khai, Khí Đãng Cửu Châu! Chiêu này tổng cộng chín chín tám mươi mốt chỗ biến hóa, kiếm mang gào thét, chói lóa mắt, uy lực quả thực bất phàm. Chẳng qua kiếm từ bên hông đâm ra, chân khí chạy hoàn cốc, vọng trường, bản thân cũng có chút chán nản, còn muốn phát huy ra cực lớn uy lực, gần như không có khả năng! Nếu như lúc này, ta có một kiện binh khí, coi như không phải trường kiếm, chỉ là một cái bình thường gậy gỗ, công kích ngươi Hối Hải huyệt, ngươi nên như thế nào chống lại?”"),t("br"),t("br"),n._v("“Công kích Hối Hải huyệt?”"),t("br"),t("br"),n._v("Hồng trưởng lão vốn là nổi giận đùng đùng, nghe nói như thế, trong đầu bổ sung Hồng Liên Hoa Khai kiếm chiêu cùng công kích, đột nhiên một cái cứng đờ."),t("br"),t("br"),n._v("Hồng Liên Hoa Khai, hoa nở thịnh thế."),t("br"),t("br"),n._v("Chiêu này chói lọi chói mắt, lấy cuồng bạo kiếm khí, đối với người tiến hành công kích, thoạt nhìn rất cường đại, nhưng cũng có trí mạng thiếu hụt, Hối Hải huyệt chính là trọng yếu nhất một chỗ, đừng nói gậy gỗ, coi như ngón tay đụng vào đi lên, toàn bộ kiếm chiêu đều sẽ như vậy tê liệt, lại không có nửa phần uy lực."),t("br"),t("br"),n._v("Chỗ thiếu hụt này hắn cũng không phát hiện, đối phương chỉ nhìn một hồi thư đã tìm được?"),t("br"),t("br"),n._v("“Không đúng, coi như ngươi biết ta Hối Hải huyệt chân khí khó mà thủ hộ, lại như thế nào? Kiếm chiêu thoáng qua rồi biến mất, chờ ngươi phát hiện, ta đã đâm ra chí ít năm kiếm, ngươi nên như thế nào chống lại?” Hồng trưởng lão đột nhiên khẽ nói."),t("br"),t("br"),n._v("Bất kể kiếm pháp vẫn là công pháp, chỉ cần vận chuyển, liền khẳng định có thiếu sót, có thiếu hụt, không thể nào làm được hoàn mỹ vô khuyết, chiêu này Hồng Liên Hoa Khai, coi như Hối Hải huyệt là lớn nhất thiếu sót lại như thế nào? Một kiếm đánh ra, khí tức ngang dọc, muốn tại trong kiếm mang tìm tới cái này chút vấn đề, gần như không có khả năng."),t("br"),t("br"),n._v("“Đâm ra năm kiếm? Ngươi sai, nếu như vừa bắt đầu, ta chân trái tiến lên ba bước, trước đâm hồng hải, trở lại lại đâm lạc kiều, bên cạnh bước ba đâm phù giang đâm ngoạn giá ba chỗ huyệt vị về sau, lại đâm hối hải, không biết ngươi còn có thể hay không tránh?”"),t("br"),t("br"),n._v("Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("“Đâm hồng hải, ta giơ kiếm phong lên, lui lại một bước, đâm lạc kiều, ta cấp tốc trở về thủ, tiến công chân khí gián đoạn, không cách nào phát huy kiếm khí, đâm phù giang, hối hải tất nhiên bỏ trống ngươi, ngươi...”"),t("br"),t("br"),n._v("Trong đầu thôi diễn đối phương chiêu số, Hồng trưởng lão vừa mới bắt đầu xem thường, một lát sau, vẻ mặt biến bạch, cả người không ngừng run rẩy."),t("br"),t("br"),n._v("Nếu thật là chiến đấu, dựa theo phương vị này liên tục ba kiếm, hắn Hồng Liên Hoa Khai chiêu này tuyệt đối lập tức tan tác, không chỉ không cách nào đả thương người, còn sẽ bởi vì chân khí phản phệ, chịu đến trọng thương!"),t("br"),t("br"),n._v("Chỉ đâm ba cái bình thường phương vị, liền chân khí đều không cần ra, hắn liền lại bởi vì kiếm pháp vấn đề mà bị thương"),t("br"),t("br"),n._v("Cái này"),t("br"),t("br"),n._v("“Đây chỉ là một chiêu, ngươi chiêu thứ hai Hồng Liên Diệu Thế, lại thêm là sai lầm chồng chất, đối mặt chiêu này, ta thậm chí có thể không dùng binh khí, trực tiếp đoàn thân xung kích trung cung, tiến đánh nhược hải, ngươi tất nhiên lui lại phía sau không cần phải nói, cũng vô cùng đơn giản!”"),t("br"),t("br"),n._v("Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("Biết thiếu hụt, kết hợp tri thức, nghĩ biện pháp phá giải cũng là trở nên hết sức dễ dàng cùng đơn giản."),t("br"),t("br"),n._v("“Ta”"),t("br"),t("br"),n._v("Hồng trưởng lão lui lại một bước."),t("br"),t("br"),n._v("Chiêu thứ hai, Hồng Liên Diệu Thế, một kiếm hoành không, khí tức như biển, là đánh xa chiêu số, thoạt nhìn lóa mắt, trên thực tế cận thân không cách nào phòng ngự, thuộc về trống rỗng tình huống, mà lúc này, chỉ cần cận thân trong công kích cung cùng nhược hải, chân khí của hắn tất nhiên hỗn loạn, kiếm chiêu đứng phá!"),t("br"),t("br"),n._v("Phá chiêu thứ nhất, đối phương liền đâm bốn cái phương vị, hắn liền tan tác, mà cái này, chỉ cần hai chiêu hắn sẽ trở nên liền phòng bị cũng không kịp!"),t("br"),t("br"),n._v("Thế nào làm?"),t("br"),t("br"),n._v("Chẳng lẽ hắn sáng lập ra kiếm pháp, thật như thế không đỡ nổi một đòn?"),t("br"),t("br"),n._v("“Không đúng ta một kiếm hoành không, ngươi làm sao có thể kịp làm ra nhiều như vậy động tác cùng chiêu số?”"),t("br"),t("br"),n._v("Lại đẩy gõ một cái, lông mày giương lên."),t("br"),t("br"),n._v("Đối phương nói, đều là lý luận, bình thường chiến đấu, hắn kiếm pháp tốc độ nhanh như vậy, đối phương làm sao có thể có thời gian, liên tục công kích nhiều lần?"),t("br"),t("br"),n._v("“Không có thời gian?” Trương Huyền lắc đầu: “Ngươi nhìn kỹ!”"),t("br"),t("br"),n._v("Thân thể nhoáng một cái, cả người lập tức hóa thành huyễn ảnh, thời gian nháy mắt liền đi qua ba cái phương vị, đồng thời hợp chỉ làm kiếm, hướng về phía trước liên tục điểm, liên tục ba lần, điểm vị trí cùng trong miệng hắn nói giống nhau như đúc."),t("br"),t("br"),n._v("“Cái này”"),t("br"),t("br"),n._v("Nhìn thấy chiêu này, Hồng trưởng lão thân thể bỗng nhiên co lại, kém chút ngã sấp xuống."),t("br"),t("br"),n._v("Tốc độ của đối phương, công kích, đều không có kẽ hở, nếu như vừa rồi hắn dùng ra kiếm chiêu, đối phương thi triển ra cái này, hai bên giao chiến trong nháy mắt, hắn liền sẽ bị đánh bại, không có bất kỳ cái gì năng lực phản kháng!"),t("br"),t("br"),n._v("Trừ phi từ bỏ kiếm chiêu, lấy thực lực tuyệt đối nghiền ép, nếu không, chỉ bằng vào kiếm thuật, sẽ bại rất thảm, thậm chí trực tiếp bị thương."),t("br"),t("br"),n._v("Mồ hôi lạnh trong nháy mắt từ sống lưng chảy xuống."),t("br"),t("br"),n._v("Sáng tạo ra nhiều bộ võ kỹ, vốn cho rằng chiêu này một màn, Vạn Quốc thành vô địch, không nghĩ tới mười năm khổ tu cư nhiên như thế không đỡ nổi một đòn!"),t("br"),t("br"),n._v("“Xin hỏi Trương sư ta chiêu này, nhưng có hoàn thiện chi pháp?”"),t("br"),t("br"),n._v("Liền ôm quyền, Hồng trưởng lão cầm học sinh lễ."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-550"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-552"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);