(window.webpackJsonp=window.webpackJsonp||[]).push([[1801],{2156:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-543-bo-quyen"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-543-bo-quyen"}},[n._v("#")]),n._v(" Chương 543: Bỏ quyền")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Bên này thảo luận kiếm chiêu, người bên ngoài đã sốt ruột đến xoay quanh."),t("br"),t("br"),n._v("“Khang đường chủ, Trương sư đều vào đến gần một canh giờ, bây giờ còn chưa đi ra, sẽ không... Hồng trưởng lão dưới cơn nóng giận, giết chết hắn đi!”"),t("br"),t("br"),n._v("Tô sư vẻ mặt sốt ruột."),t("br"),t("br"),n._v("Từ Trương Huyền đi vào phòng đến bây giờ, đã tiếp cận một canh giờ, không chỉ không có đi ra, trên vách tường liền nửa chữ cũng không có xuất hiện, cụ thể tình huống gì, hoàn toàn không biết gì cả, không chỉ hắn sốt ruột, bên trong đại sảnh tất cả mọi người không hiểu ra sao."),t("br"),t("br"),n._v("“Không cần lo lắng, coi như huyên náo không thoải mái, chỉ cần động thủ, liền sẽ thi triển chiêu số... Đến bây giờ một cái số lượng đều không có, nói rõ không có người làm như vậy...” An ủi một câu, Khang đường chủ cũng mày nhăn lại, trong mắt tràn đầy không hiểu."),t("br"),t("br"),n._v("Coi như Trương Huyền không hiểu quy củ làm loạn, Hồng trưởng lão không biết a! Sống một, hai trăm tuổi người, làm sao có thể bởi vì vì một chút chuyện nhỏ, trước mặt nhiều người như vậy đối đường đường danh sư ra tay!"),t("br"),t("br"),n._v("Lại nói, chỉ muốn xuất thủ, tất nhiên biểu hiện con số, hiện tại cái gì động tĩnh đều không có thật giống như hai người tiến gian phòng ngủ say như chết, đến cùng xảy ra chuyện gì?"),t("br"),t("br"),n._v("“Đường chủ, nếu không... Ngươi vào xem một chút đi!” Lăng sư cũng nhìn qua."),t("br"),t("br"),n._v("Lão như vậy chờ lấy cũng không phải biện pháp ah vào xem, cũng có thể biết đến cùng chuyện gì xảy ra."),t("br"),t("br"),n._v("“Tốt!” Chần chờ một chút, Khang đường chủ gật gật đầu: “Các ngươi ở chỗ này chờ, ta một người đi vào, coi như xuất hiện chuyện gì, cũng dễ giải quyết!”"),t("br"),t("br"),n._v("Tô sư, Lăng sư lên tiếng, Khang đường chủ hít sâu một hơi, đang định đẩy cửa đi vào, chỉ thấy trước mắt cửa phòng “Kẹt kẹt!” Một tiếng, Trương Huyền cùng Hồng trưởng lão sóng vai đi ra."),t("br"),t("br"),n._v("Hai người y quan chỉnh tề, khuôn mặt mang cười, nhìn bộ dáng không có bất kỳ cái gì không thoải mái."),t("br"),t("br"),n._v("Tất cả người đưa mắt nhìn nhau."),t("br"),t("br"),n._v("Náo loạn nửa ngày hai ngươi không có đánh nhau ah! Đã không có đánh nhau, làm gì không ra? Để chúng ta nhiều người chờ như vậy lấy?"),t("br"),t("br"),n._v("Ngô trưởng lão, Bạch trưởng lão, Vân trưởng lão ba người cũng nhìn nhau, trong mắt tất cả đều là không hiểu."),t("br"),t("br"),n._v("Dựa theo kinh nghiệm của bọn hắn, vốn cho rằng coi như không đánh được, Hồng trưởng lão cũng sẽ chật vật không chịu nổi, không nghĩ tới hai người cười cười nói nói, không có chút nào không vui."),t("br"),t("br"),n._v("Chẳng lẽ vị này Trương sư đổi tính?"),t("br"),t("br"),n._v("Đem bọn hắn làm thảm như vậy, lại tại Hồng trưởng lão nơi này đổi tính... Ba người đều bày tỏ sâu sắc hoài nghi."),t("br"),t("br"),n._v("“Hồng trưởng lão, các ngươi đây là...”"),t("br"),t("br"),n._v("Khang đường chủ cũng kỳ quái nhìn qua."),t("br"),t("br"),n._v("“A, không có việc gì!”"),t("br"),t("br"),n._v("Hồng trưởng lão lắc đầu, quay đầu nhìn về phía bên người thanh niên, đến bây giờ cũng nhịn không được trong lòng rung động cùng bội phục."),t("br"),t("br"),n._v("Vốn cho rằng cái tên này chỉ nhìn ra hai chiêu thiếu hụt, thông qua vừa rồi một phen nói chuyện mới hiểu được, đối phương đối tu vi lý giải, đã đi đến hạo như yên hải tình trạng, tùy tiện chỉ điểm, đều để người hưởng thụ không hết. Hắn tự cho là ngạo Hồng Liên Cửu Kiếm, tại đối phương phân tích xuống, sơ hở trăm chỗ, đâu đâu cũng có lỗ thủng!"),t("br"),t("br"),n._v("Tại cùng cấp bậc cường giả trước mặt sử dụng uy phong lẫm liệt, hiệu quả không tệ, một khi gặp được chân chính cao thủ lợi hại, tuyệt đối trong nháy mắt tức phá, căn bản ngăn cản không nổi!"),t("br"),t("br"),n._v("Thật muốn dùng loại này tuyệt chiêu cùng người khác khiêu chiến, chỉ sợ đối thủ chuyện gì không có, hắn cũng bởi vì phản phệ, trước đem bản thân giết chết."),t("br"),t("br"),n._v("Sáng chế cái đem bản thân chơi chết võ kỹ... Truyền đi, đoán chừng sẽ làm cho tất cả mọi người đều cười đến rụng răng, một thế uy danh, tang tại nước chảy."),t("br"),t("br"),n._v("Biết điểm ấy, khiêm tốn thỉnh giáo tiếp cận một canh giờ, thông qua đối phương chỉ điểm, đối bộ kiếm pháp kia lĩnh ngộ càng nhiều, ích lợi cực lớn, nếu như lại để cho hắn sửa chữa, có thể cam đoan, không bao lâu, nhất định có thể sáng chế càng hoàn mỹ hơn, lại thêm phù hợp tuyệt chiêu."),t("br"),t("br"),n._v("“Không có việc gì? Cái kia... Tuyển bạt có kết quả ư?”"),t("br"),t("br"),n._v("Thấy hắn không muốn nhiều lời, Khang đường chủ đành phải chuyển dời đến tuyển bạt đi lên."),t("br"),t("br"),n._v("“Có kết quả, ta hiện tại liền tuyên bố!”"),t("br"),t("br"),n._v("Hồng trưởng lão gật gật đầu, mỉm cười: “Trải qua ta vừa rồi khảo hạch, bọn hắn cửa thứ tư thành tích đã ra tới, Lạc Khê, Đỗ Hổ chặn lại ba kiếm, cùng là hạng năm; Phùng Mạc Sinh, Phó Tiếu Trần cùng là năm kiếm, đặt song song thứ ba, Quân Nhược Hoan sáu kiếm, vì thứ hai, thứ nhất là... Trương sư!”"),t("br"),t("br"),n._v("“Trương sư đệ nhất?”"),t("br"),t("br"),n._v("“Lại là hắn đệ nhất? Bọn hắn không phải không giao thủ ư?”"),t("br"),t("br"),n._v("Nghe được kết quả này, mọi người tất cả đều sửng sốt, tràn đầy kỳ quái."),t("br"),t("br"),n._v("Trước mặt Quân Nhược Hoan đám người, tại bức tường lưu lại thành tích, rõ như ban ngày, có thể vị này Trương sư, một chiêu cũng không có xuất hiện, làm sao lại đệ nhất?"),t("br"),t("br"),n._v("“Ta không phục!”"),t("br"),t("br"),n._v("Nhược Hoan công tử tiến về phía trước một bước: Nếu là so kiếm, phía trên xuất hiện con số vượt xa hắn, cũng nhận, có thể so sánh đều không có so, liền định thứ nhất, không khỏi có chút không công bằng đi!"),t("br"),t("br"),n._v("Thật giống như bài thi kiểm tra 99 phân, nếu như đối phương kiểm tra 100 điểm, thua thì thua, có thể... Liền khảo thí đều không có tham gia, bằng cái gì là đệ nhất?"),t("br"),t("br"),n._v("“Không phục?” Hồng trưởng lão nhướng mày."),t("br"),t("br"),n._v("“Không sai, ta thời gian một nén nhang bên trong đem Hồng Liên Cửu Kiếm lĩnh ngộ được tinh thông cảnh đỉnh phong, chỉ bằng vào kiếm chiêu, tại trước mặt trưởng lão cứ thế mà chặn lại rồi sáu kiếm nhiều... Ta muốn hỏi một chút, để hắn làm thứ nhất, hắn tu luyện đến trình độ nào? Chẳng lẽ đạt đến tiểu thành?”"),t("br"),t("br"),n._v("Nói đến đây, Nhược Hoan công tử quay đầu nhìn về phía cách đó không xa thanh niên, cắn răng một cái: “Ta không tin ngươi có thể tại thời gian một nén nhang bên trong có thể đem bộ võ kỹ này lĩnh ngộ được tiểu thành... Trừ phi, ngươi cùng ta hiện tại tỷ thí, chỉ dùng kiếm pháp, đem ta đánh bại!”"),t("br"),t("br"),n._v("“Đánh bại ngươi?” Trương Huyền lắc đầu: “Vẫn là quên đi, ta không muốn khi dễ người!”"),t("br"),t("br"),n._v("“Ta đi!”"),t("br"),t("br"),n._v("“Khẩu khí này cũng quá lớn đi!”"),t("br"),t("br"),n._v("Nghe được hắn, mọi người một hồi hai mặt nhìn nhau."),t("br"),t("br"),n._v("Nhược Hoan công tử bất kể nói thế nào đều là tứ tinh danh sư, Hóa Phàm tứ trọng sơ kỳ siêu cường nhân vật, mà lại am hiểu dùng kiếm, lối ra tỷ thí, cái tên này lại còn nói... Không muốn khi dễ người!"),t("br"),t("br"),n._v("Ngươi một cái am hiểu dùng thương gia hỏa, nói như vậy, thế nào không sợ da trâu thổi rớt răng hàm?"),t("br"),t("br"),n._v("“Ngươi...” Nhược Hoan công tử vẻ mặt cũng lúc đỏ lúc trắng, sắp nổ tung: “Không cùng ta so cũng được, chỉ cần ngươi thừa nhận, đem Hồng Liên Cửu Kiếm thời gian một nén nhang, lĩnh ngộ được tiểu thành, ta chủ động nhận thua, tuyệt không nói nhảm.”"),t("br"),t("br"),n._v("Chỉ cần hắn thừa nhận lĩnh ngộ được cao hơn ta sâu tình trạng, thuộc về đối danh sư vinh dự tín nhiệm, đương nhiên sẽ không tiếp tục dây dưa."),t("br"),t("br"),n._v("“Tiểu thành...”"),t("br"),t("br"),n._v("Trương Huyền lần nữa lắc đầu: “Hồng Liên Cửu Kiếm, ta một chiêu đều không có luyện, ngay cả nhập môn cũng chưa tới, làm sao có thể đi đến tiểu thành!”"),t("br"),t("br"),n._v("“Không có luyện?”"),t("br"),n._v("Không chỉ Nhược Hoan công tử sửng sốt, ngay cả mọi người tất cả đều ngẩn ngơ."),t("br"),t("br"),n._v("Một chiêu không có luyện? Cái kia dựa vào cái gì đánh giá vì đệ nhất?"),t("br"),t("br"),n._v("Tấm màn đen cũng quá lớn đi!"),t("br"),t("br"),n._v("Khang đường chủ, Tô sư mấy người cũng tất cả đều không hiểu ra sao."),t("br"),t("br"),n._v("Bọn hắn cùng Hồng trưởng lão ở chung nhiều năm, biết làm người, luôn luôn công bằng Chính Trực, không có khả năng làm ra làm việc thiên tư sự tình đến, có thể... Trương sư học đều không có học, dựa vào cái gì liền phán định vì đệ nhất?"),t("br"),t("br"),n._v("Đây chính là liên quan đến danh sư thi đấu tuyển bạt, làm như vậy không là có chút quá trò đùa?"),t("br"),t("br"),n._v("“Chẳng lẽ... Hồng trưởng lão thu Trương sư vì học sinh? Vì mình học sinh có thể tham gia thi đấu, cố ý nhường?”"),t("br"),t("br"),n._v("“Không đến mức đi!”"),t("br"),t("br"),n._v("“Nếu như không phải như vậy, vì cái gì hai người đợi tại gian phòng một canh giờ lâu, liền tu luyện đều không có tu luyện, liền trực tiếp tuyên bố đệ nhất?”"),t("br"),t("br"),n._v("Đám người thảo luận."),t("br"),t("br"),n._v("Trương sư cùng Hồng trưởng lão vừa mới bắt đầu không nhận ra, có thể trong nháy mắt thái độ đại biến, muốn nói không có có quan hệ gì, ai cũng không tin."),t("br"),t("br"),n._v("“Hồng trưởng lão, đây rốt cuộc chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Thực sự nhịn không được, Khang đường chủ nhìn qua."),t("br"),t("br"),n._v("Những người khác cũng đem ánh mắt đồng loạt tập trung."),t("br"),t("br"),n._v("“Thu hắn làm học sinh?”"),t("br"),t("br"),n._v("Nghe được mọi người suy đoán, Hồng trưởng lão khóe miệng giật một cái."),t("br"),t("br"),n._v("Các ngươi thực có can đảm muốn!"),t("br"),t("br"),n._v("Coi như muốn nhận... Cũng phải có bản sự này mới được ah!"),t("br"),t("br"),n._v("“Các ngươi không cần đoán bậy, là ta muốn bái Trương sư vi sư, hắn không có đồng ý!” Lắc đầu, đem nói thật ra."),t("br"),t("br"),n._v("“Bái hắn... Vi sư? Còn không có đồng ý?”"),t("br"),t("br"),n._v("“Hồng trưởng lão không phải đùa giỡn chứ?”"),t("br"),t("br"),n._v("Tất cả mọi người miệng há mở, toàn bộ đại sảnh một hồi xôn xao."),t("br"),t("br"),n._v("Hồng trưởng lão mệnh danh Vạn Quốc thành công pháp, võ kỹ đệ nhất nhân, sáng lập ra tuyệt chiêu, không ít danh sư đều có tu luyện, có thể nói... Toàn bộ bên trong đại sảnh danh sư, chí ít hơn phân nửa là hắn nửa cái học sinh, đối với hắn có nửa sư tình nghĩa."),t("br"),t("br"),n._v("Loại này siêu tuyệt nhân vật, lại muốn bái cái tên này vi sư?"),t("br"),t("br"),n._v("Thật muốn như vậy, chẳng phải toàn bộ đại sảnh hơn phân nửa người, đều là đồ tôn của hắn?"),t("br"),t("br"),n._v("Hơn nữa... Mấu chốt nhất là... Cái tên này cự tuyệt..."),t("br"),t("br"),n._v("Có lầm hay không?"),t("br"),t("br"),n._v("“Trương sư thiên tư tuyệt hảo, đối tu vi lý giải, đã đi đến cao nhân khó lường, để cho người ta ngước nhìn tình trạng, ta chiêu này Hồng Liên Cửu Kiếm, hắn mặc dù không có tu luyện, nhưng... Lý giải chi sâu, không ai bằng, coi như ta cũng kém xa lắm, vừa rồi ta xin chỉ giáo một canh giờ, tự nhận được lợi rất nhiều!”"),t("br"),t("br"),n._v("Hồng trưởng lão vẻ mặt nghiêm túc mà nói."),t("br"),t("br"),n._v("“Học tập?”"),t("br"),t("br"),n._v("Mọi người ngươi nhìn ta, ta nhìn ngươi, tất cả đều cảm thấy đầu óc chuyển không đến, bối rối."),t("br"),t("br"),n._v("Hồng Liên Cửu Kiếm không phải ngươi hoa tốn thời gian sáng tạo ra, hơn nữa tu luyện tới đại thành trình độ ư? Hướng một cái chỉ học được thời gian một nén nhang gia hỏa thỉnh giáo còn học tập... Thậm chí càng bái hắn làm thầy, được lợi rất nhiều..."),t("br"),t("br"),n._v("Làm sao nghe được cùng nói đùa giống như?"),t("br"),t("br"),n._v("Ngô trưởng lão, Bạch trưởng lão mấy người cũng trừng to mắt."),t("br"),t("br"),n._v("Vừa mới bắt đầu, thấy hai người bình yên đi ra, còn tưởng rằng vị này Trương Huyền không gây sự, hiện tại xem ra, vẫn là quá ngây thơ rồi, không phải không gây sự, mà là đem Hồng trưởng lão tâm đều tù binh, dự định muốn bái sư!"),t("br"),t("br"),n._v("Bọn hắn nhiều nhất bị thương, mà cái tên này... Thật muốn bái sư, trước đó danh khí cùng địa vị, đều đem tan thành mây khói."),t("br"),t("br"),n._v("“Cửa này khảo hạch là đúng tu vi lý giải, đồng dạng nhìn thời gian một nén nhang, Nhược Hoan công tử chỉ ở dưới tay ta ra sáu kiếm... Mà Trương sư, nhưng có thể chỉ điểm ta, cho ta làm lão sư... Coi như không có tu luyện, xếp số một, cũng hẳn là không có vấn đề gì chứ?”"),t("br"),t("br"),n._v("Hồng trưởng lão nhìn về phía cách đó không xa Quân Nhược Hoan."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nhược Hoan công tử thân thể co lại, cảm giác đến trên mặt một bộ muốn khóc biểu lộ."),t("br"),t("br"),n._v("Lấy Hồng trưởng lão địa vị, không có khả năng nói dối, nói cách khác... Tám chín phần mười thật sự, đồng dạng nhìn thời gian một nén nhang, bản thân chỉ tu luyện đến tinh thông, liền dương dương đắc ý, tự cho mình siêu phàm, mà nhân gia, trực tiếp hoàn toàn lý giải, cho sáng chế công pháp người làm lão sư..."),t("br"),t("br"),n._v("Mẹ nó!"),t("br"),t("br"),n._v("Đến cùng là từ đâu xuất hiện quái thai?"),t("br"),t("br"),n._v("Quá biến thái đi!"),t("br"),t("br"),n._v("Khó trách trước đó nói, cùng hắn tỷ thí là khi dễ... Bản thân liền Hồng trưởng lão đều chỉ có thể kiên trì sáu chiêu, đối mặt một cái có thể làm đối phương sư phụ người, thật muốn đáp ứng yêu cầu, không phải khi dễ là cái gì?"),t("br"),t("br"),n._v("“Đã thành tích đi ra, ta nhìn tiếp tục tiến hành vòng khảo hạch kế tiếp đi!”"),t("br"),t("br"),n._v("Thấy học sinh của mình bị đả kích dục tiên dục tử, nhanh sắp không kiên trì được nữa, Khang đường chủ đành phải mở miệng lần nữa, cắt ngang cái đề tài này."),t("br"),t("br"),n._v("“Kế tiếp khảo hạch cũng là tuyển bạt thi đấu cửa ải cuối cùng, vì chỉ điểm trong quá trình tu luyện chỉ điểm lỗ thủng thiếu hụt...”"),t("br"),t("br"),n._v("Đang muốn nói tiếp, đột nhiên nghe được một thanh âm vang lên, Khang đường chủ lập tức chỉ thấy Trương Huyền nhìn lại."),t("br"),t("br"),n._v("“Cửa này... Ta bỏ quyền!”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-551"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-553"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);