(window.webpackJsonp=window.webpackJsonp||[]).push([[1802],{2157:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-544-phu-tro-chuc-nghiep-khong-đu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-544-phu-tro-chuc-nghiep-khong-đu"}},[n._v("#")]),n._v(" Chương 544: Phụ trợ chức nghiệp không đủ?")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Bỏ quyền?”"),t("br"),t("br"),n._v("Mọi người tất cả đều nhìn qua."),t("br"),t("br"),n._v("Liên tục bốn cửa thứ nhất, vị này Trương Huyền đã thành lần chọn lựa này thi đấu lớn nhất hắc mã, vì sao đến cuối cùng một cửa ải, muốn bỏ quyền?"),t("br"),t("br"),n._v("“Trước bốn cửa đều đã tham gia, cái này đều cửa ải cuối cùng...” Tô sư vội vàng nói."),t("br"),t("br"),n._v("“A, cái này tuyển bạt thi đấu, lựa chọn hai vị người chiến thắng đi tham gia danh sư thi đấu, ta thành tích bây giờ, bất kể cửa thứ năm tham gia không tham gia, đều đã là đệ nhất, còn không bằng bỏ quyền, chừa chút cơ hội cho bọn hắn!” Trương Huyền thản nhiên nói: “Nếu không... Luôn được thứ nhất, cũng không có ý tứ gì.”"),t("br"),t("br"),n._v("“Luôn được thứ nhất không có ý nghĩa?”"),t("br"),t("br"),n._v("“Ta đi, lời nói này lên tốt đã nghiền.”"),t("br"),t("br"),n._v("“Đúng vậy a, chẳng qua Trương sư nói cũng đúng, liên tục bốn lần thứ nhất, tích24 điểm, còn lại thành tích tốt nhất Nhược Hoan công tử, là 18 điểm, đằng sau là Phó Tiếu Trần, 15 điểm, Phùng Mạc Sinh, 13 điểm, Lạc Khê 10 điểm, Đỗ Hổ 7 điểm. Coi như cửa này Trương sư bỏ quyền, được một tên sau cùng, tích một điểm, cộng lại cũng là 25 điểm, tên thứ hai Nhược Hoan công tử đến thứ nhất, cộng thêm 6 điểm, cũng mới 24... Quả thực, tham gia cùng không tham gia, đều râu ria!”"),t("br"),t("br"),n._v("“Cửa ải cuối cùng không cần tham gia đều có thể đến thứ nhất... Lợi hại!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được Trương Huyền giới thiệu, mọi người bừng tỉnh đại ngộ."),t("br"),t("br"),n._v("Đã tập trung thắng ván, làm gì còn muốn tiếp tục tham gia, ngao du tốn thời gian?"),t("br"),t("br"),n._v("“Cái này...” Khang đường chủ cũng hiểu được, cùng Tô sư đám người liếc mắt nhìn nhau, đồng thời cười khổ."),t("br"),t("br"),n._v("Không hổ là Trương sư, quả nhiên khí phách."),t("br"),t("br"),n._v("Người khác khảo hạch mỗi một cửa đều đem hết toàn lực, cái tên này chỉ nghiên cứu bốn cửa, liền tập trung thắng cục..."),t("br"),t("br"),n._v("Giữa người và người, là thật có khoảng cách."),t("br"),t("br"),n._v("“Đã như vậy, bỏ quyền liền bỏ quyền đi!”"),t("br"),t("br"),n._v("Nhìn những người khác một chút, gặp bọn họ cũng không muốn cùng loại này quái thai tiếp tục tỷ thí, Khang đường chủ lắc đầu: “Bất quá, còn xin Trương sư tạm thời không nên rời đi, đem cửa ải cuối cùng khảo hạch hết, sẽ cùng ngươi môn nói tỉ mỉ danh sư thi đấu sự tình!”"),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Không có hắn tham gia, cửa ải cuối cùng tiến hành rất nhanh, thời gian không dài, kết quả là đi ra, không có gì bất ngờ xảy ra, Nhược Hoan công tử được thứ nhất, cầm xuống cái thứ hai danh ngạch."),t("br"),t("br"),n._v("“Tốt, tỷ thí kết thúc, hiện tại ta tuyên bố, vạn quốc liên minh có Trương sư, Quân Nhược Hoan đại biểu tham gia danh sư thi đấu...”"),t("br"),t("br"),n._v("Khảo hạch kết thúc, nhìn đường chủ nhìn quanh một tuần, tuyên bố thành tích."),t("br"),t("br"),n._v("“Đường chủ xin chậm!”"),t("br"),t("br"),n._v("Tiếng nói còn không có kết thúc, Phó Tiếu Trần đi lên phía trước: “Ta không phủ nhận Trương sư năng lực, bất quá... Nếu như ta nhớ không lầm, tham gia danh sư thi đấu là có yêu cầu thấp nhất, chí ít cần tam tinh trở lên danh sư mới có tư chất, Trương sư chỉ là nhị tinh, cấp bậc ở đây bày biện, ta sợ hắn thật muốn tham gia, liền cửa thứ nhất tư chất xét duyệt đều không đủ!”"),t("br"),t("br"),n._v("Khang đường chủ nhướng mày."),t("br"),t("br"),n._v("Đối phương nói không sai, danh sư thi đấu quả thực có cấp bậc yêu cầu, đồng thời không phải là cái gì người đều có thể tham gia."),t("br"),t("br"),n._v("Tam tinh chính là tiêu chuẩn thấp nhất."),t("br"),t("br"),n._v("Nếu như ngay cả cái này cũng chưa tới, lại như thế nào có thể tại thi đấu bên trong trổ hết tài năng?"),t("br"),t("br"),n._v("Trương Huyền mặc dù vừa rồi mấy cửa biểu hiện rất loá mắt, nhưng chân chính cấp bậc, vẫn chỉ là nhị tinh, khoảng cách tiêu chuẩn thấp nhất, còn có một số khoảng cách."),t("br"),t("br"),n._v("“Vừa rồi khảo hạch, Trương sư đều trôi qua cực kỳ dễ dàng, đừng nói tam tinh, coi như trực tiếp xin tứ tinh danh sư, cũng không đủ đi!” Tô sư chen vào nói."),t("br"),t("br"),n._v("Nhược Hoan công tử là tứ tinh danh sư, tại Trương sư trước mặt không đỡ nổi một đòn, hắn xin cái tứ tinh, lại có gì khó?"),t("br"),t("br"),n._v("“Trương sư năng lực, xin tứ tinh danh sư quả thực không khó, thậm chí đều có thể không cần một lần nữa khảo hạch, nhưng... Tứ tinh danh sư yêu cầu thấp nhất là muốn có bốn cái đều đi đến tứ tinh cấp bậc phụ tu chức nghiệp! Điểm ấy... Liền chỉ sợ dễ dàng như vậy!”"),t("br"),t("br"),n._v("Phó Tiếu Trần nói."),t("br"),t("br"),n._v("Vốn là vị này Trương sư không xuất hiện, khẳng định là hắn cùng Nhược Hoan công tử tham gia thi đấu, hiện tại danh ngạch bị đối phương cướp đi, tự nhiên phải nghĩ biện pháp tranh thủ."),t("br"),t("br"),n._v("Mà phụ tu chức nghiệp, chính là vị này Trương sư lớn nhất uy hiếp!"),t("br"),t("br"),n._v("Hắn thấy, coi như trước mắt vị danh sư này thiên phú cao, tuổi tác ở đây bày biện, đồng dạng tinh thông, không có khả năng mọi thứ tinh thông đi!"),t("br"),t("br"),n._v("“Như thế...”"),t("br"),t("br"),n._v("“Phụ tu chức nghiệp cùng danh sư đồng dạng, cũng cần thời gian chất đống cùng học tập mới được...”"),t("br"),t("br"),n._v("Mọi người trầm mặc, Tô sư cũng nói không ra lời."),t("br"),t("br"),n._v("Quả thực."),t("br"),t("br"),n._v("Trương sư tại danh sư bên trên thiên phú và thực lực, mọi người nhìn ở trong mắt, không cách nào cãi lại, có thể... Muốn trở thành tứ tinh danh sư, không dễ dàng như vậy, còn muốn có phụ tu chức nghiệp."),t("br"),t("br"),n._v("Coi như tiêu chuẩn thấp nhất tam tinh danh sư, cũng phải có ba loại đi đến tam tinh phụ tu chức nghiệp, đây là rất cơ sở nhất yêu cầu, cùng tâm cảnh khắc độ đồng dạng, không đạt tới là liền khảo hạch tư chất đều không có."),t("br"),t("br"),n._v("“Trương sư tại thư hoạ phương diện thành tích, tuyệt đối đạt đến tứ tinh, thậm chí cao hơn, điểm ấy ta có thể đi thư họa sư công hội xin, không tính là gì!”"),t("br"),t("br"),n._v("Vân trưởng lão tiến về phía trước một bước."),t("br"),t("br"),n._v("Vị này tiện tay có thể lấy vẽ ra sáu cảnh tác phẩm, đủ để chứng minh tại thư hoạ bên trên thành tựu so với hắn còn cao hơn, loại người này, xin cái tứ tinh thư họa sư, nhẹ nhõm đơn giản."),t("br"),t("br"),n._v("“Cái kia cũng chỉ là một loại mà thôi!”"),t("br"),t("br"),n._v("Phó Tiếu Trần nói."),t("br"),t("br"),n._v("Coi như xin cái ngũ tinh thư họa sư, cũng chỉ là một loại chức nghiệp, còn chí ít kém hai loại."),t("br"),t("br"),n._v("Phụ tu chức nghiệp... Cũng không phải là dễ dàng như vậy học được, huống chi còn muốn đạt tới thấp nhất tam tinh."),t("br"),t("br"),n._v("“Trương sư, ngươi nhưng còn có những nghề nghiệp khác, đạt đến tam tinh, thậm chí trở lên?”"),t("br"),t("br"),n._v("Khang đường chủ nhìn tới."),t("br"),t("br"),n._v("“Ta...” Trương Huyền đang muốn nói chuyện, chỉ thấy một vị học đồ vội vã đi tới."),t("br"),t("br"),n._v("“Đường chủ, Thú đường Hàn đường chủ cầu kiến!”"),t("br"),n._v(" “Hàn đường chủ? Hắn tới đây làm gì?” Khang đường chủ sững sờ."),t("br"),t("br"),n._v("Vạn Quốc thành thú đường đường chủ Hàn Xung cùng hắn bình thường không thế nào giao lưu, lúc này đột nhiên chạy tới làm gì?"),t("br"),t("br"),n._v("“Cho mời!” Mặc dù nghi hoặc, vẫn là khoát tay áo."),t("br"),t("br"),n._v("Thú đường mặc dù so Danh Sư đường kém một chút, nhưng dù sao cũng là một phương hào hùng, không thể thờ ơ."),t("br"),t("br"),n._v("“Vâng!” Học đồ lui ra ngoài, chỉ chốc lát chỉ thấy hai cái lão giả đi nhanh tới, Trương Huyền quay đầu nhìn lại, chính là tại Ôn Ngôn lĩnh nhìn thấy Hàn đường chủ, còn có một vị, thì là Giám Bảo các Trại các chủ."),t("br"),t("br"),n._v("“Khang đường chủ, chư vị trưởng lão!”"),t("br"),t("br"),n._v("Thấy đại sảnh nhiều người như vậy, hai người cũng có chút khiếp sợ, bất quá bọn hắn đều là người có thân phận, thấy qua việc đời, sẽ không thái quá thất lễ, đồng thời ôm quyền."),t("br"),t("br"),n._v("“Không biết Hàn đường chủ, Trại các chủ làm sao có rảnh đến Danh Sư đường làm khách?”"),t("br"),t("br"),n._v("Hàn huyên hai câu, Hàn đường chủ nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“A, kỳ thật ta lần này tới, là chuyên đến tìm Trương sư!” Hàn đường chủ cười cười: “Nghe Trại các chủ nói đến nơi này, lúc này mới chạy tới!”"),t("br"),t("br"),n._v("“Trương sư?” Khang đường chủ nháy con mắt, mọi người cũng đồng loạt nhìn qua."),t("br"),t("br"),n._v("“Tìm ta chuyện gì?” Trương Huyền cũng có chút kỳ quái."),t("br"),t("br"),n._v("Hai người mặc dù nhận biết, cũng chỉ một mặt mà thôi, không có sâu bao nhiêu quan hệ, tìm bản thân làm gì?"),t("br"),t("br"),n._v("“A, là như vậy, Trương sư tuỳ tiện thuần phục Yêu Thần thú, ta đã đem trong chuyện này báo tổng bộ, đặc biệt đưa ngươi đề thăng làm tứ tinh thuần thú sư, lần này tới, chính là cho ngươi đưa huy chương!” Hàn đường chủ cười nói."),t("br"),t("br"),n._v("Trước mắt vị này Trương sư, một lời thuần phục Yêu Thần thú, tại thuần thú bên trên năng lực đã vượt qua hắn, sau khi trở về liền hướng tổng bộ xin, hôm nay lấy được hồi phục, liền lập tức liền chạy tới."),t("br"),t("br"),n._v("Dựa theo tình huống bình thường, thuần thú sư làm thượng cửu lưu chức nghiệp, đối tu vi yêu cầu rất nghiêm, muốn trở thành tứ tinh, tu vi ít nhất phải đi đến Hóa Phàm nhị trọng, đây tuyệt đối cái này tính được là đặc biệt."),t("br"),t("br"),n._v("“Tứ tinh thuần thú sư?”"),t("br"),t("br"),n._v("“Trương sư thế mà còn là một vị tứ tinh thuần thú sư?”"),t("br"),t("br"),n._v("Nghe được Hàn đường chủ lời nói, mọi người xôn xao."),t("br"),t("br"),n._v("Mới vừa nói xong hắn phụ tu chức nghiệp không đủ, đối phương liền đem tứ tinh thuần thú sư huy chương đưa tới."),t("br"),t("br"),n._v("Lại thêm trước đó thư họa sư, đã có hai loại chức nghiệp đầy đủ."),t("br"),t("br"),n._v("“Đa tạ...”"),t("br"),t("br"),n._v("Không nghĩ tới đối phương còn giúp hắn xin tứ tinh thuần thú sư huy chương, Trương Huyền gật gật đầu, vừa đem huy chương cầm ở trong tay, chỉ thấy ở giữa đi ra học đồ lại vội vã đi tới: “Đường chủ, Hồng Hải thành trận pháp sư công hội hội trưởng Trịnh hội trưởng cùng Y Sư công hội Mạnh hội trưởng... Cầu kiến!”"),t("br"),t("br"),n._v("“Mau mời!”"),t("br"),t("br"),n._v("Khang đường chủ lần nữa vẫy tay."),t("br"),t("br"),n._v("Hai người tới trước mặt."),t("br"),t("br"),n._v("Trận pháp sư, y sư, đều là thượng cửu lưu đỉnh tiêm chức nghiệp, so với thuần thú sư đều không kém chút nào, hai vị hội trưởng đồng thời đến đây, để cái khác danh sư cũng hai mặt nhìn nhau, tràn đầy kỳ quái."),t("br"),t("br"),n._v("“Không biết hai vị...” Khang đường chủ ôm quyền nghi ngờ nhìn tới."),t("br"),t("br"),n._v("“A, không dối gạt Khang đường chủ, ta lần này tới, là đến tìm Trương sư!” Trịnh hội trưởng nói."),t("br"),t("br"),n._v("“Cũng tìm Trương sư?” Khang đường chủ con mắt trừng lớn."),t("br"),t("br"),n._v("“Đúng vậy a!” Trịnh hội trưởng cười cười, nhìn về phía Trương Huyền: “Trương sư, lần trước chỉ cho ngươi chuẩn tứ tinh trận pháp sư huy chương, ngươi sau khi đi, ta đem sự tình hướng tổng bộ báo cáo, đã trường hợp đặc biệt phê chuẩn ngươi vì tứ tinh trận pháp sư!”"),t("br"),t("br"),n._v("Ngày đó Trương Huyền dùng kiếm, đao, thương, điêu khắc ra cấp năm Tụ Linh trận, chính là bởi vì tu vi không đủ, không cách nào thành là chân chính tứ tinh trận pháp sư, hắn liền cho một cái chuẩn tứ tinh huy chương, sau đó đem trong chuyện này báo, trước mấy ngày, cũng đã nhận được tổng bộ hồi phục, đặc biệt đề bạt làm chính thức tứ tinh trận pháp sư!"),t("br"),t("br"),n._v("“Tứ tinh chính thức trận pháp sư?”"),t("br"),t("br"),n._v("Mọi người nháy con mắt."),t("br"),t("br"),n._v("Cái này lại tới một cái chức nghiệp?"),t("br"),t("br"),n._v("Khiếp sợ còn không có giải thích, chỉ thấy đi theo một bên một cái khác lão giả cười ôm quyền."),t("br"),t("br"),n._v("“Không vội, còn có ta!” Lão giả cười cười: “Trương sư có lẽ không nhận ra ta, tại hạ Vạn Quốc thành y sư công hội trưởng Chương Mạnh Kỳ!”"),t("br"),t("br"),n._v("“Gặp qua Mạnh hội trưởng!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Trương sư xông Thiên Vũ vương quốc nghi vấn khó xử lý tường, giải quyết nhị công chúa Tiên Thiên cơ không có lực lượng, Hàn đường chủ trước đó cũng đưa ngươi cho bọn hắn trị thương tình huống nói... Y thuật kinh người, có thể nói kỳ tích, bởi vậy, ta cố ý hướng tổng bộ xin, đồng thời lấy được phê chuẩn, đây là ngươi tứ tinh y sư huy chương!”"),t("br"),t("br"),n._v("Mạnh hội trưởng cười cười."),t("br"),t("br"),n._v("Lúc trước giải quyết xong nghi vấn khó xử lý tường, liền được Vạn Quốc thành Y Sư công hội hồi phục, đặc biệt mời vì bọn họ công hội trưởng lão, cho tới hôm nay, mới nhìn thấy Chân Nhân."),t("br"),t("br"),n._v("Quả nhiên anh hùng còn trẻ, khí chất bất phàm."),t("br"),t("br"),n._v("“Bốn cái chức nghiệp...”"),t("br"),t("br"),n._v("Đem mạnh hội trưởng lời nói, nghe vào trong tai, mọi người nuốt nước bọt."),t("br"),t("br"),n._v("Nhất là Phó Tiếu Trần, kém chút không có đập đầu chết."),t("br"),t("br"),n._v("Mới vừa nói xong cái tên này danh sư bản lĩnh tuy mạnh, phụ trợ chức nghiệp còn chưa đủ, không cách nào khảo hạch tam tinh, tứ tinh danh sư, kết quả Hàn đường chủ, Trịnh hội trưởng, Mạnh hội trưởng liền đem tứ tinh huy chương đưa mau tới đây, muốn hay không khoa trương như vậy?"),t("br"),t("br"),n._v("Coi như đánh mặt, cũng không cần nhanh như vậy đi..."),t("br"),t("br"),n._v("Nước mắt ào ào, sớm biết có loại này bản lĩnh, liền không nói nhảm."),t("br"),t("br"),n._v("Đang âm thầm đau lòng, cảm thấy bị đả kích quá sức, liền nghe đến trước đó tới vị kia Trại các chủ cười ha hả tiếng âm vang lên: “Các ngươi thượng cửu lưu chức nghiệp chính là phiền phức, còn cần hướng tổng bộ xin mới có thể xác định cấp bậc, ngươi xem chúng ta Giám Bảo các, trực tiếp liền cho Trương sư ngũ tinh giám bảo sư huy chương, căn bản không có phiền toái như vậy!”"),t("br"),t("br"),n._v("“Còn có ngũ tinh giám bảo sư chức nghiệp?”"),t("br"),t("br"),n._v("Phù phù!"),t("br"),t("br"),n._v("Phó Tiếu Trần té ngã trên đất."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-552"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-554"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);