(window.webpackJsonp=window.webpackJsonp||[]).push([[1804],{2159:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-546-ho-truong-lao"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-546-ho-truong-lao"}},[n._v("#")]),n._v(" Chương 546: Hồ trưởng lão")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 546: Hồ trưởng lão"),t("br"),t("br"),n._v("Cô cô cô cục cục!"),t("br"),t("br"),n._v("Tinh thuần linh khí, dọc theo huyệt đạo tràn vào thân thể, tựa như bách lưu hợp biển, chạy như điên đi vào đan điền, Hóa Phàm nhị trọng Thiên Đạo công pháp, tại thể nội vận chuyển, Trương Huyền thần thái yên tĩnh, khí tức cả người một liên tục tăng lên."),t("br"),t("br"),n._v("Nguyên Khí cảnh sơ kỳ!"),t("br"),t("br"),n._v("Nguyên Khí cảnh trung kỳ!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nếu như cho người khác nhìn thấy hắn trạng thái tu luyện, khẳng định sẽ phát điên nổi điên."),t("br"),t("br"),n._v("Bọn hắn tốn hao mấy năm, thậm chí mấy chục năm mới có thể gia tăng tu vi, mà ở trước mặt hắn cùng ăn cơm uống nước đồng dạng, không có chút nào ràng buộc cùng áp lực."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Vạn Quốc thành một cái xa hoa phủ đệ, một thanh niên đứng tại cửa viện trước, không ngừng quanh quẩn một chỗ, khắp khuôn mặt là sốt ruột màu."),t("br"),t("br"),n._v("Nếu như Trương Huyền tại cái này, nhất định có thể nhận ra, chính là vị kia thường xuyên tại hắn cái này mua đồ đại thổ hào, Tống Siêu."),t("br"),t("br"),n._v("“Trưởng lão bế quan lúc nào mới có thể đi ra ngoài?”"),t("br"),t("br"),n._v("Xoay vài vòng, Tống Siêu nhịn không được hỏi hướng giữ ở ngoài cửa hộ vệ."),t("br"),t("br"),n._v("“Ta cũng không biết, lần trước cùng Tống sư đã gặp mặt về sau, trưởng lão liền bắt đầu bế quan, một mực không có đi ra!” Hộ vệ nói."),t("br"),t("br"),n._v("“Được rồi...”"),t("br"),t("br"),n._v("Từ dược viên trở về, hắn liền mang theo tinh kim thất phẩm quan tài tới nơi này, lấy được kết quả là trưởng lão đang lúc bế quan, không có cách nào đành phải các loại xuống dưới, ai ngờ chờ đợi ròng rã vài ngày, một mực không có đi ra."),t("br"),t("br"),n._v("Hắn cũng biết, trưởng lão khẳng định là lấy được Vô Hồn Kim Nhân về sau, bắt đầu nghiên cứu, quá mức si mê."),t("br"),t("br"),n._v("“Quên đi, về trước đi, lần sau lại tới đi!”"),t("br"),t("br"),n._v("Biết tiếp tục chờ, đối phương không xuất quan, cũng không thể xông vào, đành phải lắc đầu, quay người đi ra ngoài, còn đi chưa được mấy bước, liền nghe đến phía sau “Kẹt kẹt!” Một tiếng, cửa phòng mở ra."),t("br"),t("br"),n._v("“Ha ha!”"),t("br"),t("br"),n._v("Vội vàng xoay người, liền gặp trưởng lão từ gian phòng cười đi ra."),t("br"),t("br"),n._v("“Gặp qua Hồ trưởng lão!” Tiến về phía trước một bước, ôm quyền khom người."),t("br"),t("br"),n._v("“Ngươi tới thật đúng lúc, đầu này Vô Hồn Kim Nhân, thật là không tệ, nghiên cứu mấy ngày, để cho ta được lợi rất nhiều! Nếu như ta có thể bởi vì đột phá này đến Hợp Linh cảnh, tuyệt đối có ngươi một phần công lao.”"),t("br"),t("br"),n._v("Hồ trưởng lão vuốt râu, mỉm cười."),t("br"),t("br"),n._v("Hóa Phàm ngũ trọng Hợp Linh cảnh cùng tứ trọng là một cái cự đại cửa ải khó, trong đó liên quan linh hồn bí ẩn."),t("br"),t("br"),n._v("Hắn bị nhốt tứ trọng đỉnh phong, đã nhiều năm, vẫn muốn đột phá, nhưng không thể được, đoạn thời gian trước, vị này Tống Siêu đưa tới võ hồn kim nhân lập tức bị coi như chí bảo nghiên cứu."),t("br"),t("br"),n._v("Vô Hồn Kim Nhân, Thượng Cổ vu hồn sư cận vệ, trên người liên quan linh hồn bí ẩn, mấy ngày nay nghiên cứu, mặc dù không có hiểu rõ vì cái gì có thể hình thành, lại làm cho hắn đối linh hồn có càng sâu cảm ngộ, đối về sau đột phá Hợp Linh cảnh, có cực trợ giúp lớn."),t("br"),t("br"),n._v("“Trưởng lão nói quá lời, chỉ cần thứ này hữu dụng, cũng coi như ta không có mua lỗ...” Tống Siêu liền vội vàng gật đầu."),t("br"),t("br"),n._v("“Ừm!” Nghe được hắn như thế khiêm tốn lời nói, Hồ trưởng lão hài lòng gật đầu: “Tìm ta gấp như vậy, liên tục mấy chuyến có phải là có chuyện gì hay không?”"),t("br"),t("br"),n._v("Mặc dù đang bế quan, có thể cái tên này hầu như mỗi ngày đều đến, vẫn là biết một chút."),t("br"),t("br"),n._v("Tống Siêu liền vội vàng gật đầu: “Lần trước bán ra Vô Hồn Kim Nhân cho ta vị kia, trước mấy ngày lại gặp, lần này trong tay hắn vừa vặn có một cái chuyên môn chứa đựng kim nhân tinh kim thất phẩm quan tài, ta muốn lấy có lẽ đối trưởng lão nghiên cứu hữu dụng... Liền mua tới!”"),t("br"),t("br"),n._v("“Tinh kim thất phẩm quan tài? Vu hồn sư chuyên môn vì Vô Hồn Kim Nhân chế tạo quan tài? Ở đâu? Nhanh cho ta xem một chút!”"),t("br"),t("br"),n._v("Ánh mắt sáng lên, Hồ trưởng lão hô hấp dồn dập."),t("br"),t("br"),n._v("Hắn bởi vì không cách nào đột phá, đem lực chú ý chuyển dời đến Vu hồn một đạo bên trên, thông qua những năm này nghiên cứu, cũng biết không ít, rõ ràng loại này quan tài quan trọng."),t("br"),t("br"),n._v("Nếu như sớm có thể được đến thứ này, mấy ngày nay tiến bộ khẳng định càng lớn, coi như không cách nào đi đến Hợp Linh, khẳng định cũng có thể để linh hồn càng thêm mượt mà sung mãn."),t("br"),t("br"),n._v("“Vâng!” Cổ tay khẽ đảo, một cái cự đại quan tài xuất hiện tại trước mặt."),t("br"),t("br"),n._v("Đang là lúc trước tại dược viên, Trương Huyền lấy một cái linh thạch trung phẩm bán cho hắn chiếc kia."),t("br"),t("br"),n._v("“Lại là bảo tồn hoàn hảo tinh kim thất phẩm quan tài...” Nhìn thấy trước mắt trên quan tài điêu khắc đủ loại minh văn, Hồ trưởng lão con mắt càng ngày càng sáng."),t("br"),t("br"),n._v("Loại này quan tài là vu hồn sư chuyên môn xin luyện khí sư vì Vô Hồn Kim Nhân chế tạo, có thể để cho kim nhân bảo tồn vạn năm không hủy, giá trị bao lớn, vô khả hạn lượng."),t("br"),t("br"),n._v("“Căn cứ bán ra người giới thiệu, cái này cỗ quan tài, chính là chứa đựng trưởng lão đầu kia kim nhân!”"),t("br"),t("br"),n._v("Tống Siêu tiếp tục nói."),t("br"),t("br"),n._v("“Nha...”"),t("br"),t("br"),n._v("Hồ trưởng lão liên tục gật đầu, dọc theo dạo qua một vòng, nhịn không được ngẩng đầu: “Bán ra hai thứ đồ này đưa cho ngươi vị kia... Hiện tại ở đâu? Ngươi nhưng có hỏi, hắn là ở nơi nào lấy được những vật này?”"),t("br"),t("br"),n._v("“Cái này... Không vấn đề!” Tống Siêu lắc đầu."),t("br"),t("br"),n._v("Mỗi lần vừa thấy mặt liền bị sư ngôn thiên bẩm làm thân không theo mình, bình thường nhận biết năng lực đều không có, chỉ biết là ngoan ngoãn bỏ tiền, hỏi thế nào?"),t("br"),t("br"),n._v("“Không vấn đề?” Hồ trưởng lão nhướng mày."),t("br"),t("br"),n._v("“Mặc dù không vấn đề những này, lại biết, trong tay hắn còn có ba đầu dạng này Vô Hồn Kim Nhân cùng chí ít ba miệng tinh kim thất phẩm quan tài, thậm chí... Còn có cấp bậc cao hơn... Xích kim quan tài!”"),t("br"),t("br"),n._v("Nhớ tới ngày đó thanh niên lời nói, Tống Siêu vội vàng trả lời."),t("br"),t("br"),n._v("“Ngươi nói là loại kia xích kim khóa kín không người phá, ngủ yên vạn năm cũng không lo... Hóa Phàm cường giả đỉnh phong đều không thể mở ra, chuyên môn dùng để chứa đựng vu hồn sư thân thể xích kim quan tài?”"),t("br"),t("br"),n._v("Hồ trưởng lão hô hấp dồn dập, hai mắt tỏa ánh sáng."),t("br"),t("br"),n._v("Tinh kim thất phẩm quan tài, chỉ là chứa đựng võ hồn kim nhân đồ vật, mà xích kim quan tài, mới là vu hồn sư thứ trọng yếu nhất, đồng dạng là quan tài, nhưng giá trị tuyệt đối so với Vô Hồn Kim Nhân muốn lớn hơn!"),t("br"),t("br"),n._v("Cái tên này lại có thứ đồ tốt này?"),t("br"),t("br"),n._v("“Vâng!” Tống Siêu gật đầu."),t("br"),n._v(" “Quá tốt rồi, người ở đâu? Cái này xích kim quan tài, hắn bán ra không bán ra, ngươi mua trở lại chưa?”"),t("br"),t("br"),n._v("Hồ trưởng lão vội vàng hỏi."),t("br"),t("br"),n._v("“Cái này... Xích kim quan tài hắn nói muốn bán ấy nhỉ, chỉ là... Trên người ta không đủ tiền, mua không nổi. Không nói cái khác, liền cái này một ngụm tinh kim thất phẩm quan tài, liền muốn ròng rã một cái linh thạch trung phẩm...”"),t("br"),t("br"),n._v("Tống Siêu nói."),t("br"),t("br"),n._v("“Tinh kim thất phẩm quan tài là tứ tinh luyện khí sư luyện chế mà thành, bảo tồn Vô Hồn Kim Nhân vạn năm bất hủ, một cái linh thạch trung phẩm, không những không đắt, giá cả còn rất rẻ! Không tệ, không tệ!”"),t("br"),t("br"),n._v("Hồ trưởng lão gật đầu."),t("br"),t("br"),n._v("Tứ tinh luyện khí sư tự mình ra tay luyện chế, các loại tài liệu trân quý, lại thêm liên quan vu hồn sư bí mật, bất kể từ điểm nào nhất nhìn, một cái linh thạch trung phẩm, cũng không tính là đắt."),t("br"),t("br"),n._v("Thậm chí đã có thể nói lương tâm giá cả, già trẻ không gạt."),t("br"),t("br"),n._v("“Cái kia... Trưởng lão có ý tứ là mua?” Nghe đối phương nói mình không có mua lỗ, Tống Siêu lúc này mới thở phào nhẹ nhõm, nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Thứ này có thể cùng không thể cầu, mua khẳng định phải mua, chỉ bất quá... Ngươi cũng biết, ta tuy là trưởng lão, cung phụng cũng có hạn, tinh kim thất phẩm quan tài liền giá trị một cái linh thạch trung phẩm, xích kim quan tài khẳng định quý hơn, coi như ta... Có lẽ cũng trả giá không được ô vuông!”"),t("br"),t("br"),n._v("Mặc dù kích động, Hồ trưởng lão cũng có tự mình biết mình."),t("br"),t("br"),n._v("Hắn liền xem như tông môn trưởng lão, hàng năm cung phụng cũng là có hạn, linh thạch trung phẩm loại cấp bậc này đồ tốt, cũng không có nhiều."),t("br"),t("br"),n._v("“Đúng rồi, ngươi có chưa từng thử qua, dùng chương trình học ngọc phù mua sắm?” Nhịn không được nhìn qua."),t("br"),t("br"),n._v("“Chương trình học ngọc phù? Bán đồ người, bản thân chính là cái danh sư, cấp bậc... So ta chỉ cao hơn chứ không thấp hơn, sẽ không cần ngọc phù...”"),t("br"),t("br"),n._v("Tống Siêu cười khổ."),t("br"),t("br"),n._v("Lúc trước mua sắm bộ kia say rượu một giấc chiêm bao cảm giác, đối phương liền loại trừ linh thạch không bán, biết cái túi xách kia ở giữa là Trương sư về sau, cũng rõ ràng nguyên do."),t("br"),t("br"),n._v("Bản thân chính là danh sư, khẳng định có lợi hại hơn lão sư, chương trình học ngọc phù đối với những khác người tu luyện vô cùng trân quý, đối bọn hắn loại người này tới nói, ngược lại không quá quan tâm."),t("br"),t("br"),n._v("Thật giống như hợp kim có vàng mỏ người, hi vọng dùng vàng giao dịch những vật khác đồng dạng, khẳng định không hy vọng cầm thật vất vả lấy được bảo bối, lại dùng đến hối đoái vàng."),t("br"),t("br"),n._v("“Danh sư?” Hồ trưởng lão sững sờ."),t("br"),t("br"),n._v("Nếu như không phải danh sư, có thể dùng thủ đoạn khác, thu đồ, chỉ điểm loại hình, cùng bọn hắn thân phận đồng dạng, lại không được, thậm chí không ít thầm động tác cũng không dám đi làm."),t("br"),t("br"),n._v("Danh Sư đường, có bảo hộ danh sư nghĩa vụ, nếu quả thật dám bởi vì đồ vật mà bí quá hoá liều, bọn hắn cả cái tông môn làm không cẩn thận, đều lại nhận tổng bộ điều tra cùng xử lý."),t("br"),t("br"),n._v("“Như vậy đi, hắn ở đâu? Ngươi dẫn ta đi nhìn xem, ta muốn hỏi hỏi, xích kim quan tài đến cùng bao nhiêu tiền! Cũng có thể sớm chuẩn bị.”"),t("br"),t("br"),n._v("Do dự một lát, Hồ trưởng lão nói."),t("br"),t("br"),n._v("Không thể cứng rắn đoạt, không thể muốn những biện pháp khác, vậy cũng chỉ có thể mua, may mắn đối phương đối bán ra cũng không kháng cự."),t("br"),t("br"),n._v("“Liền ở tại vạn quốc liên minh tân khách trong phủ đệ!” Tống Siêu vội nói."),t("br"),t("br"),n._v("Mấy ngày nay không chỉ tại trưởng lão ngoài cửa chờ lấy, cũng nghe ngóng Trương Huyền nơi ở."),t("br"),t("br"),n._v("Từ dược viên rời khỏi, Trương Huyền không có che giấu dấu vết, liền Tô sư bọn người nhẹ nhõm tìm tới, hắn hỏi thăm ra đến cũng không khó."),t("br"),t("br"),n._v("“Vậy thì tốt, chuẩn bị ngựa, đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Hồ trưởng lão gật đầu."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Trương Huyền ngừng lại."),t("br"),t("br"),n._v("“Linh khí còn chưa đủ...”"),t("br"),t("br"),n._v("Vốn cho rằng một cái linh thạch trung phẩm, để hắn tấn cấp đến Nguyên Khí cảnh đỉnh phong phi thường có thể, không nghĩ tới căn bản không phải có chuyện như vậy."),t("br"),t("br"),n._v("Nương theo tu vi gia tăng, đối với linh khí cần càng nhiều, cái này một cái, cũng chỉ để hắn đi đến Nguyên Khí cảnh hậu kỳ mà thôi, khoảng cách đỉnh phong, còn có rất lớn một khoảng cách."),t("br"),t("br"),n._v("“Chí ít còn cần hai cái linh thạch trung phẩm, mới có thể đi đến đỉnh phong!”"),t("br"),t("br"),n._v("Cẩn thận đã tính toán một chút, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Từ Hàn đường chủ tay ở bên trong lấy được linh thạch, tu luyện Vu hồn dùng hết, cái này viên linh thạch lại không đủ, còn muốn tìm linh thạch trung phẩm, nào có dễ dàng như vậy."),t("br"),t("br"),n._v("Vốn cho rằng giàu có, không nghĩ tới bắt đầu tu luyện lại nên vì linh thạch sự tình bắt đầu phát sầu."),t("br"),t("br"),n._v("Hơn nữa, không chỉ hắn tu luyện, Tỉnh Hồn thảo bồi dưỡng, cũng cần cầu cực lớn, căn cứ suy tính, muốn hoàn toàn đào tạo thành chín, có lẽ so với hắn đột phá đến Nguyên Khí cảnh đỉnh phong, tiêu hao còn nhiều."),t("br"),t("br"),n._v("“Xem ra còn phải nghĩ biện pháp kiếm tiền ah...”"),t("br"),t("br"),n._v("Xoa xoa mi tâm, đứng dậy."),t("br"),t("br"),n._v("Nói có thể, có thể làm sao lợi nhuận?"),t("br"),t("br"),n._v("Bên trong phẩm cấp bậc linh thạch, Vạn Quốc thành minh chủ phủ đều không có mấy khối, chớ nói chi là những người khác."),t("br"),t("br"),n._v("“Quên đi, đi trước Âm Dương đầm, dựa theo Khang đường chủ nói, nơi đó linh khí dồi dào, thích hợp tu luyện, có lẽ không cần linh thạch cũng có thể tăng thực lực lên...”"),t("br"),t("br"),n._v("Mắt sáng lên."),t("br"),t("br"),n._v("Nếu là tu luyện bảo địa, có lẽ không cần linh thạch cũng có thể để tu vi tăng lên."),t("br"),t("br"),n._v("“Bất quá, trước đó, còn cần đem Hóa Phàm tam trọng Thiên Đạo công pháp làm đi ra!”"),t("br"),t("br"),n._v("Có công pháp, mới có thể tốt hơn tu luyện, nếu không đến linh khí đầy đủ bảo địa, không có đối ứng phương pháp tu luyện, cũng là uổng công."),t("br"),t("br"),n._v("“Loại tu luyện này công pháp, Danh Sư đường khẳng định rất nhiều, ta hiện tại là tứ tinh danh sư, có tư cách đi quan sát!”"),t("br"),t("br"),n._v("Muốn nói chỗ nào công pháp, võ kỹ nhiều nhất, khẳng định là Danh Sư đường Tàng Thư khố, đi nơi này sưu tập thư tịch, làm ít công to, khẳng định so với trước địa phương khác muốn đơn giản nhiều."),t("br"),t("br"),n._v("Hiện tại hắn là tứ tinh danh sư, có tư cách đi vào đọc sách, hiện tại vừa vặn còn có rảnh rỗi, đi qua nhìn một chút."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-554"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-556"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);