(window.webpackJsonp=window.webpackJsonp||[]).push([[1806],{2161:function(n,h,g){"use strict";g.r(h);var c=g(44),t=Object(c.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-548-nguyen-hoa-bang-nguyen"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-548-nguyen-hoa-bang-nguyen"}},[n._v("#")]),n._v(" Chương 548: Nguyên Hỏa Băng Nguyên")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("br"),g("br"),n._v("“Trong miệng các ngươi nói cái này Nguyên Hỏa Băng Nguyên người, đến cùng là cái gì?”"),g("br"),g("br"),n._v("Không để ý tới ở một bên lệ rơi đầy mặt Nhược Hoan công tử, Trương Huyền hỏi."),g("br"),g("br"),n._v("Không phải hắn không có lòng thông cảm, mà là cảm thấy khẳng định có chỗ có thể xoay chuyển, thật muốn chỉ có hai cái danh ngạch, không cách nào lấy hay bỏ, bản thân bồi thường đối phương, chỉ điểm hắn đột phá, cũng không đúng cái đại sự gì."),g("br"),g("br"),n._v("Chỉ điểm của hắn, tuyệt đối so với đi cái gì Âm Dương đầm hiệu quả càng tốt."),g("br"),g("br"),n._v("“Nguyên Hỏa Băng Nguyên là một cái thế lực lớn siêu cấp tên, cùng luyện đan sư, luyện khí sư công hội như thế, nắm trong tay không ít cường đại linh mạch, bảo địa, chính vì vậy, ta liền xem như Danh Sư đường đường chủ, cũng không dám đắc tội, chỉ có thể trả giá đắt tới trao đổi!”"),g("br"),g("br"),n._v("Khang đường chủ giải thích."),g("br"),g("br"),n._v("“Linh mạch, bảo địa?” Trương Huyền líu lưỡi."),g("br"),g("br"),n._v("Chỉ bằng vào đầu này, liền biết cái thế lực này không đơn giản, nếu không, thiên hạ nhiều như vậy lợi hại chức nghiệp giả, làm sao có thể chắp tay đem loại địa phương này để cho người ta?"),g("br"),g("br"),n._v("“Đúng vậy a, Nguyên Hỏa Băng Nguyên, mỗi cái phân bộ đều có một vị cung chủ, ta cùng giới này cung chủ, có một ít người giao tình, mới cứng rắn muốn xuống hai cái khảo hạch cơ hội... Nói thật, có thể không thể tiến vào trong đó, còn phải xem năng lực của các ngươi!”"),g("br"),g("br"),n._v("Khang đường chủ nói."),g("br"),g("br"),n._v("“Người giao tình?”"),g("br"),g("br"),n._v("ngantruyen.com"),g("br"),n._v("“Ừm, nàng từng chịu qua tổn thương, là ta ra tay cứu trị tốt!”"),g("br"),g("br"),n._v("Khang đường chủ vuốt râu: “Chính là lần trước nói cho ngươi cái kia tu luyện Đại Lăng Hoa công, ngực bị nóng rực kiếm khí đâm thủng vị kia!”"),g("br"),g("br"),n._v("“Ah!”"),g("br"),g("br"),n._v("Trương Huyền gật đầu."),g("br"),g("br"),n._v("Hôm qua mới vừa thấy Khang đường chủ thời điểm, hắn liền từng khảo nghiệm qua bản thân, nói ra một cái bản thân bị trọng thương, nhưng muốn đột phá Hóa Phàm án lệ."),g("br"),g("br"),n._v("Tình huống kia rất phức tạp, Khang đường chủ có thể giải quyết, lấy được nhân tình, đương nhiên."),g("br"),g("br"),n._v("Lại hàn huyên một hồi, Trương Huyền đối Nguyên Hỏa Băng Nguyên cũng coi như có chút ít hiểu."),g("br"),g("br"),n._v("Cái thế lực này, nắm giữ bảo địa, vật tư không thiếu, cái gọi là tài phú tự nhiên cũng là không quan tâm, cũng chỉ có danh sư, mới có thể để cho hắn cam tâm xuất ra hai cái danh ngạch, những nghề nghiệp khác, muốn đều không cần suy nghĩ."),g("br"),g("br"),n._v("Tùy tiện hàn huyên một hồi, mọi người liền bắt đầu tu luyện."),g("br"),g("br"),n._v("Cũng không đủ linh thạch, Trương Huyền cũng là không có tiếp tục đột phá cảnh giới, mà là củng cố hiện nay tu vi, gia tăng chân khí số lượng dự trữ."),g("br"),g("br"),n._v("Tu vi đi đến Nguyên Khí cảnh hậu kỳ, lực lượng lần nữa có gia tăng, trước đó chỉ có hai mươi vạn đỉnh, mà bây giờ, tăng lên gấp đôi, đạt đến trọn vẹn bốn mươi vạn đỉnh."),g("br"),g("br"),n._v("Bình thường Nguyên Khí cảnh sơ kỳ, 15 vạn đỉnh lực lượng, trung kỳ 20 vạn đỉnh, hậu kỳ 25 vạn đỉnh, đỉnh phong 30 vạn. Mà Âm Dương cảnh thì là mười vạn đỉnh lực lượng vì một cái tấn cấp đơn vị, sơ kỳ 50 vạn, trung kỳ 60 vạ, hậu kỳ 70 vạn, đỉnh phong 80 vạn!"),g("br"),g("br"),n._v("Hắn mới Nguyên Khí cảnh hậu kỳ, liền có lực lượng lớn như vậy, so Nguyên Khí cảnh đỉnh phong đều mạnh hơn rất nhiều."),g("br"),g("br"),n._v("Nếu như lại phối hợp Hóa Phàm nhị trọng đỉnh phong Vu hồn chi lực, lại thêm là vượt qua 70 vạn, Âm Dương cảnh hậu kỳ cường giả, đều có thể một trận chiến."),g("br"),g("br"),n._v("Đương nhiên, đây chỉ là trên lý luận, cường giả chân chính không chỉ là lực lượng mạnh mẽ, càng quan trọng hơn là ý thức chiến đấu cùng năng lực, vượt cấp khiêu chiến, nếu quả thật cùng suy nghĩ một chút bên trong đơn giản như vậy, Danh Sư đường khẳng định càng thêm quyền uy."),g("br"),g("br"),n._v("Dù vậy, cũng nhất định có thể nhẹ nhõm chiến thắng Âm Dương cảnh trung kỳ cường giả."),g("br"),g("br"),n._v("Lực lượng tăng lên, thân thể phản ứng, cơ bắp cũng có tiến bộ, mặc dù không có vừa bước vào Hóa Phàm rõ ràng như vậy, nhưng cũng ánh mắt càng thêm sắc bén, thân pháp càng thêm mau lẹ."),g("br"),g("br"),n._v("“Vừa vặn có thời gian, đem Thiên Đạo kiếm pháp, thương pháp, đao pháp lại nghiên cứu một chút!”"),g("br"),g("br"),n._v("Củng cố tiếp cận mười ngày tu vi, để chân khí số lượng dự trữ tăng lên không ít, tinh thần khẽ động, nghĩ đến võ kỹ."),g("br"),g("br"),n._v("Hắn hiện đang thi triển Thiên Đạo kiếm pháp, thân pháp vẫn là tại Thiên Huyền vương quốc sưu tập sửa sang lại, mặc dù sau đó tới cũng tăng thêm qua, có thể đã không thỏa mãn được tu vi hiện tại, cần một lần nữa chỉnh lý, dung hợp cấp bậc cao hơn chiêu số."),g("br"),g("br"),n._v("Cùng nhau đi tới, sưu tập không ít võ kỹ, lại thêm đem Vạn Quốc thành Danh Sư đường võ kỹ thư tịch thu sạch ghi chép, có những vật này, sửa sang lại đến cũng không khó khăn."),g("br"),g("br"),n._v("Trước chỉnh lý Thiên Đạo kiếm pháp."),g("br"),g("br"),n._v("Kiếm là bách binh chi quân, chỉ cần là người tu luyện, hầu như không có không biết, bởi vậy, liên quan tới kiếm pháp bí tịch rất nhiều, tìm ra được cũng đã rất là thuận tiện."),g("br"),g("br"),n._v("Thời gian không dài, liền chỉnh lý ra một bộ hoàn chỉnh Thiên Đạo kiếm pháp."),g("br"),g("br"),n._v("Thú trên lưng địa phương chật hẹp không cách nào thi triển, liền trong đầu thôi diễn, thời gian không dài, liền đã học được."),g("br"),g("br"),n._v("Học xong kiếm pháp, tiếp theo là đao pháp, thương pháp, quyền pháp, cước pháp, thân pháp."),g("br"),g("br"),n._v("Đem những bí tịch này tất cả đều sửa sang lại một phen, cả người sức chiến đấu lần nữa có bay vọt về chất."),g("br"),g("br"),n._v("Nếu như nói trước đó, chỉ bằng vào chân khí, chỉ có thể cùng Nguyên Khí cảnh đỉnh phong chiến đấu, mà bây giờ, vượt cấp khiêu chiến Âm Dương cảnh sơ kỳ, đều không là vấn đề."),g("br"),g("br"),n._v("Lại thêm hồn lực, coi như Hóa Phàm tam trọng hậu kỳ cường giả, chỉ sợ cũng có thể nhẹ nhõm ngược sát."),g("br"),g("br"),n._v("Đương nhiên, trước mắt hắn thủ đoạn mạnh nhất không phải tu vi, mà là Minh Lý Chi Nhãn cùng sư ngôn thiên bẩm."),g("br"),g("br"),n._v("Cái trước đó có thể thấy được thiếu hụt cùng lỗ thủng, người sau có thể mê hoặc nhân tâm, để cho người ta không tự chủ được tin phục, hai thứ này liên hợp thi triển, đừng nói Nhược Hoan công tử, ngay cả Khang đường chủ đều rất dễ dàng bị mê hoặc."),g("br"),g("br"),n._v("Hắn một mực tu luyện, một bên Triệu Nhã cũng không có nghỉ ngơi, trải qua những ngày này cố gắng, rốt cục đột phá Chí Tôn ràng buộc, đi đến nửa bước Hóa Phàm."),g("br"),g("br"),n._v("Ngắn ngủi thời gian mấy tháng, từ võ giả nhất trọng đi đến cảnh giới như thế, truyền đi tuyệt đối có thể để cho vô số người hoảng sợ."),g("br"),g("br"),n._v("“Phía trước chính là Âm Dương đầm vị trí!”"),g("br"),n._v("Đi không biết bao lâu, đang tu luyện, liền nghe đến Khang đường chủ tiếng âm vang lên, Trương Huyền đứng dậy."),g("br"),g("br"),n._v("Xuyên thấu qua cửa sổ, nhìn thấy một tòa nguy nga đại sơn đứng sừng sững ở cách đó không xa, đỉnh núi tuyết trắng mênh mang, một tòa màu xanh lá cây đậm cung điện, đứng sừng sững trong đó, hết sức chói mắt."),g("br"),g("br"),n._v("“Đây chính là Băng Nguyên cung, Âm Dương đầm ngay tại cung điện bên trong!”"),g("br"),g("br"),n._v("Khang đường chủ xa xa một chỉ."),g("br"),g("br"),n._v("Xuyên thấu qua trắng noãn núi tuyết, quả nhiên thấy phía trên cung điện bức hoành bên trên, “Băng Nguyên cung” ba chữ to, hết sức chói mắt."),g("br"),g("br"),n._v("Yên tĩnh, trang nghiêm."),g("br"),g("br"),n._v("“Nơi này quanh năm tuyết đọng, nhưng các ngươi có lẽ nghĩ không ra, Băng Nguyên cung không có thành lập trước, đây là một tòa hỏa diệm sơn, cực nóng khó nhịn, coi như Hóa Phàm cường giả đi ngang qua, đều muốn đào một lớp da.”"),g("br"),g("br"),n._v("Đứng tại phía trước cửa sổ, nhìn cách đó không xa đang Phiêu Tuyết sơn mạch, Khang đường chủ nói."),g("br"),g("br"),n._v("“Núi lửa?”"),g("br"),g("br"),n._v("Không chỉ Trương Huyền sửng sốt, ngay cả Nhược Hoan công tử cũng rất giống chưa từng nghe qua, tràn đầy kỳ lạ."),g("br"),g("br"),n._v("Trước mắt ngọn núi này, cao vút trong mây, tuyết trắng mênh mang, vừa nhìn liền biết lạnh lẽo thấu xương, làm sao có thể là hỏa diệm sơn, cường giả khó mà tới gần?"),g("br"),g("br"),n._v("“Nói thật, vừa mới bắt đầu biết tin tức này thời điểm, ta cũng không tin!”"),g("br"),g("br"),n._v("Nhìn ra mọi người nghi hoặc, Khang đường chủ lắc đầu: “Đây chính là Nguyên Hỏa Băng Nguyên người mạnh mẽ! Không phải chiếm trước bảo địa, mà là cải biến phong thuỷ, bồi dưỡng bảo địa. Trải qua tay của bọn hắn, rất nhiều nguyên bản ác liệt môi trường, đều lại biến thành linh khí hội tụ trụ sở, để cho người ta hướng tới.”"),g("br"),g("br"),n._v("“Chẳng lẽ... Cái này cái gọi là Nguyên Hỏa Băng Nguyên người, cũng là một loại đặc thù chức nghiệp?”"),g("br"),g("br"),n._v("Trương Huyền nhịn không được hỏi."),g("br"),g("br"),n._v("Có thể hóa phế vì bảo, đem người người sợ hãi hỏa diệm sơn biến thành núi tuyết, loại trừ đặc thù chức nghiệp, thực sự nghĩ không ra có người nào lợi hại như vậy."),g("br"),g("br"),n._v("“Muốn nói là đặc thù chức nghiệp, cũng đúng, muốn nói không phải, cũng không đúng!”"),g("br"),g("br"),n._v("Khang đường chủ nói xong thấy mọi người tràn đầy mê hoặc, cười cười giải thích: “Đặc thù chức nghiệp, là Danh Sư đường quyết định, cái thứ nhất quy định, chính là muốn có hoàn chỉnh truyền thừa cùng nghiêm khắc đẳng cấp phân chia. Nhưng có chút kỳ quái chức nghiệp, đẳng cấp không có cách nào kỹ càng phân chia, thật giống như dưỡng dược người.”"),g("br"),g("br"),n._v("“Chỉ phải hiểu được tri thức nhiều, liền có thể đem dược vật nuôi sống, cũng không phải là cấp bậc càng cao, bồi dưỡng dược lại càng tốt...”"),g("br"),g("br"),n._v("“Không có cách nào xác định đẳng cấp, cũng sẽ không thể định tính vì đặc thù chức nghiệp. Nguyên Hỏa Băng Nguyên người, cũng là như vậy, bọn hắn có phương pháp đặc thù, đem hỏa diệm sơn biến thành núi tuyết, cũng có biện pháp đem đầm lầy biến thành bình nguyên. Nhưng... Phương pháp không thống nhất, cũng không có truyền thừa, chỉ cùng thiên phú cùng năng lực có quan hệ!”"),g("br"),g("br"),n._v("“Lại nói, số lượng cực ít, đừng nói vạn quốc liên minh, toàn bộ Huyễn Vũ đế quốc phạm vi thống trị, cũng chỉ có như thế một chỗ... Sao có thể được xưng tụng chức nghiệp hai chữ? Nhưng muốn không nói là, lại có thường nhân khó mà với tới năng lực, đối với loại này, coi như Danh Sư đường cũng có chút đau đầu, không cách nào kết luận.”"),g("br"),g("br"),n._v("Trương Huyền gật đầu."),g("br"),g("br"),n._v("Đã mệnh danh chức nghiệp, tự nhiên là người khác có thể học được, đồng thời nắm giữ truyền thừa, phân bố cực lớn."),g("br"),g("br"),n._v("Cái này Nguyên Hỏa Băng Nguyên người, số lượng ít không nói, truyền thừa cũng có chút hỗn loạn, còn muốn có thiên phú và năng lực, tự nhiên cũng là không tạo thành cái gọi là chức nghiệp."),g("br"),g("br"),n._v("“Núi lửa cực nóng, núi tuyết băng hàn, bọn hắn làm sao làm được?” Triệu Nhã hiếu kỳ nhìn qua."),g("br"),g("br"),n._v("“Cụ thể ta cũng không biết, chỉ biết là một ngàn năm trước, nơi này vẫn là núi lửa, Nguyên Hỏa Băng Nguyên người, trải qua trăm năm cải tạo, liền thành cái bộ dáng này, hơn nữa, nghe nói còn làm ra Âm Dương đầm, Âm Dương dung hợp, Trọc Thanh giao thoa, có thể khiến người ta tu vi tăng nhiều! Chỉ cần không có đi đến Hóa Phàm ngũ trọng, ở bên trong tu luyện, đều có lợi ích rất lớn.”"),g("br"),g("br"),n._v("Khang đường chủ lắc đầu: “Bất quá, cái này Âm Dương đầm, hàng năm chỉ có mấy cái danh ngạch, mười phần thưa thớt, ngay cả nội bộ bọn họ người đều không đủ dùng, chớ nói chi là người ngoài. Rất nhiều người đều là cầu một cái danh ngạch mà không thể được, phiền phức không gì sánh được.”"),g("br"),g("br"),n._v("Như thế nào cải tạo, là Nguyên Hỏa Băng Nguyên bí mật của người, coi như hắn là danh sư, đọc rất nhiều sách, cũng không biết rõ tình hình."),g("br"),g("br"),n._v("“Có thể là mượn nhờ địa hình, sơn mạch cấu thành đại trận đi!”"),g("br"),g("br"),n._v("Nhược Hoan công tử chen vào nói."),g("br"),g("br"),n._v("Có thể cứ thế mà cải biến một hoàn cảnh, trận pháp cũng có thể làm được, có lẽ cái này băng nguyên người dùng chính là đại trận."),g("br"),g("br"),n._v("“Không phải trận pháp, nếu như là trận pháp lời nói, nơi này sơn mạch liên miên chập trùng, bao phủ mấy trăm cây số, cần muốn bao lớn trận pháp? Lại cần cỡ nào đầy đủ linh khí mới có thể duy trì?”"),g("br"),g("br"),n._v("Khang đường chủ nói."),g("br"),g("br"),n._v("Trận pháp mặc dù có thể cải biến môi trường, nhưng là phạm vi bình thường đều rất nhỏ, bao phủ mấy trăm cây số, để ròng rã một ngọn núi cũng vì đó cải biến, hầu như là không thể nào!"),g("br"),g("br"),n._v("Đừng nói một đám Hóa Phàm tứ trọng người, coi như cửu trọng cũng không thể hoàn thành, có lẽ chỉ có đi đến loại kia siêu thoát cửu trọng siêu cấp cường giả mới có thể làm đến."),g("br"),g("br"),n._v("“Quả thực không phải trận pháp!”"),g("br"),g("br"),n._v("Trương Huyền cũng lắc đầu."),g("br"),g("br"),n._v("Vừa rồi Khang đường chủ lúc nói, hắn liền dùng Minh Lý Chi Nhãn quan sát, cả toà sơn mạch không có trận pháp mảy may dấu vết, nói rõ tuyệt không phải trận pháp cải tạo mà thành."),g("br"),g("br"),n._v("Nếu không, nhờ vào nhãn lực của hắn không có khả năng nhìn không ra."),g("br"),g("br"),n._v("“Quên đi, đừng quản cái gì, chúng ta tới này mục đích là đi vào Âm Dương đầm, không phải đến nghiên cứu bọn hắn lai lịch.”"),g("br"),g("br"),n._v("Cười cười, cắt ngang mọi người tiếp tục suy đoán, Khang đường chủ nhìn lại, vẻ mặt nghiêm túc: “Một lát nữa, sau khi tới, đừng tự tiện hành động, hết thảy nghe ta phân phó, tận lực tranh thủ ba cái danh ngạch. Bây giờ bất thành công, chúng ta cũng phải bảo đảm hai cái danh ngạch, đều có thể thông qua khảo hạch!”"),g("br"),g("br"),n._v("“Ừm!” Mọi người gật đầu."),g("br"),g("br"),n._v("“Vậy thì tốt, đi xuống đi!”"),g("br"),g("br"),n._v("Nương theo một thanh âm, linh thú phi hành cánh khẽ động, thẳng tắp hướng cách đó không xa Băng Nguyên cung bay đi."),g("br"),g("br"),n._v("Convert by: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-556"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-558"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=t.exports}}]);