(window.webpackJsonp=window.webpackJsonp||[]).push([[1807],{2162:function(n,h,g){"use strict";g.r(h);var t=g(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-549-cho-nguoi-mot-co-hoi"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-549-cho-nguoi-mot-co-hoi"}},[n._v("#")]),n._v(" Chương 549: Cho ngươi một cơ hội")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("br"),g("br"),n._v("Phi hành man thú rơi trên mặt đất, bốn người đi xuống."),g("br"),g("br"),n._v("Trên núi có tuyết rơi, mọi người thực lực kém nhất Triệu Nhã đều đạt đến nửa bước Hóa Phàm, mặc dù lạnh lẽo, ngược lại cũng ảnh hưởng không lớn."),g("br"),g("br"),n._v("Sửa sang lại quần áo một chút, Trương Huyền hướng trước mắt Băng Nguyên cung nhìn lại."),g("br"),g("br"),n._v("Từ đằng xa chỉ cảm thấy cung điện hùng vĩ, đi tới trước mặt, mới biết được là cỡ nào cực lớn, vượt ngang hơn ngàn mét, độ cao cũng đạt tới mấy chục mét, tựa như một tòa cự nhân phủ đệ."),g("br"),g("br"),n._v("Trước mắt là đại môn, cao tới mười mấy mét, không biết cái gì chế tạo thành, cho người ta một loại dày nặng cảm giác."),g("br"),g("br"),n._v("“Vạn Quốc thành Khang Cảm, đúng hẹn đến đây, mong rằng Ngô cung chủ thấy một lần!”"),g("br"),g("br"),n._v("Đi tới trước mặt, Khang đường chủ hít sâu một hơi, thanh âm lang lãng vang lên, vào bên trong truyền lại mà đi."),g("br"),g("br"),n._v("Một lát sau, đại môn “Kẹt kẹt!” Mở ra, mấy cái thân ảnh đi ra, tất cả đều là một bộ áo trắng nữ tử, đi đầu một cái, đi tới trước mặt: “Khang đường chủ, cung chủ có lệnh, mời vào bên trong!”"),g("br"),g("br"),n._v("“Đa tạ!”"),g("br"),g("br"),n._v("Gật gật đầu, Khang đường chủ mang theo mọi người đi vào trong."),g("br"),g("br"),n._v("Vừa đi Trương Huyền bên cạnh hướng những nữ đệ tử này nhìn lại, tuổi tác thoạt nhìn cũng không lớn, chỉ có hai mươi tuổi, tu vi nhưng tất cả đều không yếu, đều là Hóa Phàm tam trọng khoảng chừng."),g("br"),g("br"),n._v("  Từng tuổi này, liền có như thế tu vi, coi như tại Vạn Quốc thành đều được cho người nổi bật."),g("br"),g("br"),n._v("Lời mới vừa nói nữ tử, càng là đạt đến Hóa Phàm tam trọng đỉnh phong, toàn thân áo trắng chiếu rọi tại trắng noãn trong đống tuyết, cho người ta một loại cô lạnh cảm giác."),g("br"),g("br"),n._v("Đi vào Băng Nguyên cung, vừa đi vào đại môn, lập tức cảm thấy linh khí trong đó, trở nên sinh động, bất cứ lúc nào cũng sẽ hướng trong lỗ chân lông chui, để cho người ta tu luyện càng thêm đơn giản."),g("br"),g("br"),n._v("“Thật sinh động linh khí!”"),g("br"),g("br"),n._v("Trương Huyền ánh mắt sáng lên."),g("br"),g("br"),n._v("Nơi này linh khí, mặc dù cùng ngoại giới xấp xỉ nồng độ, nhưng dị thường hoạt động mạnh, coi như không tận lực dẫn dắt, cũng có thể chui vào lỗ chân lông, rèn luyện thân thể, tịnh hóa tu vi, để cho người ta tốc độ tu luyện gấp bội."),g("br"),g("br"),n._v("Cái này rất giống có áp lực nước so sánh nước đọng như thế, đi vào cống rãnh, tốc độ khẳng định càng nhanh."),g("br"),g("br"),n._v("Những cô bé này, tuổi còn trẻ đều thực lực mạnh như vậy, xem ra ở loại địa phương này tu luyện, làm ít công to, cho dù không phải trận pháp hội tụ linh khí đầy đủ chỗ, cũng tuyệt đối được cho tu luyện bảo địa."),g("br"),g("br"),n._v("Càng hướng phía trong, linh khí hoạt động mạnh mức độ càng lớn, cho người ta một loại không nhịn được muốn ngồi xuống tu luyện xúc động."),g("br"),g("br"),n._v("Còn không có nhìn thấy cái gọi là Âm Dương đầm, liền có loại cảm giác này, khó trách Khang đường chủ tốn hao cực lớn đánh đổi, không tiếc phi hành nhiều ngày trôi qua như vậy đến nơi đây, quả thực đáng giá."),g("br"),g("br"),n._v("Trong cung điện, không riêng gì nữ tử áo trắng, còn có một số mặc quần áo màu đỏ, cũng tất cả đều là nữ tử, trên người các nàng thì không giống cái trước như vậy thanh lãnh, mà là cực nóng như lửa, cho người ta một loại mãnh liệt bộc phát cảm giác."),g("br"),g("br"),n._v("“Đây là Băng Nguyên cung dương công người tu luyện cùng âm công người tu luyện, truyền thụ cho đều là tinh khiết nhất đơn thuộc tính công pháp!”"),g("br"),g("br"),n._v("Nhìn ra hắn nghi hoặc, Khang đường chủ vừa đi vừa truyền âm tới."),g("br"),g("br"),n._v("ngantruyen.com"),g("br"),n._v("Trương Huyền gật đầu."),g("br"),g("br"),n._v("Kỳ thật không cần hắn nói, dùng Minh Lý Chi Nhãn cũng nhìn ra đầu mối."),g("br"),g("br"),n._v("Trang phục màu đỏ rực nữ tử tu luyện đều là hỏa thuộc tính hoặc là dương thuộc tính công pháp, chân khí cực nóng, tựa như hỏa diễm, cho nên mới cho người ta một loại mãnh liệt bộc phát cảm giác, mà cho bọn hắn dẫn đường nữ tử áo trắng, tu luyện thuộc tính âm hàn lực lượng, ăn nói có ý tứ, cho người ta một loại từ chối chi ngàn dặm hương vị."),g("br"),g("br"),n._v("Công pháp không giống, cho người cảm giác hoàn toàn khác biệt."),g("br"),g("br"),n._v("“Âm Dương đầm, chia làm dương đầm, âm đầm, cùng khu hỗn hợp vực, tu luyện đem đối ứng công pháp, mới có thể quản lý.”"),g("br"),g("br"),n._v("Khang đường chủ tiếp tục nói."),g("br"),g("br"),n._v("Trương Huyền kinh ngạc."),g("br"),g("br"),n._v("Trước đó nghe được Âm Dương đầm, còn tưởng rằng chính là một chỗ bảo địa danh xưng, hiện tại xem ra, chỉ sợ không có đơn giản như vậy."),g("br"),g("br"),n._v("Đi một hồi, cô gái dẫn đường ngừng lại: “Cung chủ liền tại bên trong, mấy vị mời vào bên trong.”"),g("br"),g("br"),n._v("Khang đường chủ cùng mọi người đi thẳng về phía trước."),g("br"),g("br"),n._v("Là cái rộng gian phòng, chung quanh trang trí không những không xa hoa, ngược lại thoạt nhìn có chút đơn giản, một nữ tử ngồi ngay ngắn ở ở giữa nhất vị trí bên trên, thần sắc lạnh nhạt nhìn qua."),g("br"),g("br"),n._v("Đối phương xem bọn hắn, Trương Huyền cũng đem ánh mắt tập trung tới."),g("br"),g("br"),n._v("Nữ tử trước mắt này, thoạt nhìn chừng ba mươi tuổi, chẳng qua dung mạo xinh đẹp, tuế nguyệt ở trên mặt không có lưu lại quá nhiều dấu vết, một đầu tóc dài đen nhánh, phối hợp trắng như tuyết quần áo, có vẻ như là nét bên trong chi nhân, mười phần đẹp mắt."),g("br"),g("br"),n._v("Đương nhiên, mặc dù rất xinh đẹp, cùng Triệu Nhã so ra vẫn là hơi có không bằng, nhìn thoáng qua, Trương Huyền liền quan sát tu vi của nàng."),g("br"),g("br"),n._v("Một thân khí tức giấu ở trong cơ thể, để cho người ta khó mà cảm thấy, nhưng cho Trương Huyền một loại nguy hiểm cảm giác, nhất là linh hồn mang theo cực mạnh áp bách, phảng phất giấu ở trong cơ thể võ hồn đều có thể bị tuỳ tiện xem thấu."),g("br"),g("br"),n._v("“Chẳng lẽ là... Hợp Linh cảnh?”"),g("br"),g("br"),n._v("Trong lòng hơi động."),g("br"),g("br"),n._v("Trước mắt vị này, mang đến cho hắn một cảm giác, rất rõ ràng so Triệu minh chủ, Tiết Nhất Dao, Khang đường chủ đều mãnh liệt hơn, cảm giác áp bách cũng lớn hơn, ba vị trước, đều là đi đến Hóa Phàm tứ trọng đỉnh phong nhất nhân vật, lợi hại hơn, chỉ sợ đã đột phá tầng kia ràng buộc."),g("br"),g("br"),n._v("Hóa Phàm ngũ trọng Hợp Linh cảnh, linh hồn lấy được trên phạm vi lớn gia tăng, cùng tự nhiên càng thêm phù hợp."),g("br"),g("br"),n._v("Loại này cường giả, giơ tay nhấc chân, mang theo lực lượng cường đại hơn, chiến lực phi phàm."),g("br"),g("br"),n._v("“Minh Lý Chi Nhãn!”"),g("br"),g("br"),n._v("Minh Lý Chi Nhãn vận chuyển, Trương Huyền nhìn sang, chỉ nhìn một chút, lông mày nhịn không được nhíu một cái."),g("br"),g("br"),n._v("Ngay tại quan sát, chỉ thấy Khang đường chủ tiến về phía trước một bước, cười ôm quyền."),g("br"),g("br"),n._v("“Ngô cung chủ, từ khi chia tay đến giờ không có vấn đề gì chứ!”"),g("br"),g("br"),n._v("Xem ra cũng biết thực lực của nàng mạnh mẽ, coi như Khang đường chủ thân là tứ tinh đỉnh phong danh sư, cũng không dám làm lớn, mà là thái độ cung kính."),g("br"),g("br"),n._v("“Từ khi chia tay đến giờ không có vấn đề gì chứ! Đây chính là ngươi tuyển ra muốn đi vào Âm Dương đầm khảo hạch người?”"),g("br"),g("br"),n._v("Nữ tử Ngô cung chủ trả lời một câu, con mắt rơi vào trên thân mọi người, nhướng mày: “Thế nào lại là ba cái?”"),g("br"),n._v(" “Là dạng này, ba người này, đều là thế hệ trẻ tuổi ưu tú nhất người mới, ta thực sự khó mà lấy hay bỏ, toàn bộ mang tới, là hi vọng cung chủ có thể dàn xếp, để bọn hắn đều tham gia khảo hạch, thắng được đi vào Âm Dương đầm cơ hội!”"),g("br"),g("br"),n._v("Khang đường chủ vội vàng giải thích."),g("br"),g("br"),n._v("“Ta Âm Dương đầm quy củ, đường chủ hẳn là rõ ràng, hai cái danh ngạch, chính là hai cái, không thể phá!”"),g("br"),g("br"),n._v("Ngô cung chủ khoát tay áo, hình như một khối lạnh lẽo băng u cục, không quan tâm trước kia giao tình."),g("br"),g("br"),n._v("“Cái này... Được rồi!”"),g("br"),g("br"),n._v("Thấy nàng thái độ kiên quyết, không có chút nào đường lùi, Khang đường chủ biết nói nhiều hơn nữa khẳng định cũng vô dụng, đành phải cười khổ lắc đầu, lần nữa nhìn qua: “Không biết như thế nào khảo hạch, ta cũng tốt để bọn hắn chuẩn bị một chút!”"),g("br"),g("br"),n._v("“Rất đơn giản, chỉ cần có thể tại ta băng hỏa phòng bên trong thành công đi ra, coi như thông qua!” Ngô cung chủ thản nhiên nói."),g("br"),g("br"),n._v("“Băng hỏa phòng?” Khang đường chủ nhướng mày, dường như liền hắn cũng không rõ lắm."),g("br"),g("br"),n._v("“Băng hỏa phòng, là ta Băng Nguyên cung đặc hữu khảo nghiệm đồ vật, cùng Vấn Tâm thạch như thế, nhỏ vào máu tươi, tinh thần lại nhận băng hỏa dày vò, có thể chịu đựng lấy, đồng thời tại trong vòng một canh giờ, tìm tới đường ra rời khỏi, coi như thông qua! Ra không được, mất đi tư cách!”"),g("br"),g("br"),n._v("Ngô cung chủ giải thích."),g("br"),g("br"),n._v("Mọi người gật đầu."),g("br"),g("br"),n._v("Vốn cho rằng là dạng gì khảo hạch, không nghĩ tới là trên tinh thần ma luyện."),g("br"),g("br"),n._v("Loại này khảo hạch cùng ý chí lực có quan hệ, ngược lại cùng tu vi quan hệ không lớn."),g("br"),g("br"),n._v("“Trong ba người tuyển bạt hai cái, để ai tham gia, còn hi vọng mau chóng làm ra lựa chọn!” Giải thích hết, Ngô cung chủ tiếp tục nói."),g("br"),g("br"),n._v("“Không cần tuyển chọn, Nhược Hoan, ngươi cũng đừng tham gia!” Khang đường chủ quay đầu phân phó."),g("br"),g("br"),n._v("“Vâng...”"),g("br"),g("br"),n._v("Nhược Hoan công tử mặt mũi mướp đắng màu."),g("br"),g("br"),n._v("Mặc dù không có cam lòng, nhưng lão sư làm ra quyết định, đành phải gật đầu đáp ứng."),g("br"),g("br"),n._v("Làm danh sư hàng đầu tôn sư trọng đạo, mệnh lệnh của lão sư, không thể vi phạm."),g("br"),g("br"),n._v("Đang tràn đầy mất tinh thần, cảm thấy mất đi một cơ hội, liền nghe đến cách đó không xa Trương sư thanh âm nhàn nhạt vang lên: “Trước không vội!”"),g("br"),g("br"),n._v("Nói xong, ngẩng đầu nhìn về phía nữ tử trước mắt: “Ngô cung chủ, chúng ta muốn ba người đều tham gia khảo hạch lời nói, không biết có cái gì yêu cầu?”"),g("br"),g("br"),n._v("Nghe được chen vào nói, nhướng mày, Ngô cung chủ lộ ra vẻ không vui: “Khang đường chủ, đây chính là ngươi mang tới hậu bối? Trưởng bối nói chuyện, lúc nào đến phiên một cái vãn bối xen vào!”"),g("br"),g("br"),n._v("“Trương sư...”"),g("br"),g("br"),n._v("Cũng không nghĩ tới hắn lại đột nhiên nói chuyện, Khang đường chủ nhịn không được nhìn lại, tràn đầy sốt ruột."),g("br"),g("br"),n._v("Mặc dù chỉ có hai cái danh ngạch, dù sao có cơ hội, một khi chọc giận đối phương, cơ hội này cũng là không có."),g("br"),g("br"),n._v("Hắn tại vạn quốc liên minh địa vị rất cao, nhưng đối mặt toàn bộ Huyễn Vũ đế quốc chỉ có một cái Băng Nguyên cung, đường đường Hóa Phàm ngũ trọng cường giả, một cái phân bộ đường chủ, liền có vẻ không có ý nghĩa."),g("br"),g("br"),n._v("“Đạt giả vi sư, tuổi còn nhỏ, không nhất định chính là vãn bối, tuổi tác lớn, cũng có thể là là sống vô dụng rồi hồi lâu, lãng phí tuổi xuân phơi phới... Như vậy đi, Ngô cung chủ, ta cho ngươi một cơ hội... Tự mình ra tay giúp ngươi chỉ điểm một lần, đem đổi lấy một cái khảo hạch danh ngạch, như thế nào?”"),g("br"),g("br"),n._v("Không để ý tới Khang đường chủ khẩn trương, Trương Huyền nhàn nhạt nhìn qua."),g("br"),g("br"),n._v("“Ự... C?”"),g("br"),g("br"),n._v("Mắt tối sầm lại, Khang đường chủ, Nhược Hoan công tử đồng thời nhoáng một cái, đều kém chút không có thổ huyết."),g("br"),g("br"),n._v("Trên đường tới đã nói, không nên nói lung tung, Ngô cung chủ không tốt lắm ở chung..."),g("br"),g("br"),n._v("Không thấy được ngay cả đường chủ bản nhân, cũng đều một mực thận trọng ư?"),g("br"),g("br"),n._v("Ngươi chỉ điểm nàng một lần, còn tính là cho nàng cơ hội... Ta đi, không nói trước ngươi vừa mới khảo hạch trở thành tứ tinh danh sư, coi như tứ tinh đỉnh phong Khang đường chủ cũng không có tư cách này đi!"),g("br"),g("br"),n._v("Dù sao đối phương thế nhưng là thật Hóa Phàm ngũ trọng cường giả, vô luận tu vi hay là kiến thức đều vượt qua mọi người, ngươi một cái vừa mới đột phá nhị trọng gia hỏa, nói như vậy... Thật không sợ bị đánh chết?"),g("br"),g("br"),n._v("“Chỉ điểm ta?”"),g("br"),g("br"),n._v("Quả nhiên, nghe nói như thế, Ngô cung chủ lông mày trầm xuống, một cỗ cường đại khí tức từ trên người phát ra, để cho người ta không rét mà run: “Ngươi muốn chỉ điểm ta? Còn tính là cho ta cơ hội?”"),g("br"),g("br"),n._v("“Không sai, cơ hội chỉ có một lần, ngàn vạn nắm chặt, nếu không, về sau, muốn để cho ta chỉ điểm, cũng không có!”"),g("br"),g("br"),n._v("Không để ý tới trên người đối phương đốt lên khí tức, Trương Huyền lắc đầu."),g("br"),g("br"),n._v("“Triệu Nhã, ngươi nhanh khuyên nhủ...”"),g("br"),g("br"),n._v("Thấy vị này Trương sư càng nói càng thái quá, Nhược Hoan công tử khóe miệng co giật, nhịn không được cho một bên Triệu Nhã truyền âm."),g("br"),g("br"),n._v("Hắn biết Trương sư mười phần chú trọng người học sinh này, nàng nói chuyện, có lẽ có thể tạo được tác dụng."),g("br"),g("br"),n._v("“Tốt!”"),g("br"),g("br"),n._v("Triệu Nhã gật gật đầu, đi lên phía trước: “Ngô cung chủ, nhà chúng ta lão sư nói đúng, đây là một cơ hội, ngươi tuyệt đối đừng bỏ qua, nếu không, nhất định sẽ hối hận!”"),g("br"),g("br"),n._v("“Ta đi...”"),g("br"),g("br"),n._v("Nhược Hoan công tử kém chút không có một ngụm máu tươi phun ra."),g("br"),g("br"),n._v("Đại tỷ, ta là để ngươi khuyên can ngươi lão sư, để hắn không nên nói bậy... Ngươi vẻ mặt nghiêm túc khuyên Ngô cung chủ có ý tứ gì?"),g("br"),g("br"),n._v("Còn tuyệt đối đừng bỏ qua, nhất định sẽ hối hận..."),g("br"),g("br"),n._v("Ngươi đối ngươi lão sư cũng không tránh khỏi quá tin tưởng đi!"),g("br"),g("br"),n._v("Hóa Phàm nhị trọng danh sư nói như vậy một cái Hóa Phàm ngũ trọng cường giả, còn lời thề son sắt, không có chút nào xấu hổ cùng thẹn thùng..."),g("br"),g("br"),n._v("Các ngươi hai sư đồ, ở đâu ra tự tin?"),g("br"),g("br"),n._v("“Ngươi...”"),g("br"),g("br"),n._v("Ngô cung chủ cũng khí sắp nổ tung, hất lên ống tay áo: “Khang đường chủ, còn xin ngươi đem người mang đi, ta Băng Nguyên cung, không chào đón loại này lỗ mãng tự đại chi đồ!”"),g("br"),g("br"),n._v("Convert by: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-557"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-559"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);