(window.webpackJsonp=window.webpackJsonp||[]).push([[1809],{2164:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-47-hoang-ngu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-47-hoang-ngu"}},[n._v("#")]),n._v(" Chương 47: Hoàng Ngữ")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" “Công tử là mua sách vẫn là đọc sách?” "),t("br"),t("br"),n._v(" Đi vào phòng sách, một cô thiếu nữ tiến lên đón."),t("br"),t("br"),n._v(" Mười bảy, tám tuổi dáng dấp, một mặt thanh thuần, dáng dấp khá là cảm động, tuy rằng cùng Trầm Bích Như lão sư so với, hơi có không bằng, tuyệt đối cũng coi là trăm người chọn một mỹ nữ."),t("br"),t("br"),n._v(" “Nơi này có... Võ giả sáu tầng công pháp bí tịch sao?” "),t("br"),t("br"),n._v(" Trương Huyền đem mục đích của chính mình hỏi lên."),t("br"),t("br"),n._v(" “Võ giả sáu tầng công pháp? Công tử nói đùa, loại công pháp này, mỗi một bản đều có giá trị không nhỏ, chúng ta quyển vở nhỏ chuyện làm ăn nào có tư cách bán ra... Nếu như nói là nhập môn pháp quyết tu luyện, đúng là có mấy quyển...” Nữ hài lắc đầu."),t("br"),t("br"),n._v(" Các nàng cái này nhà sách, phần lớn đều là những người này văn địa lý, danh nhân truyện ký loại hình, công pháp thuộc về người tu luyện cơ mật, làm sao có khả năng tùy tiện quan sát, tùy tiện mua?"),t("br"),t("br"),n._v(" “Cái kia... Ngươi biết nơi nào có bán ra?” Thấy quả nhiên không có, Trương Huyền thở dài, mang theo ước ao nhìn sang."),t("br"),t("br"),n._v(" “Bán ra?” Nữ hài lắc đầu: “Loại cấp bậc này công pháp, không thể có người bán ra, toàn bộ Thiên Huyền Vương Thành đều không có, bất quá... Một ít cường giả trong nhà đều sẽ sưu tập không ít, quan hệ tốt, có thể đi qua quan sát một phen!” "),t("br"),t("br"),n._v(" “Quan sát? Có thể quan sát một hồi cũng tốt!” Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v(" Nắm giữ Thiên Đạo Thư Viện, hắn không cần mua, có loại sách này tịch, tùy tiện lật xem, liền có thể tự động tạo ra."),t("br"),t("br"),n._v(" “Ngươi cũng đã biết vị tiền bối nào trong nhà có tương tự thư tịch, hơn nữa cho phép người khác quan sát?” Không nhịn được hỏi."),t("br"),t("br"),n._v(" “Cho phép người khác quan sát?” Nữ hài hơi nhướng mày đang muốn lắc đầu, đột nhiên nhớ ra cái gì đó, trong mắt loé ra một nói giảo hoạt, nở nụ cười: “Ta ngược lại thật ra biết một vị, bất quá, hắn nhưng là có tiếng quái dị! Tâm tình tốt, ngươi đem nhà hắn sách toàn bộ chuyển quang đều không có gì, tâm tình không tốt, đừng nói nhìn, cửa cũng không thể để ngươi tiến vào...” "),t("br"),t("br"),n._v(" “Còn có người như vậy?” Trương Huyền lông mày nhíu một hồi, tùy tiện nói: “Vậy cũng không làm phiền cô nương cho ta chỉ điểm một chút, đi nơi nào tìm vị tiền bối này?” "),t("br"),t("br"),n._v(" “Vừa vặn ta chỗ này cũng không có gì chuyện làm ăn, ta trực tiếp mang ngươi tới!” Nữ hài cười cợt."),t("br"),t("br"),n._v(" “Làm phiền cô nương!” "),t("br"),t("br"),n._v(" Không nghĩ đến cái này nữ hài như vậy để bụng, muốn đích thân mang chính mình tới, có chút cảm kích gật gật đầu."),t("br"),t("br"),n._v(" Nữ hài phía trước dẫn đường, hai người đi ra ngoài."),t("br"),t("br"),n._v(" Thông qua nói chuyện phiếm, Trương Huyền biết cô bé này gọi Hoàng Ngữ, căn này phòng sách, chính là từ nàng đưa ra, đọc sách phòng dáng vẻ, mở thời gian cũng không trường, nhiều nhất mấy chục ngày mà thôi."),t("br"),t("br"),n._v(" “Ngươi nói vị này trong nhà sách rất nhiều sao?” Đi rồi một hồi, Trương Huyền không nhịn được hỏi."),t("br"),t("br"),n._v(" “Đó là đương nhiên, vị này chính là toàn bộ vương quốc đều có tiếng đại nho, Trầm Truy bệ hạ đã từng lão sư! Có thể tự do ra vào vương quốc tàng thư kho người, ngươi nói nhà hắn tàng thư có thể không phong phú sao?” Hoàng Ngữ một mặt sùng bái nói."),t("br"),t("br"),n._v(" “Trầm Truy bệ hạ lão sư? Đã từng đế sư?” Trương Huyền líu lưỡi."),t("br"),t("br"),n._v(" Thiên Huyền vương quốc Quốc vương bệ hạ gọi Trầm Truy, thực lực cụ thể không ai biết, chỉ biết là là vương quốc người số một, không người có thể địch!"),t("br"),t("br"),n._v(" Có thể làm người như thế lão sư, đều là ngàn chọn vạn tuyển, đi qua tầng tầng chân chọn, coi như bọn họ Hồng Thiên học viện Viện trưởng, e sợ đều không có tư cách!"),t("br"),t("br"),n._v(" “Là hắn thư họa lão sư, mà không phải tu vi lão sư, dù vậy, cũng rất lợi hại, sâu bị bệ hạ kính trọng!” Nói đến đây, Hoàng Ngữ sắc mặt nghiêm túc bàn giao: “Vị này Lục Trầm lão tiên sinh, luôn luôn phong nhã, chú ý quy củ, đáng ghét nhất vãn bối ăn nói linh tinh, đến lúc đó, ngươi tốt nhất đừng dễ dàng mở miệng, không phải vậy, muốn hỏi hắn mượn sách nhìn, không có cửa đâu!” "),t("br"),t("br"),n._v(" “Ừm!” Trương Huyền gật đầu."),t("br"),t("br"),n._v(" “Còn có, ta cũng có những chuyện khác, đến thời điểm mượn sách sự tình ngươi chính mình đi nói, chớ đem chuyện của ta trộn lẫn!” Hoàng Ngữ đôi mắt đẹp lật một cái, nói tiếp."),t("br"),t("br"),n._v(" “Yên tâm đi, sẽ không để cho ngươi khó xử!” Biết ý của nàng, Trương Huyền khẽ mỉm cười."),t("br"),t("br"),n._v(" Đối phương có thể đem hắn mang tới liền rất cảm kích, có thể mượn đọc bên trên, là bản lãnh của chính mình, mượn đọc không lên, vậy cũng không có cách nào."),t("br"),t("br"),n._v(" Bất kể nói thế nào, chính mình cũng là nhân dân giáo sư, làm sao có thể để một cái tiểu cô nương lúng túng đây?"),t("br"),t("br"),n._v(" “Lão tiên sinh khá là yêu thích nghe lời hậu bối, bất luận lão tiên sinh nói cái gì sự tình, ngươi gật đầu đáp ứng chính là, ngươi cũng biết hắn này loại đại nho, rất là bảo thủ, có thể có chút tư tưởng cùng người trẻ tuổi không liên hệ, không muốn tính toán là được rồi!” Hoàng Ngữ lần thứ hai bàn giao."),t("br"),t("br"),n._v(" Hai người một đường tiến lên, rất đi mau ra cửa hàng, vòng qua liên tiếp để đầu người ngất hoa mắt ngõ nhỏ, đi tới một cái trước phủ đệ."),t("br"),t("br"),n._v(" “Đây chính là Lục Trầm lão tiên sinh nơi ở!” Hoàng Ngữ chỉ về phía trước."),t("br"),t("br"),n._v(" Trương Huyền ngẩng đầu nhìn lại, trước mắt phủ đệ cũng không hề tưởng tượng xa hoa cùng bao la, rất bình thường môn hộ, liền cửa biển đều không có, nếu như không phải nữ hài lĩnh hắn lại đây, đều tưởng rằng cái bình dân quật."),t("br"),t("br"),n._v(" Đây là... Đế sư chỗ ở?"),t("br"),t("br"),n._v(" Để nhân có chút không dám tin tưởng."),t("br"),n._v("  “Lục Trầm lão tiên sinh, một đời tiết kiệm, Trầm Truy bệ hạ từng nhiều lần định cho hắn trùng tu phủ đệ, đều bị hắn từ chối thẳng thắn!” Nhìn ra hắn nghi hoặc, Hoàng Ngữ giải thích."),t("br"),t("br"),n._v(" Trương Huyền gật đầu, trong lòng nổi lòng tôn kính."),t("br"),t("br"),n._v(" Cao như vậy thân phận, nhưng có thể giữ mình trong sạch, không xa hoa lãng phí, không làm bộ, vị lão tiên sinh này, xem ra đích thật là tính tình bên trong người."),t("br"),t("br"),n._v(" Cộc cộc cộc cộc!"),t("br"),t("br"),n._v(" Đang định gõ cửa đi vào, chỉ thấy một chiếc xe ngựa chạy nhanh đến, tiếng vó ngựa vang vọng ở trong hẻm nhỏ, rõ ràng có thể nghe."),t("br"),t("br"),n._v(" Kẹt kẹt!"),t("br"),t("br"),n._v(" Xe ngựa đứng ở bên cạnh hai người, một người thiếu niên vén rèm xe lên, đi ra."),t("br"),t("br"),n._v(" Là cái mười bảy, tám tuổi thiếu niên, một bộ bạch y, vóc người thon dài, mặt như Quan Ngọc, vừa nhìn chính là cái công tử nhà giàu, có chứa cao cao tại thượng khí tức."),t("br"),t("br"),n._v(" “Tiểu Ngữ? Ngươi cũng tới!” "),t("br"),t("br"),n._v(" Nhìn thấy Hoàng Ngữ, công tử áo trắng ánh mắt sáng lên, lập tức một bộ lấy lòng dáng dấp."),t("br"),t("br"),n._v(" “Ta gọi Hoàng Ngữ, biệt khiếu buồn nôn như vậy, chúng ta còn không có quen đến trình độ như thế này!” Hoàng Ngữ một mặt ghét bỏ bĩu môi."),t("br"),t("br"),n._v(" “Dựa vào hai chúng ta nhà quan hệ, tiếng kêu Tiểu Ngữ cũng không có gì, lại nói, ngươi khi còn bé ta không đều một mực gọi như vậy à? Làm sao, ngày hôm nay ngươi cũng tới bái phỏng Lục Trầm lão tiên sinh?” Công tử áo trắng nhẹ nhàng nở nụ cười, vung một cái ống tay áo, làm ra một cái trí cuốn tại cầm động tác: “Khà khà, cái thứ kia, ngươi cũng đừng nghĩ, khẳng định là của ta!” "),t("br"),t("br"),n._v(" “Là ai còn chưa chắc chắn! Chỉ sợ ngươi đến thời điểm sẽ khóc!” Hoàng Ngữ một bước cũng không nhường."),t("br"),t("br"),n._v(" “Vậy thì chờ xem đi!” "),t("br"),t("br"),n._v(" Công tử áo trắng nhẹ nhàng nở nụ cười, lúc này mới nhìn đến Trương Huyền, hơi nhướng mày: “Đây là người nào? Tiểu Ngữ, ngươi làm sao có thể đem người ngoài mang tới...” "),t("br"),t("br"),n._v(" “Ta mang người nào, ăn thua gì tới ngươi!” "),t("br"),t("br"),n._v(" Hoàng Ngữ bĩu môi, chưa cho đối phương chút nào mặt mũi."),t("br"),t("br"),n._v(" “Hừ, tiểu tử, chẳng cần biết ngươi là ai, ta khuyên ngươi tốt nhất cách Tiểu Ngữ xa một chút, nàng không phải ngươi một tiểu nhân vật có thể chia sẻ!” Nhìn về phía Trương Huyền, công tử áo trắng híp mắt lại, lạnh lùng nói."),t("br"),t("br"),n._v(" “...” "),t("br"),t("br"),n._v(" Không nghĩ tới đến mượn cái sách đều bị uy hiếp, Trương Huyền lòng tràn đầy bất đắc dĩ."),t("br"),t("br"),n._v(" Dùng kiếp trước tới nói, thực sự là tất chó!"),t("br"),t("br"),n._v(" Cái này Hoàng Ngữ chính là đem hắn mang tới mà thôi, có len sợi quan hệ? Cần thiết hay không?"),t("br"),t("br"),n._v(" Bị nhân uy hiếp, trong lòng tràn đầy khó chịu, đưa tay ra mời lưng mỏi, khoát tay chặn lại: “Ngươi còn quản thật nhiều, thật không tiện, không làm phiền ngươi bận tâm!” "),t("br"),t("br"),n._v(" “Rất tốt, hi vọng không muốn vì là câu nói này hối hận!” "),t("br"),t("br"),n._v(" Không nghĩ tới chưa từng thấy qua một cái tiểu tử, lại dám cùng hắn nói chuyện như vậy, công tử áo trắng lông mày giương lên, ánh mắt híp lại, không tiếp tục để ý hai người, nhanh chân đi tới sân trước mặt, giơ tay gõ cửa."),t("br"),t("br"),n._v(" “Ngươi làm sao như thế lỗ mãng...” "),t("br"),t("br"),n._v(" Đối phương gõ cửa, Trương Huyền bên tai truyền đến Hoàng Ngữ mang theo lo lắng truyền âm."),t("br"),t("br"),n._v(" “Thế nào?” Trương Huyền nghi ngờ nhìn tới."),t("br"),t("br"),n._v(" “Ngươi biết hắn là ai a? Liền dám như vậy nói chuyện cùng hắn?” Thấy đối phương một mặt không biết chút nào dáng vẻ, Hoàng Ngữ có chút bất đắc dĩ."),t("br"),t("br"),n._v(" Thật không biết cái tên này là trái tim lớn, vẫn là đầu óc có vấn đề."),t("br"),t("br"),n._v(" “Quản hắn là ai...” Trương Huyền xua tay."),t("br"),t("br"),n._v(" Hắn là ai có quan hệ tới ta sao?"),t("br"),t("br"),n._v(" “Ngươi...” Thấy thiếu niên dáng vẻ, Hoàng Ngữ thật hoài nghi có phải là dẫn theo cái kẻ ngu, nhìn trong mắt đối phương xác thực không để ý chút nào, căn bản không để ý thân phận đối phương, không thèm để ý chút nào dáng vẻ, không thể làm gì khác hơn là im lặng giới thiệu: “Hắn là Trấn Nam Vương con trai độc nhất, Bạch Tốn!” "),t("br"),t("br"),n._v(" Trấn Nam Vương được xưng Thiên Huyền vương quốc đệ nhất quyền thần, vị trí gần như chỉ ở Trầm Truy bệ hạ phía dưới, đắc tội con trai độc nhất của hắn, cơ hồ bằng ở toàn bộ Thiên Huyền vương quốc đều khó mà cất bước."),t("br"),t("br"),n._v(" Vốn tưởng rằng nói ra thân phận đối phương, cái tên này sẽ cảm thấy khủng hoảng, hỏi dò nàng như thế nào giải quyết, lại phát hiện Trương Huyền một mặt thật thà nhìn sang, trong mắt mang theo nghi hoặc: “Trấn Nam Vương... Là ai?” "),t("br"),t("br"),n._v("Convert by: Hoang Chau"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-55"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-57"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);