(window.webpackJsonp=window.webpackJsonp||[]).push([[1810],{2165:function(n,h,g){"use strict";g.r(h);var t=g(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-551-truong-huyen-lam-mai-2-hop-1-】"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-551-truong-huyen-lam-mai-2-hop-1-】"}},[n._v("#")]),n._v(" Chương 551: Trương Huyền làm mai [2 hợp 1 】")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("br"),g("br"),n._v("Chương 551: Trương Huyền làm mai [2 hợp 1 】"),g("br"),g("br"),n._v("Để ngươi giúp ta trị liệu, hỏi ta Khang đường chủ làm gì?"),g("br"),g("br"),n._v("Ngô cung chủ mặt mũi nghi hoặc: “Ta cùng đường chủ nhiều năm trước liền nhận biết, được cho quen thuộc.”"),g("br"),g("br"),n._v("Một bên Khang Cảm cũng đầy đầu sương mù, không làm rõ ràng được vị này Trương sư đến cùng suy nghĩ gì."),g("br"),g("br"),n._v("“Quen thuộc liền đơn giản!”"),g("br"),g("br"),n._v("Trương Huyền mỉm cười, chậm rãi mà nói: “Chúng ta đường chủ quang minh chính trực, nhiệt tình vì lợi ích chung, càng là danh sư chức nghiệp, tiền đồ vô lượng, mấu chốt nhất là... Dáng dấp cũng rất tốt...”"),g("br"),g("br"),n._v("Nghe hắn càng nói càng nhiều, Ngô cung chủ trên đầu sương mù cũng càng ngày càng nhiều, mặt mũi không rõ ràng cho lắm: “Khang đường chủ người là không tệ, vì toàn bộ Danh Sư đường, không tiếc tốn hao đánh đổi, đổi lấy đi vào Âm Dương đầm danh ngạch... Chỉ là, những này cùng giúp ta trị liệu Âm Dương xung đột có quan hệ?”"),g("br"),g("br"),n._v("“Đương nhiên có quan hệ!”"),g("br"),g("br"),n._v("Trương Huyền nhàn nhạt nhìn qua: “Nếu không... Hai ngươi kết hôn đi!”"),g("br"),g("br"),n._v("Phốc!"),g("br"),g("br"),n._v("Một bên Khang đường chủ bị nước bọt sặc một cái, thiếu chút nữa ngất đi, Nhược Hoan công tử cũng trừng to mắt."),g("br"),g("br"),n._v("Ngươi không phải chữa bệnh ư? Thế nào thấy ngược lại giống như là làm mai?"),g("br"),g("br"),n._v("Giới thiệu Khang đường chủ cùng Ngô cung chủ kết hôn... Ngươi ngược lại là thực có can đảm muốn..."),g("br"),g("br"),n._v("“Kết hôn?”"),g("br"),g("br"),n._v("Ngô cung chủ cũng là thân thể nhoáng một cái, trước mắt biến thành màu đen, chỉ làm cho cái tên này chữa bệnh, làm sao thời gian nháy mắt nói chuyện cưới gả?"),g("br"),g("br"),n._v("Muốn ta gả cho vị này Khang đường chủ cái quỷ gì?"),g("br"),g("br"),n._v("“Không sai, ngươi thân là nữ tử, tu luyện cương mãnh công pháp, không có vấn đề. Nhưng sai liền sai tại, nửa đường thay đổi trái lại lực lượng pháp quyết, dẫn đến Âm Dương xung đột lẫn nhau, tạo thành trên thân thể tai hoạ ngầm, muốn giải quyết triệt để, chỉ có tìm một cái tu vi không kém nhiều, đối chân khí, lực lượng đều khống chế mười phần tinh thuần người hôn phối, Âm Dương điều hòa!”"),g("br"),g("br"),n._v("  Trương Huyền nói: “Khang đường chủ Hóa Phàm tứ trọng đỉnh phong, khoảng cách ngũ trọng chỉ có chênh lệch nửa bước, thân là tứ tinh đỉnh phong danh sư, đối tu vi lý giải, chỉ sợ ngươi đều theo không kịp, cho nên... Hắn là phù hợp nhất điều kiện! Nếu như hai ngươi cùng một chỗ, ngắn thì ba tháng, lâu là nửa năm, hoàn toàn có thể hóa giải ngươi cái gọi là Âm Dương xung đột lẫn nhau, để ngươi khôi phục như lúc ban đầu.”"),g("br"),g("br"),n._v("“Cái này...”"),g("br"),g("br"),n._v("Ngô cung chủ ngẩn ngơ."),g("br"),g("br"),n._v("Không nghĩ tới tình huống của nàng có thể như vậy giải quyết."),g("br"),g("br"),n._v("Từ khi tu luyện, một mực thủ thân như ngọc, nam sắc chưa từng chạm phải nửa điểm, liền xem như vì chữa khỏi trên thân thể vấn đề, trực tiếp lấy chồng, trên tâm lý cũng có chút không tiếp thụ được."),g("br"),g("br"),n._v("“Chẳng lẽ không có những biện pháp khác, cái này... Khẳng định không được!”"),g("br"),g("br"),n._v("Nàng còn tại chần chờ, một bên Khang đường chủ vội vàng mở miệng."),g("br"),g("br"),n._v("Mặc dù hắn cũng chưa từng hôn phối, có thể... Để hắn cưới một cái râu quai nón mỹ nữ, làm sao cũng làm không được ah!"),g("br"),g("br"),n._v("Tưởng tượng nhớ nàng cạo râu so với chính mình còn chuyên cần, đã cảm thấy rùng mình, một hồi phát tởm."),g("br"),g("br"),n._v("“Những biện pháp khác, đương nhiên là có, có điều, đây là đơn giản nhất!” Trương Huyền nói."),g("br"),g("br"),n._v("“Chỉ cần có những biện pháp khác là tốt rồi, chỉ cần ta có thể làm được, sẽ không tiếc...” Khang đường chủ vội nói, có điều, lời còn chưa dứt liền thấy một cái ánh mắt hung tợn nhìn chằm chằm tới, đồng thời một cái âm trầm thanh âm vang lên."),g("br"),g("br"),n._v("“Khang Cảm, ngươi có ý tứ gì? Cảm thấy ta không xứng với ngươi, vẫn là cái gì? Gấp gáp như vậy từ chối?”"),g("br"),g("br"),n._v("Coi như vì chữa bệnh, nàng cũng không muốn gả cho trước mắt lão già này, có thể... Muốn từ chối, cũng là bản thân chối từ ah, lão già này, cùng đụng phải khoai lang bỏng tay giống như, ngươi ý gì?"),g("br"),g("br"),n._v("Chẳng lẽ ta cứ như vậy không được chào đón?"),g("br"),g("br"),n._v("Luận tu vi, ta Hóa Phàm ngũ trọng, vượt xa cùng ngươi; Luận địa vị, ta là Băng Nguyên cung cung chủ, vô số Danh Sư đường đường chủ cả ba kết tồn tại; Luận dung mạo, mặc dù không nói quốc sắc thiên hương, cũng tuyệt đối được cho khuynh quốc khuynh thành, vạn dặm không một, cho dù lớn tuổi, tư sắc cũng không giảm năm đó..."),g("br"),g("br"),n._v("Ta còn chưa nói muốn gả cho ngươi, bằng cái gì, ngươi cứ như vậy nóng nảy muốn phủi sạch quan hệ?"),g("br"),g("br"),n._v("“Ngô cung chủ nói đùa, ta nào có ý tứ kia, chẳng qua là cảm thấy...”"),g("br"),g("br"),n._v("Không nghĩ tới đối phương sẽ ở vấn đề này quấy rầy, Khang đường chủ khóe miệng giật một cái: “Ta không xứng với ngươi, là ta không xứng với ngươi!”"),g("br"),g("br"),n._v("“Có tự mình biết mình là tốt rồi!”"),g("br"),g("br"),n._v("Nghe nói như thế, Ngô cung chủ lúc này mới dễ chịu một chút, lần nữa nhìn về phía thanh niên trước mắt: “Ngươi nói những biện pháp khác là cái gì? Chỉ cần ta có thể làm được, sẽ không tiếc!”"),g("br"),g("br"),n._v("“...”"),g("br"),g("br"),n._v("Khang đường chủ không còn gì để nói, cái này không cùng ta nói giống nhau sao? Nếu không phải ngươi ngắt lời,"),g("br"),g("br"),n._v("Hắn khẳng định đã sớm nói xong..."),g("br"),g("br"),n._v("“Còn có một cái biện pháp, chính là tìm tới một cái tu vi cùng ngươi không kém bao nhiêu Thuần Âm thể chất cường giả, mượn nhờ máu của nàng, hóa giải trong cơ thể ngươi sót lại dương khí, không cần quá nhiều, một giọt như vậy đủ rồi.”"),g("br"),g("br"),n._v("Trương Huyền nói tiếp."),g("br"),g("br"),n._v("“Thuần Âm thể chất?”"),g("br"),g("br"),n._v("Ngô cung chủ cười khổ."),g("br"),g("br"),n._v("Thể chất đặc thù, như trong biển rộng một khỏa hạt cát, nếu như dễ dàng như vậy tìm kiếm, khẳng định người người đều là cao thủ."),g("br"),g("br"),n._v("Bọn hắn Nguyên Hỏa Băng Nguyên, để cho người ta từ nhỏ bắt đầu tu luyện âm thuộc tính công pháp, dương thuộc tính công pháp, thậm chí không tiếc làm ra Âm Dương đầm, chính là vì bồi dưỡng ra một cái thuần dương hoặc là Thuần Âm người tu luyện..."),g("br"),g("br"),n._v("Đáng tiếc, đã nhiều năm như vậy vẫn luôn không thành công."),g("br"),g("br"),n._v("Hơn nữa, coi như thành công, cũng khẳng định sẽ lập tức bị tổng bộ tiếp đi, muốn mượn dùng loại người này huyết dịch, vì nàng giải quyết tai hoạ ngầm, gần như không có khả năng."),g("br"),g("br"),n._v("“Cái này... Không có khả năng thực hiện, nếu không... Ta liền cùng Khang đường chủ kết hôn đi!”"),g("br"),g("br"),n._v("Vẻ mặt khó coi, Ngô cung chủ chần chờ nói."),g("br"),g("br"),n._v("“Không cần... Không cần cùng ta kết hôn, Thuần Âm thể chất, cái này chẳng phải có một vị sao?”"),g("br"),g("br"),n._v("Nhìn thấy trước mắt vị này râu quai nón mỹ nữ, có muốn cùng hắn kết hôn dự định, Khang đường chủ kém chút không có tại chỗ hù chết, vội vàng la lên."),g("br"),g("br"),n._v("“Có?”"),g("br"),g("br"),n._v("Ngô cung chủ sững sờ: “Nơi nào có Thuần Âm thể chất?”"),g("br"),g("br"),n._v("Loại thể chất này ngàn năm không ra một cái, thưa thớt không gì sánh được, tại sao có thể có?"),g("br"),g("br"),n._v("“Trương sư người học sinh này, Triệu Nhã, chính là Thuần Âm thể chất, hơn nữa đã kích hoạt hai mươi phần trăm...”"),g("br"),g("br"),n._v("Sợ đối phương sẽ lừa bịp bên trên hắn, Khang đường chủ vội vàng giới thiệu."),g("br"),g("br"),n._v("“Ngươi là... Thuần Âm thể chất?”"),g("br"),g("br"),n._v("Con ngươi co rụt lại, Ngô cung chủ vội vàng nhìn về phía cô gái trước mặt."),g("br"),g("br"),n._v("“Đúng, nàng là Thuần Âm thể chất, tuyệt đối không sai!”"),g("br"),g("br"),n._v("Khang đường chủ nói xong, nhịn không được nhìn về phía Trương Huyền: “Đã Thuần Âm thể chất, một giọt máu là có thể giải quyết, ngươi vì sao muốn hai ta kết hôn...”"),g("br"),g("br"),n._v("Vị này Trương sư cũng thật là, đã phương pháp thứ hai đơn giản như vậy, tại sao phải để bọn hắn kết hôn?"),g("br"),g("br"),n._v("“Triệu Nhã là đệ tử của ta, ta có nghĩa vụ muốn chiếu cố nàng không bị bất cứ thương tổn gì, tự nhiên muốn hi sinh ngươi, lại nói, giúp ngươi tìm nàng dâu, chuyện thật tốt, thật sự là không biết nhân tâm tốt...” Trương Huyền đương nhiên mà nói."),g("br"),g("br"),n._v("Khang đường chủ nhoáng một cái."),g("br"),g("br"),n._v("Bảo hộ ngươi học sinh, liền lấy ta khai đao ah..."),g("br"),g("br"),n._v("Còn tốt lòng người, tốt em gái ngươi ah!"),g("br"),g("br"),n._v("Có bản lĩnh, ngươi cưới cái râu quai nón muội tử nhìn xem..."),g("br"),g("br"),n._v("Hai người ở chỗ này tranh chấp, bên kia Ngô cung chủ toàn thân run rẩy nhìn về phía Triệu Nhã, đến bây giờ cũng không dám tin tưởng: “Ngươi thực sự là... Thuần Âm thể chất?”"),g("br"),g("br"),n._v("“Vâng!”"),g("br"),g("br"),n._v("Triệu Nhã nhướng mày, chân khí trong cơ thể vận chuyển lên đến, thể chất trong nháy mắt bộc phát, một luồng hơi lạnh, trực trùng vân tiêu."),g("br"),g("br"),n._v("Nương theo tu vi đi đến nửa bước Hóa Phàm, thể chất nàng phát huy càng thêm phát huy vô cùng tinh tế, tương đồng cấp bậc, chỉ sợ cũng liền Trương Huyền loại này biến thái mới có thể thắng qua, coi như một chút danh sư, đều rất khó là đối thủ."),g("br"),g("br"),n._v("“Quả nhiên là... Quả nhiên là... Nguyên Hỏa Băng Nguyên người được cứu rồi...”"),g("br"),g("br"),n._v("Cảm nhận được Triệu Nhã trên người phát ra khí tức, Ngô cung chủ hốc mắt đỏ lên, ngay sau đó một cái bắt lấy Triệu Nhã cánh tay: “Ngươi tên là gì?”"),g("br"),g("br"),n._v("“Triệu Nhã!”"),g("br"),g("br"),n._v("Thấy mới vừa rồi còn bình thường Ngô cung chủ, đột nhiên trở nên điên điên khùng khùng, nhướng mày, Triệu Nhã đẩy ra bàn tay của nàng."),g("br"),g("br"),n._v("“Triệu Nhã tiểu thư, ngươi... Cùng ta quay về tổng bộ, chỉ cần ngươi đồng ý, liền lập tức có thể tiếp nhận tốt nhất truyền thừa, trong khoảng thời gian ngắn, kích hoạt trăm phần trăm Thuần Âm thể chất, nhảy lên trở thành toàn bộ đại lục, đỉnh phong nhất cường giả...”"),g("br"),g("br"),n._v("Ngô cung chủ tràn đầy kích động."),g("br"),g("br"),n._v("“Không cần, ta muốn cùng với lão sư!” Triệu Nhã thần sắc thản nhiên."),g("br"),g("br"),n._v("Trương sư, một đường chỉ điểm, để nàng đi đến hiện tại, như cha như huynh, sớm đã là nàng tôn kính nhất, rất khâm phục người, không có khả năng rời khỏi."),g("br"),g("br"),n._v("“Lão sư? Ngươi lão sư là ai?”"),g("br"),g("br"),n._v("Quá quá khích động, Ngô cung chủ đều có chút nhớ không rõ."),g("br"),g("br"),n._v("“Chính là Trương Huyền Trương sư!”"),g("br"),g("br"),n._v("Triệu Nhã vẻ mặt kiêu ngạo."),g("br"),g("br"),n._v("“Trương sư?”"),g("br"),g("br"),n._v("Ngô cung chủ lúc này mới kịp phản ứng, vội vàng xem ra: “Trương sư, có thể hay không... Đưa ngươi học sinh, cấp cho chúng ta Nguyên Hỏa Băng Nguyên, chúng ta thật cần một vị Thuần Âm thể chất cường giả...”"),g("br"),g("br"),n._v("“Mượn?” Trương Huyền lắc đầu: “Học sinh của ta là người, có tư tưởng của mình, có quyết định của nàng, nguyện ý đi, ta giơ tay đồng ý, không nguyện ý, ai cũng ép buộc không đi!”"),g("br"),g("br"),n._v("Thanh âm mặc dù rất bình thản, nhưng mang theo, khó mà ngăn chặn tự tin."),g("br"),g("br"),n._v("Cũng đúng, nắm giữ Thiên Đạo thư viện, hắn nhất định đi ra một đầu cùng tất cả mọi người không giống nhau con đường, chỉ cần Triệu Nhã không đồng ý, hắn có thể cam đoan, ai cũng ép buộc không được."),g("br"),g("br"),n._v("“Ta... Không phải ý tứ này, còn xin Trương sư không nên hiểu lầm!”"),g("br"),g("br"),n._v("Nghe được tự tin như vậy lời nói, Ngô cung chủ từ trong sự kích động khôi phục lại, liền vội vàng lắc đầu."),g("br"),g("br"),n._v("“Nói một chút đến cùng chuyện gì xảy ra đi!” Trương Huyền nhìn qua."),g("br"),g("br"),n._v("Nhờ vào nhãn lực của hắn, tự nhiên đó có thể thấy được, vị này Ngô cung chủ, là chân chính cao hứng, mà không phải qua loa."),g("br"),g("br"),n._v("Nghe được có thể giải quyết thân thể nàng tai hoạ ngầm, đều không có kích động như vậy, chỉ sợ sự tình không có đơn giản như vậy."),g("br"),g("br"),n._v("Huống chi, nàng còn nói kích hoạt thể chất, Nguyên Hỏa Băng Nguyên có thể cứu loại hình lời nói."),g("br"),g("br"),n._v("“Cái này... Liên quan chúng ta Nguyên Hỏa Băng Nguyên bí mật, xin lỗi ta không cách nào làm chủ bẩm báo...”"),g("br"),g("br"),n._v("Vẻ mặt xoắn xuýt, Ngô cung chủ chần chờ một chút: “Như vậy đi, dù sao các ngươi khẳng định phải đi vào Âm Dương đầm, đợi sau khi đi ra, mặc kệ tổng bộ có đồng ý hay không, ta đều sẽ cho ngươi một cái công đạo.”"),g("br"),g("br"),n._v("“Ừm!” Trương Huyền gật đầu."),g("br"),g("br"),n._v("Mỗi cái chức nghiệp, đều có bí mật không thể nói cùng việc riêng tư, đối phương không muốn nói cũng rất bình thường."),g("br"),g("br"),n._v("“Muốn ba cái danh ngạch, hẳn là ba người các ngươi đều muốn đi vào Âm Dương đầm chứ?”"),g("br"),g("br"),n._v("Không xoắn xuýt vấn đề này, Ngô cung chủ nhìn qua."),g("br"),g("br"),n._v("“Ta, Nhược Hoan công tử cùng Triệu Nhã.” Trương Huyền gật đầu."),g("br"),g("br"),n._v("“Vậy thì tốt, phía trước chính là băng hỏa phòng, các ngươi chỉ có thông qua khảo hạch mới có thể tiến nhập Âm Dương đầm!”"),g("br"),g("br"),n._v("Ngô cung chủ giải thích: “Sở dĩ để các ngươi khảo hạch, là bởi vì Âm Dương đầm quỷ dị không gì sánh được, Âm Dương giao hòa, thống khổ không chịu nổi, nếu như ngay cả băng hỏa phòng đều kiên trì không được, tùy tiện đi vào, vô cùng có khả năng thân tử đạo vẫn, bị nóng rực dương lưu đốt thành tro bụi, lại hoặc là bị lạnh vô cùng âm lưu đông thành băng u cục.”"),g("br"),g("br"),n._v("“Thì ra là thế.”"),g("br"),g("br"),n._v("Trương Huyền gật đầu."),g("br"),g("br"),n._v("Mới vừa rồi còn kỳ lạ, vì cái gì mới nói nhiều như vậy, còn cần khảo hạch, nguyên lai nguyên nhân ở đây."),g("br"),g("br"),n._v("Liền cùng ủ phân nấm mốc làm, muốn trước da thử như thế, không nói trước tiến hành khảo hạch, làm không cẩn thận liền sẽ bởi vì không cách nào thích ứng mà tử vong."),g("br"),g("br"),n._v("Bởi vậy, cái gọi là khảo hạch, trên thực tế cũng là một loại bảo hộ thủ đoạn, một loại kiểm nghiệm có thể hay không đi vào Âm Dương đầm phương pháp."),g("br"),g("br"),n._v("Đi tới băng hỏa phòng trước mặt, Trương Huyền nhìn sang, cùng ban đầu ở Thiên Vũ vương quốc khảo hạch danh sư lúc, sử dụng tín nhiệm phòng có chút giống, cần giọt máu tươi mới có thể tiến nhập."),g("br"),g("br"),n._v("“Các ngươi ai đi vào trước?”"),g("br"),g("br"),n._v("Ngô cung chủ cười nhìn qua."),g("br"),g("br"),n._v("“Ta...”"),g("br"),g("br"),n._v("Lời còn chưa dứt, chỉ thấy Nhược Hoan công tử vẻ mặt lo lắng lẻn đến phía trước nhất."),g("br"),n._v(" Thông qua cùng vị này Trương Huyền mấy cửa khảo hạch, hắn xem như đã có kinh nghiệm, để cái này hai hàng lên trước lời nói, ánh sáng đều bị che lấp không nói, Ngô cung chủ khả năng đều muốn bị liên lụy, không phải bị thương chính là xui xẻo..."),g("br"),g("br"),n._v("Từ lúc trước Ngô trưởng lão, Vân trưởng lão, Bạch trưởng lão... Bọn hắn liền có thể nhìn ra, tuyệt đối không thể để cho cái tên này lên trước."),g("br"),g("br"),n._v("Muốn an toàn, liền muốn rời xa Trương Huyền."),g("br"),g("br"),n._v("Cùng hắn đến đằng sau gặp được các loại phiền phức, mất mặt, còn không bằng cái thứ nhất liền đem khảo hạch hoàn thành."),g("br"),g("br"),n._v("Chí ít còn có thể có chút tự tin."),g("br"),g("br"),n._v("“Tích một giọt máu ở ngoài cửa đá thạch anh bên trên, đi vào là được!”"),g("br"),g("br"),n._v("Thấy cái tên này như thế tích cực, Ngô cung chủ gật đầu dặn dò."),g("br"),g("br"),n._v("“Vâng!”"),g("br"),g("br"),n._v("Mấy bước đi tới trước mặt, Nhược Hoan công tử cắn nát ngón tay, nhỏ lên huyết dịch, nhấc chân đi vào."),g("br"),g("br"),n._v("Vừa tiến vào trong, lập tức nghe được bên trong ầm ầm thanh âm không ngừng, tựa như sấm sét vang dội, không biết đã trải qua chuyện kinh khủng gì."),g("br"),g("br"),n._v("Ngô cung chủ dường như sớm biết có thể như vậy, hai tay chắp sau lưng, an tĩnh đứng tại chỗ."),g("br"),g("br"),n._v("Kẹt kẹt!"),g("br"),g("br"),n._v("Không biết qua bao lâu, một cái toàn thân cháy đen, tóc dựng lên, không ngừng khói đen bốc lên bóng người đi ra, không phải Nhược Hoan công tử là ai."),g("br"),g("br"),n._v("Lúc này Quân Nhược Hoan, lại không còn trước đó công tử văn nhã trạng thái, ngược lại như cái mới từ trong đống rác chạy đến ăn mày, một thân khét lẹt, thê thảm không gì sánh được."),g("br"),g("br"),n._v("“Tâm cảnh mặc dù bị phá, nhưng có thể tại trong vòng một canh giờ đi tới, nói rõ cũng không tệ lắm, có thể đi vào Âm Dương đầm!”"),g("br"),g("br"),n._v("Nhìn thoáng qua bên ngoài nhen lửa nhang lớn, tính toán một chút thời gian, Ngô cung chủ gật gật đầu."),g("br"),g("br"),n._v("Vị này Nhược Hoan công tử, ở bên trong đợi đến thời gian, mặc dù tiếp cận một canh giờ, nhưng ở trong vòng thời gian quy định đi ra, thậm chí thần trí coi như thanh tỉnh, nói rõ sự nhẫn nại cùng nghị lực, đã rất kinh người, có tư cách đi vào."),g("br"),g("br"),n._v("“Ngô cung chủ không phải nói, băng hỏa phòng là sức chịu đựng cùng tâm tính khảo nghiệm ư? Làm sao...”"),g("br"),g("br"),n._v("Trương Huyền nghi ngờ nhìn qua."),g("br"),g("br"),n._v("“Băng hỏa phòng là khảo hạch sự nhẫn nại cùng tâm tính địa phương, nhưng tâm cảnh một khi mất đi phòng thủ, thân thể cũng sẽ xui xẻo, có điều, có thể dưới loại tình huống này đi tới, đủ để chứng minh sự nhẫn nại cực mạnh.”"),g("br"),g("br"),n._v("Ngô cung chủ nói xong quay đầu nhìn qua: “Trương sư, là ngươi vào, vẫn là Triệu Nhã?”"),g("br"),g("br"),n._v("“Ta tới đi!” Triệu Nhã tiến về phía trước một bước."),g("br"),g("br"),n._v("Đồng dạng trình tự, đồng dạng đi vào, có điều, chỉ qua hơn phân nửa canh giờ, cũng đã hoàn thành, nhìn Nhược Hoan buồn bực không thôi, hô to biến thái."),g("br"),g("br"),n._v("Phải biết, Triệu Nhã bất kể tuổi tác vẫn là thực lực, đều so với hắn yếu nhiều."),g("br"),g("br"),n._v("Đương nhiên, cũng không phải là Triệu Nhã năng lực so sánh với Hoan công tử còn mạnh hơn, mà là Thuần Âm thể chất, đối âm hàn không sợ, đối dương khí có chống cự... Đối người sau tới nói cửu tử nhất sinh khảo nghiệm, ở trước mặt nàng, ngược lại không có đáng sợ như vậy."),g("br"),g("br"),n._v("Bởi vậy, Triệu Nhã đi ra, thoạt nhìn cũng có chút bối rối, nhưng cùng Nhược Hoan công tử so, tốt hơn rất nhiều."),g("br"),g("br"),n._v("“Ta xem một chút đi!”"),g("br"),g("br"),n._v("Hai người đều thông qua khảo hạch, Trương Huyền cũng đi vào."),g("br"),g("br"),n._v("..."),g("br"),g("br"),n._v("“Lão sư, ngươi cảm thấy... Trương sư lần này bao lâu có thể đi ra?”"),g("br"),g("br"),n._v("Thấy Trương Huyền đi vào gian phòng, Nhược Hoan công tử nhìn qua."),g("br"),g("br"),n._v("“Ngươi tiếp cận một canh giờ, Triệu Nhã hơn nửa canh giờ, Trương sư... Ta cảm thấy khả năng không dùng đến một khắc đồng hồ, liền có thể đi ra!”"),g("br"),g("br"),n._v("Khang đường chủ nói."),g("br"),g("br"),n._v("Cái tên này sáng tạo quá nhiều kỳ tích, liền học sinh của hắn đều có thể hơn nửa canh giờ, bản thân chỉ sợ một nửa thời gian đều không dùng đến."),g("br"),g("br"),n._v("“Một khắc đồng hồ?”"),g("br"),g("br"),n._v("Nhược Hoan công tử lắc đầu: “Ta cảm thấy nhiều nhất năm phút đồng hồ!”"),g("br"),g("br"),n._v("Đối với cái tên này biến thái, thế nhưng là thấm sâu trong người, thấu hiểu rất rõ, rõ ràng cảm thấy không có khả năng thành công, lại vẫn cứ có thể lấy lại thêm ngưu bức tư thái hoàn thành."),g("br"),g("br"),n._v("Tựa như lần này, hắn cũng cảm thấy đối phương một khắc đồng hồ khoảng chừng liền có thể hoàn thành, có thể... Chẳng biết tại sao, trong tiềm thức sẽ cảm thấy, thành tích tuyệt đối so với cái này còn kinh khủng hơn."),g("br"),g("br"),n._v("“Năm phút đồng hồ?” Đem hai người đối thoại nghe vào trong tai, Ngô cung chủ lắc đầu."),g("br"),g("br"),n._v("Đây chính là băng hỏa phòng, địa phương đáng sợ nhất, năm phút đồng hồ liền có thể đi ra? Nằm mơ chứ?"),g("br"),g("br"),n._v("“Ngô cung chủ là không biết vị này Trương sư lợi hại... Nói thật, năm phút đồng hồ, ta có đôi khi đều cảm thấy có phải hay không nói thời gian dài...”"),g("br"),g("br"),n._v("Nhược Hoan công tử giải thích."),g("br"),g("br"),n._v("“Có cường đại như vậy?”"),g("br"),g("br"),n._v("Thấy vị thiên tài này danh sư, nói lời thề son sắt, xác nhận chắc chắn, Ngô cung chủ cũng có chút dao động."),g("br"),g("br"),n._v("Vị này Trương sư, có thể nhìn ra Khang đường chủ đều nhìn không ra vấn đề, nói thật, quả thực có chút cổ quái, có lẽ thật có thể hoàn thành những người khác làm không được sự tình."),g("br"),g("br"),n._v("“Đúng vậy a, chúng ta rửa mắt mà đợi đi!”"),g("br"),g("br"),n._v("Nhược Hoan công tử tràn đầy tự tin, thậm chí so với hắn bản thân khảo hạch đều có lòng tin."),g("br"),g("br"),n._v("“Năm phút đồng hồ đi qua...”"),g("br"),g("br"),n._v("Năm phút đồng hồ rất ngắn, chỉ chốc lát liền đi qua, gian phòng đồng thời không có muốn mở ra ý tứ."),g("br"),g("br"),n._v("“Có thể là... Mười phút đồng hồ...”"),g("br"),g("br"),n._v("Khuôn mặt xấu hổ, Nhược Hoan công tử đổi giọng."),g("br"),g("br"),n._v("Mười phút đồng hồ cũng trôi qua rất nhanh, vẫn như cũ không có người đi ra."),g("br"),g("br"),n._v("“Chẳng lẽ... Mười lăm phút?” Liên tục hai lần đoán không đúng, Nhược Hoan công tử có chút hoài nghi."),g("br"),g("br"),n._v("Nhưng... Rất đáng tiếc, mười lăm phút trôi qua rất nhanh, đồng dạng không có người xuất hiện."),g("br"),g("br"),n._v("Ba mươi phút!"),g("br"),g("br"),n._v("Năm mươi phút đồng hồ!"),g("br"),g("br"),n._v("Tám mươi phút đồng hồ..."),g("br"),g("br"),n._v("..."),g("br"),g("br"),n._v("Một canh giờ trôi qua rất nhanh, vị kia đi vào băng hỏa phòng Trương sư, như là thạch chìm đáy biển, một điểm động tĩnh đều không có."),g("br"),g("br"),n._v("“Thời gian kết thúc, đều không có đi ra... Xem ra hắn không có thông qua khảo hạch!”"),g("br"),g("br"),n._v("Ngô cung chủ lắc đầu, vẻ mặt tiếc hận."),g("br"),g("br"),n._v("Thật sự cho rằng sẽ cùng mọi người nói như thế, sáng tạo kỳ tích, hiện tại xem ra, suy nghĩ nhiều."),g("br"),g("br"),n._v("Không những không có sáng tạo kỳ tích, kết quả liên thông qua đều không có thông qua."),g("br"),g("br"),n._v("“Không có thông qua...”"),g("br"),g("br"),n._v("Sắc mặt nhăn nhó, Nhược Hoan công tử như là táo bón như thế khó coi."),g("br"),g("br"),n._v("Mỗi lần nghi ngờ cái tên này, hắn liền đánh mặt cho mình nhìn, lần này, thật vất vả lời thề son sắt, nói hắn có thể thành công, hơn nữa vượt mức hoàn thành... Kết quả, lại đi vào không ra được..."),g("br"),g("br"),n._v("Trương sư, ngươi là cố ý hủy đi ta đài đi..."),g("br"),g("br"),n._v("Che ngực, Nhược Hoan công tử cảm thấy không gì sánh được tâm tắc."),g("br"),g("br"),n._v("Khang đường chủ cũng thân thể lắc lư, sắp khóc."),g("br"),g("br"),n._v("Trương sư tại Danh Sư đường biểu hiện như thế biến thái, làm sao đến nơi này, ngược lại không được?"),g("br"),g("br"),n._v("Đến cùng... Bên trong chuyện gì xảy ra?"),g("br"),g("br"),n._v("..."),g("br"),g("br"),n._v("Thời gian trở lại một canh giờ trước, Trương Huyền đi vào gian phòng."),g("br"),g("br"),n._v("Cửa phòng phía sau, là cái hẹp dài thông đạo, một mực hướng nơi xa dọc theo, không biết có bao xa."),g("br"),g("br"),n._v("Ầm ầm!"),g("br"),g("br"),n._v("Đem đại môn đóng lại, lập tức cảm thấy một cỗ làm cho người hít thở không thông cực nóng truyền đến, để cho người ta cảm thấy có phải hay không lọt vào lò nướng, bất cứ lúc nào cũng sẽ bị nướng hóa."),g("br"),g("br"),n._v("Thiên Đạo vận chuyển chân khí, chống cự cực nóng, hướng (về) sau nhìn lại, lông mày kìm lòng không được nhăn lại."),g("br"),g("br"),n._v("Vừa mới đóng lại đại môn, thế mà từ phía sau lưng biến mất, hình như căn bản không tồn tại đồng dạng."),g("br"),g("br"),n._v("“Chẳng lẽ muốn từ nơi này thông đạo đi ra ngoài, mới có thể rời khỏi?”"),g("br"),g("br"),n._v("Lông mày nhíu một cái."),g("br"),g("br"),n._v("Rõ ràng vừa mới tiến đến, đại môn đã không thấy tăm hơi, chẳng lẽ cái này băng hỏa phòng, là cái phi thường lợi hại trận pháp? Mà rời đi phương pháp, là đi đến trước mắt cái lối đi này cuối cùng?"),g("br"),g("br"),n._v("Trong lòng nghi hoặc, đi thẳng về phía trước."),g("br"),g("br"),n._v("Hừng hực!"),g("br"),g("br"),n._v("Mới vừa đi mấy bước, cực nóng càng ngày càng lợi hại, bốn phía xuất hiện một mảng lớn biển lửa, muốn tiếp tục hướng phía trước nhất định phải thông qua, căn bản không có cách nào tránh né."),g("br"),g("br"),n._v("“Quên đi, đi qua đó xem!”"),g("br"),g("br"),n._v("Tìm một vòng, từ đầu đến cuối không có phát hiện đường ra, Trương Huyền đành phải nhanh chân đi thẳng về phía trước."),g("br"),g("br"),n._v("Thiên Đạo chân khí mạnh mẽ vô cùng, những ngọn lửa này mặc dù nướng làn da, cốt nhục đau nhức, vẫn còn không đả thương được hắn."),g("br"),g("br"),n._v("Đi tới hỏa diễm trước mặt, đang muốn bước qua đi, trước mắt lần nữa một hoa, tựa như rơi vào băng thiên tuyết địa, băng hàn thấu xương, dọc theo huyệt đạo tràn vào thân thể, để hắn kìm lòng không được run lên."),g("br"),g("br"),n._v("Sắp đông cứng."),g("br"),g("br"),n._v("Hắn đã là Hóa Phàm cường giả, dựa theo tình huống bình thường, coi như ngủ ở trong hầm băng, chỉ cần có chân khí hộ thể, cũng sẽ không cảm thấy lạnh lẽo, bây giờ lại kìm lòng không được run rẩy, quỷ dị không nói lên lời."),g("br"),g("br"),n._v("“Khó trách Nhược Hoan công tử thê thảm như thế, chỉ sợ chính là bị những ngọn lửa này cùng băng hàn chi khí qua lại ăn mòn đưa đến...”"),g("br"),g("br"),n._v("Chân khí chảy xuôi, chống cự lạnh lẽo, đồng thời Trương Huyền trong lòng suy tư."),g("br"),g("br"),n._v("Hóa Phàm cường giả mặc dù có chống cự tự nhiên năng lực, có thể... Chợt nóng chợt lạnh, nhiệt độ không khí đột nhiên biến hóa quá nhanh, cũng vẫn là sẽ cho người không chịu nổi, triệt để sụp đổ."),g("br"),g("br"),n._v("Thật giống như đưa tay đặt ở khối băng bên trong, có thể tiếp nhận, nhưng trước đặt ở trong nước nóng ngâm một hồi, lại bỏ vào nước đá, cho dù là tương đồng nhiệt độ, cũng sẽ trở nên khó mà tiếp nhận."),g("br"),g("br"),n._v("“Hỏa diễm, băng hàn... Không đúng, coi như tứ tinh đỉnh phong trận pháp cũng làm không được điểm ấy.”"),g("br"),g("br"),n._v("Đang muốn tiếp tục hướng phía trước đi, đột nhiên trong lòng hơi động."),g("br"),g("br"),n._v("Cái này băng hỏa phòng, nhiệt độ không khí biến hóa nhanh như vậy, liền xem như tứ tinh đỉnh phong cấp bậc trận pháp cũng làm không được, Băng Nguyên cung làm sao có thể hoàn thành?"),g("br"),g("br"),n._v("“Hơn nữa, trước khi vào cửa, không có chút nào linh khí biến hóa, nếu có trận pháp, ta khẳng định có thể nhìn ra...”"),g("br"),g("br"),n._v("Hắn là tứ tinh trận pháp sư, thật có trận pháp ở đây, coi như không vận chuyển, cũng tuyệt đối có thể phát hiện, không có trận pháp, nhưng có thể hỏa diễm, băng hàn tùy ý giao thế, để cho người ta khó lòng phòng bị..."),g("br"),g("br"),n._v("Muốn ra ngoài, chỉ sợ không đơn thuần là đi ra thông đạo đơn giản như vậy."),g("br"),g("br"),n._v("Lại thêm trước đó, Ngô cung chủ chuyên môn nói qua, tâm tính, sự nhẫn nại khảo thí, tâm cảnh bị phá đi giống như lời nói, để hắn càng thêm kỳ lạ."),g("br"),g("br"),n._v("“Nhìn xem đến cùng chuyện gì xảy ra... Minh Lý Chi Nhãn!”"),g("br"),g("br"),n._v("Có phòng bị, không tại tới trước, mà là đứng tại chỗ, Minh Lý Chi Nhãn vận chuyển lại."),g("br"),g("br"),n._v("Hô!"),g("br"),g("br"),n._v("Trước mắt ánh sáng bắn ra bốn phía, vô số hoa văn hiện lên, thời gian nháy mắt, Trương Huyền lắc đầu."),g("br"),g("br"),n._v("“Nguyên lai là trên tinh thần khảo nghiệm, thân thể chẳng qua bổ sung mà thôi... Linh hồn của ta cùng ý thức đi vào huyễn cảnh!”"),g("br"),g("br"),n._v("Thông qua Minh Lý Chi Nhãn, hắn rốt cục hiểu rõ chuyện gì xảy ra."),g("br"),g("br"),n._v("Trước mắt xuất hiện hỏa diễm cùng băng tuyết, căn bản không phải thật, mà là ảo giác."),g("br"),g("br"),n._v("Liền cùng tín nhiệm thủy tinh như thế, thông qua huyết dịch, chế tạo huyễn cảnh, để cho người ta như là trải qua hỏa diễm, băng hàn chỗ, rơi vào trong đó người, nếu như không kiên trì nổi, liền sẽ rơi vào trong đó, không có người cứu chữa không cách nào rời khỏi."),g("br"),g("br"),n._v("Minh Lý Chi Nhãn có thể Phá Hư vọng, cứ việc cái này huyễn cảnh không gì sánh được chân thực, nhưng ở quan sát, vẫn là bị tuỳ tiện phá vỡ, một chút liền có thể nhìn ra vừa rồi đại môn còn tại trước mắt, mà nhục thể của hắn đang ngồi ở trong phòng ở giữa, động cũng không động, không khí chung quanh nóng lạnh giao thế, lại sẽ không làm bị thương căn bản."),g("br"),g("br"),n._v("Nói cách khác, vừa rồi trải qua thống khổ cùng tra tấn, đều là đối ý chí tinh thần ma luyện."),g("br"),g("br"),n._v("“Đây không phải trận pháp, nhưng có thể có loại hiệu quả này, Băng Nguyên cung thật là lợi hại!”"),g("br"),g("br"),n._v("Quan sát một hồi, hắn cũng biết, cái này xác thực không phải trận pháp, có điều, nhưng có thể tạo được cùng trận pháp đem cùng tác dụng, thậm chí càng lợi hại, băng hỏa phòng quả thực không đơn giản."),g("br"),g("br"),n._v("(Hai hợp một, hơn sáu ngàn chữ, cuối cùng 12 giờ, cầu nguyệt phiếu!)"),g("br"),g("br"),n._v("Convert by: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-559"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-561"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);