(window.webpackJsonp=window.webpackJsonp||[]).push([[1812],{2167:function(n,h,g){"use strict";g.r(h);var t=g(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-553-then-thung-truong-huyen"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-553-then-thung-truong-huyen"}},[n._v("#")]),n._v(" Chương 553: Thẹn thùng Trương Huyền")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("br"),g("br"),n._v("Chương 553: Thẹn thùng Trương Huyền"),g("br"),g("br"),n._v("Bàn chân trên mặt đất đạp mạnh, Khang đường chủ cả người như là con báo, cấp tốc vọt lên phía trước, hai tay chấn động, chân khí trong cơ thể cuồng phong loạn tuôn, để tốc độ của hắn lần nữa bạo tăng. ◇ ◇"),g("br"),g("br"),n._v("Linh cấp trung phẩm võ kỹ Ưng Phi Ngạo Thiên!"),g("br"),g("br"),n._v("Đây là một bộ thân pháp, coi như không phải Vạn Quốc thành mạnh nhất, cũng không xê xích gì nhiều, một khi thi triển, tốc độ tăng vọt, coi như Triệu minh chủ loại cường giả cấp bậc này, muốn đuổi theo, cũng khó khăn."),g("br"),g("br"),n._v("Tâm hệ học sinh an ủi, tuyệt chiêu thi triển đi ra, Khang đường chủ mở miệng: “Trương sư, ta trước đi qua nhìn xem, ngươi đuổi theo là được... Hả? Người đâu? Bà mẹ nó!”"),g("br"),g("br"),n._v("Một bên tiến lên, một bên quay đầu đối Trương sư chào hỏi một tiếng, lời nói mới nói một nửa, đột nhiên thân thể cứng đờ, kém chút chân khí không có vận chuyển tới, từ không trung rơi xuống."),g("br"),g("br"),n._v("Chỉ thấy phía sau không có một ai, mà bản thân ngay phía trước, một thân ảnh cấp tốc chạy như điên, đã biến thành một cái chấm đen nhỏ, lập tức sẽ biến mất."),g("br"),g("br"),n._v("Không phải... Trương sư là ai!"),g("br"),g("br"),n._v("Bản thân dùng tốc độ nhanh nhất nhanh chóng chạy như điên, vốn cho rằng chỉ có Hóa Phàm nhị trọng sơ kỳ Trương sư, muốn đuổi theo gần như không có khả năng, mới hữu hảo nói một câu, ai ngờ... Nói còn chưa dứt lời, người đã không còn hình bóng!"),g("br"),g("br"),n._v("Tốc độ nhanh hơn hắn không chỉ một lần!"),g("br"),g("br"),n._v("Hắn nhưng là Hóa Phàm tứ trọng cường giả đỉnh phong, hơn nữa thi triển mạnh nhất thân pháp... Kém chút liền đối phương cái bóng cũng không thấy..."),g("br"),g("br"),n._v("Tận mắt nhìn thấy, đều cảm thấy có phải hay không hoa mắt, điên rồi."),g("br"),g("br"),n._v("Đây là Hóa Phàm nhị trọng ư?"),g("br"),g("br"),n._v("Coi như ngũ trọng cũng không có loại tốc độ này đi..."),g("br"),g("br"),n._v("“Không đúng, hắn không phải nhị trọng sơ kỳ? Mà là... Hậu kỳ?”"),g("br"),g("br"),n._v("Nhướng mày."),g("br"),g("br"),n._v("Trương sư toàn lực chạy như điên, chân khí không giữ lại chút nào tiết lộ ra ngoài, hiện ra tu vi thật sự... Căn bản không phải Hóa Phàm nhị trọng sơ kỳ, mà là... Hậu kỳ!"),g("br"),g("br"),n._v("Cái này sao có thể?"),g("br"),g("br"),n._v("Tuyển bạt danh sư thời điểm, đối phương tại chỗ đột phá, nhìn thật sự rõ ràng, không có khả năng giả mạo, làm sao có thể... Mới qua nửa cái tháng sau, liền tăng lên ba cái tiểu cấp bậc, đi đến nhị trọng hậu kỳ?"),g("br"),g("br"),n._v("Đây chính là Hóa Phàm nhị trọng... Ngắn như vậy thời gian, từ sơ kỳ đi đến hậu kỳ, hơn nữa nhìn bộ dáng, tu vi củng cố, bất cứ lúc nào cũng sẽ đột phá..."),g("br"),g("br"),n._v("Đến cùng làm sao làm được?"),g("br"),g("br"),n._v("Cũng quá nghịch thiên đi!"),g("br"),g("br"),n._v("Lại nói, liền xem như Nguyên Khí cảnh hậu kỳ, cùng hắn Trọc Thanh cảnh đỉnh phong so, cũng kém hai cái đại cấp bậc, làm sao có thể còn nhanh hơn hắn? Đồng thời, tốc độ nhanh như vậy xuống, chuyển biến, nhảy lên, đều đơn giản dễ dàng linh động, so với hắn tu luyện Linh cấp trung phẩm võ kỹ Ưng Phi Ngạo Thiên, đều cao minh hơn nhiều lắm!"),g("br"),g("br"),n._v("“Chẳng lẽ... Hắn tu luyện Linh cấp thượng phẩm thậm chí... Tuyệt phẩm cấp bậc võ kỹ?”"),g("br"),g("br"),n._v("Trong lòng hơi động, giật nảy mình."),g("br"),g("br"),n._v("Loại cấp bậc này võ kỹ, chỉ có đế quốc mới có tư cách nắm giữ, coi như hắn là Vạn Quốc thành Danh Sư đường đường chủ, cũng không cách nào học tập, thậm chí lấy được."),g("br"),g("br"),n._v("Một cái chưa từng nhập lưu vương quốc bên trong đi ra Trương sư... Thế mà tu luyện loại cấp bậc này võ kỹ, bối cảnh nên có mạnh như vậy..."),g("br"),g("br"),n._v("“Tô Phàm, Lăng Hằng Vũ hai người này, khẳng định không nói lời nói thật, chỉ sợ... Trương sư lão xuất thân, cũng không đơn giản! Trở về nhất định muốn cố gắng hỏi một chút!”"),g("br"),g("br"),n._v("Trong lòng toát ra một cái ý nghĩ."),g("br"),g("br"),n._v("Người năng lực không thể nào là bỗng dưng lấy được, vị này Trương sư lợi hại như thế, lão sư lại thế nào khả năng đơn giản? Gia thế nguồn gốc đâu?"),g("br"),g("br"),n._v("Tô sư, Lăng sư hai người này, khẳng định là biết thứ gì, lại không dứt lời."),g("br"),g("br"),n._v("Nếu không, tuyệt không có khả năng liều mạng hết lòng, tận hết sức lực."),g("br"),g("br"),n._v("“Trương sư... Chờ ta!”"),g("br"),g("br"),n._v("Đem khiếp sợ trong lòng ngăn chặn, giương mắt nhìn lại, chỉ thấy Trương sư bóng người đã biến mất không thấy gì nữa, Khang đường chủ vội vàng đuổi tới."),g("br"),g("br"),n._v("Băng Nguyên cung không lớn, Ngô cung chủ bộc phát ra khí tức, cách bọn họ chẳng qua mấy trăm mét khoảng cách, đi qua mấy cái chuyển biến, đi tới trước mặt, lúc này Trương sư đã đi tới, đứng tại một cái rộng trong đại sảnh."),g("br"),g("br"),n._v("Ngô cung chủ, Nhược Hoan công tử, Triệu Nhã, cũng đều ở trong đó, còn có mấy cái người mặc trang phục màu đỏ rực cùng áo trắng nữ đệ tử, không ngừng run rẩy."),g("br"),g("br"),n._v("Mấy người phía trước, là cái cự đại đầm nước, đường kính chừng chừng hai mươi thước, đầm nước bị chia cắt thành hai bộ phận, linh khí nhúc nhích, không biết khảm nạm bao nhiêu linh thạch, như là một đầm tinh thuần linh dịch."),g("br"),g("br"),n._v("“Chẳng lẽ cái này... Chính là Âm Dương đầm?”"),g("br"),g("br"),n._v("Đã sớm nghe Âm Dương đầm tên,"),g("br"),g("br"),n._v("Chẳng qua liền xem như hắn, cũng chưa từng gặp qua, đây coi như là lần thứ nhất tận mắt nhìn thấy."),g("br"),g("br"),n._v("“Không đúng... Nghe Âm Dương đầm, đầm phân Âm Dương, một bên nóng rực như lửa, một bên lạnh lẽo như băng, mà trước mắt cái này, linh khí mặc dù đầy đủ, nhưng không có cực nóng cùng băng hàn cảm giác...”"),g("br"),g("br"),n._v("Mày nhăn lại."),g("br"),g("br"),n._v("Âm Dương đầm sở dĩ vang danh thiên hạ, là bởi vì đầm phân Âm Dương, một bên lạnh lẽo thấu xương, một bên cực nóng khó nhịn, Âm Dương giao hòa, băng hỏa lưỡng trọng thiên."),g("br"),g("br"),n._v("Ở trong đó tu luyện, nóng lạnh giao thế, âm dương nhị khí rèn luyện, chân khí mới có thể càng thêm củng cố, thực lực cũng tiến bộ càng nhanh."),g("br"),g("br"),n._v("Trước mắt cái này đầm nước mặc dù thoạt nhìn chia làm hai bộ phận, đều chỉ là bình thường sóng linh khí, đồng thời không có lạnh lẽo, cực nóng cảm giác, chẳng lẽ... Cũng không phải là cái gọi là Âm Dương đầm?"),g("br"),g("br"),n._v("“Ngô cung chủ, xảy ra chuyện gì?”"),g("br"),g("br"),n._v("Nhìn thấy học sinh không bị đến tổn thương, Khang đường chủ nhẹ nhàng thở ra, đồng thời mặt mũi nghi ngờ nhìn qua."),g("br"),g("br"),n._v("Đã xảy ra chuyện gì, có thể để cho đi đến Hóa Phàm ngũ trọng Ngô cung chủ tức giận như vậy, tản mát ra như vậy kinh thiên lửa giận."),g("br"),g("br"),n._v("“Là Âm Dương đầm ra chút vấn đề...”"),g("br"),g("br"),n._v("Ngô cung chủ giải thích một câu, nhìn về phía trước mắt mấy cái nữ đệ tử: “Đem mới vừa nói, nói lại cho ta nghe!”"),g("br"),n._v("“Vâng...”"),g("br"),g("br"),n._v("Đi đầu một vị nữ đệ tử, tiến về phía trước một bước, thanh âm có chút run rẩy: “Vừa rồi chúng ta cùng trước kia như thế, đi quản lý... Mẫu thủy, ai ngờ lại phát hiện... Mẫu thủy bên trong Âm Dương lực lượng, biến mất hầu như không còn, một chút cũng không có, cùng... Cái này đầm nước giống nhau như đúc...”"),g("br"),g("br"),n._v("“Mẫu thủy chôn sâu ở lòng đất, trong đó Âm Dương chi lực, càng là câu thông địa mạch cùng băng nguyên mà hình thành, làm sao có thể biến mất?” Ngô cung chủ chết sống không tin."),g("br"),g("br"),n._v("“Bẩm báo cung chủ... Đây là chúng ta mới từ phía dưới đánh lên tới, ngươi nhìn...”"),g("br"),g("br"),n._v("Nữ đệ tử cổ tay khẽ đảo, một cái thùng nước xuất hiện tại trước mặt, trong đó một vũng nước sạch, linh khí nhúc nhích, cho người ta một loại linh khí cực kỳ hoạt động mạnh cảm giác, bất quá... Chỉ là bình thường linh khí, không có chút nào băng lãnh hoặc là cực nóng."),g("br"),g("br"),n._v("“Quả nhiên không có Âm Dương thuộc tính? Đến cùng chuyện gì xảy ra?”"),g("br"),g("br"),n._v("Ngô cung chủ tròng mắt đỏ hoe."),g("br"),g("br"),n._v("Âm Dương đầm, liên quan toàn bộ Băng Nguyên cung tồn tại hay không, một khi hủy, nàng cái phân bộ này, cũng là tương đương báo hỏng."),g("br"),g("br"),n._v("Nàng bên này nổi điên, bên kia cái thứ nhất xông tới Trương Huyền, thì khóe miệng giật một cái."),g("br"),g("br"),n._v("Vừa mới bắt đầu hắn cũng không biết đối phương nói tới mẫu thủy đến cùng là cái gì, có điều, nhìn thấy nữ đệ tử trong thùng gỗ đồ vật, lập tức rùng mình một cái."),g("br"),g("br"),n._v("Cái đồ chơi này, không phải là hắn vừa rồi tại lòng đất hấp thu đồ vật sao?"),g("br"),g("br"),n._v("Chẳng lẽ cùng Âm Dương đầm có quan hệ?"),g("br"),g("br"),n._v("“Ngô cung chủ, ngươi nói mẫu thủy là cái gì? Đến cùng chuyện gì xảy ra? Nếu có cái gì bí mật khó nói, không ngại nói ra, chúng ta có lẽ có thể hỗ trợ giải quyết...”"),g("br"),g("br"),n._v("Thấy cung chủ bộ này trạng thái, trong lòng toát ra dự cảm bất tường, Khang đường chủ nhịn không được đi lên phía trước."),g("br"),g("br"),n._v("“Cái này liên quan Băng Nguyên cung bí mật lớn nhất, vốn không muốn cùng các ngươi nói, có điều, hiện tại loại tình huống này, không chiếm được giải quyết, chỉ sợ cũng giấu diếm không được!”"),g("br"),g("br"),n._v("Thấy hắn hỏi thăm, Ngô cung chủ chần chờ một chút, thực sự nhịn không được nhẹ gật đầu."),g("br"),g("br"),n._v("Trước mắt mấy vị này mặc dù là ngoại nhân, nhưng đều là danh sư, nàng hết cách rồi, có lẽ bọn hắn sẽ có phương pháp giải quyết."),g("br"),g("br"),n._v("“Đây chính là Âm Dương đầm!”"),g("br"),g("br"),n._v("Chỉ hướng trước mắt cực lớn đầm nước, Ngô cung chủ nói: “Các ngươi có phải hay không kỳ lạ, không hề giống trong truyền thuyết, đầm phân Âm Dương, nóng lạnh xung đột lẫn nhau?”"),g("br"),g("br"),n._v("Mọi người gật đầu."),g("br"),g("br"),n._v("Nhất là Khang đường chủ, vừa đến liền phát hiện, càng tràn đầy kỳ lạ."),g("br"),g("br"),n._v("“Âm Dương trong đầm linh khí, là linh thạch hội tụ mà thành, điểm ấy các ngươi hẳn là đó có thể thấy được, mà trong đó Âm Dương chi khí, lại là... Mẫu thủy mang tới!”"),g("br"),g("br"),n._v("“Mẫu thủy, là Băng Nguyên cung dùng phương pháp đặc thù, dung hợp địa mạch cực nóng cùng băng nguyên chí hàn mà hình thành, tình huống bình thường chôn sâu ở sơn mạch phía dưới, chỉ có sử dụng thời điểm, mới có thể từ trong đó thịnh lấy linh dịch, tưới vào cái này trong ao...”"),g("br"),g("br"),n._v("Ngô cung chủ giải thích."),g("br"),g("br"),n._v("Mặc dù chỉ là đơn giản nhất giới thiệu, không liên quan Băng Nguyên cung, căn bản nhất bí mật, lại làm cho người hiểu được."),g("br"),g("br"),n._v("Cái gọi là Âm Dương đầm, cũng không phải là tự nhiên như thế, mà là con người làm ra chế tạo, đồng thời... Trọng yếu nhất chính là mẫu thủy, chỉ có tưới vào loại vật này, mới có thể để cho nước chất triệt để biến hóa, hình thành đối tu luyện có tác dụng cực lớn bảo địa."),g("br"),g("br"),n._v("Vậy thì cùng luyện chế nồng hậu dày đặc linh dịch pha loãng như thế, trọng yếu nhất cũng không phải là pha loãng sau dược vật, mà là trước đó."),g("br"),g("br"),n._v("Mà lúc này... Cái này pha loãng trước đó mẫu thủy, Âm Dương thuộc tính thế mà... Hư không tiêu thất!"),g("br"),g("br"),n._v("Không có mẫu thủy, cái gọi là Âm Dương đầm, cũng chính là một đống linh dịch mà thôi, coi như đối tu luyện có chút ích lợi, cũng mất trước đó hiệu quả..."),g("br"),g("br"),n._v("“Cái này...”"),g("br"),g("br"),n._v("Khang đường chủ khóe miệng giật một cái."),g("br"),g("br"),n._v("Còn tưởng rằng là cái gì chuyện đơn giản, có thể nhẹ nhõm giải quyết, hiện tại xem ra, căn bản không phải hắn có tư cách nhúng tay."),g("br"),g("br"),n._v("Liên lụy đến mẫu thủy, tương đương với Băng Nguyên cung lớn nhất cơ mật, làm sao có thể tùy ý rò rỉ, để hắn biết?"),g("br"),g("br"),n._v("Hơn nữa, coi như rò rỉ cho hắn, liền Ngô cung chủ loại này đắm chìm cả đời người, đều không thể giải quyết, bản thân thì có biện pháp gì?"),g("br"),g("br"),n._v("Nói thật, vận khí cũng quá củ chuối đi, vốn cho rằng lấy được danh ngạch, có thể để cho học sinh của mình trên phạm vi lớn gia tăng tu vi, nằm mơ đều không có nghĩ đến, gặp chuyện như vậy."),g("br"),g("br"),n._v("“Đúng rồi, Trương sư...”"),g("br"),g("br"),n._v("Đang muốn lắc đầu, nói cho đối phương biết vô kế khả thi, đột nhiên nhớ tới cái kia nhiều lần sáng tạo kỳ tích Trương Huyền, nhịn không được nhìn sang, không nhìn còn khá, vừa nhìn lập tức mày nhăn lại."),g("br"),g("br"),n._v("Chỉ thấy lúc này Trương sư, đang vẻ mặt cổ quái, giống như là làm cái gì việc trái với lương tâm, vẻ mặt đỏ bừng."),g("br"),g("br"),n._v("“Trương sư, ngươi làm sao?”"),g("br"),g("br"),n._v("Nhìn thấy luôn luôn tùy tiện Trương Huyền, lúc này không những không nghĩ biện pháp giải quyết vấn đề, còn bộ dáng này, Ngô cung chủ nhịn không được nhìn lại."),g("br"),g("br"),n._v("Nghe được tra hỏi, những người khác cũng kịp phản ứng, ánh mắt tập trung."),g("br"),g("br"),n._v("Trương sư bộ biểu tình này, quả thực kỳ lạ, coi như Ngô cung chủ cũng cảm thấy có chút không đúng."),g("br"),g("br"),n._v("“Ta...”"),g("br"),g("br"),n._v("Nghe được tra hỏi, Trương Huyền hơi đỏ mặt, nhịn không được vò đầu: “Cái này... Ngô cung chủ, vừa rồi tại băng hỏa phòng, ta không phải nói... Tu luyện một hồi ư?”"),g("br"),g("br"),n._v("Thấy hắn không có trả lời lời nói, ngược lại nói những này, Ngô cung chủ, Khang đường chủ mặt mũi kỳ lạ, bất quá vẫn là nhẹ gật đầu."),g("br"),g("br"),n._v("Trước đó, hắn là nói qua, tại băng hỏa phòng tu luyện làm trễ nải thời gian, lúc này mới không có thông qua khảo hạch."),g("br"),g("br"),n._v("Gãi gãi đầu, Trương Huyền tràn đầy thật không tiện: “Khụ khụ... Ta lúc tu luyện, dùng sức quá lớn, không cẩn thận... Liền đem các ngươi mẫu thủy bên trong Âm Dương chi khí... Tất cả đều hút sạch...”"),g("br"),g("br"),n._v("(Canh [3] đến, đầu tháng cầu nguyệt phiếu, tháng trước khoảng cách thứ ba, chỉ kém một chút, tháng này, chúng ta có thể vọt tới thứ mấy?!!!)"),g("br"),g("br"),n._v("Convert by: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-561"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-563"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);