(window.webpackJsonp=window.webpackJsonp||[]).push([[1813],{2168:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-554-boi-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-554-boi-thuong"}},[n._v("#")]),n._v(" Chương 554: Bồi thường")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 554: Bồi thường"),t("br"),t("br"),n._v("“A?”"),t("br"),t("br"),n._v("“Tu luyện dùng sức quá lớn?”"),t("br"),t("br"),n._v("Ngô cung chủ, Khang đường chủ bọn người ngẩn ở tại chỗ."),t("br"),t("br"),n._v("Nhược Hoan công tử càng là khóe miệng giật một cái, kém chút không có mắt tối sầm lại, trực tiếp ngất đi."),t("br"),t("br"),n._v("Trước đó đã cảm thấy kỳ quái, vì sao ngay cả chính mình cũng có thể thông qua băng hỏa phòng, vị này Trương sư nhưng thất bại, nguyên lai nguyên nhân ở đây... Trực tiếp chạy tới, đem người ta mẫu thủy đều hấp thu xong, để Âm Dương đầm biến thành phế phẩm... Thông không thông qua còn có ý nghĩa gì?"),t("br"),t("br"),n._v("May mắn trước đó hắn còn cảm giác đối phương thừa nhận thất bại, bằng phẳng đến cực điểm, để cho người ta bội phục... Hiện tại xem ra, là vô sỉ hết sức đi!"),t("br"),t("br"),n._v("Ngươi đem mẫu thủy đều hút khô, Âm Dương đầm đều không dùng, chúng ta cho dù có cái này danh ngạch, có cái rắm dùng?"),t("br"),t("br"),n._v("Quất lấy cái mũi, Nhược Hoan công tử muốn khóc."),t("br"),t("br"),n._v("Từ khi biết cái này hai hàng đến nay, bất kể tỷ thí, vượt quan, vẫn là làm gì, liền không có một lần thuận tâm, không phải là bị đánh mặt, chính là đi tại bị đánh mặt trên đường..."),t("br"),t("br"),n._v("Nếu không phải trái tim mạnh mẽ, khẳng định sớm đã bị tra tấn điên rồi."),t("br"),t("br"),n._v("“Ngươi, ngươi... Đem mẫu thủy bên trong Âm Dương thuộc tính, hấp thu hết?”"),t("br"),t("br"),n._v("Ngô cung chủ cũng kịp phản ứng, như bị điên nhìn về phía cách đó không xa thanh niên."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền ngượng ngùng."),t("br"),t("br"),n._v("Hắn mặc dù thông qua thư viện biết cái kia là mẫu thủy, có thể thật không biết đối Âm Dương đầm trọng yếu như vậy, hơn nữa... Hắn chỉ muốn tu luyện một cái linh hồn, cũng không nghĩ tới, nổi danh như vậy đồ vật, cư nhiên như thế không trải qua đánh, mới giúp hồn lực từ Hóa Phàm nhị trọng đi đến tứ trọng, liền hoàn toàn biến mất."),t("br"),t("br"),n._v("Nghĩ một lát, cũng là hiểu được, mẫu thủy chỉ phụ trách Âm Dương giao hòa, trong đó có thể cung cấp tu luyện linh khí cũng không nhiều, tựa như Âm Dương đầm, là chung quanh khảm nạm vô số linh thạch, cung cấp lực lượng."),t("br"),t("br"),n._v("Mà linh hồn hắn loại trừ hấp thu Âm Dương chi khí, chính là trong đó không có ý nghĩa linh khí, tự nhiên rất dễ dàng thôn phệ sạch sẽ."),t("br"),t("br"),n._v("Rõ ràng những này, tràn đầy tâm tắc."),t("br"),t("br"),n._v("Sớm biết cái đồ chơi này dễ dàng như vậy hư hại, hơn nữa đối với đối phương trọng yếu như vậy, đánh chết cũng bất động ah!"),t("br"),t("br"),n._v("Hắn hồn lực mạnh yếu, cùng tu vi chân khí đồng dạng, cần đầy đủ linh thạch trung phẩm là có thể giải quyết, mà Âm Dương đầm, lại là không có cách nào bù đắp."),t("br"),t("br"),n._v("Bất quá... Cũng không thể nói, mẫu thủy cùng linh thạch tác dụng đánh đồng, hấp thu Âm Dương chi khí tu luyện Vu hồn, như là đã trải qua lôi kiếp, càng thêm ngưng thực, càng thêm thuần túy, điểm ấy rất rõ ràng là linh thạch không cách nào hoàn thành."),t("br"),t("br"),n._v("“Mẫu thủy ẩn sâu dưới đất, băng hỏa phòng chỉ là liên tiếp khí tức, để cho người ta cảm thụ Âm Dương chi lực mà thôi... Khoảng cách chênh lệch mấy ngàn mét xa, như vậy cũng có thể hấp thu?”"),t("br"),t("br"),n._v("t r u y e nc u a t u i . v n"),t("br"),n._v("Ngô cung chủ vẫn như cũ không thể tin được."),t("br"),t("br"),n._v("Cái tên này liền đi một chuyến băng hỏa phòng, khoảng cách mẫu thủy còn có cách xa vạn dặm, làm sao lại hấp thu hết?"),t("br"),t("br"),n._v("“Khụ khụ, cái kia... Ta đi vào băng hỏa phòng phát hiện là cái cơ quan, liền nghĩ biện pháp mở ra đi xuống...” Trương Huyền nói."),t("br"),t("br"),n._v("Ngô cung chủ nhoáng một cái."),t("br"),t("br"),n._v("Đây chính là ngũ tinh Thiên Công sư chế tác cơ quan, liền nàng cũng chỉ có thể mượn nhờ cung chủ lệnh bài mở ra, kẻ trước mắt này, phát hiện mở ra... Còn thuận tiện đi xuống, đến cùng làm sao làm được?"),t("br"),t("br"),n._v("Mấu chốt nhất là... Đây chẳng qua là cái lấy hơi cái hố, lúc trước đào ra, một là vì băng hỏa phòng khảo hạch, thứ hai là vì có thể để cho mẫu thủy rãnh thông thiên địa, tốt hơn nở, mấy ngàn mét độ cao, không chỗ mượn lực... Nhảy đi xuống sẽ trực tiếp ngã chết!"),t("br"),t("br"),n._v("Coi như thật đi xuống, lại thế nào đi lên?"),t("br"),t("br"),n._v("“Hấp thu các ngươi Âm Dương cung mẫu thủy, là ta sơ suất, như vậy đi... Ta cũng không chiếm các ngươi tiện nghi, vừa rồi tử quan sát kỹ, các ngươi băng nguyên cung âm quyết cùng dương quyết, tồn tại cực mầm họa lớn, không nhìn lầm... Tu luyện loại này pháp quyết, bình thường đều sẽ chết yểu đi!”"),t("br"),t("br"),n._v("Đem người ta trấn cung chi bảo phế đi, Trương Huyền tràn đầy áy náy, vừa vặn trước đó quan sát những Âm Dương đó thuộc tính đệ tử, trên việc tu luyện có chút vấn đề, thuận tay hỗ trợ giải quyết, cũng coi như cấp cho bồi thường."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Ngô cung chủ sửng sốt."),t("br"),t("br"),n._v("Tu luyện âm quyết, dương quyết đệ tử, là vì bồi dưỡng mẫu thủy mà thành, chịu đến địa mạch cùng băng nguyên xung kích, bình thường đều không sống nổi quá lớn, coi như đi đến Hóa Phàm cảnh, thực lực không yếu, tuổi thọ sáu mươi cũng là một cửa ải lớn."),t("br"),t("br"),n._v("Bất quá, đây là bọn hắn băng nguyên cung bí mật... Vị này Trương sư, chỉ nhìn liếc mắt liền phát hiện?"),t("br"),t("br"),n._v('"Ta có thể cải tiến tu luyện của các ngươi công pháp,'),t("br"),t("br"),n._v('Giúp các nàng xóa cái này tai hoạ ngầm, coi như bồi thường các ngươi băng nguyên cung tổn thất!"'),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Cải tiến... Công pháp?” Ngô cung chủ run co lại."),t("br"),t("br"),n._v("Chỗ thiếu hụt này, băng nguyên cung truyền thừa vô số năm, đều chưa từng loại bỏ, nếu như có thể cải tiến, đối toàn bộ nguyên hỏa băng nguyên người mà nói, đều là vô lượng đại công đức, đừng nói hủy một cái phân bộ, coi như mười cái, tổng bộ cũng sẽ không cau mày."),t("br"),t("br"),n._v("Chỉ là... Thật có thể cải tiến, hơn nữa xuất từ một cái không đủ hai mươi thiếu niên tay?"),t("br"),t("br"),n._v("“Các ngươi cái này hai bộ công pháp vấn đề lớn nhất, ở chỗ người tu luyện, dương khí, âm khí chất đống, không cách nào thông thuận vận chuyển. Dương khí âm khí mặc dù là thiên địa chính tông nhất lực lượng, có thể tu luyện người không phải đem đối ứng thể chất, chất đống khí tức quá lâu, rất dễ dàng phá hư cơ sở. Vậy thì cùng nhân sâm tuy tốt, không ít người cũng không thể dùng đồng dạng, thể chất không giống, đại bổ vật phẩm, cũng lại biến thành độc dược...”"),t("br"),t("br"),n._v("Biết đối phương lo lắng, Trương Huyền nhìn qua: “Nếu như có thể từ trên căn bản giải quyết, thể chất cùng âm dương nhị khí tốt hơn dung hợp, khôi phục tuổi thọ vẫn là rất đơn giản!”"),t("br"),t("br"),n._v("Ngô cung chủ không có tu luyện Âm Dương Pháp Quyết, nhưng những đệ tử này, tu vi không cao, lợi dụng Minh Lý Chi Nhãn vẫn là rất dễ dàng có thể tìm tới thiếu hụt vị trí."),t("br"),t("br"),n._v("Thông qua quan sát, đã biết, những người này, trong cơ thể mang theo ám thương, không nghĩ biện pháp chữa trị, tuổi thọ đi đến bị hao tổn, sáu mươi đều rất khó sống đến."),t("br"),t("br"),n._v("Hóa Phàm cảnh tuổi thọ hai, 300 tuổi, sáu mươi đều không sống tới, được cho chết yểu."),t("br"),n._v("Ngô cung chủ vẻ mặt biến hóa, cắn răng một cái, cổ tay khẽ đảo lấy ra hai bản thư tịch đưa tới: “Còn xin Trương sư sửa chữa công pháp...”"),t("br"),t("br"),n._v("Vừa mới bắt đầu còn có chút nghi ngờ, cảm giác đối phương là không muốn nhân cơ hội xem bọn hắn băng nguyên cung pháp quyết tu luyện, nghe được giải thích, mới biết được, lấy bụng tiểu nhân đo lòng quân tử."),t("br"),t("br"),n._v("Đối phương liền tu luyện cơ mật đều nhìn ra, nói rõ bộ này âm quyết, dương quyết tại nhân gia trong mắt, căn bản không phải cái vấn đề, thật muốn công pháp, như thế nào lại dùng như thế vụng về thủ đoạn."),t("br"),t("br"),n._v("“Ừm!” Đem công pháp tiếp nhận, ngón tay tới đụng một cái, trong đầu lập tức xuất hiện một quyển sách."),t("br"),t("br"),n._v("Cúi đầu hướng thư tịch nhìn sang, cùng Minh Lý Chi Nhãn nhìn thấy đồng dạng, những pháp quyết này tu luyện, quả thực sẽ tạo thành tai hoạ ngầm, để cho người ta chết yểu."),t("br"),t("br"),n._v("Bất quá, tại thiên đạo thư viện chiếu xuống, thiếu hụt rõ ràng chói mắt, đẩy ngược sửa chữa công pháp, cũng là biến đến mức dị thường đơn giản."),t("br"),t("br"),n._v("Thời gian không dài, trên tay mở ra sách, lấy ra bút lông, đem tuổi thọ chết yểu thiếu hụt sửa chữa hoàn tất, lần nữa đưa tới."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Tiếp nhận công pháp, nhìn một hồi, Ngô cung chủ thân thể lập tức nhịn không được run."),t("br"),t("br"),n._v("Nàng thân là Hóa Phàm cảnh ngũ trọng cường giả, lại thêm khống chế băng nguyên cung nhiều năm, đối phương sửa chữa địa phương, có hay không công hiệu, vẫn là rất dễ dàng có thể nhìn ra được."),t("br"),t("br"),n._v("Chỉ cần dựa theo cái này tu luyện, không bao lâu, băng nguyên cung tất cả mọi người tai hoạ ngầm đều sẽ biến mất, một lần nữa đi lại thiên hạ."),t("br"),t("br"),n._v("Nhìn thoáng qua, thì càng đổi công pháp... Vị thanh niên này đến cùng có bao nhiêu lợi hại?"),t("br"),t("br"),n._v("Coi như Khang đường chủ cũng làm không được đi!"),t("br"),t("br"),n._v("“Đa tạ Trương sư cứu băng nguyên cung ngàn vạn đệ tử tính mệnh!”"),t("br"),t("br"),n._v("Biết bộ công pháp này, truyền về tổng bộ, tất nhiên gây nên cực đại chấn, để vô số người điên cuồng, có thể đủ cứu vớt không biết bao nhiêu tu luyện Âm Dương thuộc tính lực lượng đệ tử, Ngô cung chủ cũng nhịn không được nữa, thân thể khom người, quỳ rạp xuống đất."),t("br"),t("br"),n._v("“Ai... Ngô cung chủ, ngươi làm cái gì vậy...”"),t("br"),t("br"),n._v("Không nghĩ tới nàng sẽ đi này đại lễ, Trương Huyền giật nảy mình, liền vội vàng tiến lên, liền muốn đem nó kéo."),t("br"),t("br"),n._v("Ngô cung chủ khoát tay chặn lại, thần sắc nghiêm túc: “Ta là đại biểu toàn bộ băng nguyên cung cám ơn ngươi, cũng không phải là cá nhân ta, còn xin Trương sư chớ muốn từ chối!”"),t("br"),t("br"),n._v("Băng nguyên cung, bởi vì công pháp giới hạn, sống không quá sáu mươi đệ tử, chí ít mấy chục vạn, bộ công pháp này một màn, tính mạng của các nàng có cam đoan... Cũng đã nói lên, Trương sư cái này một động tác, cứu mấy chục vạn người, nàng quỳ xuống, đại biểu không phải mình, mà là những người này."),t("br"),t("br"),n._v("“Được rồi...”"),t("br"),t("br"),n._v("Thấy đối phương kiên trì, Trương Huyền đành phải buông xuôi bỏ mặc."),t("br"),t("br"),n._v("Một bên Khang đường chủ, Nhược Hoan công tử thì trước mắt biến thành màu đen, sắp thổ huyết."),t("br"),t("br"),n._v("Biết đem người ta mẫu thủy thôn phệ sạch sẽ, hoài nghi Ngô cung chủ sẽ giết hay không hắn, không nghĩ tới, thời gian nháy mắt liền quỳ xuống..."),t("br"),t("br"),n._v("Trương sư, ngươi đến cùng còn có bao nhiêu không muốn người biết thủ đoạn?"),t("br"),t("br"),n._v("Sửa chữa công pháp, giải quyết băng nguyên người bản thân đều không thể giải quyết nan đề..."),t("br"),t("br"),n._v("Đây cũng không phải là bình thường danh sư có thể làm được chuyện, Khang đường chủ thậm chí hoài nghi, coi như tới một vị ngũ tinh danh sư, cũng không có khả năng do hắn làm được càng tốt hơn."),t("br"),t("br"),n._v("Cung kính bị Ngô cung chủ ba bái, cung chủ lúc này mới đứng dậy, đối thanh niên trước mắt cung kính đến cực điểm."),t("br"),t("br"),n._v("Mặc dù đối phương hủy đi mẫu thủy, nhưng cứu vớt toàn bộ băng nguyên cung tính mạng của vô số người, công tội bù nhau, công lao còn lớn hơn tại sơ suất."),t("br"),t("br"),n._v("“Lần này tới đây chính là đi vào Âm Dương đầm tu hành, hiện tại không có Âm Dương đầm, hại ngươi không cách nào tấn cấp... Như vậy đi, ta giúp ngươi chỉ điểm một chút, để ngươi trong một tháng, đột phá đến Hóa Phàm tứ trọng đỉnh phong, xem như bồi thường!”"),t("br"),t("br"),n._v("Xử lý xong Ngô cung chủ sự tình, Trương Huyền nhìn về phía cách đó không xa Nhược Hoan công tử."),t("br"),t("br"),n._v("Cái tên này tràn đầy hưng phấn đi tới, vốn muốn mượn Âm Dương đầm, đột nhiên tăng mạnh, thực lực bạo tăng một cái, kết quả lại bị bản thân thôn phệ sạch sẽ, không có cách nào tu luyện, tự nhiên cũng muốn bồi thường một cái."),t("br"),t("br"),n._v("“Để cho ta trong một tháng, đi đến Hóa Phàm tứ trọng đỉnh phong?”"),t("br"),t("br"),n._v("Nhược Hoan công tử sửng sốt."),t("br"),t("br"),n._v("Hắn bản thân là danh sư, lão sư càng là Danh Sư đường đường chủ, tu luyện tới hiện tại, các loại tốt nhất thủ đoạn đều dùng, muốn muốn đạt tới Hóa Phàm tứ trọng đỉnh phong, không có mười năm, hai mươi năm, không thể nào làm được, một tháng..."),t("br"),t("br"),n._v("Nằm mơ đi!"),t("br"),t("br"),n._v("“Ngươi lúc tu luyện, có phải hay không cảm thấy, trong khí hải không gì sánh được tắc nghẽn, phảng phất đi vào chật hẹp thông đạo, khó mà tiến lên?” Trương Huyền nói."),t("br"),t("br"),n._v("“Ây... Vâng!”"),t("br"),t("br"),n._v("Nhược Hoan công tử gật đầu."),t("br"),t("br"),n._v("Hắn hiện tại tu luyện tiến lên rất chậm, cũng không phải là thiếu khuyết tài nguyên, cũng không phải không biết tu luyện như thế nào, mà là chân khí trong cơ thể giống như là bế tắc, vận chuyển như là nhúc nhích, lại không trước kia thoải mái."),t("br"),t("br"),n._v("Thật giống như đường dài chạy, lợi hại hơn nữa vô địch thế giới, vượt qua nhất định cực hạn, tốc độ cũng sẽ hạ xuống được, không có khả năng có người một mực duy trì cố định tốc độ."),t("br"),t("br"),n._v("Hắn hiện tại tựa như là đạt đến cực hạn, mỗi tiến về phía trước một bước, cũng khó khăn."),t("br"),t("br"),n._v("“Rất đơn giản, đem cái này viên thuốc ăn, có thể để cho ngươi nhẹ nhõm trong một tháng đi đến Hóa Phàm tứ trọng đỉnh phong!”"),t("br"),t("br"),n._v("Nhàn nhạt nhìn qua, Trương Huyền cổ tay khẽ đảo, một cái lớn nhỏ cỡ nắm tay, màu đỏ nhạt viên thuốc xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("Nhược Hoan công tử thân thể nhoáng một cái, khuôn mặt vặn vẹo."),t("br"),t("br"),n._v("“Đây là... Cho man thú dùng nhị phẩm Thôi Tình đan?”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-562"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-564"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);