(window.webpackJsonp=window.webpackJsonp||[]).push([[1814],{2169:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-555-bang-nguyen-đac-su-phan-2"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-555-bang-nguyen-đac-su-phan-2"}},[n._v("#")]),n._v(" Chương 555: Băng nguyên đặc sứ (phần 2)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 555: Băng nguyên đặc sứ (phần 2)"),t("br"),t("br"),n._v("Thân là tứ tinh danh sư, nhãn lực kinh người, trong tay đối phương viên đan dược kia, thoạt nhìn rất cao to bên trên, trên thực tế lại là cho man thú sử dụng Thôi Tình đan."),t("br"),t("br"),n._v("Càng là lợi hại man thú, lai giống có thể sống sót tỷ lệ cũng là càng thấp, vì tốt hơn sinh sôi, thuần thú sư bình thường đều sẽ đem loại đan dược này đút cho đối phương."),t("br"),t("br"),n._v("Man thú sau khi phục dụng, thân thể xuất hiện phản ứng, ngày đêm không ngớt, từ đó bảo đảm sinh dục."),t("br"),t("br"),n._v("Thuốc này so nắm đấm còn muốn lớn hơn một vòng, chuyên môn cho man thú thúc tình dùng, để cho mình dùng?"),t("br"),t("br"),n._v("Nhược Hoan công tử nuốt ngụm nước bọt, cảm giác muốn chết."),t("br"),t("br"),n._v("“Không sai, đây chính là cho man thú dùng dược vật, có điều, đối ngươi tình huống hiện tại vừa vặn hữu dụng, nhanh lên dùng đi... A, đúng, viên này dược nhất định phải toàn bộ nuốt vào, không thể nhai kỹ nuốt chậm...”"),t("br"),t("br"),n._v("Trương Huyền bàn giao."),t("br"),t("br"),n._v("“Cả nuốt?”"),t("br"),t("br"),n._v("Nhược Hoan công tử nhoáng một cái."),t("br"),t("br"),n._v("Lớn chừng quả đấm viên thuốc, thế nào nuốt? Coi như miệng nhét đi vào, yết hầu cũng nuối không trôi ah."),t("br"),t("br"),n._v("Đại ca, ta cùng ngươi đến cùng cái gì thù cái gì oán? Ngươi không phải muốn giúp ta tăng cao tu vi, mà là muốn đem ta giết chết đi..."),t("br"),t("br"),n._v("“Nhất định phải cả nuốt, không thể xuất hiện bất kỳ sai lầm nào, nếu không hiệu quả liền không có!”"),t("br"),t("br"),n._v("Tiện tay đem viên thuốc ném cho Khang đường chủ, Trương Huyền bàn giao: “Ngươi là lão sư hắn, thì giúp một tay để hắn nuốt đi!”"),t("br"),t("br"),n._v("“Ta?”"),t("br"),t("br"),n._v("Nhìn thấy rơi vào lòng bàn tay cho man thú ăn thúc tình dược, Khang đường chủ cũng khóe miệng co giật một cái, nhịn không được ngẩng đầu: “Cái này... Thật có thể để Nhược Hoan tiến lên?”"),t("br"),t("br"),n._v("“Đương nhiên!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Cái kia tốt...”"),t("br"),t("br"),n._v("Khang đường chủ cắn răng một cái: “Nhược Hoan, tới uống thuốc, ai, đừng chạy...”"),t("br"),t("br"),n._v("Một tiếng thấp giọng hô, bàn tay lớn vồ một cái đem Quân Nhược Hoan, theo ngã xuống đất, cắn răng một cái đem cái này viên đại viên thuốc liền nhét vào đối phương trong miệng, sau đó chân khí tuôn ra, cứ thế mà ấn vào đối phương cổ họng."),t("br"),t("br"),n._v("“Khục khục...”"),t("br"),t("br"),n._v("Nhược Hoan công tử không ngừng ho khan, nước mắt tứ chảy ngang."),t("br"),t("br"),n._v("Đâu còn có nửa điểm công tử bộ dáng, hiển nhiên một tên ăn mày."),t("br"),t("br"),n._v("Cái gì cái gọi là phong độ, khí chất, giờ khắc này tất cả đều vứt xuống trảo oa quốc, bị ném đến tận lên chín tầng mây."),t("br"),t("br"),n._v("“Đừng nói nhảm, bão thủ nguyên nhất, dựa theo công pháp tu luyện!”"),t("br"),t("br"),n._v("Thấy hắn còn có công phu lưu nước mũi, Trương Huyền hét lớn."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Mặc dù tràn đầy buồn bực, nhưng cũng biết, nếu như không làm như vậy, lại bị cho ăn một khỏa, muốn tự tử đều có, Nhược Hoan công tử nóng nảy vội khoanh chân ngồi xuống."),t("br"),t("br"),n._v("Mới vừa ngồi xuống, lập tức cảm thấy trên người một cỗ khó tả khô nóng thiêu đốt, toàn thân lỗ chân lông mở rộng."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Nguyên bản chật hẹp ngăn chặn chân khí tiến lên kinh mạch, tại dược vật kích thích bên dưới, trở nên khuếch trương lên, trước đó vẩn đục chân khí, dường như cũng trong nháy mắt trở nên rõ ràng, một tiếng oanh minh, thuận lợi thông qua."),t("br"),t("br"),n._v("Răng rắc! Răng rắc!"),t("br"),t("br"),n._v("Ràng buộc tại Hóa Phàm tứ trọng sơ kỳ tu vi, phát ra một tiếng vang giòn, đột phá đến trung kỳ."),t("br"),t("br"),n._v("“Thật... Thành công?”"),t("br"),t("br"),n._v("Khang đường chủ giật nảy mình, một bên Ngô cung chủ cũng trừng to mắt."),t("br"),t("br"),n._v("Vốn là bọn hắn thấy Trương Huyền phương pháp không thể tưởng tượng, cũng cảm thấy không thể tưởng tượng nổi, tận mắt thấy Nhược Hoan công tử đột phá, mới hiểu được, đối phương là thật có bản sự kia, hơn nữa... Tìm được chỗ mấu chốt."),t("br"),t("br"),n._v("“Ừm, hắn chân khí trong cơ thể tắc nghẽn, muốn thông qua, cần hai loại biện pháp, thứ nhất, để kinh mạch trở nên khuếch trương, thứ hai, để chân khí trở nên tinh thuần...”"),t("br"),t("br"),n._v("Thấy hai người khiếp sợ, Trương Huyền cười cười: “Vừa rồi viên kia Thôi Tình đan, đối man thú đều hữu dụng, có thể để cho hắn huyết dịch tuần hoàn tăng nhanh, kinh mạch khuếch trương, cho người ta ăn, tự nhiên cũng có đồng dạng hiệu quả!”"),t("br"),t("br"),n._v("Nhược Hoan công tử lúc trước cùng hắn lúc tỷ đấu, từng động tới danh sư năng lực, thư viện đã sớm đem thiếu hụt ghi chép, cứ việc cách xa nhau hơn nửa tháng, nhưng tu vi không thay đổi, thiếu hụt tự nhiên cũng không biến hóa."),t("br"),t("br"),n._v("Cho nên Trương Huyền rất dễ dàng đã tìm được chỗ mấu chốt."),t("br"),t("br"),n._v("Mặt khác, hắn mới vừa nói chỉ là một phần trong đó nguyên nhân, càng quan trọng hơn là... Cái này đan dược bên trong lặng lẽ chứa đựng một cỗ Thiên Đạo chân khí, đối phương ăn vào về sau, không chỉ kinh mạch khuếch trương, chân khí cũng bị pha loãng tinh thuần, đột phá lên, tự nhiên cũng là đơn giản!"),t("br"),t("br"),n._v("Chỉ bất quá, Thiên Đạo chân khí quá là quan trọng, giấu ở viên thuốc bên trong, trực tiếp nuốt vào, có thể không bị phát hiện, một khi nhai kỹ nuốt chậm, rất dễ dàng phát giác, cho nên mới để Khang đường chủ, cứ thế mà cho hắn nhét vào trong miệng."),t("br"),t("br"),n._v("Dược vật vào bụng liền hóa, Thiên Đạo chân khí cũng cùng hắn bản thân lực lượng dung hợp, pha loãng chân khí, lại không cách nào phát giác."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Khang đường chủ ngây người."),t("br"),t("br"),n._v("Mặc dù hắn cảm thấy không có đơn giản như vậy, có thể... Học sinh thuận lợi đột phá đang ở trước mắt, loại trừ lời giải thích này, thực sự nghĩ không ra lý do tốt hơn."),t("br"),t("br"),n._v("“Cái kia... Sau khi đột phá đâu? Dược hiệu chắc còn ở chứ?” Nhịn không được hỏi."),t("br"),t("br"),n._v("Loại phương pháp này mặc dù có thể làm cho người đột phá, có thể đột phá phía sau đâu?"),t("br"),t("br"),n._v("Như thế đại nhất viên thuốc, dược tính vẫn còn, như thế nào giải quyết?"),t("br"),t("br"),n._v("“A, cái này... Ta không nghĩ tới!” Trương Huyền đương nhiên lắc đầu."),t("br"),t("br"),n._v("“Không nghĩ tới?” Khang đường chủ nhoáng một cái, khoanh chân tu luyện Nhược Hoan công tử, chỉ cảm thấy ngực một buồn bực, kém chút tẩu hỏa nhập ma."),t("br"),t("br"),n._v("Ngươi không nghĩ tới ý gì?"),t("br"),t("br"),n._v("Để cho ta ăn như thế lớn thúc tình dược, sau đó... Tự sinh tự diệt?"),t("br"),t("br"),n._v("“Yên tâm đi, man thú thúc tình dược mà thôi, man thú trí tuệ thấp, gánh không được ngược lại cũng thôi, Nhược Hoan công tử, không có khả năng liền man thú cũng không bằng, không chống đỡ được đi!”"),t("br"),t("br"),n._v("Trương Huyền nói: “Làm như vậy, đến một lần có thể để cho đột phá, thứ hai, còn có thể khảo nghiệm định lực của hắn... Đường đường Hóa Phàm tứ trọng cường giả, liền điểm ấy ** đều không khống chế được, cũng không tránh khỏi quá yếu!”"),t("br"),n._v("“Ừm!” Khang đường chủ gật đầu."),t("br"),t("br"),n._v("Hóa Phàm cường giả đã đối tự thân có cực mạnh năng lực khống chế, nam nữ ham muốn mặc dù mãnh liệt, còn có thể khắc chế."),t("br"),t("br"),n._v("Cùng đột phá so ra, quả thực không mau mau muốn, không thương tổn phong nhã."),t("br"),t("br"),n._v("“...” Nhược Hoan công tử lần nữa rơi lệ."),t("br"),t("br"),n._v("Còn tưởng rằng có thể có biện pháp giải quyết, nguyên lai... Thật sự là tự sinh tự diệt ah... Trong lúc nhất thời hắn không gì sánh được khổ sở, cũng không biết là tâm tắc, hay là thân thể bên trên phản ứng."),t("br"),t("br"),n._v("“Đa tạ Trương sư!”"),t("br"),t("br"),n._v("Không biết bao lâu, rốt cục củng cố tu vi, chế trụ **, Nhược Hoan công tử cái này mới đi đến Trương Huyền trước mặt, khom người quỳ gối."),t("br"),t("br"),n._v("Mặc dù quá trình là dài dằng dặc tàn khốc, nhưng đối trước mắt vị này, nhưng là chân chính tâm phục khẩu phục."),t("br"),t("br"),n._v("Trước đó bởi vì đem mẫu thủy thôn phệ sạch sẽ oán khí cũng biến mất không còn một mảnh."),t("br"),t("br"),n._v("Nói thật, coi như Âm Dương đầm hoàn hảo như lúc ban đầu, một tháng thời gian, hắn cũng nhiều nhất từ Trọc Thanh cảnh sơ kỳ, đi đến trung kỳ mà thôi, hiện tại thời gian nháy mắt đã đột phá, đã vượt qua mong muốn."),t("br"),t("br"),n._v("“Không cần khách khí, ngươi kinh mạch mở rộng, chân khí lưu chuyển cấp tốc, chỉ cần linh thạch trung phẩm đầy đủ, trong một tháng, đột phá đến Hóa Phàm tứ trọng đỉnh phong, không thành vấn đề!”"),t("br"),t("br"),n._v("Trương Huyền hài lòng gật đầu."),t("br"),t("br"),n._v("Thiên Đạo chân khí dung nhập thân thể đối phương, để nguyên vốn có chút sền sệt chân khí, trở nên pha loãng, phía sau tu luyện khẳng định sẽ thuận buồm xuôi gió, so hiện tại tiến lên phải lớn hơn nhiều, nói hắn nhân họa đắc phúc, cũng không đủ."),t("br"),t("br"),n._v("“Trương sư để tiểu đồ tiến lên, Khang mỗ vô cùng cảm kích, cái này năm cái linh thạch trung phẩm, xem như thù lao...”"),t("br"),t("br"),n._v("Cẩn thận đem học sinh thân thể kiểm tra một lần, biết đã có biến hóa thoát thai hoán cốt, Khang đường chủ biết, tuyệt không phải ăn một cái Thôi Tình đan đơn giản như vậy, cổ tay khẽ đảo, lấy ra một cái hộp ngọc, thái độ cung kính đưa lên."),t("br"),t("br"),n._v("Danh sư chỉ điểm, cũng cần trả tiền, thủ đoạn của đối phương quỷ thần khó lường, so với hắn đều cao minh hơn, đã so ra mà vượt danh sư ngũ tinh, năm cái linh thạch trung phẩm tuy nhiều, nhưng khi làm thù lao lời nói, vẫn còn có chút không đáng chú ý."),t("br"),t("br"),n._v("Bất quá, cái này đã là hắn toàn bộ gia sản, coi như muốn cầm, cũng không bỏ ra nổi đến càng nhiều."),t("br"),t("br"),n._v("“Khang đường chủ khách khí, để Âm Dương đầm không thể sử dụng, cũng là ta sơ suất...”"),t("br"),t("br"),n._v("Khách khí một câu, tiếp nhận hộp ngọc, từ đó lấy ra một cái, ném cho cách đó không xa Nhược Hoan: “Gần nhất cố gắng tu luyện, đem cái này một cái luyện hóa, hẳn là có thể đi đến Hóa Phàm tứ trọng đỉnh phong!”"),t("br"),t("br"),n._v("Ngay sau đó lại lấy ra một cái, đưa cho Triệu Nhã, lập tức nhìn về phía cách đó không xa Ngô cung chủ: “Cung chủ, đã cái kia mẫu thủy đã phế bỏ, có thể hay không đem bọn ngươi băng nguyên cung băng nguyên mượn dùng một chút?”"),t("br"),t("br"),n._v("Mẫu thủy bên trong Âm Dương chi khí bị hấp thu sạch sẽ, địa mạch cùng băng nguyên còn tại."),t("br"),t("br"),n._v("“Ngươi muốn làm gì?”"),t("br"),t("br"),n._v("Ngô cung chủ vẻ mặt cảnh giác."),t("br"),t("br"),n._v("Băng nguyên là bọn hắn nguyên hỏa băng người vượn của quý, rất ít tuỳ tiện lấy ra."),t("br"),t("br"),n._v("“Không có gì, Triệu Nhã là Thuần Âm thể chất, dung hợp băng nguyên lời nói, có thể để cho tu vi nhanh chóng tiến lên, không có gì bất ngờ xảy ra, nửa cái tháng sau liền có thể giải quyết trong cơ thể ngươi vấn đề, muốn cho nàng dần dần từng bước tu luyện, chỉ sợ không có thời gian nửa năm rất khó làm đến.”"),t("br"),t("br"),n._v("Trương Huyền chần chờ một chút, nói."),t("br"),t("br"),n._v("Vốn là nghĩ đến Âm Dương đầm cũng có thể để Triệu Nhã nhanh chóng tiến bộ, bây giờ bị hủy, chỉ có thể muốn biện pháp khác."),t("br"),t("br"),n._v("May mắn, băng nguyên vẫn còn, dùng cái này lời nói, hiệu quả có lẽ so cái gọi là Âm Dương đầm còn tốt hơn không ít."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Ngô cung chủ ngẩn ngơ."),t("br"),t("br"),n._v("Không nghĩ tới đối phương còn muốn lấy thay nàng giải quyết chứng bệnh."),t("br"),t("br"),n._v("Đè nén hô hấp, Ngô cung chủ nói: “Nàng là Thuần Âm thể chất, đối với chúng ta nguyên hỏa băng người vượn cực kỳ trọng yếu, chớ nói cho nàng một phần băng nguyên, coi như toàn bộ cấp cho, cũng không tính là gì, ta vậy thì đi lấy...”"),t("br"),t("br"),n._v("Nói xong quay người rời khỏi."),t("br"),t("br"),n._v("Thời gian không dài, cầm đến một cái hộp ngọc."),t("br"),t("br"),n._v("Nhẹ nhàng mở ra, bên trong một cái tựa như linh thạch đồ vật, tản mát ra nồng đậm hàn khí, dường như vừa xuất hiện liền có thể đem người đông thành băng u cục."),t("br"),t("br"),n._v("Thứ này những người khác nhìn thấy đều cảm thấy giá rét thấu xương, chỉ có Triệu Nhã nhìn thấy, con mắt tỏa ánh sáng, cảm thấy toàn thân huyết dịch cũng vì đó sôi trào."),t("br"),t("br"),n._v("“Triệu Nhã, mấy ngày nay dựa theo ta truyền thụ công pháp của ngươi tu luyện, có cái gì không hiểu, trực tiếp hỏi ta!”"),t("br"),t("br"),n._v("Đem hộp ngọc đưa cho nàng, Trương Huyền bàn giao."),t("br"),t("br"),n._v("“Vâng!” Triệu Nhã gật đầu."),t("br"),t("br"),n._v("“Tốt, ta cũng muốn bế quan tu luyện!”"),t("br"),t("br"),n._v("Hết thảy an bài hết, Trương Huyền nói."),t("br"),t("br"),n._v("Khang đường chủ cho năm cái linh thạch trung phẩm, cho Nhược Hoan một cái, Triệu Nhã một cái, còn thừa lại ba cái, đầy đủ hắn tấn cấp đến Hóa Phàm nhị trọng đỉnh phong."),t("br"),t("br"),n._v("Ngô cung chủ phái người an bài mấy sạch sẽ gian phòng, để mọi người ở lại."),t("br"),t("br"),n._v("Trương Huyền, Nhược Hoan, Triệu Nhã toàn bộ cũng bắt đầu cố gắng tu luyện."),t("br"),t("br"),n._v("Thời gian trôi qua rất nhanh, đảo mắt nửa tháng trôi qua."),t("br"),t("br"),n._v("Ba cái linh thạch trung phẩm trợ giúp bên dưới, Trương Huyền cùng ngày đã đột phá đến Hóa Phàm nhị trọng đỉnh phong, về sau một mực củng cố tu vi."),t("br"),t("br"),n._v("Mà Nhược Hoan công tử, cũng rốt cục tại hai ngày trước, đạt đến Hóa Phàm tứ trọng hậu kỳ."),t("br"),t("br"),n._v("Bất quá, muốn nói trong ba người tiến lên lớn nhất, cũng không phải là hắn, mà là Triệu Nhã."),t("br"),t("br"),n._v("Thời gian nửa tháng, mượn nhờ băng nguyên, thể chất thành công kích hoạt lên ba mươi phần trăm, tu vi cũng từ nửa bước Hóa Phàm cảnh, liên tiếp đột phá, đạt đến Hóa Phàm tứ trọng! Bản thân tu vi, so Trương Huyền cái này làm lão sư còn cao hơn."),t("br"),t("br"),n._v("Tu vi tiến lên, bản thân khí chất càng thêm siêu phàm thoát tục, từ xa nhìn lại, như cùng một cái chín ngày trích lạc tiên tử, không dính khói lửa trần gian."),t("br"),t("br"),n._v("Nhìn Nhược Hoan công tử kích động không thôi, sinh ra lòng ái mộ, bất quá... Nhớ tới Trương sư mạnh mẽ, đành phải lộ vẻ tức giận thu hồi tâm tư."),t("br"),t("br"),n._v("Không có ý nghĩ xấu liền thảm như vậy, thật muốn có, thật sợ không sống tới danh sư thi đấu."),t("br"),t("br"),n._v("Thấy rõ thoải mái tiểu thuyết liền đến"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-563"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-565"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);