(window.webpackJsonp=window.webpackJsonp||[]).push([[1815],{2170:function(n,h,g){"use strict";g.r(h);var t=g(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-556-trieu-nha-roi-khoi-thuong"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-556-trieu-nha-roi-khoi-thuong"}},[n._v("#")]),n._v(" Chương 556: Triệu Nhã rời khỏi (thượng)")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("br"),g("br"),n._v("Chương 556: Triệu Nhã rời khỏi (thượng)"),g("br"),g("br"),n._v("Triệu Nhã tu vi tiến bộ, cùng Ngô cung chủ không kém nhiều, chỉ dùng một ngày công phu, Trương Huyền liền mượn nhờ huyết dịch, thuận lợi giúp người sau giải quyết quấy nhiễu nàng nhiều năm vấn đề, để hắn thiên ân vạn tạ. &"),g("br"),g("br"),n._v("“Trương sư, tổng bộ tới đặc sứ, muốn gặp ngươi!”"),g("br"),g("br"),n._v("Hôm nay tu luyện hoàn tất, cảm giác khoảng cách danh sư thi đấu thời gian cũng không dài, đang định cùng Khang đường chủ đám người rời khỏi, chỉ thấy Ngô cung chủ đi tới."),g("br"),g("br"),n._v("Giải quyết âm quyết, dương quyết tuổi thọ vấn đề, nàng đã báo lên tổng bộ, bởi vì cái này, đặc sứ chuyên môn chạy tới."),g("br"),g("br"),n._v("“Tốt!”"),g("br"),g("br"),n._v("Trương Huyền gật gật đầu, đi theo Ngô cung chủ phía sau, hướng về phía Băng Nguyên cung đại sảnh đi đến."),g("br"),g("br"),n._v("Băng Nguyên cung đại sảnh, chính là lúc trước Ngô cung chủ tiếp đãi bọn hắn địa phương, đi tới trước mặt, chỉ thấy một cái trung niên phu nhân, đứng tại ở giữa nhất, nhìn thấy hai người đi vào, quay đầu nhìn lại."),g("br"),g("br"),n._v("“Đặc sứ, vị này chính là Trương sư!” Ngô cung chủ tiến về phía trước một bước."),g("br"),g("br"),n._v("“Trương sư, tại hạ Nguyên Hỏa Băng Nguyên tổng bộ, Liễu Huyên!”"),g("br"),g("br"),n._v("Đặc sứ ôm quyền, nhìn lại, trong mắt tràn đầy tán dương."),g("br"),g("br"),n._v("Nhận được tin tức, nàng có chút không dám tin tưởng, có điều, khi thật sự thấy được sửa chữa sau công pháp, lập tức kinh vì Thiên Nhân."),g("br"),g("br"),n._v("Vốn cho rằng có thể sửa chữa công pháp, ít nhất là cái vượt qua bốn mươi danh sư, nằm mơ đều không có nghĩ đến trẻ tuổi như vậy, liền hai mươi cũng chưa tới."),g("br"),g("br"),n._v("“Danh sư, Trương Huyền!”"),g("br"),g("br"),n._v("Trương Huyền ôm quyền đáp lễ, đồng thời cẩn thận quan sát trước mắt vị này, vừa nhìn phía dưới, con ngươi kìm lòng không được co vào."),g("br"),g("br"),n._v("Vị này thực lực, như là đại dương mênh mông, sâu không lường được, cho dù là hắn hiện tại nhãn lực, cũng cái gì cũng nhìn không ra!"),g("br"),g("br"),n._v("Nói cách khác... Thực lực của đối phương, chí ít vượt qua Hóa Phàm ngũ trọng, thậm chí so... Lúc trước Mặc Hồn Sinh còn muốn đáng sợ!"),g("br"),g("br"),n._v("Mặc Hồn Sinh cũng đã đi đến Hóa Phàm cửu trọng, còn mạnh hơn hắn, đến cùng là loại nào cảnh giới?"),g("br"),g("br"),n._v("Khó trách có thể từ tổng bộ, thời gian nửa tháng liền chạy tới... Đáng sợ!"),g("br"),g("br"),n._v("“Quả nhiên là anh hùng còn trẻ!”"),g("br"),g("br"),n._v("Thấy đối phương biết thực lực của nàng bất phàm, vẫn mặt không đổi sắc, không sợ chút nào, Liễu Huyên đặc sứ hài lòng gật đầu, cười cười: “Kỳ thật, ta chuyên từ tổng bộ tới, là vì hai chuyện!”"),g("br"),g("br"),n._v("“Ồ?” Trương Huyền nhìn qua."),g("br"),g("br"),n._v("“Thứ nhất, Trương sư sửa chữa công pháp, để chúng ta Nguyên Hỏa Băng Nguyên người, miễn bị chết yểu chi ách, ta đại biểu toàn bộ Băng Nguyên cung, bày tỏ sâu sắc cảm tạ, thành ý mời ngươi đi tổng bộ làm khách, băng nguyên người, nguyện ý lấy trưởng lão chi lễ đối đãi!”"),g("br"),g("br"),n._v("“Trưởng lão?”"),g("br"),g("br"),n._v("Một bên Ngô cung chủ chấn động toàn thân."),g("br"),g("br"),n._v("Băng Nguyên cung tổng bộ, thuộc về toàn bộ danh sư đại lục, nổi danh nhất cự đầu một trong, cường đại cỡ nào, nàng biết đến rất rõ ràng, lấy trưởng lão chi vị đối đãi, chỉ bằng vào cái này một cái tên tuổi, liền xem như nàng phấn đấu vô số năm, đều làm không được."),g("br"),g("br"),n._v("Thậm chí, đừng nói vô số năm, cố gắng cả đời, đều khó có khả năng hoàn thành."),g("br"),g("br"),n._v("“Điều kiện đâu?” Trương Huyền nhìn qua."),g("br"),g("br"),n._v("Đối phương cho ra điều kiện, tự nhiên sẽ có yêu cầu, không có khả năng bỗng dưng hạ xuống cơm trưa miễn phí."),g("br"),g("br"),n._v("“Trương sư người sảng khoái nói chuyện sảng khoái!”"),g("br"),g("br"),n._v("Thấy hắn không có cao hứng quên hết tất cả, ngược lại thẳng vào chỗ yếu hại, Liễu Huyên đặc sứ lộ ra tán dương vẻ, cười cười: “Rất đơn giản, hi vọng về sau Trương sư, có thể vì băng nguyên người giải quyết công pháp nan đề... Thường trú Băng Nguyên cung!”"),g("br"),g("br"),n._v("“Thường trú Băng Nguyên cung? Không thể rời khỏi thật sao?” Trương Huyền nhướng mày."),g("br"),g("br"),n._v("Một cái nghề nghiệp truyền thừa, chủ yếu tập trung ở công pháp bên trên, bởi vậy thứ này sẽ không dễ dàng cho người khác quan sát, đối phương đã để cho mình giải quyết nan đề, đồng thời thường trú, ý tứ đã rất rõ ràng... Vì phòng ngừa công pháp truyền ra ngoài, chỉ có thể ở lại nơi đó, không gặp được cho phép, không cách nào ra ngoài!"),g("br"),g("br"),n._v("Cái này cùng biến tướng cầm tù có cái gì khác nhau?"),g("br"),g("br"),n._v("“Trương sư không nên hiểu lầm, rời khỏi tự nhiên là có thể, có điều, Băng Cung có thể muốn đầu tiên khảo nghiệm trung thành, sẽ không phản bội, ngươi cũng biết, một cái nghề nghiệp truyền thừa, là không cho phép truyền ra ngoài, Băng Cung cũng phải có lo nghĩ của mình...”"),g("br"),g("br"),n._v("Bị đối phương một cái nói ra, Liễu Huyên đặc sứ có chút xấu hổ."),g("br"),g("br"),n._v("“Ta đã biết, các ngươi có lo nghĩ của mình, ta cũng có thể lựa chọn không đi!” Trương Huyền vẫy tay."),g("br"),g("br"),n._v("Cầm tù?"),g("br"),g("br"),n._v("Nói đùa, hắn còn không có thụ ngược đãi khuynh hướng."),g("br"),g("br"),n._v('Thấy hắn trực tiếp cự tuyệt, Liễu Huyên đặc sứ giải thích nói: "Kỳ thật gia nhập chúng ta Băng Nguyên cung cũng vẫn là không sai, Băng Nguyên cung,'),g("br"),g("br"),n._v('Hầu như đều là thuần một sắc nữ tử, chỉ cần Trương sư nguyện ý, muốn cưới bao nhiêu, cưới mấy cái cũng không thành vấn đề!"'),g("br"),g("br"),n._v("Băng Nguyên cung đều là tuyệt sắc nữ tử, không ít thực lực cường đại nam tử ham sắc đẹp, cam tâm lưu tại trong đó."),g("br"),g("br"),n._v("“Nói chuyện thứ hai đi!” Trương Huyền lắc đầu."),g("br"),g("br"),n._v("Hắn nhất định phải đuổi tại ba mươi tuổi trước, trở thành cửu tinh danh sư, mới có thể cam đoan không chết, cái gọi là nữ tử, trưởng lão, hoàn toàn không có hứng thú."),g("br"),g("br"),n._v("Đây cũng là vì cái gì Mạc Vũ công chúa, Thẩm Bích Như đám người rõ ràng đối với hắn có ý tứ, nhưng không nhúc nhích nguyên nhân chủ yếu."),g("br"),g("br"),n._v("Người đều không biết lúc nào sẽ chết, sinh tử áp lực xuống, đâu còn có công phu suy nghĩ nam, nữ ở giữa sự tình."),g("br"),g("br"),n._v("“Kiện thứ hai, là học sinh của ngươi... Triệu Nhã!”"),g("br"),g("br"),n._v("Thấy hắn cự tuyệt, Liễu Huyên đặc sứ lộ ra vẻ thất vọng, có điều, lập tức điều chỉnh tâm cảnh, nhìn lại."),g("br"),g("br"),n._v("Trương Huyền nhướng mày."),g("br"),g("br"),n._v("“Thuần Âm thể chất đối với chúng ta Nguyên Hỏa Băng Nguyên tầm quan trọng, chắc hẳn... Ngô cung chủ cũng cùng ngươi đã nói.” Liễu Huyên đặc sứ nói."),g("br"),g("br"),n._v("“Là nói qua một chút, cụ thể nguyên do cũng không rõ ràng!”"),g("br"),g("br"),n._v("Trước đó Ngô cung chủ chỉ nói, Thuần Âm thể chất liên quan băng nguyên người sinh tử tồn vong, cũng không nói tỉ mỉ."),g("br"),g("br"),n._v("“Nguyên Hỏa Băng Nguyên người truyền thừa tại một vị Thuần Âm thể chất lão tổ, mỗi một thời đại cao nhất cung chủ, chỉ có Thuần Âm thể chất mới có thể tiếp nhận... Về khoảng cách một vị cung chủ tử vong, đã ròng rã một ngàn năm, không tìm được loại thể chất này! Hiện tại Băng Nguyên cung, rắn mất đầu, chịu đến rất nhiều thế lực áp bách, đã lung lay sắp đổ.”"),g("br"),n._v(" Liễu Huyên đặc sứ than thở một tiếng, lắc đầu: “Nếu như có thể tìm tới một vị Thuần Âm thể chất, kế thừa đại vị, ta Băng Nguyên cung, có lẽ liền có thể miễn trừ loại nguy cơ này, Đông Sơn tái khởi.”"),g("br"),g("br"),n._v("“Cung chủ?”"),g("br"),g("br"),n._v("Trương Huyền sửng sốt."),g("br"),g("br"),n._v("Còn tưởng rằng bọn hắn muốn tìm Triệu Nhã làm gì, không nghĩ tới là cái này."),g("br"),g("br"),n._v("“Không sai!” Liễu Huyên đặc sứ nhìn qua: “Cho nên, chuyện thứ hai rất đơn giản, ta hi vọng lần này tới có thể đưa nàng mang về tổng bộ, nghĩ biện pháp nhanh chóng kích chất, dốc lòng bồi dưỡng, tranh thủ sớm ngày kế thừa cung chủ chi vị.”"),g("br"),g("br"),n._v("“Ta đồng dạng có thể để cho thể chất nàng kích hoạt!”"),g("br"),g("br"),n._v("Trương Huyền vẫy tay."),g("br"),g("br"),n._v("Mặc dù hắn thực lực bây giờ không đủ, tài nguyên cũng không có bao nhiêu, nhưng từ Thiên Huyền vương quốc cùng nhau đi tới, để Triệu Nhã thực lực, liên tiếp đột phá... Để thể chất nàng triệt để kích hoạt cũng chỉ là vấn đề thời gian, không tính là gì."),g("br"),g("br"),n._v("“Trương sư sự tình, ta nghe nói qua, biết bản lãnh của ngươi, lưu tại bên cạnh ngươi, triệt để kích hoạt, cũng là có khả năng, bất quá... Trương sư có thể từng nghĩ tới cần bao lâu? Mười năm? Hai mươi năm? Năm mươi năm? Thậm chí... Một trăm năm, hai trăm năm?”"),g("br"),g("br"),n._v("Liễu Huyên đặc sứ nhìn qua: “Thể chất đặc thù, nương theo càng về sau, tiêu hao tài nguyên cũng là càng lớn, coi như Trương sư có thể liên tục không ngừng cung cấp, Triệu Nhã, có thể... Nguyện ý bình tĩnh tiếp nhận?”"),g("br"),g("br"),n._v("“Cái này...”"),g("br"),g("br"),n._v("Trương Huyền không cách nào phản bác."),g("br"),g("br"),n._v("Đặc sứ thể chất, nương theo kích hoạt tỉ lệ phần trăm càng cao, còn lại cũng càng khó kích hoạt, liền cùng tu vi đồng dạng, càng cao thâm càng khó đột phá."),g("br"),g("br"),n._v("Hắn coi như về sau có thể lấy được vô số tài nguyên, giúp đối phương tấn cấp, có thể Triệu Nhã đâu?"),g("br"),g("br"),n._v("Cũng không thể một mực sống ở bản thân cánh chim bao che xuống đi."),g("br"),g("br"),n._v("Nếu thật là cái gì đều vì hắn chuẩn bị kỹ càng, lại như thế nào có thể tốt hơn phát triển, trở thành cao thủ chân chính?"),g("br"),g("br"),n._v("Một mực vô điều kiện ban cho, cho dù là lão sư, cũng không có khả năng bình tĩnh tiếp nhận ah!"),g("br"),g("br"),n._v("Sư ân, là chỉ điểm, là trưởng bối quan tâm, mà không phải một vị trả giá."),g("br"),g("br"),n._v("“Nếu như nàng đến Băng Nguyên cung, chúng ta sẽ đem tốt nhất truyền thừa cùng phong phú nhất tài nguyên dâng lên, hoàn toàn có thể để cho hắn trong thời gian ngắn kích chất, trở thành chân chính danh chấn đại lục nhân vật, Trương sư đã vì mình học sinh cân nhắc, chắc hẳn, cũng hi vọng nàng có thể sớm ngày trở nên nổi bật, mà không phải cùng ngươi bốn phía bôn ba.”"),g("br"),g("br"),n._v("Liễu Huyên đặc sứ nói tiếp."),g("br"),g("br"),n._v("Trương Huyền yên lặng."),g("br"),g("br"),n._v("Cha mẹ thương con, vì kế sách sâu xa."),g("br"),g("br"),n._v("Trương Huyền thân là lão sư, cũng là loại ý nghĩ này, hắn mặc dù cũng có thể cam đoan để Triệu Nhã, về sau trở thành chân chính đỉnh phong cao thủ, kích hoạt tất cả thể chất, nhưng... Muốn bắt kịp Băng Nguyên cung tốc độ, khẳng định là không thể nào."),g("br"),g("br"),n._v("Dù sao, tài nguyên tu luyện cái này một khối, liền làm không được."),g("br"),g("br"),n._v("Lại nói, đối phương lịch đại cung chủ đều là Thuần Âm thể chất, nắm giữ hoàn chỉnh truyền thừa cùng kích chất phương pháp, so với hắn cái này cuối cùng người mù sờ voi, dễ lên vô số lần."),g("br"),g("br"),n._v("“Ý kiến của ta không quan trọng, chủ yếu là nàng, nàng lựa chọn ra sao, ta đều sẽ ủng hộ!”"),g("br"),g("br"),n._v("Trương Huyền lắc đầu."),g("br"),g("br"),n._v("Hắn chỉ là lão sư, thay thế không được học sinh làm ra quyết định, nếu như Triệu Nhã nguyện ý đi theo bản thân, hao hết tất cả tâm huyết, đều sẽ để nàng đi đến thế giới chi đỉnh."),g("br"),g("br"),n._v("Không nguyện ý... Bất luận kẻ nào cũng không thể ép buộc, Băng Nguyên cung, cũng không được!"),g("br"),g("br"),n._v("“Lão sư, ta nguyện ý cùng với nàng đi!”"),g("br"),g("br"),n._v("Vừa dứt lời, chỉ thấy nghe được một thanh âm, Triệu Nhã đi tới, trong ánh mắt mang theo kiên định."),g("br"),g("br"),n._v("“Nguyện ý?”"),g("br"),g("br"),n._v("“Vâng, cho tới nay, lão sư đều vì ta trả giá, ta nhìn ở trong mắt, ta hi vọng, cuối cùng cũng có một ngày, cũng có thể vì lão sư làm những gì...”"),g("br"),g("br"),n._v("Triệu Nhã đi tới trước mặt, nắm đấm xiết chặt: “Mà cái này... Chính là một cơ hội.”"),g("br"),g("br"),n._v("Lão sư để nàng, bỏ ra bao nhiêu vất vả, nàng rõ rõ ràng ràng, một mực không có cách nào báo đáp."),g("br"),g("br"),n._v("Hiện tại, chỉ cần đáp ứng đối phương yêu cầu, chẳng khác nào về sau nắm trong tay Băng Nguyên cung cái này thế lực lớn, cũng là có tư cách, trợ giúp lão sư, không đồng nhất thực là vướng víu."),g("br"),g("br"),n._v("Mấu chốt nhất là... Nàng còn có một số người tiểu tâm tư, có lẽ... Nương theo địa vị tăng lên, cũng có thể thực hiện."),g("br"),g("br"),n._v("“Như vậy đi, ta cho ngươi ba ngày thời gian cân nhắc, bất kể làm ra lựa chọn ra sao, ta đều ủng hộ!”"),g("br"),g("br"),n._v("Trương Huyền than thở một tiếng, nói."),g("br"),g("br"),n._v("“Không cần suy tính, ta đã nghĩ kỹ, có quyết định của mình!”"),g("br"),g("br"),n._v("Triệu Nhã hốc mắt đỏ lên, quỳ rạp xuống đất: “Triệu Nhã cảm tạ lão sư một đường bồi dưỡng cùng thành toàn, bất kể đi đến nơi nào, đều là học sinh của ngài, đều sẽ vĩnh viễn nghe một mình ngài lời nói, không oán không hối!”"),g("br"),g("br"),n._v("“Đứng lên đi!”"),g("br"),g("br"),n._v("Biết đối phương đã có lựa chọn, Trương Huyền gật gật đầu đem nàng đỡ dậy."),g("br"),g("br"),n._v("Hùng ưng, cũng nên để nó trải qua ma luyện, tự do bay lượn."),g("br"),g("br"),n._v("Học sinh cũng là như vậy."),g("br"),g("br"),n._v("Dù là dù tiếc đến đâu, cũng muốn thả, để nàng trải qua mưa gió, mới có thể trở thành chân chính hào hùng."),g("br"),g("br"),n._v("“Vâng!”"),g("br"),g("br"),n._v("Triệu Nhã đứng dậy."),g("br"),g("br"),n._v("“Liễu Huyên đặc sứ, hi vọng ngươi lời mới vừa nói đều là thật, nếu như về sau, cho ta biết trong lời của ngươi là giả, đối ta học sinh bất lợi, không chỉ sẽ giết ngươi, lại còn diệt đi các ngươi Băng Nguyên cung!”"),g("br"),g("br"),n._v("Làm ra quyết định, cũng không có gì có thể xoắn xuýt, Trương Huyền quay đầu nhìn về phía cách đó không xa đặc sứ, mắt sáng lên."),g("br"),g("br"),n._v("Hắn mặc dù bây giờ không có thực lực, nhưng đối phương thực có can đảm lừa gạt, hắn không ngại đánh tới Băng Nguyên cung, đem cái nghề nghiệp này triệt để giết chết."),g("br"),g("br"),n._v("(Làm gốc thư thứ 31 vị minh chủ  tăng thêm. Mặt khác, đề cử bạn tốt trường phong đại thần sách mới 《 binh phong vương tọa 》, tương lai thế giới, đốt lên binh vương tia lửa, mọi người có thể nhìn một chút. Cuối cùng, tiếp tục cầu hai tấm nguyệt phiếu, giữ gốc nguyệt phiếu tất cả mọi người có.)"),g("br"),g("br"),n._v("Convert by: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-564"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-566"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);