(window.webpackJsonp=window.webpackJsonp||[]).push([[1819],{2174:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-560-tien-tai-đong-nhan-tam"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-560-tien-tai-đong-nhan-tam"}},[n._v("#")]),n._v(" Chương 560: Tiền tài động nhân tâm")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 560: Tiền tài động nhân tâm"),t("br"),t("br"),n._v("Còn tưởng rằng đối phương yêu cầu cao bao nhiêu, không nghĩ tới đơn giản như vậy."),t("br"),t("br"),n._v("Một cái vạn quốc liên minh minh chủ, lại giết một cái Hóa Phàm nhị trọng sơ kỳ tiểu nhân vật, điều kiện như vậy, đổi lại ai cũng sẽ đáp ứng."),t("br"),t("br"),n._v("Quả thực cùng cho không không có gì khác biệt."),t("br"),t("br"),n._v("“Mọi người trước đừng có gấp!”"),t("br"),t("br"),n._v("Mọi người ở đây toàn bộ tràn đầy hưng phấn, muốn tranh đoạt danh ngạch thời điểm, Đinh Hồng nhìn quanh một tuần: “Nghe ta nói hết lời, mới quyết định!”"),t("br"),t("br"),n._v("Mọi người dừng lại nhìn qua."),t("br"),t("br"),n._v("“Ta muốn giết đến người này, mặc dù thực lực chẳng ra sao cả, thân phận lại tương đối đặc thù, là một vị danh sư, hơn nữa... Chính là Vạn Quốc thành tham gia lần thi đấu này người!”"),t("br"),t("br"),n._v("Đinh Hồng nói."),t("br"),t("br"),n._v("“Vạn Quốc thành tham gia thi đấu danh sư? Ngươi nói chính là... Gần nhất thanh danh vang dội Trương Huyền danh sư?”"),t("br"),t("br"),n._v("Mọi người sững sờ."),t("br"),t("br"),n._v("Xem như tham gia thi đấu đối thủ, coi như Vạn Quốc thành không thế nào để người chú ý, tên Trương Huyền vẫn là nghe nói qua."),t("br"),t("br"),n._v("Không đủ hai mươi, từ tuyển bạt bên trong trổ hết tài năng, càng là tại chỗ khảo hạch tứ tinh danh sư thành công, hấp dẫn không ít người ánh mắt."),t("br"),t("br"),n._v("Muốn giết người này?"),t("br"),t("br"),n._v("Mới vừa rồi còn kêu so sánh vui mừng mọi người, tất cả đều trong nháy mắt yên lặng."),t("br"),t("br"),n._v("Danh sư, là không thể tuỳ tiện chém giết danh sư, nếu không, tổng bộ truy xét xuống, ai cũng gánh không được."),t("br"),t("br"),n._v("Huống chi là loại này gần nhất thanh danh vang dội gia hỏa, một khi động thủ, bọn hắn vị trí thế lực, đều vô cùng có khả năng chịu đến nghi ngờ cùng điều tra."),t("br"),t("br"),n._v("“Ngươi cùng Trương sư có gì thù hận?”"),t("br"),t("br"),n._v("Mày nhăn lại, La Hoàng tông chủ nhìn lại."),t("br"),t("br"),n._v("Danh sư không thể tuỳ tiện chém giết, nhưng cũng không phải không thể giết, muốn làm như thế, nhất định phải có phía dưới mấy cái điều kiện một trong."),t("br"),t("br"),n._v("Một, hai bên có chân chính thù hận, đi đến không cách nào hóa giải mức độ."),t("br"),t("br"),n._v("Hai, vị danh sư này tự kiềm chế thân phận, làm ra làm trái sư đức sự tình, gặp vạn người phỉ nhổ."),t("br"),t("br"),n._v("Ba, đối cấp cao danh sư bất kính, làm ra ngỗ nghịch cấp cao danh sư hành vi cùng cử động."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Cũng chính là Danh Sư đường chỗ quy định... Trái lương tâm người, giết! Làm trái đức người, giết! Làm trái lễ người, giết!"),t("br"),t("br"),n._v("Liền xem như danh sư, đức hạnh cùng lễ nghi, cũng không thể vi phạm, nếu không, Danh Sư đường cũng không bảo vệ được."),t("br"),t("br"),n._v("“Hắn giết ta thân nhân, diệt ta vương quốc, cừu hận... Không đội trời chung!”"),t("br"),t("br"),n._v("Răng cắn chặt, Đinh Hồng lên tiếng gào thét."),t("br"),t("br"),n._v("Đường đường một nước lão tổ, hiện tại cùng chó nhà có tang đồng dạng, Liên gia cũng không dám trở về, nhiều người địa phương không dám đi... Cũng bởi vì cái tên này."),t("br"),t("br"),n._v("“Cừu hận không đội trời chung, như vậy là tốt rồi!”"),t("br"),t("br"),n._v("Thấy hắn biểu lộ không giống giả mạo, La Hoàng tông chủ nhẹ nhàng thở ra: “Ta có thể nghĩ biện pháp để ngươi thực lực, mau chóng đi đến Hóa Phàm tam trọng, ngươi tự mình báo thù đem hắn chém giết, được chứ?”"),t("br"),t("br"),n._v("Hắn đã cùng vị này Trương sư có không đội trời chung thù hận, phù hợp chém giết danh sư điều kiện thứ nhất, hoàn toàn có thể để cho hắn tăng thực lực lên, bản thân báo thù."),t("br"),t("br"),n._v("Hai bên đại thù, tỷ thí công bình bị giết chết, liền xem như Danh Sư đường, cũng nói không ra gì gì đó."),t("br"),t("br"),n._v("“Đúng a!”"),t("br"),t("br"),n._v("“Ta làm sao không nghĩ tới điểm ấy!”"),t("br"),t("br"),n._v("“Vẫn là La tông chủ phản ứng càng nhanh...”"),t("br"),t("br"),n._v("Nghe được hắn, mọi người vỗ trán một cái."),t("br"),t("br"),n._v("Đồng dạng là giết người, chỉ cần bọn hắn không xuất thủ, liền sẽ không bày ra trách nhiệm, về sau tổng bộ truy xét xuống, cũng tìm không thấy phiền phức."),t("br"),t("br"),n._v("“Biện pháp này tuy tốt, bất quá... Tên kia cổ quái, ta sợ không phải là đối thủ!”"),t("br"),t("br"),n._v("Đinh Hồng lắc đầu."),t("br"),t("br"),n._v("Đối phương nói mặc dù rất để hắn tâm động, nhưng khi đó từng cùng tên kia chiến đấu qua một hồi, thủ đoạn quỷ dị để cho người ta khó có thể tin."),t("br"),t("br"),n._v("Hắn Hóa Phàm nhất trọng thực lực, đều bại bởi nửa bước Hóa Phàm, coi như đi đến Âm Dương cảnh, cũng chưa chắc có thể là đối thủ."),t("br"),t("br"),n._v("Nói thật, cùng tên kia chiến đấu qua, mới biết được đáng sợ, vạn nhất không có đánh qua, còn muốn trốn, khẳng định là trốn không thoát."),t("br"),t("br"),n._v("“Không phải là đối thủ? Ngươi ý tứ, nhất định phải chúng ta chém giết mới có thể?”"),t("br"),t("br"),n._v("La Hoàng mày nhăn lại."),t("br"),t("br"),n._v("“Vâng!” Đinh Hồng nói."),t("br"),t("br"),n._v("“Cái này...” Mọi người yên lặng."),t("br"),t("br"),n._v("Đối phương nếu như là cái yên lặng vô cớ tiểu nhân vật, giết liền giết, nhưng người ta là tham gia danh sư thi đấu tuyển thủ, danh khí như thế lớn."),t("br"),t("br"),n._v("Một khi động thủ, tất nhiên gây nên Vạn Quốc thành Danh Sư đường bắn ngược, đến lúc đó chỉ cần bẩm báo tổng bộ, đừng nói thế lực khác, coi như mạnh nhất Lưu Vân tông cũng chịu đựng không nổi."),t("br"),t("br"),n._v("“Lời nói thật cùng các ngươi nói đi, cái này Khổng sư bản chép tay cũng không tại trên tay của ta, mà là tại trong tay người khác. Bất kể là ai, chỉ cần đáp ứng, chiếm được trát phía sau, giúp ta hoàn thành cái kia hai chuyện, ta liền lập tức nói cho hắn biết, bản chép tay ở nơi nào.”"),t("br"),t("br"),n._v("Thấy mọi người không nói lời nào, Đinh Hồng nói."),t("br"),t("br"),n._v("“Để ngươi trở thành vạn quốc liên minh minh chủ cùng tăng thực lực lên không khó, Trương sư sự tình... Liên quan rất lớn, coi như chúng ta Lưu Vân tông, cũng không dám tuỳ tiện đáp ứng!”"),t("br"),t("br"),n._v("La Hoàng tông chủ chần chờ một chút, nói: “Như vậy đi, ngươi nói trước đi món kia bảo vật ở nơi nào, chúng ta nhiều người như vậy, sẽ thương nghị lựa chọn ra sao, nếu như đáng giá, tự sẽ ra tay, không đáng, cũng sẽ giúp ngươi hoàn thành cái khác hai cái nguyện vọng.”"),t("br"),t("br"),n._v("Giết một vị tứ tinh danh sư, liền xem như bọn hắn cũng không dám tuỳ tiện động thủ, vẫn là tạm thời đem sự tình ngăn chặn, biết bảo vật chuẩn xác tin tức lại nói."),t("br"),n._v(" “Không sai, chúng ta nhiều như vậy thế lực, còn không đến mức bởi vì chút chuyện nhỏ này, đánh mất tín dự của chính mình!”"),t("br"),t("br"),n._v("Một cái khác tông chủ nói."),t("br"),t("br"),n._v("“Thứ này không trong tay ngươi, ngươi tin tức giá trị cũng là thấp!” Lại có người nói."),t("br"),t("br"),n._v("Nếu như Khổng sư bản chép tay trong tay hắn, coi như tiếp nhận bị tổng bộ chỉ trích nguy hiểm, giết liền giết, có thể... Đồ vật còn tại người khác nơi đó, hắn chỉ cung cấp cái tin tức... Một cái tin tức, giá trị cao như vậy, thực sự không đáng."),t("br"),t("br"),n._v("Dù sao, nếu là chỉ có một cái thế lực biết tin tức này thì cũng thôi đi, nhiều người như vậy đồng thời biết, Trương sư một khi xảy ra vấn đề, rất dễ dàng rò rỉ tin tức, đến lúc đó, Khổng sư bản chép tay không bảo vệ việc nhỏ, toàn bộ tông môn đều sẽ mang đến hoạ lớn ngập trời."),t("br"),t("br"),n._v("“Ta tin tưởng chư vị danh sư tín dự, thực không dám giấu giếm, cái này Khổng sư bản chép tay ngay tại Trương Huyền, Trương sư trong tay. Thứ này là hắn từ trong tay của ta cướp đi, ta cánh tay này cũng là bị hắn chém xuống... Chỉ cần các ngươi có thể giết hắn, liền có thể lấy được cái này bản chép tay!”"),t("br"),t("br"),n._v("Đinh Hồng nhãn châu xoay động, đem trước nghĩ kỹ lời nói nói ra."),t("br"),t("br"),n._v("“Trương sư cướp đi bức thư tay của ngươi?”"),t("br"),t("br"),n._v("“Tay ngươi cánh tay là bị hắn chém xuống?”"),t("br"),t("br"),n._v("Mọi người sững sờ, lập tức cuồng hỉ."),t("br"),t("br"),n._v("Mặc dù bọn hắn đều là danh sư, có thể nhìn ra cái tên này lời nói nghĩ một đằng nói một nẻo, thậm chí còn có một ít tự mâu thuẫn, nhưng... Cái này không trọng yếu."),t("br"),t("br"),n._v("Trọng yếu là, chỉ cần hắn thừa nhận, bản chép tay bị Trương Huyền cướp đi..."),t("br"),t("br"),n._v("Cái này một cái lý do chính là có sai lầm sư đức, có thể đủ phán xử tử hình."),t("br"),t("br"),n._v("“Nói rõ chi tiết một cái tình huống cụ thể, nếu thật là như thế, vị này Trương sư làm trái sư đức, coi như không phải chúng ta phân bộ người, làm danh sư, cũng sẽ báo thù cho ngươi, diệt trừ danh sư bên trong bại hoại!”"),t("br"),t("br"),n._v("Vung tay lên, La Hoàng tông chủ đại nghĩa lăng nhiên mà nói."),t("br"),t("br"),n._v("“Đúng vậy a, có cái gì oan ức, cứ việc nói ra chính là, đang ngồi đều là người mang chính nghĩa danh sư, nếu như đối phương thật dựa dẫm danh sư thân phận, lấy mạnh hiếp yếu, khi dễ nhỏ yếu, chúng ta sẽ thay ngươi làm chủ!”"),t("br"),t("br"),n._v("“Danh Sư đường, quyết không cho phép loại người này xuất hiện.”"),t("br"),t("br"),n._v("Lại có hai vị danh sư quát."),t("br"),t("br"),n._v("Trong mọi người, cũng có danh sư, cảm thấy sự tình không có đơn giản như vậy, nhưng nhìn thấy La Hoàng tông chủ đám người thái độ kiên quyết, tất cả đều than thở một tiếng lắc đầu."),t("br"),t("br"),n._v("Đối mặt Khổng sư bản chép tay, liền xem như bọn hắn, cũng không chống nổi nghi ngờ."),t("br"),t("br"),n._v("“Đã mọi người muốn nghe, ta liền nói rõ chi tiết đến!”"),t("br"),t("br"),n._v("Đinh Hồng hốc mắt đỏ lên: “Món bảo vật này, là Cù gia vật phẩm, mà chúng ta Đinh gia cùng Cù gia, đời đời giao hảo, thậm chí cùng hưởng vương quốc. Bất quá, Cù gia nhân khẩu không vượng, ba năm trước đây, vị cuối cùng người thừa kế qua đời, đem món bảo vật này giao cho chúng ta đảm bảo.”"),t("br"),t("br"),n._v("Nói đến đây, Đinh Hồng tràn đầy phẫn uất cùng lửa giận: “Ai ngờ, chuyện này không biết làm sao bị cái kia Trương Huyền biết, vọt tới vương quốc, đồng thời tại tôn nhi ta kế vị đại điển bên trên, cưỡng ép đem hắn chém giết, cướp đi bản chép tay, thậm chí còn diệt ta vương quốc, nếu không phải ta chạy nhanh, khả năng từ lâu chết ngay tại chỗ!”"),t("br"),t("br"),n._v("“Vì che giấu tai mắt người, hắn cố ý lập các loại lấy cớ, trên thực tế chính là muốn che giấu Khổng sư bản chép tay tin tức! May mắn trời thương có thể thấy được, ta còn sống, nếu không, tin tức này, khẳng định sẽ biến mất trên thế gian, tại không người có thể khôi phục công đạo!”"),t("br"),t("br"),n._v("Răng cắn chặt, Đinh Hồng một tiếng hí dài."),t("br"),t("br"),n._v("Đối phương là danh sư, chú ý đến thân phận, coi như muốn động thủ, cũng cần một cái lý do thích hợp."),t("br"),t("br"),n._v("Mà cái này chính là hắn chuyên môn lập đi ra."),t("br"),t("br"),n._v("Rõ ràng là bọn hắn Đinh gia cướp bóc không được, lại trở thành, Trương Huyền không để ý đạo nghĩa, như vậy trải qua, tội danh đều toàn bộ lạc đến người sau trên người, coi như lại động thủ với hắn, cũng không có cái gì cảm giác tội lỗi."),t("br"),t("br"),n._v("“Đáng giận, Danh Sư đường thế mà còn có loại người này tồn tại!”"),t("br"),t("br"),n._v("“Quả thực không để ý đạo nghĩa, bỏ qua danh sư thân phận.”"),t("br"),t("br"),n._v("“Loại người này nhất định phải cố gắng giáo huấn một lần, cho hắn biết cái gì gọi là sư Đạo Tôn nghiêm...”"),t("br"),t("br"),n._v("“Khó trách Vạn Quốc thành Danh Sư đường, một mực bài danh đếm ngược, loại này tập tục, cũng nên chỉnh đốn một chút!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe xong Đinh Hồng lời nói, rất nhiều danh sư, tất cả đều lòng đầy căm phẫn."),t("br"),t("br"),n._v("“La tông chủ, ta đề nghị, chúng ta hiện tại liền đi tìm tới vị này Trương Huyền Trương sư, để hắn đem chuyện này nói rõ ràng!”"),t("br"),t("br"),n._v("“Không sai, muốn trước để hắn đưa tay trát giao ra, để chúng ta thay đảm bảo, nếu không, loại người này phẩm thấp người, một mực cầm, là đúng Khổng sư không tôn trọng cùng vũ nhục!”"),t("br"),t("br"),n._v("“Đúng vậy a, ta cũng có này dự định!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mọi người đồng loạt đem ánh mắt tập trung đạo La Hoàng tông chủ trên người."),t("br"),t("br"),n._v("“Các ngươi nói rất có lý, danh sư, muốn bản thân đang, mới có thể có Hạo Nhiên Chính Khí, dạy bảo chúng sinh. Đương nhiên, chúng ta cũng không thể chỉ nghe vị bằng hữu này lời nói của một bên, còn muốn nghe Trương sư giải thích, nếu như... Hắn thật làm làm trái sư đức sự tình, cho dù có Vạn Quốc thành Danh Sư đường bao che, cũng tuyệt không buông tha!”"),t("br"),t("br"),n._v("Vung tay lên, La Hoàng tông chủ nghĩa chính ngôn từ."),t("br"),t("br"),n._v("“Tốt, hiện tại liền đi tìm hắn đối chất nhau!”"),t("br"),t("br"),n._v("Thấy hắn đồng ý, mọi người tất cả đều gật đầu đáp ứng."),t("br"),t("br"),n._v("“Vị bằng hữu này, ngươi cũng cùng một chỗ đi, yên tâm, sự tình không có tra tra ra manh mối trước, ngươi là chúng ta Lưu Vân tông khách nhân tôn quý nhất, không có người sẽ đối với ngươi thế nào!”"),t("br"),t("br"),n._v("Thấy mọi người thái độ, La Hoàng tông chủ hài lòng gật đầu, vung tay lên."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Đinh Hồng mắt sáng lên."),t("br"),t("br"),n._v("“Vị kia Trương sư, liền ở tại minh chủ phủ lân cận, ta lần trước chuyên môn phái người nghe qua, có thể mang các ngươi đi qua!”"),t("br"),t("br"),n._v("Một vị danh sư, hô một tiếng, đi đầu dẫn đường."),t("br"),t("br"),n._v("Làm ra quyết định, mọi người đồng loạt từ Vọng Hải lâu đi ra ngoài, thẳng tắp hướng về phía Trương Huyền ở lại phủ đệ đi tới."),t("br"),t("br"),n._v("Nhìn thấy mọi người khí thế hung hăng rời khỏi, cùng theo một lúc tới La Tuyền, kìm lòng không được lắc đầu."),t("br"),t("br"),n._v("Tất cả mọi người biết Khổng sư bản chép tay tại cái kia Trương Huyền trong tay, đi trước hỏi thăm, còn thế nào khả năng công bằng?"),t("br"),t("br"),n._v("Chỉ sợ mặc kệ hắn có thừa nhận hay không, món bảo vật này, khẳng định là giữ không được."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-568"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-570"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);