(window.webpackJsonp=window.webpackJsonp||[]).push([[1825],{2180:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-565-cac-nguoi-co-toi-2-hop-1-chuong-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-565-cac-nguoi-co-toi-2-hop-1-chuong-】"}},[n._v("#")]),n._v(" Chương 565: Các ngươi có tội! [2 hợp 1 chương 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 565: Các ngươi có tội! [2 hợp 1 chương 】"),t("br"),t("br"),n._v("Hóa Phàm cảnh cường giả, chỉ có đi đến đệ bát trọng đạp Hư Cảnh mới có thể đạp hư mà lên, chân đạp lơ lửng tăng lên, có điều, cũng chỉ có thể trên không trung ngắn ngủi dừng lại, thẳng đứng lên xuống, độ cao còn có giới hạn, không tính là phi hành."),t("br"),t("br"),n._v("Chân chính có thể bay, ít nhất cũng phải đi đến Bán Thánh!"),t("br"),t("br"),n._v("Siêu việt võ giả cửu trọng vì Hóa Phàm."),t("br"),t("br"),n._v("Siêu việt Hóa Phàm cửu trọng vì Bán Thánh."),t("br"),t("br"),n._v("Có thể coi là Bán Thánh, cũng chỉ là lăng không hư độ, không cách nào huy sái tự nhiên!"),t("br"),t("br"),n._v("Trước mắt vị này, đạp không mà đến, đem không khí xem như hữu hình cầu thang, tựa như lơ lửng tại thiên không thần chỉ, để cho người ta nhìn lên một cái, liền từ nội tâm chỗ sâu sinh ra sợ hãi chi ý."),t("br"),t("br"),n._v("Trọng yếu nhất chính là, khí tức trên thân, giống như thủy triều vô cùng vô tận, cho người ta một loại trên linh hồn mãnh liệt uy áp, để cho người ta không tự chủ thần phục, không ngẩng đầu được lên."),t("br"),t("br"),n._v("Đây không riêng gì khí thế bên trên áp bách, còn mang theo trên linh hồn trấn phục."),t("br"),t("br"),n._v("Như là một tòa núi lớn đặt ở trên đầu, căn bản sinh không nổi nửa phần ý niệm phản kháng."),t("br"),t("br"),n._v("Có thể có loại năng lực này, chỉ sợ... Đã đột phá Bán Thánh, đạt đến từ Thánh, thậm chí Thánh giả!"),t("br"),t("br"),n._v("Loại này cường giả, coi như Huyễn Vũ đế quốc, đều không có một vị... Làm sao sẽ xuất hiện ở đây?"),t("br"),t("br"),n._v("Hơn nữa... Xem ra, còn đang thay... Trương Huyền nói chuyện?"),t("br"),t("br"),n._v("La Hoàng tông chủ đám người, trái tim một chút mát như hàn băng, từng cái dọa đến vẻ mặt trắng bệch, lại không vừa rồi khí thế hùng hổ cùng phách lối."),t("br"),t("br"),n._v("Vốn cho rằng, vị này Trương Huyền, chính là cái địa phương nhỏ tới tiểu nhân vật, không nghĩ tới một cái nháy mắt, biến thành Hồng Hoang mãnh thú, có thể tuỳ tiện đem bọn hắn xé rách."),t("br"),t("br"),n._v("“Lão gia...”"),t("br"),t("br"),n._v("Mọi người dọa đến như nhũn ra, Tôn Cường thì hốc mắt đỏ lên."),t("br"),t("br"),n._v("Vị này tản mát ra khí tức cường đại, đạp không mà ra người, không phải lão gia Dương Huyền, thì là ai!"),t("br"),t("br"),n._v("Vốn cho rằng lần này tai kiếp khó thoát, không nghĩ tới lão gia đến rồi!"),t("br"),t("br"),n._v("Hơn nữa còn là bí mật mang theo uy thế vô cùng, phá không mà tới."),t("br"),t("br"),n._v("“Là cái này... Dương sư?”"),t("br"),t("br"),n._v("Một bên trọng thương Tiết Nhất Dao, nhìn thấy không trung trung niên nhân, cũng là chấn động trong lòng, vẻ mặt kích động ửng hồng."),t("br"),t("br"),n._v("Khó trách Tôn Cường tự tin như vậy, nguyên lai vị này Dương sư, cường đại như thế!"),t("br"),t("br"),n._v("Có loại thực lực này, lại là vượt qua lục tinh danh sư, có lẽ thật có thể giải quyết linh hồn của nàng vấn đề, lần nữa khôi phục như lúc ban đầu."),t("br"),t("br"),n._v("“Tô Phàm, Lăng Hằng Vũ gặp qua Dương sư!”"),t("br"),t("br"),n._v("Sân nhỏ một hồi lặng ngắt như tờ, hai bóng người phá phong mà tới, chính là Tô sư, Lăng sư."),t("br"),t("br"),n._v("Bọn hắn mặc dù cùng Khang đường chủ đồng thời lấy được tin tức, đáng tiếc tốc độ so ra kém người sau, lúc này mới chạy tới."),t("br"),t("br"),n._v("Vừa tới địa phương, liền thấy Dương sư đạp không mà tới, tản mát ra vô cùng uy thế cảnh tượng, tất cả đều trong lòng kịch chấn, cung kính ôm quyền, một câu cũng không dám nói."),t("br"),t("br"),n._v("Lúc trước nhìn thấy trong mắt của hắn Minh Lý Chi Nhãn, liền biết là một vị vượt qua lục tinh danh sư, không tầm thường, chỉ là không nghĩ tới, chân chính phát ra thực lực, vậy mà kinh khủng như vậy."),t("br"),t("br"),n._v("Để cho người ta sinh không nổi nửa điểm ý niệm phản kháng."),t("br"),t("br"),n._v("“Tiền bối hiểu lầm, vãn bối không phải ý tứ kia...”"),t("br"),t("br"),n._v("Bình phục tâm cảnh, từ trong lúc khiếp sợ khôi phục lại, La Hoàng tông chủ liền ôm quyền, lời nói mới nói một nửa, liền cảm thấy một cỗ nghiền ép linh hồn khí tức, bay thẳng mà đến, tựa hồ muốn hắn xé nát."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Một ngụm máu tươi phun ra, rút lui bảy, tám bước."),t("br"),t("br"),n._v("Chỉ một chút, là hắn biết, linh hồn đã bị trọng thương."),t("br"),t("br"),n._v("Linh hồn cùng thân thể không giống, người sau dùng đan dược, chân khí ôn nhuận liền có thể khôi phục, mà linh hồn cần thời gian tích lũy tháng ngày mới có thể chậm rãi tu bổ, lần này thương thế nghiêm trọng, đoán chừng không có trăm năm thời gian, không cách nào hoàn hảo như lúc ban đầu!"),t("br"),t("br"),n._v("Mà hắn... Rất rõ ràng, sống thêm không được trăm năm."),t("br"),t("br"),n._v("Chỉ bằng vào linh hồn, liền nghiền ép hắn tại chỗ thổ huyết, cả đời lại không cách nào tiến bộ, chẳng lẽ... Đây chính là Thánh giả thực lực?"),t("br"),t("br"),n._v("Sắc mặt trắng nhợt, La Hoàng tông chủ thân thể run rẩy."),t("br"),t("br"),n._v("Một chiêu đem hắn kích thương, không trung trung niên nhân, ánh mắt lạnh lùng nhìn qua, nhìn quanh một tuần."),t("br"),t("br"),n._v("“Ta Dương Huyền, thâm cư quả xuất, không tranh quyền thế, chưa từng chủ động trêu chọc thị phi, vãn bối một ít chuyện, cũng lười tính toán... Làm sao, dễ tính, coi ta dễ khi dễ? Liền đến tìm ta phiền phức... Ai cho lá gan?”"),t("br"),t("br"),n._v("Thanh âm không vui không buồn, nhưng lộ ra làm cho không người nào có thể cự tuyệt uy áp, dường như chỉ cần dám phản kháng, sẽ bị tại chỗ nghiền nát."),t("br"),t("br"),n._v("“Vãn bối không dám...”"),t("br"),t("br"),n._v("La Hoàng tông chủ đám người dọa đến vội vàng ôm quyền, tất cả đều lông mày nhảy loạn, sắp khóc."),t("br"),t("br"),n._v("Cái này chẳng lẽ... Chính là Trương Huyền lão sư?"),t("br"),t("br"),n._v("Ai nói chỉ là nửa bước ngũ tinh danh sư?"),t("br"),t("br"),n._v("Coi như lục tinh danh sư, cũng không có cường đại như vậy đi..."),t("br"),t("br"),n._v("“Không dám? Ta nhìn các ngươi ngược lại là rất dám...”"),t("br"),t("br"),n._v("Dương Huyền hất lên ống tay áo: “Đều đánh tới cửa chính lên, có phải hay không ta không xuất hiện, trực tiếp đem quản gia của ta, cùng mấy cái này đồ tôn tất cả đều giết?”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Mọi người run rẩy."),t("br"),t("br"),n._v("Nói thật, bọn họ đích xác có ý nghĩ này."),t("br"),t("br"),n._v("Nhưng... Nếu là sớm biết, Trương Huyền lão sư đáng sợ như thế, đánh chết cũng chỉ đến ah."),t("br"),t("br"),n._v("“Vãn bối... Không biết bọn hắn là tiền bối hậu nhân, có nhiều đắc tội...”"),t("br"),t("br"),n._v("La Hoàng tông chủ cắn răng."),t("br"),t("br"),n._v("“Không biết? Không phải mới vừa khí thế hùng hổ, uy thế mười phần ư?” Dương Huyền lông mày vung lên."),t("br"),t("br"),n._v("“Vâng... Vị này Đinh Hồng tìm tới chúng ta, nói... Nhà hắn truyền bảo vật, bị Trương sư cướp đi...” La Hoàng tông chủ vội nói."),t("br"),t("br"),n._v("Lúc này, trong lòng của hắn mắng Đinh Hồng vô số lần."),t("br"),t("br"),n._v("Nếu không phải cái tên này nói cái gì Khổng sư bản chép tay, dẫn tới bọn hắn kích động không thôi, làm gì tìm tới nơi này?"),t("br"),t("br"),n._v("Không tìm Trương sư phiền phức, lại thế nào khả năng gặp lên trước mắt vị này?"),t("br"),t("br"),n._v("“Ngươi nói là Khổng sư bản chép tay đi! Là tại ta chỗ này, làm sao, ngươi muốn?” Dương Huyền nhìn qua."),t("br"),t("br"),n._v("“Vãn bối không dám...”"),t("br"),t("br"),n._v("La Hoàng tông chủ thân thể run rẩy."),t("br"),t("br"),n._v("Khổng sư bản chép tay là đồ tốt, tiền đề cũng muốn có thể chịu được... Cùng cái tên này đoạt? Nói đùa cái gì!"),t("br"),t("br"),n._v("“Khổng sư bản chép tay, là ta đồ tôn Cù Xung đồ vật, vị này Đinh Hồng cháu trai Đinh Mục, vì cướp đoạt, đem hắn đả thương, hôn mê bất tỉnh. Đồ nhi ta Trương Huyền, lúc này mới đem hắn chém giết, tiêu diệt Hiên Viên vương quốc... Vì tuân thủ quy tắc, hắn dựa theo quy củ, xông vào Danh Sư đường, thông qua tất cả khảo hạch.”"),t("br"),t("br"),n._v("Dương Huyền híp mắt lại: “Làm sao... Đến trong miệng ngươi, ngược lại không tuân thủ quy củ?”"),t("br"),t("br"),n._v("Dựa theo tình huống bình thường, danh sư là không thể tùy ý diệt quốc, có điều, Trương Huyền tuân thủ quá trình, xông vào Danh Sư đường, coi như đằng sau tổng bộ điều tra đến, đều không có bất cứ trách nhiệm nào."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("La Hoàng tông chủ câm ở."),t("br"),t("br"),n._v("Hắn dù sao cũng là từ bên ngoài đến thế lực, những chuyện này liền Khang đường chủ đều không rõ ràng, hắn lại như thế nào biết."),t("br"),t("br"),n._v("Bất quá, đối phương thân là Thánh giả, mở miệng giải thích, đã nói lên khẳng định thật sự."),t("br"),t("br"),n._v("“Thân là danh sư, không phân phải trái, không biết tiến thối, vì bảo vật, tùy ý đối cùng nghề nghiệp danh sư ra tay, không để ý nhỏ yếu, không để ý liêm sỉ...”"),t("br"),t("br"),n._v("Hai tay chắp sau lưng, Dương Huyền lạnh lùng nhìn qua: “Ta phán ngươi —— có tội!”"),t("br"),t("br"),n._v("La Hoàng tông chủ toàn thân cứng ngắc."),t("br"),t("br"),n._v("“Vị này Bạch Tông chủ, đối ta quản gia, linh thú ra tay, bỏ qua cấp cao danh sư uy nghiêm —— có tội!”"),t("br"),t("br"),n._v("Dương Huyền tiếp tục nói."),t("br"),t("br"),n._v("Bạch Tông chủ cũng là thân thể nhoáng một cái."),t("br"),t("br"),n._v("Vừa rồi liền hắn ra tay nhiều nhất, Tôn Cường bọn người là hắn đả thương, coi như muốn phản bác, cũng phản bác không được."),t("br"),t("br"),n._v("Đánh chó cũng phải nhìn chủ nhân, cách đó không xa nằm trên đất cái tên mập mạp kia, coi như thực lực lại kém, miệng lại thiếu, cũng là một vị Thánh giả hạ nhân, đánh hắn, chẳng phải tương đương đánh Thánh giả mặt, bỏ qua vị này cấp cao danh sư tôn nghiêm?"),t("br"),t("br"),n._v("“Các ngươi, nghe theo mê hoặc, không phân tích không phán đoán, vì bản thân tư dục, tự kiềm chế thực lực, mạnh mẽ đâm tới... Có tội!”"),t("br"),t("br"),n._v("Nhìn về phía mọi người khác, Dương Huyền lần nữa tuyên án."),t("br"),t("br"),n._v("“Chúng ta...”"),t("br"),t("br"),n._v("Mọi người từng cái bờ môi trắng bệch."),t("br"),t("br"),n._v("Bị một cái hư hư thực thực Thánh giả người, định là có tội, cơ bản liền không có cách nào xoay người."),t("br"),t("br"),n._v("Phán định xong mọi người có tội, Dương Huyền không tại nhiều nói, mà là quay đầu nhìn về phía nằm trên đất Tiết viên chủ."),t("br"),t("br"),n._v("“Ngươi không sai, thời khắc mấu chốt, có thể không vì cường quyền, chỉ vì báo ân... Ta mới vừa nhìn, linh hồn ngươi từng chịu từng tới tổn thương, không cách nào đột phá, bái ta làm thầy, có thể giúp ngươi giải quyết!”"),t("br"),t("br"),n._v("“Có thể... Bái sư?”"),t("br"),t("br"),n._v("Tiết viên chủ hơi đỏ mặt, cái kia còn do dự, trực tiếp xoay người quỳ rạp xuống đất: “Tại hạ Tiết Nhất Dao, nguyện ý bái nhập Dương sư môn hạ, nghe theo phân phó, không dám vi phạm...”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Dương Huyền gật gật đầu, ngón tay duỗi ra, nhẹ nhàng trên không trung một chút."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Tiết viên chủ lập tức cảm thấy một cỗ ý niệm thẳng tắp tràn vào trong đầu, chính là khôi phục linh hồn pháp quyết tu luyện."),t("br"),t("br"),n._v("Những năm này một mực nghiên cứu như thế nào khôi phục, đối linh hồn cũng coi như lý giải cực sâu, mặc dù không bằng cái gọi là vu hồn sư, nhưng cũng so với bình thường Trọc Thanh cảnh đỉnh phong cường đại không ít. Bởi vậy, bộ công pháp này chỉ nhìn một chút, liền biết, không những có thể trị hết linh hồn nàng bên trên ám thương, thậm chí còn có thể tiến thêm một bước, xung kích Hợp Linh cảnh thành công."),t("br"),t("br"),n._v("“Đa tạ Dương sư thành toàn!”"),t("br"),t("br"),n._v("Trong lòng kích động, lần nữa quỳ mọp xuống đất."),t("br"),t("br"),n._v("“Đây chẳng lẽ là... Linh hồn truyền công?”"),t("br"),t("br"),n._v("Nhìn Dương Huyền người trên không trung, thuận tay liền đem công pháp truyền lại, La Hoàng tông chủ đám người càng là dọa đến run rẩy."),t("br"),t("br"),n._v("Linh hồn truyền công, nói rõ đối linh hồn khống chế đạt đến cực kỳ cao thâm cảnh giới, loại trừ trong truyền thuyết vu hồn sư, ít nhất phải đi đến danh sư thất tinh mới có thể làm đến."),t("br"),t("br"),n._v("Trước mắt vị này, có thể tự do bay lượn, trên người khí tức như rồng... Khẳng định không phải vu hồn sư, nói cách khác... Có thể là một vị thất tinh danh sư!"),t("br"),n._v(" Loại cấp bậc này danh sư, coi như tại Thánh giả bên trong, cũng tuyệt đối được cho cường giả!"),t("br"),t("br"),n._v("Vốn đang cho rằng chỉ là lục tinh danh sư, nói một chút mềm lời nói, đối phương có lẽ còn có thể dàn xếp, thất tinh... Đây đã vượt ra khỏi tưởng tượng của bọn hắn."),t("br"),t("br"),n._v("Trương Huyền lão sư... Mạnh như vậy?"),t("br"),t("br"),n._v("“Yêu Thần thú, trung thành hộ chủ, gặp nguy hiểm mà không lui lại, không sai!”"),t("br"),t("br"),n._v("Không để ý tới mọi người khiếp sợ, Dương Huyền quay đầu nhìn về phía một bên Yêu Thần thú, lần nữa gật đầu, duỗi ra ngón tay, nhẹ nhàng bắn ra, một đạo chân khí lập tức tràn vào đối phương trong cơ thể."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Sau một khắc, vây ở Hóa Phàm tứ trọng đỉnh phong Yêu Thần thú, giống như là lấy được cực lớn tiếp tế, đột nhiên phát ra một tiếng chói tai thét dài, không chỉ lực lượng cấp tốc tăng vọt, linh hồn cũng đã nhận được thuế biến."),t("br"),t("br"),n._v("Chỉ chốc lát, lực lượng liền bình ổn xuống, cho người ta một loại lớn mạnh đến cực điểm cảm giác, thậm chí so với La Hoàng tông chủ đám người, đều không hề yếu."),t("br"),t("br"),n._v("Hóa Phàm ngũ trọng... Hợp Linh cảnh!"),t("br"),t("br"),n._v("Lấy được Dương sư một đạo chân khí, vây ở Trọc Thanh cảnh đỉnh phong không biết bao nhiêu năm Yêu Thần thú, vậy mà trong nháy mắt đột phá, xông vào Hợp Linh cảnh thành công!"),t("br"),t("br"),n._v("Cái tên này, trên người chịu Long tộc huyết mạch, lực lượng, phòng ngự vô địch, Hóa Phàm tứ trọng đỉnh phong thời điểm, liền có thể hòa hợp linh cảnh sơ kỳ chiến đấu, không rơi vào thế hạ phong, hiện tại đột phá, coi như Hợp Linh cảnh trung kỳ, hậu kỳ, muốn vượt qua, đều rất khó."),t("br"),t("br"),n._v("“Lấy được một đạo chân khí liền phá cảnh, đây chân khí nên tinh khiết đến mức nào? Chẳng lẽ... Là bát tinh danh sư?”"),t("br"),t("br"),n._v("Vốn là tràn đầy tuyệt vọng La Hoàng tông chủ mắt tối sầm lại, lập tức cảm thấy càng thêm tuyệt vọng."),t("br"),t("br"),n._v("Trong truyền thuyết, đi đến bát tinh danh sư, sẽ bị tổng bộ triệu kiến, có một lần một lần nữa tịnh hóa bản thân chân khí cơ hội, bởi vậy, đi đến loại tu vi này danh sư, chân khí dị thường tinh thuần, đều đạt đến thượng tam phẩm cảnh giới."),t("br"),t("br"),n._v("Loại cấp bậc này chân khí, đưa vào linh thú trong cơ thể, có thể để cho nàng huyết mạch tiến thiên, thành công đột phá."),t("br"),t("br"),n._v("Vị này Dương sư, một đạo chân khí liền để Yêu Thần thú đột phá thành công, hơn nữa còn đi vào Hóa Phàm cảnh cái thứ nhất cửa ải khó... Hợp Linh cảnh, loại năng lực này, tuyệt không phải danh sư thất tinh có thể làm được!"),t("br"),t("br"),n._v("Chỉ sợ, đã đạt đến bát tinh!"),t("br"),t("br"),n._v("Đây chính là phong hào đế quốc đều không từng có nhân vật tuyệt thế, dậm chân một cái, Huyễn Vũ đế quốc liền sẽ sụp đổ siêu cường tồn tại..."),t("br"),t("br"),n._v("Tất cả xông lại muốn cướp đoạt Khổng sư bản chép tay tông chủ, chưởng môn, cũng đều ý thức được điểm ấy, chỉ cảm thấy khuôn mặt run rẩy, tất cả đều khóc."),t("br"),t("br"),n._v("Đây gọi chuyện gì!"),t("br"),t("br"),n._v("Vốn cho rằng cái này Trương Huyền là thái điểu, nhờ vào bọn hắn có thể tùy ý khi dễ, nằm mơ đều không có nghĩ đến, lão sư là cái bát tinh danh sư!"),t("br"),t("br"),n._v("Loại này cấp bậc, đừng nói bọn hắn, coi như ngũ tinh, lục tinh danh sư tới, cũng muốn quỳ gối tại chỗ!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Bên này những người này khiếp sợ muốn chết, phủ đệ cách đó không xa, một cái lão giả, cũng bờ môi run rẩy, dọa đến không dám hướng về phía trước."),t("br"),t("br"),n._v("Hồng sư, Hồng Thiển, Hồng Phong đế quốc Danh Sư đường ngũ tinh danh sư, lần này là được mời tới chủ trì lần này danh sư thi đấu."),t("br"),t("br"),n._v("Tại phủ đệ nghỉ ngơi, biết chuyện bên này, vội vàng chạy tới, dự định khuyên can, nằm mơ đều không có nghĩ đến, nhìn thấy màn này."),t("br"),t("br"),n._v("Bát tinh danh sư... Coi như toàn bộ danh sư đại lục, đều không có mấy vị, mỗi một cái, đều là quát tháo vạn cổ siêu cường tồn tại, chạy thế nào loại này chỗ thật xa tới?"),t("br"),t("br"),n._v("“Nhất định phải nhanh bẩm báo tổng bộ...”"),t("br"),t("br"),n._v("Trong lòng hơi động, một cái lớn chừng bàn tay ngọc phù xuất hiện tại lòng bàn tay, đưa tay đem tin tức viết hoàn tất, ánh sáng lóe lên, chữ viết biến mất."),t("br"),t("br"),n._v("Cấp thấp vương quốc, cần sử dụng cực lớn đưa tin tường, ngũ tinh danh sư đã nắm giữ đưa tin ngọc phù, thông qua cái này, có thể cho tổng bộ đưa tin, dù là cách nhau ngàn vạn cây số, cũng có thể trong nháy mắt đến."),t("br"),t("br"),n._v("Không phải hắn muốn lộ ra tin tức, mà là... Bát tinh danh sư, quá mức kinh thế hãi tục, coi như hắn là ngũ tinh danh sư, cũng không dám giấu diếm."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Hồng Viễn nhất đẳng đế quốc Danh Sư đường."),t("br"),t("br"),n._v("Đường chủ chớ cao xa, lục tinh đỉnh phong danh sư."),t("br"),t("br"),n._v("Hắn lúc này, ngay tại giảng bài, miệng phun hoa sen, khắp bầu trời linh khí hội tụ, đột nhiên nhướng mày, cổ tay khẽ đảo, một cái ngọc bài xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("“Một vị hư hư thực thực vượt qua lục tinh danh sư... Dương Huyền?”"),t("br"),t("br"),n._v("Mạc đường chủ nhướng mày."),t("br"),t("br"),n._v("Hồng Thiển mặc dù cảm thấy vị này Dương Huyền có thể là thất tinh, thậm chí bát tinh, nhưng lấy thực lực của hắn, nhìn không ra, không cách nào bỗng dưng ước đoán, chỉ có thể bẩm báo hư hư thực thực vượt qua lục tinh."),t("br"),t("br"),n._v("Không có viết ra phán đoán, có điều, nhưng đem nhìn thấy toàn bộ viết lên đi."),t("br"),t("br"),n._v("“Ngự không mà đi, linh hồn truyền lại công pháp, một đạo chân khí để linh thú đột phá?”"),t("br"),t("br"),n._v("Đem tin tức nhìn xong, Mạc đường chủ vẻ mặt một hồi âm một hồi tinh."),t("br"),t("br"),n._v("Căn cứ hai thứ này miêu tả, đối phương có thể là bát tinh danh sư, có thể... Cao cấp như vậy cái khác danh sư làm sao sẽ xuất hiện tại, một cái chỉ cùng bất nhập lưu đế quốc xấp xỉ cấp bậc vạn quốc liên minh?"),t("br"),t("br"),n._v("“Mạch Trần, đem danh sư bạc lấy tới!”"),t("br"),t("br"),n._v("Quay đầu nhìn về phía sau lưng một người trung niên."),t("br"),t("br"),n._v("Học sinh của hắn, ngũ tinh đỉnh phong danh sư, Mạch Trần."),t("br"),t("br"),n._v("“Vâng!” Mạch Trần quay người rời khỏi, thời gian không dài, đem một cái ngọc chất thư tịch cầm tới."),t("br"),t("br"),n._v("Danh sư bạc, ghi chép vô số danh sư họ tên, bao gồm toàn bộ đại lục, thuộc về toàn bộ Danh Sư đường cơ mật, chỉ có đường chủ mới có tư cách quan sát. Hơn nữa, bên trong còn sắp đặt đặc thù trận pháp, dù là trí nhớ siêu quần, nhìn qua không lâu, cũng sẽ quên."),t("br"),t("br"),n._v("Đây là vì bảo hộ danh sư bí mật không bị ngoại nhân phát hiện."),t("br"),t("br"),n._v("Thuận tay lật ra, từng hàng nhìn lại, rất mau đem cả quyển sách đều nhìn một lần."),t("br"),t("br"),n._v("“Vượt qua lục tinh danh sư, phía trên cơ bản đều có ghi chép, không có gọi Dương Huyền...”"),t("br"),t("br"),n._v("Lông mày nhăn thành u cục."),t("br"),t("br"),n._v("Nếu quả thật cùng đối phương nói đồng dạng, vị này Dương Huyền, thực lực tuyệt đối so với hắn phải cường đại hơn nhiều, coi như không phải bát tinh, cũng ít nhất là thất tinh... Có thể danh sư bạc bên trong, nhưng không có bất luận cái gì liên quan tới cái tên này ghi chép."),t("br"),t("br"),n._v("“Hoặc là Dương Huyền cái tên này... Căn bản không phải hắn, hoặc là... Chính là hắn không có ở Danh Sư đường khảo hạch qua cấp bậc cao hơn!”"),t("br"),t("br"),n._v("Danh sư bạc, chỉ ghi chép khảo hạch qua danh sư người, nếu như đối phương dùng giả danh, tự nhiên tra không được, còn có một loại khả năng chính là... Căn bản không có ở Danh Sư đường khảo hạch qua."),t("br"),t("br"),n._v("Không ít danh sư, ưa thích một mình tu luyện, rõ ràng đạt đến cấp bậc rất cao, lại không tiến hành qua khảo hạch, có đôi khi, trên người huy chương là ngũ tinh, lục tinh, mà thực lực bản thân, chỉ sợ sớm đã đạt đến thất tinh, bát tinh."),t("br"),t("br"),n._v("Có lẽ vị này Dương Huyền, chính là dạng này cường giả."),t("br"),t("br"),n._v("“Mặc kệ loại nào tình huống, đều cần đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Lông mày giương lên, Mạc đường chủ đứng dậy."),t("br"),t("br"),n._v("Mặc kệ đối phương là loại nào, cao minh như thế thực lực, đều đáng giá hắn tự mình đi qua bái kiến."),t("br"),t("br"),n._v("“Mạch Trần, đi đem Lang Gia Thánh Thú mang tới, ta phải đi xa nhà một chuyến!”"),t("br"),t("br"),n._v("Hất lên ống tay áo, đem danh sư bạc cất kỹ, quay đầu phân phó."),t("br"),t("br"),n._v("Lang Gia Thánh Thú là hắn thú sủng, tốc độ như điện, một ngày có thể phi hành mấy trăm vạn cây số!"),t("br"),t("br"),n._v("Vạn quốc liên minh, cách bọn họ thực sự quá xa, coi như hắn có thể phi hành, cũng không có khả năng so Thánh Thú bay nhanh."),t("br"),t("br"),n._v("“Lão sư muốn đi đâu? Có muốn hay không ta đi cùng?”"),t("br"),t("br"),n._v("Rất mau đem Thánh Thú mang tới, Mạch Trần danh sư nhịn không được nhìn qua."),t("br"),t("br"),n._v("“Ta muốn đi vạn quốc liên minh, đã ngươi muốn đuổi theo, liền cùng nhau tới đây đi!”"),t("br"),t("br"),n._v("Chần chờ một chút, Mạc đường chủ gật gật đầu."),t("br"),t("br"),n._v("Người học sinh này, khoảng cách lục tinh danh sư, chỉ thiếu một chút, đã muốn qua, liền cùng một chỗ mang theo, dọc đường cũng có thể chỉ điểm một hai, có lẽ liền có thể đột phá ràng buộc đi đến lục tinh cấp bậc."),t("br"),t("br"),n._v("Hai người đều có không gian giới chỉ, cũng không có gì cần chuẩn bị, nhảy lên Thánh Thú sống lưng, quyết định phương hướng, bay thẳng ra ngoài, hướng về phía vạn quốc liên minh phương hướng, chạy thẳng tới."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Hồng Viễn đế quốc, lục tinh đỉnh phong danh sư tự mình đến đây, bên này, La Hoàng tông chủ biết lại khó may mắn thoát khỏi, bờ môi run rẩy, cắn răng nhìn lại."),t("br"),t("br"),n._v("“Tiền bối, chúng ta nhận tội, còn xin xử phạt...”"),t("br"),t("br"),n._v("Đối phương đã tuyên án có tội, vậy liền nhận tội."),t("br"),t("br"),n._v("Thấy thế nào trừng phạt lại nói."),t("br"),t("br"),n._v("“Nhận tội là tốt rồi, ở đây mỗi người, giao ra 20 viên linh thạch trung phẩm, liền có thể rời khỏi. La tông chủ, Bạch Tông chủ, nhục ta quản gia, làm tổn thương ta hậu bối... Ta cũng lười ra tay, các ngươi... Tự sát đi!”"),t("br"),t("br"),n._v("Nhàn nhạt nhìn qua, Dương Huyền thanh âm không hề bận tâm."),t("br"),t("br"),n._v("“Tự sát?”"),t("br"),t("br"),n._v("La Hoàng tông chủ, Bạch Khải Chi tông chủ tất cả đều lông mày nhảy một cái."),t("br"),t("br"),n._v("Vốn cho rằng cái gọi là trừng phạt, chỉ là giáo huấn một lần, thậm chí bản thân bị trọng thương... Đắc tội cao minh như thế danh sư, cũng coi như trừng phạt đúng tội, không dám phản bác."),t("br"),t("br"),n._v("Có thể... Một khi tự sát, người chết đèn tắt, cái gì cũng bị mất."),t("br"),t("br"),n._v("“Muốn cho ta tự sát? Nằm mơ! Ta Bạch Khải Chi, coi như thực lực không đủ, cũng sẽ không làm ra chuyện tự sát, ngươi đã băn khoăn thân phận, không muốn tự mình ra tay, vậy liền thật không tiện... Cáo từ!”"),t("br"),t("br"),n._v("Hét dài một tiếng, Bạch Khải Chi tông chủ đột nhiên đứng dậy, thân thể nhảy lên, thẳng tắp phóng ra ngoài."),t("br"),t("br"),n._v("Ý nghĩ của hắn rất đơn giản."),t("br"),t("br"),n._v("Ngươi là cao cấp danh sư, tự lo thân phận không có khả năng tự mình đối với chúng ta những người này ra tay... Đã như vậy, đánh không lại, ta cuối cùng trốn được đi!"),t("br"),t("br"),n._v("Chỉ cần ngươi không xuất thủ, rời đi nơi này, biển rộng mặc cá bơi, trời cao mặc chim bay, chẳng lẽ còn có thể tìm được ta?"),t("br"),t("br"),n._v("Trong lòng cười lạnh, Bạch Khải Chi dưới chân nhanh chóng."),t("br"),t("br"),n._v("Hắn dùng Bạch Dương tông lợi hại nhất thân pháp, lại phối hợp Hóa Phàm ngũ trọng Hợp Linh cảnh hậu kỳ thực lực, toàn lực vận chuyển, cả người như là một đạo huyễn ảnh, một cái nháy mắt, liền thoát ra phủ đệ."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("La Hoàng tông chủ đám người, hiển nhiên không nghĩ tới vị này Bạch Tông chủ, không nhận tội, ngược lại quay người chạy trốn, tất cả đều ngẩn ngơ."),t("br"),t("br"),n._v("Từ bát tinh danh sư trước mặt chạy trốn... Lá gan đủ mập ah!"),t("br"),t("br"),n._v("Bất quá, cái này cũng quả thực là biện pháp tốt nhất, nếu như không trốn, liền sẽ chết ở chỗ này, cùng nàng tử vong, còn không bằng chạy trốn!"),t("br"),t("br"),n._v("Đối phương thân là bát tinh danh sư, cũng không thể thật không để ý đến thân phận, liền trực tiếp ra tay đi!"),t("br"),t("br"),n._v("Trong lòng đang đối vị này Bạch Tông chủ quyết đoán cảm thấy bội phục, liền thấy thấy hoa mắt."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Một cỗ mênh mông vô cùng lực lượng, tự không mà hàng, ngay tại chạy trốn Bạch Tông chủ còn không có kịp phản ứng, liền bị tại chỗ đập thành bánh thịt, nơi đó xuất hiện một cái đường kính hơn mười mét hố to."),t("br"),t("br"),n._v("Ngay sau đó, một cái thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("“Ta chỉ nói chẳng muốn ra tay... Ai nói tự mình thân phận, sẽ không xuất thủ?”"),t("br"),t("br"),n._v("Nói * đến *"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-573"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-575"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);