(window.webpackJsonp=window.webpackJsonp||[]).push([[1828],{2182:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-568-co-đong-phan-than"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-568-co-đong-phan-than"}},[n._v("#")]),n._v(" Chương 568: Cô đọng phân thân")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 570: Cô đọng phân thân"),t("br"),t("br"),n._v("Bất quá, trả giá nhiều hơn nữa đều đáng giá."),t("br"),t("br"),n._v("Linh hồn một phân thành hai, để cảm giác khác đến có hai cái bản thân, đồng thời sống sót, mặc dù hiện nay sức chiến đấu không biết gia tăng, chỉ khi nào triệt để khôi phục, liền sẽ hoàn toàn khác biệt, tương đương với trong nháy mắt bạo tăng gấp đôi."),t("br"),t("br"),n._v("Mấu chốt nhất là, hai người tư duy tương đồng, ý niệm câu thông, phối hợp ra tay, tuyệt không phải một cộng một bằng hai đơn giản như vậy, thậm chí có thể đi đến ba, bốn, thậm chí nhiều hơn."),t("br"),t("br"),n._v("Hơn nữa, chia ra tới linh hồn, có thể cùng người bình thường đồng dạng tu luyện, nương theo thời gian trôi qua, sẽ trở nên càng ngày càng mạnh."),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Linh hồn nhìn bản tôn một chút, cười nhạt một tiếng, thẳng tắp hướng về phía Cửu Thiên Liên Thai vọt tới."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Vừa đến trước mặt, lập tức cảm thấy một cỗ cực lớn hấp lực đem hắn quét sạch, sau một khắc, liền tiến vào một cái bóng tối không gian."),t("br"),t("br"),n._v("“Chủ nhân...”"),t("br"),t("br"),n._v("Đứng vững thân thể, liền thấy một cái toàn thân hắc khí trung niên nhân, đứng tại cách đó không xa, ôm quyền khom người, vẻ mặt cung kính."),t("br"),t("br"),n._v("Mặc Hồn Sinh!"),t("br"),t("br"),n._v("Vốn cho rằng cái tên này thân là lục tinh vu hồn sư, lại còn sống nhiều năm như vậy, lại là lão giả bộ dáng, không nghĩ tới mặt trắng không râu, thân thể hơi có vẻ mềm yếu, giống như là cái thư sinh."),t("br"),t("br"),n._v("Ký kết linh hồn khế ước, hắn coi như thoát khốn, cũng là vị này Trương sư hạ nhân, đối mệnh lệnh không dám có chút vi phạm, nếu không, đối phương một cái ý niệm trong đầu, liền có thể nhẹ nhõm đem hắn nghiền ép."),t("br"),t("br"),n._v("“Đây là ngươi vốn là dung mạo?” Phân thân nghi ngờ nhìn qua."),t("br"),t("br"),n._v("Linh hồn vô thường thái, có thể tùy ý biến hóa, trước mắt bộ dáng cũng có thể là cố ý biến hóa."),t("br"),t("br"),n._v("“Vâng, vu hồn sư linh hồn, có thể duy trì dẫn hồn xuất thể lúc bộ dạng, ta lúc ấy chính là bộ dáng này, coi như về sau sống thêm vạn năm, cũng sẽ không thay đổi!” Mặc Hồn Sinh vội nói."),t("br"),t("br"),n._v("Linh hồn có thể thiên biến vạn hóa, thậm chí biến đổi hình dạng, nhưng sẽ từ đầu tới cuối duy trì dẫn hồn xuất thể lúc dung mạo, vạn năm không thay đổi."),t("br"),t("br"),n._v("“Ừm, ta hiện tại đi vào thay ngươi, ngươi có thể rời đi!”"),t("br"),t("br"),n._v("Phân thân khoát khoát tay: “Bất quá, tạm thời tìm không thấy thích hợp ngươi thân thể, trước tiên có thể đợi tại Vô Hồn Kim Nhân trên người, một khi có thân thể thích hợp, sẽ lập tức để ngươi đoạt xá!”"),t("br"),t("br"),n._v("Coi như Mặc Hồn Sinh thực lực mạnh mẽ, có thể tại ngoại giới đợi một thời gian ngắn, nhưng nương theo thời gian trôi qua, đồng dạng sẽ phải gánh chịu hồn lực ngũ suy, thực lực càng ngày càng yếu."),t("br"),t("br"),n._v("Vì phòng ngừa loại hiện tượng này, liền muốn tìm một cái thân thể thích hợp."),t("br"),t("br"),n._v("Trương Huyền thân là danh sư, không thể nào để cho hắn bốn phía đoạt xá, chỉ có thể trước oan ức đi vào Vô Hồn Kim Nhân trong cơ thể, tạm thời bảo vệ tính mạng."),t("br"),t("br"),n._v("Dù sao thứ này là vu hồn sư luyện chế ra đến, dùng để phụ thể, tiến vào bên trong, hết sức dễ dàng, hơn nữa không liên quan đoạt xá suy."),t("br"),t("br"),n._v("“Vâng, chỉ cần có thể ra ngoài, sống ở đâu đều được...”"),t("br"),t("br"),n._v("Liền vội vàng gật đầu, Mặc Hồn Sinh hưng phấn mà con mắt tỏa ánh sáng."),t("br"),t("br"),n._v("Ở đây vây lại vài vạn năm, sớm đã có chút nổi điên, đừng nói đi vào Vô Hồn Kim Nhân trong cơ thể, chỉ cần có thể ra ngoài, sống ít đi mấy trăm năm đều được."),t("br"),t("br"),n._v("Lại nói, Trương sư đã đáp ứng, sẽ giúp hắn tìm thân thể đoạt xá, cũng không có gì có thể xoắn xuýt."),t("br"),t("br"),n._v("“Ta đi ra ngoài trước!”"),t("br"),t("br"),n._v("Một cắn răng, thân thể phát ra đặc thù ánh sáng, đột nhiên xông ra ngoài ra ngoài."),t("br"),t("br"),n._v("Cảm nhận được hắn muốn rời khỏi, bóng tối không gian dường như có một cỗ cực lớn lực lượng, lan tràn tới, như muốn lưu lại, phân thân hừ nhẹ một tiếng, ngăn tại phía trước."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Lực lượng đình chỉ, Mặc Hồn Sinh mượn cơ hội nhảy lên, từ cửu thiên liên thai bay ra, rơi vào bên ngoài."),t("br"),t("br"),n._v("“Đi ra!” Phía ngoài Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Vâng!” Cảm nhận được trùng hoạch tự do, Mặc Hồn Sinh nhìn quanh hai bên, tràn đầy kích động."),t("br"),t("br"),n._v("“Đi vào trước đi!”"),t("br"),t("br"),n._v("Mỉm cười, Trương Huyền cổ tay rung lên, một cái chứa đựng Vô Hồn Kim Nhân quan tài, xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Mặc Hồn Sinh nhẹ gật đầu, nhẹ nhàng nhoáng một cái, mở ra quan tài, chui vào, sau một khắc, kim nhân mắt lườm một cái, đứng lên."),t("br"),t("br"),n._v("Hồn phách nhập thể."),t("br"),t("br"),n._v("“Chủ nhân!”"),t("br"),t("br"),n._v("Thân hình cao lớn kim nhân, như là một cái tràn đầy giáp trụ tướng lĩnh, đi tới trước mặt."),t("br"),t("br"),n._v("“Giống như những người khác, bảo ta thiếu chủ đi!” Trương Huyền khoát khoát tay."),t("br"),t("br"),n._v("“Vâng, thiếu chủ!” Kim nhân ôm quyền."),t("br"),t("br"),n._v("“Ngươi bây giờ thực lực, có thể phát huy bao nhiêu?” Nhìn trước mắt kim nhân, Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("Kim nhân thực lực có hạn, nhưng Mặc Hồn Sinh tu vi mạnh mẽ, hiện tại thoát ly Cửu Thiên Liên Thai, chỉ sợ so với hắn, đều cường đại hơn không ít."),t("br"),t("br"),n._v("“Những này Vô Hồn Kim Nhân là ta lúc đầu tự tay luyện chế, xấp xỉ có thể phát huy trăm phần trăm thực lực!” Mặc Hồn Sinh nói."),t("br"),t("br"),n._v("“Trăm phần trăm? Hóa Phàm cửu trọng?” Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Cái tên này là lục tinh vu hồn sư, khi còn sống thực lực đi đến Hóa Phàm cửu trọng đỉnh phong, cụ thể đột phá Bán Thánh cũng chỉ kém một chút, mặc dù những năm này tiêu hao không ít, có thể hóa phàm cửu trọng còn đuổi theo chắc chắn có."),t("br"),t("br"),n._v("Chẳng phải cho thấy, về sau bên người có một cái Hóa Phàm cửu trọng hạ nhân?"),t("br"),t("br"),n._v("Loại này cường giả ở bên người, coi như không có “Dương sư” uy hiếp, cũng khẳng định không ai dám tới làm loạn!"),t("br"),t("br"),n._v("“Ừm!” Mặc Hồn Sinh gật đầu."),t("br"),t("br"),n._v("“Không sai, đi bên ngoài trông coi, không nên để cho bất luận kẻ nào đi vào, ta cô đọng phân thân, không thể quấy nhiễu!” Dặn dò một tiếng."),t("br"),t("br"),n._v("Mặc Hồn Sinh trả lời một câu, quay người đi ra ngoài."),t("br"),t("br"),n._v("Trương Huyền thì sắc mặt ngưng trọng nhìn về phía trước mắt liên thai."),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Ngón tay búng một cái, một đạo chân khí thẳng tắp tuôn đi vào."),t("br"),t("br"),n._v("Mặc Hồn Sinh đối thiên đạo chân khí hết sức e ngại, linh hồn phân thân nhưng không quan tâm, nương theo chân khí tràn vào càng ngày càng nhiều, liên thai nguyên bản màu sắc đen nhánh, cũng càng ngày càng trắng."),t("br"),t("br"),n._v("Dần dần hoàn thiện."),t("br"),t("br"),n._v("Liên thai đối chân khí tiêu hao mười phần cực lớn, có điều, Trương Huyền có đầy đủ linh thạch trung phẩm, không quan tâm hao tổn."),t("br"),n._v("Không biết qua bao lâu, nhẹ nhàng chấn động, toàn bộ liên thai biến thành trắng sữa vẻ, đom đóm, tản mát ra ôn nhuận ánh sáng."),t("br"),t("br"),n._v("“Không hổ là... Thần vật!”"),t("br"),t("br"),n._v("Nhìn thoáng qua, Trương Huyền đã cảm thấy chấn động toàn thân, phảng phất cả người đều bị hấp dẫn."),t("br"),t("br"),n._v("Trước đó liên thai, đen như mực, quỷ dị không gì sánh được, cùng tà vật có chút giống, mà bây giờ, cho người ta một loại từ cửu thiên mà đến, không thể xâm phạm cảm giác."),t("br"),t("br"),n._v("Quả nhiên bất phàm!"),t("br"),t("br"),n._v("Có thể đoán được, một khi cho ngoại nhân biết, khẳng định sẽ khiến cướp đoạt, dù là lục tinh, thất tinh, thậm chí bát tinh danh sư, chỉ sợ cũng không thể ngoại lệ."),t("br"),t("br"),n._v("Danh sư cũng là người, cũng có thất tình lục dục, gặp được bảo vật, cũng sẽ chiếm thành của mình, không có khả năng giống như Thánh Nhân, vô dục vô cầu."),t("br"),t("br"),n._v("Lại nói, Khổng sư còn bao che khuyết điểm, từng vì đệ tử, giận tím mặt, giết chết một nước, huống chi những người khác."),t("br"),t("br"),n._v("“Luyện hóa!”"),t("br"),t("br"),n._v("Biết thứ này, vô cùng trân quý, không thể rò rỉ, Trương Huyền cũng không có mảy may do dự, bàn tay một trảo, một đoàn chân khí, tràn vào trong đó."),t("br"),t("br"),n._v("Luyện chế thành thân thể phương pháp, Mặc Hồn Sinh đã sớm truyền lại cho hắn, cũng không phức tạp."),t("br"),t("br"),n._v("Chân khí đem liên thai bao vây, để nó hoàn mỹ phù hợp linh hồn, trước đó không lớn hoa sen, dần dần cùng tách ra linh hồn dung hợp, tạo thành một cái sạch sẽ không tỳ vết chút nào thân thể."),t("br"),t("br"),n._v("Rèn luyện không biết bao lâu."),t("br"),t("br"),n._v("Linh nhục hoàn mỹ dung hợp, chậm rãi mở mắt."),t("br"),t("br"),n._v("“Xong rồi!”"),t("br"),t("br"),n._v("Thấy phân thân đứng dậy, Trương Huyền mỉm cười."),t("br"),t("br"),n._v("Hai bên đối mặt, Trương Huyền lập tức cảm thấy một loại phi thường kỳ diệu cảm giác, như là soi gương, khi thì cảm thấy đối phương là bản thân, khi thì lại cảm thấy bản thân là đối phương."),t("br"),t("br"),n._v("Phân thân, là bản tôn linh hồn một phân thành hai, bất kể tư duy vẫn là ý tưởng, đều cùng bản tôn giống nhau như đúc, không phân khác biệt, thậm chí hai người học tập tri thức, tu luyện võ kỹ, đều có thể cùng hưởng."),t("br"),t("br"),n._v("Nói cách khác, chỉ cần phân thân đọc sách, bản tôn có thể tuỳ tiện thu hoạch được tri thức, đương nhiên, cũng chỉ giới hạn trong những này, phân thân hấp thu linh khí, tu vi tăng lên, thì cùng bản tôn không đồng dạng."),t("br"),t("br"),n._v("“Hiện tại phân thân, là nguyên thủy phôi thai, cho dù có tu vi cũng là hồn lực tự mang, trước đem thực lực tăng lên lại nói!”"),t("br"),t("br"),n._v("Phân thân từ Cửu Thiên Liên Thai luyện hóa mà thành, bản thân là không có thực lực, hiện tại cho dù có lực lượng, cũng là hòa tan vào thân thể Vu hồn mang tới, nhất định phải nhanh tăng lên tu vi chân khí."),t("br"),t("br"),n._v("Mặc xong quần áo, khoanh chân ngồi tại Tụ Linh trận ở giữa."),t("br"),t("br"),n._v("Kẽo kẹt! Kẽo kẹt!"),t("br"),t("br"),n._v("Tinh thần khẽ động, Thiên Đạo công pháp lập tức dựa theo trong đầu phương thức tiến hành vận chuyển."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Một lát sau, nhíu mày."),t("br"),t("br"),n._v("Thiên Đạo công pháp, hắn đã tu luyện qua một lần, xe nhẹ đường quen, dựa theo đạo lý, vận chuyển lại vô cùng đơn giản, vì cái gì tại trên phân thân nhưng thủy chung làm không được?"),t("br"),t("br"),n._v("Một khi vận chuyển, thật giống như có một cỗ cực lớn cảm giác áp bách đánh tới, tựa hồ muốn thân thể của hắn nghiền thành bụi phấn."),t("br"),t("br"),n._v("“Chẳng lẽ... Loại trừ ta bản tôn, Thiên Đạo công pháp bất luận kẻ nào cũng không thể tu luyện, phân thân cũng không được?”"),t("br"),t("br"),n._v("Một cái ý nghĩ xông ra."),t("br"),t("br"),n._v("Trước đó, hắn đã từng muốn đem Thiên Đạo công pháp hoàn chỉnh truyền thụ cho mấy cái học sinh, đáng tiếc, đều không tu luyện được, không có cách nào đành phải sửa đổi thoáng cái, hoặc nhiều hoặc ít lưu lại chút thiếu hụt."),t("br"),t("br"),n._v("Vốn cho rằng chỉ có những người khác như thế, hiện tại xem ra, ngay cả phân thân của hắn đều cũng giống như thế."),t("br"),t("br"),n._v("“Chỉ sợ cùng Thiên Đạo thư viện có quan hệ!”"),t("br"),t("br"),n._v("Trong lòng hơi động."),t("br"),t("br"),n._v("Thiên Đạo thư viện nương theo hắn trùng sinh, xuất hiện tại bản tôn trong cơ thể, có lẽ có thứ này tồn tại, hắn mới có thể tu luyện. Mà phân thân, dù là có linh hồn của hắn, không có thư viện tồn tại, cũng không có khả năng không chút kiêng kỵ tu luyện Thiên Đạo công pháp."),t("br"),t("br"),n._v("Thiên Đạo không thể dòm ngó, đại đạo không thể không thiếu sót, có Thiên Đạo thư viện, che đậy thiên cơ, sẽ không bị phát hiện, tu luyện thế nào đều được, không có thứ này, liền không làm được."),t("br"),t("br"),n._v("“Đã không được, vậy liền sửa chữa thoáng cái!”"),t("br"),t("br"),n._v("Thiên Đạo công pháp không thể tu luyện, vậy liền chừa lại một chút thiếu hụt, dù sao Triệu Nhã mấy người cũng là như thế này tu luyện, sớm đã xe nhẹ đường quen."),t("br"),t("br"),n._v("Cô cô cô cục cục!"),t("br"),t("br"),n._v("Dựa theo mang theo thiếu hụt công pháp tu luyện, chân khí quả nhiên thuận lợi vận chuyển, khí tức cả người, dần dần gia tăng, tu vi không ngừng tăng lên."),t("br"),t("br"),n._v("Không hổ là thần vật cô đọng thân thể, Triệu Nhã đám người tốc độ tu luyện, tới so sánh, không đáng kể chút nào."),t("br"),t("br"),n._v("Ngắn ngủi ba ngày thời gian, liền từ võ giả nhất trọng Tụ Tức cảnh, tu luyện đến Hóa Phàm tam trọng Âm Dương cảnh đỉnh phong!"),t("br"),t("br"),n._v("Cùng hắn bản tôn thực lực giống nhau như đúc."),t("br"),t("br"),n._v("Mặc dù là bởi vì linh thạch đầy đủ nguyên nhân, có thể loại tốc độ này, cũng quả thực kinh thế hãi tục, có thể đem vô số người hù chết."),t("br"),t("br"),n._v("“Tu vi đi đến, võ kỹ không cần tu luyện, trực tiếp linh hồn cùng hưởng là được!”"),t("br"),t("br"),n._v("Công pháp cần tu luyện, võ kỹ thì không cần, chỉ cần linh hồn cùng hưởng liền có thể làm đến, nói cách khác, cái này phân thân, coi như không cần tu luyện võ kỹ, Thiên Đạo kiếm pháp, quyền pháp, thân pháp... Cũng sẽ cùng bản tôn đồng dạng, không sai chút nào."),t("br"),t("br"),n._v("“Nhìn xem thực lực như thế nào... Đến, hướng về phía ta đánh một quyền!”"),t("br"),t("br"),n._v("Mỉm cười, Trương Huyền nhìn về phía phân thân."),t("br"),t("br"),n._v("Trong phòng không có đá đo lực trụ, không có cách nào xác định tu vi, có điều, hắn đối bản tôn cao bao nhiêu lực lượng, vẫn là hết sức rõ ràng, cả hai đối đầu một quyền, cần phải rất nhẹ nhàng liền có thể xác nhận thực lực."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Phân thân một quyền vọt tới, Trương Huyền nhẹ nhàng cười một tiếng, cũng một quyền nghênh đón tiếp lấy."),t("br"),t("br"),n._v("Hai quyền giao kích."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Còn không có kịp phản ứng tại, Trương Huyền đã cảm thấy cánh tay tê rần, giống như là bị cự sơn đánh trúng, đầu liên tục phá vỡ mấy tầng bức tường bay ra ngoài, một đầu cắm ở trong viện phủ đầy nham thạch trên mặt đất."),t("br"),t("br"),n._v("“Bọn ta... Đây tình huống gì...”"),t("br"),t("br"),n._v("Trương Huyền khóc."),t("br"),t("br"),n._v("(Canh [3], làm gốc thư thứ 34 vị minh chủ  tăng thêm!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-576"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-578"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);