(window.webpackJsonp=window.webpackJsonp||[]).push([[1829],{2185:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-569-mac-cao-vien-cau-kien"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-569-mac-cao-vien-cau-kien"}},[n._v("#")]),n._v(" Chương 569: Mạc Cao Viễn cầu kiến")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 569: Mạc Cao Viễn cầu kiến"),t("br"),t("br"),n._v("“Thực sự thật không tiện, nhà chúng ta thiếu gia vẫn như cũ đang bế quan, không tiện tiếp kiến!”"),t("br"),t("br"),n._v("Phủ đệ trong sân, quản gia Tôn Cường, tràn đầy khiểm nhiên nhìn về phía trước mắt hai người."),t("br"),t("br"),n._v("Trước mắt vị này Hồ trưởng lão cùng Tống Siêu danh sư, biết Trương sư sau khi trở về, hầu như mỗi ngày đều tới, đuổi đều đuổi không đi, coi như hắn đều có chút bất đắc dĩ."),t("br"),t("br"),n._v("“Ta biết, bất quá... Ngày mai thi đấu lại bắt đầu, ta nghĩ lấy, hôm nay Trương sư làm sao cũng muốn xuất quan đi...” Hồ trưởng lão vẻ mặt lúng túng cười nói."),t("br"),t("br"),n._v("Chuyện đêm hôm đó, mặc dù mười bốn thế lực lớn đem tin tức đè ép xuống, có thể làm vì tham gia thi đấu thế lực khác, vẫn là biết một chút."),t("br"),t("br"),n._v("Có thể để cho nhiều như vậy siêu cường thế lực rút đi, sau đó một câu không dám nói, vị này Trương sư bối cảnh, tất nhiên lớn như trời."),t("br"),t("br"),n._v("Bởi vậy, vốn là dự định mua sắm Vô Hồn Kim Nhân cử động, biến thành thường ngày, mục đích rất đơn giản, cùng cái này Trương sư giữ gìn mối quan hệ lại nói."),t("br"),t("br"),n._v("“Thiếu gia lúc nào xuất quan, ta cũng không biết...” Tôn Cường lắc đầu."),t("br"),t("br"),n._v("Từ khi thiếu gia trở về, liền lại không có lộ mặt qua, đây đều qua tiếp cận nửa tháng."),t("br"),t("br"),n._v("Hơn nữa, lão gia liền xuất hiện một lần kia, cũng lại không có xuất hiện qua, ngay cả hắn, đều không xác thực nhận hai người kia còn ở đó hay không phủ đệ."),t("br"),t("br"),n._v("“Cái kia... Trương sư vừa xuất quan, Tôn quản gia có thể hay không lập tức phái người cho ta biết, ngươi cũng đã nhìn ra, ta là thật tâm thực lòng, muốn mua Vô Hồn Kim Nhân!” Hồ trưởng lão nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Lông mày nhíu một cái, Tôn Cường khoát tay áo, lộ ra cấp cao danh sư quản gia khí chất: “Cái này không khỏi ta làm chủ, còn muốn xin chỉ thị thiếu gia mới được, nếu như hắn không muốn gặp ngươi, ta cũng không dám tự tiện làm chuyên!”"),t("br"),t("br"),n._v("Từ khi ngày đó về sau, hắn cái này quản gia địa vị đường thẳng tăng lên, coi như Triệu minh chủ, Khang đường chủ muốn gặp, đều cần tự mình tới, dần dà, trên người cũng mang theo một loại phong độ."),t("br"),t("br"),n._v("“Là, là, Trương sư loại thiên tài này, không phải là cái gì người gặp lại liền có thể thấy...” Hồ trưởng lão theo lời nói nói."),t("br"),t("br"),n._v("“Cái kia là tự nhiên, thiếu gia thiên phú vô song, là nhất đẳng thân phận, coi như Lưu Vân tông La Hoàng tông chủ, mấy ngày nay tới, cũng đều bị ta đuổi ra ngoài!” Tôn Cường vẻ mặt kiêu ngạo."),t("br"),t("br"),n._v("Mấy ngày nay La Hoàng tông chủ cũng tới bái phỏng qua, chỉ bất quá, còn chưa tới tới cửa liền bị hắn cầm cây gậy đuổi ra ngoài."),t("br"),t("br"),n._v("Nói đùa, lúc trước ra tay với ngươi thời điểm, thế nào không nghĩ tới có hôm nay?"),t("br"),t("br"),n._v("Biết lão gia lợi hại, lại nghĩ đến nói xin lỗi, nào có chuyện tốt như vậy."),t("br"),t("br"),n._v("“Đúng vậy a, Trương sư khí chất cao nhã, tư thái hiên ngang, muốn gặp nào có dễ dàng như vậy, ta chỉ hy vọng Tôn quản gia có thể đủ nhiều nói tốt vài câu...” Hồ trưởng lão khóe miệng giật một cái, vội vàng mở miệng."),t("br"),t("br"),n._v("Nói còn chưa dứt lời, liền nghe đến một hồi kịch liệt vang lên ầm ầm, ngay sau đó nhìn thấy một bóng người một đầu đâm vào trước mặt trên mặt đất, không ngừng run rẩy."),t("br"),t("br"),n._v("“Lớn mật, Trương sư phủ đệ, cũng nên tới làm loạn!”"),t("br"),t("br"),n._v("Thấy cái tên này lại dám ở đây ra tay, liền bức tường đều phá hủy, hét lớn một tiếng, Hồ trưởng lão đang định tiến lên biểu hiện một chút, chỉ thấy Tôn quản gia xông tới: “Thiếu gia, ngươi thế nào?”"),t("br"),t("br"),n._v("“Thiếu gia? Trương sư?”"),t("br"),t("br"),n._v("Mắt tối sầm lại, thân thể nhoáng một cái, Hồ trưởng lão chỉ cảm thấy một ngụm máu tươi ngậm trong miệng, bất cứ lúc nào cũng sẽ phun ra."),t("br"),t("br"),n._v("Cái này... Một đầu đâm vào trên mặt đất, toàn thân dơ dáy bẩn thỉu không chịu nổi, cùng ăn mày đồng dạng gia hỏa, chính là Trương sư?"),t("br"),t("br"),n._v("Còn khí chất cao nhã, tư thái hiên ngang..."),t("br"),t("br"),n._v("Đây chó gặm bùn giống như rơi xuống đất, phong tao run rẩy, đầu cắm vào mặt đất phong tình... Cùng cao nhã có nửa xu quan hệ?"),t("br"),t("br"),n._v("Mới vừa phối hợp Tôn quản gia, đem vị danh sư này thổi phồng đến mức cùng như hoa, kết quả, liền đến cái dạng này ra sân phương thức..."),t("br"),t("br"),n._v("Hồ trưởng lão đều cảm thấy mình sắp khóc."),t("br"),t("br"),n._v("Không riêng gì hắn, ngay cả một bên Tống Siêu, cũng khóe miệng co giật."),t("br"),t("br"),n._v("Tiếp xúc qua nhiều lần, hắn có thể một chút nhận ra, kẻ trước mắt này, chính là Trương Huyền Trương sư, trước kia nhìn thấy, đều là nghĩa chính ngôn từ, cho người ta một loại tràn đầy uy nghiêm cảm giác, mà bây giờ..."),t("br"),t("br"),n._v("Cái này cũng... Quả thực quá phá vỡ hình tượng."),t("br"),t("br"),n._v("“Khụ khụ khụ!”"),t("br"),t("br"),n._v("Từ nơi đó rút ra đầu, Trương Huyền tràn đầy buồn bực."),t("br"),t("br"),n._v("Đồng dạng là Hóa Phàm tam trọng đỉnh phong, hắn tu luyện Thiên Đạo công pháp, đối phương vẫn là bản rút gọn, kết quả... Đồng dạng lực lượng đối quyền, kém chút không có bị cái kia hai hàng tại chỗ đánh chết!"),t("br"),t("br"),n._v("Đây gọi chuyện gì!"),t("br"),t("br"),n._v("Bản tôn chơi không lại phân thân... Cũng quá uất ức đi."),t("br"),t("br"),n._v("Mặc dù buồn bực, có điều, rất nhanh cũng là hiểu được."),t("br"),t("br"),n._v("Phân thân là Cửu Thiên Liên Thai luyện hóa mà thành, thứ này thế nhưng là siêu việt Thánh vật bảo bối, coi như tu luyện công pháp so với chính mình hơi thấp, chỉ bằng vào thân thể, cũng tuyệt đối cùng cấp bậc vô địch, không có cách nào so."),t("br"),t("br"),n._v("Trừ phi... Hắn tiếp tục tu luyện Thiên Đạo Kim Thân, đi đến cao siêu hơn tình trạng, nếu không, chỉ sợ một đoạn thời gian tương đối dài bên trong, đều muốn bị cái này phân thân nhẹ nhõm nghiền ép."),t("br"),t("br"),n._v("“Quên đi, dù sao đều là chính ta, cũng không liên quan uất ức...”"),t("br"),t("br"),n._v("Rất nhanh chậm lại."),t("br"),t("br"),n._v("Mặc kệ phân thân vẫn là bản tôn, đều là chính hắn, cũng không có gì có thể buồn bực."),t("br"),t("br"),n._v("“Ta không sao, chỉ là lúc tu luyện, xuất hiện chút vấn đề, không có gì đáng ngại!”"),t("br"),t("br"),n._v("Thấy Tôn quản gia vẻ mặt lo lắng nhìn qua, Trương Huyền phủi phủi quần áo, nói."),t("br"),t("br"),n._v("“Ah!”"),t("br"),t("br"),n._v("Nghe là tu luyện ra hiện vấn đề, Tôn Cường mặc dù cảm thấy kỳ lạ, vẫn là đem đây tơ nghi hoặc đặt ở đáy lòng, vội vàng ôm quyền: “Thiếu gia, Hàn Thanh tông Hồ trưởng lão cùng Tống Siêu danh sư, muốn mua trong tay ngươi Vô Hồn Kim Nhân, đã liên tục tới đã mấy ngày...”"),t("br"),t("br"),n._v("“Tống Siêu, Vô Hồn Kim Nhân?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ, lập tức nghĩ tới."),t("br"),n._v(" Lúc trước tại thành nam dược viên, lừa dối Tống Siêu tốn hao một cái linh thạch mua cái cỗ quan tài, từng hứa hẹn, chỉ cần có tiền, còn có ba đầu kim nhân bán ra, không nghĩ tới cái tên này thật đúng là đến đây."),t("br"),t("br"),n._v("Bất quá, hiện tại trong thời gian ngắn cho Mặc Hồn Sinh tìm không thấy thích hợp thân thể, chỉ có thể mượn nhờ kim nhân sống sót, vạn nhất chiến đấu chịu đến tổn thương, trong tay nhiều mấy cái dự bị, còn có thể có chút thay thế..."),t("br"),t("br"),n._v("Một khi tất cả đều bán, còn muốn tìm tới, gần như không có khả năng."),t("br"),t("br"),n._v("Dù sao, vu hồn sư cái nghề nghiệp này, sớm đã biến mất tại dòng sông lịch sử, Vô Hồn Kim Nhân thứ này, dùng một cái thiếu một cái, còn muốn tìm tới con thứ năm, thực sự quá khó khăn."),t("br"),t("br"),n._v("Lại nói, linh thạch trung phẩm, hắn cũng không quá thiếu khuyết, tâm lý đã có chuyển biến, không quá muốn bán."),t("br"),t("br"),n._v("“Trương sư, tại hạ chính là Hàn Thanh tông Hồ trưởng lão, vị này Tống Siêu danh sư, chắc hẳn các ngươi đã sớm nhận biết...”"),t("br"),t("br"),n._v("Ngay tại chần chờ, Hồ trưởng lão, Tống Siêu đi tới trước mặt."),t("br"),t("br"),n._v("“A, gặp qua Hồ trưởng lão!”"),t("br"),t("br"),n._v("Thấy hắn đi tới trước mặt, Trương Huyền ôm quyền, có chút lúng túng gãi đầu một cái: “Thực sự thật không tiện, trước đó ta đích xác có ba đầu Vô Hồn Kim Nhân, bất quá... Các ngươi tới quá muộn, ta đã bán đi, hiện tại trong tay, một đầu đều không có... Làm phiền các ngươi một chuyến tay không!”"),t("br"),t("br"),n._v("Mặc Hồn Sinh sự tình, không thể nhiều lời, lại không tốt chối từ, chỉ có thể nói kim nhân đã bán ra."),t("br"),t("br"),n._v("“Bán xong?” Không nghĩ tới kết quả này, Hồ trưởng lão sững sờ."),t("br"),t("br"),n._v("“Đúng vậy a, ngươi cũng biết, Vô Hồn Kim Nhân thứ này, liên quan vu hồn sư bí mật, muốn mua người thực sự nhiều lắm, ta tổng cộng cũng là cái kia vài đầu...”"),t("br"),t("br"),n._v("Trương Huyền ngay tại tràn đầy khiểm nhiên vẫy tay, liền nghe tới mặt đất chấn động oanh minh, một bóng người cao to từ trong viện vội vã đi ra."),t("br"),t("br"),n._v("“Thiếu chủ, vừa rồi xảy ra chuyện gì, ngươi không sao chứ...”"),t("br"),t("br"),n._v("Mặc Hồn Sinh tràn đầy lo lắng nhìn qua."),t("br"),t("br"),n._v("Hắn canh giữ ở nội viện bảo hộ thiếu gia an toàn, kết quả thiếu gia bị người một quyền đánh bay ra đến, lập tức đuổi tới muốn nhìn một chút đến cùng xảy ra chuyện gì."),t("br"),t("br"),n._v("“Đây là... Còn sống Vô Hồn Kim Nhân...”"),t("br"),t("br"),n._v("Hồ trưởng lão mắt tối sầm lại."),t("br"),t("br"),n._v("Đại ca, ngươi không nói Vô Hồn Kim Nhân đều bán xong ư? Cái này thoáng cái chạy đến còn sống... Là cái quỷ gì?"),t("br"),t("br"),n._v("“Khụ khụ!”"),t("br"),t("br"),n._v("Không nghĩ tới mới vừa nói xong chưa, cái tên này liền chạy ra khỏi đến, Trương Huyền tràn đầy xấu hổ: “Cái này, cái này... Không phải kim nhân, là ta một cái hộ vệ, tu luyện một loại kỳ quái võ công, mới biến thành như vậy!”"),t("br"),t("br"),n._v("“Kỳ quái võ công?”"),t("br"),t("br"),n._v("Hồ trưởng lão, Tống Siêu lần nữa nhoáng một cái."),t("br"),t("br"),n._v("Ngươi cho chúng ta càn rỡ ah!"),t("br"),t("br"),n._v("Đây rõ ràng chính là Vô Hồn Kim Nhân có được hay không?"),t("br"),t("br"),n._v("Tất cả mọi người là danh sư, coi như nhãn lực lại kém, là hộ vệ vẫn là kim nhân, vẫn có thể phân biệt ra được."),t("br"),t("br"),n._v("“Được rồi!” Có điều, đã đối phương không muốn nói, cũng không tiện truy cứu, dù sao, kim nhân việc nhỏ, có thể cùng vị này Trương sư kết giao, mới là trọng yếu nhất."),t("br"),t("br"),n._v("Lại hàn huyên vài câu, thấy Trương Huyền không có ý định bán ra kim nhân, Hồ trưởng lão, Tống Siêu đành phải lui ra ngoài."),t("br"),t("br"),n._v("Trước khi đi, cũng nhận được dặn dò, Mặc Hồn Sinh sự tình, đừng loạn truyền."),t("br"),t("br"),n._v("Hai người vừa đi, Trương Huyền liền nổi giận đùng đùng nhìn về phía trước mắt to con: “Ngươi bộ dáng này chạy đến, là muốn người khác nhận không ra đúng không?”"),t("br"),t("br"),n._v("Vô Hồn Kim Nhân, liên quan vu hồn sư, vốn là mẫn cảm, một đầu sống, hơn nữa còn có thể chạy loạn, truyền đi, khẳng định phiền phức không ít."),t("br"),t("br"),n._v("“Ta...” Mặc Hồn Sinh cũng biết gặp rắc rối, tràn đầy thật không tiện."),t("br"),t("br"),n._v("“Có thể có biện pháp che lấp trên người ngươi khí tức cùng dung mạo ư?”"),t("br"),t("br"),n._v("Biết cũng không thể trách đối phương, dù sao trước đó hắn cũng không nghĩ tới, hừ một tiếng, hỏi."),t("br"),t("br"),n._v("“Có biện pháp, chẳng qua khả năng cần thời gian vài ngày...”"),t("br"),t("br"),n._v("Mặc Hồn Sinh chần chờ một chút nói."),t("br"),t("br"),n._v("“Vậy là tốt rồi, mấy ngày nay trước đừng xuất hiện...”"),t("br"),t("br"),n._v("Thấy có biện pháp che giấu khí tức cùng dung mạo, Trương Huyền lúc này mới gật gật đầu, nhìn về phía cách đó không xa Tôn Cường: “Tôn quản gia, ta bế quan những ngày gần đây, có chuyện gì không?”"),t("br"),t("br"),n._v("Vừa về đến liền bế quan, xấp xỉ nửa tháng, cần phải phát sinh không ít sự tình."),t("br"),t("br"),n._v("“Bẩm báo thiếu gia, lão gia lần trước sau khi xuất hiện, những tông môn khác đều khiêm tốn không ít, không có người tìm phiền toái, mấy ngày nay Khang đường chủ, Triệu minh chủ còn có Phi Vũ công chúa mỗi ngày đều đến, có điều, biết ngươi bế quan liền tất cả đều rất thất vọng rời đi.”"),t("br"),t("br"),n._v("Tôn Cường nói."),t("br"),t("br"),n._v("Có lão gia chấn nhiếp, Vạn Quốc thành trước nay chưa có yên tĩnh, trước đó phách lối không gì sánh được tông môn đệ tử, hiện tại cũng đều nhu thuận cùng mèo nhà đồng dạng, không dám tùy ý làm bậy, sợ trong lúc lơ đãng chạm Dương sư xúi quẩy, từ đó làm cho toàn bộ tông môn hủy diệt."),t("br"),t("br"),n._v("“Còn có chính là... Cho người khác châm ngòi ly gián Đinh Hồng, thiếu gia trở về vào lúc ban đêm, liền bị Lưu Vân tông La Hoàng tông chủ giết!” Suy nghĩ một chút, Tôn Cường tiếp tục nói."),t("br"),t("br"),n._v("Ngày đó Trương Huyền ngụy trang thành Dương Huyền, vốn định giết cái này tai hoạ ngầm, nhưng vừa động thủ, khẳng định sẽ rò rỉ thực lực, lại thêm Dương sư thân phận tôn quý, làm sao có thể tự mình ra tay? Cũng là không có ra tay."),t("br"),t("br"),n._v("Bất quá, xem ra vị này Lưu Vân tông tông chủ rất hiểu chuyện, biết ý nghĩ của hắn, sớm hỗ trợ giết, tránh khỏi lại đi qua tìm kiếm."),t("br"),t("br"),n._v("“Trừ đó ra, còn có cái khác sao?”"),t("br"),t("br"),n._v("Trương Huyền hỏi qua tới."),t("br"),t("br"),n._v("“Lộ Xung tiểu thiếu gia, cần phải mấy ngày nay liền sẽ tỉnh lại, còn lại chính là danh sư thi đấu, liền muốn bắt đầu...”"),t("br"),t("br"),n._v("Tôn Cường đang muốn nói tiếp, liền nghe đến một cái thanh âm nhàn nhạt từ bên ngoài truyền tới."),t("br"),t("br"),n._v("“Hồng Viễn nhất đẳng đế quốc Danh Sư đường đường chủ, lục tinh đỉnh phong danh sư Mạc Cao Viễn, cầu kiến Dương sư!”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-577"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-579"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);