(window.webpackJsonp=window.webpackJsonp||[]).push([[1830],{2184:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-570-duong-su-that-vong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-570-duong-su-that-vong"}},[n._v("#")]),n._v(" Chương 570: Dương sư thất vọng")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 570: Dương sư thất vọng"),t("br"),t("br"),n._v("“Lục tinh danh sư?”"),t("br"),t("br"),n._v("Lông mày nhảy một cái, Trương Huyền giật nảy mình."),t("br"),t("br"),n._v("Không phải nói vạn quốc liên minh lợi hại nhất liền tứ tinh danh sư ư? Lục tinh đỉnh phong... Từ nơi nào xuất hiện?"),t("br"),t("br"),n._v("Trở thành lục tinh danh sư tiêu chuẩn là tu vi Hóa Phàm cửu trọng, mà đỉnh phong... Chỉ sợ là chân chân chính chính Thánh giả!"),t("br"),t("br"),n._v("Một vị Thánh giả muốn tới thăm hỏi..."),t("br"),t("br"),n._v("Cảm thấy da đầu run lên, Trương Huyền có chút muốn thổ huyết."),t("br"),t("br"),n._v("Tại nửa bước ngũ tinh danh sư trước mặt ngụy trang, đều cảm thấy áp lực như núi, lúc nào cũng có thể sẽ lộ tẩy, tại chính thức Thánh giả trước mặt ngụy trang... Chỉ cần hơi không cẩn thận, liền sẽ lộ ra chân ngựa!"),t("br"),t("br"),n._v("Mấu chốt nhất, vạch trần ngược lại cũng thôi, ngụy trang cấp cao danh sư, lấy thân phận dọa người, vi phạm sư đức, đối phương hoàn toàn có thể động thủ, tại chỗ chém giết!"),t("br"),t("br"),n._v("Nói cách khác... Một khi bị phát hiện là giả, nhất định phải chết!"),t("br"),t("br"),n._v("“Nhất định là Dương sư xuất hiện tin tức tiết lộ...”"),t("br"),t("br"),n._v("Xoa xoa mi tâm, một hồi ưu sầu."),t("br"),t("br"),n._v("Lợi hại như vậy danh sư chạy tới, luôn mồm muốn bái kiến Dương sư, chỉ có một khả năng, cái kia chính là... Chuyện ngày đó, có người truyền đến Hồng Viễn nhất đẳng đế quốc."),t("br"),t("br"),n._v("Nếu như thực lực của hắn, mạnh hơn một chút, ngụy trang khả năng còn nhìn không ra, bây giờ người ta đều vây lại cửa, luôn không khả năng không thấy đi."),t("br"),t("br"),n._v("Nên làm cái gì?"),t("br"),t("br"),n._v("“Không được, liền nói lão sư không tại...”"),t("br"),t("br"),n._v("Một cắn răng, đang muốn mở miệng, liền nghe đến bên người một thanh âm vang lên: “Nhốn nháo cái gì mà nhốn nháo, hiện ra ngươi giọng lớn vẫn là thế nào? Một chút quy củ đều không có, trước cửa ra vào chờ lấy, ta đi bẩm báo lão gia!”"),t("br"),t("br"),n._v("Quay đầu nhìn lại, chỉ thấy một bên Tôn Cường, mặt mũi ghét bỏ biểu lộ: “Cái gì ngoạn ý nha, còn lục tinh danh sư, liền ngươi biết truyền âm ư? Quản ngươi cái gì lục tinh đỉnh phong, cái gì đường chủ, muốn gặp lão gia, ngoan ngoãn đưa lên bái thiếp, đứng ở ngoài cửa chờ lấy, nhất kinh nhất sạ, tạo phản ah!”"),t("br"),t("br"),n._v("Phù phù!"),t("br"),t("br"),n._v("Trương Huyền thân thể nhoáng một cái, toàn thân mềm nhũn, kém chút không có từng đứt đoạn khí đi."),t("br"),t("br"),n._v("Đang rầu như thế nào đối phó phía ngoài lục tinh danh sư, liền đến một câu như vậy..."),t("br"),t("br"),n._v("Lần này coi như muốn tìm lý do, cũng không tìm được!"),t("br"),t("br"),n._v("Mấu chốt nhất là... Mặc kệ thế nào nói người ta đều là lục tinh đỉnh phong danh sư, nhất đẳng đế quốc đỉnh phong nhất siêu cấp tồn tại, trong lời nói một chút mặt mũi cũng không cho... Tôn Cường, ta Cường ca, ngươi đây là muốn nghịch thiên a!"),t("br"),t("br"),n._v("Con mắt nước mắt ào ào, không biết nên làm sao bây giờ, chỉ thấy Tôn Cường vẻ mặt kỳ vọng nhìn qua: “Thiếu gia, lão gia không phải cùng ngươi cùng một chỗ, thương nghị cứu chữa Lộ Xung tiểu thiếu gia ư? Tiểu thiếu gia hiện tại sắp tỉnh, lão gia người đâu?”"),t("br"),t("br"),n._v("Lộ Xung linh hồn quá mức mạnh mẽ, mặc dù đã đánh thức, hoàn toàn dung hợp thân thể, còn cần một đoạn thời gian, có điều, đi qua nửa tháng điều chỉnh, cũng kém không nhiều nhanh tỉnh."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Nghe được tra hỏi, Trương Huyền nói không ra lời."),t("br"),t("br"),n._v("Trước đó hắn từng nói với Vương Dĩnh qua, cùng Dương sư thương nghị cứu chữa Lộ Xung, sau đó... Liền không có sau đó, hiện tại Lộ Xung đều nhanh tỉnh, Dương sư đều không có nhìn thấy tung tích, khó tránh khỏi kỳ lạ."),t("br"),t("br"),n._v("“Có phải hay không tại nội viện, ta đi qua xin phép một chút, muốn hay không thấy cái này cái gì lục tinh danh sư?” Thấy thiếu gia không nói lời nào, Tôn Cường tiếp tục nói."),t("br"),t("br"),n._v("“Khụ khụ, không cần, ta đi xin phép đi, ngươi trước đem vị này Mạc đường chủ, đưa đến phòng khách...”"),t("br"),t("br"),n._v("Thấy hắn càng hỏi càng nhiều, biết cũng không còn cách nào né tránh, Trương Huyền đành phải cắt ngang hắn tiết tấu."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Tôn Cường lên tiếng, đi ra ngoài."),t("br"),t("br"),n._v("Thấy hắn đi xa, hướng vào phía trong viện đi đến, Trương Huyền vừa đi vừa nhu lông mày."),t("br"),t("br"),n._v("Cho cái tên này một pha trộn, “Dương sư” không lộ diện không được, có điều, cẩn thận nói đến, coi như hắn không chen vào nói, đối phương chuyên mà đến, “Dương sư” không xuất hiện, cũng tất nhiên gặp phải hoài nghi."),t("br"),t("br"),n._v("Cho nên, coi như hắn không nói, “Dương sư” cũng sớm muộn gì muốn đi ra, chỉ bất quá trước thời hạn một chút mà thôi."),t("br"),t("br"),n._v("Có thể... Đối mặt một vị lục tinh đỉnh phong danh sư, nên như thế nào ngụy trang, mới có thể để cho nó tin tưởng?"),t("br"),t("br"),n._v("Loại cường giả cấp bậc này, vô cùng có khả năng ngưng luyện Minh Lý Chi Nhãn, có thể xem thấu hết thảy hư ảo, trang giống như, cũng vô dụng!"),t("br"),t("br"),n._v("Thật giống như hắn mượn nhờ Mặc Hồn Sinh khí thế, để cho người ta tưởng lầm là Thánh giả, mà nếu như lại dùng loại khí thế này xuất hiện, đối phương có thể tuỳ tiện phát hiện, là giả!"),t("br"),t("br"),n._v("Còn có, phi hành là lợi dụng Hồng Trần Đạp Thiên bộ, giấu diếm không biết phi hành tứ tinh, ngũ tinh danh sư coi như cũng được, muốn lừa qua biết bay Thánh giả... Khẳng định không có khả năng!"),t("br"),t("br"),n._v("Võ kỹ phương thức phi hành cùng Thánh giả phương thức phi hành, khẳng định là không giống."),t("br"),t("br"),n._v("“Đã... Làm sao giả bộ đều sẽ bị phát hiện, không bằng không giả bộ!”"),t("br"),t("br"),n._v("Đi tới nội viện gian phòng, chuyển hai vòng, đột nhiên một cắn răng."),t("br"),t("br"),n._v("Khí thế không thể mượn dùng, phi hành cũng không thể thi triển, đã như vậy... Vậy liền không giả."),t("br"),t("br"),n._v("Nghĩ đến điểm này, không do dự nữa, thuần thục đem “Dương sư” quần áo thay, lúc này mới chậm rãi phòng khách đi đến."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mạc Cao Viễn ngồi ở phòng khách trên chỗ ngồi, Mạch Trần, Hồng Thiển ngoan ngoãn đứng ở phía sau."),t("br"),t("br"),n._v("Đi tới Vạn Quốc thành, không có chút nào ngừng, trực tiếp tìm được Hồng Thiển, đem ngày đó tình huống kỹ càng hỏi thăm một phen."),t("br"),t("br"),n._v("Nghe tới vị này Dương sư, đạp trời mà đến, một chỉ để Yêu Thần thú phá cảnh, cùng truyền lại tin tức giống nhau như đúc, trong lòng cũng tràn đầy rung động."),t("br"),t("br"),n._v("Bởi vì không cách nào xác định đối phương cấp bậc, lúc này mới mang theo hai người đến đây bái kiến, dự định tận mắt chứng kiến một phen."),t("br"),t("br"),n._v("Ngồi tại gian phòng, ngắm nhìn bốn phía, nhìn một hồi, mày nhăn lại."),t("br"),t("br"),n._v("Nơi này hết thảy bố cục, đều rất bình thường, không giống một cái cấp cao danh sư ở lại vị trí, mấu chốt nhất là, linh khí mỏng manh, cùng nàng hắn địa phương không khác."),t("br"),t("br"),n._v("Dựa theo tình huống bình thường, một vị bát tinh danh sư chỗ ở, coi như không có bố trí trận pháp, cường giả bản thân lực lượng, cũng có thể để hoang nguyên biến thành Tịnh thổ."),t("br"),t("br"),n._v("Cường đại danh sư, phù hợp tự nhiên, đi đến nơi nào, đều sẽ lấy hắn làm trung tâm, không tự chủ hình thành một cái tụ linh khí tràng, thoải mái cải thiện lấy hoàn cảnh chung quanh."),t("br"),t("br"),n._v("Chính vì vậy, Danh Sư đường tổng bộ, linh khí bức người, lại bình thường người tu luyện tiến vào bên trong, đều sẽ trở thành thiên tài tu luyện, tiến bộ kinh người."),t("br"),t("br"),n._v("Trước mắt hoàn cảnh này, không có biến hóa chút nào, thật giống như bát tinh danh sư chưa từng tới đồng dạng, chuyện gì xảy ra?"),t("br"),t("br"),n._v("Ngay tại kỳ lạ, chỉ thấy một người trung niên chậm rãi đi tới."),t("br"),t("br"),n._v("Người này thực lực bước chân lỗ mãng, trong cơ thể không có chút nào chân khí ba động, cùng người bình thường không khác, chợt nhìn, còn tưởng rằng là tới quét dọn vệ sinh nhân viên quét dọn nhân viên."),t("br"),t("br"),n._v("Vừa định hỏi thăm, cái tên này từ nơi nào xuất hiện, chỉ thấy một bên Tôn quản gia vội vàng nghênh đón tiếp lấy: “Lão gia!”"),t("br"),n._v("Bên người Hồng Thiển danh sư cũng run rụt lại, truyền âm vang lên: “Mạc đường chủ, vị này chính là Dương sư...”"),t("br"),t("br"),n._v("“Hắn?”"),t("br"),t("br"),n._v("Mạc Cao Viễn sững sờ tại nguyên chỗ."),t("br"),t("br"),n._v("Mặc dù hắn chưa thấy qua bát tinh danh sư, nhưng gặp qua thất tinh cường giả, nhất cử nhất động, dung hợp tự nhiên, cho người ta một loại khác nhau áp bách... Trước mắt cái này, nếu như không nói là danh sư, còn tưởng rằng là người bình thường..."),t("br"),t("br"),n._v("Bát tinh?"),t("br"),t("br"),n._v("Nhìn lầm đi?"),t("br"),t("br"),n._v("Nhướng mày, nhịn không được nhìn Hồng Thiển một chút, thấy hắn xác nhận là Dương sư không sai, lúc này mới đứng dậy, liền ôm quyền: “Tại hạ Hồng Viễn nhất đẳng đế quốc Danh Sư đường đường chủ, lục tinh đỉnh phong danh sư Mạc Cao Viễn, gặp qua Dương sư!”"),t("br"),t("br"),n._v("Nói xong lặng lẽ quan sát."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Nghe được tên tuổi của hắn, Dương sư dường như cũng không thèm để ý, mà là quay người lại ngồi ở chủ vị, nhàn nhạt nhìn qua: “Thấy ta chuyện gì?”"),t("br"),t("br"),n._v("“Ta...” Không nghĩ tới đối phương hỏi trực tiếp như vậy, Mạc Cao Viễn không biết trả lời như thế nào."),t("br"),t("br"),n._v("Cũng không thể nói, danh sư bạc bên trên không có tra được tên của ngươi, muốn tới đây nhìn xem, có phải thật vậy hay không đi!"),t("br"),t("br"),n._v("“Nghe Dương sư đi tới vạn quốc liên minh, tại hạ lo lắng chiếu cố không chu toàn, đến đây nhìn xem...”"),t("br"),t("br"),n._v("Xấu hổ thoáng cái, Mạc Cao Viễn nói."),t("br"),t("br"),n._v("Nói còn chưa dứt lời, liền nghe đến đối diện trung niên nhân thanh âm nhàn nhạt vang lên: “Nhìn xong?”"),t("br"),t("br"),n._v("“Ây... Nhìn xong!”"),t("br"),t("br"),n._v("“Vậy thì đi thôi!”"),t("br"),t("br"),n._v("Nói xong, Dương sư khoát tay áo, hai tay chắp sau lưng chậm rãi đi ra ngoài."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Mạc Cao Viễn khóe miệng giật một cái."),t("br"),t("br"),n._v("Cái này... Tình huống gì?"),t("br"),t("br"),n._v("Còn có thể hay không vui vẻ nói chuyện phiếm?"),t("br"),t("br"),n._v("Coi như ngươi là bát tinh danh sư, cũng không thể như thế miệt thị người chứ?"),t("br"),t("br"),n._v("Bất kể nói thế nào, ta đều là Hồng Viễn nhất đẳng đế quốc Danh Sư đường đường chủ, địa vị tôn sùng, chuyên đến bái kiến, trực tiếp đuổi người... Không khỏi quá không lễ phép!"),t("br"),t("br"),n._v("“Thực không dám giấu giếm, tại hạ không chỉ lo lắng chiếu cố không chu toàn, còn có chút trên việc tu luyện nghi hoặc, muốn hướng Dương sư thỉnh giáo...”"),t("br"),t("br"),n._v("Một cắn răng, đứng dậy."),t("br"),t("br"),n._v("Đặc biệt tới nhìn vị này Dương sư có phải thật vậy hay không, cái gì đều không có nhìn ra, liền rời đi, thực sự băn khoăn!"),t("br"),t("br"),n._v("Cùng nó thăm dò, còn không bằng trực tiếp thỉnh giáo, nếu như là hắn cho rằng rất khó vấn đề, đối phương có thể nhẹ nhõm giải quyết, tất nhiên là vượt qua tên của hắn sư."),t("br"),t("br"),n._v("Không giải quyết được, cái này cái gọi là bát tinh, liền ý vị sâu xa."),t("br"),t("br"),n._v("“Nói đi!”"),t("br"),t("br"),n._v("Dương sư cũng không quay đầu, thanh âm thản nhiên."),t("br"),t("br"),n._v("“Vâng... Thánh giả nhất trọng về mặt tu luyện, vấn đề xuất hiện...” Mạc Cao Viễn vội nói."),t("br"),t("br"),n._v("“Thánh giả nhất trọng?”"),t("br"),t("br"),n._v("Trương Huyền ngụy trang Dương sư, lông mày nhảy một cái, thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Ngươi để cho ta giải thích một chút Hóa Phàm một hai ba trọng tu vi, còn có thể nói đến đạo lý rõ ràng... Thánh giả?"),t("br"),t("br"),n._v("Loại này cấp bậc, vẫn là mới vừa nghe nói không lâu, trước đó liền Bán Thánh cũng không biết, loại này cường giả tu luyện, lại như thế nào có thể biết?"),t("br"),t("br"),n._v("Đừng nói chỉ điểm, liền bí tịch như thế nào đều không có gặp qua..."),t("br"),t("br"),n._v("Một khi mở miệng, khẳng định ngay lập tức sẽ lộ tẩy!"),t("br"),t("br"),n._v("“... Chính là trục không huyệt như thế nào mở ra, có thể làm cho người tu luyện, lực lượng không tiết lộ, còn có thể tu vi không ngừng gia tăng...”"),t("br"),t("br"),n._v("Không biết “Dương sư” trong lòng xoắn xuýt, Mạc Cao Viễn đem nghi ngờ của mình nói ra."),t("br"),t("br"),n._v("Vấn đề này, từng khốn hoặc hắn hồi lâu, tra duyệt vô số thư tịch, mới cuối cùng tìm tới đáp án, nếu như đối phương nói cùng hắn biết đến đồng dạng, cái kia chính là cấp cao danh sư, mà nói không giống, đã làm cho khảo cứu."),t("br"),t("br"),n._v("“Không biết... Dương sư nhưng có biện pháp tốt?”"),t("br"),t("br"),n._v("Nói xong, vẻ mặt mong đợi nhìn lại."),t("br"),t("br"),n._v("“Trục không huyệt? Lực lượng không tiết lộ? Tu vi gia tăng?”"),t("br"),t("br"),n._v("Trương Huyền đầu to, kém chút không có thổ huyết."),t("br"),t("br"),n._v("Cái này huyệt đạo nghe đều không có nghe qua, làm sao biết lực lượng không tiết lộ, tu vi gia tăng? Nghe đều nghe không hiểu, lại như thế nào có biện pháp tốt?"),t("br"),t("br"),n._v("Cái này hết rồi!"),t("br"),t("br"),n._v("“Dương sư...”"),t("br"),t("br"),n._v("Thấy hắn chậm chạp không nói chuyện, mà là đứng tại chỗ không nhúc nhích, Mạc Cao Viễn nghi ngờ nhìn qua, đang muốn hỏi thăm, liền nghe đến một cái hừ lạnh vang lên."),t("br"),t("br"),n._v("“Ngươi thật là lục tinh đỉnh phong danh sư?”"),t("br"),t("br"),n._v("Dương sư quay mặt lại, ánh mắt lạnh lùng, mang theo không vui."),t("br"),t("br"),n._v("“Ta... Đúng a!”"),t("br"),t("br"),n._v("Mạc Cao Viễn sững sờ."),t("br"),t("br"),n._v("Hắn đường đường lục tinh đỉnh phong danh sư, Hồng Viễn nhất đẳng đế quốc đệ nhất nhân, không thể giả được, làm sao có thể không phải?"),t("br"),t("br"),n._v("“Là? Cái kia thế mà liền loại này đơn giản vấn đề cũng không biết, còn cần thỉnh giáo?”"),t("br"),t("br"),n._v("Hai tay chắp sau lưng, Dương sư ánh mắt lộ ra nồng đậm thất vọng: “Xem ra nhiều năm không có bốn phía đi dạo, lục tinh danh sư cũng đều có chút hữu danh vô thực!”"),t("br"),t("br"),n._v("(Đề cử lão nhai sách cũ 《 vô tận đan điền 》, còn không có nhìn, có thể nhìn một chút, cùng quyển sách đồng dạng đặc sắc!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-578"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-580"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);