(window.webpackJsonp=window.webpackJsonp||[]).push([[1832],{2188:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-571-duong-su-ta-sai-roi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-571-duong-su-ta-sai-roi"}},[n._v("#")]),n._v(" Chương 571: Dương sư, ta sai rồi!")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Chương 571: Dương sư, ta sai rồi!"),i("br"),i("br"),n._v("Hữu danh vô thực?"),i("br"),i("br"),n._v("Nói Mạc sư hữu danh vô thực?"),i("br"),i("br"),n._v("Hồng Thiển, Mạch Trần tất cả đều rung động."),i("br"),i("br"),n._v("Vị này chính là Hồng Viễn các loại đế quốc, cường đại nhất danh sư, nói như vậy, tương đương với ở trước mặt nghi ngờ nghề nghiệp của hắn năng lực, xem như cực lớn vũ nhục."),i("br"),i("br"),n._v("Quả nhiên, nghe nói như thế, Mạc Cao Viễn mày nhíu lại, tràn đầy không vui: “Tại hạ là tại Thiên Hồng phong hào đế quốc Danh Sư đường hoàn thành khảo hạch. Trở thành lục tinh danh sư ba mươi bảy năm, chỉ điểm ra lục tinh danh sư hai vị, ngũ tinh danh sư mười hai vị, tứ tinh, tam tinh vô số kể. Dẫn đầu Hồng Viễn đế quốc Danh Sư đường, liên tục thu hoạch ba lần danh sư thi đấu mười vị trí đầu...”"),i("br"),i("br"),n._v("“Như thế nào hữu danh vô thực? Mong rằng Dương sư chỉ ra!”"),i("br"),i("br"),n._v("Việc quan hệ phẩm đức nghề nghiệp, coi như đối phương thật sự là tinh danh sư, hắn cũng sẽ không lùi bước."),i("br"),i("br"),n._v("“Muốn người sáng mắt người trước rõ ràng, muốn chính nhân người trước chính mình. Nhà giáo cũng thế!”"),i("br"),i("br"),n._v("Đối mặt hắn nghi ngờ, Dương sư cũng không khẩn trương, thần sắc thản nhiên: “Muốn truyền thụ học sinh, để hắn không đi sai đường, đầu tiên muốn bảo đảm bản thân không phải là sai, chính mình cũng đi đến sai lầm trên đường, lại truyền thụ học sinh, thành tựu càng cao, đối học sinh tổn thương cũng là càng lớn!”"),i("br"),i("br"),n._v("Sai một ly đi nghìn dặm, làm nhà giáo, truyền đạo học nghề giải thích nghi hoặc, chính mình cũng lý giải sai, học sinh càng nhiều, truyền bá sai lầm cũng là càng rộng, cuối cùng hại người cũng càng nhiều."),i("br"),i("br"),n._v("“Ngươi vừa rồi vấn đề kia, là tương đối đơn giản, đơn giản như vậy vấn đề, đều không hiểu rõ, còn cần hỏi thăm, có thể thấy được bản thân đối tu luyện hiểu là bực nào không đủ, loại tình huống này, lại như thế nào có thể dạy học sinh tốt?”"),i("br"),i("br"),n._v("Dương sư lắc đầu: “Như vậy đi, ngươi đánh hai quyền, để cho ta nhìn xem. Nếu như không có đoán sai, đơn giản như vậy vấn đề đều không hiểu rõ, về mặt tu luyện, đã đi nhầm vào lạc lối!”"),i("br"),i("br"),n._v("“Đi nhầm vào lạc lối?”"),i("br"),i("br"),n._v("Mạc Cao Viễn vẻ mặt biến thành màu đen."),i("br"),i("br"),n._v("Đổi lại ai, bị ở trước mặt nói như vậy, khẳng định cũng sẽ cảm thấy mất hứng."),i("br"),i("br"),n._v("Huống chi, vấn đề kia hắn biết đáp án, chỉ là muốn thăm dò đối phương mà thôi."),i("br"),i("br"),n._v("“Không đánh, cũng không quan trọng!” Thấy hắn không nói lời nào, Dương sư khoát tay áo, trên mặt thất vọng chi ý càng đậm: “Tôn Cường, tiễn khách!”"),i("br"),i("br"),n._v("“Được rồi!”"),i("br"),i("br"),n._v("Tôn Cường liền vội vàng gật đầu, đi tới trước mặt: “Vị này hữu danh vô thực Mạc sư, xin rời khỏi đi, nơi này không chào đón ngươi!”"),i("br"),i("br"),n._v("“Ngươi...” Vốn cũng không cao hứng, thấy cái nho nhỏ quản gia thế mà mở miệng châm chọc, Mạc Cao Viễn tức đến gần thổ huyết."),i("br"),i("br"),n._v("Đối phương hư hư thực thực tinh danh sư, nói hai câu ngược lại cũng thôi, có thể... Ngươi thì tính là cái gì?"),i("br"),i("br"),n._v("“Lão gia nguyện ý mở miệng chỉ điểm, là ngươi sửa chữa mấy đời phúc phận mới có cơ duyên, còn không nhanh chóng đánh quyền... Ngốc như vậy danh sư, thật sự là lần thứ 2 nhìn thấy, lục tinh đỉnh phong...”"),i("br"),i("br"),n._v("Tôn Cường lộ ra nồng đậm xem thường: “Ta nhổ vào!”"),i("br"),i("br"),n._v("Ầm!"),i("br"),i("br"),n._v("Hồng Thiển, Mạch Trần chỉ cảm thấy trước mắt đen, không có kém chút hôn mê tại chỗ."),i("br"),i("br"),n._v("Đại ca, ngươi biết đối diện thân phận của người này đến cỡ nào tôn sùng ư? Liền dám nói như vậy?"),i("br"),i("br"),n._v("Hồng Viễn đế quốc thứ người, lục tinh đỉnh phong danh sư, bất cứ lúc nào cũng sẽ đột phá, ngay cả đế quốc Hoàng đế bệ hạ nhìn thấy, cũng muốn lấy học sinh chi lễ đãi chi... Ngươi cái chỉ có Hóa Phàm trọng mập mạp chết bầm, thế mà tràn đầy khinh bỉ “Ta nhổ vào”..."),i("br"),i("br"),n._v("“Phi” em gái ngươi ah!"),i("br"),i("br"),n._v("Ngươi có tư cách đây ngươi?"),i("br"),i("br"),n._v("“Ngươi...”"),i("br"),i("br"),n._v("Hai người phát điên, Mạc Cao Viễn cũng thiếu chút không có tại chỗ ngất đi."),i("br"),i("br"),n._v("Từ khi trở thành lục tinh danh sư, ba mươi bảy năm qua, bất kể là ai, nhìn thấy hắn đều cung kính hữu lễ, không dám nói câu cuồng vọng phách lối lời nói, hôm nay ngược lại tốt, tới thăm dò vị này Dương sư, trước bị nghi ngờ, lại bị xem thường..."),i("br"),i("br"),n._v("Mấu chốt nhất là... Xem thường hắn vẫn là cái hạ nhân!"),i("br"),i("br"),n._v("Vẻ mặt như là đáy nồi, Mạc Cao Viễn cố nén lửa giận: “Dương sư, tại hạ vừa vặn tại bộ võ kỹ bên trên có chỗ nghi hoặc, mong rằng chỉ điểm!”"),i("br"),i("br"),n._v("Đánh chó cũng phải nhìn chủ nhân, mặc dù rất tức giận cái tên mập mạp này, mà dù sao là Dương sư hạ nhân, không dễ làm mặt nổi giận, vẫn là trước thăm dò hết, biết mức độ mới quyết định."),i("br"),i("br"),n._v("Nói xong, cũng không cần đối phương trả lời, liền quyền đánh ra."),i("br"),i("br"),n._v("Không hổ là Thánh giả, ra quyền, cả phòng đều giống như bị cổ lực lượng cường đại bao phủ, mọi người tựa như bị cất vào cái từ đối phương khống chế chiếc lồng, bất kể chạy trốn tới nơi nào, chỉ cần ra quyền, liền sẽ bị đánh."),i("br"),i("br"),n._v("Nói cách khác, đối mặt Thánh giả, chạy trốn... Tuyệt không có khả năng!"),i("br"),i("br"),n._v("Quyền liền phong tỏa không gian, để cho người ta không thể không nghênh chiến... Đáng sợ!"),i("br"),i("br"),n._v("Không để ý tới quyền pháp lực lượng như thế nào, tâm động, Dương sư (Trương Huyền) não bản sách báo nổi lên."),i("br"),i("br"),n._v("Nhìn thấy nội dung bên trong, kỹ càng phong phú, lúc này mới thở phào nhẹ nhõm, khẽ cười, không khỏi tán dương nhìn Tôn Cường mắt."),i("br"),i("br"),n._v("Có thể để cho đường đường Thánh giả, lục tinh đỉnh phong danh sư, có thể thất thố như vậy, cái tên này làm người tức giận công phu, thật không phải mạnh mẽ."),i("br"),i("br"),n._v("Hô!"),i("br"),i("br"),n._v("Mạc Cao Viễn ngừng lại, ôm quyền: “Còn xin Dương sư chỉ bảo!”"),i("br"),i("br"),n._v("“Ừm!”"),i("br"),n._v(" Mí mắt nhấc, Dương sư chậm rãi đi tới: “Song Thanh Long Dương quyền tu luyện không sai, có lẽ đi đến tiểu thành trình độ, nếu như không nhìn lầm, ngươi tu luyện bộ võ kỹ này, chí ít có hai mươi năm trở lên hỏa hầu đi!”"),i("br"),i("br"),n._v("Mạc Cao Viễn sững sờ."),i("br"),i("br"),n._v("Song Thanh Long Dương quyền, mặc dù người tu luyện không nhiều, nhưng cũng không tính là ít thấy, đối phương có thể nhận ra rất bình thường, có thể... Hắn tu luyện bao nhiêu năm, là thế nào nhìn ra được?"),i("br"),i("br"),n._v("Có ít người, tu luyện ba ngày liền có thể đem võ kỹ đi đến tiểu thành, cũng có chút người, đắm chìm sinh đều không thể tiến lên, bản thân bộ võ kỹ này chỉ là tiểu thành tình trạng, khoảng cách đại thành còn có định khoảng cách. Bất luận kẻ nào nhìn thấy, đều sẽ cảm giác đến, thời gian tu luyện không dài... Đối phương nhưng mắt thấy ra hai mươi năm hỏa hầu, đây nhãn lực... Quá lợi hại đi!"),i("br"),i("br"),n._v("Tâm rung động, thái độ cũng biến thành cung kính xuống: “Không sai, cho tới hôm nay, vừa lúc là hai mươi năm lẻ tháng...”"),i("br"),i("br"),n._v("Sớm biết hắn lại như vậy trả lời, Dương sư bàn tay vác tại phía sau, nhàn nhạt nhìn qua: “Tu luyện hai mươi năm võ kỹ, nhưng thủy chung quanh quẩn một chỗ tại tiểu thành, liền đại thành cũng chưa tới, ngươi cũng đã biết nguyên nhân?”"),i("br"),i("br"),n._v("“Không biết...”"),i("br"),i("br"),n._v("Mạc Cao Viễn lắc đầu."),i("br"),i("br"),n._v("Cái khác võ kỹ, hắn tất cả đều có thể rất nhanh đạt đến đại thành tình trạng, liền bộ quyền pháp này, từ đầu đến cuối tiến triển không lớn, tra duyệt không ít sách vở, đều không dùng."),i("br"),i("br"),n._v("Vừa rồi đối phương để hắn đánh quyền, tâm động, mới cố ý đánh ra đến, muốn cho hắn quan sát, có thể hay không nhìn ra mấu chốt."),i("br"),i("br"),n._v("“Song Thanh Long Dương quyền, cường đại không tại ‘Long Dương’, mà là tại ‘Song Thanh’!”"),i("br"),i("br"),n._v("Thấy hắn không hiểu, Dương sư giải thích nói: “Quyền sợ trẻ trung, côn sợ lão Lang. Quyền pháp cương mãnh, không riêng gì lực lượng đầy đủ, càng quan trọng hơn là tinh khí thần đi đến đỉnh phong. Bởi vậy, cái gọi là Song Thanh, xanh trong người, xanh trong lòng!”"),i("br"),i("br"),n._v("“Ngươi tu luyện bộ quyền pháp này thời điểm, nếu như chỉ có trăm tuổi khoảng chừng, đạt đến đại thành sẽ không quá lâu, đáng tiếc, trăm hai mươi tuổi mới bắt đầu tu luyện, thân thể không còn trẻ nữa, khí tức trở nên đục ngầu... Muốn đánh ra thanh niên mới có thể có cương mãnh quyền pháp, nào có dễ dàng như vậy!”"),i("br"),i("br"),n._v("“Đương nhiên, tâm tính bên trên có thể tuổi trẻ, nhờ vào đối võ kỹ lý giải, đem bộ này chiêu số, thôi diễn đến đại thành cũng không khó... Đáng tiếc, ngươi xung kích Trục Không cảnh thời điểm, xuất hiện biến cố, dẫn đến trên tâm lý có cực lớn bóng mờ, từ đầu đến cuối không cách nào phù hợp võ kỹ chân ý!”"),i("br"),i("br"),n._v("“Ta xung kích Trục Không cảnh xảy ra vấn đề, ngươi... Làm sao ngươi biết?”"),i("br"),i("br"),n._v("Dọa nhảy, Mạc Cao Viễn lại không trước đó bình tĩnh."),i("br"),i("br"),n._v("Thánh giả nặng Trục Không cảnh, lấy tên truy đuổi hư không chi ý, đi đến loại cảnh giới này, có thể tự do bay lượn, tại thiên không phi hành."),i("br"),i("br"),n._v("“Làm sao biết?”"),i("br"),i("br"),n._v("Dương sư nhẹ nhàng cười: “Song Thanh Long Dương quyền, mệnh danh quyền pháp, trên thực tế lại là mượn nhờ bàn chân chạm đất lực lượng, từ chi dưới lan tràn lên phía trên, đem lực lượng toàn thân đánh lên, khí a thành!”"),i("br"),i("br"),n._v("Cao minh quyền pháp, dựa vào là lực lượng toàn thân, mà không phải đơn độc cánh tay cơ bắp."),i("br"),i("br"),n._v("Nghe được đây, không chỉ Mạc Cao Viễn, ngay cả Hồng Thiển, Mạch Trần hai người cũng đồng thời gật đầu."),i("br"),i("br"),n._v("Gặp bọn họ đồng ý, Dương sư tiếp tục nói: “Thế nhưng là... Ngươi vừa rồi đánh quyền thời điểm, khí tức phù phiếm, mặc dù thoạt nhìn, vận dụng toàn thân, trên thực tế, lực lượng chỉ từ bắp chân bắt đầu, không có đi qua bàn chân! Dưới mặt bàn chân mới chính là trục không huyệt vị trí, kết hợp với trước ngươi vấn đề, suy đoán ra xung kích Trục Không cảnh xảy ra vấn đề, không khó!”"),i("br"),i("br"),n._v("Vừa rồi Mạc Cao Viễn vấn đề chính là “Trục không huyệt như thế nào mở ra, có thể làm cho người tu luyện, lực lượng không tiết lộ, còn có thể tu vi không ngừng gia tăng...”"),i("br"),i("br"),n._v("Chân khí không thông qua mang theo trục không huyệt bàn chân, kết hợp với câu nói này, suy đoán ra những này liền không khó."),i("br"),i("br"),n._v("“Trên việc tu luyện xuất hiện vấn đề, dẫn đến trục không huyệt lực lượng rò rỉ, tu vi khó mà gia tăng... Tu luyện Song Thanh Long Dương quyền thời điểm, vẫn không có có thể giải quyết. Bởi vậy, vì để tránh cho lực lượng rò rỉ, cố ý lách qua bàn chân vị trí... Mặc dù về sau tìm được phương pháp, cũng làm cho thực lực của ngươi, lần nữa tiến bộ... Đáng tiếc, Long Dương quyền đã thành thói quen, lại không cách nào tiến bộ!”"),i("br"),i("br"),n._v("Dương sư nói tiếp."),i("br"),i("br"),n._v("“Đây, cái này...” Mạc Cao Viễn toàn thân cứng ngắc, nói không ra lời."),i("br"),i("br"),n._v("Đối phương nói điểm cũng không tệ."),i("br"),i("br"),n._v("Lúc trước xung kích Trục Không cảnh thời điểm, xuất hiện biến cố, dẫn đến huyệt đạo xuất hiện vấn đề, lực lượng rò rỉ, rất nhiều năm sau mới để giải quyết, mà bộ này Song Thanh Long Dương quyền, chính là không có giải quyết mấu chốt trước tu luyện."),i("br"),i("br"),n._v("Hắn có thể xác định, lần thứ 2 nhìn thấy trước mắt vị này, chỉ nhìn mắt đánh quyền, liền suy đoán ra những thứ này..."),i("br"),i("br"),n._v("Nhãn lực nên mạnh bao nhiêu?"),i("br"),i("br"),n._v("Chí ít hắn làm không được! Coi như thất tinh danh sư, sợ là cũng không thể hoàn thành!"),i("br"),i("br"),n._v("Chẳng lẽ, thật sự là vị... Bát tinh danh sư?"),i("br"),i("br"),n._v("Muốn nói trước khi đến, đối trước mắt cái này niên nhân thân phận có chút hoài nghi, mà bây giờ, đã tin tưởng bảy, tám phân."),i("br"),i("br"),n._v("“Nếu như không nhìn lầm, ngươi xung kích Trục Không cảnh thời điểm, là gặp nguy hiểm gì, hoặc là ngay tại chiến đấu đi!”"),i("br"),i("br"),n._v("Ngay tại khiếp sợ, liền nghe đến đối diện Dương sư tiếp tục hỏi."),i("br"),i("br"),n._v("“Vâng...” Mạc Cao Viễn gật đầu."),i("br"),i("br"),n._v("Lúc trước hắn đang bị kẻ thù truy sát, sống chết phát, xung kích Trục Không cảnh thành công, cứ việc nguy hiểm giải trừ, thành công chém giết cừu nhân, trục không huyệt cũng bởi vì cưỡng ép tăng lên, nhận lấy tổn thương... Lúc này mới có phía sau rất nhiều sự tình."),i("br"),i("br"),n._v("“Thân là lục tinh danh sư, biết rõ xung kích Trục Không cảnh cần tuyệt đối yên tĩnh, hơn nữa muốn dùng ôn nhuận chân khí tẩm bổ... Kết quả lại làm cho bản thân rơi vào sống chết lựa chọn, không thể không đột phá bẫy rập chi!”"),i("br"),i("br"),n._v("“Đối tu vi khống chế không đủ, đối với đối thủ biết không rõ... Tri kỷ không đủ, biết người không rõ... Đây chính là vị gần đi đến cấp cao danh sư chuyện phải làm? Nói ngươi ‘Hữu danh vô thực’, đã coi như là nhẹ! Làm sao... Ngươi đối ta lời này có dị nghị? Lại hoặc là...”"),i("br"),i("br"),n._v("Lông mày nhăn, Dương sư không giận tự uy: “Cảm thấy... Ta vị này cấp cao danh sư, là giả?”"),i("br"),i("br"),n._v("“Ta...”"),i("br"),i("br"),n._v("Vẻ mặt đỏ lên, Mạc Cao Viễn tràn đầy xấu hổ."),i("br"),i("br"),n._v("“Có lỗi với Dương sư, là ta... Ta sai rồi!”"),i("br"),i("br"),n._v("(Xung quanh, sớm đổi mới, cầu hai tấm phiếu đề cử!)"),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-579"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-581"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);