(window.webpackJsonp=window.webpackJsonp||[]).push([[1835],{2190:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-574-thi-đau-ban-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-574-thi-đau-ban-thuong"}},[n._v("#")]),n._v(" Chương 574: Thi đấu ban thưởng")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Đi tới Vạn Quốc thành chính là vì trận này thi đấu, mặc dù cuối cùng ban thưởng, một cái linh thạch trung phẩm, đã đối với hắn không tạo được hấp dẫn, có thể chỉ cần lấy được quán quân, chịu đến Danh Sư đường coi trọng, lại tìm kiếm Hóa Phàm tứ trọng công pháp liền sẽ đơn giản không ít."),t("br"),t("br"),n._v("Còn nữa, tu vi, thực lực, đi đến hắn loại trình độ này, vạn quốc liên minh hiển nhiên đã thành ràng buộc, nhất định phải đi vào cao hơn quốc gia, thế lực mới được, mà tiến vào mười vị trí đầu, sẽ bị đưa vào Hồng Viễn danh sư học viện học tập, chính là một lần cơ hội tốt."),t("br"),t("br"),n._v("Bởi vậy, đối với lần thi đấu này, vẫn tương đối mong đợi."),t("br"),t("br"),n._v("“Trương sư, đây là ta trong mấy ngày qua sưu tập, thi đấu đứng đầu nhân tuyển danh sách, ngươi xem một chút...”"),t("br"),t("br"),n._v("Khang đường chủ đưa qua một quyển sách."),t("br"),t("br"),n._v("Nửa tháng này hắn cũng không có nhàn rỗi, để cho người ta nhiều mặt nghe ngóng, rốt cục làm ra cái này, cũng tốt để cho mình bên này tuyển thủ, có cái sớm chuẩn bị, biết người biết ta, trăm trận trăm thắng."),t("br"),t("br"),n._v("“Đa tạ!” Biết đối phương khẳng định hao tốn không ít công phu, Trương Huyền nhẹ gật đầu, thuận tay lật ra."),t("br"),t("br"),n._v("“Tiềm lực tên thứ nhất, La Tuyền, Lưu Vân tông La Hoàng tông chủ học sinh thân truyền, tứ tinh đỉnh phong danh sư, tu vi Hóa Phàm tứ trọng đỉnh phong, phụ trợ chức nghiệp là: Tứ tinh y sư, tứ tinh trận pháp sư...”"),t("br"),t("br"),n._v("“Cái tên này là tiềm lực tên thứ nhất?” Trương Huyền kỳ lạ."),t("br"),t("br"),n._v("Người khác không nhận ra cái tên này, hắn nhưng là gặp mặt qua, nửa tháng trước, hướng về phía “Dương sư” xin, dùng 150 viên linh thạch trung phẩm, đổi La Hoàng tông chủ tính mệnh."),t("br"),t("br"),n._v("Hơn nữa, căn cứ Tôn Cường nói, cái tên này còn từng bị Yêu Thần thú đặt mông ngồi vào nơi đó, kém chút không có treo ở tại chỗ."),t("br"),t("br"),n._v("Dạng này hai hàng, cũng có thể là tiềm lực tên thứ nhất?"),t("br"),t("br"),n._v("“Khụ khụ!” Nhìn thấy hắn ánh mắt cổ quái, biết hắn nghĩ cái gì, Khang đường chủ ho khan một tiếng: “Kỳ thật La Tuyền không có ngươi nghĩ tới như vậy không chịu nổi, thực lực chân thật cực mạnh, coi như ta cùng hắn so, có thể thắng hay không ra cũng còn khó mà nói...”"),t("br"),t("br"),n._v("Suy nghĩ một chút, Trương Huyền cũng gật gật đầu."),t("br"),t("br"),n._v("Cũng đúng, Yêu Thần thú người mang Long tộc huyết mạch, hắn đánh không lại bình thường, không có nghĩa là thực lực yếu! Còn ngày đó nhìn thấy, bản thân thân là “Dương sư”, khí thế chèn ép xuống, nửa bước ngũ tinh danh sư cũng không dám nói nhảm, một cái tứ tinh đỉnh phong tự nhiên cũng là không thấy được."),t("br"),t("br"),n._v("Chân chính nếu nói, vẫn là rất cường đại."),t("br"),t("br"),n._v("Chí ít, Hóa Phàm tứ trọng đỉnh phong thực lực, hắn cũng không phải là đối thủ."),t("br"),t("br"),n._v("“Đương nhiên, cái này tiềm lực thứ nhất, cũng chỉ là một loại sắp xếp mà thôi, phía trên viết mười vị trí đầu cái, đều không kém nhiều, cuối cùng ai có thể đến thứ nhất, dù ai cũng không cách nào xác nhận!”"),t("br"),t("br"),n._v("Khang đường chủ tiếp tục nói."),t("br"),t("br"),n._v("Trương Huyền tiếp tục hướng xuống nhìn lại."),t("br"),t("br"),n._v("Quả nhiên cùng đối phương nói đồng dạng, tiềm lực mười vị trí đầu, hầu như đều thuần một sắc tứ tinh danh sư, Trọc Thanh cảnh cường giả đỉnh phong."),t("br"),t("br"),n._v("Thực lực tương đồng, danh sư cấp bậc tương đương, ai có thể chiến thắng, độc chiếm vị trí đầu, đúng là không tốt lắm nói."),t("br"),t("br"),n._v("Rất mau đem thư tịch lật hết."),t("br"),t("br"),n._v("Hai mươi tám chỗ thế lực, tổng cộng phái ra năm mươi sáu vị tuyển thủ, kém nhất đều là tứ tinh sơ kỳ danh sư, cũng chính là... Hắn loại này cấp bậc."),t("br"),t("br"),n._v("Suy nghĩ một chút lần trước tuyển bạt thi đấu, chỉ có Nhược Hoan công tử một vị tứ tinh, khó trách Vạn Quốc thành mỗi lần đều cầm không lên thứ tự... Nội tình thực sự quá yếu."),t("br"),t("br"),n._v("Thi đấu cầm tới tên hay lần, mới có thể có đến tôn trọng, thu hoạch được cao hơn tài nguyên, có tài nguyên, liền có thể bồi dưỡng ra càng nhiều, lợi hại hơn danh sư... Trái lại, đồng lý."),t("br"),t("br"),n._v("Loại tình huống này tuần hoàn bên dưới, tốt càng ngày càng tốt, kém càng ngày càng kém."),t("br"),t("br"),n._v("Có thể nói, nếu như vạn quốc liên minh lại liên tục nhiều lần lấy không được tên hay lần, toàn bộ thế lực đều sẽ suy yếu, cuối cùng đều sẽ bị thế lực khác chiếm đoạt, biến mất tại dòng sông lịch sử."),t("br"),t("br"),n._v("Chính vì vậy, mỗi lần thi đấu, đều cực kỳ trọng yếu, ai cũng không dám phớt lờ."),t("br"),t("br"),n._v("“Tống Siêu?”"),t("br"),t("br"),n._v("Lật đến thứ hai mươi nhiều tên, thấy được một cái tên quen thuộc, Tống Siêu."),t("br"),t("br"),n._v("Không nghĩ tới cái này mỗi lần nhìn thấy đều bị dao động gia hỏa, tại tiềm lực bảng vị trí cũng không thấp."),t("br"),t("br"),n._v("Lật một lần, đối lần thi đấu này thế lực, xem như có nhất định lý giải."),t("br"),t("br"),n._v("“Trương sư tại đối danh sư cùng phụ trợ nghề nghiệp lý giải bên trên, không người có thể so sánh, liền tu vi kém chút, chỉ cần vận khí tốt, vào mười vị trí đầu... Vấn đề không lớn!”"),t("br"),t("br"),n._v("Khang đường chủ an ủi một câu."),t("br"),t("br"),n._v("Vị này Trương sư, tại Vạn Quốc thành Danh Sư đường biểu hiện, mười phần bất phàm, nhưng để ở nhiều như vậy thiên tài chồng bên trong, liền không có như vậy chói mắt, không nói cái khác, chỉ nói thực lực... Chính là khuyết điểm lớn nhất."),t("br"),t("br"),n._v("Thật giống như làm thành thùng gỗ tấm ván gỗ, tất cả dài, trang nước mới nhiều, một cái ngắn, cái khác lại dài cũng vô dụng!"),t("br"),t("br"),n._v("Danh sư thi đấu, khảo nghiệm là tổng hợp, là toàn bộ đều tốt, có một cái rõ ràng thiếu hụt, liền sẽ trở thành những người khác mục tiêu công kích, muốn tấn cấp càng cao danh hơn lần, gần như không có khả năng."),t("br"),t("br"),n._v("“Lần thi đấu này, khảo hạch cái gì, xác định đến không?” Biết hắn lo lắng cái gì, Trương Huyền hỏi."),t("br"),t("br"),n._v("“Khẳng định đã sớm xác định, bất quá... Khảo hạch cái gì, ta cũng không hiểu biết, là Hồng Thiển Hồng sư tự mình thiết kế, nghe nói cực kỳ công bằng, tuyệt không có khả năng xuất hiện làm việc thiên tư!” Khang đường chủ suy nghĩ một chút, nói."),t("br"),t("br"),n._v("Làm dự thi thế lực, cụ thể khảo hạch nội dung, hắn cũng là không có tư cách biết đến, nếu không, tin tức rò rỉ, tổ chức bản thân tuyển thủ dự thi, sớm thích ứng, tỷ thí cũng là không công bằng."),t("br"),t("br"),n._v("Hồng Thiển Hồng sư, là Hồng Phong đế quốc Danh Sư đường danh sư, không thuộc về thi đấu 28 chỗ thế lực, hơn nữa còn đạt đến ngũ tinh tình trạng, từ hắn chủ trì, bất kỳ người nào đều yên tâm."),t("br"),t("br"),n._v("Hồng Phong đế quốc là Huyễn Vũ đế quốc cấp dưới đế quốc, so Lưu Vân tông, Bạch Dương tông, vạn quốc liên minh nhóm thế lực, mạnh hơn rất nhiều."),t("br"),t("br"),n._v("Xem như bất nhập lưu trong đế quốc, bài danh phía trên."),t("br"),t("br"),n._v("“Mặc dù không biết cụ thể khảo hạch cái gì, nhưng ban thưởng, nhưng thả ra tin tức!” Khang đường chủ con mắt tỏa ánh sáng."),t("br"),t("br"),n._v("“Ban thưởng? Không phải một cái linh thạch trung phẩm, cùng đi vào Hồng Viễn danh sư học viện học tập cơ hội ư?” Trương Huyền nghi ngờ nhìn qua."),t("br"),n._v(" Cái này ban thưởng, trước đó cũng đã nói, không có gì có thể đáng giá ngạc nhiên."),t("br"),t("br"),n._v("“Trước đó quả thực là nói như vậy, mà lần này, đột nhiên tăng lên ban thưởng, kim ngạch bao lớn, liền xem như ta, suy nghĩ một chút đều có chút kìm nén không được!”"),t("br"),t("br"),n._v("Khang đường chủ hưng phấn nói."),t("br"),t("br"),n._v("Đêm qua, bọn hắn các thế lực lớn người phụ trách, đột nhiên nhận được Hồng Thiển Hồng sư tin tức, thi đấu ban thưởng gia tăng, khi thấy rõ ban thưởng nội dung, coi như hắn là một chỗ thế lực cường giả đỉnh phong, Danh Sư đường đường chủ, cũng không nhịn được hô hấp dồn dập, tràn đầy không thể tin được."),t("br"),t("br"),n._v("Trước kia thi đấu, ban thưởng coi như đủ phong phú, có thể cùng lần này so sánh... Quả thực cái gì cũng không tính, như là trò trẻ con!"),t("br"),t("br"),n._v("“Ồ? Đều có cái gì ban thưởng?” Trương Huyền rất hứng thú nhìn qua."),t("br"),t("br"),n._v("“Tên thứ nhất ban thưởng, là một cái linh thạch thượng phẩm!” Khang đường chủ nói."),t("br"),t("br"),n._v("“Linh thạch thượng phẩm?”"),t("br"),t("br"),n._v("Trước đó không phải nói linh thạch trung phẩm ư? Làm sao đổi thành thượng phẩm?"),t("br"),t("br"),n._v("“Linh thạch thượng phẩm, một cái tương đương với một vạn viên linh thạch trung phẩm, hơn nữa có tiền mà không mua được! Giá trị bao lớn, không thể đo lường!” Sinh hắn không biết, Khang đường chủ giải thích."),t("br"),t("br"),n._v("“Một vạn viên trung phẩm?” Trương Huyền hô hấp dồn dập."),t("br"),t("br"),n._v("Hắn nhọc nhằn khổ sở, trang bức kém chút đem bản thân giả chết, giả bộ nhổ, mới từ Lưu Vân tông mười bốn cái tông môn vơ vét 600 viên khoảng chừng linh thạch trung phẩm. Lấy được thứ nhất, liền có thể thu hoạch được một cái thượng phẩm, tương đương với một vạn... Thế này thì quá mức rồi!"),t("br"),t("br"),n._v("“Đúng vậy a! Không chỉ có như thế, còn có đi vào Hồng Viễn danh sư học viện học tập cùng tại  tu luyện ba ngày cơ hội...”"),t("br"),t("br"),n._v("Khang đường chủ tiếp tục nói."),t("br"),t("br"),n._v("“Hóa Thanh trì? Đó là cái gì?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Là Huyễn Vũ đế quốc, hoàng thất trân quý nhất bảo vật, cũng là lập quốc dựa dẫm!”"),t("br"),t("br"),n._v("Khang đường chủ mở miệng, mặt mũi hâm mộ: “Tu luyện tới Trọc Thanh cảnh có lẽ rõ ràng, trọc vì thân thể, thanh vì linh hồn. Hóa Thanh trì, chính là tẩm bổ linh hồn địa phương, ở đây tu luyện, linh hồn có thể có được thuế biến, để xung kích Hợp Linh cảnh tỷ lệ tăng lớn gấp bội!”"),t("br"),t("br"),n._v("“Nếu như ta có cơ hội ở bên trong tu luyện ba ngày, xung kích Hợp Linh cảnh tỷ lệ thành công, có thể đi đến năm mươi phần trăm trở lên! Đáng tiếc... Hóa Thanh trì là Huyễn Vũ đế quốc vật trân quý nhất, có danh ngạch giới hạn, mười năm một lần danh sư thi đấu, cũng chỉ cung cấp mấy cái danh ngạch mà thôi!”"),t("br"),t("br"),n._v("Nghe được là tu luyện linh hồn địa phương, Trương Huyền nhẹ gật đầu, âm thầm khiếp sợ."),t("br"),t("br"),n._v("Có thể để cho Khang đường chủ xung kích Hợp Linh tỷ lệ gia tăng đến năm mươi phần trăm trở lên, cái này Hóa Thanh trì... Đủ nghịch thiên."),t("br"),t("br"),n._v("Một cái linh thạch thượng phẩm, đi vào Hồng Viễn danh sư học viện học tập cơ hội, Hóa Thanh trì ba ngày..."),t("br"),t("br"),n._v("Mỗi một cái xuất ra đi cũng có thể làm cho vô số người điên cuồng, ba cái tập trung ở cùng một chỗ, đáng sợ!"),t("br"),t("br"),n._v("“Tên thứ hai đâu?”"),t("br"),t("br"),n._v("Đây là tên thứ nhất ban thưởng, cái kia tên thứ hai đâu?"),t("br"),t("br"),n._v("“Tên thứ hai còn kém rất nhiều, là 300 viên linh thạch trung phẩm, Hồng Viễn danh sư học viện học tập, cùng Hóa Thanh trì hai ngày!”"),t("br"),t("br"),n._v("Khang đường chủ nói."),t("br"),t("br"),n._v("Mặc dù chỉ kém một tên, ban thưởng nhưng tổn thất một nửa còn nhiều hơn, không nói Hóa Thanh trì thiếu một trời, chỉ nói linh thạch, còn kém nhiều lắm."),t("br"),t("br"),n._v("Một cái linh thạch thượng phẩm cùng 300 viên trung phẩm, kém ròng rã hơn ba mươi lần."),t("br"),t("br"),n._v("“Tên thứ ba là một trăm viên linh thạch trung phẩm cùng Hóa Thanh trì một ngày! Còn lại thứ tư đến hạng mười, liền không có cơ hội đi vào Hóa Thanh trì, linh thú cũng chỉ có mỗi người 10 viên, may mắn, chỉ cần là mười vị trí đầu, đều có đi Hồng Viễn danh sư học viện học tập cơ hội.”"),t("br"),t("br"),n._v("Khang đường chủ đem mười vị trí đầu ban thưởng, nói một lần."),t("br"),t("br"),n._v("“Nhìn tới... Nhất định phải đến đệ nhất!”"),t("br"),t("br"),n._v("Trương Huyền nắm đấm xiết chặt."),t("br"),t("br"),n._v("Trước đó còn cảm thấy lần thi đấu này, chỉ cần tranh cái mười vị trí đầu là tốt rồi, thành tích không quan trọng, hiện tại xem ra... Nhất định phải cầm tới thứ nhất."),t("br"),t("br"),n._v("Không nói cái khác, chỉ riêng một cái linh thạch thượng phẩm, cũng quá mê người."),t("br"),t("br"),n._v("Có thứ này, hắn chỉ sợ tu luyện tới Hóa Phàm đỉnh phong, đều không tại cần vì linh thạch mà lo lắng."),t("br"),t("br"),n._v("“Đúng vậy a, nhất định phải tranh thứ nhất, đáng tiếc... Thứ nhất nào có tốt như vậy tranh!”"),t("br"),t("br"),n._v("Nghe được hắn, Khang đường chủ cười khổ."),t("br"),t("br"),n._v("Thứ nhất, người người đều muốn, ban thưởng gia tăng, vô số người tu luyện đều vót đến nhọn cả đầu, muốn có được, nào có dễ dàng như vậy."),t("br"),t("br"),n._v("Dương sư tên tuổi, mặc dù vang dội, có thể khiến người ta kiêng kị, có thể tin tức này, cũng chỉ lưu truyền tại mười bốn thế lực tông môn, chưởng giáo bên trong, tham gia thi đấu những kia tuổi trẻ tài tuấn, cũng không cảm kích."),t("br"),t("br"),n._v("Bởi vậy, chân chính so ra, không có người lại bởi vì ngươi là Dương sư học sinh mà lưu tình."),t("br"),t("br"),n._v("Cái khác tỷ thí quả thực không có gì, chỉ khi nào... Tỷ thí tu vi, thật sự kém nhiều lắm."),t("br"),t("br"),n._v("Khang đường chủ mặc dù đối vị này Trương sư, ôm lấy rất lớn kỳ vọng, nhưng đối với hắn có thể cầm quán quân, vẫn là không có cái gì trông cậy vào."),t("br"),t("br"),n._v("“Đến chỗ rồi!”"),t("br"),t("br"),n._v("Nghe được hắn cảm khái, Trương Huyền đang muốn nói chuyện, liền nghe đến Nhược Hoan công tử thanh âm vang lên, ngẩng đầu nhìn lại, ba người đã đi tới minh chủ trước phủ quảng trường, lúc này, người ta tấp nập, chen chúc như nước thủy triều."),t("br"),t("br"),n._v("Danh sư thi đấu, đã trải qua vô số mưa gió, rốt cục... Muốn bắt đầu!"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-582"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-584"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);