(window.webpackJsonp=window.webpackJsonp||[]).push([[1839],{2194:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-578-kinh-khung-di-linh-toc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-578-kinh-khung-di-linh-toc"}},[n._v("#")]),n._v(" Chương 578: Kinh khủng Dị Linh tộc")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 578: Kinh khủng Dị Linh tộc"),t("br"),t("br"),n._v("“Sửa?”"),t("br"),t("br"),n._v("Nhìn xem đầy đất bã vụn, cùng đại pháo oanh qua phế tích, toàn thân cứng đờ, Hồng sư mắt tối sầm lại, sắp ngất đi."),t("br"),t("br"),n._v("Ngươi chỉ phá hủy một mặt tường, một cái hành lang, còn có thể tu bổ, cái này quỷ bộ dáng, để cho ta làm sao sửa?"),t("br"),t("br"),n._v("Sửa len sợi ah!"),t("br"),t("br"),n._v("Xông cái nhốt mà thôi, có thể đi tới liền đi tới, đi không ra, cũng không có gì đến cùng ta cái này Quỷ Vực huyễn thành làm sao đắc tội ngươi, liền hủy đi thành như vậy?"),t("br"),t("br"),n._v("Lại nói, lúc này mới qua vài phút? Hai phút đồng hồ? Vẫn là ba phút?"),t("br"),t("br"),n._v("Ngắn như vậy thời gian bên trong, ngay cả ta đều rất khó đi đến trung tâm, ngươi đến cùng làm sao hủy đi?"),t("br"),t("br"),n._v("Chỉ cảm thấy trái tim run rẩy, Hồng sư sắp điên rồi."),t("br"),t("br"),n._v("“Quả nhiên”"),t("br"),t("br"),n._v("Cùng Hồng sư nổi điên không giống, Khang đường chủ đám người đồng thời vỗ trán một cái."),t("br"),t("br"),n._v("Sớm đoán được vị này Trương sư có thể sẽ náo ra cái gì yêu thiêu thân, nhìn thấy mới biết được yêu thiêu thân vẫn là xem thường, đây là muốn đem danh sư thi đấu, làm không có cách nào lại làm hạ thấp đi ah!"),t("br"),t("br"),n._v("Khảo hạch tâm tính, chỗ dựa lớn nhất chính là cái này Quỷ Vực huyễn thành, ngươi bây giờ để thành đều sập, còn thế nào kiểm tra?"),t("br"),t("br"),n._v("Đi con đường của mình, để người khác không đường có thể đi. Xông tự mình nhốt, để người khác không quan hệ có thể xông!"),t("br"),t("br"),n._v("Trương sư ngươi cũng quá mãnh liệt đi!"),t("br"),t("br"),n._v("Bên này tràn đầy im lặng, Liêu Vũ Chí theo trong lúc khiếp sợ khôi phục lại, cũng thân thể nhoáng một cái."),t("br"),t("br"),n._v("Vừa rồi, Nhược Hoan công tử nói hắn lo lắng Quỷ Vực huyễn thành sẽ có nguy hiểm, tự mình cảm thấy là truyện cười, lời nói vô căn cứ, hiện tại xem ra thật sự!"),t("br"),t("br"),n._v("Đi vào không đến ba phút, đem người ta một tòa tòa thành phá hủy"),t("br"),t("br"),n._v("Ta đã biết, ngươi không phải vượt quan danh sư, là phá dỡ đội a!"),t("br"),t("br"),n._v("Chung quanh khiếp sợ lặng ngắt như tờ, vạn chúng nhìn trừng trừng ở dưới Trương Huyền, vỗ vỗ bụi đất trên người, cũng là tràn đầy bất đắc dĩ."),t("br"),t("br"),n._v("Liền muốn an tĩnh để trận pháp đình chỉ, nghênh ngang đi ra ngoài, nhưng như thế đơn giản nguyện vọng, làm sao lại khó như vậy thực hiện đâu?"),t("br"),t("br"),n._v("“Sửa khẳng định là không sửa được!”"),t("br"),t("br"),n._v("Mặc dù đau lòng nhỏ máu, tức giận đến sắp ngất đi, Hồng sư vẫn là rất nhanh khôi phục lại."),t("br"),t("br"),n._v("Bất kể nói thế nào đối phương đều là Dương sư học sinh, cũng không phải cố ý, khó thực hiện quá phận."),t("br"),t("br"),n._v("Mặc dù đau lòng Quỷ Vực huyễn thành món bảo vật này, có thể như là đã đổ sụp, nghĩ đến nhiều hơn nữa cũng vô dụng, đành phải đánh rớt răng nuốt đến trong bụng, tự nhận xui xẻo."),t("br"),t("br"),n._v("Chỉ là cái đồ chơi này khảo hạch người đầu tiên liền biến thành bộ dáng này, người phía dưới còn thế nào kiểm tra?"),t("br"),t("br"),n._v("Cái khác danh sư cũng đồng loạt nhìn qua."),t("br"),t("br"),n._v("“Quỷ Vực huyễn thành hủy hoại, tâm tính khảo hạch vậy liền tạm thời coi như không có gì, phía dưới đối chư vị dũng khí tiến hành khảo hạch!” Dừng lại một chút, Hồng sư cắn răng nói."),t("br"),t("br"),n._v("Tuyển bạt thi đấu có bốn nhốt, thiếu một nhốt, cứ việc có chút ảnh hưởng, còn không tính quá lớn, chí ít tại khống chế bên trong."),t("br"),t("br"),n._v("“Dũng khí khảo hạch?”"),t("br"),t("br"),n._v("“Cái này làm sao kiểm tra?”"),t("br"),t("br"),n._v("Nghe được muốn tiến hành cửa ải tiếp theo khảo hạch, mọi người cũng theo trong lúc khiếp sợ khôi phục lại."),t("br"),t("br"),n._v("“Khổng sư sáng lập danh sư cái nghề nghiệp này ý nghĩa, chắc hẳn không cần ta nhiều lời, chư vị cũng đều rõ ràng!”"),t("br"),t("br"),n._v("Hồng sư đạo."),t("br"),t("br"),n._v("“Vì để cho nhân loại càng thêm cường đại, tốt hơn đối kháng Dị Linh tộc, đối kháng tai nạn!” Một vị danh sư mở miệng nói."),t("br"),t("br"),n._v("Khổng sư sáng lập danh sư dự tính ban đầu, là muốn người người như rồng, chỉ có bản thân mạnh mẽ, mới có thể càng thêm phồn vinh, hưng thịnh, toàn bộ chủng tộc càng ngày càng thịnh vượng."),t("br"),t("br"),n._v("“Không sai, mấy vạn năm trước, Dị Linh tộc tàn phá bừa bãi, xâm nhập nhân loại, trong thiên địa sinh linh đồ thán, vì cứu vãn sinh mệnh tại thủy hỏa, Khổng sư mới chế định danh sư cái nghề nghiệp này, trải qua hơn vạn năm phát triển, có trước nay chưa có huy hoàng!”"),t("br"),t("br"),n._v("Hồng sư gật gật đầu, nhìn về phía mọi người, ánh mắt bay lên: “Dũng khí khảo hạch, rất đơn giản, chính là tận danh sư chức trách, làm chuyện nên làm, một mình đối mặt Dị Linh tộc!”"),t("br"),t("br"),n._v("“Dị Linh tộc?”"),t("br"),t("br"),n._v("“Cái chủng tộc này không phải đã diệt tuyệt ư? Chẳng lẽ còn có sống?”"),t("br"),t("br"),n._v("Tất cả mọi người là ngẩn ngơ."),t("br"),t("br"),n._v("Dị Linh tộc, mấy vạn năm trước liền bị Khổng sư dẫn người xua đuổi, theo đại lục ở bên trên đã mất đi tung tích, đối mặt cái chủng tộc này có ý tứ gì?"),t("br"),t("br"),n._v("“Dị Linh tộc là theo vạn năm liền bị xua đuổi, nhưng đại lục ở bên trên còn có một số còn sống, bọn họ ngụy trang thành nhân loại, giấu ở trong đám người!”"),t("br"),t("br"),n._v("Hồng sư lông mày ngưng trọng, bàn tay lớn một chút."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Phía sau sân khấu, bị mê vụ bao phủ một khối khác, hiển lộ ra, là cái cự đại nhà đá."),t("br"),t("br"),n._v("Mặc dù cửa phòng đóng chặt, không nhìn thấy bất kỳ vật gì, nhưng có thể từ trong đó cảm nhận được nồng đậm sát ý cùng hùng hồn khí tức không ngừng truyền đến, phảng phất chỉ cần có người tới gần, liền sẽ đem nó xé rách thành bụi phấn."),t("br"),t("br"),n._v("“Hung mãnh quá khí tức, chẳng lẽ trong nhà đá chính là Dị Linh tộc?”"),t("br"),t("br"),n._v("“Nghe nói Dị Linh tộc hung tàn thành tính, đem nhân loại xem như sâu kiến chém giết trên người mang theo cực kỳ nồng đậm sát khí, nhân loại bình thường, đừng nói giết hắn, khả năng liên sát chọc tức đều không thể chống chọi, liền sẽ bị tại chỗ dọa ngất!”"),t("br"),t("br"),n._v("“Cách nhà đá, đều để lòng người kinh, chân chính gặp mặt, lại nên mạnh cỡ nào?”"),t("br"),t("br"),n._v("Cảm nhận được trong nhà đá truyền đến khí tức, tất cả mọi người là biến sắc."),t("br"),t("br"),n._v("Quá kinh khủng!"),t("br"),t("br"),n._v("Tới gần sân khấu, hầu như đều là tứ tinh danh sư dạng này cường giả, còn không có mở cửa, liền cảm thấy sát lục chi khí, để cho người ta chân cẳng như nhũn ra, bên trong đang đóng Dị Linh tộc lại hẳn là lợi hại?"),t("br"),t("br"),n._v("“Dị Linh tộc tàn nhẫn giảo hoạt, không chỉ giết người, vì mạnh mẽ, có khi ngay cả mình tộc nhân đều thôn phệ! Trong mắt bọn hắn, nhân loại chỉ là cấp thấp sinh mệnh, con mồi mà thôi!”"),t("br"),n._v(" Hồng sư hừ một tiếng: “Đầu này gia hỏa, giấu ở Hồng Phong đế quốc cảnh nội, giết chí ít mấy ngàn tên nhân loại vô tội, ta đuổi hắn ròng rã ba năm, mới đem bắt lấy.”"),t("br"),t("br"),n._v("“Mặc dù bây giờ bị ta dùng Kim Hồn tỏa, khóa lại căn cốt, không cách nào vận dụng lực lượng, cũng không thể chiến đấu, lại có thể đối với người tiến hành trên linh hồn áp bách, để cho người ta từ nội tâm chỗ sâu sinh ra nồng nặc nhất sợ hãi, đừng nói tứ tinh đỉnh phong danh sư, coi như một chút chuẩn ngũ tinh cường giả, cũng không dám tới chính diện giao phong.”"),t("br"),t("br"),n._v("Tựa như người gặp được lão hổ, dù là đối phương không nhúc nhích, cái trước cũng sẽ một cách tự nhiên cảm thấy chân cẳng như nhũn ra, không có bất kỳ cái gì khí lực."),t("br"),t("br"),n._v("Cái này liên lụy đến can đảm."),t("br"),t("br"),n._v("“Muốn giết hổ, liên tiếp gần lão hổ lực lượng đều không có, làm sao có thể thành công? Danh sư, đối kháng Dị Linh tộc, cũng không dám tiếp cận, coi như khảo hạch cao cấp đến đâu, cũng vô dụng!”"),t("br"),t("br"),n._v("“Khảo nghiệm dũng khí, rất đơn giản, chính là đi tới hắn trước mặt ngồi xuống, ai khoảng cách gần nhất, ai can đảm lớn nhất, lấy năm mét làm hạn định, không cách nào đến gần, hết thảy không quá quan.”"),t("br"),t("br"),n._v("Vung tay lên, Hồng sư cong ngón búng ra."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Thạch thất bức tường, lập tức nứt ra, một cái cao hơn ba mét, tựa như người khổng lồ bộ dáng dữ tợn quái thú, xuất hiện ở trước mặt mọi người."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Quái thú xuất hiện, cuồng bạo khí tức chợt tứ tán ra, tới gần sân khấu không ít người xem, còn không có kịp phản ứng, đã cảm thấy trong đầu một ngất, mắt tối sầm lại, bị tươi sống dọa ngất đi qua."),t("br"),t("br"),n._v("Không chỉ bọn họ, ngay cả trên đài Tống Siêu đám người, cũng vẻ mặt trắng bệch, bờ môi phát tím."),t("br"),t("br"),n._v("Dị Linh tộc, nô dịch nhân loại, nếu không phải Khổng sư lăng không xuất hiện, sáng chế danh sư chức nghiệp, đem nó đuổi đi, chỉ sợ hiện tại đại lục, đều muốn khuất phục tại đối phương dưới dâm uy."),t("br"),t("br"),n._v("Lần thứ nhất nhìn thấy, đối tâm linh trên đất xung kích, căn bản không phải người bình thường có thể tiếp nhận."),t("br"),t("br"),n._v("“Đây chính là Dị Linh tộc?”"),t("br"),t("br"),n._v("Trương Huyền lông mày cũng là nhảy một cái."),t("br"),t("br"),n._v("Kẻ trước mắt này, tựa như một đầu dữ tợn cự thú, toàn thân trên dưới đều mang nồng đậm mùi máu tanh, dường như cùng hắn tới gần, sẽ bị tại chỗ xé rách, khó mà sống sót."),t("br"),t("br"),n._v("Đừng nói những người khác, liền xem như hắn, muốn tới gần năm mét cũng khó khăn."),t("br"),t("br"),n._v("“Đáng sợ!”"),t("br"),t("br"),n._v("Khó trách thứ này coi như biến mất vài vạn năm, đều để Danh Sư đường không dám thư giãn, quả thực quá dọa người."),t("br"),t("br"),n._v("“Hắn bây giờ bị khốn, không cách nào động đậy, chư vị có thể đi qua, lấy nửa nén hương thời gian là hạn, không cách nào đi tới năm mét phạm vi bên trong, coi như thất bại!”"),t("br"),t("br"),n._v("Hồng sư không nói thêm lời, nhen lửa một khỏa nhang lớn, bàn tay vung lên: “Bắt đầu!”"),t("br"),t("br"),n._v("Nghe được tính theo thời gian bắt đầu, trên sân khấu mọi người, tất cả đều sắc mặt nghiêm túc, hướng về phía trước mắt đại quái vật đi tới."),t("br"),t("br"),n._v("Trương Huyền cũng theo ở phía sau, đi thẳng về phía trước, mới một bước đi ra, vẻ mặt lần nữa biến đổi."),t("br"),t("br"),n._v("Đi tới một bước, áp lực nhưng tăng lên tiếp cận gấp đôi, hung mãnh giết chóc, để hắn hai chân như nhũn ra, khó mà phụ cận."),t("br"),t("br"),n._v("Quay đầu nhìn về phía những người khác, dường như cũng không khá hơn hắn bao nhiêu, tất cả đều trên đầu đổ mồ hôi, vẻ mặt trắng bệch."),t("br"),t("br"),n._v("Lại hướng về phía trước một bước, áp lực càng thêm cực lớn, tựa như rơi vào trận bão thuyền con, bị sóng lớn tập kích, bất cứ lúc nào cũng sẽ đắm chìm, lại hình như đứng tại bên bờ vực, một cước đạp không, liền sẽ rơi vào a tì địa ngục, mãi mãi không thể thoát khỏi."),t("br"),t("br"),n._v("“Như vậy đừng nói năm mét, coi như mười mét cũng khó khăn!”"),t("br"),t("br"),n._v("Vốn cho rằng là cái rất đơn giản khảo hạch, không nghĩ tới khủng bố như vậy."),t("br"),t("br"),n._v("Dựa theo loại tình huống này, liền xem như hắn, đều rất khó trước đi vào khoảng cách cái tên này mười mét địa phương, chớ nói chi là năm mét."),t("br"),t("br"),n._v("Lần nữa tiến lên, lúc này, cùng hắn song song cũng chỉ còn lại mười mấy."),t("br"),t("br"),n._v("“Vận chuyển chân khí thử một chút!”"),t("br"),t("br"),n._v("Lần nữa hướng về phía trước, cảm giác cả người đều nhanh không chịu nổi, Trương Huyền lúc này mới trong lòng hơi động."),t("br"),t("br"),n._v("Hắn mặc dù tiến lên, lại không vận chuyển chân khí, là muốn nhìn một chút, đến cùng có thể kiên trì bao lâu, kết quả lại phát hiện, bốn bước đã là cực hạn, lại khó tiến lên mảy may."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Tinh thần khẽ động, Thiên Đạo chân khí tại thể nội chảy xuôi, lập tức liền cảm thấy cả người buông lỏng, áp lực tiêu hết."),t("br"),t("br"),n._v("Mới vừa rồi còn để hắn bất cứ lúc nào cũng sẽ sụp đổ lực lượng, giờ phút này nhưng giống gió nhẹ quất vào mặt đồng dạng, không mang đến mảy may gợn sóng."),t("br"),t("br"),n._v("“Quả nhiên cùng đoán đồng dạng, Thiên Đạo chân khí, có thể hóa giải!”"),t("br"),t("br"),n._v("Thiên Đạo chân khí, không thuộc tính, lại có thể hóa giải bất luận cái gì thuộc tính, trước đó suy đoán một khi vận chuyển chân khí, có thể sẽ triệt tiêu lực lượng, hiện tại xem ra, quả nhiên."),t("br"),t("br"),n._v("Lặng lẽ nhìn về phía người khác."),t("br"),t("br"),n._v("Những người khác cùng hắn không giống, vừa bắt đầu liền vận chuyển chân khí, có điều, vẫn như cũ khuôn mặt khó coi, cưỡng ép kiên trì."),t("br"),t("br"),n._v("Đi ở trước nhất mấy người, đã tiếp cận mười mét khoảng cách."),t("br"),t("br"),n._v("“Lợi hại!”"),t("br"),t("br"),n._v("Gặp bọn họ chân khí không cách nào hóa giải áp lực, đều có thể đi đến khoảng cách gần như thế, Trương Huyền khuôn mặt ngưng trọng, lộ ra khâm phục chi ý."),t("br"),t("br"),n._v("Khó trách mệnh danh thiên tài, Khang đường chủ đối với hắn cũng không coi trọng, những người này dũng khí, quả nhiên thật lợi hại."),t("br"),t("br"),n._v("Nếu không phải hắn có Thiên Đạo chân khí gian lận, có thể hay không có bọn họ đi xa, cũng đều không biết."),t("br"),t("br"),n._v("“Đã cái này Dị Linh tộc tản mát ra năng lực đặc thù đối với nhân loại tiến hành linh hồn áp bách, như vậy có tính không vận dụng võ kỹ?” Không có áp lực, tâm tư cũng sinh động."),t("br"),t("br"),n._v("Có thể để cho mọi người sợ hãi đến không dám tới gần, nửa bước khó đi, đối phương khẳng định là thi triển một loại nào đó trên linh hồn áp bách."),t("br"),t("br"),n._v("Đây là thủ đoạn coi như không phải võ kỹ, cũng cùng võ kỹ có tương đồng hiệu quả, đã như vậy, Thiên Đạo thư viện có phải hay không có thể nhìn ra thiếu hụt?"),t("br"),t("br"),n._v("“Thiếu hụt!” Trong lòng thấp giọng hô."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Một quyển sách xuất hiện tại trong óc."),t("br"),t("br"),n._v("(Nguyệt phiếu rớt xuống thứ tám, chư vị còn gì nữa không?)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-586"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-588"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);