(window.webpackJsonp=window.webpackJsonp||[]).push([[1841],{2196:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-580-tu-sat-đi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-580-tu-sat-đi"}},[n._v("#")]),n._v(" Chương 580: Tự sát đi!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Cùng mọi người thấy đồng dạng, Dị Linh tộc nhân thật sắp điên rồi."),t("br"),t("br"),n._v("Coi như ngươi không sợ ta, cũng không trở thành biểu hiện rõ ràng như vậy đi!"),t("br"),t("br"),n._v("Ta hựu hống hựu khiếu, toàn bộ sức mạnh đều tác dụng đến rồi, khổ cực như vậy, bất kể nói thế nào, cũng cho cái phản ứng ah!"),t("br"),t("br"),n._v("Ngươi nhìn trên đài dưới đài, đều gục xuống một mảnh, liền ngươi đứng tại cái này không nhúc nhích... Có phải hay không Thái Bạch tôn trọng đối thủ?"),t("br"),t("br"),n._v("“Ta muốn ngươi chết...”"),t("br"),t("br"),n._v("Càng nghĩ càng giận, lần nữa gào thét, cao lớn Dị Linh tộc nhân, một cắn răng, một ngụm máu tươi phun ra, con mắt trở nên càng thêm đỏ tươi, khí tức trên thân ầm vang mà lên."),t("br"),t("br"),n._v("“Nguy rồi, Dị Linh tộc nhân huyết công cuồng hóa chi pháp, sử dụng sau đó, lực lượng gấp bội!”"),t("br"),t("br"),n._v("“Huyết công cuồng hóa?”"),t("br"),t("br"),n._v("“Đúng vậy a, Dị Linh tộc nhân đơn thể mạnh mẽ không nói, càng đáng sợ là, thiêu đốt tinh huyết, đánh đổi khá nhiều, còn có thể cuồng hóa để công kích gấp bội! Ta xem qua trịnh tiêu trịnh sư viết , phía trên từng có giới thiệu, nghe nói không ít danh sư, đều chết tại bọn họ loại công kích này xuống.”"),t("br"),t("br"),n._v("“Vậy làm sao bây giờ?”"),t("br"),t("br"),n._v("“Ta cũng không biết, bất quá... Đã Hồng sư ở trong cơ thể nó lên Kim Hồn tỏa, khóa lại căn cốt, coi như cuồng hóa cũng hẳn là không vận dụng được lực lượng!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nhìn thấy đầu này đại gia hỏa, bắt đầu nổi điên, dưới đài cảm kích danh sư, tất cả đều vẻ mặt lo lắng."),t("br"),t("br"),n._v("Bình thường Dị Linh tộc nhân cứ như vậy đáng sợ, sau khi cuồng hóa nên mạnh bao nhiêu?"),t("br"),t("br"),n._v("Hồng sư cũng đem binh khí nắm trong tay, chỉ cần cái tên này dám động thủ, liền tuyệt đối không chút do dự đem nàng chém giết."),t("br"),t("br"),n._v("Còn hiện tại... Hắn đối Kim Hồn tỏa vẫn tương đối có lòng tin, chỉ cần không thể vận dụng thực lực, chỉ dựa vào đe dọa, vấn đề không lớn, dù sao, lần này là dùng hắn chuyên môn khảo hạch đảm khí, không cần thiết không có ra kết quả trước liền động thủ."),t("br"),t("br"),n._v("Lại nói, đây chính là một đầu còn sống Dị Linh tộc nhân, hao tốn như thế lớn đánh đổi mới bắt lấy, còn dự định mang đến tổng bộ, tiếp nhận ban thưởng, một khi giết, thật sự cái gì giá trị cũng không có."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Tất cả mọi người đang sợ, sốt ruột, Dị Linh tộc nhân rốt cục tích súc được rồi lực lượng, đối với Trương Huyền, chợt gào thét."),t("br"),t("br"),n._v("“Chết...”"),t("br"),t("br"),n._v("Trong tiếng hô, sát lục chi khí cùng trên linh hồn áp bách, như là cửu thiên chi sấm sét rơi xuống, toàn bộ sân khấu, đều phát ra “Kẹt kẹt” thanh âm, dường như gánh chịu không được."),t("br"),t("br"),n._v("Thanh niên phía trước phía sau danh sư, người tu luyện, ngã một mảnh, tất cả đều run lẩy bẩy, không dám đứng dậy."),t("br"),t("br"),n._v("Nhưng mà... Trương Huyền vẫn tại tại chỗ ngây người, đối với đối phương công kích, thờ ơ lãnh đạm."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Dị Linh tộc nhân sắp tức nổ tung, vùng vẫy một hồi, lần nữa một ngụm máu tươi phun ra, thân thể chợt một chặt, đi về phía trước mấy bước."),t("br"),t("br"),n._v("Hắn mặc dù bị Kim Hồn tỏa khóa lại căn cốt, không cách nào cùng người chiến đấu, lại là có thể đi lại, vừa rồi không đi, là sợ Hồng sư đem nàng chém giết, hiện tại vừa vội vừa tức, rốt cuộc không quản được nhiều như vậy."),t("br"),t("br"),n._v("“Ta muốn giết ngươi...”"),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Đi tới Trương Huyền trước mặt, cực lớn đầu lâu tới gần hắn không đến năm mươi centimet địa phương, lần nữa rống to."),t("br"),t("br"),n._v("Vừa rồi khoảng cách xa như vậy, liền để vô số người cảm nhận được dày đặc khó mà chống lại áp lực, hiện tại mặt hầu như dán tới, có vẻ càng khủng bố hơn, cho người cảm giác là bất cứ lúc nào cũng sẽ đem nàng xé nát."),t("br"),t("br"),n._v("Thế nhưng là... Trương Huyền vẫn như cũ động cũng không động, vẫn còn ở đó... Ngây người."),t("br"),t("br"),n._v("“Trương sư...”"),t("br"),t("br"),n._v("Cách đó không xa Hồng sư, bây giờ nhìn không nổi nữa."),t("br"),t("br"),n._v("Ngươi đến cùng chuyện gì xảy ra?"),t("br"),t("br"),n._v("Coi như thất thần, cũng không trở thành đi triệt để như vậy chứ?"),t("br"),t("br"),n._v("“A?”"),t("br"),t("br"),n._v("Nghe được gọi hắn tên, Trương Huyền lúc này mới kịp phản ứng."),t("br"),t("br"),n._v("Liên quan tới Dị Linh tộc nhân thiếu sót giới thiệu, thực sự quá nhiều, quá dọa người, quá làm cho hắn rung động, coi như hắn kiến thức rộng rãi, vừa nhìn phía dưới, cũng không chịu được khiếp sợ tột đỉnh, khó mà tự kềm chế."),t("br"),t("br"),n._v("Còn đối phương lại rống lại gọi... Vừa rồi cái tên này đều gọi đã nửa ngày, còn tưởng rằng đang hù dọa người khác, căn bản không để ý."),t("br"),t("br"),n._v("Ý thức theo thư tịch bên trên rời khỏi, đột nhiên phát hiện một cái dữ tợn khuôn mặt xuất hiện ở trước mắt, giật nảy mình, một cái tát quất tới."),t("br"),t("br"),n._v("“Đùng!”"),t("br"),t("br"),n._v("Một tiếng vang giòn, vang vọng toàn bộ sân khấu."),t("br"),t("br"),n._v("Tất cả mọi người mộng tại nguyên chỗ."),t("br"),t("br"),n._v("Dị Linh tộc nhân cũng trừng to mắt, sắp điên rồi."),t("br"),t("br"),n._v("Vì thi triển linh hồn uy áp, điều động trên người sát lục chi khí, hắn mệt thổ huyết, thậm chí vì để cho đối phương chịu đau khổ, giãy dụa lấy đi tới trước mặt... Kết quả, nhân gia đánh rắm không có, còn quất hắn một bạt tai!"),t("br"),t("br"),n._v("“Ngươi cái hèn mọn nhân loại, chết!”"),t("br"),t("br"),n._v("Một tiếng bạo rống, Dị Linh tộc nhân lại không quản trên người có hay không mang theo Kim Hồn tỏa, thô to nắm đấm chợt liền đối trước mắt thanh niên đập xuống."),t("br"),t("br"),n._v("Hô hô!"),t("br"),n._v(" Tiếng gió phần phật."),t("br"),t("br"),n._v("Căn cốt bị khóa, hắn phát huy thực lực, chỉ là đơn thuần thân thể, không tính là gì, có điều, Dị Linh tộc trời sinh mạnh mẽ, coi như nhục thể, bình thường Hóa Phàm tam trọng người cũng khó có thể chống chọi."),t("br"),t("br"),n._v("“Nghiệt súc, tự tìm cái chết...”"),t("br"),t("br"),n._v("Thấy hắn dám động thủ, Hồng sư lông mày giương lên, trường kiếm xoay một cái, liền muốn ra tay, còn không có động, chỉ thấy Trương Huyền thân thể nhoáng một cái, tránh thoát công kích, nhìn lại, mặt mũi không vui."),t("br"),t("br"),n._v("“Ngươi nói ai hèn mọn? Láo xược!”"),t("br"),t("br"),n._v("Hất lên ống tay áo, Trương Huyền ngạo nghễ đứng thẳng, danh sư trường bào tại gió hiu hiu bên dưới, bay phất phới, cho người ta một loại thế ngoại cao nhân cảm giác."),t("br"),t("br"),n._v("“Dị Linh tộc cùng Trần Giáp thú hậu đại, một thân huyết mạch bác mà không thuần, lực lượng tạp mà không tinh, thi triển linh hồn áp bách cùng công kích, càng là trăm ngàn chỗ hở, ngay cả ta một người bình thường đều không đối phó được, ngươi... Có tư cách gì nói nhân loại hèn mọn?”"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Không nghĩ tới trước mắt vị này, một cái liền hô lên huyết mạch của hắn lai lịch, Dị Linh tộc nhân sững sờ phía dưới, quên tiếp tục ra tay."),t("br"),t("br"),n._v("“Cái gì ngươi không ngươi?”"),t("br"),t("br"),n._v("Bàn tay vác tại phía sau, Trương Huyền chậm rãi đi tới: “Trên người mang theo tạp chủng huyết mạch ngược lại cũng thôi, Dị Linh tộc trời sinh nắm giữ huyết mạch truyền thừa, ngươi lại không lấy được, vì mạnh mẽ, học tập ngôn ngữ nhân loại, tu luyện nhân loại công pháp, lại nói nhân loại hèn mọn, ta muốn hỏi ngươi, ai cho ngươi tự tin?”"),t("br"),t("br"),n._v("“Ta...” Dị Linh tộc nhân lui về phía sau một bước."),t("br"),t("br"),n._v("“Cái này, cái này... Tình huống gì?”"),t("br"),t("br"),n._v("Nhìn thấy Trương sư mới vừa rồi còn đang ngẩn người, thanh lọc tỉnh lại, liền mở miệng quát lớn, tất cả đều từng cái mắt lớn trừng mắt nhỏ, như là gặp quỷ."),t("br"),t("br"),n._v("Ngay cả Hồng sư cũng đầy đầu sương mù... Cái tên này còn không phải thuần túy Dị Linh tộc?"),t("br"),t("br"),n._v("Ngay cả bắt hắn chính mình cũng không biết, vị này Trương sư là như thế nào biết đến? Lại thế nào nhìn ra, còn người mang Trần Giáp thú huyết mạch?"),t("br"),t("br"),n._v("Mấu chốt nhất là... Dị Linh tộc có truyền thừa sự tình, hắn thân là ngũ tinh danh sư, đều không biết, cái tên này, nhưng có thể kỹ càng nói tới... Chẳng lẽ là Dương sư dạy?"),t("br"),t("br"),n._v("Cũng đúng, chỉ sợ cũng chỉ có bát tinh trở lên Dương sư, mới có thể biết những này Dị Linh tộc bí mật, hắn coi như đột phá ngũ tinh, lại không tiếp thụ qua cấp bậc cao hơn Danh Sư đường tẩy lễ, đối với mấy cái này sự tình, vẫn là biết không nhiều."),t("br"),t("br"),n._v("“Ngươi sinh ra ở Hồng Phong đế quốc Vong Nguyên sơn một cái sơn thôn, bị nhân loại nhận nuôi, nuôi dưỡng thành người. Hai mươi tuổi trước, loại trừ kích cỡ rất lớn, cùng nhân loại không có cái gì khác nhau, cũng không biết tự mình đặc thù, kết quả, bởi vì một cái việc vặt, chém giết thôn dân, bị cha mẹ nuôi trách cứ, dưới cơn nóng giận kích hoạt lên trong cơ thể dị linh huyết mạch, đem bọn hắn sát hại!”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua: “Ta nói đúng không?”"),t("br"),t("br"),n._v("“...” Dị Linh tộc nhân thân thể run rẩy."),t("br"),t("br"),n._v("Thật sự là hắn là bị nhân loại nuôi dưỡng lớn lên, thâm sơn cùng cốc chỗ, thôn dân đều rất thuần phác, sinh hoạt cũng mười phần túng quẫn."),t("br"),t("br"),n._v("Từ nhỏ mặc dù thể trạng cực lớn, người nhà nhưng đối đãi hắn vô cùng tốt, coi như lại nghèo, đều không có để hắn ăn không no, bỏ đói một bữa bụng."),t("br"),t("br"),n._v("Nhớ kỹ có lần, tuyết lớn liên tục xuống năm ngày, không có một chút đồ ăn, phụ thân đạp tuyết xa nhà đi săn, kết quả trọng thương mà về, chỉ bắt lấy một cái rất bình thường chương vĩ thỏ, cùng hái một chút bình thường rau dại rễ, con thỏ không lớn, căn bản không đủ một nhà mấy miệng người ăn, cha mẹ nuôi trong nhà, không chỉ có hắn, còn có một cái nhỏ hơn mấy tuổi muội muội."),t("br"),t("br"),n._v("Vì không cho hắn bị đói, hầu như toàn bộ con thỏ, đều cho hắn ăn, những người khác chỉ ăn một chút đồ ăn rễ."),t("br"),t("br"),n._v("Bây giờ suy nghĩ một chút, đều cảm thấy rất đáng thương."),t("br"),t("br"),n._v("“Bọn họ tuy là ngươi cha mẹ nuôi, chủng tộc không giống, nhưng đối với hắn lại là hết sức yêu chiều, xem như tự mình thân sinh hài tử, mà ngươi đây? Là thế nào báo đáp? Huyết mạch kích hoạt, dưới cơn nóng giận, đem trọn cái thôn tàn sát sạch sẽ, đem trước kia bạn bè huynh đệ, cha mẹ người thân... Tất cả đều ăn!”"),t("br"),t("br"),n._v("Trương Huyền sắc mặt tái xanh, ánh mắt như điện: “Không chỉ có như thế, còn đem toàn bộ thôn trang một mồi lửa đốt thành tro bụi... Ta muốn hỏi ngươi, làm ra những việc này, trong lòng của ngươi có thể từng hối hận, có thể từng bất an qua?”"),t("br"),t("br"),n._v("Dị Linh tộc nhân lần nữa run lên."),t("br"),t("br"),n._v("Hắn mặc dù kế thừa Dị Linh tộc nhân hung tàn, dù sao tại trong nhân loại lớn lên, hiểu được lễ nghĩa liêm sỉ, những sự tình này như đồng tâm bên trong một cây gai, không giây phút nào không kích thích lấy hắn, để hắn không dám quay đầu."),t("br"),t("br"),n._v("“Hối hận, ta là có chút hối hận, vậy thì thế nào, là bọn họ... Là bọn họ nhục mạ ta, nói ta dáng dấp không có hình người, là súc sinh, ta mới ngược lại đánh chết, có cái gì sai lầm?”"),t("br"),t("br"),n._v("Một cắn răng gầm thét lên."),t("br"),t("br"),n._v("“Không sai? Vậy thì tốt, thảm sát dưỡng phụ dưỡng mẫu, diệt đi thôn trang phía sau, vì nhét đầy cái bao tử, đi tới gần nhất Hướng Dương thành, bởi vì thân hình cao lớn, hình thể cường tráng, gia nhập nơi đó một cái cường hào gia tộc. Đối phương truyền cho ngươi tu luyện công pháp, cho ngươi cơm ngon áo đẹp, thậm chí còn vì ngươi cưới vợ, kết quả ngươi làm sao đối bọn hắn?”"),t("br"),t("br"),n._v("Trương Huyền thanh âm băng lãnh: “Vừa ý nhân gia tiểu thư, đem nàng cưỡng gian rồi giết chết, vì phòng ngừa sự tình bại lộ, đem một nhà hơn bảy mươi miệng toàn bộ giết sạch, đáng thương vị kia lão viên ngoại, trước khi chết đều không thể tin được, ngươi sẽ làm ra loại sự tình này! Cái này... Ta nói không sai chứ!”"),t("br"),t("br"),n._v("Đầu này Dị Linh tộc, thi triển công kích linh hồn cùng áp bách, tồn tại rất nhiều lỗ thủng, thiếu hụt đúng là hắn trong lòng những chuyện này."),t("br"),t("br"),n._v("Trong lòng mang theo xoắn xuýt cùng hối hận, mới khiến cho tâm hắn cảnh càng ngày càng tệ, không cách nào phát huy lực lượng mạnh nhất."),t("br"),t("br"),n._v("Nếu không, Dị Linh tộc đáng sợ như thế, coi như Hồng sư là ngũ tinh danh sư, phối hợp nhiều vị danh sư, hợp lực ra tay, muốn bắt lấy, cũng không dễ dàng như vậy."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Dị Linh tộc nhân lần nữa run lên."),t("br"),t("br"),n._v("“Danh Sư đường phái người bắt ngươi, bị ngươi chém giết hơn ba mươi vị danh sư, thậm chí bị Hồng sư bắt lấy, còn chết cũng không hối cải, mưu toan chạy trốn, giết người hấp thu lực lượng... Liền loại người như ngươi không người, súc sinh không súc sinh tạp chủng, thế mà còn dám nói nhân loại hèn mọn, ta liền muốn hỏi một chút, ngươi... Có tư cách gì!”"),t("br"),t("br"),n._v("Không đợi hắn mở miệng, Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("Thanh âm nhanh chóng, mang theo chất vấn, cho người ta tâm hồn áp bách."),t("br"),t("br"),n._v("“Thân mang Dị Linh tộc huyết mạch, lại không lấy được Dị Linh tộc tán thành; Sinh hoạt tại thế giới loài người, nhưng giết thân giết hữu, bị vạn người phỉ nhổ, ta chỉ muốn hỏi ngươi, còn mặt mũi nào sống trên đời?”"),t("br"),t("br"),n._v("Hất lên ống tay áo, Trương Huyền nghĩa chính ngôn từ, thanh âm bên trong mang theo đặc hữu ý vị: “Tự sát đi!”"),t("br"),t("br"),n._v("Dị Linh tộc nhân thân thể nhoáng một cái, ánh mắt trống rỗng: “Đúng vậy a, ta có cái gì mặt mũi còn sống!”"),t("br"),t("br"),n._v("Đùng!"),t("br"),t("br"),n._v("Bàn tay nâng lên, trực tiếp đập vào trên trán, ngay sau đó Dị Linh tộc nhân thân thể to lớn nhoáng một cái, ngã trên mặt đất, đoạn tuyệt hô hấp."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-588"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-590"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);