(window.webpackJsonp=window.webpackJsonp||[]).push([[1842],{2197:function(n,h,i){"use strict";i.r(h);var g=i(44),t=Object(g.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-50-ngang-hang-luan-giao"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-50-ngang-hang-luan-giao"}},[n._v("#")]),n._v(" Chương 50: Ngang hàng luận giao")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v(" “Rất đơn giản!” Trương Huyền không biết mọi người trong đầu suy nghĩ cùng chấn động, nhẹ nhàng nở nụ cười: “Xích Hùng này loại Man Thú, phòng ngự tuy rằng vô địch, cả người nhưng là lưu tuyến hình, dường như một thanh kiếm sắc, am hiểu nhanh công, lấy tốc độ thắng đối thủ, mà không phải dựa vào sức mạnh, dày nặng, đối với kẻ địch mạnh mẽ nghiền ép! Từ điểm đó có thể đoán ra, hơi thở của nó là sắc bén dường như lợi kiếm, mà không phải dày nặng khác nào Đại Sơn! Họa một cái Man Thú, lại ngay cả khí chất đều tính sai, không phải ước đoán là cái gì?” "),i("br"),i("br"),n._v(" Lục Trầm đại sư thân thể loáng một cái."),i("br"),i("br"),n._v(" Đối phương không phải nói sai, mà là nói quá đúng rồi!"),i("br"),i("br"),n._v(" Trước hắn vẫn cho rằng, Xích Hùng phòng ngự vô địch, nên dày nặng như núi, trải qua thiếu niên vừa đề tỉnh, nhất thời nghĩ tới, một quyển sách trên tựa hồ giới thiệu qua, này loại Man Thú, tốc độ cực nhanh, giết địch cùng vô hình, chính vì như thế, rất ít người gặp chân chính khuôn mặt!"),i("br"),i("br"),n._v(" Thư họa đại sư vẽ tranh, kiêng kỵ nhất chính là khí chất tính sai, này đều sai lầm, lại vật quý giá cũng là đã biến thành rác rưởi, hắn chỉ nói không sai, không nói lời quá đáng, đã rất cho mặt mũi."),i("br"),i("br"),n._v(" “Tốt, tốt!” Liên tiếp hai tiếng tán dương, Lục Trầm đại sư hưng phấn sắc mặt đỏ lên."),i("br"),i("br"),n._v(" Nếu như một tấm họa, có thể là may mắn, trùng hợp, liên tục hai tấm, đều nhìn ra, hơn nữa gãi đúng chỗ ngứa, hắn đã biết người thiếu niên trước mắt này, mặc dù coi như tuổi trẻ, nhưng là thực sự thư họa đại sư!"),i("br"),i("br"),n._v(" Trình độ so với hắn chỉ cao chớ không thấp hơn!"),i("br"),i("br"),n._v(" Bằng hữu dễ kiếm, Tri Âm khó tìm kiếm, hắn có thể trở thành là đế sư, thư họa một đường, ở toàn bộ Thiên Huyền vương quốc, không người có thể đưa ra phải, bình thường cho người khác giảng giải, những người này đều là thuận miệng ứng phó, cực lực khen, không ai chân chính có thể xem hiểu, chớ nói chi là vạch ra khuyết điểm!"),i("br"),i("br"),n._v(" Cũng bởi vì như thế, mới phát giác được khí phách tiêu điều, thường thường mở rộng môn hộ, đốc xúc hiếu học vãn bối, hy vọng có thể có người nối nghiệp."),i("br"),i("br"),n._v(" Giờ khắc này, gặp phải một cái có thể nói ra khuyết điểm, vạch ra sai lầm, tương đương với gặp Tri Âm nhân, làm sao không hưng phấn?"),i("br"),i("br"),n._v(" Không tại chỗ nhảy dựng lên, coi như ý hắn chí lực kiên cường, khắc chế vô cùng tốt."),i("br"),i("br"),n._v(" Nhìn thấy đại sư hưng phấn dáng dấp, Hoàng Ngữ cùng Bạch Tốn hai người liếc nhau một cái, lại không trước đấu võ mồm, tất cả đều con ngươi trợn tròn, sắp rơi xuống đất."),i("br"),i("br"),n._v(" Vị đại sư này, bọn họ từ nhỏ liền nhận thức, xưa nay đều là Thái Sơn vỡ lâm mặt không thay đổi, bất kỳ cái gì sự tình đều không để ở trong lòng, lần trước Quốc vương bệ hạ chuyên môn phái người đưa tới cực địa Dạ Minh Châu, giá trị Liên Thành, nhưng hắn đều không có liếc mắt nhìn, để hạ nhân tiện tay thu vào gian phòng!"),i("br"),i("br"),n._v(" Thiếu niên tùy tiện trả lời một vấn đề cứ như vậy... Lẽ nào hắn nói là sự thật?"),i("br"),i("br"),n._v(" Nghĩ tới đây, hai người đồng loạt nhìn về phía cách đó không xa thiếu niên, có thể bất luận nhìn thế nào, đều hết sức bình thường, không có bất kỳ cái gì chỗ đặc thù."),i("br"),i("br"),n._v(" “Tiểu Ngữ, còn không mau nhanh giới thiệu cho ta một hồi vị công tử này?” "),i("br"),i("br"),n._v(" Chính đang nghi hoặc, liền nghe đến Lục Trầm đại sư câu hỏi vang lên."),i("br"),i("br"),n._v(" “Giới thiệu? Hắn...” Bị hỏi dò, Hoàng Ngữ sửng sốt một chút, lúc này mới nhớ tới, căn bản không có hỏi tên của đối phương, khuôn mặt đỏ lên, không nhịn được gãi đầu một cái, nhìn về phía Trương Huyền: “Ai, đúng, ngươi gọi là cái gì nhỉ...” "),i("br"),i("br"),n._v(" Nghe nói như thế, ngồi ở một bên Bạch Tốn một mặt táo bón dáng dấp."),i("br"),i("br"),n._v(" Vừa nãy hắn còn ghen ăn gần chết, lấy vì là nữ thần của mình bị người khác nhanh chân đến trước, hận không thể cuồng đánh thiếu niên này một trận, bây giờ mới biết... Hoàng Ngữ liền tên hắn cũng không biết!"),i("br"),i("br"),n._v(" Sớm biết như vậy, sinh cái gì khí a!"),i("br"),i("br"),n._v(" Bất quá, của hắn xoắn xuýt còn không có kết thúc, liền nghe đến Lục Trầm đại sư tức giận âm thanh vang lên: “Cái gì ngươi nha, ta nha, không hề có một chút quy củ! Vị tiểu huynh đệ này, cùng ta ngang hàng luận giao, sau đó muốn xưng hô đại sư!” "),i("br"),i("br"),n._v(" “Ngang hàng luận giao?” "),i("br"),i("br"),n._v(" Lần này, không riêng Hoàng Ngữ, Bạch Tốn hôn mê, liền ngay cả một bên quản gia thành bá cũng không khỏi ngẩn ngơ, như là nhìn quái vật nhìn về phía Trương Huyền."),i("br"),i("br"),n._v(" Lục Trầm đại sư thân phận gì?"),i("br"),i("br"),n._v(" Trầm Truy bệ hạ lão sư, chân chính đế sư!"),i("br"),i("br"),n._v(" Toàn bộ vương quốc đều không ai dám cùng hắn ngang hàng, bởi vì một khi làm như vậy, liền biểu thị là Quốc vương bệ hạ trưởng bối!"),i("br"),i("br"),n._v(" Như vậy thân phận người, bây giờ lại muốn cùng một cái không đủ hai mươi tuổi thiếu niên ngang hàng luận giao? Không nghe lầm chứ?"),i("br"),i("br"),n._v(" “Ta không nghe?” "),i("br"),i("br"),n._v(" Thấy hai người đờ ra, Lục Trầm lần thứ hai quát lớn."),i("br"),i("br"),n._v(" “Rõ!” Bạch Tốn, Hoàng Ngữ vội vã cúi đầu khom người: “Xin chào đại sư!” "),i("br"),i("br"),n._v(" “Không cần khách khí!” Không nghĩ tới vị này Lục Trầm khách khí như thế, Trương Huyền bất đắc dĩ lắc đầu: “Tại hạ Trương Huyền, chỉ là đánh bậy đánh bạ xem hiểu đại sư bức tranh mà thôi, không xứng với đại sư tên gọi!” "),i("br"),n._v(" “Hóa ra là Trương Huyền tiểu huynh đệ, lời không thể nói như vậy, quy củ chính là quy củ! Ngươi một chút có thể vạch ra sai lầm của ta chỗ, thuyết minh đối với thư họa có rất sâu nghiên cứu, nhãn lực siêu quần, nếu như này cũng không đáng xưng là, ta người đại sư này, chẳng phải cũng chỉ là hư danh?” Lục Trầm nói."),i("br"),i("br"),n._v(" Trương Huyền cười gượng."),i("br"),i("br"),n._v(" Hắn vạch ra sai lầm không giả, nhưng căn bản không phải dựa vào trình độ nhìn ra được, mà là dùng Thiên Đạo Thư Viện cái này máy nói dối."),i("br"),i("br"),n._v(" Không cần vật này, đừng nói nhìn ra rồi, liền vẽ là cái gì e sợ đều như hòa thượng sờ mãi không thấy tóc, một mặt mộng bức."),i("br"),i("br"),n._v(" “Tốt, đừng lại từ chối, những vãn bối này, sau đó nhìn thấy tùy tiện quát lớn là tốt rồi!” "),i("br"),i("br"),n._v(" Không biết Trương Huyền nghĩ cái gì, Lục Trầm đại sư thuận miệng nói."),i("br"),i("br"),n._v(" “Vãn bối? Tùy tiện quát mắng?” "),i("br"),i("br"),n._v(" Bạch Tốn, Hoàng Ngữ nhanh muốn khóc lên."),i("br"),i("br"),n._v(" Mẹ nó, tuổi tác không chênh lệch nhiều, một hồi liền biến thành đời ông nội... Đại ca, ngươi đây cũng quá mãnh liệt đi!"),i("br"),i("br"),n._v(" Không để ý tới một mặt buồn bực hai người, Lục Trầm đại sư nghi ngờ nhìn sang: “Tiểu huynh đệ cùng sau lưng Tiểu Ngữ tới chỗ của ta, hẳn không phải là đi dạo đi, không biết chuyện gì, ta có thể giúp đỡ?” "),i("br"),i("br"),n._v(" “Nghe nói đại sư nơi này tàng thư rất nhiều, ta lại đây, là muốn tìm một số võ giả sáu tầng công pháp nhìn...” "),i("br"),i("br"),n._v(" Nghe hắn hỏi mục đích của chính mình, Trương Huyền vội vàng nói."),i("br"),i("br"),n._v(" Tới đây chính là vì cái này, ngàn vạn không thể bỏ qua cơ hội."),i("br"),i("br"),n._v(" “Tàng thư ta chỗ này xác thực không ít, bất quá, đều là chút thư họa tác phẩm, công pháp cũng không phải nhiều, còn võ giả sáu tầng, chỉ có mấy quyển, đều ở thư phòng của ta, đến, ta dẫn ngươi đi xem nhìn!” "),i("br"),i("br"),n._v(" Nghe được mục đích của hắn, Lục Trầm đại sư cười vuốt vuốt chòm râu, trước tiên đứng dậy."),i("br"),i("br"),n._v(" “Lão gia, thư phòng của ngươi...” Quản gia thành bá không nhịn được đi lên phía trước."),i("br"),i("br"),n._v(" Lão gia thư phòng, thu gom không ít mọi người bản đơn lẻ, mười phần quý giá, chưa bao giờ cho phép người ngoài tiến vào, lần trước Trầm Truy bệ hạ lại đây, đều bị che ở ngoài cửa!"),i("br"),i("br"),n._v(" Lúc trước một cái hầu gái không biết tình huống chạy vào đi quét tước, kết quả bị trực tiếp trượng giết!"),i("br"),i("br"),n._v(" Chính vì như thế, ở toàn bộ Lục phủ, thư phòng là tuyệt đối cấm khu bên trong cấm khu, không ai dám đi vào..."),i("br"),i("br"),n._v(" Bây giờ lại lão gia muốn dẫn một cái mới quen thiếu niên đi vào, để hắn có chút không biết làm sao."),i("br"),i("br"),n._v(" “Thư phòng của ta không cho người khác tiến vào, là sợ dính dáng tới trần tục khí tức, Trương Huyền tiểu huynh đệ là chúng ta mọi người, thư họa đại sư, có thể vào giúp ta chỉ điểm nhìn, vinh hân cực kỳ, có gì có thể ngăn trở?” "),i("br"),i("br"),n._v(" Lục Trầm đại sư sầm mặt lại."),i("br"),i("br"),n._v(" “Rõ!” Thành bá vội vã lùi tới một bên."),i("br"),i("br"),n._v(" Hắn mặc dù đối với thư họa một chữ cũng không biết, nhưng giờ khắc này cũng biết, thiếu niên mới vừa nói những cái kia khẳng định không có bất cứ vấn đề gì!"),i("br"),i("br"),n._v(" Bằng không, lão gia cũng không thể thái độ xoay chuyển một trăm tám mươi độ, như vậy hòa ái dễ gần."),i("br"),i("br"),n._v(" “Đi!” "),i("br"),i("br"),n._v(" Lục Trầm đại sư trước tiên đi về phía trước, Trương Huyền theo sau lưng, đi rồi một hồi, đi tới một cái rộng rãi gian phòng."),i("br"),i("br"),n._v(" Không hổ là Hoàng Ngữ tán dương không ngớt đại sư, trong thư phòng tàng thư quả nhiên mười phần phong phú, trên giá sách bày đầy đủ loại thư tịch, có ít nhất mấy trăm ngàn bản, vừa tiến vào trong, dường như tiến nhập một cái thư viện."),i("br"),i("br"),n._v(" Đi ở giá sách bên trong, tùy tiện nhìn lại, quả nhiên cùng đối phương nói như thế, cơ hồ tất cả đều là thư họa loại hình thư tịch, công pháp, bí tịch loại hình, cơ hồ không có."),i("br"),i("br"),n._v(" “Công pháp ở chỗ này, tổng cộng liền mấy quyển, vẫn là lúc trước tu luyện xong tùy tiện ném!” "),i("br"),i("br"),n._v(" Lục Trầm đại sư cười giới thiệu, mang theo Trương Huyền hướng về một góc nhỏ của thư phòng đi tới."),i("br"),i("br"),n._v("Convert by: Hoang Chau"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-58"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-60"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=t.exports}}]);