(window.webpackJsonp=window.webpackJsonp||[]).push([[1843],{2198:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-581-ty-thi-đoc-sach"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-581-ty-thi-đoc-sach"}},[n._v("#")]),n._v(" Chương 581: Tỷ thí đọc sách?")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Tự sát?”"),t("br"),t("br"),n._v("“Trương sư vậy mà nói... Đầu này Dị Linh tộc nhân tự sát?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Dưới đài lặng ngắt như tờ."),t("br"),t("br"),n._v("Dị Linh tộc, danh sư đối thủ, kinh khủng đến cực điểm!"),t("br"),t("br"),n._v("Trước mắt cái này, càng là Hợp Linh cảnh cường giả, ở đây loại trừ Hồng sư, hầu như không người có thể địch, kết quả... Bị hắn nói mấy câu, liền một chưởng đánh vào trên trán mình, tự sát mà chết..."),t("br"),t("br"),n._v("Muốn hay không như thế Huyền Huyễn?"),t("br"),t("br"),n._v("Không phải thật sự a?"),t("br"),t("br"),n._v("“Là... Sư ngôn thiên bẩm!”"),t("br"),t("br"),n._v("“Đây không phải là giảng bài mới có thể thi triển năng lực ư?”"),t("br"),t("br"),n._v("“Chỉ cần nói nội dung, chính xác không sai, phối hợp tâm cảnh khắc độ, liền có thể đem người mê hoặc, thân thể chịu hắn ngôn ngữ ảnh hưởng, làm ra liền chính hắn đều không thể khống chế sự tình! Xem ra, Trương sư mới vừa nói những cái kia, cũng là đúng, đầu này Dị Linh tộc mới xúc động phẫn nộ phía dưới, đem bản thân chụp chết!”"),t("br"),t("br"),n._v("“Sư ngôn thiên bẩm có thể để người ta tự sát? Cái kia chẳng phải... Chỉ cần tìm được chính xác sự kiện, danh sư có thể tùy tiện giết người?”"),t("br"),t("br"),n._v("“Không dễ dàng như vậy, không chỉ nói sự tình chính xác, còn muốn phù hợp đối thoại người nội tâm, mới có thể để cho hắn trúng chiêu, nếu không, không có một chút tác dụng nào! Thật giống như ngươi khẩn cấp tu luyện, sư ngôn thiên bẩm dẫn động ngươi nội tức tự động vận chuyển, trong tiềm thức sẽ không kháng cự! Đầu này Dị Linh tộc nhân, mặc dù hung tàn thành tính, dù sao cũng là nhân loại nuôi dưỡng lớn lên, hiểu được nhân tình lễ nghi, huyết mạch cuồng hóa giết nhiều người như vậy, nội tâm cũng đầy là sám hối. Cho nên, Trương sư mới có thể sử dụng nói để hắn tự sát!”"),t("br"),t("br"),n._v("“Thì ra là thế...”"),t("br"),t("br"),n._v("“Bất quá, có thể đem Hợp Linh cảnh Dị Linh tộc nhân mê hoặc, tứ tinh đỉnh phong tâm cảnh khắc độ cũng làm không được đi! Cái kia... Trương sư tâm cảnh khắc độ, đến cùng đạt đến cảnh giới gì? 15. 0? 16. 0?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Một lát sau, mọi người dưới đài hiểu được, toàn bộ xôn xao."),t("br"),t("br"),n._v("Đầu này Dị Linh tộc nhân, cam tâm tình nguyện tự sát, chết ở trước mặt mọi người, hiển nhiên trúng sư ngôn thiên bẩm."),t("br"),t("br"),n._v("Nhưng... Tứ tinh danh sư cực hạn, nhiều nhất có thể mê hoặc Hóa Phàm tứ trọng cường giả, ngũ trọng liền gần như không có khả năng."),t("br"),t("br"),n._v("Trước mắt cái này Dị Linh tộc nhân, linh hồn vốn là mạnh mẽ, vẫn như cũ bị hắn mê hoặc, cái kia... Vị này Trương sư tâm cảnh khắc độ đến cùng đạt đến cái gì trình độ?"),t("br"),t("br"),n._v("Không khỏi quá kinh khủng đi!"),t("br"),t("br"),n._v("“Cứ thế mà nói... Chết rồi?”"),t("br"),t("br"),n._v("Thấy cảnh này, Khang đường chủ đám người lệ rơi đầy mặt."),t("br"),t("br"),n._v("Vốn đang cho rằng Trương Huyền đứng tại chỗ ngây người, chính là kiếm chuyện, náo ra yêu thiêu thân, hiện tại xem ra, bọn họ vẫn là quá đơn thuần!"),t("br"),t("br"),n._v("Qua hai quan, hủy hai quan, khiến người khác không đường có thể đi..."),t("br"),t("br"),n._v("Đây mới là mục đích của hắn, mới là kiếm chuyện..."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Dưới đài khiếp sợ, trên đài Nhược Hoan công tử, La Tuyền đám người, cũng là ngậm miệng không nói gì."),t("br"),t("br"),n._v("Đầu này đại gia hỏa đáng sợ, bọn họ tận mắt thấy, đối mặt khủng bố như thế sát khí, liền chống lại đều làm không được, cái tên này không những không có việc gì, còn đem người cứ thế mà nói chết..."),t("br"),t("br"),n._v("Các ngươi xác định, cái tên này thật so với chúng ta nhỏ mười tuổi, cùng chúng ta cùng một cái cấp bậc?"),t("br"),t("br"),n._v("Mà không phải lục tinh, thất tinh danh sư chạy tới giả mạo?"),t("br"),t("br"),n._v("Trước đó còn cảm thấy không nhìn trúng vị này Trương Huyền Liêu Vũ Chí, lúc này cũng dọa đến bờ môi run rẩy, một câu nói nhảm cũng không dám lại nói."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Không đi quản ý nghĩ của mọi người, nhìn xem nằm dưới đất cực lớn thi thể, Trương Huyền lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Vừa rồi sở dĩ rơi vào “Ngây người”, cũng bởi vì những này việc ác thực sự quá mức khiếp sợ, vốn là nổi giận, thấy cái tên này thế mà không biết hối cải, chạy tới nhục mạ nhân loại, cũng nhịn không được nữa, thi triển sư ngôn thiên bẩm đem nó chém giết!"),t("br"),t("br"),n._v("Hắn hiện tại tâm cảnh khắc độ, đã đạt đến 16. 1, so một chút ngũ tinh danh sư đều mạnh hơn, dù vậy, mê hoặc một đầu Dị Linh tộc nhân, đồng thời để hắn tự sát, vẫn như cũ tiêu hao rất nhiều, nếu không phải ý niệm mạnh mẽ, đều có chút không kiên trì nổi."),t("br"),t("br"),n._v("(Bản thân chỉ có 0.1 tâm cảnh khắc độ, sử dụng hai lần kim sắc thư tịch gia tăng 10. 0, một lần sư chữ luyện tâm gia tăng 1. 0, hai lần trời nhận danh sư 3.0, một lần tâm cảnh đốn ngộ 2. 0, hợp lại vừa vặn 16. 1)"),t("br"),t("br"),n._v("Bất quá, may mắn thành công."),t("br"),t("br"),n._v("Hít sâu mấy hơi điều chỉnh thoáng cái, quay đầu nhìn về phía Hồng sư, chỉ thấy vị này trong đám người đỉnh phong nhất danh sư, khóe miệng co giật, cả người đều muốn khóc."),t("br"),t("br"),n._v("Hiện tại Hồng sư, nội tâm thật là sụp đổ."),t("br"),t("br"),n._v("Trước đó cái kia Quỷ Vực huyễn thành, xem như hắn toàn bộ tài sản, bị làm hỏng ngược lại cũng thôi... Vốn nghĩ, chỉ cần mang theo đầu này Dị Linh tộc trở về tổng bộ, đối phương phong phú ban thưởng, tất nhiên có thể bù lại, kết quả..."),t("br"),t("br"),n._v("Thời gian nháy mắt, liền bị nói chết rồi!"),t("br"),t("br"),n._v("Đại ca, ngươi còn để cho người ta có sống hay không?"),t("br"),t("br"),n._v("Ngươi đến cùng là đến tiến hành danh sư thi đấu, vẫn là tới làm loạn?"),t("br"),t("br"),n._v("Lại nói... Ta cái này dũng khí khảo hạch mới tiến hành một nửa, liền bị chặn ngang chặt đứt, phía dưới còn thế nào tiến hành?"),t("br"),t("br"),n._v("Cửa thứ nhất như vậy, nhờ vào đằng sau ba cửa ải, còn có thể nói ảnh hưởng không lớn, cửa thứ hai cũng như vậy... Ngươi là muốn cho lần này danh sư thi đấu hoàn toàn tiến hành không đi xuống ah!"),t("br"),t("br"),n._v("“Hồng sư, đầu này thi thể làm sao bây giờ?”"),t("br"),t("br"),n._v("Ngay tại buồn bực, muốn hay không đem cái tên này đá ra đi, đừng để hắn so quên đi, liền nghe đến đối phương tra hỏi thanh âm."),t("br"),t("br"),n._v("“Dị Linh tộc, hung ác tàn bạo, không có nhân tính, đem nó chém giết, cũng là một cái đại công đức, chết thì chết, không có gì!”"),t("br"),t("br"),n._v("Trong lòng nhỏ máu, trên mặt nhưng xấu hổ cười một tiếng."),t("br"),t("br"),n._v("Không nói như vậy cũng hết cách rồi, con hàng này đều treo, lại truy cứu thì có ích lợi gì? Làm không cẩn thận, lại còn để rất nhiều danh sư, cho là hắn bao che Dị Linh tộc."),t("br"),t("br"),n._v("“Được rồi, đầu này Dị Linh tộc chết, dũng khí khảo hạch, cũng không có cách nào tiếp tục tiến hành, trừ bỏ vừa rồi không chịu nổi áp lực, chạy trốn ba vị, chúng ta trên đài, còn thừa lại 53 vị danh sư, những người này, đem tiếp tục tiến hành xuống mặt khảo hạch!”"),t("br"),t("br"),n._v("Biết tiếp tục xoắn xuýt vô dụng, Hồng sư miễn cưỡng vui cười, đem so với thi đấu tiến hành tiếp."),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Nghe nói như thế, trước đó nằm rạp trên mặt đất, nằm trên mặt đất, tràn đầy bối rối danh sư, tất cả đều đứng lên, âm thầm nhẹ nhàng thở ra, lần nữa nhìn về phía cách đó không xa Trương sư, tràn đầy cảm kích."),t("br"),t("br"),n._v("Nếu không phải vị này Trương sư, chỉ sợ bọn họ bên trong sẽ có ít nhất mười cái, khó mà qua ải."),t("br"),t("br"),n._v("Mà bây giờ, nương theo Dị Linh tộc nhân chết, nguy hiểm biến mất."),t("br"),t("br"),n._v("“Tuyển bạt cửa thứ ba, vì khảo thí chư vị trí tuệ!”"),t("br"),t("br"),n._v("Không để ý tới ý nghĩ của mọi người, Hồng sư mở miệng."),t("br"),t("br"),n._v("“Trí tuệ vì người tu luyện học tập kiến thức mới vốn liếng, trí tuệ càng cao, năng lực học tập càng mạnh, đối danh sư tới nói, cũng là tiềm lực càng lớn! Cửa này khảo nghiệm, mọi người yên tâm, không có vừa rồi nguy hiểm như vậy, chỉ là... Đọc sách!”"),t("br"),t("br"),n._v("“Đọc sách?”"),t("br"),t("br"),n._v("Mọi người sững sờ."),t("br"),t("br"),n._v("“Ừm, nơi này có 56 cái giá sách, mỗi cái giá sách bên trên, đều có năm ngàn sách sách báo, là ta tư nhân cất giấu bản độc nhất, nội dung hoàn toàn tương đồng, có thể cam đoan, các ngươi tất cả cũng không có nhìn qua. Trong đó bao gồm tu luyện công pháp, võ kỹ, linh thú chăm sóc, cùng ta đối tu luyện lý giải các loại, cho chư vị một canh giờ thời gian lật xem, nhớ, lý giải!”"),t("br"),t("br"),n._v("Bàn tay vung lên, trong sân khấu ở giữa mê vụ biến mất, từng dãy giá sách xuất hiện ở trước mặt."),t("br"),t("br"),n._v("Trên giá sách bày đầy thư tịch, mỗi một cái đều có năm ngàn sách nhiều."),t("br"),t("br"),n._v("“Cửa này khảo hạch rất đơn giản, chính là những sách vở này tùy ý quan sát, một canh giờ sau, ta sẽ dùng phương thức đặc thù đối với các ngươi tiến hành khảo hạch, nhớ kỹ nội dung, lý giải nội dung càng nhiều, nói rõ trí tuệ càng cao!”"),t("br"),t("br"),n._v("Hồng sư đạo."),t("br"),t("br"),n._v("“Khảo hạch cái này?”"),t("br"),t("br"),n._v("Nghe rõ quy tắc, Trương Huyền gãi gãi đầu, tràn đầy xấu hổ."),t("br"),t("br"),n._v("So cái khác, hắn không có nắm chắc, đọc sách... Tuyệt đối có thể bỏ rơi ở đây chư vị, vô số đầu đường phố, coi như chân chính bát tinh danh sư tới, cũng giống vậy phải quỳ!"),t("br"),t("br"),n._v("Kiểm tra cái này... Có phải hay không quá thắng mà không vẻ vang gì?"),t("br"),t("br"),n._v("“Quên đi, thực sự không được, một lát nữa trả lời thời điểm... Cố ý nói sai mấy đạo là được, dù sao mục đích của ta là qua ải, lần này... Lại không có thể đem người nhà đồ vật làm hỏng!”"),t("br"),t("br"),n._v("Trương Huyền làm ra quyết định."),t("br"),t("br"),n._v("Liên tục hai quan, làm đằng sau đều không có biện pháp khảo hạch, hắn cũng đầy là bất đắc dĩ."),t("br"),t("br"),n._v("Cửa thứ nhất Quỷ Vực huyễn thành, chỉ là muốn để trận pháp đình chỉ, ai ngờ... Phân thân cái kia hai hàng, ra sức quá lớn, hắn thật không phải cố ý."),t("br"),t("br"),n._v("Còn cửa thứ hai, nhìn thấy Dị Linh tộc làm ra nhiều như vậy chuyện xấu, đổi lại ai, khẳng định cũng nhịn không được, chỉ nói là chết, xem như nhẹ, nếu không phải danh sư thi đấu, thậm chí đều muốn tự mình ra tay..."),t("br"),t("br"),n._v("Đương nhiên, tự mình ra tay, làm không cẩn thận còn không phải đối thủ, sẽ bị đối phương hoàn ngược."),t("br"),t("br"),n._v("Cái này hai quan làm tình cảnh xấu hổ, cửa thứ ba nhất định muốn cẩn thận, cùng lắm thì một lát nữa, ít nhìn chút thư, lại hoặc là... Cố ý trả lời sai lầm mấy đạo, cam đoan qua ải là có thể."),t("br"),t("br"),n._v("Khiêm tốn!"),t("br"),t("br"),n._v("Mục đích vẫn là muốn khiêm tốn một chút, không thể quá mức rêu rao!"),t("br"),t("br"),n._v("Nếu như cho bị người ta biết trong lòng của hắn ý tưởng, khẳng định sẽ làm trận khóc chết."),t("br"),t("br"),n._v("Cái này còn gọi khiêm tốn?"),t("br"),t("br"),n._v("Ngươi nhìn Hồng sư, đều sắp bị cả điên rồi, cái này muốn gọi khiêm tốn, cái khác cao điệu, đều không cần sống."),t("br"),t("br"),n._v("“Được rồi, riêng phần mình lựa chọn một cái giá sách, tính theo thời gian... Bắt đầu!”"),t("br"),t("br"),n._v("Bàn tay lớn vẫy một cái, Hồng sư lên tiếng hét lớn."),t("br"),t("br"),n._v("“Nhanh!”"),t("br"),t("br"),n._v("“Nhớ năm ngàn quyển sách? Một canh giờ? Cái này sao có thể hoàn thành!”"),t("br"),t("br"),n._v("“Ngươi ngốc ah, không cần nhìn xong, có thể nhớ kỹ mấy quyển là mấy quyển, lại không nói muốn toàn bộ nhớ kỹ...”"),t("br"),t("br"),n._v("“Cũng thế, mặc dù đi đến danh sư tứ tinh, đã có thể đã gặp qua là không quên được, có thể một canh giờ, có thể nhớ kỹ mấy trăm quyển là tốt lắm rồi, năm ngàn bản, coi như lục tinh danh sư cũng làm không được đi!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được tỷ thí bắt đầu, mọi người lại không để ý tới cái khác, thẳng tắp đối rất nhiều giá sách vọt tới."),t("br"),t("br"),n._v("Trương Huyền cũng tùy tiện tìm một cái, đi tới trước mặt."),t("br"),t("br"),n._v("Trên giá sách thư tịch, cũng là mới tinh, xem ra hẳn là sao chép không lâu, ghi chú tên cổ quái kỳ lạ, quả nhiên trước kia một bản đều không có gặp qua."),t("br"),t("br"),n._v("Hắn sưu tập không ít Tàng Thư khố, trong đầu tri thức lượng, không nói so ở đây tất cả mọi người nhiều, nhưng cũng không kém nhiều!"),t("br"),t("br"),n._v("Như thế lớn tri thức dự trữ, thế mà một bản đều không có nhìn qua, xem ra vì lần thi đấu này, Hồng sư thật đúng là dụng tâm lương khổ, nhọc lòng."),t("br"),t("br"),n._v("Ngón tay ở thư tịch bên trên nhẹ nhàng xẹt qua, thu sạch vào trong óc, lúc này mới quan sát những sách này nội dung."),t("br"),t("br"),n._v("“Ừm? Hóa Phàm tứ trọng công pháp lại có nhiều như vậy?”"),t("br"),t("br"),n._v("Vừa rồi thu vào thư tịch, cũng là vô ý thức, hiện tại kỹ càng quan sát, lập tức ánh mắt sáng lên."),t("br"),t("br"),n._v("Giá sách bên trong trưng bày trong thư tịch, lại có mấy trăm bản Hóa Phàm tứ trọng công pháp!"),t("br"),t("br"),n._v("Cảnh giới này Thiên Đạo công pháp, vốn là không có sưu tập hoàn chỉnh, có nhiều như vậy thư, có lẽ liền có thể hình thành hoàn chỉnh Trọc Thanh cảnh Thiên Đạo công pháp, từ đó thực lực tăng nhiều, để thực lực lần nữa có một bước nhảy vượt bậc."),t("br"),t("br"),n._v("“Dung hợp!”"),t("br"),t("br"),n._v("Thuận tay cầm lấy một quyển sách, làm bộ lật xem, tinh thần nhưng tập trung ở trong óc, một tiếng thấp giọng hô."),t("br"),t("br"),n._v("Thư viện một hồi lay động, trước đó sưu tập Hóa Phàm tứ trọng công pháp và mới vừa thu vào tới một đống, xuất hiện biến hóa."),t("br"),t("br"),n._v("Một bản mới tinh thư tịch xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-589"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-591"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);