(window.webpackJsonp=window.webpackJsonp||[]).push([[1845],{2201:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-583-đao-ha-chi-thu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-583-đao-ha-chi-thu"}},[n._v("#")]),n._v(" Chương 583: Đào Hà Chi Thư")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Đào Hà Chi Thư? Đó là cái gì?”"),t("br"),t("br"),n._v("“Ta cũng không biết!”"),t("br"),t("br"),n._v("“Có thể để cho Hồng sư hướng về phía tổng bộ xin, khẳng định không đơn giản!”"),t("br"),t("br"),n._v("“Đúng vậy a!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được Đào Hà Chi Thư, dưới đài rất nhiều danh sư, toàn mãn khuôn mặt mờ mịt."),t("br"),t("br"),n._v("Thứ này, tất cả mọi người chưa từng nghe qua, ngay cả Khang đường chủ, Tô sư đám người, cũng đều là không hiểu ra sao, không rõ ràng cho lắm."),t("br"),t("br"),n._v("“Chư vị đều là danh sư, chắc hẳn đều từng nghe nói qua, tri thức, không riêng gì đơn thuần văn tự cùng tin tức, cũng là có trọng lượng!”"),t("br"),t("br"),n._v("Thấy mọi người không hiểu, Hồng sư đạo."),t("br"),t("br"),n._v("“Lời này ta nghe nói qua!”"),t("br"),t("br"),n._v("“Học tri thức càng nhiều, tâm tính cũng là càng trầm ổn, khí chất cũng sẽ trở nên hoàn toàn khác biệt!”"),t("br"),t("br"),n._v("“Tri thức tích lũy càng nhiều, linh hồn sẽ trở nên càng thêm nặng nề. Truyền thuyết, năm đó Khổng sư, bàn chân một chặt, mặt đất vì đó lún xuống, hải dương vì đó nghiêng, cũng là bởi vì trong cơ thể hắn tri thức, bao gồm một giới, không người có thể so sánh!”"),t("br"),t("br"),n._v("“Đúng vậy a, về sau Khổng sư đạp không mà đi, bảy mươi hai chân truyền học sinh thực lực, cũng không kém gì hắn, lại đều không có loại kia trấn áp bát hoang lực lượng!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mọi người thảo luận."),t("br"),t("br"),n._v("Trương Huyền cũng không nhịn được gật đầu."),t("br"),t("br"),n._v("Mặc dù Khổng sư sự tích, hắn biết đến không nhiều, nhưng nhìn qua trong thư tịch, cũng từng có tri thức có trọng lượng giới thiệu."),t("br"),t("br"),n._v("Thứ này, mặc dù là một loại tin tức, nhưng học càng nhiều, càng có thể khiến người ta hàm dưỡng gia tăng, tâm tính cũng sẽ trở nên dày nặng."),t("br"),t("br"),n._v("“Có trọng lượng, tự nhiên cũng là có thể đo lường, cũng là có tới đối ứng vật phẩm, chính là cái này... Đào Hà Chi Thư!”"),t("br"),t("br"),n._v("Hồng sư nói tiếp: “Đãi tận cát sông thấy chân kim, đẩy ra mê vụ thấy trăng sáng. Đào Hà Chi Thư, đo lường người tri thức có hay không, có thể tuỳ tiện tháo bỏ xuống một người ngụy trang.”"),t("br"),t("br"),n._v("“Thứ này, là một vạn năm trước một vị bát tinh danh sư, sáng tạo mà thành, ẩn chứa trong đó sư giả dò xét ý thức, chỉ cần bàn tay xoa ở phía trên, liền có thể tự động đo lường ra, một cái đoạn thời gian bên trong, nhớ tri thức lượng bao nhiêu, không thể nào làm bộ!”"),t("br"),t("br"),n._v("“Đương nhiên, chỉ là tri thức số lượng, cũng không thể đo lường suy nghĩ của ngươi cùng ý tưởng, không cần lo lắng! Ta theo tổng bộ mượn tới cái này, cứ việc chỉ là hạ phẩm, nhưng vì chư vị đo lường, tuyệt đối đầy đủ.”"),t("br"),t("br"),n._v("“Đo lường tri thức lượng?”"),t("br"),t("br"),n._v("“Cái này... Thật là lợi hại!”"),t("br"),t("br"),n._v("“Đúng vậy a, quá nghịch thiên, không hổ là tổng bộ bảo vật!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được món pháp bảo này, có thể căn cứ tri thức trọng lượng, đến đo lường học được tri thức nhiều ít, mọi người tất cả đều xôn xao."),t("br"),t("br"),n._v("“Lão sư cho học sinh bố trí nhiệm vụ cùng làm việc, người sau lười biếng không hoàn thành, cái trước cũng không có cách nào kiểm tra, có thứ này, liền đơn giản, chỉ cần dò xét một chút, phát hiện tri thức lượng không đủ, liền cho thấy không có học tập.”"),t("br"),t("br"),n._v("“Đào Hà Chi Thư, chính là căn cứ vào nguyên nhân này, mới được sáng tạo ra.”"),t("br"),t("br"),n._v("Nói đến đây, Hồng sư ngón tay búng một cái, một quyển sách xuất hiện ở trước mặt mọi người."),t("br"),t("br"),n._v("Sách không lớn, toàn thân trắng như tuyết, lại ẩn chứa mãnh liệt sóng linh khí, vừa nhìn liền biết là kiện cấp bậc không thấp bảo vật."),t("br"),t("br"),n._v("Đem thư tịch đặt ở trước mặt trên mặt bàn, Hồng sư lần nữa nhìn về phía mọi người: “Đào Hà Chi Thư, có thể đối đo lường tri thức lượng, làm ra phản ứng, từ đó phóng xuất ra hào quang bảy màu, theo thứ tự là, đỏ, cam, vàng, lục, thanh, lam, tím! Mỗi cái cấp bậc, đều đối ứng 800 bản thư tịch. Nói cách khác... Phát hồng quang, nói rõ ngươi thông qua khảo hạch; Phát ánh cam, nói rõ nhớ kỹ 1600 quyển sách tri thức lượng, cứ thế mà suy ra. Chúng ta khảo hạch, là đơn giản nhất, chỉ cần đụng vào thư tịch, để hắn phóng xuất ra hồng quang, coi như thông qua!”"),t("br"),t("br"),n._v("“A, nguyên lai đơn giản như vậy!”"),t("br"),t("br"),n._v("“Thứ này, công bằng công chính, không có người có dị nghị!”"),t("br"),t("br"),n._v("Nghe Hồng sư giải thích xong, mọi người bừng tỉnh đại ngộ, đồng thời gật đầu."),t("br"),t("br"),n._v("Nếu là tổng bộ bảo vật, khẳng định không có vấn đề."),t("br"),t("br"),n._v("“Hết rồi...”"),t("br"),t("br"),n._v("Cùng mọi người cao hứng không giống, Trương Huyền mắt tối sầm lại, thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Sớm biết dùng cái đồ chơi này tự động đo lường, hắn liền không nhìn nhiều như vậy sách, một canh giờ, người khác đều là mấy trăm quyển, chừng một ngàn bản, mà hắn ở thư viện, dùng linh hồn quan sát, tốc độ cực nhanh, đã xấp xỉ nhìn hơn hai mươi vạn bản..."),t("br"),t("br"),n._v("Vốn cho rằng chỉ là khảo thí, có thể lựa chọn lấp cùng không lấp, trả lời cùng không trả lời, muốn điệu thấp cũng chỉ trong một ý nghĩ... Không bị người vì khống chế, còn thế nào giấu diếm?"),t("br"),t("br"),n._v("Căn bản che giấu không được ah!"),t("br"),t("br"),n._v("Nên làm cái gì?"),t("br"),t("br"),n._v("“Được rồi, hiện tại bắt đầu đo lường, các ngươi ai tới trước?”"),t("br"),t("br"),n._v("Trương Huyền bên này vẻ mặt xoắn xuýt, một bên khác, Hồng sư vuốt râu, nhìn về phía mọi người."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Hai mặt nhìn nhau, mọi người chần chờ một chút, đột nhiên đồng loạt nhìn về phía cách đó không xa Trương Huyền."),t("br"),t("br"),n._v("“Nếu không... Vẫn là Trương sư tới trước đi!”"),t("br"),t("br"),n._v("“Đúng, Trương sư, ngươi kiểm tra trước, chúng ta không vội!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mấy người cười nói."),t("br"),t("br"),n._v("Trương Huyền còn chưa kịp nói chuyện, một bên Hồng sư một cái lảo đảo, kém chút không có thổ huyết."),t("br"),t("br"),n._v("Các ngươi không vội, là muốn chờ vị này lại làm ra sự tình, nhẹ nhõm qua ải đi!"),t("br"),t("br"),n._v("Như vậy... Các ngươi đương nhiên không vội, nhưng ta cấp bách ah!"),t("br"),t("br"),n._v("Hơi đỏ mặt, vội vàng vẫy tay: “Quên đi, chúng ta... Dựa theo dựa theo tuổi tác sắp xếp đi, từ lớn đến nhỏ, Trương sư cuối cùng!”"),t("br"),t("br"),n._v("Không phải hắn không muốn để cho cái tên này lên, mà là cái tên này thực sự... Quá dọa người."),t("br"),n._v(" Thật nếu để cho hắn lên trước, thật không biết cái này Đào Hà Chi Thư, có thể hay không bảo tồn lại."),t("br"),t("br"),n._v("Quỷ Vực huyễn thành là hắn đồ vật, Dị Linh tộc nhân cũng là hắn tự tay bắt lấy, coi như tổn thất, cũng có thể nghĩ biện pháp giải quyết, có thể thứ này là theo tổng bộ mượn, một khi hư hại, không chịu nổi ah!"),t("br"),t("br"),n._v("“Ta cuối cùng?”"),t("br"),t("br"),n._v("Không nghĩ tới đường đường ngũ tinh cấp bậc danh sư, bị hắn sợ đến như vậy, Trương Huyền lắc đầu, có chút dở khóc dở cười."),t("br"),t("br"),n._v("Cuối cùng liền cuối cùng, đối với hắn mà nói cũng không ảnh hưởng, chỉ cần có thể qua ải là được, không cần thiết xoắn xuýt trước sau."),t("br"),t("br"),n._v("Hiện tại vẫn là suy nghĩ thật kỹ, như thế nào che giấu vừa rồi học được tri thức đi..."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được quyết định này, Khang đường chủ mấy người cũng tràn đầy im lặng, có thể ép có trách nhiệm khảo hạch người, không dám để cho ngươi lên, Trương sư, ngươi thật là một cái người mới!"),t("br"),t("br"),n._v("Xác định quy củ, còn lại liền đơn giản, cái thứ nhất khảo hạch chính là cái kia tuổi tác lớn nhất danh sư —— Minh Hạ đế quốc người dự thi."),t("br"),t("br"),n._v("Vị danh sư này, cũng xếp tại tiềm lực bảng mười vị trí đầu, một thân tu vi đạt đến Trọc Thanh cảnh đỉnh phong, hai mắt như điện, nghe được Hồng sư để hắn đo lường lời nói phía sau, nhấc chân đi tới Đào Hà Chi Thư trước mặt."),t("br"),t("br"),n._v("Ánh mắt mọi người cũng tập trung tới."),t("br"),t("br"),n._v("Hít sâu một hơi, vị danh sư này bàn tay ở trong sách vở sờ một cái, lập tức thu hồi, màu tuyết trắng thư tịch trong nháy mắt lắc lư hai lần, ngay sau đó phát ra một tiếng thanh minh, một đạo hồng quang lập loè mà ra."),t("br"),t("br"),n._v("“Hồng quang, thông qua được!”"),t("br"),t("br"),n._v("“Đúng vậy a, quá tốt rồi...”"),t("br"),t("br"),n._v("“Có thể đo lường học tập tri thức lượng, món bảo vật này quả nhiên mạnh mẽ!”"),t("br"),t("br"),n._v("Nhìn thấy Đào Hà Chi Thư thật cùng nói tới đồng dạng, mọi người tất cả đều tràn đầy kích động."),t("br"),t("br"),n._v("Rất nhiều người nhận biết bên trong, pháp bảo đều là dùng để chiến đấu hoặc là tăng cao tu vi, dùng để đo lường sở học tri thức, còn là lần đầu tiên nhìn thấy."),t("br"),t("br"),n._v("“Ngươi vừa rồi nhìn bao nhiêu quyển sách?”"),t("br"),t("br"),n._v("Hồng sư hài lòng gật đầu, hỏi."),t("br"),t("br"),n._v("“Bẩm báo Hồng sư, nhìn 1,238 bản!” Vị danh sư này gật đầu."),t("br"),t("br"),n._v("“Ừm, không sai!” Tán dương gật đầu, Hồng sư tuyên bố thành tích: “Minh Hạ đế quốc liễu tuyền thông qua, kế tiếp, Hằng Sa đế quốc Trương Minh Chí.”"),t("br"),t("br"),n._v("Nghe được tiếng la, người thứ hai đi tới, vị này Trương Minh Chí, mặc dù tuổi tác trong chúng nhân chiếm cứ thứ hai, thực lực chân thật cũng chỉ có Hóa Phàm tứ trọng sơ kỳ, cũng không nổi bật."),t("br"),t("br"),n._v("Mấy bước đi tới trước mặt, đưa tay một đáp."),t("br"),t("br"),n._v("Bất quá, cùng vừa rồi không giống nhau lắm, Đào Hà Chi Thư, không có bất kỳ cái gì phản ứng, hình như bàn tay của hắn căn bản không có đụng vào đồng dạng."),t("br"),t("br"),n._v("“Xem ra là không có nhìn đến tám trăm bản!”"),t("br"),t("br"),n._v("Chờ một hồi, thấy thư tịch không có động tĩnh, mọi người tất cả đều lắc đầu."),t("br"),t("br"),n._v("“Ta chỉ nhìn bảy trăm bảy mươi mốt bản...” Trương Minh Chí cười khổ giải thích."),t("br"),t("br"),n._v("Vốn cho rằng, khoảng cách tám trăm vốn chỉ kém một chút, có lẽ có thể đục nước béo cò, hiện tại xem ra, Đào Hà Chi Thư mạnh mẽ vô cùng, căn bản cũng không khả năng gian lận."),t("br"),t("br"),n._v("“Ừm!” Hồng sư gật gật đầu: “Trương Minh Chí chưa thông qua, mất đi tham gia thi đấu tư cách. Kế tiếp...”"),t("br"),t("br"),n._v("Có trước mặt ví dụ, mọi người cũng đều biết cái này Đào Hà Chi Thư thần kỳ, dựa theo tuổi tác sắp xếp nhao nhao tiến lên tiến đi đo lường."),t("br"),t("br"),n._v("Một canh giờ sau, tham gia tỷ thí 53 vị người dự thi, loại trừ Trương Huyền, đã đo lường hoàn tất."),t("br"),t("br"),n._v("Cửa này, khảo hạch trí tuệ, quả nhiên không tưởng tượng đơn giản như vậy, trọn vẹn đào thải mười bảy vị, mà những người này đi qua hỏi thăm, nhớ thư tịch tất cả đều không đủ 800 bản, thậm chí có một cái, nhớ kỹ 799 bản, cũng không thông qua, chỉ có thể ảm đạm rời khỏi."),t("br"),t("br"),n._v("“Thật chuẩn!”"),t("br"),t("br"),n._v("Nhìn thấy Đào Hà Chi Thư, xác nhận không sai, liền một hai bản sách chênh lệch, đều có thể rõ ràng chi tiết đoán được, mọi người kìm lòng không được cảm khái."),t("br"),t("br"),n._v("Không hổ là tổng bộ bảo vật, đáng sợ!"),t("br"),t("br"),n._v("Không thêm Trương Huyền, hiện tại thông qua khảo hạch là 35 người, thành tích cao nhất, cũng không phải là tiếng hô lớn nhất La Tuyền, cũng không phải trước đó, xem thường Trương Huyền Liêu Vũ Chí, lại là Trương Huyền người quen biết cũ... Tống Siêu!"),t("br"),t("br"),n._v("Vậy mà lóe ra màu cam quang mang, cũng là cho thấy... Nhớ kỹ trọn vẹn 1600 bản!"),t("br"),t("br"),n._v("Một canh giờ nhiều như vậy thư tịch, gần như là quét mắt một vòng liền nhớ kỹ nội dung, loại này trí nhớ, có thể nói là siêu cấp biến thái."),t("br"),t("br"),n._v("Hắn có thể có loại này thành tích, coi như Trương Huyền, đều cảm thấy mười phần ngoài ý muốn."),t("br"),t("br"),n._v("Cái tên này thoạt nhìn ngốc ngốc, làm sao đều không có nghĩ đến nhớ phương diện có cường đại như vậy năng lực."),t("br"),t("br"),n._v("Còn Nhược Hoan công tử, hơn một ngàn hai trăm bản, thứ tự xem như khá cao."),t("br"),t("br"),n._v("“Cái cuối cùng, Trương Huyền Trương sư!”"),t("br"),t("br"),n._v("Nương theo Hồng sư tiếng la, ánh mắt của mọi người toàn bộ tập trung cái này vừa đến liền không yên ổn thanh niên trên người."),t("br"),t("br"),n._v("“Trương sư muốn đo, rất muốn nhìn hắn, có thể có dạng gì thành tích!”"),t("br"),t("br"),n._v("“Thành tích của hắn chỉ sợ không tốt lắm đâu, vừa rồi hắn đọc sách thời điểm, ta kỹ càng nhìn, lấy ra một quyển sách, là ở chỗ đó ngây người... Năm ngàn quyển sách giá sách, tổng cộng liền lật mấy quyển... Muốn có thành tích tốt, khó!”"),t("br"),t("br"),n._v("“Ta cũng cảm thấy như vậy, bất quá... Đây chính là Trương sư, hai cửa trước, như thế nghịch thiên, ta không tin có thể ở cửa này bị đào thải!”"),t("br"),t("br"),n._v("“Vẫn là xem một chút đi...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Đi qua hai cửa trước khảo hạch, Trương Huyền đã là tất cả mọi người tiêu điểm rồi, tất cả mọi người muốn nhìn một chút cái này liền quỳ hai quan gia hỏa, cửa này có thể hay không lại ra cái gì yêu thiêu thân."),t("br"),t("br"),n._v("Thấy né lâu như vậy, vẫn là đến phiên chính mình, Trương Huyền vẻ mặt mất tinh thần."),t("br"),t("br"),n._v("Cái này nửa ngày, các loại biện pháp đều suy nghĩ, kết quả vẫn như cũ không hiểu ra sao, cái gì cũng không có phương pháp."),t("br"),t("br"),n._v("“Quên đi, cứ như vậy đi...”"),t("br"),t("br"),n._v("Biết muốn tránh cũng tránh không khỏi, đành phải kiên trì đi tới trước mặt, bàn tay một đáp, lập tức thu hồi."),t("br"),t("br"),n._v("Ngay sau đó vội vàng đi qua, muốn nhìn một chút có thể phát ra dạng gì quang mang."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-591"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-593"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);