(window.webpackJsonp=window.webpackJsonp||[]).push([[1847],{2202:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-585-thien-tinh-co"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-585-thien-tinh-co"}},[n._v("#")]),n._v(" Chương 585: Thiên Tinh cờ")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Thần thức đọc sách pháp?”"),t("br"),t("br"),n._v("“Thánh giả nhị trọng?”"),t("br"),t("br"),n._v("Trên đài dưới đài sở hữu danh sư, đều mắt lớn trừng mắt nhỏ, tràn đầy không hiểu."),t("br"),t("br"),n._v("Cái danh xưng này, đừng nói gặp, nghe đều không có nghe qua."),t("br"),t("br"),n._v("“Võ giả phía trên là Hóa Phàm, Hóa Phàm phía trên là Bán Thánh, Tòng Thánh, Thánh giả, những này cấp bậc chư vị có lẽ đều biết đi!”"),t("br"),t("br"),n._v("Không có trực tiếp trả lời, Hồng sư ngược lại hỏi."),t("br"),t("br"),n._v("“Ừm!” Mọi người gật đầu."),t("br"),t("br"),n._v("Ở đây hầu như đều là danh sư, mặc dù rất nhiều cấp bậc khoảng cách rất xa, nhưng vẫn là nghe qua một chút."),t("br"),t("br"),n._v("“Bán Thánh cùng Tòng Thánh, chỉ là đơn thuần một cái cấp bậc, mà Thánh giả cùng Hóa Phàm đồng dạng, riêng phần mình chia làm cửu trọng!”"),t("br"),t("br"),n._v("Hồng sư nói tiếp."),t("br"),t("br"),n._v("Bán Thánh, Tòng Thánh, cùng Hóa Phàm cảnh nguyên khí, Âm Dương đồng dạng, chỉ là cái tiểu cấp bậc, mà Thánh giả thì là cái lớn gọi chung."),t("br"),t("br"),n._v("Cùng võ giả đồng dạng, chia làm chín cái đẳng cấp."),t("br"),t("br"),n._v("“Trong đó, đệ nhất trọng gọi Trục Không cảnh, cũng chính là đi đến loại cảnh giới này, chân chính có thể bay đi. Đệ nhị trọng liền gọi... Thần Thức cảnh!”"),t("br"),t("br"),n._v("Hồng sư khuôn mặt ngưng trọng: “Chỉ có đi đến loại cảnh giới này, mới có thể mở ra thai nhãn, sinh ra thần thức.”"),t("br"),t("br"),n._v("“Thần thức... Là cái gì?”"),t("br"),t("br"),n._v("Có người nhịn không được hỏi."),t("br"),t("br"),n._v("Bọn họ nghe qua hồn lực, Vu hồn, nhưng từ chưa nghe qua thần thức."),t("br"),t("br"),n._v("Hồng sư đạo: “Thần thức là Thánh giả mới có đặc thù thủ đoạn. Chúng ta đều biết, tu luyện tới Hợp Linh cảnh, linh nhục hợp nhất, liền có thể rèn luyện linh hồn, để hắn càng ngày càng cường đại!”"),t("br"),t("br"),n._v("Mọi người gật đầu."),t("br"),t("br"),n._v("Hóa Phàm ngũ trọng Hợp Linh cảnh, là cái chuyển hướng, đi đến loại cảnh giới này, người tu luyện rèn luyện mới không chỉ thân thể, chân khí... Còn có linh hồn."),t("br"),t("br"),n._v("“Linh hồn càng ngày càng cường đại, có thể làm ra một chút để cho người ta không thể tưởng tượng sự tình, điểm ấy, chư vị đi đến cảnh giới, tự nhiên có thể lý giải. Ta nói cho đúng là, làm linh hồn đi đến cảnh giới nhất định... Có thể thấu thể mà ra, mặc kệ cái gì, chỉ cần bị che kín, liền và tận mắt thấy, thậm chí so con mắt nhìn thấy còn muốn rõ ràng... Đây chính là thần thức!”"),t("br"),t("br"),n._v("Hồng sư nói."),t("br"),t("br"),n._v("“Cái kia... Không phải là linh hồn giãn ra?”"),t("br"),t("br"),n._v("“Linh hồn có thể ly thể quan sát... Nên mạnh cỡ nào đại?”"),t("br"),t("br"),n._v("“Hồng sư ý tứ không phải là muốn nói... Trương sư đã có loại năng lực này, đi đến... Thánh giả nhị trọng!”"),t("br"),t("br"),n._v("Tất cả mọi người giật nảy mình."),t("br"),t("br"),n._v("Thánh giả nhị trọng? Cái này sao có thể?"),t("br"),t("br"),n._v("Trương Huyền cũng không hiểu ra sao, vừa rồi hắn liền tin miệng nói bậy, làm sao vị này Hồng sư càng tán dóc càng xa, lấy tới Thánh giả trên thân?"),t("br"),t("br"),n._v("“Trương sư tự nhiên không có Thánh giả tu vi, mọi người cũng không cần gấp, hãy nghe ta nói hết, liền hiểu!”"),t("br"),t("br"),n._v("Hồng sư lắc đầu: “Lợi dụng thần thức đọc sách, liền có thể không cần lật ra trang sách, tùy ý quan sát, chính vì vậy, Thánh giả, mới có thể học tri thức cực nhanh, càng ngày càng cường đại.”"),t("br"),t("br"),n._v("“Trương sư, không phải Thánh giả, vì cái gì cũng nắm giữ loại năng lực này, nếu như không có đoán sai, hẳn là... Một vị siêu cấp cao thủ, đem Thánh giả nhị trọng thai nhãn đào ra, dùng phương pháp đặc thù, đặt ở trên người hắn, mới có thể dùng để đọc sách, nhớ nhanh chóng!”"),t("br"),t("br"),n._v("Vừa nói, Hồng sư bên cạnh cảm thấy rung động."),t("br"),t("br"),n._v("Thánh giả chỉ có mở ra thai nhãn, mới có thể sinh ra thần thức."),t("br"),t("br"),n._v("Bởi vậy, nếu có người đem thai nhãn đào ra, đặt ở một cái vãn bối trên người, xử lý thỏa đáng, cũng đồng dạng có thể để cho nắm giữ loại năng lực này."),t("br"),t("br"),n._v("Đương nhiên, hắn cái này, cùng Thánh giả nhị trọng không cách nào so sánh được, nhiều nhất chỉ có thể dùng để nhìn xem sách, tác dụng khác liền ít hơn nhiều."),t("br"),t("br"),n._v("Dù vậy, đối với cấp bậc thấp danh sư tới nói, cũng coi như được nghịch thiên."),t("br"),t("br"),n._v("Người khác nhìn một quyển sách thời gian, ngươi có thể nhìn mười bản, một trăm bản, còn thế nào so?"),t("br"),t("br"),n._v("Khó trách Trương sư trẻ tuổi như vậy, liền tinh thông nhiều như vậy chức nghiệp, đối danh sư lý giải, cũng đi đến để cho người ta hoảng sợ tình trạng, nguyên lai nắm giữ loại năng lực này!"),t("br"),t("br"),n._v("Chỉ sợ... Chính là Dương sư vì để cho hắn nhanh chóng tiến bộ, chuyên môn làm."),t("br"),t("br"),n._v("Đem Thánh giả nhị trọng thai nhãn đào ra, đặt vào trên thân người khác, còn có thể hoàn mỹ dung hợp... Vị này Dương sư đến cùng đạt đến loại nào đáng sợ cảnh giới?"),t("br"),t("br"),n._v("Suy nghĩ một chút đều cảm thấy kinh khủng!"),t("br"),t("br"),n._v("Khó trách Mạc đường chủ đều kiêng kỵ như vậy, chuyên môn lấy ra linh thạch thượng phẩm lấy lòng, mạnh như thế người, cũng quả thực có lấy lòng vốn liếng."),t("br"),t("br"),n._v("“Đem Thánh giả nhị trọng thai nhãn đào ra, đặt ở trên thân người khác?”"),t("br"),t("br"),n._v("“Đọc sách không cần lật giấy, nhanh chóng đọc?”"),t("br"),t("br"),n._v("“Còn có thể nhìn thấy trữ vật giới chỉ bên trong thư tịch?”"),t("br"),t("br"),n._v("“Khó trách có thể ở một canh giờ nhìn xong nhiều như vậy sách, bất quá... Nhìn xong có thể nhớ kỹ, cái này ức lực cũng nghịch thiên!”"),t("br"),t("br"),n._v("“Đúng vậy a, đọc sách không phải là nhớ kỹ, nhìn nhanh, còn nhớ kỹ, mới là thật đáng sợ!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được Hồng sư giải thích, mọi người giờ mới hiểu được tới, lần nữa nhìn về phía thanh niên trước mắt, tràn đầy hoảng sợ."),t("br"),t("br"),n._v("Có thể đem Thánh giả nhị trọng cường giả thai nhãn đào ra, cái tên này bối cảnh nên thâm hậu cỡ nào?"),t("br"),t("br"),n._v("Mọi người khiếp sợ, một bên Nhược Hoan công tử thì mí mắt xoay loạn, một hồi buồn bực."),t("br"),t("br"),n._v("Vừa rồi nhìn xong sách, hỏi đối phương, nhìn bao nhiêu, trả lời gần giống như hắn, để hắn cao hứng hồi lâu, cảm thấy rốt cục lại một lần nữa có thể cùng cái tên này ngang hàng, kết quả..."),t("br"),t("br"),n._v("Nhà các ngươi hơn hai vạn bản, cùng chừng một ngàn bản xấp xỉ a?"),t("br"),t("br"),n._v("An ủi người, cũng có có chút thành ý, không mang theo như thế hồ lộng!"),t("br"),n._v("“Được rồi, mặc kệ phương thức gì, cửa này khảo hạch là trí tuệ, là trong thời gian ngắn đối tri thức nhớ, Trương sư nhớ hai vạn bản nội dung, đem Đào Hà Chi Thư đều chấn vỡ, xem như qua ải, chư vị không có ý kiến chứ?”"),t("br"),t("br"),n._v("Cắt ngang mọi người thảo luận, Hồng sư đạo."),t("br"),t("br"),n._v("“Không có...”"),t("br"),t("br"),n._v("Mọi người liền vội vàng gật đầu."),t("br"),t("br"),n._v("Nói đùa, nhớ tám trăm bản đều qua, nhân gia nhớ hơn hai vạn bản, làm Đào Hà Chi Thư đều phế đi, sẽ không lại cho thông qua, Hồng sư cũng sẽ không đáp ứng!"),t("br"),t("br"),n._v("“Ừm, đã như vậy, liền tiến hành tuyển bạt cửa ải cuối cùng, ngộ tính!”"),t("br"),t("br"),n._v("Hồng sư nhìn lại."),t("br"),t("br"),n._v("“Lão sư dẫn vào cửa, tu hành tại cá nhân. Tu luyện loại trừ cố gắng cùng thiên phú bên ngoài, càng quan trọng hơn một chút chính là ngộ tính, đồng dạng công pháp, đồng dạng tiết học, không giống học sinh, lấy được kết quả lại hoàn toàn khác biệt. Có thiên phú, muốn trở thành danh sư dễ dàng, đều muốn trở thành lợi hại danh sư, khó!”"),t("br"),t("br"),n._v("“Bởi vậy, ngộ tính đối danh sư rất là trọng yếu.”"),t("br"),t("br"),n._v("“Ngộ tính đối danh sư quan trọng, đây là sự thật, thế nhưng là... Ngộ tính làm sao kiểm tra? Chẳng lẽ muốn cho một bộ công pháp, để hắn tìm hiểu?”"),t("br"),t("br"),n._v("“Tìm hiểu công pháp mà nói, liên quan liền có thêm, thiên phú, trí tuệ đều tính ở trong đó, không thể là một loại ngộ tính khảo hạch!”"),t("br"),t("br"),n._v("Mọi người lần nữa nghi hoặc."),t("br"),t("br"),n._v("Ngộ tính, bất kể tu luyện, vẫn là trở thành danh sư, đều rất là trọng yếu, chỉ bất quá, thứ này cùng trí tuệ đồng dạng, không cách nào định lượng, rất khó khá là."),t("br"),t("br"),n._v("Trí tuệ, ngươi có thể dùng đọc thuộc lòng tri thức đến phán định cao thấp, có thể ngộ tính đâu?"),t("br"),t("br"),n._v("Coi như tìm hiểu công pháp, có người thích hợp, có người không thích hợp, tương đối, cũng có sai lầm bất công đi."),t("br"),t("br"),n._v("“Ha ha, những này mọi người có thể nghĩ đến, ta tự nhiên rõ ràng.”"),t("br"),t("br"),n._v("Biết mọi người lo lắng, Hồng sư cười cười, ngón tay búng một cái, sân khấu góc bên trên mê vụ tán đi, lộ ra một cái không lớn cái đình."),t("br"),t("br"),n._v("Cái đình bên trong ngồi một cái ông lão mặc áo trắng, trước mặt trên bàn đá, bày biện một bộ màu nâu xanh bàn cờ, phía trên trắng đen giao nhau, Âm Dương tấn công, lại là một bộ ván cờ."),t("br"),t("br"),n._v("Lão giả mặt không biểu tình, trong cơ thể không có sinh cơ chút nào, vừa nhìn liền biết là cái khôi lỗi."),t("br"),t("br"),n._v("“Đây là Thiên Tinh cờ, quy tắc cùng bên dưới pháp, cùng sở hữu cờ đường đều không tương đồng, tự xưng hệ thống. Là năm đó ta du lịch khắp nơi thời điểm, ở một chỗ trong di tích ngẫu nhiên đoạt được, không biết người nào sáng tạo, cũng không biết cụ thể quy tắc là cái gì, nghiên cứu nhiều năm, cũng chỉ tìm hiểu bốn, năm điểm mà thôi.”"),t("br"),t("br"),n._v("Lần nữa bắn ra, cái đình cách đó không xa trên vách tường, quang mang lóe lên, hiện ra trên bàn cờ ván cờ, cùng tất cả mọi người đã gặp toàn bộ không giống nhau, coi như trước kia học qua cờ thuật, cũng không có nửa điểm tham khảo ý nghĩa."),t("br"),t("br"),n._v("“Lại là trân lung kỳ cục?”"),t("br"),t("br"),n._v("“Đúng vậy a, không biết quy tắc, còn khó giải, thứ này... Như thế nào khảo hạch?”"),t("br"),t("br"),n._v("“Không biết!”"),t("br"),t("br"),n._v("Trong đám người không thiếu có tinh thông cờ thuật, nhìn thấy trên mặt tường biểu hiện cờ đường, lôi kéo khắp nơi, cùng sở học qua, hoàn toàn khác biệt, cả đám đều có chút choáng váng."),t("br"),t("br"),n._v("“Thiên Tinh cờ, lấy thiên địa làm ván cờ, tinh thần làm quân cờ, khí phách hùng hậu, không bàn mà hợp thiên địa chí lý. Thứ này, ở trên vách tường nhìn, tự nhiên cái gì cũng nhìn không ra, nhưng đình nhỏ trên trụ đá, có ván cờ chủ nhân lưu lại đôi câu vài lời.”"),t("br"),t("br"),n._v("“Thông qua những lời này, có thể lĩnh ngộ nhất định cờ đường quy tắc.”"),t("br"),t("br"),n._v("“Biết quy tắc phía sau, có thể cùng đình nhỏ bên trong khôi lỗi giao đấu, ở dưới không đúng hoặc là thất bại, đều không thể đem quân cờ đặt vào trên bàn cờ. Cửa này, cùng thiên phú, tu vi, không có bất cứ quan hệ nào, chỉ cùng ngộ tính có quan hệ, ngộ tính cao cổ ngộ quy tắc nhiều, ở dưới bước mấy nhiều; Ngộ tính thấp, lĩnh ngộ quy tắc ít, ở dưới bước mấy cũng là ít, có thể kiên trì sáu bước, xem như thông qua!”"),t("br"),t("br"),n._v("Hồng sư đem quy tắc nói rõ chi tiết."),t("br"),t("br"),n._v("“Cái này... Lấy ván cờ định ngộ tính, không sai!”"),t("br"),t("br"),n._v("“Thiên địa làm ván cờ, tinh thần làm quân cờ, hảo phách lực, cái này ván cờ, quả nhiên lợi hại!”"),t("br"),t("br"),n._v("“Đúng vậy a, dùng cái khảo hạch này ngộ tính, quả thực cái khác đều không liên quan, công bằng, công chính!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe rõ quy tắc, mọi người gật đầu."),t("br"),t("br"),n._v("Cờ thuật, có ít người, học cả một đời, nghiên cứu mấy chục năm, đều chưa hẳn như một cái mới vừa học mấy ngày hài đồng! Đây cũng không phải là thông minh hay không, mà là ngộ tính."),t("br"),t("br"),n._v("Nguyên nhân chính là như thế, kỳ đạo lưu truyền một câu lời lẽ chí lý, vì tất cả người vui nói: “Hai mươi tuổi không được danh thủ quốc gia, cả đời vô vọng!”"),t("br"),t("br"),n._v("Không có ngộ tính, thiên phú, coi như học lại lâu, đều tăng thêm không lớn."),t("br"),t("br"),n._v("Cái này Thiên Tinh cờ, cùng sở hữu cờ đường không giống, lợi dụng đôi câu vài lời, lĩnh ngộ quy tắc, tiến hành đánh cờ, dùng sử dụng số lượng đến bình xét ngộ tính, có thể đem hắn định lượng, là tốt nhất tỷ thí phương pháp."),t("br"),t("br"),n._v("Không hổ là ngũ tinh danh sư, khảo hạch thủ đoạn đều không giống bình thường."),t("br"),t("br"),n._v("“Đi vào đình nhỏ danh sư, mỗi người chỉ có thời gian một nén nhang quan sát trên trụ đá ngôn ngữ, sau đó nhất định phải đánh cờ, vượt qua thời gian bất động, hoặc là quân cờ xuống không được đi, đều phán định thất bại.”"),t("br"),t("br"),n._v("Hồng sư vung tay lên: “Được rồi, quy tắc nói xong, bắt đầu khảo hạch đi!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Mọi người gật đầu."),t("br"),t("br"),n._v("“Bởi vì đánh cờ, chỉ có thể một người một người đến, chư vị, vẫn là muốn xác định trình tự...”"),t("br"),t("br"),n._v("Hồng sư nhìn về phía mọi người."),t("br"),t("br"),n._v("Cửa thứ nhất Trương Huyền cái thứ nhất kiểm tra, Quỷ Vực huyễn thành phá hư, không có tuyển ra danh ngạch; Cửa thứ hai, Dị Linh tộc nhân uy hiếp, đào thải ba người; Cửa thứ ba đào thải 17 cái."),t("br"),t("br"),n._v("28 thế lực lớn, 56 vị người dự thi, trừ bỏ đào thải 20 cái, còn thừa lại ròng rã 36 vị."),t("br"),t("br"),n._v("“Nếu không... Vẫn là ta tới trước đi!”"),t("br"),t("br"),n._v("Trương Huyền hướng về phía trước."),t("br"),t("br"),n._v("3 6 người, mỗi người thời gian một nén nhang, toàn bộ thi xong, đoán chừng trời đã tối rồi, cùng hắn ở chỗ này chờ, còn không bằng trước làm xong, nghỉ ngơi trước."),t("br"),t("br"),n._v("“Khụ khụ...” Nhìn thấy hắn đi tới, Hồng sư kém chút không có bị nước bọt sặc chết: “Ngươi vẫn là thôi đi!”"),t("br"),t("br"),n._v("Qua ba cửa ải hủy ba cửa ải, để ngươi lên trước... Ta còn không có sao mà to gan như vậy."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-593"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-595"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);