(window.webpackJsonp=window.webpackJsonp||[]).push([[185],{537:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1143-ve-nhiem-tuyet-su-ty"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1143-ve-nhiem-tuyet-su-ty"}},[n._v("#")]),n._v(" Chương 1143: Vệ Nhiễm Tuyết sư tỷ")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Sở Tường suýt chút nữa không té ngã trên đất."),t("br"),t("br"),n._v("Ta muốn nhìn Tử Yên tiên tử múa đơn, là muốn cùng nàng một chỗ... Ngươi một cái đại lão gia, ai nhìn ah!"),t("br"),t("br"),n._v("Ta mới không nặng như vậy khẩu vị!"),t("br"),t("br"),n._v("“Trương sư, tỉnh táo một chút...”"),t("br"),t("br"),n._v("Hồ Yêu Yêu nhìn không được, vội vàng kéo một cái."),t("br"),t("br"),n._v("Lạc Thất Thất, Ngọc Phi Nhi cũng đỡ cái trán, một mặt bất đắc dĩ."),t("br"),t("br"),n._v("Sớm biết vị này Trương sư EQ rất thấp, thế nhưng là không nghĩ tới thấp đến loại tình trạng này."),t("br"),t("br"),n._v("Vừa rồi vị này Sở Tường công tử, nước bọt chảy thành như thế, là nhìn kinh hồng vũ cái kia có biểu hiện ư?"),t("br"),t("br"),n._v("Ngươi cho hắn nhảy..."),t("br"),t("br"),n._v("Không trả tiền cũng không nhìn, còn muốn để cho người ta tốn một cái tinh nguyên linh thạch thượng phẩm... Thật sự là suy nghĩ nhiều."),t("br"),t("br"),n._v("“Trương Huyền công tử, cái này... Ngươi muốn nhảy, vẫn là cùng mấy vị này mỹ nữ nhảy đi, ta... Không tốt lắm cái này miệng!”"),t("br"),t("br"),n._v("Kìm nén sắc mặt đỏ lên, Sở Tường công tử lúc này mới lên tiếng."),t("br"),t("br"),n._v("“Đáng tiếc, ngươi mất đi một lần cơ hội thật tốt!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Sở dĩ nói những lời kia, cũng không phải là nhất thời nóng não, mà là vừa rồi vận chuyển Minh Lý Chi Nhãn, lặng lẽ quan sát."),t("br"),t("br"),n._v("Vị này Sở Tường công tử, thoạt nhìn hoàn khố kinh khủng, nhưng có thể tiện tay lấy ra một cái tinh nguyên linh thạch thượng phẩm, tuổi còn trẻ liền đạt tới Thánh vực tam trọng, làm sao có thể chỉ là cái thấy sắc liền mờ mắt hạng người vô năng?"),t("br"),t("br"),n._v("Mong muốn trả giá như vậy đánh đổi, nhìn đối phương múa một điệu, nghe không thể tưởng tượng, trên thực tế lại là vì tu bổ trên linh hồn thiếu hụt, để thực lực tiến thêm một bước."),t("br"),t("br"),n._v("Nếu như không thể chữa trị, về sau xung kích Nguyên Thần cảnh, liền hầu như không có bất kỳ cái gì khả năng."),t("br"),t("br"),n._v("Chính là nhìn ra điểm ấy, hắn mới nói muốn cho đối phương khiêu vũ."),t("br"),t("br"),n._v("Cứ việc dưới đài vị kia Tử Yên tiên tử là thất tinh kinh hồng sư, nhưng ở chữa trị trên linh hồn, so với hắn vẫn là kém rất nhiều."),t("br"),t("br"),n._v("Sở Tường chỉ cần đáp ứng, tự mình ra tay, tuyệt đối nắm giữ cực lớn hiệu quả, đáng tiếc... Hắn không có trân quý."),t("br"),t("br"),n._v("“Cơ hội? Thôi được rồi...”"),t("br"),t("br"),n._v("Sở Tường vội vàng lắc đầu."),t("br"),t("br"),n._v("Hai người bọn họ nói chuyện phiếm, phía dưới đấu giá, cũng xác định ra."),t("br"),t("br"),n._v("Một cái tinh nguyên linh thạch thượng phẩm giá cả, đã vượt qua một ngàn viên linh thạch thượng phẩm, hơn nữa còn là có tiền mà không mua được, cái này danh ngạch, cuối cùng bị Sở Tường thu hoạch được."),t("br"),t("br"),n._v("Đấu giá kết thúc không lâu, phòng cửa phòng liền từ từ mở ra, vừa rồi tại dưới đài Tử Yên tiên tử, đi tới trước mặt."),t("br"),t("br"),n._v("Nàng lúc này lại đổi một bộ y phục, không giống trước đó như vậy bại lộ, trái lại còn phải thể vừa vặn, từ xa nhìn lại, như là tưởng là tiểu thư khuê các, phong trần hương vị, biến mất không thấy hình bóng."),t("br"),t("br"),n._v("Đi tới trước mặt, mới phát hiện nữ tử này không chỉ vóc người xinh đẹp, làn da cũng là thổi qua liền phá, nhất là theo trong cơ thể sinh ra khí chất, càng làm cho người nhìn lên một cái, liền không muốn dời đi con mắt."),t("br"),t("br"),n._v("“Tử Yên gặp qua Sở Tường công tử!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Tử Yên nói."),t("br"),t("br"),n._v("“Tiên tử xin kính chào...” Sở Tường vội vàng đứng lên, giới thiệu một câu: “Vị này là bằng hữu của ta, Trương Huyền Trương công tử!”"),t("br"),t("br"),n._v("“Gặp qua Trương công tử!”"),t("br"),t("br"),n._v("Tử Yên gật đầu."),t("br"),t("br"),n._v("Trương Huyền cũng gật gật đầu, nhìn lại: “Tiên tử nếu là nơi này kinh hồng sư, ta muốn hỏi một chút, lục tinh đỉnh phong kinh hồng sư, muốn khảo hạch thất tinh, nên đi tìm ai? Lại tại nơi nào?”"),t("br"),t("br"),n._v("Vừa đến cứ như vậy nhiều người, tình cảnh lớn như vậy, đều không có cơ hội tìm người hỏi, hiện tại vị này đi tới qua trước mặt, tự nhiên hỏi thăm một chút."),t("br"),t("br"),n._v("“Khảo hạch thất tinh kinh hồng sư?”"),t("br"),t("br"),n._v("Nhìn một cái, Tử Yên tiên tử con mắt rơi vào cách đó không xa Hồ Yêu Yêu trên thân: “Là vị muội muội này muốn khảo hạch ư?”"),t("br"),t("br"),n._v("“Là ta!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Ngươi?” Tử Yên tiên tử sững sờ."),t("br"),t("br"),n._v("Kinh hồng sư hầu như đều là nữ tử học tập, một vị nam tử tới khảo hạch, còn chưa bao giờ thấy qua."),t("br"),t("br"),n._v("“Không sai!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Hồ Yêu Yêu đi qua chỉ điểm của mình, mặc dù đang kinh ngạc hồng sư trên chức nghiệp, cũng đạt tới lục tinh tiêu chuẩn, nhưng khoảng cách có thể đột phá, còn có khoảng cách nhất định."),t("br"),t("br"),n._v("Nói hết, Trương Huyền cổ tay khẽ đảo, lấy ra một cái lục tinh huy chương."),t("br"),t("br"),n._v("Chính là lúc trước Vệ Nhiễm Tuyết giúp hắn xin."),t("br"),t("br"),n._v("“Quả nhiên là lục tinh kinh hồng sư, ta vậy thì mang ngươi tới... Sở Tường công tử, thực sự thật không tiện, ngươi có thể hay không chờ một lát, ta xử lý xong chuyện này, lại tới vì ngươi múa đơn?”"),t("br"),t("br"),n._v("Nhìn một cái, xác định không phải giả, Tử Yên tiên tử lúc này mới mặt mũi áy náy nhìn qua."),t("br"),t("br"),n._v("“Không sao cả!”"),t("br"),t("br"),n._v("Sở Tường liền vội vàng gật đầu, nói: “Dù sao ta cũng không có việc gì, nếu không... Cùng các ngươi cùng đi làm sao?”"),t("br"),t("br"),n._v("Vị này Trương Huyền, lại là kinh hồng sư, hắn là hết sức ngạc nhiên."),t("br"),t("br"),n._v("Một cái nam nhân khiêu vũ, thiên kiều bá mị... Suy nghĩ một chút đều làm người kích động..."),t("br"),t("br"),n._v("“Cái này...” Tử Yên tiên tử nhìn về phía Trương Huyền."),t("br"),t("br"),n._v("Khảo hạch là việc riêng tư, hơn nữa cần tuyệt đối yên tĩnh, nàng muốn nghe theo người trong cuộc ý kiến."),t("br"),t("br"),n._v("“Đương nhiên có thể!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Trong mắt hắn, kinh hồng là một cái chức nghiệp, cho dù trước mặt mọi người biểu diễn, cũng không tính là gì."),t("br"),t("br"),n._v("“Trương công tử, mời tới bên này!”"),t("br"),t("br"),n._v("Biết trước mắt vị này là tới khảo hạch thất tinh, Tử Yên tiên tử thái độ trở nên hiền lành rất nhiều, đi đầu dẫn đường."),t("br"),t("br"),n._v("Đi ra đại sảnh rất mau tới đến phía sau một cái rộng lớn đại điện."),t("br"),t("br"),n._v("“Tử Yên, ngươi làm sao đem người tới nơi này?”"),t("br"),t("br"),n._v("Một cái trung niên phu nhân tới đón, nhướng mày."),t("br"),t("br"),n._v("“Lão sư, vị này Trương công tử, là lục tinh kinh hồng sư, ý định khảo hạch thất tinh...” Nói hết đem trong tay lệnh bài đưa tới."),t("br"),n._v("“Trương công tử?” Cúi đầu nhìn một cái, trung niên phụ nhân một mặt ngạc nhiên: “Ngươi chính là Trương Huyền? Hồng Viễn đế quốc tới Trương sư?”"),t("br"),t("br"),n._v("“Ách?”"),t("br"),t("br"),n._v("Trương Huyền hơi nghi hoặc một chút: “Ngươi biết ta?”"),t("br"),t("br"),n._v("“Tại hạ Vệ Nhiễm Cầm, là cái này kinh hồng sư phân bộ hội trưởng, cũng là Vệ Nhiễm Tuyết đồng môn sư tỷ!”"),t("br"),t("br"),n._v("Trung niên phụ nhân cười nói: “Trước đó, nàng chuyên môn đưa tin cùng ta nói qua ngươi, tuổi còn trẻ, liền lĩnh ngộ phản phác quy chân cảnh giới, thiên phú nghe rợn cả người!”"),t("br"),t("br"),n._v("“Nguyên lai là Vệ hội trưởng!”"),t("br"),t("br"),n._v("Trương Huyền bừng tỉnh hiểu ra."),t("br"),t("br"),n._v("Vệ Nhiễm Tuyết mặc dù chưa nói qua người này, nhưng đối phương nói như thế, hơn nữa một cái nói ra lai lịch của mình, hẳn là không có vấn đề."),t("br"),t("br"),n._v("“Không cần khách khí! Nhiễm Tuyết sư muội, nhận hạn chế bản thân danh sư thân phận, dáng múa có nhiều thả không ra, bởi vậy một mực ràng buộc cùng lục tinh đỉnh phong không cách nào đột phá, nhận được ngươi chỉ điểm, lúc này mới tiến thêm một bước, đạt tới thất tinh cấp bậc!”"),t("br"),t("br"),n._v("Vệ Nhiễm Cầm hội trưởng cười nói."),t("br"),t("br"),n._v("Vệ Nhiễm Tuyết, tiếp nhận Trương Huyền chỉ điểm về sau, tại đối kinh hồng nghề nghiệp lý giải bên trên, đã đột phá."),t("br"),t("br"),n._v("“Nàng chuyên đưa tin tới, nói ngươi ít ngày nữa sẽ đi tới nơi này, muốn ta gặp phải lời nói, hảo hảo cảm ơn một phen, đã muốn khảo hạch thất tinh, ta hiện tại đi chuẩn bị ngay...”"),t("br"),t("br"),n._v("Vệ Nhiễm Cầm hội trưởng gật đầu."),t("br"),t("br"),n._v("“Làm phiền hội trưởng...” Trương Huyền ôm quyền."),t("br"),t("br"),n._v("“Không tính làm phiền, công hội nhiều ra thiên tài, đối với chúng ta tới nói, cũng là chuyện may mắn...” Vệ Nhiễm Cầm cười cười, quay đầu dặn dò: “Tử Yên, ngươi ở đây hảo hảo bồi Trương sư một chút, ta đi chuẩn bị một chút, rất nhanh liền trở về!”"),t("br"),t("br"),n._v("“Đúng!”"),t("br"),t("br"),n._v("Vệ Nhiễm Cầm rời khỏi, Tử Yên tiên tử cùng một bên Sở Tường lập tức quay đầu nhìn lại, tất cả đều tràn đầy ngạc nhiên."),t("br"),t("br"),n._v("Vệ hội trưởng luôn luôn là thâm cư không ra ngoài, không màng danh lợi, đối với người nào đều hỉ nộ không lộ, liền xem như Tử Yên tiên tử, cũng là lần thứ nhất thấy lão sư hưng phấn như thế."),t("br"),t("br"),n._v("“Trương Huyền huynh đệ, ngươi thật sự là... Kinh hồng sư?”"),t("br"),t("br"),n._v("Sở Tường vẫn như cũ có chút không dám tin tưởng."),t("br"),t("br"),n._v("“Đúng!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Ai nha! Ta làm sao đần như vậy...”"),t("br"),t("br"),n._v("Thấy hắn thừa nhận, Sở Tường vỗ trán một cái."),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Trương Huyền khó hiểu."),t("br"),t("br"),n._v("“Học tập kinh hồng sư chức nghiệp, liền có thể có cơ hội tiếp xúc kinh hồng sư, về sau muốn nhìn bao nhiêu kinh hồng vũ, đều không cần dùng tiền, trọng yếu nhất chính là... Làm quan hưởng lộc vua, ở chùa ăn lộc Phật, nhìn Trương huynh ngươi, tới đây đều có như thế tuyệt sắc ba vị mỹ nữ làm bạn, quả thực liền là nhân sinh người thắng lớn...”"),t("br"),t("br"),n._v("Sở Tường một mặt hối hận: “Ta lúc đầu làm sao lại choáng váng, không đi học tập, nếu không, cuộc sống bây giờ, khẳng định cũng hoàn toàn lấy khác biệt...”"),t("br"),t("br"),n._v("“...” Trương Huyền."),t("br"),t("br"),n._v("Không thèm để ý cái này đầy trong đầu đều là những thứ này gia hỏa, Trương Huyền nhìn về phía trước mắt Tử Yên tiên tử: “Ta muốn hỏi thăm một chút, thất tinh kinh hồng sư làm sao khảo hạch?”"),t("br"),t("br"),n._v("“Ngươi không biết?”"),t("br"),t("br"),n._v("Tử Yên tiên tử sững sờ."),t("br"),t("br"),n._v("Không biết liền dám tới khảo hạch, lá gan thật là đủ lớn."),t("br"),t("br"),n._v("“Không biết...” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Thất tinh kinh hồng sư, thật ra thì cùng lục tinh khảo hạch không khác nhau quá nhiều, ngươi đã kiểm tra qua lục tinh, cũng không cần lo lắng!” Tử Yên tiên tử gật đầu."),t("br"),t("br"),n._v("“Khụ khụ, ta có thể hỏi một chút... Lục tinh làm sao kiểm tra sao?” Trương Huyền vò đầu."),t("br"),t("br"),n._v("Hắn nhận được lục tinh huy chương, là Vệ Nhiễm Tuyết hỗ trợ xin, cũng không tham gia qua khảo hạch."),t("br"),t("br"),n._v("Đối với làm sao khảo hạch, thì sao xem như thông qua, đồng dạng không rõ ràng."),t("br"),t("br"),n._v("Nhướng mày, Tử Yên tiên tử trên mặt không vui."),t("br"),t("br"),n._v("Thất tinh khảo hạch không hiểu, thì cũng thôi đi, ngươi đều là lục tinh kinh hồng sư, thế mà liền khảo hạch lục tinh cũng không biết, ngươi là cố ý tới đùa nghịch ta chơi a?"),t("br"),t("br"),n._v("“Ta trở thành lục tinh kinh hồng sư, cũng không tham gia khảo hạch, mà là... Vệ viện trưởng trực tiếp hỗ trợ xin, cho nên, đối với khảo thí quá trình cũng không rõ ràng!”"),t("br"),t("br"),n._v("Thấy nàng hiểu lầm, Trương Huyền nói."),t("br"),t("br"),n._v("“Trực tiếp hỗ trợ xin? Cái này sao có thể? Ngươi không thi triển kinh hồng vũ, người khác cũng không biết, ngươi đối cái nghề nghiệp này lý giải bao sâu, làm sao có thể trực tiếp ban tặng huy chương? Quả thực vớ vẩn!”"),t("br"),t("br"),n._v("Tử Yên tiên tử hất lên ống tay áo."),t("br"),t("br"),n._v("Khoác lác cũng phải có hạn độ đi!"),t("br"),t("br"),n._v("Mặc dù kinh hồng sư chức nghiệp, không giống thượng cửu lưu chức nghiệp khảo hạch sâm nghiêm như vậy, nhưng cũng là đặc thù chức nghiệp, mỗi một lần khảo hạch, đều cần ít nhất cùng cấp bậc, hàng trưởng lão cùng một chỗ xét duyệt, xác định về sau, mới có thể ban bố đẳng cấp huy chương..."),t("br"),t("br"),n._v("Khảo hạch đều không có khảo hạch, trực tiếp ban tặng... Nói đùa cái gì?"),t("br"),t("br"),n._v("Dù sao nàng theo bắt đầu tu luyện kinh hồng sư đến bây giờ, được người xưng là hiếm có thiên tài, cũng không gặp qua như vậy chuyện nghịch thiên."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Không nghĩ tới nói thật, đối phương thế mà không tin, Trương Huyền không biết giải thích như thế nào, không thể làm gì khác hơn nói: “Như vậy đi, các ngươi công hội Tàng Thư khố ở nơi nào, có thể hay không mang ta tới nhìn một chút?”"),t("br"),t("br"),n._v("Đối phương không nói cũng không sao, chỉ cần có sách, tìm kiếm một chút, có lẽ liền có thể tìm tới đáp án."),t("br"),t("br"),n._v("Lại nói, hắn hiện tại chỉ là có thể so với thất tinh, chỉ có nhìn thất tinh cấp bậc sách, ngưng tụ suốt ngày đạo công pháp, mới tính được là bên trên chân chính thất tinh kinh hồng sư."),t("br"),t("br"),n._v("“Lão sư một hồi liền đến, trước tiên ở nơi này chờ xem...”"),t("br"),t("br"),n._v("Khoát tay áo, Tử Yên tiên tử đang nghĩ nói chuyện, chỉ thấy lão sư Vệ Nhiễm Cầm hội trưởng đi tới, còn chưa tới đến trước mặt, liền một mặt vui vẻ."),t("br"),t("br"),n._v("“Chúc mừng Trương sư, ngươi đối kinh hồng sư lý giải, Vệ Nhiễm Tuyết đã báo lên tổng bộ, hơn nữa thông qua được xét duyệt, vừa rồi xin một chút, tổng bộ hồi phục không cần khảo hạch, trực tiếp ban tặng thất tinh huy chương...”"),t("br"),t("br"),n._v("Vệ Nhiễm Cầm cười nói, đem một cái huy chương đưa tới."),t("br"),t("br"),n._v("“Đa tạ!”"),t("br"),t("br"),n._v("Nhận lấy huy chương, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("“Thật có thể trực tiếp ban tặng?”"),t("br"),t("br"),n._v("Tử Yên tiên tử ngốc tại chỗ."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1151"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1153"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);