(window.webpackJsonp=window.webpackJsonp||[]).push([[1851],{2206:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-589-lai-dung-suc-lon-roi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-589-lai-dung-suc-lon-roi"}},[n._v("#")]),n._v(" Chương 589: Lại dùng sức lớn rồi")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Nguy rồi, Trương sư làm sao lại đáp ứng tỷ thí quyền pháp?”"),t("br"),t("br"),n._v("Thấy rõ ràng trên đài tình huống, Khang đường chủ tràn đầy lo lắng."),t("br"),t("br"),n._v("Lúc trước hắn không cho rằng Trương sư có thể thu hoạch được quán quân nguyên nhân chủ yếu, chính là sợ xuất hiện loại tình huống này."),t("br"),t("br"),n._v("Tu vi quá yếu, chân chính đối chiến thời điểm, liền sẽ bị đối thủ có thể thừa dịp."),t("br"),t("br"),n._v("Luận võ, dựa vào là thực lực, dựa vào là tu vi, coi như trước đó biểu hiện lại nghịch thiên, tâm cảnh khắc độ lại cao hơn, cũng vô dụng!"),t("br"),t("br"),n._v("“Đúng vậy a, là có chút lỗ mãng...”"),t("br"),t("br"),n._v("Tô sư, Lăng sư cũng khuôn mặt xanh xám, có chút khó coi."),t("br"),t("br"),n._v("Mặc dù vạn quốc liên minh, dựa theo lần trước thành tích, không cách nào quyết định so cái gì, có thể... Vẫn là có một lần cơ hội cự tuyệt, đối phương khiêu chiến luận võ, vậy ngươi liền cự tuyệt a?"),t("br"),t("br"),n._v("“Kỳ thật... Cự tuyệt cũng không có tác dụng gì!” Một bên Triệu Phi Vũ nhìn ra mấy vị danh sư ý tưởng, cười khổ mở miệng."),t("br"),t("br"),n._v("“Ừm?” Mọi người nhướng mày, nhìn lại."),t("br"),t("br"),n._v("Chỉ cần không luận võ, Trương sư tại cái khác phương diện trình độ, khẳng định không kém gì bất luận kẻ nào, đối phương muốn chiến thắng, gần như không có khả năng!"),t("br"),t("br"),n._v("Vì cái gì nói cự tuyệt cũng vô dụng?"),t("br"),t("br"),n._v("“Đối phương biết Trương sư liền tu vi yếu kém cái này một cái khâu, làm sao có thể từ bỏ cơ hội? Nếu như Trương sư không đáp ứng so quyền, khẳng định sẽ nói tiến hành so kiếm, hoặc là so đao... Thậm chí, so chân khí hùng hậu mức độ, có thể cự tuyệt một cái, khẳng định cự tuyệt không được cái thứ hai!” Triệu Phi Vũ nói."),t("br"),t("br"),n._v("Nàng mặc dù chưa có xem chân chính danh sư thi đấu, nhưng nhìn qua không ít sách vở, đối với quy tắc, vẫn là biết rất rõ ràng."),t("br"),t("br"),n._v("t r u y e n c u a tu i . v n"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Tô sư, Khang đường chủ đám người sắc mặt cứng đờ."),t("br"),t("br"),n._v("Không phải bọn họ đần, không nghĩ tới điểm ấy, mà là... Quan tâm sẽ bị loạn, trong lúc nhất thời không có kịp phản ứng mà thôi."),t("br"),t("br"),n._v("Hơn nữa, thân là danh sư, tỷ thí lại là công bằng công chính, ai sẽ nghĩ đến như vậy đầu cơ trục lợi?"),t("br"),t("br"),n._v("Lúc này nghe cô bé trước mắt nói ra, tất cả đều khí râu ria thổi lên, tràn đầy tức giận."),t("br"),t("br"),n._v("“Hèn hạ!”"),t("br"),t("br"),n._v("Lăng sư hét lớn một tiếng: “Quả thực quá hèn hạ!”"),t("br"),t("br"),n._v("“Kỳ thật cũng không tính được hèn hạ, đã tỷ thí, đương nhiên muốn xuất ra tuyệt chiêu lợi hại nhất.” Khang đường chủ lắc đầu: “Hiện tại liền xem, có thể hay không lại phát sinh kỳ tích...”"),t("br"),t("br"),n._v("“Kỳ tích? Rất khó, Trương sư là vừa vặn đột phá tu vi, không nói trước củng cố không củng cố, chỉ nói đối phương Trọc Thanh cảnh trung kỳ thực lực, hai người chênh lệch hai cái tiểu cấp bậc, sẽ rất khó chiến thắng...” Tô sư lắc đầu."),t("br"),t("br"),n._v("Đi đến Hóa Phàm, mỗi một cái cấp bậc ở giữa, đều như là lạch trời, không nói cái khác, chỉ nói lực lượng, Âm Dương cảnh đỉnh phong là 80 vạn đỉnh lực lượng, mà Trọc Thanh cảnh trung kỳ là 1 20 vạn đỉnh, chênh lệch ròng rã 40 vạn, không phải đơn thuần vận khí tốt liền có thể triệt tiêu chênh lệch."),t("br"),t("br"),n._v("“Đúng vậy a, bọn họ chênh lệch quá lớn, ta cũng không coi trọng Trương sư... A?”"),t("br"),t("br"),n._v("Gật gật đầu, Khang đường chủ cũng đầy tâm tro tối, đang cảm thấy Trương sư có thể sẽ bại, đột nhiên một lần cứng đờ, cả người mở to hai mắt nhìn, như là cứng đờ."),t("br"),t("br"),n._v("“Làm sao vậy...”"),t("br"),t("br"),n._v("Tô sư nhịn không được nhìn qua."),t("br"),t("br"),n._v("“Mau nhìn trên đài...”"),t("br"),t("br"),n._v("Khang đường chủ vội nói."),t("br"),t("br"),n._v("“Trên đài?” Tô sư vội vàng ngẩng đầu, chỉ thấy Đinh số lôi đài, chỉ có Trương Huyền một người đứng ở phía trên, mà đối diện Ngô Thiên Hào nhưng không thấy tung tích."),t("br"),t("br"),n._v("“Cái kia cùng hắn so quyền gia hỏa đâu?”"),t("br"),t("br"),n._v("Tô sư vội vàng hỏi."),t("br"),t("br"),n._v("Làm sao lại liếc mắt không có nhìn, cái tên này đã không thấy tăm hơi?"),t("br"),t("br"),n._v("Tỷ thí sau khi bắt đầu, chỉ cần rời khỏi tỷ thí đài, sẽ bị trực tiếp phán định thua!"),t("br"),t("br"),n._v("“Hắn... Hắn...”"),t("br"),t("br"),n._v("Khang đường chủ bờ môi run rẩy, hướng về phía không trung một ngón tay: “Hắn đang bay...”"),t("br"),t("br"),n._v("“Bay?”"),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Tô sư đám người, kém chút ngã sấp xuống."),t("br"),t("br"),n._v("Biết bay? Chẳng lẽ tên kia là cái Thánh giả?"),t("br"),t("br"),n._v("Đồng loạt theo ngón tay nhìn lại, vừa nhìn phía dưới, lập tức khắp khuôn mặt là cổ quái."),t("br"),t("br"),n._v("Chỉ thấy bầu trời quả nhiên một cái bóng người màu đen, ở tự do bay lượn, đã bay ra chí ít một trăm mét có hơn, vẫn còn tiếp tục kéo lên."),t("br"),t("br"),n._v("Nếu như là công kích, khẳng định là càng bay càng gần, mà cái tên này, vẻ mặt kinh hoảng, giống như gặp quỷ không ngừng thét lên, lấy cực nhanh tốc độ về sau phi hành, giống như bị đá bay bóng đá."),t("br"),t("br"),n._v("“Đây là có chuyện gì?” Tô sư thực sự nhịn không được."),t("br"),t("br"),n._v("“Trương sư... Nhẹ nhàng đẩy, hắn... Liền bay!” Khang đường chủ khóe miệng giật một cái."),t("br"),t("br"),n._v("Coi như tình cảnh vừa nãy, tận mắt thấy, cũng là cảm thấy không thể tưởng tượng nổi."),t("br"),t("br"),n._v("Vị này tông môn tới Trọc Thanh cảnh trung kỳ cường giả, dựa dẫm tu vi, đối Trương sư một quyền oanh kích mà tới."),t("br"),t("br"),n._v("Xem chiêu này uy lực, liền xem như hắn, đều muốn tạm thời tránh mũi nhọn, có thể... Trương sư động đều không có động, bàn tay nghênh đón tiếp lấy, nhẹ nhàng đẩy, sau đó, liền không có sau đó..."),t("br"),t("br"),n._v("Cái tên này đã bay..."),t("br"),t("br"),n._v("“Nhẹ nhàng đẩy?”"),t("br"),t("br"),n._v("Tô sư, Lăng sư, Kim Tòng Hải đám người nhìn nhau, cảm thấy giống như là nghe sách, tất cả đều mộng."),t("br"),t("br"),n._v("Hóa Phàm tứ trọng trung kỳ, cùng bọn hắn thực lực không kém bao nhiêu, mở mang kiến thức, danh sư cấp bậc, cũng không hề yếu, bị Trương sư bàn tay nhẹ nhàng đẩy liền bay... Làm sao nghe, cùng kể chuyện xưa đồng dạng, như thế không khiến người ta tin tưởng đâu?"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mộng bức không riêng gì bọn họ, toàn bộ quảng trường cũng tất cả đều lặng ngắt như tờ."),t("br"),t("br"),n._v("Vốn là, mọi người thấy vị này Ngô Thiên Hào, trực tiếp khiêu chiến quyền pháp, cũng cảm thấy có chút tức giận, nhưng làm thấy cảnh này, tất cả đều mắt lớn trừng mắt nhỏ, tròng mắt sắp rơi ra tới."),t("br"),n._v(" Làm sao cái tình huống?"),t("br"),t("br"),n._v("Bọn họ nghĩ tới Trương sư có thể sẽ thắng, thế nhưng phải kể tới chiêu về sau, giành được mười phần gian nan, mà trước mắt đẩy liền bay, cùng ném đi cái tảng đá, thật hay giả?"),t("br"),t("br"),n._v("Lúc nào, Trọc Thanh cảnh trung kỳ cường giả dễ dàng như vậy đánh bại?"),t("br"),t("br"),n._v("“Là Trương sư nhãn lực quá lợi hại!”"),t("br"),t("br"),n._v("“Đem Ngô sư đánh bay, dùng cũng không phải là lực lượng của mình, mà là mượn lực đả lực!”"),t("br"),t("br"),n._v("“Đúng vậy a, trong nháy mắt nhìn ra đối phương lực lượng quỹ tích vận hành, đồng thời sử dụng thủ pháp đem hắn áp chế, dựa thế đánh bay... Chiêu này quả thực chính là chiến đấu bên trong, lấy yếu thắng mạnh bản mẫu!”"),t("br"),t("br"),n._v("“Nhanh như vậy đối chiến tốc độ, hắn là như thế nào nhìn ra được?”"),t("br"),t("br"),n._v("Rất nhanh, không ít người kịp phản ứng, từng cái tràn đầy khiếp sợ."),t("br"),t("br"),n._v("Vừa rồi giao thủ động tác mặc dù nhanh, nhưng tại trận tứ tinh danh sư không ít, nhãn lực kinh người, vẫn có thể nhìn ra một vài vấn đề."),t("br"),t("br"),n._v("Ngô Thiên Hào một quyền đánh tới, dùng ra lực lượng toàn thân, kết quả... Mới vừa không thể lâu, nhu không thể giữ, quá cứng dễ yểu, đối phương trong nháy mắt liền phát hiện chiêu số lỗ thủng, mượn cơ hội dùng bàn tay kéo một cái, liền một chiêu đều không có kết thúc, liền bị ném bay ra ngoài."),t("br"),t("br"),n._v("Ngay sau đó... Liền xuất hiện cái này không trung phi nhân một màn."),t("br"),t("br"),n._v("Ngô Thiên Hào đồng bạn thấy cảnh này, cũng mí mắt xoay loạn, kém chút thổ huyết."),t("br"),t("br"),n._v("Sớm biết thực lực của người này lợi hại như vậy, ra cái gì chủ ý ngu ngốc? Cái này tốt, coi như Ngô Thiên Hào không chút bị thương, có thể lần này phi hành... Quăng không chết cũng kém không nhiều phế đi!"),t("br"),t("br"),n._v("Chẳng lẽ... Chính mình là trong truyền thuyết “Heo đồng đội” ?"),t("br"),t("br"),n._v("Ngay tại tự trách, quả nhiên thấy bay không biết bao xa Ngô Thiên Hào, không có lực lượng, thẳng tắp từ không trung rơi xuống dưới, một đầu đâm vào minh chủ phủ cao lớn nhà lầu, một hồi bụi mù dâng lên cùng như giết heo kêu thảm... Lập tức biến mất ở ánh mắt."),t("br"),t("br"),n._v("“Thiên Hào sư đệ...”"),t("br"),t("br"),n._v("Chạm điện nhảy một cái, vội vàng hướng về phía hắn rơi xuống phương hướng phóng đi, còn chưa đi xa, liền nghe đến Đinh số trên lôi đài thanh niên, tràn đầy thanh âm vô tội vang lên: “Cái này, xấu hổ... Lại dùng sức lớn rồi...”"),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Dùng sức lớn rồi?"),t("br"),t("br"),n._v("Thân thể nhoáng một cái, kém chút té ngã trên đất."),t("br"),t("br"),n._v("Ngươi thế nhưng là thực lực yếu, tu vi thấp, đem tu vi cao đánh bay, lại còn nói dùng sức lớn..."),t("br"),t("br"),n._v("Càng nghĩ càng thấy đến buồn bực, có chút phát điên, tuyển bạt thi đấu, làm quan báo cáo phế, thi đấu vừa bắt đầu, đã đem vượt qua bản thân hai cái tiểu cấp bậc cường giả, ném lên bầu trời..."),t("br"),t("br"),n._v("Cái tên này đến cùng là cái dạng gì quái thai?"),t("br"),t("br"),n._v("Còn có thiên lý hay không?"),t("br"),t("br"),n._v("Hắn buồn bực sắp phát điên, kỳ thật nói thật, Trương Huyền càng thêm chút buồn bực."),t("br"),t("br"),n._v("Cái tên này khí thế hùng hổ mà đến, vừa ra tay liền dùng ra lực lượng cường đại nhất, đổi lại người khác là nghiền ép, nhưng đối với hắn tới nói, không đáng kể chút nào."),t("br"),t("br"),n._v("Chân khí của hắn lực lượng 100 vạn đỉnh, hồn lực 1 60 vạn đỉnh, chỉ cần Vu hồn không rời đi thân thể, không có người có thể nhìn ra, 2 60 vạn đỉnh cự lực, so Hợp Linh cảnh trung kỳ cường giả đều chỉ mạnh không yếu, cái tên này có thể đỡ nổi mới là lạ."),t("br"),t("br"),n._v("Bất quá, hắn cũng biết, đang đối mặt quyền, đem hắn đánh bay, khẳng định quá mức kinh thế hãi tục, cố ý dùng Minh Lý Chi Nhãn tìm cái sơ hở, muốn đem hắn ném lôi đài..."),t("br"),t("br"),n._v("Ai ngờ, cái tên này dùng lực lượng quá lớn, lại thêm hắn sau khi tấn thăng, đối Hóa Phàm tứ trọng cường giả thực lực cụ thể nắm chắc không được, hơi tăng thêm chút khí lực..."),t("br"),t("br"),n._v("Theo cao như vậy địa phương ngã xuống, coi như Trọc Thanh cảnh trung kỳ thực lực, đoán chừng cũng phải rơi thất điên bát đảo, cho dù chết không được, một, hai tháng muốn đứng lên cũng khó."),t("br"),t("br"),n._v("Cẩn thận nói đến, hắn thật không phải cố ý."),t("br"),t("br"),n._v("Chỉ cùng phân thân giao thủ qua, lúc ấy bị ngược cùng chó đồng dạng, vốn cho rằng Hóa Phàm tứ trọng cường giả, đều cực kỳ cường đại, ai nghĩ đến quăng ra liền bay xa như vậy..."),t("br"),t("br"),n._v("“Quên đi, Hồng sư, ta đây coi là thắng sao?”"),t("br"),t("br"),n._v("Biết người đều bay, xoắn xuýt nhiều hơn nữa cũng vô dụng, Trương Huyền đành phải bất đắc dĩ nhìn về phía Hồng sư."),t("br"),t("br"),n._v("“Thắng, thắng!”"),t("br"),t("br"),n._v("Hồng sư khóe miệng giật một cái."),t("br"),t("br"),n._v("Xem ra, cái tên này có thể làm hỏng không riêng gì pháp bảo, còn có người..."),t("br"),t("br"),n._v("Không chỉ hắn ý nghĩ này, trên đài tham gia tỷ thí không ít danh sư, tất cả đều lông mày nhảy loạn."),t("br"),t("br"),n._v("“Tuyệt đối đừng cùng cái tên này luận võ!”"),t("br"),t("br"),n._v("“Đúng vậy a, có thể... Không luận võ so cái gì?”"),t("br"),t("br"),n._v("“Ta cũng không biết, dù sao không luận võ là được rồi, nếu không, chết rồi cũng không biết thế nào chết...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Vốn cho rằng, cái tên này chỉ có Âm Dương cảnh đỉnh phong, chỉ cần thi triển thực lực chân thật, vượt qua hắn vẫn là rất nhẹ nhàng, nằm mơ đều không có nghĩ đến... Căn bản không phải chuyện này."),t("br"),t("br"),n._v("Ngô Thiên Hào chính là cái thê thảm đau đớn ví dụ."),t("br"),t("br"),n._v("Không phải tự mình trải qua, đều cảm thấy đau nhếch miệng, huống chi người trong cuộc."),t("br"),t("br"),n._v("Trương Huyền bên này rất nhanh thắng được, cái khác ba cái sân bãi, thời gian dài cũng đều có kết quả."),t("br"),t("br"),n._v("Lần này tỷ thí, liền hắn luận võ, còn lại ba cái sân bãi, đều dùng danh sư phương thức giải quyết chiến đấu."),t("br"),t("br"),n._v("Thậm chí một cái, còn dùng tới “Đấu khẩu”."),t("br"),t("br"),n._v("Danh sư, không chỉ đối công pháp lý giải sâu, tri thức lượng còn muốn rộng."),t("br"),t("br"),n._v("Đấu khẩu, cùng biện đan có chút giống, chính là dùng ngôn ngữ, công kích đối phương, nói ra làm cho đối phương không cách nào trả lời tri thức, ai trả lời trước không ra, ai liền nhận thua."),t("br"),t("br"),n._v("Vì công bằng, loại này tỷ thí bình thường đều là ba cục hai thắng."),t("br"),t("br"),n._v("Tiến hành đấu khẩu hai bên, đi qua ba cục tỷ thí, cùng La Tuyền cùng một tông môn một cái khác danh sư, thu được thắng lợi."),t("br"),t("br"),n._v("Lại nhìn một hồi, phát hiện mọi người tỷ thí cơ bản đều cơ bản giống nhau, Trương Huyền liền đã mất đi hứng thú, không biết qua bao lâu, Hồng sư thanh âm vang lên lần nữa."),t("br"),t("br"),n._v("“Tốt, hiện tại mười sáu người đứng đầu đã xuất hiện, mọi người nghỉ ngơi nửa canh giờ, sau đó tiến hành trước tám cạnh tranh!”"),t("br"),t("br"),n._v("“Top 16 đi ra?”"),t("br"),t("br"),n._v("Giương mắt muốn nhìn một chút đều có ai đi vào Top 16, chỉ thấy Nhược Hoan công tử vẻ mặt lo lắng đi tới."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-597"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-599"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);