(window.webpackJsonp=window.webpackJsonp||[]).push([[1852],{2207:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-590-trieu-phi-vu-phan-tich"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-590-trieu-phi-vu-phan-tich"}},[n._v("#")]),n._v(" Chương 590: Triệu Phi Vũ phân tích")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Trương sư...”"),t("br"),t("br"),n._v("Đi tới trước mặt, Nhược Hoan công tử chần chờ một chút, một cắn răng: “Tại hạ, có việc muốn nhờ!”"),t("br"),t("br"),n._v("“Mời nói!”"),t("br"),t("br"),n._v("Thấy vị này Vạn Quốc thành cao ngạo nhất công tử, vậy mà đi tìm đến, Trương Huyền mỉm cười."),t("br"),t("br"),n._v("Hai người vừa mới bắt đầu là có chút hiểu lầm, có điều, ở đi Băng Nguyên cung thời điểm, đã hóa giải, hơn nữa cùng là vạn quốc liên minh người dự thi, có thể trợ giúp, chắc chắn sẽ không cự tuyệt."),t("br"),t("br"),n._v("“Vốn là ta đối mười vị trí đầu không có cái gì hi vọng, nhưng... Đã đi vào Top 16, nếu như không tranh một lần, thực sự không cam tâm...” Nhược Hoan công tử hơi đỏ mặt."),t("br"),t("br"),n._v("Hắn quất trúng “Giáp nhất”, tự động luân không, nói cách khác, theo thi đấu bắt đầu đến bây giờ, một hồi khảo hạch đều không có trải qua, liền tiến vào thập lục cường."),t("br"),t("br"),n._v("Người là có ham muốn, nếu là trước đó liền dừng bước, cũng là không xoắn xuýt. Hiện tại cũng đi vào thập lục cường, tái chiến thắng một hồi liền có thể đi vào mười vị trí đầu, đổi lại ai, đều sẽ tâm động không dứt."),t("br"),t("br"),n._v("“... Cùng ta đối chiến, là Bạch Dương tông Tần Lỗi!”"),t("br"),t("br"),n._v("Đem tình huống nói một lần, Nhược Hoan công tử trên mặt thần sắc lo lắng: “Ta không có tự tin có thể vượt qua!”"),t("br"),t("br"),n._v("“Tần Lỗi? Tiềm lực bảng xếp hạng thứ ba vị kia?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Trước đó Khang đường chủ cho danh sách thượng, có cái tên này, Bạch Dương tông siêu cấp thiên tài, tứ tinh đỉnh phong danh sư, Hóa Phàm tứ trọng cường giả đỉnh phong, ở tiềm lực trên bảng thứ tự, gần với Lưu Vân tông La Tuyền cùng Hàn Khí tông Liêu Vũ Chí, là toàn bộ thi đấu, đều cực kỳ nổi danh đoạt giải quán quân đứng đầu."),t("br"),t("br"),n._v("“Vâng!” Nhược Hoan công tử gật đầu."),t("br"),t("br"),n._v("“Nếu như là hắn, muốn vượt qua, chỉ sợ không quá dễ dàng!” Trương Huyền cau mày."),t("br"),t("br"),n._v("Nhược Hoan công tử mặc dù trong khoảng thời gian này tiến bộ rất lớn, thực lực cũng đạt tới Hóa Phàm tứ trọng đỉnh phong, nhưng muốn cùng đối phương loại này tông môn đệ tử so, vẫn là kém một mảng lớn."),t("br"),t("br"),n._v("Nội tình ở nơi nào bày biện, không phải một hai tháng lâm trận mới mài gươm liền có thể bù đắp."),t("br"),t("br"),n._v("“Cho nên, muốn cầu Trương sư chỉ điểm... Mặc kệ về sau có thể thắng hay không ra, tại hạ đều vô cùng cảm kích!” Nhược Hoan công tử vội nói."),t("br"),t("br"),n._v("Vị này Trương sư lần thi đấu này thực sự quá chói mắt, ngay cả nhân khí vượng nhất La Tuyền, Liêu Vũ Chí bọn người bị ép xuống."),t("br"),t("br"),n._v("Muốn nói ai có thể để hắn cách thành công tiếp cận, tuyệt không phải lão sư Khang đường chủ, mà là trước mắt vị này."),t("br"),t("br"),n._v("“Chỉ điểm không có vấn đề, chỉ là...”"),t("br"),t("br"),n._v("Không nghĩ tới hắn đánh cái chủ ý này, Trương Huyền lắc đầu: “Nhưng... Các ngươi liền tỷ thí cái gì nội dung cũng không biết, như thế nào chỉ điểm?”"),t("br"),t("br"),n._v("Danh sư thi đấu, tỷ thí nội dung, đa dạng, vạn quốc liên minh làm lần trước thi đấu xếp hạng hạng chót tồn tại, không có tư cách tuyển chọn, chỉ có thể bị động tiếp nhận, đối phương sẽ chọn dạng gì tỷ thí đều không rõ ràng, làm sao chỉ điểm?"),t("br"),t("br"),n._v("Coi như thật muốn thắng, cũng phải nói trước nhân gia ra bài gì đi, mặt bài cũng không biết, đừng nói bản thân, liền xem như chân chính lục tinh, thất tinh danh sư, cũng bất lực."),t("br"),t("br"),n._v("Mình coi như muốn giúp cũng lực bất tòng tâm!"),t("br"),t("br"),n._v("“Bên trên một cửa ải cùng hắn đối chiến chính là Chân Tức tông Ngô Thanh, hai người bất kể từ chỗ nào một điểm, đều không kém nhiều, lúc này mới đưa ra giáo thư dục nhân tỷ thí, cũng dùng cái này chiến thắng, đối mặt ta... Ta cảm thấy chắc chắn sẽ không phiền toái như vậy, có khả năng trực tiếp tuyển chọn luận võ!”"),t("br"),t("br"),n._v("Nhược Hoan công tử chần chờ một chút nói."),t("br"),t("br"),n._v("Ngô Thanh, cũng là tiềm lực bảng bài danh mười vị trí đầu danh sư, kết quả vận khí không tốt, gặp được vị này Tần Lỗi, cửa thứ nhất đều không có qua, liền bị đào thải."),t("br"),t("br"),n._v("Hai người thực lực không kém bao nhiêu, thủ đoạn khác cũng rất gần, Tần Lỗi đưa ra giáo thư dục nhân tỷ thí."),t("br"),t("br"),n._v("Bất quá, đối mặt hắn, chắc chắn sẽ không như vậy lớn phí trắc trở, vô cùng có khả năng đưa ra luận võ."),t("br"),t("br"),n._v("Mặc dù cùng là Hóa Phàm tứ trọng đỉnh phong, hắn vừa mới đột phá, đối phương đắm chìm không biết bao lâu, lại thêm tông môn bất kể võ kỹ vẫn là đối thực lực khống chế, đều càng cao thâm hơn, muốn chiến thắng, vẫn là rất đơn giản!"),t("br"),t("br"),n._v(" Bởi vậy, đoán không sai, đối phương gặp được hắn, khẳng định sẽ trực tiếp dùng nghiền ép phương thức."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền vò đầu."),t("br"),t("br"),n._v("Nếu như là luận võ, ngược lại cũng dễ nói, nhờ vào chỉ điểm của mình, để hắn trong vòng nửa canh giờ, tăng lên một đoạn, cũng không phải làm không được, chỉ bất quá... Vạn nhất so không phải cái này làm sao bây giờ?"),t("br"),t("br"),n._v("Hết thảy không đều trắng chuẩn bị?"),t("br"),t("br"),n._v("“Trương sư, Nhược Hoan, xuống ăn vài thứ, khôi phục thể lực!”"),t("br"),t("br"),n._v("Đang muốn nói cái gì, liền nghe đến Khang đường chủ thanh âm vang lên."),t("br"),t("br"),n._v("Quay đầu nhìn lại, lúc này mới phát hiện, đi qua cả ngày thi đấu, không ít người đều đã mệt mỏi, đi xuống sân khấu, nghỉ ngơi nghỉ ngơi, ăn uống ăn uống."),t("br"),t("br"),n._v("“Đi xuống trước đi!”"),t("br"),t("br"),n._v("Có biết hay không đối phương muốn khiêu chiến cái gì, thương nghị nhiều hơn nữa cũng vô ích, hai người đi xuống sân khấu."),t("br"),t("br"),n._v("Minh chủ phủ đã chuẩn bị xong các loại ăn uống, mười phần phong phú, thậm chí còn có không ít linh thú thịt, có thể cực lớn điều kiện bổ sung tiêu hao."),t("br"),t("br"),n._v("Không thể không nói, vừa nhìn thấy những này ăn, Trương Huyền thật đúng là cảm thấy đói lả."),t("br"),t("br"),n._v("“Các ngươi vừa rồi thương nghị cái gì đâu?”"),t("br"),t("br"),n._v("Triệu Phi Vũ cười khanh khách nhìn lại."),t("br"),t("br"),n._v("“A, Nhược Hoan công tử kết cục đối chiến Bạch Dương tông Tần Lỗi, muốn cho ta chỉ điểm một chút, nhưng là, không biết đối phương chọn loại phương thức nào tranh tài!”"),t("br"),t("br"),n._v("Trương Huyền vừa ăn vừa nói."),t("br"),t("br"),n._v("“Loại phương thức nào?” Triệu Phi Vũ hai tay nâng cằm lên, ngồi ở một bên, chần chờ một chút, nói: “Ta cảm thấy... Hẳn là sẽ là đối tu vi lý giải!”"),t("br"),t("br"),n._v("“Đối tu vi lý giải?” Trương Huyền, Nhược Hoan công tử bọn người nhìn qua."),t("br"),t("br"),n._v("Bọn họ vừa rồi đoán là luận võ, làm sao nàng đoán hoàn toàn khác biệt?"),t("br"),t("br"),n._v("“Đối tu vi lý giải? Cái này khá là phức tạp, hơn nữa phân tích lên, mười phần phiền phức, có rất ít người tuyển chọn cái này tỷ thí đi!” Tô sư nói."),t("br"),t("br"),n._v("Đối tu vi lý giải, có thể theo mấy cái phương diện giải thích, giảng giải cùng một bản võ kỹ hoặc là giảng giải cùng một bản công pháp... Bất quá, loại nào, đều hao thời hao lực, mấu chốt nhất là, ông nói ông có lý bà nói bà có lý, chỉ cần không nói hiểu trổ hết tài năng, vượt qua quá nhiều, rất khó để cho người ta phán định thắng thua."),t("br"),t("br"),n._v("Tất cả mọi người là tứ tinh đỉnh phong danh sư, đối công pháp lý giải, không kém nhiều, tuyển chọn loại này khá là phiền toái tỷ thí, sợ rằng cũng không nguyện ý đi!"),t("br"),t("br"),n._v("“Nói nghe một chút!”"),t("br"),t("br"),n._v("Biết trước mắt vị công chúa này, mặc dù tu vi không thế nào cao, lại từ nhỏ đọc sách, tri thức lượng cực lớn, cũng không ít quỷ tâm tư, Trương Huyền hỏi."),t("br"),t("br"),n._v("“A, rất đơn giản, nhìn hắn lần này thi đấu bên trong, muốn lấy được nhất cái gì!”"),t("br"),n._v("Triệu Phi Vũ cười cười."),t("br"),t("br"),n._v("“Được cái gì?” Nhược Hoan công tử sững sờ: “Tham gia thi đấu, tự nhiên nghĩ tới quán quân...”"),t("br"),t("br"),n._v("Nếu là tham gia danh sư thi đấu, ai không muốn quán quân? Vị này Tần Lỗi, càng là đoạt giải quán quân đứng đầu, không nghĩ mới kỳ quái."),t("br"),t("br"),n._v("“Vậy được rồi, mà bây giờ, thông hướng quán quân con đường, lớn nhất chướng ngại là cái gì?” Triệu Phi Vũ con mắt cong thành trăng lưỡi liềm, trắng noãn khuôn mặt, chuyển hướng trước mắt cách đó không xa thanh niên."),t("br"),t("br"),n._v("“Đương nhiên là... Trương sư!”"),t("br"),t("br"),n._v("Điểm ấy không cần Nhược Hoan công tử nói, người người đều biết."),t("br"),t("br"),n._v("Trương Huyền đang tuyển chọn thi đấu bên trên biểu hiện, có thể dùng như bẻ cành khô để hình dung, biểu hiện quá mức nghịch thiên. Thi đấu bắt đầu phía sau, để cho người ta một lần cho rằng yếu kém tu vi, cũng toả hào quang rực rỡ, đã trở thành lần thi đấu này, đoạt giải quán quân sốt dẻo nhất người, ngọn gió so La Tuyền đám người, càng hơn một bậc."),t("br"),t("br"),n._v("Muốn nói, vị này Tần Lỗi, muốn đến quán quân lớn nhất chướng ngại, không thể nghi ngờ là hắn."),t("br"),t("br"),n._v("“Đúng a, nếu là Trương sư, ngươi tự nhiên không phải mục tiêu của hắn, nói cách khác... Căn bản không có đưa ngươi đặt ở ngang hàng vị trí!” Triệu Phi Vũ nói."),t("br"),t("br"),n._v("Nhược Hoan công tử hơi đỏ mặt."),t("br"),t("br"),n._v("Mặc dù lời này có chút đả kích người, nhưng hắn biết, nói không sai."),t("br"),t("br"),n._v("Lấy đối phương thực lực, thật sự là hắn không có chiến thắng vốn liếng, còn không đạt tới để cho người ta kiêng kỵ tình trạng."),t("br"),t("br"),n._v("“Ngươi đồng dạng đến từ vạn quốc liên minh, mục tiêu của hắn lại là đánh bại Trương sư, thu hoạch được quán quân... Cho nên, khẳng định sẽ dùng Trương sư am hiểu phương thức, đánh bại ngươi, xem như khiêu khích.”"),t("br"),t("br"),n._v("Triệu Phi Vũ nói tiếp."),t("br"),t("br"),n._v("“Ta am hiểu nhất?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("“Ừm, vừa rồi tuyển bạt thi đấu, ngươi mặc dù ở tâm tính, trí tuệ các phương diện, biểu hiện đều rất kinh người, nhưng muốn tổng kết kinh người nhất, khẳng định là ngộ tính của ngươi cùng sư ngôn thiên bẩm!”"),t("br"),t("br"),n._v("“Không sai!” Khang đường chủ gật đầu."),t("br"),t("br"),n._v("Vừa rồi Trương sư biểu hiện mặc dù rất tạp, nhưng muốn nói để cho người ta ấn tượng khắc sâu nhất, khẳng định là hai cái này."),t("br"),t("br"),n._v("Sư ngôn thiên bẩm mê hoặc Dị Linh tộc nhân tự sát; Ngộ tính cao, nhìn thoáng qua liền xuống Thiên Tinh cờ khôi lỗi nhận thua sụp đổ."),t("br"),t("br"),n._v("Còn đọc thuộc lòng hơn hai vạn bản thư tịch, đó là bởi vì có cái tốt xuất thân, lấy được thần thức đọc sách pháp, rung động mức độ, ngược lại không bằng hai thứ này."),t("br"),t("br"),n._v("“Sư ngôn thiên bẩm, liên quan hồn lực khắc độ, coi như hắn cảm thấy có thể thắng, cũng không vẻ vang, không tạo được oanh động, mà... Ngộ tính lại khác biệt! Ngộ tính cao, đối tu vi lý giải liền cao, dùng cái này tỷ thí, không chỉ có thể chiến thắng Nhược Hoan công tử, còn có thể che chắn Trương sư ánh sáng, lớn như thế đại biểu hiện cơ hội, làm sao có thể không đi chọn chọn?”"),t("br"),t("br"),n._v("Triệu Phi Vũ mỉm cười, đem phân tích của mình toàn bộ nói ra: “Còn các ngươi nói luận võ, ta cảm thấy ngược lại rơi xuống bên dưới thành, đường đường danh sư, chém chém giết giết, thắng cũng không vẻ vang!”"),t("br"),t("br"),n._v("“Đúng a!”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Triệu Phi Vũ liền đối phương tâm lý đều phỏng đoán xuyên, phân tích rõ ràng không sai, đạo lý rõ ràng, so trước đó Nhược Hoan công tử lời nói đáng tin cậy gấp bội."),t("br"),t("br"),n._v("“Như vậy phân tích là không tệ, chẳng lẽ, còn muốn tìm một bản công pháp, bí tịch, phân tích nội dung?” Nhược Hoan công tử mặt xoắn xuýt."),t("br"),t("br"),n._v("Tỷ thí đối tu vi lý giải, trực tiếp nhất thủ đoạn, chính là giảng giải cùng một bản bí tịch, có thể... Đi nơi nào tìm quyển sách này? Cầm ai đều không công bằng đi!"),t("br"),t("br"),n._v("“Đối tu vi lý giải, không chỉ phân tích bí tịch một loại, nếu như ta nhớ kỹ không sai... Có  loại này tỷ thí đi!” Triệu Phi Vũ cười nói."),t("br"),t("br"),n._v("“Thiếu hụt chỉ bảo? Ngươi nói chính là... Tỷ thí hai bên, riêng phần mình thi triển một bộ võ kỹ, sau đó để đối phương phê bình chỉ điểm, ai nói vấn đề lỗ thủng nhiều, ai liền chiến thắng tỷ thí phương pháp?”"),t("br"),t("br"),n._v("Nhược Hoan công tử sững sờ: “Nhưng cái này... Cần vừa ý lực, tri thức lượng có cực cao kiến giải mới được, cấp bậc cao chỉ điểm cấp bậc thấp tạm được, hắn chỉ cho ta đang...”"),t("br"),t("br"),n._v("Hắn mặc dù chưa nói xong, lại biểu đạt rất rõ ràng."),t("br"),t("br"),n._v("Cùng cấp bậc, muốn cho hắn chỉ bảo, cái tên này có phải hay không thật ngông cuồng một chút?"),t("br"),t("br"),n._v("“Không sai, đây cũng là đối tu vi lý giải một loại tỷ thí một trong. Yên tâm, cái này Tần Lỗi, đã muốn làm náo động, để ngươi bại tâm phục khẩu phục, thuận tiện che lấp Trương sư ánh sáng, khẳng định có thể như vậy tuyển chọn!”"),t("br"),t("br"),n._v("Triệu Phi Vũ cười nói."),t("br"),t("br"),n._v("Cái tên này đã muốn làm quán quân, liền muốn đánh phá Trương Huyền trong lòng hắn áp bách, mà cùng hắn cùng một nơi tới Nhược Hoan công tử, chính là tốt nhất đá đặt chân."),t("br"),t("br"),n._v("Dùng nhất lộ mặt phương thức đem hắn đạp xuống đi, mới có thể phụ trợ mạnh mẽ, khí thế bên trên không thua bởi Trương sư."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Nhược Hoan công tử mặt như mướp đắng."),t("br"),t("br"),n._v("Cái này gọi chuyện gì."),t("br"),t("br"),n._v("Vốn cho rằng, nhờ vào thực lực của hắn, có thể gây nên coi trọng của người khác, náo loạn nửa ngày, nhân gia chỉ coi hắn là làm tùy ý giẫm công cụ."),t("br"),t("br"),n._v("“Móa nó, giết chết nha!”"),t("br"),t("br"),n._v("Càng nghĩ càng giận, một cắn răng, quay đầu nhìn về phía cách đó không xa Trương Huyền: “Nếu quả thật muốn như vậy tỷ thí, Trương sư nhưng có cái gì tốt biện pháp, để cho ta chiến thắng?”"),t("br"),t("br"),n._v("Hiện trường tỷ thí, sau đó chỉ bảo cùng cấp bậc cường giả thiếu hụt... Quả thực có thể kinh thế hãi tục, thật như vậy so, bản thân khẳng định không thắng được đối phương."),t("br"),t("br"),n._v("Chỉ có thể ký thác ở trước mắt vị này sáng tạo kỳ tích thanh niên."),t("br"),t("br"),n._v("“Làm sao bây giờ?” Trương Huyền cũng nhíu mày, suy tư phút chốc, ánh mắt sáng lên, trên mặt lộ ra vẻ cổ quái: “Biện pháp ngược lại là có, chính là không tiện lắm làm!”"),t("br"),t("br"),n._v("“Ồ? Chỉ cần có thể vượt qua hắn, bất kể nhiều khó khăn ta đều làm!”"),t("br"),t("br"),n._v("Nghe được có biện pháp, Nhược Hoan công tử ánh mắt sáng lên, vội vàng nhìn qua, trong mắt mang theo thành khẩn: “Kính xin Trương sư truyền thụ...”"),t("br"),t("br"),n._v("“Ây... Vậy được rồi!”"),t("br"),t("br"),n._v("Trương Huyền thuận tay bưng lên mới vừa uống một nửa canh thịt: “Cầm cái này đi qua, hắt hắn mặt!”"),t("br"),t("br"),n._v("“Ự... C? Hắt mặt...”"),t("br"),t("br"),n._v("Nhược Hoan công tử con mắt trợn tròn."),t("br"),t("br"),n._v("Hoành Tảo Thiên Nhai nói"),t("br"),t("br"),n._v("Nhanh cuối tháng, còn có nguyệt phiếu ư?"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-598"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-600"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);