(window.webpackJsonp=window.webpackJsonp||[]).push([[1856],{2211:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-592-dao-đong-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-592-dao-đong-thuong"}},[n._v("#")]),n._v(" Chương 592: Dao động (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 592: Dao động (thượng)"),t("br"),t("br"),n._v("Mặc dù không biết có phải hay không thật gặp quỷ, lại biết Trương sư phán đoán không có sai lầm chút nào, phục sát đất."),t("br"),t("br"),n._v("Trước kia, hắn mệnh danh Vạn Quốc thành thiên tài thứ nhất, một mực đắc chí, cảm thấy anh hùng thiên hạ không người ra hắn phía trên, nhìn thấy trước mắt người thanh niên này, mới hiểu được, cùng thiên tài chân chính so... Còn kém xa lắm."),t("br"),t("br"),n._v("Một lần nữa trở lại trước mặt, thái độ cung kính ôm quyền: “Trương sư, phía dưới nên làm cái gì?”"),t("br"),t("br"),n._v("“Ăn cơm!”"),t("br"),t("br"),n._v("Trương Huyền vẫy tay."),t("br"),t("br"),n._v("Nhược Hoan công tử ngẩn ngơ."),t("br"),t("br"),n._v("Ngươi để cho ta lại hắt canh thịt, lại khiêu khích, còn tưởng rằng sẽ có biện pháp gì tốt, liền... Đơn giản ăn cơm?"),t("br"),t("br"),n._v("“Tranh tài lập tức bắt đầu, ăn no rồi mới có khí lực, nếu không, làm sao có thể thắng?” Trương Huyền liếc một cái."),t("br"),t("br"),n._v("Tổng cộng liền nghỉ ngơi nửa canh giờ, hiện tại cũng sắp kết thúc rồi, lại không mau ăn điểm, tranh tài lập tức liền muốn bắt đầu."),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Biết không có biện pháp nào khác, đành phải đối phương nói cái gì làm cái gì, một cắn răng ngồi xuống, ăn như gió cuốn."),t("br"),t("br"),n._v("Không thể không nói minh chủ phủ chuẩn bị bữa tối quả thực phong phú, rất nhanh ăn uống no nê, mà lúc này tranh tài cũng gần bắt đầu."),t("br"),t("br"),n._v("“Đem phía trên nội dung học thuộc, tỷ thí bắt đầu, dựa theo đọc là được!”"),t("br"),t("br"),n._v("Đang nghĩ ngợi nên như thế nào đối phó Tần Lỗi, chỉ thấy Trương sư đưa qua một tấm giấy trắng, phía trên lít nha lít nhít viết đầy chữ viết, hiển nhiên là vừa rồi hắn lúc ăn cơm viết xuống."),t("br"),t("br"),n._v("Trong lòng kỳ lạ, cúi đầu đối với trang giấy quét tới, vừa nhìn phía dưới, con mắt trợn tròn: “Trương sư, thật mà nói... Cái này?”"),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Mắt tối sầm lại, Nhược Hoan công tử sắp khóc."),t("br"),t("br"),n._v("Đại ca, ngươi đây là ngại vừa rồi đối phương không có đánh chết ta à!"),t("br"),t("br"),n._v("Thật muốn theo cái này nói, đoán chừng tên kia tình nguyện không tham gia danh sư thi đấu, cũng phải đem bản thân bóp chết."),t("br"),t("br"),n._v("“Đừng nói nhảm, nhanh lên một chút nhớ, lên đài phía sau, không thể sửa đổi một chữ, nếu không, ta không bảo đảm ngươi có thể thắng lợi!”"),t("br"),t("br"),n._v("Ngay tại buồn bực, chỉ thấy đối diện Trương sư nhướng mày."),t("br"),t("br"),n._v("Nhược Hoan công tử trong mắt rưng rưng."),t("br"),t("br"),n._v("Sớm biết liền không tìm hắn thỉnh giáo, làm sao cảm giác lên phải thuyền giặc? Cái này tốt, việc đã đến nước này, không làm như vậy cũng không có biện pháp."),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Một cắn răng, đem trang giấy bên trên nội dung ghi xuống, mới vừa ghi vào trong óc, chỉ thấy chiêng đồng gõ lên, Hồng sư rộng lớn thanh âm lần nữa vang lên: “Tiếp tục tranh tài, đi vào lôi đài danh sư mời lên đài!”"),t("br"),t("br"),n._v("Mọi người lên tiếng."),t("br"),t("br"),n._v("Đi tới sân khấu, hít sâu một hơi, Nhược Hoan công tử nhanh chân hướng về phía Giáp số lôi đài đi tới."),t("br"),t("br"),n._v("Rất có anh dũng hy sinh phong phạm."),t("br"),t("br"),n._v("Hiện tại còn muốn những biện pháp khác cũng đã đã chậm, chỉ có thể kiên trì nghe theo Trương sư phân phó, chết thì chết đi!"),t("br"),t("br"),n._v("“A!”"),t("br"),t("br"),n._v("Tần Lỗi cũng tới đến lôi đài, nhớ tới vừa rồi cái tên này khiêu khích, vẫn như cũ lên cơn giận dữ, cả người sắp nổ tung."),t("br"),t("br"),n._v("“Tranh tài bắt đầu, hiện tại riêng phần mình thương nghị cần khảo hạch nội dung!”"),t("br"),t("br"),n._v("Thấy tham gia tỷ thí đi tới trên đài, Hồng sư nhìn quanh một tuần."),t("br"),t("br"),n._v("“Bạch Dương tông lần trước thi đấu, thứ tự khá cao, sở hữu, do ta tuyển chọn!”"),t("br"),t("br"),n._v("Hừ lạnh một tiếng, Tần Lỗi nhìn về phía thanh niên trước mắt: “Ta tuyển chọn... Đối tu vi lý giải!”"),t("br"),t("br"),n._v("“A?”"),t("br"),t("br"),n._v("Nghe được cùng đoán đồng dạng, cái tên này quả nhiên lựa chọn cái này, Nhược Hoan công tử sững sờ."),t("br"),t("br"),n._v("“Làm sao? Không dám?” Mắt sáng lên."),t("br"),t("br"),n._v("“Có gì không dám, so liền so, nói quy tắc đi!” Nhược Hoan công tử vội nói."),t("br"),t("br"),n._v("“Tính ngươi thức thời!”"),t("br"),t("br"),n._v("Còn tưởng rằng đối phương sẽ cự tuyệt, không nghĩ tới sảng khoái như vậy, Tần Lỗi nhẹ nhàng thở ra: “Rất đơn giản, ngươi ta thi triển võ kỹ, riêng phần mình cho đối phương chỉ điểm, ai nói ra lỗ thủng nhiều, ai liền chiến thắng.”"),t("br"),t("br"),n._v("“Ừm!” Nhược Hoan công tử gật đầu."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Cùng ngươi đoán đồng dạng!”"),t("br"),t("br"),n._v("Đem một màn này nhìn ở trong mắt, Khang đường chủ đám người nhìn về phía Triệu Phi Vũ."),t("br"),t("br"),n._v("Cô bé này không phải danh sư, lại đem thi đấu nội dung đoán một điểm không sai, phần này trí tuệ làm cho người khâm phục."),t("br"),t("br"),n._v("“Ừm, phía dưới liền nhìn Trương sư cho tài liệu có hữu dụng hay không!”"),t("br"),t("br"),n._v("Triệu Phi Vũ nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("Nàng cũng là có thể suy đoán ra tỷ thí cái gì nội dung mà thôi, còn có thể hay không chiến thắng, còn tốt nhìn vừa rồi Trương sư để Nhược Hoan công tử ghi nhớ đồ vật là cái gì."),t("br"),t("br"),n._v("“Ừm!” Mọi người gật đầu."),t("br"),t("br"),n._v("Mấy người nói chuyện công pháp, trên đài hai người đều riêng phần mình thi triển xong võ kỹ."),t("br"),t("br"),n._v("Nhìn xong Tần Lỗi đánh ra chiêu số, Nhược Hoan công tử không hiểu ra sao."),t("br"),t("br"),n._v("Đối phương quyền pháp, chưa bao giờ thấy qua, thậm chí nghe đều không có nghe qua... Làm sao chỉ ra thiếu hụt?"),t("br"),t("br"),n._v("“Dựa theo quy định, ta trước hết bắt đầu!”"),t("br"),n._v("Thấy đối phương mặt mê mang, Tần Lỗi cười lạnh một tiếng: “Vừa rồi Nhược Hoan công tử đánh ra quyền pháp, nếu như không nhìn lầm, hẳn là Linh cấp trung phẩm võ kỹ, , đây là rất lợi hại chiêu số, đáng tiếc, ngươi tu luyện không đúng chỗ... Thiếu sót có bốn!”"),t("br"),t("br"),n._v("“Thứ nhất, chân khí vừa mới tăng lên tới Hóa Phàm tứ trọng đỉnh phong, lực lượng còn chưa đủ tinh thuần, sử dụng đi ra chiêu số, mặc dù thoạt nhìn đúng chỗ, trên thực tế lại chỉ tốt ở bề ngoài...”"),t("br"),t("br"),n._v("“Thứ hai, Thất Tuyệt quyền, coi trọng tuyệt tâm, tuyệt tình, tuyệt ý, tuyệt ân, tuyệt duyên, tuyệt niệm, tuyệt si, mà ngươi, hoàn toàn làm không được, chiêu không thể cùng tình hợp, tình không thể cùng ý hợp, thì thôi thi triển, cũng là cái thùng rỗng!”"),t("br"),t("br"),n._v("“Thứ ba...”"),t("br"),t("br"),n._v("“Thứ tư...”"),t("br"),t("br"),n._v("Rất nhanh, Tần Lỗi đem Nhược Hoan công tử tình huống vừa rồi nói một lần, mỗi một câu đều thẳng vào chỗ yếu hại, nghe nhiều, rất dễ dàng đánh mất lòng tin."),t("br"),t("br"),n._v("“Lợi hại!”"),t("br"),t("br"),n._v("“Chỉ nhìn liếc mắt, liền nói ra tứ đại thiếu hụt, không hổ là có tư cách cạnh tranh quán quân nhân vật, đáng sợ!”"),t("br"),t("br"),n._v("“Đúng vậy a, vừa rồi Nhược Hoan công tử chiêu số ta cũng nhìn, một vấn đề đều không có nhìn ra...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được Tần Lỗi mà nói, dưới đài một hồi xôn xao."),t("br"),t("br"),n._v("Tất cả mọi người là danh sư, cũng có nhãn lực, Nhược Hoan công tử vừa rồi chiêu số, viên mãn như ý, cho người ta một loại mượt mà cảm giác, vừa nhìn liền biết chí ít đắm chìm mấy năm..."),t("br"),t("br"),n._v("Dù vậy, đều có thể thuận miệng nói ra bốn dạng thiếu hụt, nhãn lực mạnh, thậm chí so với một ít nửa bước ngũ tinh danh sư, đều không hề yếu."),t("br"),t("br"),n._v("“Xem ra cửa này, Nhược Hoan công tử muốn thắng rất khó!”"),t("br"),t("br"),n._v("“Ừm, dù sao chí ít vừa rồi Tần Lỗi thi triển võ kỹ là cái gì, ta không nhìn ra!”"),t("br"),t("br"),n._v("“Ta cũng nhìn không hiểu...”"),t("br"),t("br"),n._v("Mọi người hạ giọng nói thầm, tất cả đều nhìn về phía lôi đài, muốn nhìn một chút Nhược Hoan công tử ứng đối ra sao."),t("br"),t("br"),n._v("Ngay cả Tần Lỗi cũng cười lạnh một tiếng, nhìn lại."),t("br"),t("br"),n._v("Hắn vừa rồi võ kỹ, đến từ một chỗ di tích, không biết thất truyền đã bao nhiêu năm, thì thôi Hồng sư đều chưa hẳn có thể nhận ra, huống chi kẻ trước mắt này."),t("br"),t("br"),n._v("Bởi vậy, có tuyệt đối tự tin, thắng bên dưới ván này."),t("br"),t("br"),n._v("“Nói đi, đến phiên ngươi, nếu như nói không ra, thì thôi nhận thua!”"),t("br"),t("br"),n._v("Thấy trước mắt Nhược Hoan công tử mặt xoắn xuýt, kìm nén khuôn mặt thấu đỏ, đều không có nói chuyện, Tần Lỗi khẽ nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nhược Hoan công tử giống như là làm ra quyết định gì, một cắn răng: “Nếu như ta không nhìn lầm, ngươi... Đầu óc có vấn đề!”"),t("br"),t("br"),n._v("“Ự... C?”"),t("br"),t("br"),n._v("Mọi người lặng ngắt như tờ."),t("br"),t("br"),n._v("Không phải giải thích võ kỹ ư? Làm sao trực tiếp đổi lấy mắng chửi người?"),t("br"),t("br"),n._v("“Ngươi nói cái gì?”"),t("br"),t("br"),n._v("Quả nhiên, nghe nói như thế, Tần Lỗi kém chút nhịn không được, tại chỗ nổ tung."),t("br"),t("br"),n._v("“Linh hồn ngươi còn không có tẩm bổ tốt, liền cưỡng ép xung kích Hợp Linh cảnh, tùy ý chân khí chạy thức hải, linh hồn đã bị hao tổn, đại não nhận lấy tổn thương, nhưng có việc này?”"),t("br"),t("br"),n._v("Nhược Hoan công tử đạo."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Tần Lỗi nhướng mày."),t("br"),t("br"),n._v("Bạch Khải Chi tông chủ, cũng chính là lão sư hắn, bị chém giết phía sau, có chút nóng nảy, đánh sâu vào một lần Hợp Linh cảnh."),t("br"),t("br"),n._v("Muốn tiến vào Hợp Linh cảnh, chân khí muốn đi vào thức hải, va chạm Hợp Linh huyệt, chỉ có đem cái này huyệt đạo giải khai, mới có thể thuận lợi đột phá... Hắn thực lực còn chưa đủ, cưỡng ép xung kích, quả thực để linh hồn nhận lấy nhất định tổn thương, vừa đến đêm khuya liền cảm thấy đau đầu."),t("br"),t("br"),n._v("Chỉ là, chuyện này, hắn ai cũng chưa nói qua, ngay cả bản tông trưởng lão, đều chưa hề nói, đối phương làm thế nào nhìn ra được tới?"),t("br"),t("br"),n._v("“Không chỉ có như thế, ngươi toàn thân cao thấp, cũng đều tồn tại trên việc tu luyện tai hoạ ngầm. Vì có thể lần này thi đấu bên trong, một tiếng hót lên làm kinh người, cố ý tu luyện một bộ không biết tên võ kỹ... Không nhìn lầm, muốn luyện thành, ít nhất phải toàn thân ba mươi hai chỗ huyệt đạo cộng minh đi!”"),t("br"),t("br"),n._v("“Ba mươi hai chỗ huyệt đạo cộng minh, rất nhiều võ kỹ cũng có thể làm đến, nhưng võ kỹ này, hết lần này tới lần khác đem bách hội cùng dũng tuyền kết nối, một cái hấp thu bầu trời thanh khí, một cái hấp thu mặt đất vẩn đục chi tức, hai hai giao hòa, tựa như tạo thành lồng giam, cứ thế mà đưa ngươi linh hồn, khóa kín tại thân thể bên trong!”"),t("br"),t("br"),n._v("“Nói cách khác, không có đặc thù cơ hội, muốn xung kích Hợp Linh cảnh, gần như không có khả năng! Nếu như không tin, ngươi có thể dùng chân khí thử một chút bích hải huyệt, huyệt đạo tê dại, nói rõ linh hồn đã bắt đầu suy yếu; Cũng có thể thử một chút cần chân huyệt, huyệt đạo ngứa, nói rõ thân thể cứng rắn, cùng lồng giam không có gì khác biệt...”"),t("br"),t("br"),n._v("“Người khác tu luyện, là vì tăng lên, ngươi tu luyện lại là cho mình gông xiềng, không cách nào tiếp tục tiến bộ... Không phải đầu óc có vấn đề là cái gì?”"),t("br"),t("br"),n._v("Như là đã mở đầu, Nhược Hoan công tử không do dự nữa, một cắn răng đem Trương sư để hắn đọc thuộc lòng nội dung, một mạch nói ra."),t("br"),t("br"),n._v("Dù sao chết thì chết, không nói, đối phương võ kỹ, hắn cũng nhìn không hiểu, khẳng định cũng phải thua trận tranh tài."),t("br"),t("br"),n._v("Nói xong một đoạn lớn, vốn cho rằng đối phương sẽ trực tiếp xông lại, đem bản thân đánh gần chết, kém cỏi nhất cũng phải mắng cái vòi phun máu chó, đã thấy đối phương không nhúc nhích, gặp chuyện bất khả tư nghị gì, dọa đến vẻ mặt trắng bệch."),t("br"),t("br"),n._v("“Chẳng lẽ... Nói đúng?”"),t("br"),t("br"),n._v("Trong lòng “Lộp bộp!” Một lần."),t("br"),t("br"),n._v("Vừa rồi nhìn thấy những nội dung này thời điểm, cảm thấy khẳng định là bịa đặt đồn nhảm, không thể nào là thật, một khi dựa theo đọc lên, đối phương không giết bản thân là tốt lắm rồi, không nghĩ tới... Vậy mà bộ dáng này, không biết cái này gia hỏa liền giơ một lần bàn tay, Trương sư liền thật sự đem hắn trên người tồn tại vấn đề cùng thiếu hụt, đều nhìn ra chứ?"),t("br"),t("br"),n._v("“Đương nhiên, nếu như còn chưa tin... Có thể tiếp tục xung kích một lần Hợp Linh cảnh, ngươi sẽ cảm thấy, càng thêm không có lực lượng, thậm chí chân khí, đều không thể đi vào thức hải, liền sẽ bị một cỗ đặc thù lực lượng áp chế lại.”"),t("br"),t("br"),n._v("Ánh mắt sáng lên, Nhược Hoan công tử tăng thêm vô cùng lòng tin, mặc kệ đối phương biểu lộ, tiếp tục nói: “Thân là tứ tinh đỉnh phong danh sư, không có làm rõ võ kỹ bản chất, liền mù quáng không có tu luyện, còn không biết xấu hổ cùng ta so đối tu luyện... Ai cho ngươi lá gan?”"),t("br"),t("br"),n._v("Tần Lỗi thân thể nhoáng một cái."),t("br"),t("br"),n._v("Đối phương nói những bệnh trạng kia, hắn mới vừa thử, vậy mà tất cả đều có, chẳng lẽ... Thật tu luyện sai, ngộ nhập lạc lối?"),t("br"),t("br"),n._v("Thân là danh sư, bản thân luyện lung ta lung tung, như thế nào chỉ điểm người khác? Có thể nói, không cần tỷ thí, chẳng khác nào đã thất bại."),t("br"),t("br"),n._v("“Tốt, ta liền nói nhiều như vậy, bất quá... Mới vừa phạm sai lầm, còn có thể giải quyết, một khi thời gian dài, còn muốn khôi phục sẽ trễ...”"),t("br"),t("br"),n._v("Nhược Hoan công tử khoát tay chặn lại: “Như vậy đi, ta đại nhân có đại lượng, chỉ cần ngươi chắp tay nhận thua, liền truyền thụ cho ngươi phương pháp giải quyết.”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-600"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-602"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);