(window.webpackJsonp=window.webpackJsonp||[]).push([[1861],{2215:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-597-truyen-kiem"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-597-truyen-kiem"}},[n._v("#")]),n._v(" Chương 597: Truyền kiếm")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Vạn kiếm tề minh, long ngâm khiếu thiên là Kiếm Tâm cảnh biểu hiện, đi đến loại cảnh giới này, trường kiếm dựng dục ra linh tính, cùng người phù hợp, như khu cánh tay, để cho người ta sức chiến đấu gia tăng gấp đôi không thôi."),i("br"),i("br"),n._v("Lĩnh ngộ loại cảnh giới này, cơ bản có thể khai tông lập phái, trở thành một đời tông chủ."),i("br"),i("br"),n._v("Thậm chí, không ít thế lực quốc chủ, tông chủ, cũng đều không có đi đến, trong sương mù, rốt cuộc là ai, thi triển ra loại cảnh giới này, để ngoại giới rất nhiều trường kiếm, khó mà tự chế?"),i("br"),i("br"),n._v("Là ai, kiếm ý không ra, đã kiếm khí di tán, khí tức ngang dọc?"),i("br"),i("br"),n._v("“Khẳng định là Liêu Vũ Chí, hai chúng ta tông môn đều ở tại Vạn Quốc thành bên ngoài chân núi, mỗi sáng sớm đều nhìn thấy hắn đón thác nước luyện kiếm, kiếm reo sơn cốc, thanh động cửu châu, lúc ấy đã cảm thấy coi như không phải Kiếm Tâm cảnh, cũng chí ít đi đến kiếm ý đỉnh phong!”"),i("br"),i("br"),n._v("“Ngươi nói như vậy, ta nhớ ra rồi, ngày đó ta trùng hợp thấy được hắn luyện kiếm dấu vết lưu lại, kiếm ý rơi mấy trượng ở ngoài, mặt đất lưu lại dấu vết, chỉ sợ cũng chỉ có đi đến Kiếm Tâm cảnh, mới có thể để cho cái hố như thế quy luật, sâu cạn tương đồng!”"),i("br"),i("br"),n._v("“Liêu Vũ Chí, mệnh danh Hàn Khí tông vô thượng kiếm đạo thiên tài, tự nhiên không phải là dùng để trưng cho đẹp.”"),i("br"),i("br"),n._v("“Đúng vậy a, Kiếm Tâm cảnh một màn, cùng cấp bậc nghiền ép, Trương sư thực lực bản thân cũng không bằng đối phương, chỉ sợ lần này muốn vượt qua, khó khăn!”"),i("br"),i("br"),n._v("“Quan điểm của các ngươi ta không đồng ý, ta lại cảm thấy là Trương sư đạt đến Kiếm Tâm cảnh! Trước mấy quan tình huống, các ngươi cũng không phải không thấy được...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Cảm thấy trong sương mù, kiếm khí ngang dọc, để cho người ta khiếp sợ, dưới đài rất nhiều danh sư từng cái khuôn mặt ngưng trọng."),i("br"),i("br"),n._v("“Kiếm Tâm cảnh?”"),i("br"),i("br"),n._v("Không trung trở về Ngô Thiên Hào, thân thể run lên."),i("br"),i("br"),n._v("Lúc trước hắn muốn khiêu chiến Trương sư cũng không phải là quyền pháp, chưởng pháp, mà là kiếm pháp, vốn cho rằng nhờ vào hắn đối kiếm lĩnh ngộ, đánh bại đối phương dễ như trở bàn tay, ai ngờ Trương sư trực tiếp tiếp nhận khiêu chiến, rơi vào bị động."),i("br"),i("br"),n._v("Vốn cho rằng, lại có một cơ hội khiêu chiến, khẳng định dùng kiếm, lại không vẽ vời cho thêm chuyện ra, cảm nhận được phía trước kiếm khí ngang dọc, lập tức trở nên lại không tự tin."),i("br"),i("br"),n._v("Mặc kệ lĩnh ngộ kiếm tâm người là ai, có thể cùng đối kháng, kiếm thuật lại thế nào khả năng kém?"),i("br"),i("br"),n._v("Chí ít không phải hắn loại tu vi này có thể chống lại."),i("br"),i("br"),n._v("“Trương sư... Có thể thắng sao?”"),i("br"),i("br"),n._v("Khang đường chủ mấy người cũng tràn đầy lo lắng."),i("br"),i("br"),n._v("“Yên tâm đi, nhất định có thể thắng, chỉ là...” Triệu Phi Vũ đôi mi thanh tú nhăn thành cục."),i("br"),i("br"),n._v("“Chỉ là cái gì?”"),i("br"),i("br"),n._v("Biết vị này nhị công chúa, cùng Trương sư quan hệ không tệ, mấy người nhìn qua."),i("br"),i("br"),n._v("“Trương sư kiếm thuật, không cần hoài nghi, đã sớm đạt đến kiếm tâm chi cảnh!” Triệu Phi Vũ gật đầu."),i("br"),i("br"),n._v("Lúc trước tại Hiên Viên vương quốc tế thiên đài, Trương sư liền thi triển ra Hạ Kiếm Tâm cảnh giới kiếm khí, nàng cùng Kim Tòng Hải đám người đứng tại man thú trên lưng, nhìn rõ rõ ràng ràng."),i("br"),i("br"),n._v("Hiện tại nương theo thực lực gia tăng, đối cảnh giới lĩnh ngộ, khẳng định càng thêm cường đại, nếu như nói bên trong thi triển ra Kiếm Tâm cảnh người là ai, nàng có thể xác định, tất nhiên là Trương sư chắc chắn."),i("br"),i("br"),n._v("Coi như không phải, đối kiếm lĩnh ngộ, cũng tuyệt không so cái này kém."),i("br"),i("br"),n._v("“Chỉ là... Hắn không có binh khí thích hợp...” Triệu Phi Vũ nói tiếp."),i("br"),i("br"),n._v("Trương sư liên tiếp quật khởi quá nhanh, trên người linh thạch không ít, vũ khí nhưng hầu như không có mấy cái ra dáng."),i("br"),i("br"),n._v("Lúc trước tại Hồng Hải thành trận pháp sư công hội, điêu khắc trận bàn, dùng kiếm còn cũng là mượn tới."),i("br"),i("br"),n._v("  Cùng người so kiếm, đối kiếm lý giải tuy quan trọng, vũ khí cũng đồng dạng chiếm cứ rất lớn một bộ phận tác dụng, nếu như không có đầy đủ cấp bậc trường kiếm chống lại, lĩnh ngộ coi như mạnh hơn, chỉ sợ cũng phải rơi vào hạ phong."),i("br"),i("br"),n._v("“Cái kia... Làm sao bây giờ?”"),i("br"),i("br"),n._v("Khang đường chủ đám người sững sờ."),i("br"),i("br"),n._v("Vốn cho rằng có Dương sư lợi hại như vậy lão sư, vật tư khẳng định không thiếu, không nghĩ tới liền một chuôi Linh cấp trung phẩm trường kiếm đều không có, thật muốn như vậy... Còn thế nào so?"),i("br"),i("br"),n._v("Sớm biết như thế, liền tùy tiện hỗ trợ mượn một cái, cái này tỷ thí đã bắt đầu, coi như mượn cũng đã đã chậm."),i("br"),i("br"),n._v("“Chờ kết quả đi, ai cũng không có cách nào!”"),i("br"),i("br"),n._v("Triệu Phi Vũ lắc đầu."),i("br"),i("br"),n._v("Mấy người ngay tại lo lắng, liền nghe đến trong sương mù một cái lang lãng thanh âm vang lên: “Kính xin Hồng sư rút lui khai trận pháp!”"),i("br"),i("br"),n._v("“Chiến đấu kết thúc?”"),i("br"),i("br"),n._v("Mọi người lần nữa ngây người."),i("br"),i("br"),n._v("Không muốn bị người nhìn đến so thử kết quả, bây giờ lại muốn rút lui khai trận pháp, chẳng phải bày tỏ hai người chiến đấu đã ra khỏi kết quả?"),i("br"),i("br"),n._v("Lúc này mới vài phút? Hai phút đồng hồ không đến... Chẳng lẽ liền có người thắng, có người bại?"),i("br"),i("br"),n._v("Tốc độ cũng quá nhanh đi!"),i("br"),i("br"),n._v("Hô!"),i("br"),i("br"),n._v("Ánh mắt nghi hoặc bên trong, trước mắt mê vụ chậm rãi tản ra, ngay sau đó lộ ra hai bóng người."),i("br"),i("br"),n._v("Trương sư cùng Liêu Vũ Chí, đều đứng tại vừa rồi vị trí, dường như chưa hề động đậy, trên người cũng không có bất luận cái gì thương thế cùng vết kiếm, vốn cho rằng rất nguy hiểm kiếm thuật giao đấu, chỉ từ tình cảnh bên trên nhìn, thật giống như còn chưa bắt đầu giống nhau."),i("br"),i("br"),n._v("“Cái này... Rốt cuộc người nào thắng?”"),i("br"),i("br"),n._v("“Ta cũng không biết, bất quá... Trương sư kiếm đâu?”"),i("br"),i("br"),n._v("“Đúng a, không phải so kiếm sao? Trương sư làm sao không có cầm kiếm?”"),i("br"),i("br"),n._v("“Không phải là Trương sư quăng kiếm nhận thua chứ?”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Tỷ thí hai bên mặc dù đều không có động địa phương, nhưng Liêu Vũ Chí cầm trong tay trường kiếm, hàn khí bức người, mà Trương sư, tay không tấc sắt, cũng không biết là đem kiếm thu vào trữ vật giới chỉ, vẫn là căn bản là không có lấy ra."),i("br"),i("br"),n._v("Bất quá, liền hai người bộ dạng, căn bản nhìn không ra thắng thua."),i("br"),i("br"),n._v("“Ta thua!”"),i("br"),i("br"),n._v("Ngay tại suy đoán, liền nghe Liêu Vũ Chí thanh âm vang lên, ngay sau đó vị thiên tài này lắc đầu, trường kiếm trong tay thu lại, bỏ vào trữ vật giới chỉ."),i("br"),i("br"),n._v("“Liêu sư thua?”"),i("br"),i("br"),n._v("“Rốt cuộc chuyện gì xảy ra?”"),i("br"),i("br"),n._v("Một mảnh xôn xao."),i("br"),i("br"),n._v("Trương sư rốt cuộc làm cái gì, hai phút đồng hồ không đến, liền để kiếm thuật thông huyền Liêu Vũ Chí, cam tâm tình nguyện nhận thua?"),i("br"),i("br"),n._v("“Trương sư kiếm thuật thông huyền, ta mặc cảm không bằng!”"),i("br"),i("br"),n._v("Không để ý tới mọi người dưới đài kinh ngạc, Liêu Vũ Chí lắc đầu, nhìn về phía thanh niên trước mắt, ôm quyền nói: “Ta đi đến Hạ Kiếm Tâm sơ kỳ, cảm thấy còn muốn tiến bộ, mười phần khó khăn, không biết Trương sư có thể hay không chỉ điểm ta một hai, nói cho ta biết tu luyện phương hướng!”"),i("br"),i("br"),n._v("Toàn bộ Hàn Khí tông, loại trừ hắn đi đến Kiếm Tâm cảnh, không còn gì khác người, coi như muốn tấn cấp, cũng mất phương hướng."),i("br"),i("br"),n._v("Trước mắt vị thanh niên này, mặc dù tuổi tác so với hắn còn nhỏ, nhưng đối kiếm lý giải, đã vượt qua hắn nhiều lắm, nếu như có thể thừa cơ thỉnh giáo, đối tu luyện về sau, khẳng định có trợ giúp thật lớn."),i("br"),n._v("“Chỉ điểm?”"),i("br"),i("br"),n._v("Không nghĩ tới vị này Liêu Vũ Chí thua thản nhiên như vậy, trực tiếp thỉnh giáo, Trương Huyền thần sắc lạnh nhạt nhẹ gật đầu, bàn tay duỗi ra: “Kiếm đến!”"),i("br"),i("br"),n._v("“Vâng!”"),i("br"),i("br"),n._v("Ánh mắt sáng lên, Liêu Vũ Chí vội vàng đem trường kiếm lấy ra, đưa tới trước mặt."),i("br"),i("br"),n._v("Ngón tay búng một cái, trường kiếm thoát xác, âm trầm kiếm khí, xao động mà ra, như là ánh sáng, chói lóa mắt."),i("br"),i("br"),n._v("“Trương sư đây là... Muốn chỉ điểm Liêu Vũ Chí?”"),i("br"),i("br"),n._v("“Xem bộ dáng là, chỉ là... Chỉ điểm mà nói, vì cái gì không cần của mình kiếm? Người khác kiếm, đều là nhận chủ, một khi linh tính phản kháng, ngược lại thi triển không xuất kiếm ý cùng hiệu quả!”"),i("br"),i("br"),n._v("“Đúng vậy a, thật kỳ quái...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Nhìn thấy Trương Huyền muốn kiếm, mọi người tất cả đều nhíu mày thành cục."),i("br"),i("br"),n._v("Trường kiếm đi đến Linh cấp, liền có thuộc về mình linh tính, một khi nhận chủ, chỉ cần chủ nhân không chết, người ngoài rất khó khống chế, Trương sư đã muốn truyền kiếm, vì cái gì không cần binh khí của mình, mà mượn nhờ người khác?"),i("br"),i("br"),n._v("Ngay tại kỳ lạ, muốn nhìn một chút vị này Trương sư rốt cuộc làm thế nào, liền nghe đến trường kiếm trong tay của hắn, một tiếng chói tai vang lên."),i("br"),i("br"),n._v("“Linh tính xao động, chủ động nhận chủ?”"),i("br"),i("br"),n._v("“Sờ một cái Liêu sư trường kiếm, liền để hắn nhận chủ?”"),i("br"),i("br"),n._v("Tất cả mọi người giật nảy mình."),i("br"),i("br"),n._v("Muốn cho Linh cấp vũ khí nhận chủ, mười phần phiền phức, có ít người mỗi ngày lau, nhiều năm đều chưa từng thành công, Trương sư ngược lại tốt, Liêu sư còn tại tại chỗ đứng đấy, bàn tay hắn đụng một cái, đối phương trường kiếm liền xao động không dứt, cùng trước đó Thanh Ưng Thú giống nhau, dự định nhận chủ..."),i("br"),i("br"),n._v("Đây cũng quá bất khả tư nghị chứ?"),i("br"),i("br"),n._v("Liêu Vũ Chí cũng không nghĩ tới, sợ hãi đến trừng to mắt."),i("br"),i("br"),n._v("Hắn lúc trước vì để cho chuôi kiếm này linh tính nhận chủ, hao tốn trọn vẹn nửa năm lâu, thậm chí còn dụng tâm đầu máu tẩm bổ ròng rã một tháng, mới khiến cho hắn hoàn toàn thần phục, kiếm ý tăng nhiều."),i("br"),i("br"),n._v("Đối phương liền tìm ra một lần, liền quỳ cầu cái này nhận chủ... Chênh lệch này thật là quá lớn đi."),i("br"),i("br"),n._v("“Nhìn kỹ, ta chỉ thi triển một lần!”"),i("br"),i("br"),n._v("Ngay tại khiếp sợ, liền nghe đến Trương sư thanh âm nhàn nhạt vang lên."),i("br"),i("br"),n._v("Vội vàng ổn định tâm thần, hai mắt tập trung nhìn qua."),i("br"),i("br"),n._v("Dưới đài cũng lập tức lặng ngắt như tờ, tất cả mọi người không dám chớp mắt, sợ nhìn lọt cái gì."),i("br"),i("br"),n._v("Có thể tướng lĩnh ngộ kiếm tâm Liêu sư đánh bại, bọn họ cũng muốn nhìn xem, vị này Trương sư rốt cuộc đối kiếm lĩnh ngộ được loại cảnh giới nào."),i("br"),i("br"),n._v("Hô!"),i("br"),i("br"),n._v("Ánh mắt mọi người tập trung bên dưới, Trương Huyền trường kiếm trong tay động."),i("br"),i("br"),n._v("Trước đó hàn khí bức người trường kiếm, trong tay hắn, hình như không có ánh sáng, cũng mất uy lực, giống một cái rơm rạ, không nặng chút nào, lại hình như là cánh tay dọc theo, mảy may nhìn không ra không hài hòa."),i("br"),i("br"),n._v("Soạt!"),i("br"),i("br"),n._v("Trường kiếm đối Liêu Vũ Chí bổ tới, đi tới trước mặt, lập tức thu hồi, bởi vì tốc độ quá nhanh, thậm chí có không ít người đều không có thấy rõ."),i("br"),i("br"),n._v("Leng keng!"),i("br"),i("br"),n._v("Trương Huyền đem trường kiếm cắm vào trở về vỏ kiếm, nhấc chân hướng dưới đài đi đến."),i("br"),i("br"),n._v("“Thật nhanh kiếm pháp!”"),i("br"),i("br"),n._v("“Đây là Trương sư đối kiếm lĩnh ngộ?”"),i("br"),i("br"),n._v("Mọi người hai mặt nhìn nhau, không rõ ràng cho lắm."),i("br"),i("br"),n._v("Ngay cả Liêu Vũ Chí cũng không hiểu ra sao."),i("br"),i("br"),n._v("Trương sư vừa rồi một kiếm đánh xuống, tốc độ mặc dù rất nhanh, nhưng... Nhờ vào thực lực của hắn, vẫn là rất nhẹ nhàng có thể làm được, thậm chí, còn có thể còn nhanh hơn này... Chỉ là như vậy, cũng không đáng được bản thân học tập ah!"),i("br"),i("br"),n._v("Hô!"),i("br"),i("br"),n._v("Ngay tại nghi hoặc, quay người kéo theo gió thổi tới, một người có mái tóc từ trên trán trôi xuống."),i("br"),i("br"),n._v("“Vừa rồi... Chặt đứt ta một sợi tóc?”"),i("br"),i("br"),n._v("Nhìn thấy cái này chậm rãi rơi xuống tóc dài, Liêu Vũ Chí con ngươi co rụt lại."),i("br"),i("br"),n._v("Đầu hắn phát nồng hậu dày đặc, một kiếm phách lên đi chỉ chặt đứt một cái, không thương tổn cái khác, đối lực lượng khống chế, quả thực nghe rợn cả người, chẳng lẽ..."),i("br"),i("br"),n._v("Không hổ là Trương sư, quả nhiên đáng sợ!"),i("br"),i("br"),n._v("“Một kiếm cắt tóc, mà Liêu sư không tự biết?”"),i("br"),i("br"),n._v("“Đây là cái gì kiếm thuật? Đối kiếm khống chế đến cảnh giới gì, mới có thể làm đến?”"),i("br"),i("br"),n._v("“Không biết... Bất quá, chỉ sợ đã đạt đến Hạ Kiếm Tâm đỉnh phong nhất, thậm chí... Trung Kiếm Tâm cảnh giới đi!”"),i("br"),i("br"),n._v("“Trung Kiếm Tâm?”"),i("br"),i("br"),n._v("Dưới đài tất cả mọi người yên lặng."),i("br"),i("br"),n._v("Một kiếm chém ra, gãy một sợi tóc, phần này lực khống chế, không phải tận mắt nhìn thấy, không thể tin được."),i("br"),i("br"),n._v("“Đa tạ Trương sư chỉ điểm...”"),i("br"),i("br"),n._v("Liêu Vũ Chí ôm quyền, thái độ cung kính."),i("br"),i("br"),n._v("“Ngươi xem hiểu?”"),i("br"),i("br"),n._v("Trương Huyền dừng bước."),i("br"),i("br"),n._v("“Xem hiểu, Trương sư một kiếm cắt tóc, lực khống chế kinh người, ý là để cho ta, nhiều tu luyện lực khống chế...”"),i("br"),i("br"),n._v("Liêu Vũ Chí vội vàng đem lĩnh ngộ của mình nói ra."),i("br"),i("br"),n._v("Lời còn chưa dứt, chỉ thấy Trương sư lắc đầu, bóng lưng dưới ánh trăng, có vẻ dị thường thất lạc: “Xem ra... Ngươi nhìn không hiểu!”"),i("br"),i("br"),n._v("Nói xong nhẹ nhàng vung lên ống tay áo."),i("br"),i("br"),n._v("Hô!"),i("br"),i("br"),n._v("Một ngọn gió thổi tới, vừa rồi rơi xuống tóc, bị nhẹ nhàng cuốn lên, trong nháy mắt chia năm xẻ bảy, cắt thành mấy chục đoạn nhỏ, bốn phía bay lượn."),i("br"),i("br"),n._v("“Cái này... Không phải một kiếm?”"),i("br"),i("br"),n._v("Con ngươi co rụt lại, Liêu Vũ Chí vẻ mặt trắng bệch."),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-605"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-607"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);