(window.webpackJsonp=window.webpackJsonp||[]).push([[1862],{2217:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-598-ty-thi-day-hoc-trong-nguoi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-598-ty-thi-day-hoc-trong-nguoi"}},[n._v("#")]),n._v(" Chương 598: Tỷ thí dạy học trồng người")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Ống tay áo vung ra gió, bình ổn thản nhiên, không có khả năng thổi gãy lông tóc, sở dĩ như vậy, chỉ có một khả năng, cái kia chính là... Trương sư vừa rồi chặt đứt hắn tóc, căn bản không phải một kiếm, mà là... Mấy chục kiếm!"),t("br"),t("br"),n._v("Chỉ bất quá, những kiếm chiêu này, chưa đem tóc trực tiếp chặt đứt, thoạt nhìn vẫn là nguyên một rễ, gió nhẹ phất một cái rốt cuộc không chịu nổi, cắt thành mấy chục đoạn."),t("br"),t("br"),n._v("Cúi đầu nhìn về phía phất phới tóc rối, mỗi một đoạn dài ngắn hoàn toàn tương đồng, đứt gãy chỗ, bóng loáng mượt mà, không có một chút ẩu tả."),t("br"),t("br"),n._v("Một kiếm chặt đứt tóc, rất nhiều kiếm thuật cường giả cũng có thể làm đến, có thể liên tục chém mấy chục kiếm, đều không ngừng, mỗi một chiêu đều vừa đúng... Đây là dạng gì lực khống chế?"),t("br"),t("br"),n._v("Toàn thân run rẩy, Liêu Vũ Chí đã nói không ra lời."),t("br"),t("br"),n._v("Vốn cho rằng đối phương một kiếm cắt tóc, đã nhìn rõ, bây giờ mới biết... Căn bản là không có xem hiểu!"),t("br"),t("br"),n._v("Cái này đã không riêng gì lực khống chế vấn đề, mà là kiếm như cùng hắn ngón tay giống nhau, đã có cảm xúc, biết tóc có bao nhiêu to, chém tới loại trình độ nào, có thể được gió thổi gãy..."),t("br"),t("br"),n._v("Thần sắc giật mình, vội vàng ngẩng đầu nhìn lại, chỉ thấy Trương sư đã đi xuống tỷ thí đài, về tới vị trí của mình."),t("br"),t("br"),n._v("“Ta sẽ cố gắng cố gắng, không phụ lòng ngươi truyền kiếm chi ân!”"),t("br"),t("br"),n._v("Đem vừa rồi Trương Huyền vung kiếm động tác cùng thần thái, tại trong óc nhớ lại một lần, biết được lợi rất nhiều, nắm đấm xiết chặt, Liêu Vũ Chí cũng đi xuống, mới vừa trở lại Hàn Khí tông, liền bị trong môn phái tất cả trưởng lão vây quanh."),t("br"),t("br"),n._v("“Vừa rồi rốt cuộc tại sao thua? Ta không nhìn hắn cầm kiếm ah, hắn rốt cuộc dùng cái gì đưa ngươi đánh bại?”"),t("br"),t("br"),n._v("Mọi người tràn đầy kỳ lạ."),t("br"),t("br"),n._v("Trương sư liền kiếm đều không có lấy ra, vì cái gì Liêu Vũ Chí liền chủ động nhận thua?"),t("br"),t("br"),n._v("“Dùng cái gì đem ta đánh bại?”"),t("br"),t("br"),n._v("Nhớ tới vừa rồi cảnh tượng, Liêu Vũ Chí cười khổ một tiếng, con ngươi kìm lòng không được co lại, thân thể khẽ run: “Một ngón tay...”"),t("br"),t("br"),n._v("“Một ngón tay?”"),t("br"),t("br"),n._v("Tất cả mọi người đều ngây người."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Tám tiến bốn, vận khí tác dụng liền không lớn, Tống Siêu, Nhược Hoan công tử, đều dừng bước ở đây, có thể tấn cấp loại trừ Trương Huyền tu vi hơi yếu chút, cái khác ba cái đều đạt đến tứ tinh đỉnh phong, Hóa Phàm tứ trọng đỉnh phong."),t("br"),t("br"),n._v("Hầu như đều là trước đó, tranh đoạt quán quân tiếng hô lớn nhất."),t("br"),t("br"),n._v("Nhất là La Tuyền cùng Nhược Hoan công tử đối chiến, toàn bộ hành trình đều tại nghiền ép, căn bản không phải cùng một cái cấp bậc."),t("br"),t("br"),n._v("Bất quá, mặc dù chưa đi đến vào trước bốn, Nhược Hoan công tử cũng đủ hài lòng, nếu như không phải Trương sư, hắn khả năng tuyển bạt thi đấu liền bị đào thải, làm sao có thể đi vào trước tám, có cơ hội đi vào Hồng Viễn danh sư học viện học tập?"),t("br"),t("br"),n._v("“Trận tiếp theo, Lưu Vân tông La Tuyền đối chiến Vọng Giang tông Tất Giang Hải, Lạc Sa tông Hoàng Tranh đối chiến vạn quốc liên minh Trương Huyền!”"),t("br"),t("br"),n._v("Hồng sư đem phía sau số trận chiến đấu nói ra."),t("br"),t("br"),n._v("“Lạc Sa tông Hoàng Tranh? Tiềm lực bảng xếp hạng thứ tư gia hỏa?”"),t("br"),t("br"),n._v("Trương Huyền nhìn về phía trên đài một thanh niên."),t("br"),t("br"),n._v("Cái tên này, cũng tại tiềm lực trong bảng, xếp hạng thứ tư, vô luận tu vi hay là thực lực, so với Liêu Vũ Chí, không kém chút nào."),t("br"),t("br"),n._v("“Ừm, Trương sư cẩn thận, vị này Hoàng Tranh tranh tài ta đều nhìn, mỗi một trận đều không giống nhau, lại đều có thể nghiền ép thức chiến thắng, thật sự là thực lực, thật không đơn giản! Ta cũng hoài nghi, trước đó thứ tự không có Liêu Vũ Chí đám người cao, có phải hay không lặng lẽ che giấu thực lực!”"),t("br"),t("br"),n._v("Khang đường Chủ Thần sắc mặt ngưng trọng mà nói."),t("br"),t("br"),n._v("Trước đó vị này Hoàng Tranh tranh tài, hắn toàn bộ hành trình đều nhìn, mấy lần tỷ thí, không có một hồi tương đồng, lại đều có thể lấy vô địch dáng dấp chiến thắng, chân thực thủ đoạn, chỉ sợ so ngoại giới truyền ra không kém chút nào."),t("br"),t("br"),n._v("Thậm chí cùng La Tuyền so, cũng sẽ không rơi xuống hạ phong."),t("br"),t("br"),n._v("“Quản hắn là ai, gặp được Trương sư, nhức đầu cũng nên là hắn!” Triệu Phi Vũ cười nói."),t("br"),t("br"),n._v("“Này ngược lại là...” Khang đường chủ xấu hổ cười một tiếng."),t("br"),t("br"),n._v("Muốn nói trước đó, còn lo lắng Trương sư tu vi là thiếu hụt, khả năng khó mà đi đến cuối cùng, đã trải qua Ngô Thiên Hào cùng Liêu Vũ Chí tỷ thí, hoàn toàn mất hết mà loại ý nghĩ này."),t("br"),t("br"),n._v("Trước mắt vị này Trương Huyền, liền cùng vô địch Chiến Thần giống nhau, ai gặp được, ai liền xui xẻo, bị đả kích lòng tự tin đều không có."),t("br"),t("br"),n._v("Vị này Hoàng Tranh mặc dù trước mấy quan thoạt nhìn rất lợi hại, đó cũng là gặp được người khác, gặp được Trương sư... Chỉ sợ kết quả không thể so với những người khác tốt nhiều lắm."),t("br"),t("br"),n._v("Thấy mọi người đối với hắn tự tin như vậy, Trương Huyền cười khổ một tiếng, nhấc chân đi đến lôi đài."),t("br"),t("br"),n._v("Lúc này vị này Hoàng Tranh đã đi tới đối diện, thấy hắn đi lên, liền ôm quyền: “Trương sư!”"),t("br"),t("br"),n._v("“Hoàng sư!” Trương Huyền cũng cung kính đáp lễ."),t("br"),t("br"),n._v("“Ta muốn cùng Trương sư, tỷ thí dạy học trồng người!” Hoàng Tranh thản nhiên nói."),t("br"),t("br"),n._v("“Dạy học trồng người?”"),t("br"),t("br"),n._v("“Ừm, quy tắc rất đơn giản, từ trên quảng trường tùy ý tuyển chọn một trăm người, đồng thời giảng bài, không cho phép sử dụng sư ngôn thiên bẩm, giảng bài kết thúc, bằng lòng lạy ai là thầy nhiều người, ai liền chiến thắng!” Hoàng Tranh nói ra quy tắc."),t("br"),t("br"),n._v("Đồng dạng trong tỉ thí cho, quy tắc cũng nhiều loại đa dạng, đương nhiên, nếu như ngươi cảm thấy không công bằng, cũng có thể cự tuyệt."),t("br"),t("br"),n._v("“Có thể!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Tỷ thí dạy học trồng người? Còn không cho phép sử dụng sư ngôn thiên bẩm? Cái này có chút quá mức đi!”"),t("br"),t("br"),n._v("“Đúng vậy a, sư ngôn thiên bẩm là danh sư tiêu chí, danh sư nhất làm cho người tin phục thủ đoạn, không cho sử dụng, làm sao so?”"),t("br"),t("br"),n._v("“Hoàng sư đánh cho thủ đoạn tính toán thật hay, căn cứ trước đó Trương sư trải qua, so cái gì đều rất khó chiến thắng, dạy học trồng người không giống, có đôi khi lão sư khá hơn nữa, học sinh không thích, cũng rất khó học được tri thức, bởi vậy... So cái này, có rất lớn ngẫu nhiên tính, lại thêm không cho phép sử dụng sư ngôn thiên bẩm, kết quả... Rốt cuộc ai chiến thắng, liền khó nói!”"),t("br"),t("br"),n._v("“Cũng là ah...”"),t("br"),t("br"),n._v("Nghe được trên đài tỷ thí nội dung, dưới đài nghị luận ầm ĩ."),t("br"),t("br"),n._v("Dạy học trồng người, cũng không phải là lão sư danh khí lớn, liền có thể để cho người ta càng tin phục đi học, còn có rất nhiều những nhân tố khác ảnh hưởng, thật giống như hoàn mỹ đến đâu, lại ưu tú người, cũng không thể để người người đều ưa thích giống nhau."),t("br"),t("br"),n._v("Đây là dung hợp nhân loại chuyện tình cảm, không thể dùng đúng chuẩn cân nhắc."),t("br"),t("br"),n._v("Cái khác tỷ thí, Trương sư đều có thể nghiền ép, mà cái này, liền không nói được rồi, thắng bại, ai cũng không nói chắc được."),t("br"),t("br"),n._v("Không hổ là thi đấu nổi danh thiên tài, một lần đã tìm được nơi mấu chốt, đưa ra thích hợp cho hắn nhất tỷ thí."),t("br"),n._v("Nếu không, so bất kỳ vật gì, hắn đều chỉ sợ không phải vị này Trương sư đối thủ."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Tỷ thí quy tắc xác định, hai bên đều không có dị nghị, dự định lên cũng nhanh."),t("br"),t("br"),n._v("Thời gian không dài, trên lôi đài an vị tốt một trăm người, có chút là vạn quốc liên minh hộ vệ, cũng có chút là đến đây quan sát danh sư thi đấu người bình thường."),t("br"),t("br"),n._v("Có thể được tuyển chọn, lắng nghe hai vị danh sư giảng bài, mọi người tất cả đều hưng phấn mà con mắt sáng lên."),t("br"),t("br"),n._v("Loại này cơ hội tốt, cũng không phải là ai cũng có thể may mắn đích thân tới."),t("br"),t("br"),n._v("“Những người này tu vi đều không tương đồng, ta nhìn chúng ta liền giảng giải một ít tu luyện kiến thức căn bản đi, nếu không, ta sợ tu vi thấp nghe không hiểu, cao không thích hợp!”"),t("br"),t("br"),n._v("Hoàng Tranh nói."),t("br"),t("br"),n._v("Tuyển ra một trăm người, tu vi cao thấp không đều, từ võ giả nhất trọng đến Hóa Phàm tam trọng, tất cả đều có, giảng giải Hóa Phàm cảnh công pháp, võ giả không hiểu, giảng giải cấp thấp, tu vi cao lại cảm thấy không thú vị."),t("br"),t("br"),n._v("Chỉ có thể giảng giải một ít tu luyện cơ sở kiến thức bình thường, làm cho tất cả mọi người đều gây nên cộng minh."),t("br"),t("br"),n._v("“Tốt!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Quy tắc mà thôi, đối phương muốn làm sao định liền làm sao định, giảng giải cái khác khả năng còn không được, cơ sở... Đoán chừng Khổng sư đến rồi, đều chưa hẳn so với hắn nói càng tốt hơn."),t("br"),t("br"),n._v("Thiên Đạo thư viện chỉnh lý ra cơ sở, không có sai lầm, đây là bất luận cái gì danh sư, đều khó có khả năng làm được."),t("br"),t("br"),n._v("“Tốt, ngươi ta cùng một chỗ dùng truyền âm giảng bài, bọn họ bằng lòng nghe ai, liền nghe ai, không liên quan tới nhau, thời gian là một nén nhang. Sau một nén nhang, trong một trăm người bằng lòng lạy ai nhiều lắm, ai liền chiến thắng!”"),t("br"),t("br"),n._v("Hoàng Tranh đem so với thử nội dung nói xong, vẫy bàn tay lớn một cái: “Bắt đầu đi!”"),t("br"),t("br"),n._v("“Ừm, ngươi tới trước!”"),t("br"),t("br"),n._v("Trương Huyền cũng không vội vã, mỉm cười."),t("br"),t("br"),n._v("“Vậy ta liền không khách khí!”"),t("br"),t("br"),n._v("Hoàng Tranh mắt sáng lên, lập tức mở miệng."),t("br"),t("br"),n._v("Dùng truyền âm, loại trừ trên lôi đài mọi người có thể nghe được ở ngoài, phía ngoài chỉ có thể nhìn thấy miệng khép mở, cũng không biết nói tới nội dung."),t("br"),t("br"),n._v("Nghe một hồi, cảm thấy đối phương giảng giải cũng không tệ, Trương Huyền nhẹ gật đầu, lúc này mới tiến về phía trước một bước, cũng bắt đầu giảng giải."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Tỷ thí bắt đầu!”"),t("br"),t("br"),n._v("“Hai người đồng thời giảng bài, nên nghe ai, không nghe ai như thế nào nhận biết?”"),t("br"),t("br"),n._v("“Rất đơn giản, ai nội dung hấp dẫn ngươi, càng có thể để ngươi về mặt tu luyện có chút tinh ích, liền nghe người đó!”"),t("br"),t("br"),n._v("“Cũng đúng, đồng dạng người tu luyện mặc dù làm không được phân tâm nhị dụng, che đậy một người nói chuyện, còn có thể làm được!”"),t("br"),t("br"),n._v("Nhìn đến so thử bắt đầu, dưới đài thảo luận."),t("br"),t("br"),n._v("“Các ngươi nói, bọn họ ai có thể chiến thắng?”"),t("br"),t("br"),n._v("“Cái này khó mà nói, có điều, ta vẫn là cảm thấy Trương sư có thể thắng!”"),t("br"),t("br"),n._v("“Nếu như có thể sử dụng sư ngôn thiên bẩm, Trương sư khẳng định không có vấn đề, hai bên đều không sử dụng, vậy liền xem ai đối tu luyện cơ sở nắm giữ kiên cố...”"),t("br"),t("br"),n._v("“Hoàng Tranh xuất thân tông môn, các loại bí tịch đông đảo, chỉ sợ cơ sở muốn so Trương sư kiên cố đi!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Có tỷ thí, liền có người quan tâm thắng bại, nghe không được cụ thể nói thứ gì, mọi người có chút vò đầu bứt tai, từng cái muốn từ trên đài biến hóa, nhìn ra thứ gì."),t("br"),t("br"),n._v("Nhưng rất đáng tiếc, cái gì cũng nhìn không ra."),t("br"),t("br"),n._v("Ngồi nghe giảng bài mọi người, giống như là bị thôi miên, từng cái như si như say nghe giảng bài, cũng không biết là nghe Trương sư nội dung biến thành như vậy, vẫn là nghe Hoàng sư khóa như thế."),t("br"),t("br"),n._v("“La Tuyền cùng Tất Giang Hải tỷ thí, đã kết thúc...”"),t("br"),t("br"),n._v("Ngay tại mọi người tràn đầy nghi hoặc, muốn biết hai người tới đáy nói cái gì thời điểm, trong đám người không biết ai hô lên, vội vàng nhìn lại, quả nhiên thấy cái thứ nhất trên lôi đài tỷ thí đã kết thúc."),t("br"),t("br"),n._v("Tất Giang Hải lông mi giơ lên, khí tức như rồng, cả người mang theo không thể xâm phạm khí tức."),t("br"),t("br"),n._v("Mà quán quân tiếng hô lớn nhất La Tuyền, an tĩnh đứng tại đối diện, không ngừng lắc đầu, tựa hồ có chút thất lạc."),t("br"),t("br"),n._v("“Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("“Người nào thắng?”"),t("br"),t("br"),n._v("Thấy cảnh này, mọi người tất cả đều sững sờ."),t("br"),t("br"),n._v("Tất Giang Hải, danh khí cũng không lớn, thậm chí tiềm lực bảng mười vị trí đầu đều không có xếp tới, mọi người vốn cho rằng cùng tiềm lực đệ nhất La Tuyền so, khẳng định tại chỗ thất bại, nhìn thế nào lên không giống có chuyện như vậy?"),t("br"),t("br"),n._v("“La sư thua!”"),t("br"),t("br"),n._v("Vừa rồi quan sát bọn họ tỷ thí một cái danh sư, nhịn không được nói, dường như nói ra lời này, ngay cả hắn, đều có chút không tin."),t("br"),t("br"),n._v("“La sư mệnh danh tiềm lực thứ nhất, có khả năng nhất đoạt giải quán quân nhân vật, làm sao sẽ thua?” Không ít người không thể tin được."),t("br"),t("br"),n._v("“Bọn họ vừa rồi tỷ thí chính là tu vi, hai người thực lực tương đương, thậm chí... La sư càng hơn một bậc, dựa theo đạo lý, hắn khẳng định có thể chiến thắng, ai ngờ... Tất Giang Hải tất sư cũng không biết là vận khí tốt, vẫn là tích lũy đến... Thế mà tại tỷ thí quá trình bên trong nhất cử đột phá, đạt đến Hợp Linh cảnh!”"),t("br"),t("br"),n._v("Vị danh sư này cười khổ nói."),t("br"),t("br"),n._v("“Nhất cử đột phá?”"),t("br"),t("br"),n._v("“Hợp Linh cảnh?”"),t("br"),t("br"),n._v("Tất cả mọi người đồng thời ngẩn ngơ."),t("br"),t("br"),n._v("(Khoảng cách tên thứ ba cũng không xa, mọi người còn có nguyệt phiếu ư?)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-606"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-608"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);