(window.webpackJsonp=window.webpackJsonp||[]).push([[1863],{2218:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-599-cho-ta-truoc-đot-pha-mot-cai"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-599-cho-ta-truoc-đot-pha-mot-cai"}},[n._v("#")]),n._v(" Chương 599: Chờ, ta trước đột phá một cái!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Hợp Linh cảnh, là Hóa Phàm cửu trọng cái thứ nhất cửa ải khó, rất nhiều người vây ở Trọc Thanh cảnh đỉnh phong, không cách nào lại tiến một bước."),t("br"),t("br"),n._v("Cửa này coi trọng linh hồn cùng thân thể phù hợp, hồn lực càng thêm bền bỉ, dốc lòng tu luyện đều rất khó đột phá, cùng người thời điểm chiến đấu, xung kích thành công... Vị này Tất sư, vận khí quá tốt đi mất!"),t("br"),t("br"),n._v("Đây chính là rất nhiều người tu luyện tha thiết ước mơ, đều muốn có trải qua."),t("br"),t("br"),n._v("“Đúng vậy a, tỷ thí thời điểm đột phá, La sư mặc dù thua, nhưng cũng không oan!”"),t("br"),t("br"),n._v("“Lâm trận đột phá, không riêng gì vận khí tốt, còn có trước kia tích lũy cùng can đảm, xem ra vị này Tất sư mới là lần thi đấu này lớn nhất hắc mã!”"),t("br"),t("br"),n._v("“Trước đó không có gì danh khí, thời khắc mấu chốt một tiếng hót lên làm kinh người, lợi hại!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe rõ chuyện gì xảy ra, mọi người từng cái bội phục không thôi."),t("br"),t("br"),n._v("Giao đấu vốn là cửu tử nhất sinh, nguy hiểm vô cùng, ở thời điểm này đột phá, cần phải có bao lớn trái tim mới có thể tiếp nhận?"),t("br"),t("br"),n._v("Không nói trước vị này Tất sư, trước kia rốt cục mạnh đến mức nào, liền nói phần này tâm trí, cũng không phải là bình thường người có thể so sánh được."),t("br"),t("br"),n._v("La Tuyền thua, cũng không lỗ."),t("br"),t("br"),n._v("“Cuộc tỷ thí này kết quả đi ra, liền nhìn Trương sư cùng Hoàng sư ai có thể chiến thắng...”"),t("br"),t("br"),n._v("Bốn nhà hai, một hồi kết thúc, mọi người lần nữa đem ánh mắt tập trung ở một cái khác sân bãi bên trên, muốn nhìn một chút lần này danh sư thi đấu, rốt cuộc là cái nào hai vị siêu cấp thiên tài, chân chính trổ hết tài năng, tranh đoạt quan á quân."),t("br"),t("br"),n._v("“Mau nhìn, giảng bài ngừng!”"),t("br"),t("br"),n._v("“Ừm, đã đến giờ, cũng nên kết thúc!”"),t("br"),t("br"),n._v("Tiếng nghị luận bên trong, trên lôi đài giảng bài hai người ngừng lại, ngồi xếp bằng hơn một trăm người, từng cái từ từ mở mắt, hai mắt kim quang lóng lánh."),t("br"),t("br"),n._v("Xem ra, tuy chỉ nghe thời gian một nén nhang giảng bài, nhưng tất cả đều được lợi cực lớn, giải quyết không ít trên việc tu luyện nan đề."),t("br"),t("br"),n._v("“Bọn họ ai có thể chiến thắng?”"),t("br"),t("br"),n._v("“Tạm thời còn nhìn không ra, liền nhìn cái này một trăm người, như thế nào bái sư đi!”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Thấy tỷ thí kết thúc, biết kết quả lập tức liền sẽ ra ngoài, tất cả mọi người mặt khẩn trương nhìn sang, muốn nhìn một chút, Trương sư cùng vị này Hoàng sư tranh đoạt, rốt cuộc ai có thể chiến thắng."),t("br"),t("br"),n._v("Giảng bài kết thúc, Trương Huyền cũng nhẹ nhàng thở ra."),t("br"),t("br"),n._v("Mặc dù hắn đối với mình chương trình học rất có tự tin, nhưng đối với những người này lựa chọn như thế nào, vẫn là không dám trăm phần trăm xác định."),t("br"),t("br"),n._v("Dù sao, không thể thi triển sư ngôn thiên bẩm, hắn giảng giải nội dung, có độ tin cậy liền sẽ hạ thấp không ít, thậm chí quá mức thâm ảo, không ít người khó có thể lý giải được."),t("br"),t("br"),n._v("“Thấy kết quả đi!”"),t("br"),t("br"),n._v("Lắc đầu, không nghĩ nhiều nữa, ngẩng đầu nhìn về phía trước mắt mọi người: “Chư vị...”"),t("br"),t("br"),n._v("Đang muốn mở miệng, hỏi thăm có bao nhiêu người hướng hắn bái sư, chỉ thấy bóng người trước mắt hoa một cái, một thân ảnh quỳ gối trước mặt: “Kính xin Trương sư thu ta làm đồ đệ, ta nhất định sẽ nghiêm túc học tập, không phụ lòng lão sư dạy bảo...”"),t("br"),t("br"),n._v("“Hoàng sư, ngươi làm cái gì vậy?”"),t("br"),t("br"),n._v("Thấy rõ ràng quỳ gối bóng người trước mặt, Trương Huyền giật nảy mình."),t("br"),t("br"),n._v("Không phải người khác, chính là cùng hắn tỷ thí Hoàng Tranh."),t("br"),t("br"),n._v("Hai người vừa rồi mặc dù truyền âm giảng bài, nhưng cũng có thể nghe được đối phương giảng giải nội dung, Trương Huyền nói nội dung nội dung sâu sắc, lời lẽ dễ hiểu, nhắm thẳng vào tu luyện bản chất, để hắn khó mà tự chế, khống chế không nổi muốn bái sư."),t("br"),t("br"),n._v("đọc truyện tại //truyenｃuatui"),t("br"),n._v(".net/ “Cái gì?”"),t("br"),t("br"),n._v("“Giảng xong khóa, những người khác còn không có phân biệt ra được, Hoàng sư đến bái sư trước?”"),t("br"),t("br"),n._v("“Ai có thể nói cho ta biết xảy ra chuyện gì?”"),t("br"),t("br"),n._v("“Kiến thức căn bản, nói tứ tinh đỉnh phong danh sư quỳ lạy... Rốt cuộc nói cái gì nội dung?”"),t("br"),t("br"),n._v("“Đúng vậy a, mấu chốt còn không có sử dụng sư ngôn thiên bẩm?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Dưới đài điên rồi."),t("br"),t("br"),n._v("Dựa theo trước kia Trương sư biểu hiện, thi triển sư ngôn thiên bẩm, đem Hoàng sư mê hoặc quỳ xuống, cũng là có khả năng, nhưng lần này... Sư ngôn thiên bẩm không cho phép thi triển, lại giảng giải chính là cơ sở nhất tri thức..."),t("br"),t("br"),n._v("Loại tình huống này, Hoàng sư một cái quỳ gối..."),t("br"),t("br"),n._v("Rốt cuộc chuyện gì xảy ra?"),t("br"),t("br"),n._v("Hừng hực khí thế giao đấu, còn không có kết thúc, đối phương nhất định phải quỳ xuống bái sư, chuyện này là sao... Coi như mọi người dưới đài trái tim rất lớn, vẫn như cũ cảm thấy sắp nổ."),t("br"),t("br"),n._v("Muốn hay không khoa trương như vậy?"),t("br"),t("br"),n._v("“Trương sư, chúng ta cũng muốn bái nhập môn hạ của người...”"),t("br"),t("br"),n._v("Khiếp sợ còn không có kết thúc, chỉ thấy trên đài một trăm người tu luyện, cũng đồng loạt quỳ rạp xuống đất, nhìn về phía Trương Huyền, tràn đầy thành kính cùng tôn kính."),t("br"),t("br"),n._v("“Một trăm người toàn bộ bái sư?”"),t("br"),t("br"),n._v("Chung quanh lặng ngắt như tờ."),t("br"),t("br"),n._v("Hai cái tương đồng cấp bậc danh sư đồng thời giảng bài, bình thường đều là một nửa một nửa, dù sao người người đều có tình cảm, có đủ loại cứng nhắc thành kiến, kết quả... Một trăm người tu luyện đồng loạt lạy Trương Huyền vi sư."),t("br"),t("br"),n._v("Rốt cuộc nói cái gì..."),t("br"),t("br"),n._v("Không chỉ bắt được một vị tứ tinh đỉnh phong danh sư, liền nhiều như vậy người tu luyện đều bắt được?"),t("br"),t("br"),n._v("Vậy liền coi là là... Ngũ tinh danh sư cũng làm không được đi!"),t("br"),t("br"),n._v("“Tốt, đều đứng lên đi, thi đấu kết thúc, mặc kệ thành tích như thế nào, ta đều sẽ cho chư vị nói một tiết khóa!” Trương Huyền cũng không nghĩ tới cái này nhiều người đồng thời bái sư, nhịn không được có chút nhức đầu."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Nghe được Trương sư đáp ứng giảng bài, mọi người lúc này mới khôi phục lại, đồng loạt gật đầu."),t("br"),t("br"),n._v("“Cuộc tỷ thí này... Trương sư chiến thắng!”"),t("br"),t("br"),n._v("Thấy trấn an mọi người, Hồng sư gật gật đầu tuyên bố kết quả."),t("br"),t("br"),n._v("“Đừng kéo ta, ta muốn lạy Trương sư vi sư...”"),t("br"),t("br"),n._v("Đang muốn tiếp tục tuyên bố, sau cùng trận chung kết, liền nghe đến một cái thê lương kêu thảm, ngẩng đầu nhìn lên, lập tức khóe miệng co giật."),t("br"),t("br"),n._v("Chỉ thấy mới vừa rồi còn ý khí phong phát Hoàng sư, quỳ gối Trương Huyền trước mặt, chuyên tâm bái sư, bị đồng môn người kéo xuống, còn không ngừng la lên, như là điên."),t("br"),t("br"),n._v("“Ôi, đứa bé này đã xong...”"),t("br"),n._v("Nhìn thấy cái tên này như si như say, bị điên, Hồng sư lắc đầu."),t("br"),t("br"),n._v("Làm tứ tinh đỉnh phong danh sư, đối tu luyện lý giải càng thêm khắc sâu, nghe được Trương sư giảng giải, lý giải đương nhiên so những người khác càng sâu, lâm vào càng nhiều."),t("br"),t("br"),n._v("Nhìn này tấm như si như say trán bộ dáng, đoán chừng Trương sư không đáp ứng thu đồ, cũng không được."),t("br"),t("br"),n._v("Trương sư qua ải, mỗi một quan không hỏng đồ vật, không khiến người ta tin tưởng, hiện tại cùng người tỷ thí, cùng hắn giao đấu, loại trừ bị thương chính là nổi điên..."),t("br"),t("br"),n._v("Chuyện này là sao!"),t("br"),t("br"),n._v("Trương sư đây cũng không phải là phá hoại bảo vật, ai cùng hắn so, xem ra đều sẽ bị tiến công mất đi tự tin, không có bản thân."),t("br"),t("br"),n._v("“Tốt, nghỉ ngơi mười phút đồng hồ, bắt đầu cuối cùng một hồi quan á quân tranh đoạt! Trương sư đối chiến... Tất Giang Hải, Tất sư!”"),t("br"),t("br"),n._v("Không để ý tới nổi điên ở những người khác lôi kéo bên dưới, không ngừng co giật Hoàng Tranh, Hồng sư tuyên bố kết quả."),t("br"),t("br"),n._v("Trương Huyền cũng chậm rãi đi xuống tỷ thí đài."),t("br"),t("br"),n._v("“Trương sư, vừa rồi Tất sư trực tiếp đột phá đến Hợp Linh cảnh, đem La sư đánh bại...”"),t("br"),t("br"),n._v("Thấy hắn đi tới trước mặt, Khang đường chủ vội vàng đem một cái khác lôi đài tỷ thí tình huống nói một lần."),t("br"),t("br"),n._v("“Trong tỉ thí đột phá tu vi, đồng thời đem La Tuyền đánh bại?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Liền xem như hắn, nghe đều cảm thấy kỳ lạ."),t("br"),t("br"),n._v("“Đúng vậy a, Hợp Linh cảnh cùng Trọc Thanh cảnh đỉnh phong, mặc dù chỉ kém một cái cấp bậc, thắng lợi nhưng lại có long trời lở đất chênh lệch, ta sợ... Hắn cửa này, sẽ khiêu chiến tu vi!”"),t("br"),t("br"),n._v("“Không cần nghĩ, hắn khẳng định sẽ!”"),t("br"),t("br"),n._v("Khang đường chủ, Tô sư đám người tràn đầy lo lắng."),t("br"),t("br"),n._v("Trương sư đối mặt Hóa Phàm tứ trọng Liêu Vũ Chí, lấy kiếm pháp chiến thắng, có thể đối mặt Hợp Linh cảnh cường giả, muốn vượt qua liền khó khăn."),t("br"),t("br"),n._v("Hợp Linh cảnh, linh hồn lấy được rèn luyện, cùng thân thể viên mãn dung hợp, không chỉ sức chiến đấu tăng nhiều, tốc độ phản ứng cùng năng lực, đều muốn so Trọc Thanh cảnh cường giả cường đại không ít."),t("br"),t("br"),n._v("Xem như chân chính bước vào cao thủ cảnh giới."),t("br"),t("br"),n._v("Đối phương phương diện khác, không có nắm chắc có thể vượt qua Trương sư, khẳng định chỉ có thể ở cái này phía trên làm văn chương."),t("br"),t("br"),n._v("Dù sao, Trương sư, cho tới bây giờ, chỉ có Hóa Phàm tam trọng đỉnh phong, coi như thiên phú cao, nhãn lực tốt, có thể vượt cấp chiến đấu, thế nhưng không có khả năng vượt ngang hai cái đại cấp bậc, đem Hợp Linh cảnh cường giả đều đánh bại."),t("br"),t("br"),n._v("Đây là Danh Sư đường, thậm chí toàn bộ danh sư đại lục, đều chưa từng có."),t("br"),t("br"),n._v("“Đối mặt Trương sư, hắn khẳng định khiêu chiến tu vi, vậy làm sao bây giờ? Trừ phi... Cự tuyệt!”"),t("br"),t("br"),n._v("“Cự tuyệt? Hắn muốn quyết tâm tiến hành luận võ, cự tuyệt có làm được cái gì?”"),t("br"),t("br"),n._v("Mọi người tất cả đều tràn đầy gấp gáp."),t("br"),t("br"),n._v("“Trương sư có thể vượt cấp khiêu chiến, nếu như... Bây giờ có thể đột phá đến Trọc Thanh cảnh, có lẽ, còn có thể một trận chiến...”"),t("br"),t("br"),n._v("Triệu Phi Vũ nhịn không được nói."),t("br"),t("br"),n._v("Trương sư Hóa Phàm tam trọng đỉnh phong thời điểm liền đánh bại Trọc Thanh cảnh đỉnh phong Liêu Vũ Chí, nếu như bây giờ có thể đi đến Trọc Thanh cảnh, có lẽ đối mặt vừa mới bước vào Hợp Linh cảnh Tất sư, cũng có sức đánh một trận."),t("br"),t("br"),n._v("“Đột phá Trọc Thanh cảnh... Nào có dễ dàng như vậy! Chỉ nghỉ ngơi mười phút đồng hồ, hiện tại thời gian đã qua nửa, ngươi ý tứ, muốn Trương sư trong vòng năm phút đồng hồ đột phá?”"),t("br"),t("br"),n._v("Nghe được nàng, Khang đường chủ cười khổ lắc đầu."),t("br"),t("br"),n._v("Tu vi cùng lên thang lầu giống nhau, cần một bước một bậc thang, không có khả năng một lần mà thôi."),t("br"),t("br"),n._v("Trương sư trong khoảng thời gian này tốc độ tiến bộ liền rất nhanh, để hắn tại trong vòng năm phút đồng hồ, lần nữa đột phá?"),t("br"),t("br"),n._v("Làm sao có thể?"),t("br"),t("br"),n._v("Đây không phải là nói đùa sao?"),t("br"),t("br"),n._v("“Cũng phải...” Triệu Phi Vũ gật gật đầu, đang muốn tiếp tục suy nghĩ những biện pháp khác, chỉ thấy bên người thanh niên nhìn qua: “Các ngươi là ý nói, ta chỉ cần đột phá Trọc Thanh cảnh, muốn thắng ta liền không dễ dàng đúng không!”"),t("br"),t("br"),n._v("“Vâng...”"),t("br"),t("br"),n._v("Triệu Phi Vũ gật đầu."),t("br"),t("br"),n._v("“Vậy thì tốt, các ngươi chờ, ta trước đột phá một cái...”"),t("br"),t("br"),n._v("Trương sư gật gật đầu, cổ tay khẽ đảo, một cái linh thạch trung phẩm xuất hiện tại lòng bàn tay, chắp tay trước ngực, khí tức trên thân trong nháy mắt giống nhen lửa xăng, rầm rầm dâng lên."),t("br"),t("br"),n._v("“Chờ?”"),t("br"),t("br"),n._v("“Sẽ không Trương sư thật dự định hiện tại đột phá chứ?”"),t("br"),t("br"),n._v("“Cái này có cái gì không có khả năng, hắn cũng không phải không có ở thời khắc mấu chốt đột phá qua...”"),t("br"),t("br"),n._v("Nghe được Trương sư lời nói, nhìn thấy cử động của hắn, tất cả mọi người ngẩn ngơ."),t("br"),t("br"),n._v("Muốn hay không khoa trương như vậy?"),t("br"),t("br"),n._v("Nói đột phá đã đột phá, ngươi coi đột phá là ăn khoai lang a?"),t("br"),t("br"),n._v("Đơn giản như vậy?"),t("br"),t("br"),n._v("Bất quá, mọi người khiếp sợ còn không có kết thúc, liền nghe đến một tiếng nổ vang, Trương sư lực lượng giống như là phá vỡ cái nào đó ràng buộc, liên tục tăng lên."),t("br"),t("br"),n._v("“Hóa Phàm tứ trọng... Trọc Thanh cảnh?”"),t("br"),t("br"),n._v("Khang đường chủ đám người khóe miệng giật một cái."),t("br"),t("br"),n._v("Vốn cho rằng tùy tiện nói một chút, ngươi đến thật?"),t("br"),t("br"),n._v("Cảm nhận được bên này khí tức ba động, cách đó không xa Tất Giang Hải nhìn lại."),t("br"),t("br"),n._v("Vốn cho rằng đột phá đến Hợp Linh cảnh, đối phó đối phương dễ như trở bàn tay, không nghĩ tới cái tên này cũng đột phá..."),t("br"),t("br"),n._v("Trương sư, còn có cái gì ngươi làm không được sao?"),t("br"),t("br"),n._v("“Không được, không thể để cho hắn tiếp tục đột phá, nếu không cuộc tỷ thí này khẳng định thất bại...”"),t("br"),t("br"),n._v("Một cắn răng đứng lên, thân thể nhoáng một cái, đi tới tỷ thí trên đài, vung tay áo một cái: “Trương sư, ta đã nghỉ ngơi tốt, đã tất cả mọi người chờ lấy, ta nhìn cũng là đừng kéo dài, lập tức bắt đầu đi!”"),t("br"),t("br"),n._v("“Lập tức bắt đầu?”"),t("br"),t("br"),n._v("“Hắn là nhìn thấy Trương sư đột phá, sợ hãi đi!”"),t("br"),t("br"),n._v("“Khẳng định đúng vậy, Trương sư mới vừa đột phá, tu vi còn không có củng cố, lúc này chiến đấu, có thể nói qua sông không tế, vừa vặn có thể chiếm được thượng phong, một khi củng cố, ai biết sẽ xuất hiện tình huống gì?”"),t("br"),t("br"),n._v("Thấy Tất Giang Hải không đợi nghỉ ngơi kết thúc, liền đưa ra tỷ thí, mọi người tất cả đều mày nhăn lại."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-607"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-609"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);