(window.webpackJsonp=window.webpackJsonp||[]).push([[1867],{2221:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-602-chien-hon-the"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-602-chien-hon-the"}},[n._v("#")]),n._v(" Chương 602: Chiến Hồn thể")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Tốt, quan á quân xuất hiện, phía dưới tiến hành danh thứ khác tranh đấu...”"),t("br"),t("br"),n._v("Trấn an kích động mọi người, Hồng sư tiếp tục tổ chức tranh tài."),t("br"),t("br"),n._v("Hiện tại chỉ là quyết ra một, hai tên mà thôi, còn lại ba đến mười, còn cần tiếp tục tỷ thí."),t("br"),t("br"),n._v("Có trước mặt châu ngọc, phía sau tỷ thí, mọi người đã không hứng lắm, lại tiến hành một canh giờ, lần này danh sư thi đấu mười hạng đầu, rốt cục toàn bộ ra lò."),t("br"),t("br"),n._v("Cùng Trương Huyền giao đấu thua rất thảm Vị Trường Thanh, tại còn lại trong tỉ thí biểu hiện nổi bật, thành công tấn cấp mười vị trí đầu, bị Nhược Hoan công tử đào thải cái kia Tần Lỗi, tại còn lại trong tỉ thí cũng biểu hiện loá mắt, trở thành thứ chín."),t("br"),t("br"),n._v("Kỳ thật nếu không phải Trương Huyền hỗ trợ, hắn ít nhất là năm vị trí đầu thứ tự, có điều, lấy được thứ chín cũng chí ít nắm giữ đi Hồng Viễn danh sư học viện cơ hội, chuyến đi này không tệ."),t("br"),t("br"),n._v("“Trương sư, đây là phần thưởng của ngươi!”"),t("br"),t("br"),n._v("Thứ tự xác định, bắt đầu trao giải, tên thứ nhất lấy được một cái linh thạch thượng phẩm, đi vào Hồng Viễn danh sư học viện học tập cơ hội, cùng Hóa Thanh trì tu luyện ba ngày cơ hội."),t("br"),t("br"),n._v("Đem ban thưởng ban Trương Huyền, Hồng sư truyền âm ngay tại vang lên bên tai: “Trương sư, linh thạch thượng phẩm là Mạc Cao Viễn Mạc đường chủ tư nhân cấp cho tên thứ nhất...”"),t("br"),t("br"),n._v("Mạc đường chủ có ý định lấy lòng, tự nhiên muốn làm cho đối phương biết, nếu không, chẳng phải làm không vô dụng công?"),t("br"),t("br"),n._v("Trương Huyền lúc này mới kịp phản ứng."),t("br"),t("br"),n._v("Khó trách năm nay thi đấu ban thưởng so những năm qua đều muốn phong phú, nguyên lai nguyên nhân ở đây!"),t("br"),t("br"),n._v("Kỳ thật, chuyện này cũng không khó nghĩ, “Dương sư” biểu hiện thủ đoạn như thế nghịch thiên, Mạc đường chủ có ý định lấy lòng, cũng không đủ là lạ."),t("br"),t("br"),n._v("Bất quá, đối với hắn như vậy tới nói tốt nhất."),t("br"),t("br"),n._v("Đây chính là linh thạch thượng phẩm, giá trị một vạn viên linh thạch trung phẩm, nếu không phải ngại vì mặt mũi, khẳng định đã sớm mở hộp ngọc ra, xem xét tỉ mỉ."),t("br"),t("br"),n._v("Đem ban thưởng thu sạch tốt, Trương Huyền tràn đầy kích động cùng hưng phấn."),t("br"),t("br"),n._v("Có thứ này, tu luyện về sau liền sẽ đơn giản không ít, không cần lại vì linh khí, tiền tài phát sầu."),t("br"),t("br"),n._v("“Tốt, lần này danh sư thi đấu kết thúc mỹ mãn, thu hoạch được thi đấu mười vị trí đầu, ngày mai mời cùng ta cùng một chỗ đi tới Huyễn Vũ đế quốc, tiếp nhận ngợi khen nhận lấy đi vào Hóa Thanh trì tu luyện cơ hội!”"),t("br"),t("br"),n._v("Trao giải hoàn tất, Hồng sư nhìn quanh một tuần, nói."),t("br"),t("br"),n._v("“Ngày mai liền đi?”"),t("br"),t("br"),n._v("“Gấp gáp như vậy?”"),t("br"),t("br"),n._v("“Kỳ thật cũng không tính là gấp gáp đi, Huyễn Vũ đế quốc cách nơi này vượt qua trăm vạn cây số, không nhanh chóng chạy tới, một khi bỏ lỡ Hóa Thanh trì mở ra, liền nguy rồi!”"),t("br"),t("br"),n._v("“Này ngược lại là, Hóa Thanh trì mỗi lần đều tại đầu năm mở ra, khoảng cách hiện tại đã không đến một tháng, lại không nhanh lên một chút quả thực không còn kịp rồi...”"),t("br"),t("br"),n._v("Nghe được Hồng sư bảo ngày mai liền đi, tất cả mọi người mặc dù ngoài ý muốn, vẫn là rõ ràng hắn ý tứ."),t("br"),t("br"),n._v("Hóa Thanh trì là Huyễn Vũ đế quốc, hoàng thất trân quý nhất bảo vật, cũng là lập quốc dựa dẫm, mười năm mở ra một lần, khoảng cách hiện tại đã thời gian không nhiều, lại không nhanh lên một chút, một khi bỏ lỡ, ai cũng không cách nào bồi thường."),t("br"),t("br"),n._v("“Tốt, chư vị trở về chuẩn bị đi, ngày mai buổi trưa xuất phát!”"),t("br"),t("br"),n._v("Hồng sư vung tay lên."),t("br"),t("br"),n._v("Thi đấu tiến hành đến hiện tại, đã đến đêm khuya, lưu cho mọi người thời gian đã không đủ một ngày."),t("br"),t("br"),n._v("“Ta cũng chuẩn bị một chút!”"),t("br"),t("br"),n._v("Trương Huyền cũng đứng dậy."),t("br"),t("br"),n._v("Hắn phải xử lý sự tình tương đối nhiều, một ngày thời điểm thật có chút khẩn trương."),t("br"),t("br"),n._v("“Khang đường chủ, đa tạ trong khoảng thời gian này chiếu cố, đây là lão sư ta chuyên môn vì ngươi viết pháp quyết tu luyện, dựa theo phía trên tu luyện, có lẽ có thể giúp ngươi đột phá đến Hợp Linh cảnh!”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, một quyển sách xuất hiện tại lòng bàn tay, Trương Huyền đưa tới."),t("br"),t("br"),n._v("Trong khoảng thời gian này nhờ có Khang đường chủ chiếu cố, nhất là Băng Nguyên cung, không có nhân tình của hắn, bản thân không có khả năng để linh hồn trở nên cường đại như thế, thuận lợi một phân thành hai, ngưng tụ phân thân thành công, cũng không có khả năng có Triệu Nhã cơ duyên."),t("br"),t("br"),n._v("Có thể nói, Khang đường chủ sự giúp đỡ dành cho hắn rất lớn, bởi vậy, trong khoảng thời gian này căn cứ trên người đối phương thiếu hụt, chuyên môn sửa sang lại một bộ thích hợp hắn công pháp, chỉ cần dần dần từng bước tu luyện, đột phá Hợp Linh cảnh chỉ là vấn đề thời gian."),t("br"),t("br"),n._v("“Đột phá Hợp Linh? Dương sư viết?”"),t("br"),t("br"),n._v("Khang đường chủ hô hấp lập tức trở nên gấp rút, tròng mắt đỏ hoe."),t("br"),t("br"),n._v("Dương sư, đây chính là nghi tự bát tinh danh sư, có thể được đến chỉ điểm của hắn tuyệt đối là tam sinh hữu hạnh, không chỉ có như thế, còn cho hắn chuyên môn viết một bộ công pháp, phần ân tình này cũng không tránh khỏi quá lớn!"),t("br"),t("br"),n._v("Phù phù!"),t("br"),t("br"),n._v("Đầu gối mềm nhũn quỳ rạp xuống đất, Khang đường chủ mặt ngưng trọng đối với Trương Huyền phủ đệ dập đầu tám cái, lúc này mới đứng dậy."),t("br"),t("br"),n._v("“Tô sư, Lăng sư, đây là các ngươi...”"),t("br"),t("br"),n._v("Cho Khang đường chủ, đương nhiên cũng không thể quên Tô sư cùng Lăng sư, không có hai người, sao có thể lấy được lần thi đấu này danh ngạch cùng cơ hội."),t("br"),t("br"),n._v("Tiếp nhận công pháp, Tô sư, Lăng sư cũng là thiên ân vạn tạ."),t("br"),t("br"),n._v("Có ân báo ân, có oán báo oán, Trương Huyền luôn luôn ân oán rõ ràng."),t("br"),t("br"),n._v("“Trương sư, ta có thể đi theo ngươi Hồng Viễn danh sư học viện ư?”"),t("br"),t("br"),n._v("Biết Trương Huyền muốn đi, Triệu Phi Vũ hốc mắt ửng đỏ."),t("br"),t("br"),n._v("Vị thanh niên này chữa khỏi bệnh của nàng chứng, để hắn không cần tiếp tục nằm ở trên giường, bản thân liền mang theo cảm kích, lại đi qua Hiên Viên vương quốc, Vu hồn mộ các nơi ở chung, sớm đã đối với hắn có không nói rõ được cũng không tả rõ được cảm giác, chẳng qua là ngượng ngùng xuyên phá giấy cửa sổ mà thôi."),t("br"),t("br"),n._v("Hiện tại ly biệt sắp đến, rốt cuộc ngăn chặn không được."),t("br"),t("br"),n._v("“Thôi được rồi, thực lực ngươi quá thấp, tùy tiện đi qua, rất là phiền phức, ở lại chỗ này, do Khang đường chủ chỉ điểm, tương đối ổn thỏa!”"),t("br"),t("br"),n._v("Suy nghĩ một chút Trương Huyền mở miệng, nói xong nhìn về phía cách đó không xa lão giả: “Khang đường chủ, Phi Vũ công chúa liền nhờ ngươi!”"),t("br"),t("br"),n._v("“Ha ha, Trương sư yên tâm, ta sẽ thu nàng làm học sinh thân truyền, cố gắng chỉ điểm, không phụ lòng Dương sư đối ta hậu ái!” Khang đường Chủ Thần sắc mặt ngưng trọng gật đầu cam đoan."),t("br"),t("br"),n._v("“Đa tạ!” Trương Huyền ôm quyền."),t("br"),t("br"),n._v("Kỳ thật, hắn cấp cho Khang đường chủ công pháp một nguyên nhân khác, chính là căn cứ vào cái này cân nhắc."),t("br"),t("br"),n._v("Cùng nhau đi tới, Triệu Phi Vũ cũng cho cực lớn trợ giúp, đối phương là minh chủ nữ nhi, đường đường công chúa, giá trị, vật tư các loại coi như cho, cũng nhìn không thuận mắt, còn không bằng tìm lão sư tốt, về sau bất kể tu luyện vẫn là tiếp tục tại Vạn Quốc thành sinh hoạt, đều sẽ dễ dàng rất nhiều."),t("br"),t("br"),n._v("Khang đường chủ chính là người chọn lựa thích hợp nhất."),t("br"),t("br"),n._v("“Đúng, đa tạ Trương sư...”"),t("br"),t("br"),n._v("Nghe được sắp xếp của hắn, Triệu Phi Vũ hốc mắt đỏ lên, mặc dù tràn đầy không cam lòng, vẫn gật đầu."),t("br"),n._v("Trương sư nhất định là rồng phượng trong loài người, không phải nàng một cái nho nhỏ liên minh công chúa có thể so sánh, coi như dù tiếc đến đâu cũng phải từ bỏ."),t("br"),t("br"),n._v("Cùng Khang đường chủ đám người cáo từ, Trương Huyền trở lại phủ đệ, mới vừa vào cửa, chỉ thấy Mặc Hồn Sinh đi nhanh tới."),t("br"),t("br"),n._v("“Trương sư!”"),t("br"),t("br"),n._v("Mặc Hồn Sinh ôm quyền."),t("br"),t("br"),n._v("“Thế nào?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Tham gia danh sư thi đấu trước, từng để vị này điều tra Lộ Xung tình huống, hiện tại qua hơn một ngày, có lẽ có kết quả."),t("br"),t("br"),n._v("“Bẩm báo thiếu gia, tiểu thiếu gia vừa rồi tu luyện hoàn tất, ta chuyên môn nhìn, nếu như không có đoán sai, hẳn là vu hồn sư bên trong thể chất đặc thù... !”"),t("br"),t("br"),n._v("Mặc Hồn Sinh nói."),t("br"),t("br"),n._v("“Thể chất đặc thù?” Trương Huyền nghi hoặc."),t("br"),t("br"),n._v("Nếu như hắn thật sự có thể chất đặc thù, Thiên Đạo thư viện khẳng định đã sớm phát hiện, làm sao sẽ không có chút nào phát giác?"),t("br"),t("br"),n._v("“Chiến Hồn thể là Hậu Thiên tạo thành, bình thường đều là trải qua sống chết, đại bi đại hỉ mới có thể thành công, chỉ có thể ngộ mà không thể cầu, không cách nào phục chế, tiểu thiếu gia, cũng hẳn là vừa mới thức tỉnh...” Mặc Hồn Sinh vội nói."),t("br"),t("br"),n._v("“Đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Trương Huyền gật gật đầu, nhấc chân hướng vào phía trong viện đi đến."),t("br"),t("br"),n._v("Vừa đi vào hành lang không xa, liền thấy Lộ Xung đang đứng trong sân tu luyện, Vu hồn tại thiên không bay lượn, mang theo lăng lệ khí tức."),t("br"),t("br"),n._v("“Hóa Phàm nhị trọng Vu hồn?” Trương Huyền con mắt trợn tròn, nhịn không được sững sờ."),t("br"),t("br"),n._v("Buổi sáng rời đi thời điểm, đối phương vẫn là Tông Sư cảnh thực lực, làm sao một ngày không thấy, giống như này mạnh mẽ?"),t("br"),t("br"),n._v("Vu hồn tu luyện cùng chân khí tu luyện đồng dạng, cũng cần thời điểm tích lũy, một ngày thời gian đã đột phá Chí Tôn, nửa bước Hóa Phàm, Tăng Thọ chờ bốn cái đại cấp bậc, tốc độ tu luyện không khỏi quá nhanh chút đi!"),t("br"),t("br"),n._v("“Thiếu hụt!”"),t("br"),t("br"),n._v("Tràn đầy kỳ lạ, trong lòng thấp giọng hô."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Một tiếng kêu khẽ, trong óc thêm ra một quyển sách."),t("br"),t("br"),n._v("Vội vàng mở ra, nhìn một hồi, Trương Huyền sửng sốt."),t("br"),t("br"),n._v("Cùng Mặc Hồn Sinh nói đồng dạng, thời khắc này Lộ Xung quả thực cùng trước đó không giống, có hắn nói tới Chiến Hồn thể."),t("br"),t("br"),n._v("Chẳng qua xem ra, có lẽ còn không có hoàn toàn thức tỉnh."),t("br"),t("br"),n._v("“Đoán chừng chính là ta nhìn thấy cái kia...”"),t("br"),t("br"),n._v("Nhớ tới ban đầu ở đối phương thức hải nhìn thấy một màn kia, Trương Huyền trong lòng suy đoán."),t("br"),t("br"),n._v("Dùng Tỉnh Hồn hương triệu hoán đối phương linh hồn thời điểm, từng nhìn thấy một cái to lớn thân ảnh nằm tại đối phương thức hải, hiện tại xem ra hẳn là cái gọi là Chiến Hồn thể."),t("br"),t("br"),n._v("“Loại thể chất này... Như thế nào thức tỉnh?”"),t("br"),t("br"),n._v("Nhịn không được nhìn về phía cách đó không xa Vô Hồn Kim Nhân."),t("br"),t("br"),n._v("“Bẩm báo thiếu gia, hoàn toàn thức tỉnh mười phần phiền phức, có thể muốn đi Vu hồn tổ địa!”"),t("br"),t("br"),n._v("Suy nghĩ một chút, Mặc Hồn Sinh nói."),t("br"),t("br"),n._v("“Vu hồn tổ địa?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("“Ừm, đây là vu hồn sư nguồn gốc chi địa, trong đó nắm giữ Tổ Vu lực lượng, Chiến Hồn thể muốn hoàn toàn kích hoạt, cũng chỉ có thể dựa vào loại lực lượng này, nếu không, không có những biện pháp khác!”"),t("br"),t("br"),n._v("Mặc Hồn Sinh chần chờ một chút nói."),t("br"),t("br"),n._v("“Vu hồn tổ địa? Ở nơi nào?” Trương Huyền cau mày."),t("br"),t("br"),n._v("“Tại... Đại lục mặt đông nhất, cách nơi này không biết bao xa...” Mặc Hồn Sinh gãi đầu một cái."),t("br"),t("br"),n._v("“Không biết bao xa?” Trương Huyền sầm mặt lại."),t("br"),t("br"),n._v("Quá xa mà nói, làm sao đi?"),t("br"),t("br"),n._v("Danh sư đại lục bao la vô cùng, mấy trăm ngàn cây số liền muốn tốn hao không biết bao nhiêu ngày phi hành mới có thể đến, nghe Mặc Hồn Sinh khẩu khí, cái này khoảng cách rất xa, khẳng định vượt qua ngàn vạn cây số thậm chí hơn trăm triệu..."),t("br"),t("br"),n._v("Đi qua mà nói, không có mấy năm thời gian, rất khó hoàn thành."),t("br"),t("br"),n._v("“Kỳ thật... Thiếu gia yên tâm lời nói, có thể để cho ta mang theo tiểu thiếu gia tiến đến, cũng không chậm trễ ngươi bình thường tu hành, cũng có thể để tiểu thiếu gia, mau chóng phát triển, hai không chậm trễ.”"),t("br"),t("br"),n._v("Mặc Hồn Sinh nói."),t("br"),t("br"),n._v("“Ngươi dẫn đi?”"),t("br"),t("br"),n._v("Trương Huyền chần chờ."),t("br"),t("br"),n._v("Hắn muốn đi danh sư lộ tuyến, liền muốn một mực sống ở mọi người chú mục bên dưới, lại thêm nguy hiểm cho sinh mệnh Tiên Thiên thai độc, căn bản không cho phép hắn tốn hao mấy năm công phu đi tìm cái gì cái gọi là Vu hồn tổ địa."),t("br"),t("br"),n._v("Mà để Lộ Xung một mực đợi tại bên cạnh mình, đối với hắn phát triển cũng quả thực có chút không quá công bằng."),t("br"),t("br"),n._v("Nếu như có thể chia binh hai đường tất nhiên là tốt nhất."),t("br"),t("br"),n._v("“Nhìn hắn ý tưởng đi!”"),t("br"),t("br"),n._v("Suy nghĩ một chút, cảm thấy mình cũng không cách nào thay Lộ Xung làm chủ, Trương Huyền nói."),t("br"),t("br"),n._v("Để Lộ Xung dừng lại tu luyện, đem sự tình nói rõ chi tiết một lần."),t("br"),t("br"),n._v("“Lão sư, ta muốn đi!” Nghe xong, thiếu niên ánh mắt ngưng trọng gật đầu."),t("br"),t("br"),n._v("Hắn cùng Triệu Nhã ý tưởng đồng dạng, một mực lưu tại lão sư bên người, vẫn là cái liên lụy, mà có loại cơ hội này, về sau có lẽ liền có thể đến giúp lão sư, thực sự trở thành trợ lực."),t("br"),t("br"),n._v("Mà không phải vướng víu."),t("br"),t("br"),n._v("“Đã ngươi có quyết định, ta cũng không miễn cưỡng, Mặc Hồn Sinh, Lộ Xung liền giao cho ngươi!”"),t("br"),t("br"),n._v("Thấy hắn có quyết định, Trương Huyền than thở một hơi, nhẹ gật đầu."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-610"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-612"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);