(window.webpackJsonp=window.webpackJsonp||[]).push([[187],{540:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1145-tho-lam-vuon"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1145-tho-lam-vuon"}},[n._v("#")]),n._v(" Chương 1145: Thợ làm vườn")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1145: Thợ làm vườn"),t("br"),t("br"),n._v("Trương Huyền là người xuyên việt, mang theo người Trung Quốc tốt phẩm chất, cùng thói quen tốt, từ trước tới nay không muốn cùng con người làm ra ác, phần lớn đều là hòa khí sinh tài, chuyện lớn hóa nhỏ, chuyện nhỏ hóa không."),t("br"),t("br"),n._v("Nhưng mà... Rồng có vảy ngược, chạm vào hẳn phải chết!"),t("br"),t("br"),n._v("Tôn Cường theo Thiên Huyền vương quốc liền theo hắn, chú tâm quản lý bên người việc vặt, chịu mệt nhọc, tận tâm tận trách nhiệm, mặc dù đối cái tên này tu luyện, không muốn chửi bậy, nhưng nếu như không có vị này, hắn tuyệt đối không có khả năng, hoàn toàn miễn rơi nỗi lo về sau, toàn thân tâm tu luyện, ngắn ngủi một năm không đến thời gian, liền đạt tới tình trạng như thế."),t("br"),t("br"),n._v("Có thể nói, vị này Tôn quản gia, đã thành tính mạng hắn bên trong người trọng yếu nhất, cùng Vương Dĩnh, Triệu Nhã đám người địa vị đồng dạng."),t("br"),t("br"),n._v("Kẻ trước mắt này, thế mà coi đây là áp chế, làm sao không tức giận?"),t("br"),t("br"),n._v("“Ngươi dám đánh ta?”"),t("br"),t("br"),n._v("Hiển nhiên không nghĩ tới, có con tin trong tay, trước mắt vị này lại còn dám động thủ, thanh niên mặc áo đen giãy dụa lấy đứng dậy, ánh mắt lộ ra nồng đậm hận ý."),t("br"),t("br"),n._v("“Ta muốn ngươi...”"),t("br"),t("br"),n._v("Gào lên một tiếng, bỗng nhiên lao đến, nói còn chưa dứt lời, liền cảm thấy toàn thân xiết chặt, bị người nắm cổ, nhấc lên."),t("br"),t("br"),n._v("Lập tức thanh niên liền thấy một đôi lãnh đạm ánh mắt."),t("br"),t("br"),n._v("“Muốn chết, ta có thể tác thành ngươi!”"),t("br"),t("br"),n._v("Cảm nhận được trong đó sát lục chi khí, cùng lạnh lùng như băng lời nói, thanh niên mặc áo đen vẻ mặt trắng bệch, vốn còn muốn nói cái gì, giờ phút này tất cả đều nuốt đến miệng bên trong, một câu đều nói không ra."),t("br"),t("br"),n._v("Hắn trải qua sinh tử, đó có thể thấy được người trước mắt này thái độ, thực có can đảm nhiều lời nửa câu, tuyệt đối dám động thủ đem hắn chém giết."),t("br"),t("br"),n._v("“Lão gia các ngươi là ai? Tôn Cường hiện tại ở đâu?”"),t("br"),t("br"),n._v("Thấy đối phương lại không nói nhảm, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Ta...” Thanh niên mặc áo đen run rụt lại: “Lão gia nhà chúng ta là Thanh Nguyên đế quốc đệ nhất vương gia, Trung Thanh Vương...”"),t("br"),t("br"),n._v("“Trung Thanh Vương?” Trương Huyền nhíu nhíu mày: “Hắn tại sao muốn bắt người của ta?”"),t("br"),t("br"),n._v("Hắn đi tới Thanh Nguyên đế quốc, chẳng qua ba ngày nhiều thời giờ, hoạt động phạm vi cũng chỉ hạn chế tại khải linh sư công hội, kinh hồng sư công hội, cùng Chiến sư đường."),t("br"),t("br"),n._v("Địa phương khác đi đều không có đi qua, làm sao sẽ đắc tội vị này cái gọi là đệ nhất vương gia?"),t("br"),t("br"),n._v("Nếu như không phải đắc tội, đối phương hẳn là còn không có đầy đủ can đảm, hướng danh sư động thủ đi!"),t("br"),t("br"),n._v("Cho dù là danh sư hạ nhân, cũng quan hệ đối phương tôn nghiêm, ngang nhiên bắt đi, hơn nữa phái người tới uy hiếp... Lá gan thật là đủ lớn."),t("br"),t("br"),n._v("“Ta chỉ là cái hạ nhân... Những chuyện khác, thật không biết!” Thanh niên mặc áo đen run rẩy."),t("br"),t("br"),n._v("“Phía trước dẫn đường!”"),t("br"),t("br"),n._v("Nhìn đối phương bộ dáng, khả năng thật không rõ ràng, Trương Huyền tại hắn sống lưng vỗ một cái, tiện tay ném xuống đất."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Rụt cổ một cái, thanh niên mặc áo đen tại không có trước đó kiêu ngạo, vội vã hướng về phía trước đi đến."),t("br"),t("br"),n._v("“Trương sư, vị này Trung Thanh Vương, ta trước đó nghe nói qua, ngươi phải cẩn thận...”"),t("br"),t("br"),n._v("Theo sát ở phía sau, Ngọc Phi Nhi đôi mi thanh tú nhàu thành viên."),t("br"),t("br"),n._v("Nàng là Hồng Viễn đế quốc công chúa, mặc dù không thích chính trị, nhưng mưa dầm thấm đất vẫn là biết một chút."),t("br"),t("br"),n._v("“Ồ?” Trương Huyền xoay đầu lại."),t("br"),t("br"),n._v("“Cái này vương gia, vốn là một giới bình dân, hơn hai mươi năm trước, Dị Linh tộc nhân không biết từ nơi nào tìm được thông đạo, ồ ạt xâm phạm, Danh Sư đường, Chiến sư đường cùng Thanh Nguyên đế quốc, phái người ngăn cản... Tam đại thế lực, đều tổn thất nặng, Chiến sư đường hình Thiên Minh đường chủ, bản thân bị trọng thương, suýt chút nữa vẫn lạc!”"),t("br"),t("br"),n._v("“Lúc ấy... Thanh Nguyên đế quốc Hoàng đế Sở Thiên Hành, ngự giá thân chinh, bị Dị Linh tộc nhân mai phục, khó mà chạy ra, ngay lúc sắp bị giết, là vị này Trung Thanh Vương, đột nhiên xuất hiện, đại sát tứ phương, đem người cứu ra... Về sau, lại lũ lịch công lao. Ngắn ngủi chừng hai mươi năm, liền theo người bình thường thành công Phong Vương, hơn nữa còn là đứng hàng thứ nhất khác họ vương!”"),t("br"),t("br"),n._v("Ngọc Phi Nhi nói."),t("br"),t("br"),n._v("“Dị Linh tộc nhân xâm phạm?” Trương Huyền cau mày."),t("br"),t("br"),n._v("Hình đường chủ nguyên thần bên trên vết rách, liền là hơn hai mươi năm trước lưu lại."),t("br"),t("br"),n._v("“Hoàng đế bên cạnh, đều có vô số cao thủ bảo hộ, thực lực bản thân hẳn là cũng không thấp... Bị Dị Linh tộc nhân bao vây, đều không trốn thoát được, vị này Trung Thanh Vương, thực lực gì, có thể đem người cứu ra, còn không bị hao tổn tổn thương?”"),t("br"),t("br"),n._v("Trương Huyền hỏi."),t("br"),t("br"),n._v("Liền Hình đường chủ đều không có kháng trụ, suýt chút nữa vẫn lạc, một cái tên không kinh truyền gia hỏa, dựa dẫm thủ đoạn gì, theo Dị Linh tộc nhân trong vòng vây, trốn ra được?"),t("br"),t("br"),n._v("“Cụ thể ta không rõ lắm... Ai, đúng, ta từng nghe phụ hoàng, cùng người khác nhắc tới qua một lần, hình như là... Dùng độc!”"),t("br"),t("br"),n._v("Lắc đầu, Ngọc Phi Nhi đột nhiên nhớ tới cái gì nói."),t("br"),t("br"),n._v("“Độc?”"),t("br"),t("br"),n._v("Trương Huyền cau mày: “Chẳng lẽ... Vị này vương gia là một vị độc sư?”"),t("br"),t("br"),n._v("Dùng độc lời nói, đến là rất có thể!"),t("br"),t("br"),n._v("Độc sư chức nghiệp, sở dĩ làm người ta nghe đến đã biến sắc, chủ yếu nhất cũng là bởi vì không e ngại quần công, chỉ cần dùng đúng, nhiều hơn nữa đều có thể tuỳ tiện chém giết."),t("br"),t("br"),n._v("Thật giống như Thanh Điền thập đại vương giả, tám chín phần mười đều bị hắn Thiên Đạo chân khí độc chết."),t("br"),t("br"),n._v("Nếu như chiến đấu, hắn chết sớm không biết mấy lần."),t("br"),t("br"),n._v("“Là không phải độc sư ta không rõ lắm, có điều, phản đối vị này Trung Thanh Vương rất nhiều đại thần, hai mươi năm qua, tất cả đều không bệnh mà chết... Nguyên nhân chính là như vậy, tại triều đình quyền thế rất lớn, lại thêm đã từng từng cứu bệ hạ, rất được chú trọng, ai cũng muốn kiêng kị mấy phần!”"),t("br"),t("br"),n._v("Ngọc Phi Nhi nói."),t("br"),n._v("Nhẹ gật đầu, Trương Huyền chân mày nhíu nhanh hơn."),t("br"),t("br"),n._v("Mặc kệ cái tên này, có phải hay không độc sư, chính mình cũng không nhận ra, cũng chưa từng gặp qua, vì sao muốn bắt Tôn Cường?"),t("br"),t("br"),n._v("“Có thể bắt lấy Tôn Cường, đã nói lên căn bản không quan tâm ta lục tinh danh sư cùng danh sư học viện viện trưởng thân phận, các ngươi cũng không cần đi qua... Một mình ta là được!”"),t("br"),t("br"),n._v("Dừng lại một chút, Trương Huyền nói."),t("br"),t("br"),n._v("Mặc kệ đối phương ý tứ là cái gì, rất rõ ràng là hướng về phía hắn tới, đã như vậy, không cần thiết để hai vị này chuyến cái này vũng nước đục."),t("br"),t("br"),n._v("“Trương sư, ngươi là lão sư của ta, làm học sinh, không có khả năng trơ mắt nhìn lão sư đi địa phương nguy hiểm, mà bản thân chạy trốn, nếu không, ta làm sao đối mặt danh sư chức nghiệp?”"),t("br"),t("br"),n._v("Tiến về phía trước một bước, Lạc Thất Thất trong ánh mắt mang theo kiên định."),t("br"),t("br"),n._v("Nàng là Trương Huyền luyện đan, luyện khí học sinh, mặc dù không phải thân truyền, cũng không có qua thu đồ nghi thức, nhưng cũng có thầy trò tình nghĩa."),t("br"),t("br"),n._v("“Ta cũng không trở về! Ta muốn cùng Trương sư chung tiến thối...” Ngọc Phi Nhi gương mặt xinh đẹp vừa nhấc."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Thấy hai người ánh mắt kiên định, sẽ không dao động, Trương Huyền lắc đầu, cổ tay khẽ đảo lấy ra hai cái đan dược: “Đây là ta luyện chế giải độc đan, có thể giải bách độc, các ngươi trước dùng, nếu như đối phương thật sự là độc sư, dùng cái này có thể cam đoan an toàn!”"),t("br"),t("br"),n._v("Hắn cái gọi là giải độc đan, chỉ là bình thường đan dược, truyền vào Thiên Đạo chân khí mà thôi."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Hai người biết không đáp ứng, khẳng định không mang theo đi qua, không nói thêm lời, đem dược vật để vào trong miệng, nuốt xuống."),t("br"),t("br"),n._v("“Đi thôi!”"),t("br"),t("br"),n._v("Bọn họ trao đổi đều là truyền âm, cũng không ảnh hưởng đi lại, đi theo thanh niên mặc áo đen sau lưng, thời gian không dài, liền thấy một cái to lớn phủ đệ xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Phủ đệ đồ sộ, mặc dù so ra kém Chiến sư đường loại này thế lực lớn, nhưng cũng không kém chút nào, bên trong trận pháp dày đặc, còn chưa tới đến trước mặt, liền thấy linh khí bốn phía, chậm rãi hướng trong đó hội tụ, nghiễm nhiên một cái linh khí dư dả tu luyện bảo địa."),t("br"),t("br"),n._v("Cho dù người bình thường, ở tại loại này địa phương, khẳng định tuổi thọ cũng có thể kéo dài."),t("br"),t("br"),n._v("“Đây chính là vương phủ...”"),t("br"),t("br"),n._v("Thanh niên mặc áo đen đi tới trước mặt, cùng canh giữ ở cửa ra vào hộ vệ lặng lẽ nói thứ gì, một cái hộ vệ nhìn Trương Huyền trừng người một cái, đi vào."),t("br"),t("br"),n._v("Thời gian không dài lần nữa đi ra, nhẹ gật đầu."),t("br"),t("br"),n._v("“Mời vào bên trong đi!”"),t("br"),t("br"),n._v("Thanh niên trong ánh mắt lóe lên cùng nhau tàn nhẫn chi ý, chẳng qua rất nhanh che giấu, đi đầu đi vào trong."),t("br"),t("br"),n._v("Trương Huyền giả bộ như không nhìn thấy, mang theo Ngọc Phi Nhi, Lạc Thất Thất, theo ở phía sau, đi vào."),t("br"),t("br"),n._v("Không hổ là phong hào đế quốc đệ nhất vương gia vương phủ, thoạt nhìn, so Hồng Viễn đế quốc hoàng thất đều muốn rộng lớn, đủ loại trân quý khoáng thạch, xây dựng thành đạo đường, nhìn một cái, cho người ta một loại xa hoa vô cùng cảm giác."),t("br"),t("br"),n._v("“Cái này kiến trúc, không chỉ có rất lợi hại trận pháp sư bố cục, xem ra còn có thợ làm vườn tiến hành thiết kế!”"),t("br"),t("br"),n._v("Đi vài bước, Lạc Thất Thất nói."),t("br"),t("br"),n._v("“Thợ làm vườn?” Trương Huyền lần đầu tiên nghe được cái tên này."),t("br"),t("br"),n._v("“Ừm, thợ làm vườn cùng khải linh sư đồng dạng, không thuộc về cửu lưu chức nghiệp, nhưng là rất nhiều đại thế lực, đại gia tộc thượng khách!” Lạc Thất Thất nói."),t("br"),t("br"),n._v("“Ồ? Các ngươi Hồng Viễn đế quốc nhưng có thợ làm vườn?” Trương Huyền nhìn về phía Ngọc Phi Nhi."),t("br"),t("br"),n._v("“Chưa từng nghe qua...” Ngọc Phi Nhi lắc đầu."),t("br"),t("br"),n._v("“Ta là theo một chỗ trong cổ tịch nhìn thấy...”"),t("br"),t("br"),n._v("Lạc Thất Thất cười cười: “Nghe nói loại này thợ làm vườn, có thể căn cứ địa hình, phong thuỷ, hoàn cảnh bốn phía... Cùng tất cả điều kiện, thiết kế nơi ở hoặc là tông môn, bất kể kiến trúc vẫn là trận pháp, cơ quan, có thể hoàn mỹ dung hợp lại cùng nhau, bổ sung lẫn nhau, phát huy ra lực lượng mạnh nhất! Một cái tông môn, hoặc là một cái gia tộc, có thể có được một vị thợ làm vườn tới thiết kế, toàn thể lực phòng ngự, tuyệt đối có thể gia tăng gấp đôi không chỉ!”"),t("br"),t("br"),n._v("“Đương nhiên, loại nghề nghiệp này mười phần thưa thớt, hơn nữa chưa hoàn chỉnh truyền thừa... Ta cũng là xem bố cục của nơi này, không phải là kỳ lạ, cùng bốn phía trận pháp, lẫn nhau cân bằng, mới suy đoán!”"),t("br"),t("br"),n._v("“Quả thực rất phẳng nhận định!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Hắn thân là thất tinh đỉnh phong trận pháp sư, lại thêm nắm giữ Minh Lý Chi Nhãn, Lạc Thất Thất đều có thể nhìn ra, tự nhiên đã sớm nhìn ra."),t("br"),t("br"),n._v("Hồng Viễn đế quốc hoàng cung, hắn cũng đi qua, trận pháp mặc dù rất nhiều, thế nhưng là, hầu như mỗi một cái đều mạnh ai nấy làm, chỉ có đụng vào mới có thể kích hoạt... Mà ở trong đó, rõ ràng nối liền với nhau, phối hợp kiến trúc... Một khi đụng phải một cái, liền sẽ có liên tiếp theo nhau mà đến, để cho người ta khó mà chạy trốn."),t("br"),t("br"),n._v("Nếu như đem Hồng Viễn trong hoàng cung trận pháp, xem như nguyên một đám trưng bày tốt bẫy chuột, chỉ có đụng vào mới có thể có mà thay đổi tĩnh, mà ở trong đó liền là Domino quân bài, chỉ cần đụng phải bất kỳ một cái nào, đều sẽ gây nên phản ứng dây chuyền, để cho người ta mọc cánh khó thoát."),t("br"),t("br"),n._v("Chỉ sợ chân như đối phương nói, cùng thợ làm vườn liên quan đến."),t("br"),t("br"),n._v("Trong lòng phỏng đoán, đang theo tại thanh niên mặc áo đen tiến lên, liền nghe đến chung quanh đột nhiên rầm rầm liên tiếp bước chân, mấy chục cái hộ vệ lao đến."),t("br"),t("br"),n._v("Thời gian nháy mắt liền đem ba người vây quanh ở trung tâm."),t("br"),t("br"),n._v("“Tiểu tử, ngươi không phải mới vừa rất kiêu ngạo ư? Cho ta hảo hảo giáo huấn một lần, cho hắn biết chúng ta Trung Thanh Vương phủ người, không thể đắc tội!”"),t("br"),t("br"),n._v("Vọt đến đám người đằng sau, thanh niên mặc áo đen cười lạnh, vẻ mặt dữ tợn."),t("br"),t("br"),n._v("Hắn đi mời đối phương, vốn cho rằng là cái thoải mái mà việc phải làm, không nghĩ tới, suýt chút nữa bị một chân đạp chết, đối trước mắt người này, sớm đã hận thấu xương, vừa rồi tại cửa ra vào thời điểm, lặng lẽ báo tin, để hộ vệ chuẩn bị xong mai phục."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Nghe được hắn, chung quanh hộ vệ, đồng loạt hướng về phía trước, binh khí trong tay bay phất phới, tản mát ra lăng lệ hàn quang, phối hợp chung quanh trận pháp, trong nháy mắt khí tức như rồng."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1153"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1155"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);