(window.webpackJsonp=window.webpackJsonp||[]).push([[1870],{2225:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-605-huyen-vu-đe-đo"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-605-huyen-vu-đe-đo"}},[n._v("#")]),n._v(" Chương 605: Huyễn Vũ Đế đô")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Từ khi kế thừa vị trí minh chủ bắt đầu, hắn vẫn cố gắng, muốn khai cương khoách thổ, để liên minh càng thêm cường đại, nhưng cũng biết, vạn quốc liên minh nội tình thực sự quá yếu."),t("br"),t("br"),n._v("Thành công khả năng cực kỳ bé nhỏ, thậm chí có thể nói, hoàn toàn không có khả năng."),t("br"),t("br"),n._v("Liền loại này không có khả năng bị Trương sư một bài giảng đánh vỡ!"),t("br"),t("br"),n._v("Hơn triệu người nghe chương trình học, về sau đều chính là quốc gia nhân tài trụ cột, muốn không quật khởi cũng khó khăn!"),t("br"),t("br"),n._v("“Trương sư, cám ơn...”"),t("br"),t("br"),n._v("Hốc mắt đỏ lên, Triệu minh chủ nắm đấm xiết chặt."),t("br"),t("br"),n._v("Là người thanh niên này, mang cho vạn quốc liên minh quật khởi hi vọng!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Trương sư đây là dùng thâm ảo ngôn ngữ dẫn động thiên địa linh khí, cho người tẩy kinh phạt tủy, hắn... Nói thật sự là kiến thức căn bản?”"),t("br"),t("br"),n._v("“Nguyên bản ta cho rằng, kiến thức căn bản, nghiên cứu lại thấu triệt cũng vô dụng, hiện tại xem ra, sai, sai quá bất hợp lí.”"),t("br"),t("br"),n._v("“Kiến thức căn bản là tu luyện căn cơ, xây lâu đồng dạng, chỉ có đánh sâu, lĩnh ngộ sâu, mới có thể đi càng xa...”"),t("br"),t("br"),n._v("“Cái này một bài giảng thật sự là được ích lợi không nhỏ, từ hôm nay trở đi, chúng ta đều cùng Trương sư có nửa sư tình nghĩa, về sau người nào muốn động vạn quốc liên minh, sợ là chúng ta đều là có thể khoanh tay đứng nhìn...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe giảng bài rất nhiều cường giả, trong lòng cũng đồng thời cảm khái."),t("br"),t("br"),n._v("Nghe Trương sư khóa, chẳng khác nào thành hắn nửa cái học sinh, về sau có việc, liền làm không được thờ ơ lãnh đạm."),t("br"),t("br"),n._v("“... Bão thủ nguyên nhất, tâm về tự nhiên, là vì cơ sở! Tốt, bài học hôm nay liền lên đến nơi này.”"),t("br"),t("br"),n._v("Nương theo một cái nhàn nhạt lời nói, trên đài thanh niên, cổ tay khẽ đảo, một chi bút lông xuất hiện, lăng không vẽ tranh, thời gian nháy mắt, một cái to lớn Phượng Hoàng liền xuất hiện ở trước mặt mọi người."),t("br"),t("br"),n._v("Bàn chân tại mặt đất đạp mạnh, thanh niên lăng không bay lên, ngồi tại Phượng Hoàng trên lưng, bay lượn mà đi."),t("br"),t("br"),n._v("Sách sử ghi chép."),t("br"),t("br"),n._v("Vạn quốc liên minh thứ ba ngàn ba trăm bảy mươi hai năm, đông."),t("br"),t("br"),n._v("Trương sư giảng bài Vạn Quốc thành, trăm vạn tu sĩ đều tụ, nói tụ linh khí, mười dặm chân không."),t("br"),t("br"),n._v("Khóa xong, đạp tranh mà đi."),t("br"),t("br"),n._v("Trăm vạn tu sĩ quỳ gối, miệng hô lão sư."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Bầu trời xanh thẳm, mây trắng trôi nổi, vài đầu to lớn linh thú phi hành đi ngang qua mà qua, dọa đến rất nhiều man thú, chim tước bay loạn."),t("br"),t("br"),n._v("Linh thú sống lưng bên trên, ngồi xếp bằng Trương Huyền mở mắt."),t("br"),t("br"),n._v("Ngày đó giảng xong khóa phía sau, trực tiếp cùng Hồng sư đám người rời đi, đến bây giờ đã tiếp cận một tháng."),t("br"),t("br"),n._v("Thời gian dài như vậy, hầu như mỗi ngày đều ở trên trời vượt qua, cả người đều cảm thấy có chút mệt mỏi."),t("br"),t("br"),n._v("Bởi vì đi thời gian tương đối dài, mỗi cái danh sư đều mang theo người nhà, người hầu các loại, Hồng sư dự định ròng rã mười đầu linh thú phi hành mới đủ đủ ngồi xuống."),t("br"),t("br"),n._v("Nhiều như vậy linh thú, không có một cái thấp hơn Hóa Phàm tứ trọng, đi đến bất kỳ địa phương nào đều gây nên một hồi oanh động, rất nhiều người vì đó sợ hãi."),t("br"),t("br"),n._v("Đi đường trong khoảng thời gian này, Trương Huyền một bên đọc sách một bên tu luyện, chẳng những đem Hóa Phàm tứ trọng đỉnh phong tu vi hoàn toàn củng cố, còn để hồn lực một lần đột phá Hóa Phàm ngũ trọng, đạt đến đỉnh phong."),t("br"),t("br"),n._v("Hồn lực Hóa Phàm ngũ trọng, lực lượng 3 20 vạn đỉnh, lại thêm chân khí 200 vạn đỉnh, nói cách khác, cả người có thể trong nháy mắt bộc phát 5 20 vạn đỉnh lực lượng, so với Hóa Phàm lục trọng Kiều Thiên cảnh trung kỳ đều chỉ mạnh không yếu."),t("br"),t("br"),n._v("Hóa Phàm lục trọng Kiều Thiên cảnh sơ kỳ 400 vạn, trung kỳ 4 80 vạn, hậu kỳ 5 60 vạn, đỉnh phong 6 20 vạn đỉnh!"),t("br"),t("br"),n._v("5 20 vạn đỉnh, đã tiếp cận hậu kỳ, chỉ sợ chân chính chiến đấu, Hồng sư muốn vượt qua cũng không quá dễ dàng."),t("br"),t("br"),n._v("Tứ tinh danh sư, tu vi cấp bậc vì Hóa Phàm nhị trọng đến tứ trọng."),t("br"),t("br"),n._v("Ngũ trọng cùng Hợp Linh cảnh vì nửa bước ngũ tinh."),t("br"),t("br"),n._v("Chỉ có đạt tới Hóa Phàm lục trọng Kiều Thiên cảnh, mới có tư cách tấn cấp ngũ tinh danh sư."),t("br"),t("br"),n._v("Hồng sư vừa mới tấn cấp ngũ tinh danh sư không lâu, nói cách khác, thực lực chân thật tại Kiều Thiên cảnh sơ kỳ, nếu như không mượn cảnh giới, chỉ bằng vào lực lượng, chỉ sợ liền hắn một quyền đều không tiếp nổi."),t("br"),t("br"),n._v("Mới trở thành tứ tinh danh sư hai cái tháng sau, liền nắm giữ có thể so với ngũ tinh sơ kỳ danh sư thực lực, chỉ sợ cũng liền hắn tên biến thái này mới có loại năng lực này."),t("br"),t("br"),n._v("Một tháng thời gian, không chỉ hắn thực lực có to lớn bay vọt, ngay cả Trịnh Dương, Vương Dĩnh mấy người cũng tiến bộ rất lớn."),t("br"),t("br"),n._v("Triệu Nhã cùng Lộ Xung rời đi, để bọn hắn càng thêm trân quý cùng Trương sư chung đụng thời gian, mỗi ngày đều cố gắng tu luyện, tại đại lượng linh thạch cung cấp bên dưới, thực lực mỗi ngày đều có cái này biến hóa kinh người."),t("br"),t("br"),n._v("Bốn người tuần tự đột phá Hóa Phàm cảnh, đã vững chắc tại nhị trọng Nguyên Khí cảnh."),t("br"),t("br"),n._v("Không chỉ có như thế, Trương Huyền lần nữa sửa sang lại Thiên Đạo võ kỹ, truyền cho mọi người để hắn sức chiến đấu cũng gia tăng thật lớn."),t("br"),t("br"),n._v("Muốn nói tiến bộ chậm nhất, chỉ sợ phải kể tới quản gia Tôn Cường."),t("br"),t("br"),n._v("Cái tên này đối tu luyện không có chút nào hứng thú, một không chú ý liền bắt đầu lười biếng, nếu không phải Vương Dĩnh chuyên môn giám sát, muốn tiến bộ, gần như không có khả năng."),t("br"),t("br"),n._v("Bất quá, một tháng vây ở thú trên lưng không cách nào rời đi, chỉ có thể không ngừng tu luyện, một tháng thời gian, cũng cứ thế mà từ Hóa Phàm nhất trọng Tăng Thọ cảnh sơ kỳ đạt đến nhị trọng Nguyên Khí cảnh đỉnh phong."),t("br"),t("br"),n._v("So với tu vi cao nhất Vương Dĩnh, đều thậm chí lợi hại hơn một chút."),t("br"),t("br"),n._v("Có thể nhanh như vậy tiến bộ, cũng không phải tốc độ tu luyện của hắn nhanh, thiên phú vượt xa mọi người, mà là hắn thôn phệ Mặc Hồn Sinh một bộ phận linh hồn, người sau đối tu vi lĩnh ngộ liền một cách tự nhiên chuyển dời đến hắn trên người, có nhiều như vậy kinh nghiệm, lại thêm Trương Huyền chỉ điểm cùng linh thạch đầy đủ cung cấp, chỉ đề thăng một cái cấp bậc xem như rất chậm."),t("br"),n._v(" Mọi người tu vi tiến bộ, thật đáng mừng, nhưng Trương Huyền lúc này lại cao hứng không nổi."),t("br"),t("br"),n._v("Bởi vì... Từ Lưu Vân tông rất nhiều tông môn, cướp đoạt tới hơn sáu trăm viên linh thạch trung phẩm đã tiêu hao hầu như không còn, tiêu hao sạch sẽ."),t("br"),t("br"),n._v("Trịnh Dương, Tôn Cường đám người dùng cũng không nhiều, chủ yếu là hắn tiêu phí đầu to."),t("br"),t("br"),n._v("Lúc trước thúc Cửu Thiên Liên Thai, liền tiêu hao không dưới hai, 300 viên, Lộ Xung rời đi lại cho một bộ phận, lại thêm hồn lực đột phá Hợp Linh cảnh, tiêu hao cũng thành gia tăng gấp bội..."),t("br"),t("br"),n._v("Hơn sáu trăm viên linh thạch sống đến bây giờ đã coi như là không tệ."),t("br"),t("br"),n._v("“Về sau muốn tiếp tục tấn cấp, chỉ sợ chỉ có thể vận dụng viên kia linh thạch thượng phẩm...”"),t("br"),t("br"),n._v("Xoa xoa mi tâm, Trương Huyền trầm tư."),t("br"),t("br"),n._v("Danh sư thi đấu quán quân lấy được ban thưởng linh thạch thượng phẩm, hắn còn không có cam lòng dùng, thậm chí liền cầm đều không có lấy ra, trên người bây giờ linh thạch trung phẩm sử dụng hầu như không còn, cũng nên đem thứ này lấy ra sử dụng."),t("br"),t("br"),n._v("“Trương sư, Huyễn Vũ đế quốc Huyễn Vũ Đế đô lập tức đến!”"),t("br"),t("br"),n._v("Đang muốn đem linh thạch lấy ra nhìn xem, chỉ thấy Hồng sư đi tới."),t("br"),t("br"),n._v("Hồng sư không cùng những người khác cùng một chỗ, mà là lựa chọn cùng hắn cưỡi một đầu linh thú, trên đường đi Trương Huyền cũng giúp hắn giải quyết một ít trên việc tu luyện nan đề, xem như hồi báo lúc trước phá hoại bảo vật bồi thường."),t("br"),t("br"),n._v("Còn vì cái gì hắn sẽ nhiều như thế, tự nhiên là lão sư chỉ điểm."),t("br"),t("br"),n._v("Dù sao đối phương đối với hắn “Lão sư” tin tưởng không nghi ngờ, nhiều mượn dùng một ít, cũng không quan trọng."),t("br"),t("br"),n._v("Lấy được hắn chỉ điểm, Hồng sư quả thực tiến bộ rất lớn, trong lòng cũng âm thầm may mắn, đối vị này so với hắn thực lực thấp danh sư, càng thêm tôn trọng."),t("br"),t("br"),n._v("“Đế đô?”"),t("br"),t("br"),n._v("Trương Huyền đứng dậy đến phía trước cửa sổ, quả nhiên thấy một cái thành phố khổng lồ xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Đen như mực tường thành khảm nạm ở trên mặt đất, như là một cái hùng hồn áo giáp, rất nhiều xinh đẹp cao ngất kiến trúc, đứng sừng sững ở phía chân trời xa xôi, từ xa nhìn lại hình như từng dãy uốn lượn núi non chập chùng, ghi chép tuyên cổ lịch sử cùng tang thương."),t("br"),t("br"),n._v("“Huyễn Vũ đế quốc, có hơn một vạn năm lịch sử, đời thứ nhất quốc chủ kêu Diệp Huyễn Vũ, dẫn đầu bốn cái huynh đệ, chinh chiến nam bắc, lúc này mới xông ra cái này lớn như vậy giang sơn!”"),t("br"),t("br"),n._v("Hồng sư cũng nhìn xuống dưới, trong mắt mang theo ngưng trọng cùng bội phục: “Đế đô đông gần Thanh Tắc sơn, tây tiếp Long Lân hà, từ xa nhìn lại, như là một cái nằm dưới đất người khổng lồ.”"),t("br"),t("br"),n._v("“Ừm!” Trương Huyền cúi đầu nhìn lại."),t("br"),t("br"),n._v("Đối phương nói không sai, cái này Đế đô quả thực mười phần hùng vĩ, vượt ngang mấy trăm dặm, tựa như một cái pháo đài to lớn, cho người ta một loại không thể phá vỡ cảm giác."),t("br"),t("br"),n._v("Mặc dù không ẩn chứa trận pháp, nhưng dung hợp tự nhiên, cho người ta một loại sạch sẽ, chỉnh tề, không cách nào phản kháng cảm giác."),t("br"),t("br"),n._v("“Huyễn Vũ đế quốc Danh Sư đường đường chủ, Vệ Giang, là lão sư của ta, là một vị ngũ tinh đỉnh phong danh sư, Hóa Phàm bát trọng cường giả đỉnh phong. Quốc chủ, Diệp Vấn Thiên bệ hạ, thực lực đạt đến Hóa Phàm bát trọng, một thân tu vi sâu không lường được.”"),t("br"),t("br"),n._v("Hồng sư giới thiệu."),t("br"),t("br"),n._v("“Ngũ tinh đỉnh phong danh sư? Hóa Phàm bát trọng?”"),t("br"),t("br"),n._v("Trương Huyền khóe miệng giật một cái."),t("br"),t("br"),n._v("Hóa Phàm ngũ trọng Hợp Linh cảnh, đạt tới loại cảnh giới này, linh hồn cùng thân thể hợp nhất, hồn lực tăng cường, cả người đối tu luyện lý giải cùng kiến thức đều sẽ gia tăng thật lớn, lực lượng đi vào một cái cao tốc gia tăng giai đoạn."),t("br"),t("br"),n._v("Đột phá loại cảnh giới này chính là Kiều Thiên cảnh."),t("br"),t("br"),n._v("Kiều Thiên cảnh, câu thông thiên địa chi kiều, con người cùng tự nhiên sinh ra đặc thù nào đó ăn ý, coi như không cần tu luyện, linh khí đều sẽ theo cầu nối, không ngừng tẩm bổ thân thể, để thực lực của hắn, không ngừng tăng lên."),t("br"),t("br"),n._v("Loại cảnh giới này, bất kể chân khí vẫn là linh hồn, đều sẽ gấp bay mãnh liệt giương, đạt tới một loại làm cho người trình độ đáng sợ, chính thức có được di sơn đảo hải năng lực."),t("br"),t("br"),n._v("Đột phá tầng này, chính là Hóa Phàm thất trọng, Quy Nhất cảnh."),t("br"),t("br"),n._v("Quy Nhất cảnh, vạn pháp quy nhất, lực lượng càng thêm chất phác, tinh khiết, càng thêm phù hợp tự nhiên, hồn lực cũng sẽ trở nên càng thêm cường đại."),t("br"),t("br"),n._v("Về sau mới là võ giả bát trọng Đạp Hư cảnh, đạt tới loại thực lực này, không chỉ chân khí cùng linh hồn có to lớn bay vọt, càng quan trọng hơn là, đã có thể khống chế một bộ phận tự nhiên quy tắc, có thể đạp không mà đi."),t("br"),t("br"),n._v("Nói cách khác... Đã có thể trên không trung thẳng từ trên xuống dưới, chỉ bất quá, bởi vì rất khó vượt ngang, không tính là phi hành mà thôi."),t("br"),t("br"),n._v("Huyễn Vũ đế quốc Danh Sư đường đường chủ, quốc chủ, rõ ràng đều là loại cao thủ cấp bậc này, suy nghĩ một chút đều cảm thấy đáng sợ."),t("br"),t("br"),n._v("Hắn gần nhất coi như thực lực tiến bộ rất nhiều, cùng loại này siêu cấp cường giả so, vẫn là kém nhiều lắm, hoàn toàn không thể so sánh."),t("br"),t("br"),n._v("Đương nhiên, nắm giữ Hồng Trần Đạp Thiên bộ, nếu như đánh không lại thật muốn chạy trốn, coi như loại thực lực này cường giả, cũng rất khó đuổi theo."),t("br"),t("br"),n._v("“Chúng ta hôm nay đến tin tức, đã báo lên Danh Sư đường, có lẽ rất nhanh liền có người tới nghênh đón...”"),t("br"),t("br"),n._v("Giới thiệu xong, Huyễn Vũ đế quốc quốc chủ cùng Danh Sư đường đường chủ, Hồng sư cười cười."),t("br"),t("br"),n._v("28 chỗ thế lực danh sư thi đấu, mặc dù tại toàn bộ Huyễn Vũ đế quốc, không tính là quá thu hút, nhưng cũng có nhất định địa vị, huống chi còn là một vị ngũ tinh danh sư tự mình chủ trì."),t("br"),t("br"),n._v("Thu thu thu!"),t("br"),t("br"),n._v("Hống hống hống!"),t("br"),t("br"),n._v("Vừa dứt lời, liền nghe đến liên tiếp gầm rú, mười mấy đầu to lớn linh thú phi hành, thẳng tắp bay tới."),t("br"),t("br"),n._v("Những này linh thú phi hành cái đầu cũng không lớn, mỗi một cái phía trên đều đứng đấy một thân ảnh, mười mấy đầu linh thú, mang theo mười mấy người bay tới."),t("br"),t("br"),n._v("“Tốt, nghênh tiếp tới, một lát nữa cùng bọn hắn liền có thể trực tiếp đi vào hoàng cung...”"),t("br"),t("br"),n._v("Mỉm cười, Hồng sư đang muốn nói chuyện, liền nghe đến phía trước linh thú phía trên, một cái thanh âm nhàn nhạt vang lên lên."),t("br"),t("br"),n._v("“Hồng Thiển, đã lâu không gặp... Bất quá, lần này ngươi tới không phải lúc, vẫn là trở về đi!”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-613"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-615"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);