(window.webpackJsonp=window.webpackJsonp||[]).push([[1871],{2226:function(n,h,g){"use strict";g.r(h);var t=g(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-606-ngan-can"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-606-ngan-can"}},[n._v("#")]),n._v(" Chương 606: Ngăn cản")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("br"),g("br"),n._v("Không phải lúc?"),g("br"),g("br"),n._v("Trở về?"),g("br"),g("br"),n._v("Trương Huyền nhướng mày."),g("br"),g("br"),n._v("Cái này dường như không phải nghênh đón, mà là khiêu khích đi!"),g("br"),g("br"),n._v("Nghênh tiếp lời nói, làm sao có thể trực tiếp để cho người ta trở về?"),g("br"),g("br"),n._v("Cái khác linh thú trên lưng danh sư, cũng đều nhô đầu ra, muốn nhìn một chút, rốt cuộc chuyện gì xảy ra."),g("br"),g("br"),n._v("Là ai có lá gan lớn như vậy, ngay trước Hồng sư mặt dám nói như vậy."),g("br"),g("br"),n._v("“Phùng Vũ?”"),g("br"),g("br"),n._v("Nghe nói như thế, Hồng sư nhướng mày."),g("br"),g("br"),n._v("“Không sai?”"),g("br"),g("br"),n._v("Đi đầu linh thú bên trên người nhẹ nhàng cười một tiếng."),g("br"),g("br"),n._v("Lúc này, hai bên bay tới gần, đại khái cách nhau mấy trăm mét, đã có thể thấy rõ, là cái hơn năm mươi tuổi lão giả, người mặc trường bào màu vàng kim nhạt, ba sợi màu nâu xanh chòm râu theo gió phiêu lãng, bộ dáng rất có tiên phong đạo cốt."),g("br"),g("br"),n._v("“Tại hạ phụng Danh Sư đường cùng Huyễn Vũ đế quốc mời, chuyên đến đây, đương nhiên sẽ không trở về, nếu như Phùng huynh muốn về mà nói, tại hạ ngược lại là có thể đưa lên đoạn đường!”"),g("br"),g("br"),n._v("Hồng sư mở miệng."),g("br"),g("br"),n._v("Nói xong, thấp giọng giới thiệu."),g("br"),g("br"),n._v("“Cái này Phùng Vũ là Phong Nguyên đế quốc Danh Sư đường đường chủ, cùng ta tưởng tượng không hợp nhau. Chúng ta Hồng Phong đế quốc phía dưới 28 chỗ thế lực tiến hành thi đấu, bọn họ cũng tiến hành...”"),g("br"),g("br"),n._v("“Phong Nguyên đế quốc? Tứ đại chư hầu quốc Phong Nguyên đế quốc?”"),g("br"),g("br"),n._v("Trương Huyền gật đầu."),g("br"),g("br"),n._v("Trên đường tới, mặc dù tu luyện tương đối nhiều, nhưng có đôi khi cũng cùng vị này ngũ tinh danh sư hỏi thăm một ít chuyện, biết Huyễn Vũ đế quốc phân cấp."),g("br"),g("br"),n._v("Huyễn Vũ đế quốc là cái nhị đẳng đế quốc, phía dưới chia làm bốn cái các nước chư hầu, theo thứ tự là: Hồng Phong đế quốc, Phong Nguyên đế quốc, Tiềm Phong đế quốc, Trục Nguyệt đế quốc."),g("br"),g("br"),n._v("Cái này bốn cái quốc gia là năm đó kiến quốc Hoàng đế Diệp Huyễn Vũ, phân đất phong hầu cho cùng một chỗ giành chính quyền bốn cái huynh đệ, mệnh danh cùng Huyễn Vũ đế quốc bình khởi bình tọa, trên thực tế nhưng thấp một cái tiểu cấp bậc."),g("br"),g("br"),g("span",[g("br"),n._v("//tr"),g("br"),n._v("uyencuatui.net/"),g("br")]),n._v(" Bốn cái các nước chư hầu phân biệt nắm trong tay rất nhiều tiểu quốc đo cùng thế lực, thật giống như Hồng sư vị trí Hồng Phong đế quốc, nắm trong tay 28 chỗ thế lực, chính là vạn quốc liên minh, Lưu Vân tông các loại chỗ."),g("br"),g("br"),n._v("Cái khác ba cái các nước chư hầu, cũng không kém bao nhiêu, đều có nhất định phạm vi thế lực cùng quốc gia phân chia."),g("br"),g("br"),n._v("Nếu như dùng kiếp trước đẳng cấp cân nhắc, Huyễn Vũ đế quốc là hoàn chỉnh quốc gia, hồng phong, phong nguyên những này các nước chư hầu chính là hành tỉnh, 28 chỗ thế lực là huyện thị, Hiên Viên phong hào vương quốc thì là hương trấn, nhất đẳng vương quốc, nhị đẳng vương quốc những này liền cùng nông thôn, đại đội, đơn nguyên một cái cấp bậc."),g("br"),g("br"),n._v("Các nước chư hầu khống chế rất nhiều thế lực, cùng Hồng Phong đế quốc đồng dạng, cũng tiến hành danh sư thi đấu, tự nhiên cũng có đi vào Hóa Thanh trì danh ngạch cùng cơ hội."),g("br"),g("br"),n._v("“Ừm! Ta tưởng rằng Huyễn Vũ đế quốc người của hoàng thất, tới đón ta bọn họ, không nghĩ tới bọn họ lấy được trước tin tức. Chúng ta là ngang cấp Danh Sư đường, ở giữa khó tránh khỏi lẫn nhau đọ sức, trực tiếp ngăn chặn, chỉ sợ kẻ đến không thiện.”"),g("br"),g("br"),n._v("Hồng sư mày nhăn lại."),g("br"),g("br"),n._v("Trương Huyền gật đầu."),g("br"),g("br"),n._v("Đối phương nói chuyện không có chút nào khách khí, khẳng định không phải nói chuyện phiếm."),g("br"),g("br"),n._v("Quả nhiên, hai người đối thoại công phu, đối diện Phùng Vũ danh sư, lông mày giương lên, thanh âm truyền đến: “Hảo tâm nói cho ngươi, không phải không nghe! Hồng Phong đế quốc đế quốc cách nơi này xa nhất, ngươi tới muộn, có thể có chút sự tình cũng không rõ ràng, vậy ta liền cùng ngươi nói một chút. Năm nay đi vào Hóa Thanh trì danh ngạch mặc dù không thay đổi, nhưng hoàng thất Diệp Tiền thái tử, nhiều muốn ba cái, chúng ta tự nhiên cũng ít đi. Nói cách khác, các ngươi... Đã không có chỗ trống, hiện tại vẫn là trở về đi, nếu không đi qua cũng là tự chuốc nhục nhã!”"),g("br"),g("br"),n._v("“Nhiều muốn ba cái danh ngạch?” Hồng sư nhướng mày."),g("br"),g("br"),n._v("Hóa Thanh trì cùng Âm Dương trì đồng dạng, danh ngạch là có hạn chế, nhưng cơ bản đều là phân tốt, làm sao có thể tới chậm, liền không có vị trí, đường đường thái tử cướp đoạt các nước chư hầu danh ngạch?"),g("br"),g("br"),n._v("“Không sai, ta nhìn các ngươi vẫn là đi trước Hồng Viễn danh sư học viện đưa tin đi, dù sao tiếp tục lưu lại đã không có ý nghĩa!”"),g("br"),g("br"),n._v("Phùng Vũ nhẹ nhàng cười một tiếng."),g("br"),g("br"),n._v("“Có ý nghĩa hay không, không phải ngươi nói tính toán, cũng không nhọc đến Phùng sư phí tâm!”"),g("br"),g("br"),n._v("Biết đối phương ngăn lại, khẳng định có toan tính mưu, chẳng muốn cùng hắn nét bút, Hồng sư khoát tay chặn lại, xoay người phân phó: “Khống chế tốt linh thú, chúng ta đi trước Danh Sư đường.”"),g("br"),g("br"),n._v("Những này linh thú phi hành đều là thuê, do Thú đường người khống chế, có thể nghe theo phân phó, kết đội phi hành."),g("br"),g("br"),n._v("Rầm rầm!"),g("br"),g("br"),n._v("Mệnh lệnh một chút, tại thuần thú sư khống chế bên dưới, rất nhiều linh thú xếp thành một hàng, bất cứ lúc nào cũng sẽ giương cánh bay cao."),g("br"),g("br"),n._v("“Hồng Thiển, ngươi làm như vậy liền không có ý nghĩa, ta hảo tâm tới nói cho, không lĩnh tình ngược lại cũng thôi, xoay người rời đi, có ý tứ gì?”"),g("br"),g("br"),n._v("Phùng Vũ lông mày nhíu một cái."),g("br"),g("br"),n._v("“Danh ngạch có đủ hay không, ta tự sẽ đến hỏi lão sư cùng bệ hạ!”"),g("br"),g("br"),n._v("Hồng sư khoát tay chặn lại."),g("br"),g("br"),n._v("Soạt!"),g("br"),g("br"),n._v("Dưới thân linh thú phi hành, đồng thời giương cánh, liền muốn vượt qua đối phương, bay về phía trời cao."),g("br"),g("br"),n._v("“Rượu mời không uống chỉ thích uống rượu phạt, Trần Mặc!”"),g("br"),g("br"),n._v("Thấy hắn không nghe bản thân khuyên can, chuyên tâm muốn đi, Phùng Vũ tròng mắt hơi híp, quay đầu kêu một tiếng."),g("br"),g("br"),n._v("“Vâng!”"),g("br"),g("br"),n._v("Phía sau một thanh niên lên tiếng, cổ tay khẽ đảo, một cái cổ cầm xuất hiện tại lòng bàn tay, nhẹ nhàng đẩy, thanh âm vang lên."),g("br"),g("br"),n._v("Leng keng, leng keng!"),g("br"),g("br"),n._v("Tiếng đàn chợt cao chợt thấp, vang vọng trên không trung, truyền vào trong tai cho người ta một loại nhẵn nhụi cảm giác, mười phần nhịn nghe."),g("br"),g("br"),n._v("“Đánh đàn làm gì?”"),g("br"),g("br"),n._v("“Không biết...”"),g("br"),g("br"),n._v("Nghe được tiếng đàn bên trong, chẳng những không có ý sát phạt, ngược lại mang theo rả rích thanh âm, nghe vô cùng thoải mái, mọi người một hồi kỳ lạ."),g("br"),g("br"),n._v("Không trung chặn đường, khẳng định có ý nghĩ của hắn, hiện tại muốn rời khỏi, nhưng đánh một cái không đau không ngứa tiếng đàn, làm gì?"),g("br"),g("br"),n._v("Mặc dù có Ma âm sư cái nghề nghiệp này, có thể lợi dụng sóng âm đối với người tiến hành công kích, nhưng này cũng là công kích linh hồn, để cho người ta sinh ra huyễn cảnh, cái này âm nhạc gió êm sóng lặng, muốn cho bọn họ bị thương tổn... Không thể nào!"),g("br"),g("br"),n._v("Càng quan trọng hơn là... Bọn họ đám người này mặc dù không phải rất mạnh, nhưng cũng đều là tứ tinh đỉnh phong danh sư, càng có một vị ngũ tinh, phải có bao nhiêu mạnh lực công kích mới có thể để cho tất cả mọi người chịu ảnh hưởng?"),g("br"),n._v("“Không đúng, không phải công kích chúng ta, là công kích linh thú!”"),g("br"),g("br"),n._v("Hồng sư đột nhiên kịp phản ứng, sầm mặt lại."),g("br"),g("br"),n._v("“Linh thú?”"),g("br"),g("br"),n._v("Mọi người sững sờ, vội vàng hướng phụ trách khống chế thuần thú sư nhìn lại, quả nhiên thấy bọn họ từng cái sắc mặt tái xanh."),g("br"),g("br"),n._v("“Phản ứng không chậm ah, không sai, Trần Mặc cái này đầu , quả thực có thể thôi miên linh thú, để hắn không cách nào nhẹ tiến lên! Nói cách khác, chỉ cần bên này âm nhạc không ngừng, các ngươi liền không cách nào tiến lên...”"),g("br"),g("br"),n._v("Phùng Vũ nhẹ nhàng cười một tiếng."),g("br"),g("br"),n._v("“Thiên âm tịch tuyệt?”"),g("br"),g("br"),n._v("Hồng sư muốn khởi động dưới thân linh thú, quả nhiên cảm thấy linh thú cùng mình đã mất đi cảm ứng, chỉ có thể ở không trung lơ lửng, muốn tiến lên, căn bản làm không được."),g("br"),g("br"),n._v("Giống như bị giam cầm tại lồng bên trong, lại không cách nào đi tới."),g("br"),g("br"),n._v("Không thể tiến lên, tự nhiên cũng là không đến được Đế đô, chớ nói chi là đi vào hoàng thành."),g("br"),g("br"),n._v("“Phùng Vũ, ngươi rốt cuộc muốn làm gì?”"),g("br"),g("br"),n._v("Vẻ mặt khó coi, Hồng sư lạnh lùng nhìn sang."),g("br"),g("br"),n._v("“Không làm gì, chính là lưu chư vị ở chỗ này chờ bên trên một ngày!”"),g("br"),g("br"),n._v("Phùng Vũ nhẹ nhàng cười một tiếng, quay đầu dặn dò: “Trần Mặc, một mực tại nơi này đánh đàn, để bọn hắn không có cách nào tiến lên!”"),g("br"),g("br"),n._v("“Vâng!” Gọi là Trần Mặc thanh niên lên tiếng, bàn tay múa, tiếng đàn lanh lảnh."),g("br"),g("br"),n._v("“Hừ, ngươi chẳng lẽ cho là ta không có biện pháp?”"),g("br"),g("br"),n._v("Không nghĩ tới đối phương mềm không được, tới cứng, trực tiếp không cho bọn họ tiến lên, Hồng sư khí lông mày giơ lên, cũng ngón tay làm kiếm, nhẹ nhàng bắn ra."),g("br"),g("br"),n._v("Ô ô!"),g("br"),g("br"),n._v("Một cái chói tai thanh âm, một đạo kiếm khí tự đầu ngón tay bắn ra ngoài, thẳng tắp đâm về Trần Mặc trong tay cổ cầm."),g("br"),g("br"),n._v("Chỉ cần đem đàn đánh nát, không có thanh âm, linh thú ràng buộc tự nhiên cũng là giải trừ."),g("br"),g("br"),n._v("Bất quá, kiếm khí còn chưa tới đến trước mặt, liền nghe đến “Ông!” Một tiếng, không khí xao động, Trần Mặc phía trước xuất hiện một cái to lớn màng bảo hộ, cho dù Hồng sư công kích, đều không thể bài trừ."),g("br"),g("br"),n._v("“Là trận pháp, mười mấy đầu linh thú bên trên người, liên hợp cùng một chỗ, bố trí một cái phòng ngự trận, chuyên môn bảo hộ vị này Trần Mặc, chúng ta khoảng cách xa như vậy, có trận pháp này thủ hộ, căn bản công kích không đến!”"),g("br"),g("br"),n._v("Trương Huyền lắc đầu."),g("br"),g("br"),n._v("Vừa rồi liền kỳ lạ, đối phương mang nhiều người như vậy làm gì, đều là danh sư, không có khả năng thật đánh nhau, nguyên lai mục đích ở đây."),g("br"),g("br"),n._v("Những người này cũng đều là trận pháp sư, trên người mang theo đồ vật gì, lấy linh thú vì trận kỳ, tại vị này Trần Mặc chung quanh bố trí một cái phòng ngự tính trận pháp."),g("br"),g("br"),n._v("Nếu như khoảng cách gần, đừng nói Hồng sư, coi như hắn đều có thể một quyền đánh tan, nhưng chênh lệch khoảng cách mấy trăm mét, lực công kích bản thân liền yếu đi, đối phương lại có sớm chuẩn bị, muốn đánh trúng, ngăn cản đối phương đánh đàn, chỉ sợ rất khó."),g("br"),g("br"),n._v("Ma âm sư chỗ đáng sợ ngay tại đây."),g("br"),g("br"),n._v("Chỉ cần thanh âm có thể truyền tới địa phương, liền có thể sinh ra công kích, bỏ qua hết thảy vật lý ngăn cản."),g("br"),g("br"),n._v("Đối phương khẳng định cũng là đoán chắc điểm ấy, mới có ỷ lại không sợ gì."),g("br"),g("br"),n._v("“Đáng giận...”"),g("br"),g("br"),n._v("Hồng sư răng cắn chặt."),g("br"),g("br"),n._v("Trương Huyền có thể nhìn ra, hắn tự nhiên cũng có thể suy nghĩ ra."),g("br"),g("br"),n._v("Bị đối phương vây ở chỗ này, cách xa mặt đất chí ít hơn ngàn mét khoảng cách, không thể đi xuống, cũng đi không được, thật phiền phức lớn."),g("br"),g("br"),n._v("“Hắn nói danh ngạch có thể là thật, đoán chừng hôm nay liền có thể ra kết quả, vây khốn ta bọn họ một ngày, chỉ cần không qua được, liền vô cùng có khả năng không chiếm được... Hắn có lẽ đánh cái chủ ý này!”"),g("br"),g("br"),n._v("Hồng sư nói."),g("br"),g("br"),n._v("“Đúng vậy a!” Trương Huyền gật đầu."),g("br"),g("br"),n._v("Sự tình rất dễ dàng đoán, danh sư lại có thể quan sát tỉ mỉ, đối phương đều biểu hiện rõ ràng như vậy, lại nhìn không ra, liền có thể đập đầu chết."),g("br"),g("br"),n._v("“Từ không trung nhìn Đế đô rất gần, nhưng đi bộ mà nói, không có nửa ngày thời gian, không đến được, lại nói... Chúng ta hiện tại thân ở không trung, linh thú không cách nào phi hành, chẳng khác nào vây ở nơi này, không thể đi xuống, lên không nổi... Cái này nên làm cái gì?”"),g("br"),g("br"),n._v("Hồng sư tràn đầy gấp gáp."),g("br"),g("br"),n._v("Thật muốn bởi vì như vậy mất đi danh ngạch, coi như sau đó tìm đối phương phiền phức cũng vô ích."),g("br"),g("br"),n._v("Nhưng bây giờ... Bị nhốt không trung, muốn ngăn cản đối phương đánh đàn cũng làm không được, chẳng lẽ thật muốn trơ mắt nhìn lấy danh ngạch chạy đi?"),g("br"),g("br"),n._v("“Kỳ thật muốn giải quyết, cũng rất đơn giản!” Trương Huyền cười cười."),g("br"),g("br"),n._v("“Đơn giản? Làm sao đơn giản?” Hồng sư sững sờ: “Khoảng cách xa như vậy, công kích không đến, chúng ta lại không thể bay qua...”"),g("br"),g("br"),n._v("“Bay qua? Quá phiền toái...” Trương Huyền lắc đầu, quay đầu phân phó: “Chư vị, đều đem để cho mình linh thú đóng lại lỗ tai.”"),g("br"),g("br"),n._v("“Tốt!”"),g("br"),g("br"),n._v("Nghe được hắn nói chuyện, mọi người sửng sốt một chút, vẫn gật đầu."),g("br"),g("br"),n._v("Đối với vị danh sư này thi đấu thứ nhất, bọn họ đều vẫn là tương đối tin tưởng."),g("br"),g("br"),n._v("Rất nhanh, rất nhiều linh thú, đều nhắm lại lỗ tai."),g("br"),g("br"),n._v("Làm xong dự định, Trương Huyền nhẹ nhàng cười một tiếng, nhìn về phía trước."),g("br"),g("br"),n._v("“Đơn giản? Tuổi không lớn lắm, khẩu khí cũng không nhỏ, Hồng sư, đây chính là học sinh của ngươi? Làm sao càng ngày càng lui bước, không hiểu quy củ, trưởng bối nói chuyện, nào có một tiểu nhân vật xen vào phần...”"),g("br"),g("br"),n._v("Đem hai người đối thoại nghe vào trong tai, Phùng Vũ cười lạnh một tiếng, đang muốn quát lớn vài câu, chỉ thấy đối diện thanh niên, miệng há mở, một cái thanh âm nhàn nhạt vang lên."),g("br"),g("br"),n._v("“Hồng!”"),g("br"),g("br"),n._v("Hô! Hô! Hô! Hô!"),g("br"),g("br"),n._v("Còn không có kịp phản ứng, Phùng Vũ đám người dưới thân linh thú, liền trực tiếp làm tự do rơi xuống đất, thẳng tắp hướng phía dưới rơi xuống."),g("br"),g("br"),n._v("“Bọn ta, làm sao cái tình huống, ah...”"),g("br"),g("br"),n._v("(Nàng dâu chưa từng nhìn tiểu thuyết mạng, trước mấy ngày để nàng nhìn quyển sách này, hôm nay hỏi cảm tưởng làm sao, nàng nói: Trong cảm giác Trương Huyền giống như ngươi. Ta đại hỉ: Chẳng lẽ ta cũng cùng nhân vật chính đồng dạng, sư đạo như núi, uy nghiêm như biển, thông minh oai hùng, không hướng vô cùng? Nàng dâu lắc đầu mặt đồng tình nhìn qua: Không phải, là ngươi giống như hắn tiện. Ta:"),g("br"),g("br"),n._v("Hiện tại khóc rống bên trong, cầu nguyệt phiếu an ủi, cũng chỉ có nguyệt phiếu mới có thể an ủi tâm linh của ta bị thương. Ô ô!)"),g("br"),g("br"),n._v("Convert by: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-614"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-616"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);