(window.webpackJsonp=window.webpackJsonp||[]).push([[1874],{2230:function(n,h,i){"use strict";i.r(h);var g=i(44),t=Object(g.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-609-hong-su-đieu-kien"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-609-hong-su-đieu-kien"}},[n._v("#")]),n._v(" Chương 609: Hồng sư điều kiện")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Nguyên khí tổn hao nhiều, bị thương..."),i("br"),i("br"),n._v("Bị em gái ngươi ah!"),i("br"),i("br"),n._v("Mới vừa rồi cùng Phùng sư cũng kêu chiến đấu?"),i("br"),i("br"),n._v("Ta nhìn gọi cuồng ẩu xấp xỉ, thân ngươi bất động hơi thở không gấp, làm sao lại nguyên khí tổn hao nhiều, trong vòng mười năm không cách nào chiến đấu?"),i("br"),i("br"),n._v("Chân chính tổn hao nhiều chính là nằm dưới đất Phùng sư có được hay không?"),i("br"),i("br"),n._v("Ngươi nhìn hắn... Cũng bắt đầu mắt trợn trắng..."),i("br"),i("br"),n._v("Trái lại ngươi, đánh xong đối phương, sảng khoái tinh thần, cao hứng miệng đều nứt đến lỗ tai..."),i("br"),i("br"),n._v("“Bị không bị tổn thương, ngươi ta trong lòng rõ ràng, liền nói ngươi có dám hay không nghênh chiến đi!”"),i("br"),i("br"),n._v("Càng nghĩ càng giận, La Chiêu danh sư cắn răng hét."),i("br"),i("br"),n._v("“Nếu như ngươi thật không để ý danh sư đạo nghĩa cùng tôn nghiêm, muốn lợi dụng người nguy hiểm, đáp ứng ngươi cũng không có gì, như vậy đi, ta định tỷ thí quy tắc.”"),i("br"),i("br"),n._v("Hồng sư nhàn nhạt nhìn qua: “Chúng ta mỗi người một đầu linh thú phi hành, trên không trung chí ít một ngàn mét không trung chiến đấu, ngươi dám... Vẫn là không dám!”"),i("br"),i("br"),n._v("“Ngươi...”"),i("br"),i("br"),n._v("La Chiêu nhoáng một cái."),i("br"),i("br"),n._v("Không trung chiến đấu?"),i("br"),i("br"),n._v("Vừa rồi theo ghi chép ngọc tinh trông được đến cảnh tượng còn rõ mồn một trước mắt, thật muốn cùng cái tên này trên không trung giao đấu, sợ rằng sẽ cùng nằm trên đất Phùng sư giống nhau, trực tiếp ngã đến bán sống bán chết, sau đó... Lại bị xông lại đánh một trận..."),i("br"),i("br"),n._v("Đừng nói giáo huấn đối phương, có thể hay không sống sót, cũng còn khó nói."),i("br"),i("br"),n._v("“Không dám?” Hồng sư con mắt liếc tới, tràn đầy xem thường: “Không dám cũng không cần khiêu chiến, đường đường danh sư, khiêu chiến một cái người bị thương, thế mà còn không dám nghênh chiến, mất mặt!”"),i("br"),i("br"),n._v("“...”"),i("br"),i("br"),n._v("Vẻ mặt đỏ lên, La Chiêu khí một ngụm máu tươi kém chút phun ra ngoài."),i("br"),i("br"),n._v("Trước kia nhìn lấy Hồng sư rất bình thường, hiện tại làm sao vô sỉ như vậy?"),i("br"),i("br"),n._v("“Tốt, đã các ngươi không nghĩ khiêu chiến, ta cũng lười tiếp tục truy cứu, lần này đến tìm các ngươi, chỉ có một việc, các ngươi trên không trung cản ta, phá hoại công bằng, công chính, chuyện này nhất định phải cho cái thuyết pháp!”"),i("br"),i("br"),n._v("Hừ một tiếng, Hồng sư tiếp tục nói."),i("br"),i("br"),n._v("Tới này mục đích, chính là vì đòi một lời giải thích, hiện tại đánh xong Phùng Vũ, khí đã tiêu tan hơn phân nửa, nhìn xem... Còn có thể hay không lại tìm trở về chút công đạo."),i("br"),i("br"),n._v("Vốn có, an bài tốt mọi người, liền định tới, là Trương sư đem hắn gọi lại, hàn huyên một hồi."),i("br"),i("br"),n._v("Hiện tại xem ra, vẫn là Trương sư phương pháp hữu dụng."),i("br"),i("br"),n._v("Nếu không, lấy lúc trước hắn ý tưởng, xông lại mắng to một trận, sau đó rời đi, không những không cho đối phương nhớ kỹ giáo huấn, bản thân lửa cũng tiêu tan không được."),i("br"),i("br"),n._v("Thậm chí, đối phương liên thủ, còn muốn chịu thiệt."),i("br"),i("br"),n._v("Hiện tại, đi lên liền dùng danh sư khiêu chiến đại nghĩa, đem đối phương ngăn chặn nói không ra lời, sau đó lại dùng cưỡi linh thú phi hành chiến đấu, làm đối phương không dám nghênh chiến, coi như bọn họ muốn phản kích, cũng rơi xuống xuống thành."),i("br"),i("br"),n._v("“Thuyết pháp? Ngươi còn muốn thuyết pháp?”"),i("br"),i("br"),n._v("La Chiêu cùng một cái khác danh sư, hai mặt nhìn nhau, kém chút không có tức chết."),i("br"),i("br"),n._v("Ngươi vừa đến liền đánh Phùng sư suýt tắt thở, còn hỏi chúng ta muốn thuyết pháp?"),i("br"),i("br"),n._v("“Rất đơn giản, dùng thủ đoạn hèn hạ ngăn cản chúng ta, nhất định phải cấp cho bồi thường! Như vậy đi, ta cũng không nhiều muốn, 500 viên linh thạch trung phẩm, chuyện này, cứ tính như vậy, ta cũng không bẩm báo lão sư cùng bệ hạ, nếu không, không phải đem chuyện này náo đi lên, để mọi người cho ta phân xử thử!”"),i("br"),i("br"),n._v("Hồng sư khoát tay chặn lại."),i("br"),i("br"),n._v("Hắn lực lượng mười phần, Trương sư thế nhưng là Hồng Viễn Danh Sư đường Mạc đường chủ, đều bằng lòng lấy ra linh thạch thượng phẩm kết giao nhân vật, chuyện này không nháo còn tốt, một khi náo đi lên, ba tên này, khẳng định phải chịu đến trừng phạt."),i("br"),i("br"),n._v("“500 viên... Linh thạch trung phẩm?”"),i("br"),i("br"),n._v("Hai người cơ thể nhoáng một cái."),i("br"),i("br"),n._v("Bọn họ mặc dù là ngũ tinh danh sư, có thể trong tay cũng không có nhiều lắm linh thạch trung phẩm, có thể lấy ra một hai trăm cái, coi như rất nhiều, một hơi muốn 500... Quả thực chính là cướp bóc!"),i("br"),i("br"),n._v("Có thể chuyện này, quả thực là bọn họ đuối lý."),i("br"),i("br"),n._v("Dựa theo trước đó ý tưởng, cái tên này bị nhốt một ngày, đem ba cái danh ngạch đều làm mất rồi, khẳng định không mặt mũi gặp người, xoay người rời đi, hơn nữa, coi như nói ra, cũng là ngươi không có bản lĩnh..."),i("br"),i("br"),n._v("Ai ngờ, người ta đánh rắm không có, còn chạy tới, chiếm “Đại nghĩa” thượng phong, để cho ngươi muốn cự tuyệt đều không được."),i("br"),i("br"),n._v("Buồn bực suy nghĩ muốn thổ huyết."),i("br"),i("br"),n._v("“Chuyện này, chúng ta là làm được có chút lỗ mãng, có điều, Hồng Thiển, ngươi cũng không cần đúng lý không tha người. Lại nói, cũng không không có đem các ngươi làm gì ư? Đối nhân xử thế lưu một đường, lần sau cũng dễ nói, không cần thiết làm được như thế tuyệt đi!”"),i("br"),i("br"),n._v("La Chiêu hất lên ống tay áo."),i("br"),i("br"),n._v("“Không sai, tất cả mọi người là ngũ tinh danh sư, tai to mặt lớn, ngươi đánh cũng đánh, đánh cũng đánh, còn muốn làm gì? Còn ỷ lại vào chúng ta hay sao?”"),i("br"),i("br"),n._v("Cái thứ hai lão giả, cũng khí sắc mặt đỏ lên."),i("br"),i("br"),n._v("“Làm sao không nghĩ cho? Vẫn là thanh toán không được sao?”"),i("br"),i("br"),n._v("Hồng sư hai tay chắp sau lưng."),i("br"),i("br"),n._v("“Hừ, linh thạch trung phẩm, đối với chúng ta ngũ tinh danh sư, đều là bảo vật quý đồ vật, năm trăm viên, ngươi có thể duy nhất một lần cầm ra được?”"),i("br"),i("br"),n._v("La Chiêu khẽ nói."),i("br"),i("br"),n._v("“Không bỏ ra nổi đến cũng được, ta nghe nói, thái tử tối nay yến khách, thương nghị như thế nào giải quyết ba cái danh ngạch vấn đề, như thế nào giải quyết, giải quyết như thế nào, các ngươi đến Đế đô sớm như vậy, chắc hẳn đã sớm hỏi thăm rõ ràng đi! Chỉ cần đem phương pháp giải quyết nói ra, cho ra các ngươi thương nghị tốt ứng đối phương pháp, chuyện này, cũng là như vậy coi như không có gì!”"),i("br"),i("br"),n._v("Hồng sư vẫy tay."),i("br"),i("br"),n._v("500 linh thạch trung phẩm, ngũ tinh danh sư là không thể nào cầm ra được, rao giá trên trời, ngay tại chỗ trả tiền mà thôi!"),i("br"),i("br"),n._v("Hiện tại việc cấp bách, vẫn là biết thái tử như thế nào giải quyết ba cái danh ngạch vấn đề, cũng tốt sớm có chút chuẩn bị, không đến mức luống cuống tay chân."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),n._v("La Chiêu cùng một cái khác lão giả, liếc mắt nhìn nhau, đồng thời lắc đầu."),i("br"),i("br"),n._v("“Thái tử địa vị tôn sùng, làm sao tuyển bạt, trước đó không có lộ ra, chúng ta thực sự không biết...”"),i("br"),i("br"),n._v("“Nếu như có thể sớm biết khảo hạch cái gì, có ứng đối, cũng không trở thành muốn ra ngăn cản các ngươi dạng này hạ sách.”"),i("br"),i("br"),n._v("Thái tử mặc dù là vãn bối, lại là Hồng Viễn danh sư học viện cao tài sinh, liền xem như bọn họ đoán không ra, cũng không dám trực tiếp ép hỏi."),i("br"),i("br"),n._v("“Không biết?” Hồng sư nhíu mày."),i("br"),i("br"),n._v("“Vâng!”"),i("br"),i("br"),n._v("La Chiêu lên tiếng."),i("br"),i("br"),n._v("Thấy bọn họ nói như vậy, Hồng sư bất đắc dĩ lắc đầu."),i("br"),i("br"),n._v("Cũng đúng, nếu như đối phương biết quy tắc, sớm chuẩn bị, không cần thiết ra hạ sách này, chuyên môn chạy tới chặn đường, để cho mình nắm được cán."),i("br"),i("br"),n._v("“Như vậy đi, thái tử khảo hạch cái gì, ta không biết, nhưng... Có thể sớm nói với các ngươi một chút, Phi Nhi công chúa cùng bốn cái danh ngạch sự tình, đây cũng là chúng ta nghe ngóng hồi lâu mới biết.”"),i("br"),i("br"),n._v("La Chiêu chần chờ một chút, nói."),i("br"),i("br"),n._v("“Tốt, chỉ cần ngươi đem sự tình nói ra, chuyện lúc trước, xóa bỏ!” Hồng sư gật đầu."),i("br"),i("br"),n._v("Trương sư đám người sớm muộn muốn đi phía sau Hồng Viễn danh sư học viện, vị này Phi Nhi công chúa nếu là học viện người, sớm biết một ít, giữ gìn mối quan hệ, cũng tốt có cái ứng đối."),i("br"),i("br"),n._v("“Ừm, Phi Nhi công chúa là Hồng Viễn đế quốc Lục công chúa, tuổi không lớn lắm, liền đo lường ra đối danh sư có cực mạnh thiên phú, năm nay hai mươi bốn tuổi, đã khảo hạch ngũ tinh danh sư thành công!”"),i("br"),i("br"),n._v("La Chiêu nói."),i("br"),i("br"),n._v("“Hai mươi bốn tuổi... Ngũ tinh danh sư?”"),i("br"),i("br"),n._v("Hồng sư con ngươi co rụt lại."),i("br"),i("br"),n._v("Có thể trở thành ngũ tinh danh sư, biết loại cấp bậc này độ khó, không riêng gì có danh sư thiên phú liền có thể làm được, còn muốn tinh thông chí ít năm loại chức nghiệp, đồng thời toàn bộ học tập đến ngũ tinh cấp bậc."),i("br"),i("br"),n._v("Hắn hao tốn hơn 160 năm mới làm được, đối phương hai mươi bốn tuổi..."),i("br"),i("br"),n._v("Không phải chính tai nghe được, đều khó mà tin tưởng."),i("br"),i("br"),n._v("“Loại thiên phú này, chỉ sợ cũng liền Trương sư, có thể cùng so sánh đi!”"),i("br"),i("br"),n._v("Cảm khái một tiếng, tràn đầy cười khổ."),i("br"),i("br"),n._v("Không thể phủ nhận, thiên tài chân chính, quả thực không phải hắn loại người này, có thể lý giải."),i("br"),i("br"),n._v("“Vâng, chính vì vậy, Diệp Tiền thái tử mới chủ động kết giao, để bệ hạ lấy ra bốn cái danh ngạch!” La Chiêu gật đầu."),i("br"),i("br"),n._v("Hắn mới vừa biết tin tức này thời điểm, cũng bị khiếp sợ không nhẹ, nếu không phải tâm lý tố chất quá cứng, chỉ sợ đều muốn dọa xụi lơ."),i("br"),i("br"),n._v("Hai mươi bốn tuổi ngũ tinh danh sư... Đây là trước đó nghĩ cũng không dám nghĩ."),i("br"),i("br"),n._v("“Phi Nhi công chúa chỉ có một người, muốn bốn cái danh ngạch làm gì?”"),i("br"),i("br"),n._v("Hồng sư nhìn qua."),i("br"),i("br"),n._v("Trước đó liền muốn hỏi Trang Tần, đáng tiếc địa vị hắn thấp, những chuyện này không nghe được, hỏi cũng là hỏi không, liền không có mở miệng, đã đối phương bằng lòng nói, vừa vặn hỏi thăm rõ ràng."),i("br"),i("br"),n._v("“Công chúa là một người, có điều, nàng còn có một người bạn, cùng hai cái danh sư học viện đồng học, hình như là đến Thanh Tắc sơn thi hành nhiệm vụ gì, lúc này mới bị thái tử mời, nếu không, Hồng Viễn đế quốc công chúa, làm sao có thể hạ mình đi tới nơi này?”"),i("br"),i("br"),n._v("La Chiêu nói."),i("br"),i("br"),n._v("Hồng sư gật đầu."),i("br"),i("br"),n._v("Hồng Viễn nhất đẳng đế quốc, tại danh sư đại lục, cũng có thể coi là được một phương thế lực, phía dưới quản hạt Huyễn Vũ đế quốc lớn nhỏ nhị đẳng đế quốc, không dưới mấy trăm, nếu như không phải có cái gì tình huống đặc biệt, làm sao có thể hạ mình tới?"),i("br"),i("br"),n._v("Coi như Hóa Thanh trì thần kỳ, cũng chỉ là đối Hợp Linh cảnh, Kiều Thiên cảnh linh hồn của cường giả hữu dụng mà thôi, mà loại tác dụng này, tại nhất đẳng đế quốc loại này vật tư phong phú quốc gia, hoàn toàn có thể dùng rất nhiều bảo vật thay thế, không cần thiết bắt kịp không biết dài đến đâu thời điểm đường, hao hết vất vả tới này."),i("br"),i("br"),n._v("“Mấy người này tên cùng thân phận, các ngươi đều tra ra được?”"),i("br"),i("br"),n._v("Hồng sư hỏi tiếp."),i("br"),i("br"),n._v("“Phi Nhi công chúa bạn bè, kêu Lạc Thất Thất, nghe nói cũng là cái nào đó nhất đẳng vương quốc công chúa, cụ thể thân phận, đối phương không nói, chúng ta cũng không có quyền hạn đi thăm dò, có điều, danh sư đẳng cấp dường như không thể so với Phi Nhi công chúa kém, tu vi càng là càng hơn một bậc, theo ta suy đoán, chúng ta mấy cái cộng lại, chỉ sợ cũng không là đối thủ!”"),i("br"),i("br"),n._v("La Chiêu lộ ra vẻ mặt ngưng trọng."),i("br"),i("br"),n._v("Mặc dù hắn cùng vị này Lạc Thất Thất, chỉ gặp mặt qua một lần, nhưng vẫn là có thể nhìn ra, thực lực của đối phương sâu không lường được."),i("br"),i("br"),n._v("Chí ít không phải hắn có thể chống lại."),i("br"),i("br"),n._v("“Còn còn lại hai người, cùng công chúa là bạn học cùng lớp, đều là nam, tu vi cùng thực lực, cũng đều không yếu, không thể so với công chúa kém! Mấu chốt nhất là, trên người bọn họ đều có con em quý tộc khí tức, đoán không sai, thân phận cũng đều không đơn giản. Còn tên...”"),i("br"),i("br"),n._v("La Chiêu suy nghĩ một chút, nói: “Một cái gọi Hình Viễn, một cái khác, hình như kêu Ngô Chấn... Cụ thể chi tiết tình huống, cũng không biết!”"),i("br"),i("br"),n._v("“Lạc Thất Thất, Hình Viễn, Ngô Chấn?”"),i("br"),i("br"),n._v("Hồng sư gật gật đầu."),i("br"),i("br"),n._v("Có thể theo danh sư học viện đi ra, khẳng định đều là danh sư, cùng Phi Nhi công chúa cùng lớp, nói cách khác, cũng đều là ngũ tinh cấp bậc."),i("br"),i("br"),n._v("Hồng Viễn danh sư học viện học sinh, hơn nữa được phái ra hoàn thành một loại nào đó nhiệm vụ, mấy người này sau này thành tựu, khẳng định vô khả hạn lượng."),i("br"),i("br"),n._v("Như thế có thể kết giao cơ hội, Huyễn Vũ đế quốc đương nhiên sẽ không từ bỏ."),i("br"),i("br"),n._v("Khó trách Diệp Tiền thái tử, như thế nhiệt tâm."),i("br"),i("br"),n._v("“A, đúng, Phi Nhi công chúa tính khí không tốt lắm, cái kia Lạc Thất Thất cũng cực kỳ lạnh lùng, nếu như gặp phải, tốt nhất đừng đắc tội, nếu không, Huyễn Vũ đế quốc đều không chịu nổi!”"),i("br"),i("br"),n._v("Nghĩ tới điều gì, La Chiêu vội vàng dặn dò."),i("br"),i("br"),n._v("“Đắc tội? Yên tâm đi, chúng ta gặp không được, khẳng định cũng không có khả năng đắc tội!”"),i("br"),i("br"),n._v("Hồng sư cười cười, mặt chân thành: “Chúng ta đều là người đàng hoàng!”"),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-617"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-619"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=t.exports}}]);