(window.webpackJsonp=window.webpackJsonp||[]).push([[1877],{2232:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-611-lac-xa-noi-đan"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-611-lac-xa-noi-đan"}},[n._v("#")]),n._v(" Chương 611: Lạc Xà nội đan")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Một nam một nữ, thoạt nhìn tư thế hiên ngang, tinh khí mười phần."),t("br"),t("br"),n._v("Đi ở phía trước nữ tử, một bộ màu vàng nhạt quần áo bó, đem hoàn mỹ dáng người phác hoạ ra đến, làn da trắng nõn, tóc dài đen nhánh đáp lên trên vai, eo đẹp uyển chuyển vừa nắm, đơn thuần dung mạo, không chút nào tại Mạc Vũ, Triệu Phi Vũ bên dưới."),t("br"),t("br"),n._v("Nam tử bên người, ba mươi hai, ba tuổi bộ dáng, dáng người to lớn, khuôn mặt cương nghị, hai đầu lông mày mang theo một cỗ khí thế, vừa nhìn liền biết, rất có thân phận."),t("br"),t("br"),n._v("Trương Huyền cũng không thèm để ý dung mạo của đối phương, muốn nói xinh đẹp, ai cũng so ra kém thể chất mở ra Triệu Nhã, thậm chí Mạc Vũ, Triệu Phi Vũ đều không hề yếu, ba người này, đều không thế nào nhìn kỹ, huống chi không nhận ra cái nào nữ tử."),t("br"),t("br"),n._v("Nhìn về phía tu vi, vừa nhìn phía dưới, không khỏi kinh ngạc."),t("br"),t("br"),n._v("Hai cái này, tuổi tác cũng không tính là quá lớn, thực lực lại đều cực mạnh, cho người ta một loại như vực sâu biển lớn cảm giác, so với Hồng sư, đều chỉ mạnh không yếu."),t("br"),t("br"),n._v("“Hóa Phàm lục trọng cường giả?”"),t("br"),t("br"),n._v("Lông mày giương lên."),t("br"),t("br"),n._v("Hóa Phàm lục trọng, 28 chỗ thế lực, người mạnh nhất đều không đạt tới, đi ra mua cái đồ vật, liền thấy hai cái, hơn nữa trẻ tuổi như vậy, không hổ là Huyễn Vũ Đế đô, quả nhiên tàng long ngọa hổ."),t("br"),t("br"),n._v("Các nước chư hầu chính là các nước chư hầu, cùng Đế đô, vẫn là không có cách nào so."),t("br"),t("br"),n._v("“Không cần, nếu như ngươi cảm thấy nhàm chán, có thể đi trở về!”"),t("br"),t("br"),n._v("Mỹ mạo nữ tử thanh âm lạnh nhạt lên tiếng, cũng không để ý tới hảo ý của đối phương, xem ra hai người quan hệ, cũng không phải là mười điểm hòa thuận."),t("br"),t("br"),n._v("“Ha ha, làm sao sẽ cảm thấy nhàm chán, ngươi muốn nhìn, cùng ngươi là được!” Nam tử xấu hổ cười một tiếng."),t("br"),t("br"),n._v("Hai người tiếp tục dọc theo quầy hàng quan sát, Trương Huyền cũng là chẳng muốn lại đi quản, cũng từng kiện từng kiện bảo vật nhìn sang."),t("br"),t("br"),n._v("Kích hoạt Long tê huyết mạch, cần thuộc tính tương đồng linh thú huyết dịch hoặc là nội đan, lại hoặc là đặc thù dược vật, khoáng thạch, những vật này, mười điểm thưa thớt, muốn tìm được, không dễ dàng như vậy."),t("br"),t("br"),n._v("“Tử Thần châu? Đeo trên người, có thể tẩy rửa bụi bặm, để cho người ta không dính một hạt bụi!”"),t("br"),t("br"),n._v("Con mắt rơi vào một cái viên châu bên trên."),t("br"),t("br"),n._v("Thứ này, hắn nhìn qua ghi chép, là một loại đặc thù pháp bảo, không phải là tính công kích, cũng không phải phòng ngự tính, mà là một loại người tu luyện ưa thích đeo vật phẩm."),t("br"),t("br"),n._v("Một khi mang ở trên người, có thể để cho quần áo, cơ thể không nhuốm bụi trần, coi như không cần loại trừ, cũng mặt mày tỏa sáng."),t("br"),t("br"),n._v("“Thứ này thế mà cũng là ‘Hai’...” Lắc đầu."),t("br"),t("br"),n._v("Một cái thất cảnh hóa thành, mới giá trị “Hai” thứ này thế mà cũng tương đồng giá cả."),t("br"),t("br"),n._v("Xem ra cái này kỳ trân lâu, cũng không phải là dựa theo giá trị đến đặt trước con số, mà là lấy trình độ hiếm hoi để phán đoán."),t("br"),t("br"),n._v("Tử Thần châu, đối với võ giả tới nói, mặc dù tác dụng không lớn, lại là vô cùng khó được bảo bối, sâu nữ tử ưa thích."),t("br"),t("br"),n._v("Đại lục bao la, có lúc vừa bay thật nhiều ngày, không cách nào loại trừ, đổi lại ai cũng sẽ khó chịu đến cực điểm, mà có cái này, thời khắc duy trì sạch sẽ, đừng nói một, hai tháng, coi như một năm không tắm, cũng không ảnh hưởng toàn cục."),t("br"),t("br"),n._v("Bởi vì khan hiếm, mới lộ ra trân quý."),t("br"),t("br"),n._v("Bất quá, cũng có thể nhìn ra thư hoạ giá trị, cùng chân chính vũ khí, pháp bảo so, vẫn là không cao."),t("br"),t("br"),n._v("Chẳng qua đây cũng chỉ là thất cảnh tác phẩm, nếu như đạt tới bát cảnh, hoặc là cao hơn, có thể Hải Thị Thận Lâu, lấy hư hóa thực, giá trị liền hoàn toàn khác biệt, một ít lợi hại tác phẩm hội họa, thậm chí có thể huyễn hóa thành một tòa thành thị, đem người tu luyện khốn vào trong đó, mãi mãi cũng không cách nào chạy ra."),t("br"),t("br"),n._v("Có thể được Danh Sư đường sắc phong làm đặc thù chức nghiệp, mỗi một cái đi đến cuối cùng, đều phi thường cường đại, có thường nhân khó mà chống lại lực lượng."),t("br"),t("br"),n._v("Thật giống như hắn đoạn thời gian trước lĩnh ngộ Thiên Tinh cờ, nghe nói ngộ đạo cảnh giới tối cao, thật có thể lấy thiên địa làm bàn cờ, tinh thần làm quân cờ, xê dịch Càn Khôn, nghịch chuyển Âm Dương."),t("br"),t("br"),n._v("Đáng tiếc... Hồng sư lấy được chỉ là tàn thiên, coi như khôi lỗi đem truyền thừa cho mình, cũng không phải hoàn chỉnh, không đạt tới loại hiệu quả này."),t("br"),t("br"),n._v("Tiếp tục xem đi, rất nhanh toàn bộ lầu hai dạo qua một vòng, vẫn như cũ không có phát hiện thích hợp bảo vật, Trương Huyền mang theo hai người, hướng lầu ba đi đến."),t("br"),t("br"),n._v("Lầu ba vật phẩm càng thêm trân quý, nhìn thoáng qua, trong này bảo vật, ghi chú đã chẳng phải chỉnh tề, có chút là “Ba” có chút là “Bốn”, cũng có chút là “Năm”."),t("br"),t("br"),n._v("Dọc theo quầy hàng lần lượt nhìn lại, thấy được một bộ lục cảnh tác phẩm hội họa, thế mà ghi chú “Bốn”, so trước đó thất cảnh tác phẩm hội họa, còn muốn đắt gấp đôi."),t("br"),t("br"),n._v("Nghi ngờ một chút, rất nhanh liền hiểu được."),t("br"),t("br"),n._v("Bức họa này, mặc dù cấp bậc so sánh với một bức thấp, lại là Huyễn Vũ đế quốc rất nổi danh một vị thư họa sư lưu lại bản vẽ đẹp, nghe nói là hắn cùng người chiến đấu, trước khi chết, lấy tâm đầu huyết lưu lại tác phẩm hội họa, ẩn chứa vị này ngũ tinh thư họa sư lớn nhất chấp niệm cùng oán khí, trân quý dị thường, định giá cao cũng là chuyện đương nhiên."),t("br"),t("br"),n._v("“Ừm, đây là Linh cấp trung phẩm đỉnh phong cấp bậc trường kiếm?”"),t("br"),t("br"),n._v("Rất mau nhìn đến một kiện binh khí."),t("br"),t("br"),n._v("Hắn kiếm pháp siêu quần, nhưng vẫn không có binh khí thích hợp, nếu như có thể phối hợp một chuôi, thực lực thế nào cũng tăng nhiều."),t("br"),t("br"),n._v("Chuôi kiếm này, so trước đó tại Hồng Hải thành, đã gặp cấp bậc cũng cao hơn một ít, cũng càng thêm sắc bén, hàn mang bắn ra bốn phía, cho người ta một loại băng hàn cảm giác."),t("br"),t("br"),n._v("“Băng Vũ kiếm, sáu!”"),t("br"),t("br"),n._v("Phía dưới viết bốn chữ."),t("br"),t("br"),n._v("“Giá trị là sáu?” Trương Huyền tắc lưỡi."),t("br"),t("br"),n._v("Lầu một cũng nhìn thấy qua Linh cấp trung phẩm vũ khí, nhưng đều là “Một” khoảng chừng, cái này nhưng ghi chú “Sáu”, không khỏi quá khoa trương đi!"),t("br"),t("br"),n._v("“Vị công tử này, chuôi kiếm này, là luyện khí đại sư Hồ Thanh Tử tự mình rèn đúc, là , gắng sức lần nữa rèn luyện, vô cùng trân quý, không phải phía dưới những cái kia bình thường vũ khí, có thể sánh ngang!”"),t("br"),t("br"),n._v("Thấy hắn nghi hoặc, bên quầy bên trên một cái gã sai vặt giới thiệu."),t("br"),t("br"),n._v("“Có thể tấn thăng pháp bảo?” Trương Huyền sững sờ, lập tức gật đầu."),t("br"),t("br"),n._v("Hắn mặc dù không cài thống học qua luyện khí, nhưng loại sách này tịch, nhưng góp nhặt không ít, biết trong đó đẳng cấp."),t("br"),t("br"),n._v("Bình thường vũ khí, luyện chế tốt về sau, chính là thành phẩm, hoàn toàn cố định, một khi lại tôi vào nước lạnh, rất dễ dàng mất đi phong mang, biến thành phế vật."),t("br"),n._v("Mà loại này có thể tấn thăng pháp bảo không giống, về sau có tốt hơn khoáng thạch, hoàn toàn có thể tôi vào nước lạnh dung nhập trong đó, từ đó tăng lên vũ khí phẩm chất, để hắn tấn thăng cấp bậc."),t("br"),t("br"),n._v("Nói cách khác, loại binh khí này, mặc dù hiện nay là Linh cấp trung phẩm, về sau cuối cùng đạt tới cấp bậc gì, lại là không cách nào lường được, quả thực đáng giá bên trên “Sáu” cái này định giá."),t("br"),t("br"),n._v("“Ừm, Lạc Xà nội đan?”"),t("br"),t("br"),n._v("Đối chuôi kiếm này có chút ý động, chẳng qua cũng không vội vã, tiếp tục hướng xuống nhìn lại, mới đi hai bước, đột nhiên ánh mắt sáng lên."),t("br"),t("br"),n._v("Cách đó không xa trên quầy, trưng bày một cái vòng tròn nhuận linh thú nội đan, đỏ rực như lửa, tản mát ra nóng rực khí tức."),t("br"),t("br"),n._v("Hóa Phàm thất trọng linh thú, Lạc Xà nội đan!"),t("br"),t("br"),n._v("Lạc Xà, là một loại sinh hoạt tại sông núi Đại Trạch linh thú, toàn thân lân phiến, phòng ngự vô địch, mặc dù cơ thể lạnh lẽo như sắt, trong đó đan nhưng đỏ thẫm như than, cho người ta một loại thiêu đốt cảm giác."),t("br"),t("br"),n._v("“Lạc Xà ẩn chứa Long tộc huyết mạch, có lẽ có thể kích hoạt Long tê huyết mạch!”"),t("br"),t("br"),n._v("Trương Huyền mỉm cười."),t("br"),t("br"),n._v("Lạc Xà, mặc dù là rắn một loại, trong cơ thể nhưng ẩn chứa giao long huyết mạch, lại thêm phòng ngự vô địch, đối Viên Đào Long tê huyết mạch chính là đại bổ."),t("br"),t("br"),n._v("Cúi đầu nhìn xuống, chỉ thấy trên da cừu viết một nhóm chữ: “Lạc Xà nội đan, mười!”"),t("br"),t("br"),n._v("“ ‘Mười điểm’... Bất quá, cũng đáng cái giá tiền này!”"),t("br"),t("br"),n._v("Hóa Phàm thất trọng linh thú nội đan, cũng chỉ có Hóa Phàm bát trọng cường giả, mới có thể săn giết, săn bắt, kêu giá “Mười”, cũng coi như đáng cái giá này."),t("br"),t("br"),n._v("“Thứ này ta muốn!” Trong lòng âm thầm quyết định: “Bất quá, thứ này nộ khí quá vượng, còn cần một ít dược vật trung hoà, nhìn xem có cái gì lúc nào dược liệu không có!”"),t("br"),t("br"),n._v("Tìm được thích hợp Viên Đào đồ vật, Trương Huyền hài lòng gật đầu, tiếp tục hướng xuống nhìn lại, muốn tìm một gốc thích hợp dược liệu trung hoà dược tính, liền nghe đến một cái thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("“Cái này gốc Đỗ Ưu thảo, ta muốn, làm sao mua?”"),t("br"),t("br"),n._v("Là nữ tử thanh âm, giương mắt nhìn lại, chính là vừa rồi tại lầu hai thời điểm gặp phải đôi kia tuấn nam tịnh nữ, chẳng biết lúc nào cũng tới đến tầng này."),t("br"),t("br"),n._v("“Đỗ Ưu thảo?” Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Đỗ Ưu thảo là một loại thuộc tính hàn dược liệu, tính tình dịu dàng ngoan ngoãn, đối cực nóng thuộc tính dược vật, có cực lớn trung hoà tác dụng, càng với thân thể người nội tạng, kinh mạch, có cực mạnh ôn dưỡng cùng bảo hộ, chính là trung hoà Lạc Xà nội đan tốt nhất dược liệu."),t("br"),t("br"),n._v("“Bẩm báo vị tiểu thư này, chúng ta kỳ trân lâu bảo vật, cũng không bán ra, muốn mua, cần thu hoạch được điểm tích lũy, điểm tích lũy đầy đủ, có thể trực tiếp hối đoái!”"),t("br"),t("br"),n._v("Phụ trách quản lý quầy hàng gã sai vặt, thấy hai người không hiểu, vội vàng giải thích."),t("br"),t("br"),n._v("“Điểm tích lũy? Điểm tích lũy là cái gì?” Thanh niên nhướng mày."),t("br"),t("br"),n._v("“A, chúng ta kỳ trân lâu lầu bốn có hai hàng cột đá, trong đó niêm phong đủ loại bảo vật, trong vòng thời gian quy định nhận ra, thu hoạch được điểm tích lũy, nhận càng nhiều, điểm tích lũy càng nhiều...” Gã sai vặt đem quy tắc nói một lần."),t("br"),t("br"),n._v("Cùng Viên Đào nói cơ bản giống nhau, chẳng qua càng thêm kỹ càng."),t("br"),t("br"),n._v("“Còn muốn thứ này? Phiền phức! Bất quá, nhận ra bảo vật mà thôi, không tính là gì!”"),t("br"),t("br"),n._v("Nghe được còn có phức tạp như vậy quy củ, thanh niên nhíu mày, lập tức trên mặt lộ ra sự tự tin mạnh mẽ, quay đầu nhìn về phía bên người nữ tử: “Sáu... Tiểu thư, ở đây chờ một chút, ta hiện tại liền cho ngươi đổi điểm tích lũy tới!”"),t("br"),t("br"),n._v("“Không cần hỗ trợ của ngươi, điểm tích lũy, chính ta sẽ kiếm lấy!”"),t("br"),t("br"),n._v("Tên là Lục tiểu thư nữ tử, tựa hồ đối với thanh niên lấy lòng, cũng không thèm để ý, ngược lại có có chút không vui, khoát tay áo, nhìn về phía trước mắt gã sai vặt: “Phía trước dẫn đường, ta mau mau đến xem!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Gã sai vặt thấy có người muốn xông bia đá, liền vội vàng gật đầu, mang theo hai người đi lên lầu."),t("br"),t("br"),n._v("“Chúng ta cũng đi qua đi!”"),t("br"),t("br"),n._v("Trương Huyền nói một tiếng."),t("br"),t("br"),n._v("Tới này mục đích đúng là vì tìm kiếm kích hoạt Viên Đào thể chất đồ vật, đã kỳ trân lâu có thứ này, vừa vặn kiếm lấy chút điểm tích lũy, cùng nhau đổi."),t("br"),t("br"),n._v("Đi theo mọi người phía sau, đi đến lầu bốn, thấy được một cái rộng lớn đại sảnh, ở giữa là hai hàng chỉnh tề cột đá, chừng mười mấy cái, bề mặt sáng bóng trơn trượt, không biết dùng cái gì điêu khắc mà thành, mấy người đứng ở phía trước, không ngừng lắc đầu, tràn đầy mất tinh thần."),t("br"),t("br"),n._v("“Chư vị không nên nản chí, không nhận ra được cũng không sao!”"),t("br"),t("br"),n._v("Trước gian phòng, một cái lão giả cười nhạt nói."),t("br"),t("br"),n._v("Xem ra mấy cái này lắc đầu gia hỏa hẳn là dự định xông cột đá, mà thất bại."),t("br"),t("br"),n._v("Trương Huyền nhìn lại, mấy người kia đều người mặc đại biểu thân phận trang phục, lại có hai vị là tứ tinh giám bảo sư."),t("br"),t("br"),n._v("Tứ tinh giám bảo sư đều thất bại, xem ra cái này phân biệt bảo vật, cũng không tưởng tượng đơn giản như vậy."),t("br"),t("br"),n._v("“Lâu chủ, hai vị này, cũng muốn xông một chút cột đá!”"),t("br"),t("br"),n._v("Gã sai vặt mấy bước đi tới lão giả trước mặt, hướng thanh niên cùng nữ tử một ngón tay."),t("br"),t("br"),n._v("Vị lão giả này chính là kỳ trân lâu lâu chủ, cam một ổn định."),t("br"),t("br"),n._v("“Tốt...” Cam một ổn định gật gật đầu, xoay đầu lại, mỉm cười: “Kính xin trước giao nạp tiền thế chấp đi!”"),t("br"),t("br"),n._v("“Tiền thế chấp? Đúng, ta còn không có tiền tiền thế chấp...”"),t("br"),t("br"),n._v("Cách đó không xa Trương Huyền sửng sốt một chút, lập tức có chút xấu hổ."),t("br"),t("br"),n._v("(Lần trước có độc giả hỏi ta, làm sao nhận lấy nhai quân hiệu chương, hôm nay vừa lúc là cơ hội. Mở ra khách hàng mang, có cái mùa hạ trà lạnh vì mục đích nào đó, click nhận lấy nhiệm vụ giới diện, lục soát , liền có thể nhận lấy “Nhai quân” huy chương, có điều, có thể muốn khen thưởng mười đồng tiền. Đề nghị trong tay dư dả bạn bè nhận lấy, không dư dả, chính bản gắng sức, già nhai liền rất vui vẻ. Hiện tại nhận lấy mười khối, một khi đổi thành đại thần hẹn, liền có thể muốn đánh thưởng 50, mới có thể nhận lấy. Hiện tại chúng ta nhai quân, có hơn năm ngàn vị thành viên, tại toàn bộ điểm xuất phát, đều được cho tương đối lớn hội fan hâm mộ. Hắc hắc!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-619"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-621"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);