(window.webpackJsonp=window.webpackJsonp||[]).push([[1878],{2235:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-612-xong-cot-đa"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-612-xong-cot-đa"}},[n._v("#")]),n._v(" Chương 612: Xông cột đá")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chỉ mới nghĩ đến kiếm chút điểm tích lũy, quên trên người hắn loại trừ một cái linh thạch thượng phẩm, một cái linh thạch trung phẩm đều không có."),t("br"),t("br"),n._v("Trước đó mặc dù theo Lưu Vân tông rất nhiều tông môn lấy tiếp cận sáu trăm viên linh thạch trung phẩm, nhưng còn chưa tới Huyễn Vũ Đế đô, liền tiêu hao hầu như không còn, hắn hiện tại, có thể nói một nghèo hai trắng, kẻ nghèo hèn không bằng."),t("br"),t("br"),n._v("“Bao nhiêu tiền thế chấp?” Thanh niên cũng không biết quy củ."),t("br"),t("br"),n._v("“Mười cái linh thạch trung phẩm!” Cam Nhất Bình nói."),t("br"),t("br"),n._v("“Cho! Hai người chúng ta đều xông một chút thử một chút.” Cũng không tỉ mỉ hỏi, thanh niên cổ tay khẽ đảo, lấy ra hai mươi viên linh thạch trung phẩm."),t("br"),t("br"),n._v("Đối với người khác tới nói, táng gia bại sản đều khó mà gọp đủ hai mươi viên linh thạch trung phẩm, dường như trong tay hắn, cũng không thèm để ý."),t("br"),t("br"),n._v("Tiếp nhận linh thạch, Cam Nhất Bình cười cười, chỉ về phía trước, nói ra quy tắc: “Cái này có hai hàng cột đá, mỗi một cái đều phong tỏa một cái bảo vật, phía trên có gợi ý tính ngôn ngữ, căn cứ cái này, còn có bản thân phán đoán, suy đoán ra bên trong rốt cuộc là loại nào vật phẩm.”"),t("br"),t("br"),n._v("“Mỗi người chỉ có thời gian một nén nhang, chỉ cần nhận ra, nói ra tên, cột đá liền sẽ tự động nứt ra. Nứt ra càng nhiều, lấy được điểm tích lũy càng nhiều, cũng là có thể hối đoái càng nhiều bảo vật!”"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Thanh niên gật đầu, trên mặt lộ ra lấy lòng hương vị: “Lục tiểu thư, ngươi chờ ở tại đây, ta đi một chút liền đến!”"),t("br"),t("br"),n._v("Nói xong trực tiếp đi thẳng về phía trước, nhanh chân đi tới cột đá trước mặt."),t("br"),t("br"),n._v("Trương Huyền mấy người cũng nhìn sang."),t("br"),t("br"),n._v("Quy tắc là nghe rõ, muốn biết rốt cuộc làm sao khảo hạch, độ khó rốt cuộc lớn đến bao nhiêu."),t("br"),t("br"),n._v("Đứng tại cột đá trước mặt, thanh niên dường như thấy được phía trên lưu lại gợi ý, dọc theo cái thứ nhất cột đá, dạo qua một vòng, vốn có tràn đầy tự tin trên mặt, lộ ra mê mang, dường như càng xem càng hồ đồ rồi."),t("br"),t("br"),n._v("[ truyen cua tu"),t("br"),n._v("i ʘʘ vn ] Cột đá bóng loáng mượt mà, không có bất kỳ cái gì đặc thù, bên trong chứa đựng đồ vật gì, nhìn không thấy, cũng sờ không được, thậm chí liền hình dạng cũng không biết, cùng cách khí cụ đoán vật phẩm giống nhau, độ khó bao lớn, vượt ra khỏi tưởng tượng ra."),t("br"),t("br"),n._v("Lại xoay hai vòng, sắc mặt tái xanh."),t("br"),t("br"),n._v("Mới vừa cùng Lục tiểu thư khoe khoang khoác lác, đi nói đến liền đến, chủ ý là cầm xuống điểm tích lũy, chiếm được mỹ nhân tâm, kết quả, cái gì cũng chưa nhận ra được, nếu như vậy trở về, cũng thực sự quá mất mặt đi!"),t("br"),t("br"),n._v("Gãi gãi đầu, thanh niên có chút nóng nảy, xòe bàn tay ra chạm đến cột đá, chân khí tuôn ra, muốn rót vào trong đó, lại phát hiện, cột đá dường như mang theo ngăn cách chân khí năng lực, một chút tác dụng đều không nổi."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Rất nhanh, các loại có thể nghĩ tới thủ đoạn tất cả đều dùng một lần, cũng nhịn không được nữa, quay đầu nhìn về phía Cam Nhất Bình."),t("br"),t("br"),n._v("“Lâu chủ, ngươi cái này, cái gì đặc thù đều không có, làm sao nhìn ra?”"),t("br"),t("br"),n._v("Hố người a?"),t("br"),t("br"),n._v("Cái đồ chơi này chính là cái cột đá, ngươi để cho ta nhìn ra cái gì?"),t("br"),t("br"),n._v("Đừng nói ta, coi như ngũ tinh giám bảo sư, lục tinh giám bảo sư cũng nhìn không ra đến ah!"),t("br"),t("br"),n._v("“Công tử không nên gấp gáp, trên trụ đá lưu lại gợi ý, có thể căn cứ gợi ý cân nhắc!”"),t("br"),t("br"),n._v("Dường như biết đối phương có thể như vậy nói, Cam Nhất Bình mỉm cười."),t("br"),t("br"),n._v("“Cân nhắc?”"),t("br"),t("br"),n._v("Lần nữa nhìn về phía trên trụ đá chữ viết, nhìn hồi lâu dường như cũng không hiểu, thấy thời gian một nén nhang, lập tức liền muốn đi qua, đành phải cắn răng nói bậy: “Là kiếm... Đao, dược liệu, cây gỗ...”"),t("br"),t("br"),n._v("Nói liên tục mười mấy dạng, đều không có bất luận cái gì động tĩnh."),t("br"),t("br"),n._v("“Thời gian đến!” Cam Nhất Bình cười khổ lắc đầu."),t("br"),t("br"),n._v("Lần đầu tiên nhìn thấy, liền biết vị thanh niên này bất phàm, vốn cho rằng có thể phá vỡ cục diện bế tắc, hiện tại xem ra, suy nghĩ nhiều."),t("br"),t("br"),n._v("“Được rồi...”"),t("br"),t("br"),n._v("Vẻ mặt kìm nén thấu đỏ, thanh niên mãn thị lúng túng đi trở về."),t("br"),t("br"),n._v("Cái này gọi chuyện gì, vốn cho rằng cùng giám bảo cược vận giống nhau, nhờ vào thực lực của hắn, có thể đơn giản nhìn ra vật phẩm chủng loại, tên, thu hoạch được điểm tích lũy, giúp Lục tiểu thư mua được muốn đồ vật, lấy mỹ nhân niềm vui, làm sao đều không có nghĩ đến, cái gì đều không có nhìn ra, thất bại tan tác mà quay trở về."),t("br"),t("br"),n._v("Cái này tốt, ngọn gió không có ra, ngược lại mất mặt."),t("br"),t("br"),n._v("“Công tử không nên nản chí, những này cột đá, là ta tại một chỗ di tích bên trong phát hiện, không biết là ai lưu lại, vô cùng thần bí, liền xem như ta mỗi ngày quan sát, không dưới năm mươi năm, cũng chỉ phá giải ba khối!”"),t("br"),t("br"),n._v("Thấy hắn tâm tình không tốt, Cam Nhất Bình an ủi."),t("br"),t("br"),n._v("“Năm mươi năm? Ba khối?” Thanh niên sững sờ."),t("br"),t("br"),n._v("“Đúng vậy a, chỉ có xác thực biết bên trong đồ vật là vì vật gì, xác thực hiệu quả, mới có thể mở ra. Những năm này, ta các loại thủ đoạn đều thử qua, thực sự phá giải không được, mới mở cái này kỳ trân lâu, muốn tìm người giúp ta vấn đề khó khăn.”"),t("br"),t("br"),n._v("Cam Nhất Bình lắc đầu: “Kỳ thật cũng không cần gấp, thời gian một nén nhang, chỉ là quy định quan sát, cho người phía sau để địa phương, ngươi mười cái kim tệ đã giao, có thể đem phía trên văn tự sao chép trở về, lúc nào phá giải, lúc nào trực tiếp tới giải đáp là được, sẽ không lại thu lấy phí tổn!”"),t("br"),t("br"),n._v("“Sao chép?” Thanh niên lắc đầu: “Chúng ta chỉ là đi ngang qua Huyễn Vũ Đế đô, ở lại thời gian không dài, hơn nữa, cũng chỉ là đối ngươi một gốc dược liệu có hứng thú mà thôi!”"),t("br"),t("br"),n._v("Nếu không phải Lục tiểu thư nhất định phải tới, lấy thân phận của hắn cùng lịch duyệt, mới không đến loại địa phương nhỏ này."),t("br"),t("br"),n._v("Cái này kỳ trân lâu, mặc dù không ít thứ đều rất thưa thớt, rất trân quý, lấy hắn thân gia, thật muốn, vẫn có thể lấy ra không ít thứ cùng sánh vai."),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Thấy hắn hứng thú cũng không phải là rất lớn, Cam Nhất Bình ánh mắt lộ ra vẻ thất vọng, lắc đầu, trong giọng nói mang theo cô đơn: “Những này cột đá từ khi lấy được, hao tốn ta hơn nửa đời người tâm huyết, nhìn tới... Thật chỉ có thể mang theo tiếc nuối đi vào quan tài!”"),t("br"),t("br"),n._v("“Tuổi thọ của hắn...”"),t("br"),t("br"),n._v("Nghe được hắn cảm khái, nhìn thoáng qua, Trương Huyền nhịn không được lắc đầu."),t("br"),t("br"),n._v("Vị lão giả này, mặc dù thực lực cực mạnh, nhưng trong cơ thể đã sinh ra suy bại khí tức, hiển nhiên tuổi thọ đã nhanh đi đến cuối cùng."),t("br"),t("br"),n._v("Võ giả đi vào Hóa Phàm cảnh nhất trọng, sinh mệnh bản chất cải biến, tuổi thọ sẽ trên phạm vi lớn nhảy vọt, trực tiếp đạt tới hai trăm tuổi khoảng chừng."),t("br"),t("br"),n._v("Về sau nương theo thực lực gia tăng, cứ việc tuổi thọ cũng sẽ gia tăng một ít, cũng đã cực kỳ bé nhỏ. Bởi vậy, không đột phá Thánh giả, hơn hai trăm tuổi chính là một đại giới hạn, bảo dưỡng tốt một chút, cũng là chừng ba trăm tuổi."),t("br"),n._v(" Vị lão giả này tuổi thọ vượt qua hai trăm năm mươi tuổi, đã đi đến cuối cùng."),t("br"),t("br"),n._v("Đào móc ra nhiều như vậy kỳ quái cột đá, muốn biết bí mật, lại nghiên cứu không ra, đành phải mở cái này kỳ trân lâu, đem cả một đời đều sưu tập bảo vật lấy ra, hấp dẫn người khác đến đây phá giải."),t("br"),t("br"),n._v("Còn mười cái linh thạch trung phẩm, chỉ là một cái đúng chuẩn cùng yêu cầu mà thôi, nếu không, ai cũng tới đụng đại vận, một ngày cũng không cần làm chuyện khác."),t("br"),t("br"),n._v("Không được đến điểm tích lũy, thanh niên nhìn về phía cô bé trước mắt tràn đầy áy náy."),t("br"),t("br"),n._v("“Lục tiểu thư, thực sự thật không tiện...”"),t("br"),t("br"),n._v("“Ta xem một chút!”"),t("br"),t("br"),n._v("Biết khó mà nhận ra, Lục tiểu thư cũng khơi dậy lòng háo thắng, lông mày giương lên, đi tới, cũng tới đến cái thứ nhất cột đá trước mặt, nhìn thoáng qua chữ viết phía sau, lông mày cũng cùng thanh niên giống nhau nhăn lại."),t("br"),t("br"),n._v("Thời gian một nén nhang, rất nhanh kết thúc, nàng cũng không nhịn được lắc đầu, mặt mất tinh thần."),t("br"),t("br"),n._v("Rất hiển nhiên, liền xem như nàng, cũng không có cái gì biện pháp."),t("br"),t("br"),n._v("“Đáng tiếc!”"),t("br"),t("br"),n._v("Thấy hai vị này đồng dạng không có phá giải cột đá, Cam Nhất Bình than thở."),t("br"),t("br"),n._v("Hai người kia, vô luận tu vi hay là phong thái, đều không phải bình thường, rất rõ ràng là không thể thấy nhiều thiên tài, vốn cho là bọn họ có thể có chút đột phá, không nghĩ tới, giống nhau một cái cũng không phá giải."),t("br"),t("br"),n._v("“Lâu chủ, tại hạ là một vị danh sư!”"),t("br"),t("br"),n._v("Thấy Lục tiểu thư cũng không thành công, thanh niên đi tới lão giả trước mặt, cổ tay khẽ đảo, lòng bàn tay thêm ra một cái huy chương."),t("br"),t("br"),n._v("“Ngũ tinh...”"),t("br"),t("br"),n._v("Cam Nhất Bình con ngươi co rụt lại."),t("br"),t("br"),n._v("Trước đó liền nhìn ra hai người bất phàm, vốn cho rằng là cái gì công tử thế gia, tiểu thư, không nghĩ tới lại là ngũ tinh danh sư!"),t("br"),t("br"),n._v("Loại cấp bậc này danh sư, coi như đi vào hoàng thành, đều muốn chịu đến bệ hạ tự mình tiếp đãi, tôn sùng là khách quý."),t("br"),t("br"),n._v("“Vâng, tại hạ chỉ là gần nhất tại Huyễn Vũ đế quốc làm khách, ngươi lầu dưới gốc kia Đỗ Ưu thảo đối ta có tác dụng lớn, có thể hay không bỏ những thứ yêu thích, bán ra cho ta? Ta bằng lòng ra gấp đôi giá cả, gấp ba, gấp năm lần cũng có thể!”"),t("br"),t("br"),n._v("Thấy đối phương khiếp sợ thân phận của mình, thanh niên hài lòng gật gật đầu."),t("br"),t("br"),n._v("Ngũ tinh danh sư, bất kể đi đến bất kỳ địa phương nào, đều có địa vị cực cao cùng uy tín."),t("br"),t("br"),n._v("“Ngươi muốn mua gốc kia Đỗ Ưu thảo?” Cam Nhất Bình mang theo chần chờ."),t("br"),t("br"),n._v("“Vâng!” Thanh niên gật đầu: “Vật kia thật đối ta hữu dụng, mong rằng thành toàn!”"),t("br"),t("br"),n._v("“Như vậy đi, gấp đôi, gấp ba giá cả, ta cũng không thu, thậm chí có thể không cần tiền của ngươi, chỉ cần bằng lòng cho ta nói một tiết khóa, Đỗ Ưu thảo, tặng ngươi cũng không sao!”"),t("br"),t("br"),n._v("Một lát sau, Cam Nhất Bình nói."),t("br"),t("br"),n._v("Hắn tuổi thọ lập tức đến cuối cùng, nếu như có thể lại làm đột phá, còn có thể sống lâu mấy năm, trước mắt vị này nếu là ngũ tinh danh sư, có lẽ có thể đối với hắn có chút trợ giúp."),t("br"),t("br"),n._v("Lại nói, kỳ trân lâu mở hai mươi năm, đều không có tìm tới có thể phá giải những này cột đá người, tiếp tục chờ đi xuống, đoán chừng cũng là uổng công, cùng hắn tử thủ quy củ, còn không bằng thuận tiện đối phương, kết giao một vị ngũ tinh danh sư."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Trước đó không có báo hy vọng quá lớn, không nghĩ tới vừa nói như vậy, đối phương thật đồng ý, thanh niên vẻ mặt vui mừng, vội vàng gật đầu."),t("br"),t("br"),n._v("Một tiết khóa mà thôi, không tính là gì."),t("br"),t("br"),n._v("“Không cần khách khí!” Cam Nhất Bình khoát khoát tay, gọi cách đó không xa gã sai vặt: “Ngươi đi xuống đem lầu ba Đỗ Ưu thảo cho ta với tay cầm, cho vị công tử này gói kỹ...”"),t("br"),t("br"),n._v("“Là...”"),t("br"),t("br"),n._v("Gã sai vặt gật gật đầu, đang muốn rời đi, liền nghe đến một thanh âm vang lên: “Chậm đã!”"),t("br"),t("br"),n._v("Ngay sau đó liền thấy một tên mập đi tới."),t("br"),t("br"),n._v("Tôn Cường!"),t("br"),t("br"),n._v("Thiếu gia vì tiểu thiếu gia Viên Đào tìm kiếm kích hoạt thể chất vật phẩm, mặc dù không nói cần gì, nhưng con mắt rơi vào cái này gốc Đỗ Ưu thảo bên trên thời gian rất lâu, rất hiển nhiên thứ này có tác dụng lớn."),t("br"),t("br"),n._v("Nếu như cho đối phương lấy đi, còn muốn tìm một gốc đi ra, chỉ sợ cũng không có dễ dàng như vậy, tự nhiên không thể nào để cho hắn lấy đi."),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Thấy một tên mập mở miệng ngăn cản, thanh niên lông mày nhíu một cái."),t("br"),t("br"),n._v("“Không có gì, chẳng qua là cảm thấy không công bằng mà thôi!”"),t("br"),t("br"),n._v("Tôn Cường quay đầu nhìn về phía lão giả: “Thân là kỳ trân lâu lâu chủ, đã xác định dùng điểm tích lũy mới có thể hối đoái bảo vật quy định, liền không thể phá lệ bán ra, nếu không, còn thế nào tin phục tại người?”"),t("br"),t("br"),n._v("Đã có quy củ, vậy liền muốn tuân thủ, thay đổi xoành xoạch, còn thế nào làm cho người tin phục?"),t("br"),t("br"),n._v("“Quy củ là cần tuân thủ, có điều, ta tuổi thọ suýt đến cuối cùng, bia đá phá giải thoạt nhìn không có cái gì hi vọng... Cho nên, thôi được rồi, từ hôm nay trở đi, kỳ trân lâu hoàn toàn đóng lại, đều đóng lại... Đâu còn có cái gì quy tắc có thể nói!”"),t("br"),t("br"),n._v("Than thở một tiếng, Cam Nhất Bình lắc đầu."),t("br"),t("br"),n._v("Đại nạn buông xuống, những ngày này hắn rõ ràng cảm giác lực bất tòng tâm, nhiều năm như vậy đều không có biết đáp án, tiếp tục chờ đi xuống, đoán chừng cũng không có hy vọng, mặc dù trong lòng không cam lòng, cũng biết còn không bằng như vậy coi như không có gì."),t("br"),t("br"),n._v("“Phá giải không có hi vọng? Đó là chúng ta nhà thiếu gia không có ra tay! Chỉ cần hắn ra tay, ngươi những thứ kia, tất cả đều có thể phá giải sạch sẽ, không còn một mống!”"),t("br"),t("br"),n._v("Tôn Cường hừ một tiếng."),t("br"),t("br"),n._v("“Phá giải sạch sẽ? Khẩu khí thật lớn!”"),t("br"),t("br"),n._v("Gương mặt xinh đẹp trầm thấp, Lục tiểu thư hừ lạnh một tiếng."),t("br"),t("br"),n._v("(Canh thứ hai đến, 21 chút, còn có Canh [3]!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-620"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-622"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);